	[50448] = { id = 50448, Voice = '', Text = '那就去黄金时钟调查一下吧。', Speaker = 6645, Option = '', Action = _EmptyTable,}, 
	[50447] = { id = 50447, Voice = '', Text = '你个凡人倒是有点幽默感。', Speaker = 6645, Option = '不是玩笑', Action = _EmptyTable,}, 
