	[50444] = { id = 50444, Voice = '', Text = '你们不要逼我…', Speaker = 6550, Option = '', Action = _EmptyTable,}, 
	[50443] = { id = 50443, Voice = '', Text = '不…我不能再说了！！…', Speaker = 6550, Option = '', Action = _EmptyTable,}, 
