	[50438] = { id = 50438, Voice = '', Text = '我就知道你会回来。', Speaker = 6550, Option = '你知道？', Action = _EmptyTable,}, 
	[50437] = { id = 50437, Voice = '', Text = '就是那老头，他还有点能力。', Speaker = 6645, Option = '', Action = _EmptyTable,}, 
	[50436] = { id = 50436, Voice = '', Text = '罗曼斯基该是个知情者！', Speaker = 6645, Option = '时间先知', Action = _EmptyTable,}, 
