	[50429] = { id = 50429, Voice = '', Text = '四只异时空魔物突然袭来。', Speaker = 6645, Option = '', Action = _EmptyTable,}, 
	[50428] = { id = 50428, Voice = '', Text = '因为奇点球已经被过度使用，打开了异时空的黑洞！', Speaker = 6645, Option = '这么严重', Action = _EmptyTable,}, 
