	[50424] = { id = 50424, Voice = '', Text = '冒险者，你来了。', Speaker = 6645, Option = '是的。', Action = _EmptyTable,}, 
	[50423] = { id = 50423, Voice = '', Text = '我们先告一段落，从这里出去说话。', Speaker = 6625, Option = '', Action = _EmptyTable,}, 
	[50422] = { id = 50422, Voice = '', Text = '是啊，这儿对詹金斯来说太危险了。', Speaker = 6625, Option = '', Action = _EmptyTable,}, 
	[50421] = { id = 50421, Voice = '', Text = '会长威克斯想立刻见到你们，这很紧急。', Speaker = 6786, Option = '了解了', Action = _EmptyTable,}, 
	[50420] = { id = 50420, Voice = '', Text = '是炼金术士公会派我来的。', Speaker = 6786, Option = '发生了什么？', Action = _EmptyTable,}, 
	[50419] = { id = 50419, Voice = '', Text = '先生们，紧急事件！', Speaker = 6786, Option = '', Action = _EmptyTable,}, 
