	[50412] = { id = 50412, Voice = '', Text = '你是说？', Speaker = 6625, Option = '', Action = _EmptyTable,}, 
	[50411] = { id = 50411, Voice = '', Text = '事情已经很明显了，那些魔物根本就不攻击他！', Speaker = 6785, Option = '', Action = _EmptyTable,}, 
	[50410] = { id = 50410, Voice = '', Text = '你难道还看不出来吗！？', Speaker = 6785, Option = '', Action = _EmptyTable,}, 
