	[50395] = { id = 50395, Voice = '', Text = '诺曼留下来保护我，我可不想再被一群讨厌的巫婆围着。', Speaker = 6785, Option = '', Action = _EmptyTable,}, 
	[50394] = { id = 50394, Voice = '', Text = '不如这样，你和瑞德一起去数，两个人效率高一些。', Speaker = 6785, Option = '也好', Action = _EmptyTable,}, 
