	[50391] = { id = 50391, Voice = '', Text = '我破译了一下。', Speaker = 6785, Option = '', Action = _EmptyTable,}, 
	[50390] = { id = 50390, Voice = '', Text = '好像需要密令。', Speaker = 6785, Option = '什么密令？', Action = _EmptyTable,}, 
	[50389] = { id = 50389, Voice = '', Text = '是的，我发现了这蛇洞的入口，就在那边那个巨大齿轮柱上。', Speaker = 6785, Option = '怎么进入？', Action = _EmptyTable,}, 
	[50388] = { id = 50388, Voice = '', Text = '对！我和诺曼提过此事。', Speaker = 6785, Option = '真的吗！', Action = _EmptyTable,}, 
	[50387] = { id = 50387, Voice = '', Text = '一个蛇洞！', Speaker = 6785, Option = '', Action = _EmptyTable,}, 
	[50386] = { id = 50386, Voice = '', Text = '之前也只是假说，现在我更确信了。', Speaker = 6785, Option = '什么？', Action = _EmptyTable,}, 
	[50385] = { id = 50385, Voice = '', Text = '我刚才发现了重要线索，你猜是什么？', Speaker = 6785, Option = '', Action = _EmptyTable,}, 
	[50384] = { id = 50384, Voice = '', Text = '是啊！你们不来，我可能就没机会再见到你们了。', Speaker = 6785, Option = '为什么乱跑？', Action = _EmptyTable,}, 
	[50383] = { id = 50383, Voice = '', Text = '我说了有人一定会来取我性命的。', Speaker = 6785, Option = '刚才危险', Action = _EmptyTable,}, 
	[50373] = { id = 50373, Voice = '', Text = '应该就在这附近！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[50372] = { id = 50372, Voice = '', Text = '看来他有危险，得快点找到他。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[50371] = { id = 50371, Voice = '', Text = '这是詹金斯的眼镜！！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[50370] = { id = 50370, Voice = '', Text = '那是什么！？', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[50369] = { id = 50369, Voice = '', Text = '那我们分头找找吧，这家伙本来就够危险的了，还乱跑。', Speaker = 6625, Option = '', Action = _EmptyTable,}, 
	[50368] = { id = 50368, Voice = '', Text = '你没在附近看到他？', Speaker = 6625, Option = '没有', Action = _EmptyTable,}, 
	[50367] = { id = 50367, Voice = '', Text = '问了其他员工，说他发现重要线索，去钟楼了。', Speaker = 6625, Option = '', Action = _EmptyTable,}, 
	[50366] = { id = 50366, Voice = '', Text = '是啊，正想回去问他，可没在雷根贝勒见到他。', Speaker = 6625, Option = '', Action = _EmptyTable,}, 
	[50365] = { id = 50365, Voice = '', Text = '根据他提供的信息，我们可能发现了一个蛇洞。', Speaker = 6625, Option = '蛇洞？', Action = _EmptyTable,}, 
	[50364] = { id = 50364, Voice = '', Text = '这智慧往往会惹来一些杀身之祸。', Speaker = 6625, Option = '没错', Action = _EmptyTable,}, 
	[50363] = { id = 50363, Voice = '', Text = '不过他虽胆小，但却有过人的智慧。', Speaker = 6625, Option = '', Action = _EmptyTable,}, 
	[50362] = { id = 50362, Voice = '', Text = '原来如此，你是帮詹金斯那胆小鬼搞些药品。', Speaker = 6625, Option = '是啊', Action = _EmptyTable,}, 
	[50361] = { id = 50361, Voice = '', Text = '冒险者，你怎么在这儿！？', Speaker = 6625, Option = '', Action = _EmptyTable,}, 
