Table_Dialog_Quest6 =
{ 
	[1312406] = { id = 1312406, Voice = '', Text = '那真是太遗憾了。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312405] = { id = 1312405, Voice = '', Text = '那真是太遗憾了。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312404] = { id = 1312404, Voice = '', Text = '这么快就收集好了！冒险者你真是太棒了！', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312403] = { id = 1312403, Voice = '', Text = '这么快就收集好了！冒险者你真是太棒了！', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312402] = { id = 1312402, Voice = '', Text = '咦？这点[c][ffff00]恶魔角[-][/c]可不够呢…', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312401] = { id = 1312401, Voice = '', Text = '这么快就收集来了吗？我来看看数量……', Speaker = 1592, Option = '', Action = { num = 1, actionid = 200,},}, 
	[1312400] = { id = 1312400, Voice = '', Text = '唔，故事讲完了，作为报酬，我需要一些[c][ffff00]恶魔角[-][/c]，你能帮我弄来吗？', Speaker = 1592, Option = '', Action = { num = 1, actionid = 201,},}, 
	[1312399] = { id = 1312399, Voice = '', Text = '这就是医者诺雅的故事。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312398] = { id = 1312398, Voice = '', Text = '而诺雅也在凯文·雷诺的建议下，躲在了一个不为人知的地方，继续进行她的药剂研究。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312397] = { id = 1312397, Voice = '', Text = '统帅为了保护诺雅，也派出了一些护卫队，前往南部巡逻，寻找那些不明身份的神秘人。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312396] = { id = 1312396, Voice = '', Text = '幸好有凯文·雷诺以及其他猎人存在，才使得她安全的活了下来。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312395] = { id = 1312395, Voice = '', Text = '消息很快就传了出去，医者诺雅也经历几次暗杀与绑架。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312394] = { id = 1312394, Voice = '', Text = '她第一时间就销毁了这份药剂，但是这么久的试验，还是被有心人发现了。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312393] = { id = 1312393, Voice = '', Text = '因为诺雅清楚，当这种药剂落入坏人的手里，会造成无法想象的灾难。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312392] = { id = 1312392, Voice = '', Text = '当作用在人身上，会使人能够在夜间视物，这截然不同的结果使得诺雅不得不停止研究。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312391] = { id = 1312391, Voice = '', Text = '诺雅发现，这份药剂能够使魔物的战斗力直线上升，魔物们会进入一种狂暴的状态。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312390] = { id = 1312390, Voice = '', Text = '危机解除后，诺雅在凯文·雷诺的陪同下，进行了一次又一次的试验。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312389] = { id = 1312389, Voice = '', Text = '狂暴的白鼠被这闪过的银光钉死在墙上，斐扬有名的猎人凯文·雷诺及时出现，化解了诺雅的危机。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312388] = { id = 1312388, Voice = '', Text = '直视着被突然变化而惊呆的诺雅，咻——一道银色的光芒闪过。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312387] = { id = 1312387, Voice = '', Text = '墨色的药水直接渗入到小白鼠的体内，它逐渐变大，双眼冒出令人心悸的红芒。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312386] = { id = 1312386, Voice = '', Text = '一次偶然，诺雅打翻了幽光草的汁液制成的药剂，药水滴落在实验用的小白鼠身上。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312385] = { id = 1312385, Voice = '', Text = '这么快就回来了？好好好。那我就继续我的故事了。', Speaker = 1592, Option = '', Action = { num = 1, actionid = 200,},}, 
	[1312384] = { id = 1312384, Voice = '', Text = '想知道接下来的故事吗？那就帮我消灭一些[c][ffff00]僵尸[-][/c]来吧。', Speaker = 1592, Option = '', Action = { num = 1, actionid = 201,},}, 
	[1312383] = { id = 1312383, Voice = '', Text = '诺雅研究发现，这种药草没有任何药草能够与之相配。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312382] = { id = 1312382, Voice = '', Text = '她发现了散发出幽蓝色光芒的奇异药草，诺雅称之为幽光草。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312381] = { id = 1312381, Voice = '', Text = '而诺雅也是整天沉迷于药草研究，在斐扬南部的一处深谷中。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312380] = { id = 1312380, Voice = '', Text = '猎人们对她的帮助心存感激，经常带给她一些奇奇怪怪的药草。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312379] = { id = 1312379, Voice = '', Text = '发明了以提升体力的体力药剂，所有药剂都被她无偿提供给斐扬的猎人们。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312378] = { id = 1312378, Voice = '', Text = '但是她的功绩毋庸置疑，她发明了加快伤口痊愈的金创药。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312377] = { id = 1312377, Voice = '', Text = '使人变成斐扬野猪的变身药剂，以及会使人全身瘙痒的痒痒水等等。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312376] = { id = 1312376, Voice = '', Text = '她专注于研究新的药剂，总有一些奇怪的药剂出自她手。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312375] = { id = 1312375, Voice = '', Text = '诺雅就是现今斐扬最厉害的药剂师，她是斐扬草药世家的传人。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312374] = { id = 1312374, Voice = '', Text = '药剂师在这方面就显得随心所欲，他们更大的兴趣是在于研究和探索。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312373] = { id = 1312373, Voice = '', Text = '牧师们致力于升华他们的精神，通过服务大众来表达他们坚定不移的信念。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312372] = { id = 1312372, Voice = '', Text = '他们发现了草药的秘密，经过久远的发展，斐扬形成了一个新的医学流派。他们称自己为药剂师或者医者。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312371] = { id = 1312371, Voice = '', Text = '直到某位斐扬人发现，看似路边不起眼的小草竟然能使得伤口快速痊愈。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312370] = { id = 1312370, Voice = '', Text = '在很久的一段时间内，斐扬的人们饱受病痛的折磨。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312369] = { id = 1312369, Voice = '', Text = '由于这里的斐扬位置距离大陆中心较远，又处在深山之中，极少有牧师来到这里。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312368] = { id = 1312368, Voice = '', Text = '那我们今天就讲讲医者诺雅的故事。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312367] = { id = 1312367, Voice = '', Text = '咦？这点[c][ffff00]刹勒空结晶[-][/c]可不够呢…', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312366] = { id = 1312366, Voice = '', Text = '这么快就收集来了吗？我来看看数量……', Speaker = 1592, Option = '', Action = { num = 1, actionid = 200,},}, 
	[1312365] = { id = 1312365, Voice = '', Text = '唔，故事讲完了，作为报酬，我需要一些[c][ffff00]刹勒空结晶[-][/c]，你能帮我弄来吗？', Speaker = 1592, Option = '', Action = { num = 1, actionid = 201,},}, 
	[1312364] = { id = 1312364, Voice = '', Text = '这就是流浪剑士盖因·落斯的故事。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312363] = { id = 1312363, Voice = '', Text = '据说他已经得到了一些有用的消息，在静静地等待着占卜师所说的“同盟者”。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312362] = { id = 1312362, Voice = '', Text = '而现在，盖因·落斯来到了斐扬，这位到处流浪的剑士，只是在不停地寻找一个神物，传说中充满光明圣洁力量的光明之心。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312361] = { id = 1312361, Voice = '', Text = '但是他心中发誓，一定会再次来到这里，拯救出他心爱的澈。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312360] = { id = 1312360, Voice = '', Text = '大战过后，澈消失在古城深处，而盖因·落斯也受了重伤逃离了古城。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312359] = { id = 1312359, Voice = '', Text = '她极速向盖因·落斯冲去，双手带着毁灭力量向着盖因迎头落下。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312358] = { id = 1312358, Voice = '', Text = '她发现了正在注视着她的这个剑士，她在迟疑着什么，旋即被暴虐的黑暗气息冲昏了头脑。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312357] = { id = 1312357, Voice = '', Text = '无神的双眼中发散着紫色的妖光，暴虐的力量在澈的身体中流转。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312356] = { id = 1312356, Voice = '', Text = '当他来到暗黑气息的中心点，他却见到了自己最不想在此刻见到的人——澈。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312355] = { id = 1312355, Voice = '', Text = '盖因·落斯只身一人杀入古城，暗黑气息肆意暴虐。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312354] = { id = 1312354, Voice = '', Text = '呆立了片刻，盖因·落斯坚定了眼神，发誓要为这些受尽折磨而死的人们报仇。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312353] = { id = 1312353, Voice = '', Text = '盖因看着这地狱般的惨况却无能为力，只能跪倒在古城前，看着他熟悉的亲友们尸骨无存。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312352] = { id = 1312352, Voice = '', Text = '那些备受折磨的村民在爆发的诅咒下，如冬雪一般消融，甚至，连一点痕迹都没有留下。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312351] = { id = 1312351, Voice = '', Text = '邪恶的诅咒与魔物的生命相连，魔物一旦逝去，邪恶的诅咒就会随之爆发。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312350] = { id = 1312350, Voice = '', Text = '盖因·落斯无法压抑内心的暴怒，用自己锤炼许久的剑术解决了那些低劣的魔物。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312349] = { id = 1312349, Voice = '', Text = '女神之手的人利用强大的黑暗魔法，折磨着故乡中的人们。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312348] = { id = 1312348, Voice = '', Text = '他的心情如同归巢的雀儿般欣喜，他来到了古城，却见到了无法忍受的一幕。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312347] = { id = 1312347, Voice = '', Text = '盖因与他们大战一场，却无意中得到了澈的消息，澈就在古城，盖因兴奋得不能自已。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312346] = { id = 1312346, Voice = '', Text = '他在梦罗克的沙漠中遭遇了一股神秘势力，他们称自己为女神之手。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312345] = { id = 1312345, Voice = '', Text = '这么快就回来了？好好好。那我就继续我的故事了。', Speaker = 1592, Option = '', Action = { num = 1, actionid = 200,},}, 
	[1312344] = { id = 1312344, Voice = '', Text = '想知道接下来的故事吗？那就帮我消灭一些[c][ffff00]鬼火[-][/c]来吧。', Speaker = 1592, Option = '', Action = { num = 1, actionid = 201,},}, 
	[1312343] = { id = 1312343, Voice = '', Text = '一边游历大陆，锻炼自己的剑术修为，一边寻找着澈的身影。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312342] = { id = 1312342, Voice = '', Text = '混乱中，盖因与他亲爱的澈走散了，盖因无奈之下，离开了古城。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312341] = { id = 1312341, Voice = '', Text = '理所当然，这个隐世的小村庄被卷入这疯狂的暴乱中。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312340] = { id = 1312340, Voice = '', Text = '平静许久的古城没有了魔王的掌控，彻底陷入了暴乱。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312339] = { id = 1312339, Voice = '', Text = '在重重包围下成功刺杀了黑暗之王，一个称霸古城的强大魔王。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312338] = { id = 1312338, Voice = '', Text = '直到有一天，刺客公会的无名者，带着决然的杀意进入古城。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312337] = { id = 1312337, Voice = '', Text = '而盖因·落斯是村庄中极具天赋的年轻人，他和他的青梅竹马，澈，无忧无虑的生活在一起。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312336] = { id = 1312336, Voice = '', Text = '传说曾是闻名大陆的圣剑士欧姆的隐居之所，这里的人们人人都有强大的剑术在身。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312335] = { id = 1312335, Voice = '', Text = '流浪剑士盖因·落斯，出生在古城周围的一个隐世小村庄。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312334] = { id = 1312334, Voice = '', Text = '那我们今天就讲讲盖因·落斯的故事。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[1312333] = { id = 1312333, Voice = '', Text = '咦？这点[c][ffff00]不死心脏[-][/c]可不够呢…', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312332] = { id = 1312332, Voice = '', Text = '这么快就收集来了吗？我来看看数量……', Speaker = 1591, Option = '', Action = { num = 1, actionid = 200,},}, 
	[1312331] = { id = 1312331, Voice = '', Text = '唔，故事讲完了，作为报酬，我需要一些[c][ffff00]不死心脏[-][/c]，你能帮我弄来吗？', Speaker = 1591, Option = '', Action = { num = 1, actionid = 201,},}, 
	[1312330] = { id = 1312330, Voice = '', Text = '这就是占卜师星痕的故事。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312329] = { id = 1312329, Voice = '', Text = '紫水晶中，你的未来将无所遁藏——星痕。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312328] = { id = 1312328, Voice = '', Text = '而星痕，隐居在斐扬某处，关注着斐扬的命运。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312327] = { id = 1312327, Voice = '', Text = '如今，当年的预言之子早已长大成人，新的一代预言师也已成长起来。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312326] = { id = 1312326, Voice = '', Text = '她从斐扬中挑选了一位少女继承她的衣钵。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312325] = { id = 1312325, Voice = '', Text = '星痕失去了占卜师的能力，斐扬的占卜师不能断绝传承。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312324] = { id = 1312324, Voice = '', Text = '轻声宣布：这个孩子以后将成长为斐扬的统帅，整个王国都将传播着他的事迹。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312323] = { id = 1312323, Voice = '', Text = '占星师抱着孩子离开了小屋，把他交给他的父亲，一个勇猛的猎人。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312322] = { id = 1312322, Voice = '', Text = '一时的安逸也意味着不久后毁灭的到来。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312321] = { id = 1312321, Voice = '', Text = '在孩子耳畔轻轻诉说：孩子，当面临选择的时候，仔细思考，有时候一时的毁灭意味着新的开始。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312320] = { id = 1312320, Voice = '', Text = '使得斐扬在灾难中依旧存活下来，占星师轻轻抱起孩子，带着些许魔力。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312319] = { id = 1312319, Voice = '', Text = '孩子成长后成为斐扬最伟大的勇士，他庇护着斐扬。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312318] = { id = 1312318, Voice = '', Text = '开始了第二次占卜，这次她的头发全白了，但是她也得到了想看到的结果。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312317] = { id = 1312317, Voice = '', Text = '孩子睁大好奇的双眼，注视着星痕，对视片刻后，星痕放下了短刀。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312316] = { id = 1312316, Voice = '', Text = '星痕抓起身边镌刻铭文的华丽短刀，意图结束这恶魔的化身。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312315] = { id = 1312315, Voice = '', Text = '她乌黑的头发在冥想中半黑半白，这次占卜，她付出了近二十年的寿命，却只得到了这样毁灭的结果。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312314] = { id = 1312314, Voice = '', Text = '火光伴随着长大的孩子狰狞的笑容，富饶的斐扬沦为废墟。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312313] = { id = 1312313, Voice = '', Text = '星痕拿出了星牌——一个古老的占卜道具，她点燃了蜡烛，进入了冥想。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312312] = { id = 1312312, Voice = '', Text = '这么快就回来了？好好好。那我就继续我的故事了。', Speaker = 1591, Option = '', Action = { num = 1, actionid = 200,},}, 
	[1312311] = { id = 1312311, Voice = '', Text = '想知道接下来的故事吗？那就帮我消灭一些[c][ffff00]野猪[-][/c]来吧。', Speaker = 1591, Option = '', Action = { num = 1, actionid = 201,},}, 
	[1312310] = { id = 1312310, Voice = '', Text = '星痕抱着孩子走进她占卜的小屋，她要为这孩子进行一次占卜。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312309] = { id = 1312309, Voice = '', Text = '片刻后，星痕看到了这个预言之夜诞生的孩子。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312308] = { id = 1312308, Voice = '', Text = '占卜师并不是神，我们能够看到的，只是那一角未来，而这次的灾难，我甚至不能窥清全部，星痕如此回答。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312307] = { id = 1312307, Voice = '', Text = '未知的未来——占卜师大人，伟大如您，也有不可预知的未来吗？', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312306] = { id = 1312306, Voice = '', Text = '星痕沉默，今晚斐扬可有新生的生命？命运之子于今夜降临，而他，将带领着斐扬走向未知的未来！', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312305] = { id = 1312305, Voice = '', Text = '民众们高呼——占卜师大人，请给予我们指引。您是我们心中的明灯，您必将带领我们度过灾难！', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312304] = { id = 1312304, Voice = '', Text = '火，无尽的火海——整个斐扬都不复存在！', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312303] = { id = 1312303, Voice = '', Text = '斐扬的民众围了上来，尊敬的占卜师大人，您在水晶中看到了什么？', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312302] = { id = 1312302, Voice = '', Text = '星痕跌跌撞撞地走出房门，奋力大喊——灾难将至，你们，快逃吧！', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312301] = { id = 1312301, Voice = '', Text = '砰——水晶炸裂，星痕呢喃：红光闪现，灾难将至，死亡与重生，系于一人！', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312300] = { id = 1312300, Voice = '', Text = '随之而来冲天燃烧的烈焰照亮了星痕的脸。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312299] = { id = 1312299, Voice = '', Text = '幽深的紫色中闪过一缕红芒，一个小孩的笑脸呈现在水晶中。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312298] = { id = 1312298, Voice = '', Text = '这天，她凝视着她心爱的紫水晶：美丽的紫水晶啊，告诉我，斐扬的民众能否幸福美满？', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312297] = { id = 1312297, Voice = '', Text = '而星痕也不负众望，在继承师傅的重任后，带着斐扬从一个小村庄，一步一步走向繁荣。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312296] = { id = 1312296, Voice = '', Text = '前代占卜师甚至断言，星痕是这千年来最伟大的占卜师。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312295] = { id = 1312295, Voice = '', Text = '星痕从小便展示出异于常人的天赋，她的双眼无比深邃，放佛隐藏着无尽的星空。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312294] = { id = 1312294, Voice = '', Text = '明亮的星光照耀着斐扬，星痕会引领斐扬走向富饶！前代占卜师这样预言道。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312293] = { id = 1312293, Voice = '', Text = '那我们今天就讲讲星痕的故事。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312292] = { id = 1312292, Voice = '', Text = '咦？这点[c][ffff00]坚硬外皮[-][/c]可不够呢…', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312291] = { id = 1312291, Voice = '', Text = '这么快就收集来了吗？我来看看数量……', Speaker = 1591, Option = '', Action = { num = 1, actionid = 200,},}, 
	[1312290] = { id = 1312290, Voice = '', Text = '唔，故事讲完了，作为报酬，我需要一些[c][ffff00]坚硬外皮[-][/c]，你能帮我弄来吗？', Speaker = 1591, Option = '', Action = { num = 1, actionid = 201,},}, 
	[1312289] = { id = 1312289, Voice = '', Text = '这就是统帅的故事。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312288] = { id = 1312288, Voice = '', Text = '那些觊觎着伊米尔之心的宵小很快就会到来，而统帅早已做好了战斗的准备。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312287] = { id = 1312287, Voice = '', Text = '而他也知道，安宁的日子就快结束了。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312286] = { id = 1312286, Voice = '', Text = '如今，在大大小小的战斗中，年轻的统帅变得越发强大。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312285] = { id = 1312285, Voice = '', Text = '而要想对抗这些觊觎，只能不断让自己变得更强。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312284] = { id = 1312284, Voice = '', Text = '统帅心里知道，经过这一次事件，伊米尔之心会招来无数人的觊觎。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312283] = { id = 1312283, Voice = '', Text = '大战过后，虎王带着满身伤痕离开了斐扬，而统帅则接受着民众的膜拜。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312282] = { id = 1312282, Voice = '', Text = '虎王悲鸣一声，感觉现在的统帅无可匹敌，他的力量放佛能与巨人相提并论，虎王它怕了——', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312281] = { id = 1312281, Voice = '', Text = '统帅抢先出手，刺中了虎王的腹部，把它仰面打倒在地。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312280] = { id = 1312280, Voice = '', Text = '他仰天怒吼：虎王，为你今日的行为赎罪吧！', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312279] = { id = 1312279, Voice = '', Text = '神力在身体中流淌，统帅感受着身体中几欲狂暴的力量。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312278] = { id = 1312278, Voice = '', Text = '柔和的红光变得强烈起来，浓烈得如同血液一般笼罩着年轻的统帅。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312277] = { id = 1312277, Voice = '', Text = '统帅轻蔑道：无礼的畜生，今日就是让你知道冒犯斐扬的代价！ ', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312276] = { id = 1312276, Voice = '', Text = '年轻的统帅拿出那抹妖异的红光，虎王眼中的觊觎不加掩饰。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312275] = { id = 1312275, Voice = '', Text = '我要守护斐扬直到最后一刻，斐扬绝不会在我手中消失，我是预言之子！', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312274] = { id = 1312274, Voice = '', Text = '看着斐扬的伤亡不断增加，统帅心道：在搏斗中，唤起我内心战斗的情绪。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312273] = { id = 1312273, Voice = '', Text = '统帅与虎王对立，年轻的统帅力量无法与虎王抗衡。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312272] = { id = 1312272, Voice = '', Text = '嘶吼与剑鸣交织，血色似乎染红了斐扬的天空。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312271] = { id = 1312271, Voice = '', Text = '它如同问道腥味的猫，悄无声息地带着南部的魔物，团团围住了暮色中的山城。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312270] = { id = 1312270, Voice = '', Text = '灾难如约而至，晶石的光芒虽然柔和，但在虎王眼里却无比刺眼。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312269] = { id = 1312269, Voice = '', Text = '这么快就回来了？好好好。那我就继续我的故事了。', Speaker = 1591, Option = '', Action = { num = 1, actionid = 200,},}, 
	[1312268] = { id = 1312268, Voice = '', Text = '想知道接下来的故事吗？那就帮我消灭一些[c][ffff00]长老树精[-][/c]来吧。', Speaker = 1591, Option = '', Action = { num = 1, actionid = 201,},}, 
	[1312267] = { id = 1312267, Voice = '', Text = '统帅挥手回答道：未来在我手中，星痕叹气，希望以后您不会后悔。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312266] = { id = 1312266, Voice = '', Text = '一次斐扬毁灭，一次斐扬新生，是我无能，分辨不清模糊的未来。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312265] = { id = 1312265, Voice = '', Text = '占卜师佝偻着身躯，回答道：在您年幼时，我为您占卜两次。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312264] = { id = 1312264, Voice = '', Text = '年轻的统帅，微微一笑回答道：善良的占卜师啊，你不是已经失去了所有力量吗？你是否看到了什么？', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312263] = { id = 1312263, Voice = '', Text = '它是一切灾难的起源，它拥有无上的魔力，这不详的红光必将招来灾厄！', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312262] = { id = 1312262, Voice = '', Text = '年轻的统帅啊，您是否得到过不详的红光？', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312261] = { id = 1312261, Voice = '', Text = '翌日，星痕跌跌撞撞地走到统帅面前。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312260] = { id = 1312260, Voice = '', Text = '他闭着双眼甚至能够感受到晶石的澎湃力量，拥有了这，他甚至觉得自己不需要在意星痕所说的灾难。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312259] = { id = 1312259, Voice = '', Text = '他走到地宫深处，看清着散发着柔和红光的晶石。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312258] = { id = 1312258, Voice = '', Text = '某夜，统帅看到斐扬皇宫的地宫中散发出了柔和的红光。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312257] = { id = 1312257, Voice = '', Text = '但是统帅的力量远不如儿时的同伴金胜，这使他非常苦恼。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312256] = { id = 1312256, Voice = '', Text = '在年迈星痕的教导下，很快就成为了斐扬的城主，斐扬在他的带领下愈发强盛。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312255] = { id = 1312255, Voice = '', Text = '统帅明白，他是为了斐扬而生，在大运金胜等人中，年轻的统帅以智慧著称。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312254] = { id = 1312254, Voice = '', Text = '新生或是毁灭都在你的一念之间，星痕对着年幼的统帅说着。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312253] = { id = 1312253, Voice = '', Text = '某些人一出生就被赋予了使命，他们的人生不再平凡，统帅就是其中之一。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312252] = { id = 1312252, Voice = '', Text = '那我们今天就讲讲统帅的故事。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312251] = { id = 1312251, Voice = '', Text = '咦？这点[c][ffff00]恶魔角[-][/c]可不够呢…', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312250] = { id = 1312250, Voice = '', Text = '这么快就收集来了吗？我来看看数量……', Speaker = 1591, Option = '', Action = { num = 1, actionid = 200,},}, 
	[1312249] = { id = 1312249, Voice = '', Text = '唔，故事讲完了，作为报酬，我需要一些[c][ffff00]恶魔角[-][/c]，你能帮我弄来吗？', Speaker = 1591, Option = '', Action = { num = 1, actionid = 201,},}, 
	[1312248] = { id = 1312248, Voice = '', Text = '这就是大运的故事。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312247] = { id = 1312247, Voice = '', Text = '他希望凭借着自己的力量来保护多灾多难的斐扬。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312246] = { id = 1312246, Voice = '', Text = '大运成功的得到了自己的梦寐以求的力量，如今的大运带着强大的力量回到了斐扬。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312245] = { id = 1312245, Voice = '', Text = '为了神，传达着神的声明，以他拥有的肉体来显示他的光荣。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312244] = { id = 1312244, Voice = '', Text = '他成为了理解了神的真意，并自行履行神的真意的人。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312243] = { id = 1312243, Voice = '', Text = '大运毅然放弃了圣光之力，转而加强体魄与格斗技巧的锻炼。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312242] = { id = 1312242, Voice = '', Text = '其效果甚至更甚于别人拿刀棍时所产生的破坏力，他从没有想到原来力量还可以如此锻炼。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312241] = { id = 1312241, Voice = '', Text = '他们可以自由的幻化出一颗肉眼看的到的气，藉由这些气来进行各种强力的攻击。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312240] = { id = 1312240, Voice = '', Text = '这里的人们不断地段练自己的肉体，调和体内运行的气使其活性化。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312239] = { id = 1312239, Voice = '', Text = '在那里，他找到了一种改变自己力量的方法。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312238] = { id = 1312238, Voice = '', Text = '终于，他来到了一个修道院，人们称之为圣卡毕利那修道院。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312237] = { id = 1312237, Voice = '', Text = '他走过平原，爬过高山，越过海洋。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312236] = { id = 1312236, Voice = '', Text = '大运思量再三，终于离开了斐扬，寻找变强的方法。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312235] = { id = 1312235, Voice = '', Text = '大运坚持认为自己空有治疗救人的能力，却救不了金初羽爷爷，认为只有力量才是永恒。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312234] = { id = 1312234, Voice = '', Text = '金初羽就这样去世了，他们把他埋在了斐扬树林。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312233] = { id = 1312233, Voice = '', Text = '狼王野性的力量在伤口肆虐，大运的圣光之力对这伤口毫无办法。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312232] = { id = 1312232, Voice = '', Text = '当他们找到人来支援的时候，金初羽已经倒在地上无法动弹了。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312231] = { id = 1312231, Voice = '', Text = '大运回头看到了狼王残暴的姿态以及金初羽不屈的背影。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312230] = { id = 1312230, Voice = '', Text = '狼王暴怒，弱小的剑士还敢对它挥剑，狼王奋力一爪，锋锐的钢爪瞬间击断了他的剑。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312229] = { id = 1312229, Voice = '', Text = '他手握重剑，迈着坚实的大步，像是一个誓死不回的将士冲向狼王，临走之前也要给予狼王一定的教训。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312228] = { id = 1312228, Voice = '', Text = '看着三人成功逃离，金初羽刚毅的脸上挂着浅笑。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312227] = { id = 1312227, Voice = '', Text = '金初羽的剑术将三人保护得密不透风，狼王似乎感到自己的威严受到了挑衅，狂暴的气息瞬间冲击着金初羽的身体', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312226] = { id = 1312226, Voice = '', Text = '三人早已经筋疲力尽，金初羽尽力拖住了狼王和它的爪牙们，并回头对三人大呼：快走！这里交给我！', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312225] = { id = 1312225, Voice = '', Text = '金胜的爷爷金初羽在关键时刻出现，击退了包围三人的狼王爪牙。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312224] = { id = 1312224, Voice = '', Text = '这么快就回来了？好好好。那我就继续我的故事了。', Speaker = 1591, Option = '', Action = { num = 1, actionid = 200,},}, 
	[1312223] = { id = 1312223, Voice = '', Text = '想知道接下来的故事吗？那就帮我消灭一些[c][ffff00]将军魔碑[-][/c]来吧。', Speaker = 1591, Option = '', Action = { num = 1, actionid = 201,},}, 
	[1312222] = { id = 1312222, Voice = '', Text = '在再这样下去，三人迟早会成为狼王的食物。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312221] = { id = 1312221, Voice = '', Text = '狼王的力量远不是三人能够抵抗的，他们开始受伤，受伤的速度比大运治疗的速度还要快。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312220] = { id = 1312220, Voice = '', Text = '三人之间的默契配合使得他们一直有惊无险，直到遇到了斐扬树林中强大的狼王。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312219] = { id = 1312219, Voice = '', Text = '年轻的“铁三角”们似乎不再满足于仅仅在斐扬南部历练，他们开始前往斐扬树林寻求历练与突破。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312218] = { id = 1312218, Voice = '', Text = '每当统帅金胜与魔物争斗时，大运的治疗总能很及时的赶到，他们三人也被称之为“斐扬铁三角”。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312217] = { id = 1312217, Voice = '', Text = '但是大运并没有成为一个战士的天赋，大运的天赋更多的是展现在治疗救人上。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312216] = { id = 1312216, Voice = '', Text = '他也立志守护着斐扬，不容它受到一点伤害。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312215] = { id = 1312215, Voice = '', Text = '作为斐扬的一员，大运一直以自己是斐扬的一员为荣。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[1312214] = { id = 1312214, Voice = '', Text = '那我们今天就讲讲大运的故事。', Speaker = 1591, Option = '', Action = _EmptyTable,}, 
	[872837] = { id = 872837, Voice = '', Text = '德斯黎含泪逃离了时间黑洞的范围。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[132166] = { id = 132166, Voice = '', Text = '没…没想到你这么强…居然击伤了我…不过…来自异世界的彩蝶王会把你撕成碎片！哈哈哈…', Speaker = 2578, Option = '', Action = _EmptyTable,}, 
	[132165] = { id = 132165, Voice = '', Text = '踏入传送阵，前往深海神殿！', Speaker = 0, Option = '前往。', Action = _EmptyTable,}, 
	[132164] = { id = 132164, Voice = '', Text = '但是我们绝对不会因此放弃，为了陛下与尘世大陆，我们一定会坚持下去的！', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132163] = { id = 132163, Voice = '', Text = '我们要忍受着魔物的进攻和长时间的风餐露宿，还要进行这么复杂的研究，这对我们魔物学者可是一个非常大的考验呢！', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132162] = { id = 132162, Voice = '', Text = '当然啦，研究魔物可不是件简单的事情，尤其是人型魔物，他们的结构更加复杂多样。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132161] = { id = 132161, Voice = '', Text = '我们人型魔物的研究却还在原地踏步，这很不好，要知道人型魔物研究可是我们研究会的重中之重呢。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132160] = { id = 132160, Voice = '', Text = '最近我们魔物研究会对不死系魔物的研究有了很显著的进步。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132159] = { id = 132159, Voice = '', Text = '魔物研究会是为了有效的解决层出不穷的魔物所衍生出来的问题。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132158] = { id = 132158, Voice = '', Text = '我们研究会会尽自己最大的力量，找到这种类人型魔物的根来源，为国王陛下分忧！', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132157] = { id = 132157, Voice = '', Text = '国王陛下觉得一旦这种魔物形成一定的数量会对王国造成巨大的危害，所以派遣我们魔物研究会来这里研究兽人。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132156] = { id = 132156, Voice = '', Text = '据我所知，兽人绝对不会拥有铁器的冶炼技艺。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132155] = { id = 132155, Voice = '', Text = '但是最让人感到奇怪的是，之前兽人所使用的武器大多都是石质，木质的武器，但是最近我看到了有的兽人用起了铁质的武器。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132154] = { id = 132154, Voice = '', Text = '是的，他们也是魔物，但是他们的身体里没有类似杰勒比结晶的身体组织，很奇怪吧？', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132153] = { id = 132153, Voice = '', Text = '但是进攻性极强的兽人们又让我想起狂暴的魔物，所以根据我的研究来看，兽人们是具有一定智慧的魔物。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132152] = { id = 132152, Voice = '', Text = '这是类人型魔物一大特点，他们懂得使用武器与火，他们甚至懂得建造简单的房屋用于居住。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132151] = { id = 132151, Voice = '', Text = '类人型的魔物一般都拥有与我们一般的四肢，无论是行走还是进攻方式都与我们一致，甚至于他们还拥有一定的智慧。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132150] = { id = 132150, Voice = '', Text = '这种类人型魔物的出现是一个了不起的发现！国王陛下非常关注这件事情。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132149] = { id = 132149, Voice = '', Text = '这种类人型魔物的出现引起了我们魔物研究会的关注。这种类人型魔物也是我研究的方向，所以我就来到了这里进行我的研究。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132148] = { id = 132148, Voice = '', Text = '他们的骚扰导致周围几个村落苦不堪言。一旦被兽人攻陷，恐怕村落都不会留有活口。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132147] = { id = 132147, Voice = '', Text = '而兽人又是极具进攻性的魔物，地下不能扩张了，他们只能选择向周围的村落进攻。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132146] = { id = 132146, Voice = '', Text = '不过最近不知道为什么原因，兽人村的地下洞穴入口都被封闭了。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132145] = { id = 132145, Voice = '', Text = '兽人村的兽人们扩张领地的方式与众不同，他们大多崇尚大地，认为大地是一切生命力量的源泉，所以他们都是往地下挖掘洞穴来扩张生存空间的。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132144] = { id = 132144, Voice = '', Text = '这次多谢你了，冒险者，请拿好你的奖励。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132143] = { id = 132143, Voice = '', Text = '狂暴因子使得兽人天生就具有极强的进攻性与战斗意识。这个狂暴因子植入到其他魔物身上的话，也会有同样的作用。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132142] = { id = 132142, Voice = '', Text = '兽人的进攻性这么强烈，都是这个狂暴因子的原因，这种狂暴因子只出现在拥有一定智慧的魔物身上。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132141] = { id = 132141, Voice = '', Text = '看来兽人狂暴化的原因就是这种成分了，我决定给它命名，叫做“狂暴因子”，怎么样？  ', Speaker = 2586, Option = '挺好的', Action = _EmptyTable,}, 
	[132140] = { id = 132140, Voice = '', Text = '真是吓我一跳呢！幸好有你在，不然我可危险了。虽然危险，但是我也得到了相当宝贵的数据。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132139] = { id = 132139, Voice = '', Text = '不好，小白鼠变异了！', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132138] = { id = 132138, Voice = '', Text = '唔，我这里有实验用的小白鼠，把这个成分注入到它的体内吧。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132137] = { id = 132137, Voice = '', Text = '研究结果出来了，我从骨骼中提取到一种之前从未见过的成分，现在我需要了解一下这个成分到底有什么作用。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132136] = { id = 132136, Voice = '', Text = '干的不错冒险者！我的运气真是好，找到了这么一个强大的冒险者来帮忙！', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132135] = { id = 132135, Voice = '', Text = '所以冒险者，收集一些兽人的骨骼带给我吧。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132134] = { id = 132134, Voice = '', Text = '我之前的所有研究都表明，骨骼中所蕴含着一个物种大部分的信息。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132133] = { id = 132133, Voice = '', Text = '这兽人的狂暴进攻的根本原因，应该不是这些玻璃珠。根据我的经验，研究一个物种的最快方法就是研究他们的骨骼。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132132] = { id = 132132, Voice = '', Text = '无论从材质，形状，还有魔力反应来看，都是与我家孩子所拥有的一模一样！', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132131] = { id = 132131, Voice = '', Text = '这个玻璃珠，怎么看都像是我们普隆德拉生产出的玩具，我记得我家的孩子就有很多呢！', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132130] = { id = 132130, Voice = '', Text = '很奇怪，冒险者。这个恐怕不对啊。', Speaker = 2586, Option = '怎么了？', Action = _EmptyTable,}, 
	[132129] = { id = 132129, Voice = '', Text = '这点材料恐怕不太够，冒险者你还是再收集一些来吧。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132128] = { id = 132128, Voice = '', Text = '这么快就收集好了！冒险者，你真是太棒了！', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132127] = { id = 132127, Voice = '', Text = '这么快就回来了？让我来检查一下玻璃珠的数量....', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132126] = { id = 132126, Voice = '', Text = '所以冒险者，我想请你帮助我，收集一些兽人身上的玻璃珠来吧。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132125] = { id = 132125, Voice = '', Text = '但是我手中的玻璃珠太少了...而且我也只是个学者，对于消灭魔物这种事还是有点力不从心呢。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132124] = { id = 132124, Voice = '', Text = '现在我想研究一下玻璃珠对魔物的狂暴是否产生了影响。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132123] = { id = 132123, Voice = '', Text = '在我们对兽人的深入研究后，并没有在兽人的体内找到杰勒比结晶这种晶体，反而是玻璃珠倒是找到了一些。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132122] = { id = 132122, Voice = '', Text = '众所周知，魔物的狂暴的原因有很多种，其中我们最常见的就是魔物身体组织中有杰勒比结晶。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132121] = { id = 132121, Voice = '', Text = '我们魔物研究会刚刚进驻这片地区就受到了他们的疯狂攻击。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132120] = { id = 132120, Voice = '', Text = '这里的兽人“魔物”...姑且称之为"魔物"....他们的进攻性实在是太强了。', Speaker = 2586, Option = '', Action = _EmptyTable,}, 
	[132119] = { id = 132119, Voice = '', Text = '冒险者，是你啊。我还以为是那些野蛮的家伙呢。     ', Speaker = 2586, Option = '野蛮的家伙？', Action = _EmptyTable,}, 
	[132117] = { id = 132117, Voice = '', Text = '我们可都是为了各位的安全在做研究的呢！', Speaker = 2571, Option = '', Action = _EmptyTable,}, 
	[132116] = { id = 132116, Voice = '', Text = '啊，我太激动了，不好意思，呵呵…所以说，希望你遇见魔物学者的时候态度可以亲切一点。', Speaker = 2571, Option = '', Action = _EmptyTable,}, 
	[132115] = { id = 132115, Voice = '', Text = '但是我们绝对不会因此放弃，为了各位与尘世大陆，我们一定会坚持下去！', Speaker = 2571, Option = '', Action = _EmptyTable,}, 
	[132114] = { id = 132114, Voice = '', Text = '在我们之中就有很多学者因此而牺牲了他们宝贵的生命…', Speaker = 2571, Option = '', Action = _EmptyTable,}, 
	[132113] = { id = 132113, Voice = '', Text = '当然啦，研究魔物可不是件简单的事情，我们可是置自己的生死于不顾，在密穴以及原野研究这些魔物的。', Speaker = 2571, Option = '', Action = _EmptyTable,}, 
	[132112] = { id = 132112, Voice = '', Text = '为了透过研究与探险，挖掘魔物们的根来源，我们正在努力的朝目标迈进。', Speaker = 2571, Option = '', Action = _EmptyTable,}, 
	[132111] = { id = 132111, Voice = '', Text = '这是在特力斯坦国王陛下的名义下所成立的研究组织，目前聚集所有尘世大陆上的各方面人才。', Speaker = 2571, Option = '', Action = _EmptyTable,}, 
	[132110] = { id = 132110, Voice = '', Text = '魔物研究会是为了有效的解决层出不穷的魔物所衍生出来的问题。', Speaker = 2571, Option = '', Action = _EmptyTable,}, 
	[132109] = { id = 132109, Voice = '', Text = '为了达成国王陛下与遗族们的心愿，我们研究会将尽最大的努力找出不死系魔物的根来源的，一定！', Speaker = 2571, Option = '', Action = _EmptyTable,}, 
	[132108] = { id = 132108, Voice = '', Text = '国王陛下特力斯坦三世也希望不死系魔物不再出现，所以大力的支援我们协会。', Speaker = 2571, Option = '', Action = _EmptyTable,}, 
	[132107] = { id = 132107, Voice = '', Text = '这跟原本就有生命的生命体变成魔物是不一样的，国王陛下对于僵尸特别感兴趣，因为他们原本都是这个国家的居民。', Speaker = 2571, Option = '', Action = _EmptyTable,}, 
	[132106] = { id = 132106, Voice = '', Text = '但是他们失去了灵魂，在人世间徘徊留连。', Speaker = 2571, Option = '', Action = _EmptyTable,}, 
	[132105] = { id = 132105, Voice = '', Text = '他们原本都是斐扬居民，希望能够得到永远的安息。', Speaker = 2571, Option = '', Action = _EmptyTable,}, 
	[132104] = { id = 132104, Voice = '', Text = '在密穴中出没的不死系魔物特征是有来由的。', Speaker = 2571, Option = '', Action = _EmptyTable,}, 
	[132103] = { id = 132103, Voice = '', Text = '我负责的部分正好是研究那些不死系魔物的特性。', Speaker = 2571, Option = '', Action = _EmptyTable,}, 
	[132102] = { id = 132102, Voice = '', Text = '再加上村庄正好面对着密穴，在密穴中出没的魔物大多为不死系魔物，所以引起许多学者的关注。', Speaker = 2571, Option = '', Action = _EmptyTable,}, 
	[132101] = { id = 132101, Voice = '', Text = '斐扬因为居处在深山里头，所以常常受到魔物的威胁。', Speaker = 2571, Option = '', Action = _EmptyTable,}, 
	[132099] = { id = 132099, Voice = '', Text = '该死，还有一些试炼马克！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[132098] = { id = 132098, Voice = '', Text = '前面是洛马斯，他好像有麻烦了！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[132097] = { id = 132097, Voice = '', Text = '如果不是你，恐怕我将永远抱有遗憾，为了感谢你，这点小礼物你就收下吧。         ', Speaker = 1329, Option = '谢谢', Action = _EmptyTable,}, 
	[132096] = { id = 132096, Voice = '', Text = '相信我的妻子在众神的怀抱中也一定能够感受到我对她最炙热的爱恋。', Speaker = 1329, Option = '', Action = _EmptyTable,}, 
	[132095] = { id = 132095, Voice = '', Text = '谢谢你帮助我完成了我妻子的遗愿。', Speaker = 1329, Option = '', Action = _EmptyTable,}, 
	[132094] = { id = 132094, Voice = '', Text = '你帮了我这么大的忙我一定要好好谢谢你。', Speaker = 1329, Option = '', Action = _EmptyTable,}, 
	[132093] = { id = 132093, Voice = '', Text = '谢谢你陪着我，冒险者，我先回去了。一会你来找我吧。', Speaker = 1329, Option = '', Action = _EmptyTable,}, 
	[132092] = { id = 132092, Voice = '', Text = '好了，时间快到了。我要离开这里了。', Speaker = 1329, Option = '好的', Action = _EmptyTable,}, 
	[132091] = { id = 132091, Voice = '', Text = '摩天轮将见证着我对你的爱，哪怕你已逝去，我也不离不弃。', Speaker = 1329, Option = '', Action = _EmptyTable,}, 
	[132090] = { id = 132090, Voice = '', Text = '现在我带你来到这里，只是想完成你的遗愿。', Speaker = 1329, Option = '', Action = _EmptyTable,}, 
	[132089] = { id = 132089, Voice = '', Text = '莎莉，你看到了么 ？这就是你一直向往的摩天轮。', Speaker = 1329, Option = '', Action = _EmptyTable,}, 
	[132088] = { id = 132088, Voice = '', Text = '终于能来到这里…冒险者，请帮我[c][ffff00]拍张照片[-][/c]吧。', Speaker = 1329, Option = '', Action = _EmptyTable,}, 
	[132087] = { id = 132087, Voice = '', Text = '你们准备好了么？                       ', Speaker = 1182, Option = '准备好了', Action = _EmptyTable,}, 
	[132086] = { id = 132086, Voice = '', Text = '你们两个都到齐了，现在你们去拍照吧。', Speaker = 1182, Option = '', Action = _EmptyTable,}, 
	[132085] = { id = 132085, Voice = '', Text = '亚尔斯一会就来？好吧，那就等等他。', Speaker = 1182, Option = '', Action = _EmptyTable,}, 
	[132084] = { id = 132084, Voice = '', Text = '冒险者，你在路德维希身边等我吧，我很快就到。', Speaker = 1329, Option = '', Action = _EmptyTable,}, 
	[132083] = { id = 132083, Voice = '', Text = '我…我去拿我妻子的相片…   ', Speaker = 1329, Option = '…好吧', Action = _EmptyTable,}, 
	[132082] = { id = 132082, Voice = '', Text = '请你稍等。。我去准备一下。。。         ', Speaker = 1329, Option = '准备什么', Action = _EmptyTable,}, 
	[132081] = { id = 132081, Voice = '', Text = '真的吗！太好了冒险者。', Speaker = 1329, Option = '', Action = _EmptyTable,}, 
	[132080] = { id = 132080, Voice = '', Text = '真是拿你没办法啊，你去找亚尔斯吧。我负责望风，你们动作快一点！', Speaker = 1182, Option = '', Action = _EmptyTable,}, 
	[132079] = { id = 132079, Voice = '', Text = '冒险者，这点数量可不能弥补我的损失，你还是再找一些来把。', Speaker = 1182, Option = '', Action = _EmptyTable,}, 
	[132078] = { id = 132078, Voice = '', Text = '很好，数量都对了。。。。', Speaker = 1182, Option = '', Action = _EmptyTable,}, 
	[132077] = { id = 132077, Voice = '', Text = '这么快就收集好了？我来看看数量…', Speaker = 1182, Option = '', Action = _EmptyTable,}, 
	[132076] = { id = 132076, Voice = '', Text = '我需要一些[c][ffff00]雪勒结晶[-][/c]来弥补我的损失。', Speaker = 1182, Option = '', Action = _EmptyTable,}, 
	[132075] = { id = 132075, Voice = '', Text = '我冒着被处罚的危险让你拍照，要是被发现我就惨了！', Speaker = 1182, Option = '', Action = _EmptyTable,}, 
	[132074] = { id = 132074, Voice = '', Text = '冒险者接下来这件事情，如果你完成了。你就可以叫上亚尔斯去拍照了。', Speaker = 1182, Option = '', Action = _EmptyTable,}, 
	[132073] = { id = 132073, Voice = '', Text = '这么快？冒险者你真是强大呢。', Speaker = 1182, Option = '', Action = _EmptyTable,}, 
	[132072] = { id = 132072, Voice = '', Text = '在这个森林里，我经常受到[c][ffff00]哥布灵[-][/c]的侵扰，你帮我赶走他们吧。', Speaker = 1182, Option = '小事一桩', Action = _EmptyTable,}, 
	[132071] = { id = 132071, Voice = '', Text = '摩天轮不是相爱的人不能进入，但是在外面拍拍照片还是可以的。你得答应我几件事情，我就允许你在外面拍照~', Speaker = 1182, Option = '', Action = _EmptyTable,}, 
	[132070] = { id = 132070, Voice = '', Text = '原来是这样，好吧冒险者，我承认，亚尔斯对妻子的心意打动了我。', Speaker = 1182, Option = '', Action = _EmptyTable,}, 
	[132069] = { id = 132069, Voice = '', Text = '进入摩天轮需要两个相爱的人才可以，不是恋人是不允许进入的哦！', Speaker = 1182, Option = '叙述经过', Action = _EmptyTable,}, 
	[132068] = { id = 132068, Voice = '', Text = '冒险者，你是想进入摩天轮吗？', Speaker = 1182, Option = '', Action = _EmptyTable,}, 
	[132067] = { id = 132067, Voice = '', Text = '据说有个[c][ffff00]神秘人物[-][/c]一直看守着这个摩天轮，你现在能帮我找到他吗？       ', Speaker = 1329, Option = '好的', Action = _EmptyTable,}, 
	[132066] = { id = 132066, Voice = '', Text = '但是我现在是一个人了，进不去摩天轮了，所以我想在摩天轮外拍个照，告诉我的妻子，我很想念她。', Speaker = 1329, Option = '该怎么做？', Action = _EmptyTable,}, 
	[132065] = { id = 132065, Voice = '', Text = '我的妻子以前很想进去看看，我是协会的魔法师，我总有做不完的事情。', Speaker = 1329, Option = '', Action = _EmptyTable,}, 
	[132064] = { id = 132064, Voice = '', Text = '传说，哥布灵森林深处有个[c][ffff00]摩天轮[-][/c]，只有感情至深的恋人才能进入。', Speaker = 1329, Option = '', Action = _EmptyTable,}, 
	[132063] = { id = 132063, Voice = '', Text = '我的妻子是个很善良的女孩，但是却逝去的太早。', Speaker = 1329, Option = '', Action = _EmptyTable,}, 
	[132062] = { id = 132062, Voice = '', Text = '我没事，只是，我想起了我逝去的妻子。', Speaker = 1329, Option = '', Action = _EmptyTable,}, 
	[132061] = { id = 132061, Voice = '', Text = '哦，冒险者…你找我有什么事情吗？         ', Speaker = 1329, Option = '心情不好？', Action = _EmptyTable,}, 
	[132060] = { id = 132060, Voice = '', Text = '这是你应得的奖励，拿去吧。', Speaker = 1225, Option = '', Action = _EmptyTable,}, 
	[132059] = { id = 132059, Voice = '', Text = '你在外冒险也要小心，这次你帮了这么大的忙，我估计霍格之后还会找你麻烦。', Speaker = 1225, Option = '', Action = _EmptyTable,}, 
	[132058] = { id = 132058, Voice = '', Text = '居然被他跑了！放心冒险者，我们吉芬会全力找到这个家伙的。', Speaker = 1225, Option = '', Action = _EmptyTable,}, 
	[132057] = { id = 132057, Voice = '', Text = '请你把消息带回吉芬，我想我们的任务应该完成了。', Speaker = 1330, Option = '', Action = _EmptyTable,}, 
	[132056] = { id = 132056, Voice = '', Text = '原来是这样，这次多谢你了冒险者。', Speaker = 1330, Option = '', Action = _EmptyTable,}, 
	[132055] = { id = 132055, Voice = '', Text = '拉格要回去接受惩罚了，冒险者。你也离开这里吧。', Speaker = 2580, Option = '', Action = _EmptyTable,}, 
	[132054] = { id = 132054, Voice = '', Text = '可恶，拉格居然让这个家伙跑了！', Speaker = 2580, Option = '', Action = _EmptyTable,}, 
	[132053] = { id = 132053, Voice = '', Text = '不好！他要逃走！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[132052] = { id = 132052, Voice = '', Text = '这次是你们赢了，很快我们就会再见面的，下次见面的时候我不会轻易放过你的！', Speaker = 2581, Option = '', Action = _EmptyTable,}, 
	[132051] = { id = 132051, Voice = '', Text = '嘿嘿嘿…居然被包围了…哼！冒险者，这典籍就先还给你们…', Speaker = 2581, Option = '', Action = _EmptyTable,}, 
	[132050] = { id = 132050, Voice = '', Text = '你就是霍格？快束手就擒，跟我去见国王！', Speaker = 2580, Option = '', Action = _EmptyTable,}, 
	[132049] = { id = 132049, Voice = '', Text = '看来就是那个叫做[c][ffff00]霍格[-][/c]的了。冒险者，你愿意去看一看么？', Speaker = 2580, Option = '好', Action = _EmptyTable,}, 
	[132048] = { id = 132048, Voice = '', Text = '拉格队长，发现一个不明身份的魔法师！', Speaker = 2580, Option = '', Action = _EmptyTable,}, 
	[132047] = { id = 132047, Voice = '', Text = '拉格这就安排，如果一有霍格的消息，我会第一时间通知你！', Speaker = 2580, Option = '', Action = _EmptyTable,}, 
	[132046] = { id = 132046, Voice = '', Text = '族里的学者大人们说的没错！果然是魔法师！', Speaker = 2580, Option = '', Action = _EmptyTable,}, 
	[132045] = { id = 132045, Voice = '', Text = '冒险者你发现了什么消息吗？', Speaker = 2580, Option = '', Action = _EmptyTable,}, 
	[132044] = { id = 132044, Voice = '', Text = '现在你能去找[c][ffff00]拉格[-][/c]吗？把我们吉芬的善意带给哥布灵。', Speaker = 1225, Option = '', Action = _EmptyTable,}, 
	[132043] = { id = 132043, Voice = '', Text = '哼，现在我们吉芬魔法协会正式通缉他。冒险者，如果你遇到他，请把他抓回来。     ', Speaker = 1225, Option = '我尽力', Action = _EmptyTable,}, 
	[132042] = { id = 132042, Voice = '', Text = '霍格曾经涉嫌偷取魔法学院的魔法典籍，被逐出了吉芬，没想到他竟然又做出这种事！', Speaker = 1225, Option = '', Action = _EmptyTable,}, 
	[132041] = { id = 132041, Voice = '', Text = '霍格？？这个叛徒…我当初的决定果然是正确的！  ', Speaker = 1225, Option = '叛徒？', Action = _EmptyTable,}, 
	[132040] = { id = 132040, Voice = '', Text = '冒险者，你回来了？讲讲现在的情况。', Speaker = 1225, Option = '叙述经过', Action = _EmptyTable,}, 
	[132039] = { id = 132039, Voice = '', Text = '可恶！竟然跑了！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[132038] = { id = 132038, Voice = '', Text = '冒险者，该说的我也说了，放我离开这里吧！', Speaker = 2581, Option = '这…', Action = _EmptyTable,}, 
	[132037] = { id = 132037, Voice = '', Text = '就因为魔法典籍是远古传下来的？她甚至把我逐出了魔法学院！所以我决定报复她！', Speaker = 2581, Option = '', Action = _EmptyTable,}, 
	[132036] = { id = 132036, Voice = '', Text = '我是魔法学院的学生，我观看魔法典籍是应该的！她凭什么不给我看？', Speaker = 2581, Option = '', Action = _EmptyTable,}, 
	[132035] = { id = 132035, Voice = '', Text = '古老的魔法典籍是我偷走的，目的是为了报复吉芬魔法协会，报复玛莉亚！', Speaker = 2581, Option = '为什么', Action = _EmptyTable,}, 
	[132034] = { id = 132034, Voice = '', Text = '别…别伤害我！我都说！', Speaker = 2581, Option = '', Action = _EmptyTable,}, 
	[132033] = { id = 132033, Voice = '', Text = '是你！冒险者，我知道你！既然你看到了，你就不用走了！', Speaker = 2581, Option = '', Action = _EmptyTable,}, 
	[132032] = { id = 132032, Voice = '', Text = '什么人？', Speaker = 2581, Option = '', Action = _EmptyTable,}, 
	[132031] = { id = 132031, Voice = '', Text = '这个魔法典籍到底应该藏在哪里呢？或许我应该藏在哥布灵国王身边？', Speaker = 2581, Option = '', Action = _EmptyTable,}, 
	[132030] = { id = 132030, Voice = '', Text = '我想关于这个人的事情，只能交给你去调查了。  ', Speaker = 1330, Option = '好吧', Action = _EmptyTable,}, 
	[132029] = { id = 132029, Voice = '', Text = '难道是他再次潜入了学院偷取了典籍？', Speaker = 1330, Option = '', Action = _EmptyTable,}, 
	[132028] = { id = 132028, Voice = '', Text = '可能是霍格，曾经的霍格就因为偷取魔法典籍被玛莉亚导师逐出了魔法学院。', Speaker = 1330, Option = '', Action = _EmptyTable,}, 
	[132027] = { id = 132027, Voice = '', Text = '什么？不明身份的人物？难道是？？', Speaker = 1330, Option = '是谁？', Action = _EmptyTable,}, 
	[132026] = { id = 132026, Voice = '', Text = '冒险者，如果你看到了这个人记得告诉拉格。拉格会替国王陛下感谢你的帮助。', Speaker = 2580, Option = '', Action = _EmptyTable,}, 
	[132025] = { id = 132025, Voice = '', Text = '他的力量非常强大，拉格听族中的学者说，只有魔法的力量才能做到。', Speaker = 2580, Option = '', Action = _EmptyTable,}, 
	[132024] = { id = 132024, Voice = '', Text = '拉格只知道最近森林里有不明身份的人物出现，在森林中寻找着什么，打扰了我们哥布灵的宁静。', Speaker = 2580, Option = '', Action = _EmptyTable,}, 
	[132023] = { id = 132023, Voice = '', Text = '魔法典籍？拉格不知道这个东西呢……', Speaker = 2580, Option = '', Action = _EmptyTable,}, 
	[132022] = { id = 132022, Voice = '', Text = '别，别打了…你比拉格强大，拉格听你解释……', Speaker = 2580, Option = '', Action = _EmptyTable,}, 
	[132021] = { id = 132021, Voice = '', Text = '来跟拉格打！打赢了拉格，拉格就听你解释！', Speaker = 2580, Option = '', Action = _EmptyTable,}, 
	[132020] = { id = 132020, Voice = '', Text = '不用解释这么多！拉格只看到了你在伤害拉格的士兵！', Speaker = 2580, Option = '', Action = _EmptyTable,}, 
	[132019] = { id = 132019, Voice = '', Text = '你是什么人，竟然敢伤害拉格的士兵？', Speaker = 2580, Option = '', Action = _EmptyTable,}, 
	[132018] = { id = 132018, Voice = '', Text = '不好，有人埋伏在这里！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[132017] = { id = 132017, Voice = '', Text = '冒险者，不如你帮我调查一下吧？', Speaker = 1330, Option = '', Action = _EmptyTable,}, 
	[132016] = { id = 132016, Voice = '', Text = '但是现在典籍的魔力我一点都感受不到。', Speaker = 1330, Option = '', Action = _EmptyTable,}, 
	[132015] = { id = 132015, Voice = '', Text = '那就好，很奇怪，古老的魔法典籍都有强大的魔力，我们魔法师用心感应很快就能感受到典籍的位置。', Speaker = 1330, Option = '', Action = _EmptyTable,}, 
	[132014] = { id = 132014, Voice = '', Text = '怎么样冒险者？消息替我传达到了吗？', Speaker = 1330, Option = '', Action = _EmptyTable,}, 
	[132013] = { id = 132013, Voice = '', Text = '冒险者，现在你能去问问[c][ffff00]伊尔[-][/c]详细的情况吗？', Speaker = 1225, Option = '好的', Action = _EmptyTable,}, 
	[132012] = { id = 132012, Voice = '', Text = '我这里没有收到任何魔法传信！我会去调查一下这些事情的。', Speaker = 1225, Option = '', Action = _EmptyTable,}, 
	[132011] = { id = 132011, Voice = '', Text = '你是说，进入哥布灵森林的魔法小队没有找到典籍的任何消息？', Speaker = 1225, Option = '', Action = _EmptyTable,}, 
	[132010] = { id = 132010, Voice = '', Text = '你好冒险者，你找我有什么事吗？', Speaker = 1225, Option = '', Action = _EmptyTable,}, 
	[132009] = { id = 132009, Voice = '', Text = '冒险者你能够帮我把消息传递给吉芬的[c][ffff00]玛莉亚[-][/c]大魔导师吗？         ', Speaker = 1330, Option = '好的', Action = _EmptyTable,}, 
	[132008] = { id = 132008, Voice = '', Text = '我曾经给魔法协会发过消息请求支援，但是不知道为什么至今都没有消息传来。', Speaker = 1330, Option = '', Action = _EmptyTable,}, 
	[132007] = { id = 132007, Voice = '', Text = '几天前，我们魔法协会图书馆中的重要典籍丢失了。我奉命进入[c][ffff00]哥布灵森林[-][/c]调查典籍的下落，但是却始终没有找到。', Speaker = 1330, Option = '', Action = _EmptyTable,}, 
	[132006] = { id = 132006, Voice = '', Text = '这…这可怎么办啊…            ', Speaker = 1330, Option = '出了什么事', Action = _EmptyTable,}, 
	[132005] = { id = 132005, Voice = '', Text = '难道你不是魔法协会派来支援我的人吗？', Speaker = 1330, Option = '不是', Action = _EmptyTable,}, 
	[132004] = { id = 132004, Voice = '', Text = '冒险者？你是来帮助我的吗？      ', Speaker = 1330, Option = '帮助？', Action = _EmptyTable,}, 
	[132003] = { id = 132003, Voice = '', Text = '这是我的一点心意，请收下吧！', Speaker = 2588, Option = '', Action = _EmptyTable,}, 
	[132002] = { id = 132002, Voice = '', Text = '这…这…这太棒了！多谢你冒险者，没有你的帮助，我想我也得不到这么大的功劳。', Speaker = 2588, Option = '', Action = _EmptyTable,}, 
	[132001] = { id = 132001, Voice = '', Text = '事情已经圆满解决了吧。嘿嘿嘿~这次我功劳不小，不知道会得到什么奖励呢！  ', Speaker = 2588, Option = '告知奖励', Action = _EmptyTable,}, 
	[132000] = { id = 132000, Voice = '', Text = '这次事情的发现者在找你。你去看看他吧，顺便带去协会的嘉奖——允许他进入吉芬学院图书馆进修半个月！', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131999] = { id = 131999, Voice = '', Text = '根据当时黑暗化彩蝶精灵王身上的能量波动来看，没有人能够控制这个魔物，还好你能够解决掉他，否则吉芬不会安宁的。多谢你了。冒险者。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131998] = { id = 131998, Voice = '', Text = '多姆似乎找到了控制这些黑暗化魔物的方式，但是还不成熟。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131997] = { id = 131997, Voice = '', Text = '我知道你想说什么，黑暗化魔法我们已经禁止了，原因就是因为转化元素后的魔物不可控制。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131996] = { id = 131996, Voice = '', Text = '多姆已经得到了应有的惩罚，吉芬总算宁静下来了。             ', Speaker = 1321, Option = '这个魔法...', Action = _EmptyTable,}, 
	[131995] = { id = 131995, Voice = '', Text = '处罚你永世关押吉芬塔地下监牢！多谢你冒险者，不过等会再叙旧，我现在要带走这个罪人！', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131994] = { id = 131994, Voice = '', Text = '哼，吉芬魔法学院高级魔法导师多姆，我现在以无视魔法协会禁令为由对你进行处罚！', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131993] = { id = 131993, Voice = '', Text = '冒险者，你没事吧？看来我没来晚！', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131992] = { id = 131992, Voice = '', Text = '哼，有我在，你不可能碰到它的！', Speaker = 2578, Option = '', Action = _EmptyTable,}, 
	[131991] = { id = 131991, Voice = '', Text = '你！我肯定会粉碎你的阴谋！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131990] = { id = 131990, Voice = '', Text = '不过可惜，已经晚了！魔法咒文我早已吟唱完了，看到了么？这个小家伙就要出来了！', Speaker = 2578, Option = '', Action = _EmptyTable,}, 
	[131989] = { id = 131989, Voice = '', Text = '你的实力还不错嘛，我的[c][ffff00]魔法士兵[-][/c]都没办法解决你。', Speaker = 2578, Option = '', Action = _EmptyTable,}, 
	[131988] = { id = 131988, Voice = '', Text = '上吧！士兵们，干掉这个无知的冒险者！', Speaker = 2578, Option = '', Action = _EmptyTable,}, 
	[131987] = { id = 131987, Voice = '', Text = '我的魔法元素转化理论是多么地正确！到那时，我将成为整个吉芬最伟大的魔法师！', Speaker = 2578, Option = '', Action = _EmptyTable,}, 
	[131986] = { id = 131986, Voice = '', Text = '什么黑暗化不可掌控！我要亲手证明给你们这些腐朽的老家伙看！', Speaker = 2578, Option = '', Action = _EmptyTable,}, 
	[131985] = { id = 131985, Voice = '', Text = '看着吧，当我控制着[c][ffff00]黑暗化彩蝶精灵王[-][/c]出现在你们面前的时候，你们就知道错了！', Speaker = 2578, Option = '', Action = _EmptyTable,}, 
	[131984] = { id = 131984, Voice = '', Text = '我早就想消灭你，只是一直没有机会，没想到你现在却自己送上门来了。', Speaker = 2578, Option = '', Action = _EmptyTable,}, 
	[131983] = { id = 131983, Voice = '', Text = '哼！多管闲事的冒险者，出来吧，我的士兵们！', Speaker = 2578, Option = '', Action = _EmptyTable,}, 
	[131982] = { id = 131982, Voice = '', Text = '多姆，你在做什么？', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131981] = { id = 131981, Voice = '', Text = '不好，他跑了！看样子是往[c][ffff00]哥布灵森林[-][/c]方向逃走了。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131980] = { id = 131980, Voice = '', Text = '这可不是一个好兆头，我要去找魔法协会汇报这件事情。你能够帮我盯住多姆吗？    ', Speaker = 2588, Option = '荣幸之至', Action = _EmptyTable,}, 
	[131979] = { id = 131979, Voice = '', Text = '从那天起，魔法协会就禁止了类似的研究。但是现在，学院里竟然出现了黑暗化魔物。', Speaker = 2588, Option = '', Action = _EmptyTable,}, 
	[131978] = { id = 131978, Voice = '', Text = '结果却发现，无论怎么样，都无法把不死系生物转化为圣光系生物。反倒是圣光系会轻而易举的堕落成不死系生物。', Speaker = 2588, Option = '', Action = _EmptyTable,}, 
	[131977] = { id = 131977, Voice = '', Text = '一开始魔法协会认为这样能够抑制魔物的数量，大量进行实验。', Speaker = 2588, Option = '', Action = _EmptyTable,}, 
	[131976] = { id = 131976, Voice = '', Text = '举个例子，他的理论如果实现，就能够把不死系魔物转化为圣光系生物。', Speaker = 2588, Option = '', Action = _EmptyTable,}, 
	[131975] = { id = 131975, Voice = '', Text = '你一定想不到，他的论文里提出转化魔法元素的属性的可行性。', Speaker = 2588, Option = '', Action = _EmptyTable,}, 
	[131974] = { id = 131974, Voice = '', Text = '在几年前，多姆曾经提交过一次魔法研究报告，而这份报告是关于魔法元素的。', Speaker = 2588, Option = '', Action = _EmptyTable,}, 
	[131973] = { id = 131973, Voice = '', Text = '对了！黑暗化魔物本身就是一个疑点，这肯定与多姆脱不了干系。', Speaker = 2588, Option = '那是什么?', Action = _EmptyTable,}, 
	[131972] = { id = 131972, Voice = '', Text = '又是多姆？黑暗化气息？等等，冒险者，这件事情有点诡异，我需要好好想一想。。。', Speaker = 2588, Option = '', Action = _EmptyTable,}, 
	[131971] = { id = 131971, Voice = '', Text = '看来是你击败了这些魔物，做得很好，我会告知学院你的功绩。现在你可以离开这里了。', Speaker = 2578, Option = '', Action = _EmptyTable,}, 
	[131970] = { id = 131970, Voice = '', Text = '这是……黑暗化生物的气息？！这里是学院，为什么会出现这种东西？', Speaker = 2578, Option = '', Action = _EmptyTable,}, 
	[131969] = { id = 131969, Voice = '', Text = '这里发生了什么事？为什么会有打斗声？嗯？又是你？', Speaker = 2578, Option = '', Action = _EmptyTable,}, 
	[131968] = { id = 131968, Voice = '', Text = '这股黑暗的气息是？！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131967] = { id = 131967, Voice = '', Text = '冒险者，你可以再去探查一次吗？这次小心点，不要被多姆发现了。', Speaker = 2588, Option = '没问题', Action = _EmptyTable,}, 
	[131966] = { id = 131966, Voice = '', Text = '再加上多姆最近总是神神秘秘的，我总觉得很可疑。', Speaker = 2588, Option = '', Action = _EmptyTable,}, 
	[131965] = { id = 131965, Voice = '', Text = '很奇怪，我们学院是开放式的学院，并没有什么陌生人不能靠近吉芬塔的说法。', Speaker = 2588, Option = '', Action = _EmptyTable,}, 
	[131964] = { id = 131964, Voice = '', Text = '多姆是我们学院的教授魔法元素理论的老师。 ', Speaker = 2588, Option = '', Action = _EmptyTable,}, 
	[131963] = { id = 131963, Voice = '', Text = '多姆？原来是他啊…… ', Speaker = 2588, Option = '', Action = _EmptyTable,}, 
	[131962] = { id = 131962, Voice = '', Text = '怎么样冒险者，查到什么消息了吗？', Speaker = 2588, Option = '', Action = _EmptyTable,}, 
	[131961] = { id = 131961, Voice = '', Text = '冒险者，你回来的真快！', Speaker = 2588, Option = '他是？', Action = _EmptyTable,}, 
	[131960] = { id = 131960, Voice = '', Text = '如果没有事情，请你尽快离开这里。', Speaker = 2578, Option = '', Action = _EmptyTable,}, 
	[131959] = { id = 131959, Voice = '', Text = '作为学院的导师，我有义务阻止你来这里。', Speaker = 2578, Option = '', Action = _EmptyTable,}, 
	[131958] = { id = 131958, Voice = '', Text = '冒险者，这里不是你该来的地方，这里是吉芬塔，整个学院最重要的地方。', Speaker = 2578, Option = '', Action = _EmptyTable,}, 
	[131957] = { id = 131957, Voice = '', Text = '你是谁？是学院的学生吗？', Speaker = 2578, Option = '不是', Action = _EmptyTable,}, 
	[131956] = { id = 131956, Voice = '', Text = '什么声音…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131955] = { id = 131955, Voice = '', Text = '(@#￥%&*&%￥@#￥&￥%&%￥……)', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131954] = { id = 131954, Voice = '', Text = '冒险者，你愿意帮我探查一下那片区域吗？        ', Speaker = 2588, Option = '没问题', Action = _EmptyTable,}, 
	[131953] = { id = 131953, Voice = '', Text = '当我走近后我却什么声音都听不到了。所以我觉得那里一定有问题。', Speaker = 2588, Option = '', Action = _EmptyTable,}, 
	[131952] = { id = 131952, Voice = '', Text = '你明白恶魔使人堕落时的低声呢喃吗？没错，就是这种声音。', Speaker = 2588, Option = '', Action = _EmptyTable,}, 
	[131951] = { id = 131951, Voice = '', Text = '但是就在前几天，我在夜间巡逻时，在吉芬塔附近听到了一些不同寻常的声音。', Speaker = 2588, Option = '', Action = _EmptyTable,}, 
	[131950] = { id = 131950, Voice = '', Text = '我们吉芬魔法学院是为了大陆培养高级魔法师的学院，学院的守卫很严密。', Speaker = 2588, Option = '', Action = _EmptyTable,}, 
	[131949] = { id = 131949, Voice = '', Text = '我想请你帮我调查一个…嗯…奇怪的事情。                          ', Speaker = 2588, Option = '什么事情', Action = _EmptyTable,}, 
	[131948] = { id = 131948, Voice = '', Text = '[c][ffff00][PlayerName][-][/c]对吗？你好！我是吉芬魔法学院的守卫。           ', Speaker = 2588, Option = '你好', Action = _EmptyTable,}, 
	[131947] = { id = 131947, Voice = '', Text = '嗯，不管怎么样，多谢你冒险者。', Speaker = 1341, Option = '', Action = _EmptyTable,}, 
	[131946] = { id = 131946, Voice = '', Text = '也许是沉着冷静的心态和严谨学习的态度吧。  ', Speaker = 1341, Option = '想通就好', Action = _EmptyTable,}, 
	[131945] = { id = 131945, Voice = '', Text = '除了强大的魔法实力之外，还应该具备什么？', Speaker = 1341, Option = '', Action = _EmptyTable,}, 
	[131944] = { id = 131944, Voice = '', Text = '什么样的魔导师才是一个合格的魔导师？', Speaker = 1341, Option = '', Action = _EmptyTable,}, 
	[131943] = { id = 131943, Voice = '', Text = '不，我并没有感到沮丧，在回来的路上，我一直在思考。', Speaker = 1341, Option = '', Action = _EmptyTable,}, 
	[131942] = { id = 131942, Voice = '', Text = '冒险者你来了…..', Speaker = 1341, Option = '不要沮丧', Action = _EmptyTable,}, 
	[131941] = { id = 131941, Voice = '', Text = '经过这一次危险也算是让他清醒了。你去看看他吧。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131940] = { id = 131940, Voice = '', Text = '在没有把握的时候冒然尝试，会付出惨痛的代价。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131939] = { id = 131939, Voice = '', Text = '很早我就跟他说过，魔法是这世界上最严谨的学问之一。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131938] = { id = 131938, Voice = '', Text = '他现在情绪很低落，大概是受不了这个打击吧。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131937] = { id = 131937, Voice = '', Text = '辛苦你了冒险者，你再不回来，我就要去找你了呢。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131936] = { id = 131936, Voice = '', Text = '我带着他先离开这里。周围剩下的试炼魔物就请你帮忙清理一下吧。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131935] = { id = 131935, Voice = '', Text = '哼！真是不自量力，居然敢背着我参加魔导师考核！回去再收拾你！', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131934] = { id = 131934, Voice = '', Text = '别紧张！雷藏大人一会就到了。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131933] = { id = 131933, Voice = '', Text = '要死了么….雷藏大人说的没错，我的能力还不能参加魔导师考试。', Speaker = 1341, Option = '', Action = _EmptyTable,}, 
	[131932] = { id = 131932, Voice = '', Text = '冒险者，请去[c][ffff00]海底神殿[-][/c]帮我找到他。我先召唤传送阵送你过去，一会我们再会合！', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131931] = { id = 131931, Voice = '', Text = '他现在的能力还不足以参加魔导师考核，我怕他会遇到危险。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131930] = { id = 131930, Voice = '', Text = '证明自己？他有这个能耐么？魔法是严谨不容一丝差错的。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131929] = { id = 131929, Voice = '', Text = '什么？他竟然敢去进行魔导师考试？ ', Speaker = 1321, Option = '他想证明自己', Action = _EmptyTable,}, 
	[131928] = { id = 131928, Voice = '', Text = '这种程度的魔法也敢来糊弄我！在吉芬，没什么事情能够瞒住我！说吧，那个小家伙去哪里了？ ', Speaker = 1321, Option = '其实是这样的', Action = _EmptyTable,}, 
	[131927] = { id = 131927, Voice = '', Text = '关于...我旁边这个长得和洛马斯一模一样的人偶是怎么回事？！', Speaker = 1321, Option = '这....', Action = _EmptyTable,}, 
	[131926] = { id = 131926, Voice = '', Text = '我最近很好，多谢挂念。如果你没有事情找我的话，那么我倒是想问问你。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131925] = { id = 131925, Voice = '', Text = '嗯？冒险者，你怎么会突然到我这里来？', Speaker = 1321, Option = '来看看你', Action = _EmptyTable,}, 
	[131924] = { id = 131924, Voice = '', Text = '如果雷藏大人识破了假人，那么到时候请帮我拖住雷藏大人，不要让他干扰到我的考试。   ', Speaker = 1341, Option = '这..我尽力', Action = _EmptyTable,}, 
	[131923] = { id = 131923, Voice = '', Text = '魔法人偶会造成我仍然在吉芬的假象，希望能够瞒住雷藏大人。', Speaker = 1341, Option = '', Action = _EmptyTable,}, 
	[131922] = { id = 131922, Voice = '', Text = '这次的考试题目是去海底神殿，我休息一会就要去了。我会留下一个魔法人偶。', Speaker = 1341, Option = '', Action = _EmptyTable,}, 
	[131921] = { id = 131921, Voice = '', Text = '太好了冒险者，有这些我一定能够通过魔法试炼。', Speaker = 1341, Option = '', Action = _EmptyTable,}, 
	[131920] = { id = 131920, Voice = '', Text = '如果方便的话，你能帮我弄点过来吗？      ', Speaker = 1341, Option = '没问题', Action = _EmptyTable,}, 
	[131919] = { id = 131919, Voice = '', Text = '那就好，我要做最后的准备了，我还缺少一些[c][ffff00]人鱼血液[-][/c]用来刻画魔法阵。', Speaker = 1341, Option = '', Action = _EmptyTable,}, 
	[131918] = { id = 131918, Voice = '', Text = '所以冒险者，请千万不要告诉雷藏大人！', Speaker = 1341, Option = '好的', Action = _EmptyTable,}, 
	[131917] = { id = 131917, Voice = '', Text = '在雷藏大人眼里我的能力还不足以担任魔导师这一职称，这也是我迫切地想证明自己的原因。', Speaker = 1341, Option = '', Action = _EmptyTable,}, 
	[131916] = { id = 131916, Voice = '', Text = '其实雷藏大人并不知道我参加了魔导师的考试。如果大人知道了肯定会阻止我的吧。', Speaker = 1341, Option = '为什么', Action = _EmptyTable,}, 
	[131915] = { id = 131915, Voice = '', Text = '这点材料恐怕不太够，冒险者你还是再收集一些来吧。', Speaker = 1341, Option = '', Action = _EmptyTable,}, 
	[131914] = { id = 131914, Voice = '', Text = '这么快就收集好了！不愧是雷藏大人夸奖过的人呢！', Speaker = 1341, Option = '', Action = _EmptyTable,}, 
	[131913] = { id = 131913, Voice = '', Text = '冒险者你回来了？东西收集好了么？', Speaker = 1341, Option = '', Action = _EmptyTable,}, 
	[131912] = { id = 131912, Voice = '', Text = '你能帮我收集一些[c][ffff00]加勒结晶[-][/c]来吗？据说海底洞穴的[c][ffff00]水母[-][/c]身上就有。', Speaker = 1341, Option = '', Action = _EmptyTable,}, 
	[131911] = { id = 131911, Voice = '', Text = '现在我正在尝试通过魔法公会魔导师的考核，但是我还缺少一些考试用的材料。', Speaker = 1341, Option = '', Action = _EmptyTable,}, 
	[131910] = { id = 131910, Voice = '', Text = '我很久之前就曾经听雷藏大人谈起你呢，说你是王国的不可多得的人才呢。', Speaker = 1341, Option = '', Action = _EmptyTable,}, 
	[131909] = { id = 131909, Voice = '', Text = '嘿~冒险者，你就是[c][ffff00][PlayerName][-][/c]吧？', Speaker = 1341, Option = '', Action = _EmptyTable,}, 
	[131908] = { id = 131908, Voice = '', Text = '就差最后一件啦，我还缺一些友情之证，冒险者帮帮忙啦。', Speaker = 2157, Option = '好的', Action = _EmptyTable,}, 
	[131907] = { id = 131907, Voice = '', Text = '除了雪勒结晶，我还需要一些不死心脏，冒险者你能帮我弄来吗？', Speaker = 2157, Option = '没问题', Action = _EmptyTable,}, 
	[131906] = { id = 131906, Voice = '', Text = '光粒和星星的角你都能这么快找过来，那接下来我需要的零件也请你帮帮忙啦。', Speaker = 2157, Option = '好的', Action = _EmptyTable,}, 
	[131905] = { id = 131905, Voice = '', Text = '嘻嘻，除了光粒我还需要一些星星的角，我想这对你来说不是很困难吧？', Speaker = 2157, Option = '没问题', Action = _EmptyTable,}, 
	[131904] = { id = 131904, Voice = '', Text = '嘻嘻，冒险者，你身上的莫拉硬币不太够呢……', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131903] = { id = 131903, Voice = '', Text = '以后你就可以在我这里进行高级附魔啦~', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131902] = { id = 131902, Voice = '', Text = '交易完成~~哈哈，冒险者，你一定会享受到高级附魔所带来的种种好处的。', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131901] = { id = 131901, Voice = '', Text = '嘻嘻嘻…不过分吧？你只要给我10个莫拉硬币就可以了~', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131900] = { id = 131900, Voice = '', Text = '不过给你高级附魔的力量对我的消耗很大，所以我需要一些莫拉硬币作为补偿….', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131899] = { id = 131899, Voice = '', Text = '既然你完成了我的考验，那么我也会如你所愿，给你高级附魔的力量。', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131898] = { id = 131898, Voice = '', Text = '天啊，竟然这么快就回来了！看来我还是小瞧你了呢。', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131897] = { id = 131897, Voice = '', Text = '那接下来的考验就是击败一次斐扬南部的幽影之卡利斯格！完成了考验再来找我吧！', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131896] = { id = 131896, Voice = '', Text = '想要得到高级附魔的力量，你需要有与高级附魔相匹配的武力呢。', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131895] = { id = 131895, Voice = '', Text = '你可真厉害，看来这点考验难不住你了呢。', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131894] = { id = 131894, Voice = '', Text = '咦，我缺少的材料可不止这么多呢。你还是在找一些来吧。', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131893] = { id = 131893, Voice = '', Text = '数量都对了呢，干的不错冒险者。', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131892] = { id = 131892, Voice = '', Text = '呀~冒险者你这么快就回来了，让我看看材料吧~', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131891] = { id = 131891, Voice = '', Text = '我需要一点光粒，冒险者快去找吧。', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131890] = { id = 131890, Voice = '', Text = '这些材料相当稀少，如果你能够帮我的话，我就帮你进行高级附魔哦~', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131889] = { id = 131889, Voice = '', Text = '要进行高级附魔的话，首先我需要一些材料。', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131888] = { id = 131888, Voice = '', Text = '冒险者，你想进行高级附魔嘛~ ', Speaker = 2157, Option = '当然想', Action = _EmptyTable,}, 
	[131887] = { id = 131887, Voice = '', Text = '甚至，你还可以对自己的饰品进行附魔呢。是不是很棒？', Speaker = 2157, Option = '这么好', Action = _EmptyTable,}, 
	[131886] = { id = 131886, Voice = '', Text = '是的，高级附魔哦~~你可以更大程度上的提升自己的属性。', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131885] = { id = 131885, Voice = '', Text = '我现在觉得凭你的实力可以进行高级附魔了。 ', Speaker = 2157, Option = '高级附魔？', Action = _EmptyTable,}, 
	[131884] = { id = 131884, Voice = '', Text = '喔呀~~冒险者，你真的是越来越强大了呢。', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131883] = { id = 131883, Voice = '', Text = '嘻嘻，冒险者，你身上的莫拉硬币不太够呢……', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131882] = { id = 131882, Voice = '', Text = '以后你就可以在我这里进行中级附魔啦~', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131881] = { id = 131881, Voice = '', Text = '交易完成~~哈哈，冒险者，你一定不会后悔的~', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131880] = { id = 131880, Voice = '', Text = '你再给我5个莫拉硬币作为开启中级附魔的辛苦费，我就对你开放中级附魔哦~', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131879] = { id = 131879, Voice = '', Text = '那么冒险者，你成功完成了我的考验哦~', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131878] = { id = 131878, Voice = '', Text = '咦，我缺少的材料可不止这么多呢。你还是在找一些来吧。', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131877] = { id = 131877, Voice = '', Text = '数量都对了呢，干的不错冒险者。', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131876] = { id = 131876, Voice = '', Text = '呀~冒险者你这么快就回来了，让我看看材料吧~', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131875] = { id = 131875, Voice = '', Text = '如果可以的话，你能帮我找一些雪勒结晶吗？', Speaker = 2157, Option = '没问题', Action = _EmptyTable,}, 
	[131874] = { id = 131874, Voice = '', Text = '唔，我可以帮你哦~不过我最近需要一些材料，我实在没有精力去找。', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131873] = { id = 131873, Voice = '', Text = '冒险家，想不想进行中级附魔呢？', Speaker = 2157, Option = '当然想', Action = _EmptyTable,}, 
	[131872] = { id = 131872, Voice = '', Text = '哦呀~冒险者你回来了？看来你已经了解中级附魔的事情了呢。', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131871] = { id = 131871, Voice = '', Text = '我建议你还是回去找猫小友吧，毕竟她才是专业的附魔师呢。', Speaker = 1552, Option = '', Action = _EmptyTable,}, 
	[131870] = { id = 131870, Voice = '', Text = '唔，该解答的事情我也解答了，如果你现在就想得到中级附魔的力量的话。', Speaker = 1552, Option = '', Action = _EmptyTable,}, 
	[131869] = { id = 131869, Voice = '', Text = '不过我听说高级附魔的条件十分苛刻，所以冒险者，你还是先得到中级附魔的力量比较好。', Speaker = 1552, Option = '', Action = _EmptyTable,}, 
	[131868] = { id = 131868, Voice = '', Text = '而高级的附魔甚至可以强化你的饰品，使你的全身属性有飞跃般的成长。', Speaker = 1552, Option = '', Action = _EmptyTable,}, 
	[131867] = { id = 131867, Voice = '', Text = '中级的附魔可以提高附魔属性的条数和上限，如果你想变的更加强大的话，中级附魔也是一个不错的选择。', Speaker = 1552, Option = '', Action = _EmptyTable,}, 
	[131866] = { id = 131866, Voice = '', Text = '原来是附魔的事情啊，这个你可就问对人咯。', Speaker = 1552, Option = '', Action = _EmptyTable,}, 
	[131865] = { id = 131865, Voice = '', Text = '哟~冒险者，你好像很久没来找我啦！', Speaker = 1552, Option = '', Action = _EmptyTable,}, 
	[131864] = { id = 131864, Voice = '', Text = '不过我想你可以去找哈沃德，他应该会知道具体附魔效果。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131863] = { id = 131863, Voice = '', Text = '高级附魔后的武器甚至可以比拟众神使用的武器。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131862] = { id = 131862, Voice = '', Text = '而中级、高级的附魔在一定程度上借用了众神的力量，使得武器在初级附魔的基础上更加强悍。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131861] = { id = 131861, Voice = '', Text = '附魔分为初级附魔，中级附魔还有高级附魔，不同等级的附魔会有不同的效果。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131860] = { id = 131860, Voice = '', Text = '根据古籍上的记载，附魔是众神创造出来，为了让人们得到更加强大力量的一种秘法。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131859] = { id = 131859, Voice = '', Text = '关于附魔，我倒是查到了一些资料。。', Speaker = 1321, Option = '什么资料', Action = _EmptyTable,}, 
	[131858] = { id = 131858, Voice = '', Text = '这么快就回来了？冒险者你的实力进步很快啊！', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131857] = { id = 131857, Voice = '', Text = '他们的数量又开始增长了，再这么下去，恐怕会对吉芬产生威胁。', Speaker = 1321, Option = '没问题', Action = _EmptyTable,}, 
	[131856] = { id = 131856, Voice = '', Text = '在此期间，你能帮我清理一下吉芬附近的哥布灵吗？', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131855] = { id = 131855, Voice = '', Text = '附魔啊…唔，吉芬的图书馆中有关于这个的资料，我可以为你去查一查。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131854] = { id = 131854, Voice = '', Text = '冒险者，你来了。这次有什么事情吗？', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131853] = { id = 131853, Voice = '', Text = '想要了解更多的话，就去找吉芬的雷藏吧！', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131852] = { id = 131852, Voice = '', Text = '是啊，你永远无法想象附魔的力量…..嘻嘻嘻……', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131851] = { id = 131851, Voice = '', Text = '看来初级的附魔已经不能满足你的需要了吧？嘿嘿嘿……', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131850] = { id = 131850, Voice = '', Text = '嘿，冒险者你很不错，想不到你已经成长得这么厉害了。', Speaker = 2157, Option = '', Action = _EmptyTable,}, 
	[131431] = { id = 131431, Voice = '', Text = '这些材料可能不太够。', Speaker = 1067, Option = '', Action = _EmptyTable,}, 
	[131430] = { id = 131430, Voice = '', Text = '放心，我们都会支持你的，冒险者，加油吧！', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131429] = { id = 131429, Voice = '', Text = '和平来之不易，但是总有一些人想尽办法破坏这种和平，冒险者，你的使命很重啊。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131428] = { id = 131428, Voice = '', Text = '太可恶了，怎么能够这样！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131427] = { id = 131427, Voice = '', Text = '我觉得应该是某个邪恶的魔法师施加了魔法，而这个魔法师，是人类。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131426] = { id = 131426, Voice = '', Text = '嗯，我觉得应该是众神预言到了海底的变化，知道海神幻影会被污染，至于为什么被污染……', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131425] = { id = 131425, Voice = '', Text = '你知道了什么嘛？', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131424] = { id = 131424, Voice = '', Text = '污染的海神幻影…原来是这样…', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131423] = { id = 131423, Voice = '', Text = '这…好吧，你一定要来找我玩呀！', Speaker = 1163, Option = '', Action = _EmptyTable,}, 
	[131422] = { id = 131422, Voice = '', Text = '不客气，卢西娅，虽然我很想跟你聊聊天，可惜我还有急事，以后再见吧。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131421] = { id = 131421, Voice = '', Text = '你…你太厉害了！你又帮助了我一次。', Speaker = 1163, Option = '', Action = _EmptyTable,}, 
	[131420] = { id = 131420, Voice = '', Text = '啊……杀！杀！！杀！！！', Speaker = 2523, Option = '', Action = _EmptyTable,}, 
	[131419] = { id = 131419, Voice = '', Text = '石碑周围好像有什么线索……', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131418] = { id = 131418, Voice = '', Text = '要知道我们人鱼是海神的血脉，那里肯定出现了什么异常，你能帮我去看看吗？', Speaker = 1163, Option = '', Action = _EmptyTable,}, 
	[131417] = { id = 131417, Voice = '', Text = '在另一座岛上，我感应到了一些不同寻常的气息。', Speaker = 1163, Option = '', Action = _EmptyTable,}, 
	[131416] = { id = 131416, Voice = '', Text = '你回来啦？', Speaker = 1163, Option = '', Action = _EmptyTable,}, 
	[131415] = { id = 131415, Voice = '', Text = '一些凶猛的魔物出现了……', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131414] = { id = 131414, Voice = '', Text = '可以得话你能帮我赶走他们吗？', Speaker = 1163, Option = '', Action = _EmptyTable,}, 
	[131413] = { id = 131413, Voice = '', Text = '很多在海底的人类和善良的人鱼们都受到了袭击。', Speaker = 1163, Option = '', Action = _EmptyTable,}, 
	[131412] = { id = 131412, Voice = '', Text = '就在海底洞穴的第二层，出现了很多莫名其妙的魔物，我现在都不敢过去玩耍了呢。', Speaker = 1163, Option = '', Action = _EmptyTable,}, 
	[131411] = { id = 131411, Voice = '', Text = '海底发生了什么？', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131410] = { id = 131410, Voice = '', Text = '我很好，只是海底最近有点不平静。', Speaker = 1163, Option = '', Action = _EmptyTable,}, 
	[131409] = { id = 131409, Voice = '', Text = '啊，卢西娅，好久不见，你最近还好吗？', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131408] = { id = 131408, Voice = '', Text = '你…你怎么会在这里呀？', Speaker = 1163, Option = '', Action = _EmptyTable,}, 
	[131407] = { id = 131407, Voice = '', Text = '大概是这样…现在你要去海底看看，确认一下是不是这样。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131406] = { id = 131406, Voice = '', Text = '事情已经逐渐明朗化了，我估计，应该是海神在海底封印了一些被魔法污染的人鱼，残害了很多的人类。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131405] = { id = 131405, Voice = '', Text = '再加上我解读出的魔法和海神。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131404] = { id = 131404, Voice = '', Text = '这一段…这一段…分别代表的意思是：人类，人鱼和污染。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131403] = { id = 131403, Voice = '', Text = '对了，他们已经把消息传过来了。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131402] = { id = 131402, Voice = '', Text = '这点材料恐怕不太够~', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131401] = { id = 131401, Voice = '', Text = '很好，数目都对了~', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131400] = { id = 131400, Voice = '', Text = '东西收集好了吗？', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131399] = { id = 131399, Voice = '', Text = '我现在需要一些材料。嗯…一些杰勒比结晶，柔毛还有腐烂绷带。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131398] = { id = 131398, Voice = '', Text = '但是冒险者，我们前期得做一些准备，估计这次的事情不简单。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131397] = { id = 131397, Voice = '', Text = '他这么说，一定有他的道理。这样吧，我来写魔法信件询问他们。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131396] = { id = 131396, Voice = '', Text = '他让我找史芬斯，史丹利，希盖伊兹和你来辨认这个羊皮纸，哦，这个是在海底洞穴发现的。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131395] = { id = 131395, Voice = '', Text = '那个旅行者有跟你说什么消息吗？', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131394] = { id = 131394, Voice = '', Text = '魔法，海神？这两者有什么联系呢？', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131393] = { id = 131393, Voice = '', Text = '这，说不准，要知道魔法是最不能按常理判断的东西。如果一有消息，我派人告诉你的。', Speaker = 1321, Option = '好吧…', Action = _EmptyTable,}, 
	[131392] = { id = 131392, Voice = '', Text = '好的，雷藏。不过研究出来会有多久？', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131391] = { id = 131391, Voice = '', Text = '太感谢你了冒险者，如果魔法被我研究出来，那么我会第一个给你看。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131390] = { id = 131390, Voice = '', Text = '太好了，多谢你冒险者。有了这个….一个未知的魔法要在我手中诞生…..', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131389] = { id = 131389, Voice = '', Text = '人鱼的血恐怕能与海神的魔法能产生共鸣，你能帮我收集一些来吗？', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131388] = { id = 131388, Voice = '', Text = '还差一些人鱼的血液。据说海神也是人鱼族的呢。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131387] = { id = 131387, Voice = '', Text = '这点加勒结晶恐怕不够，你还是再找一些来吧。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131386] = { id = 131386, Voice = '', Text = '很好，这就是我要的加勒结晶。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131385] = { id = 131385, Voice = '', Text = '我要的东西呢？', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131384] = { id = 131384, Voice = '', Text = '你能帮我找一些加勒结晶嘛？我想我需要这个来进行我的研究。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131383] = { id = 131383, Voice = '', Text = '是啊，说不定，这是海神冕下留下的魔法，我得研究研究。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131382] = { id = 131382, Voice = '', Text = '我看看上面写着什么…这是“魔法”,这个是….“海神”？', Speaker = 1321, Option = '{魔法,60},{海神,70}', Action = _EmptyTable,}, 
	[131381] = { id = 131381, Voice = '', Text = '这，这是众神的文字啊！要知道众神的文字可是充满了无限的魔力。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131380] = { id = 131380, Voice = '', Text = '神秘的羊皮纸？我看看。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131379] = { id = 131379, Voice = '', Text = '冒险者，找我有什么事情。我还要研究魔法呢。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[131378] = { id = 131378, Voice = '', Text = '说起来也是应该我谢你才对。谢谢你，冒险者。', Speaker = 1067, Option = '', Action = _EmptyTable,}, 
	[131377] = { id = 131377, Voice = '', Text = '不用谢我，要知道众神的文字对学者的诱惑是无与伦比的。', Speaker = 1067, Option = '', Action = _EmptyTable,}, 
	[131376] = { id = 131376, Voice = '', Text = '看看你种下的小树有没有成长起来。哈哈哈哈！', Speaker = 1067, Option = '', Action = _EmptyTable,}, 
	[131375] = { id = 131375, Voice = '', Text = '唔，众神的意思果然是指沙漠之灾吗？冒险者有空去梦罗克看看吧。', Speaker = 1067, Option = '', Action = _EmptyTable,}, 
	[131374] = { id = 131374, Voice = '', Text = '冒险者，你回来了？', Speaker = 1067, Option = '', Action = _EmptyTable,}, 
	[131373] = { id = 131373, Voice = '', Text = '干得不错，冒险者，谢谢你，我想有你们在，这个灾难肯定是不会发生的。', Speaker = 1554, Option = '', Action = _EmptyTable,}, 
	[131372] = { id = 131372, Voice = '', Text = '愿这些小生命能够茁壮成长为参天大树……', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131371] = { id = 131371, Voice = '', Text = '这些都是非常耐旱得植物，就算不去管它，它也不会那么容易死。', Speaker = 1554, Option = '', Action = _EmptyTable,}, 
	[131370] = { id = 131370, Voice = '', Text = '这些种子让成为阻止沙漠蔓延的最大功臣！', Speaker = 1554, Option = '', Action = _EmptyTable,}, 
	[131369] = { id = 131369, Voice = '', Text = '现在我给你一些种子，请你到梦罗克城外的沙漠中掩埋。', Speaker = 1554, Option = '', Action = _EmptyTable,}, 
	[131368] = { id = 131368, Voice = '', Text = '干得好冒险者，梦罗克会记住你的！', Speaker = 1554, Option = '', Action = _EmptyTable,}, 
	[131367] = { id = 131367, Voice = '', Text = '我们总得做点什么，你能清除一些沙漠之狼嘛？有他们在我们总是不能好好的保护环境。', Speaker = 1554, Option = '', Action = _EmptyTable,}, 
	[131366] = { id = 131366, Voice = '', Text = '是啊，沙漠越来越大了，总有一天会蔓延到普隆德拉吧。', Speaker = 1554, Option = '', Action = _EmptyTable,}, 
	[131365] = { id = 131365, Voice = '', Text = '原来是你啊，冒险者，有什么事吗？', Speaker = 1554, Option = '', Action = _EmptyTable,}, 
	[131364] = { id = 131364, Voice = '', Text = '我觉得你应该去问问梦罗克城的人，也许他们有什么线索。', Speaker = 1067, Option = '', Action = _EmptyTable,}, 
	[131363] = { id = 131363, Voice = '', Text = '难道人类要迎来一次大的灾难吗？我听说梦罗克的沙漠已经开始向普隆德拉蔓延了。', Speaker = 1067, Option = '', Action = _EmptyTable,}, 
	[131362] = { id = 131362, Voice = '', Text = '诺，这里是“人类”的意思，我想众神是想说明什么吧？', Speaker = 1067, Option = '', Action = _EmptyTable,}, 
	[131361] = { id = 131361, Voice = '', Text = '唔，众神的文字？这个我要研究研究，不过我不敢保证能研究出什么。', Speaker = 1067, Option = '', Action = _EmptyTable,}, 
	[131360] = { id = 131360, Voice = '', Text = '啊，好久不见冒险者，你来找我有什么事情吗？', Speaker = 1067, Option = '', Action = _EmptyTable,}, 
	[131359] = { id = 131359, Voice = '', Text = '不用谢我不用谢我，我也没有做什么。如果遇到难题，记得来找我。我会尽力帮你的。', Speaker = 1067, Option = '', Action = _EmptyTable,}, 
	[131358] = { id = 131358, Voice = '', Text = '也许众神只是想说明人类是一种弱小而又缺乏安全感的种族呢。', Speaker = 1067, Option = '', Action = _EmptyTable,}, 
	[131357] = { id = 131357, Voice = '', Text = '怎么样，有什么线索吗？', Speaker = 1067, Option = '', Action = _EmptyTable,}, 
	[131356] = { id = 131356, Voice = '', Text = '我还要安排安抚的物品，就不多留你了。', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
	[131355] = { id = 131355, Voice = '', Text = '除此之外还有什么异常？这个…好像没有什么异常了。', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
	[131354] = { id = 131354, Voice = '', Text = '很好，冒险者。谢谢你所做的一切。王国会记住你们的。', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
	[131353] = { id = 131353, Voice = '', Text = '冒险者，你回来了？那些该死的蜈蚣怎么样了？', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
	[131352] = { id = 131352, Voice = '', Text = '如果可以的话，你可以去看看。顺便清理一下那里的艾吉松蜈蚣。', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
	[131351] = { id = 131351, Voice = '', Text = '最近那里的魔物暴动的厉害。妙勒尼的人们都惧怕万分，不敢进入山脉了。', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
	[131350] = { id = 131350, Voice = '', Text = '嗯？最近有什么异常？要说最近的异常，估计就是妙勒尼山脉吧。', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
	[131349] = { id = 131349, Voice = '', Text = '关于这点，我觉得你应该拜访一下我们尊敬的卢恩内务官。', Speaker = 1067, Option = '', Action = _EmptyTable,}, 
	[131348] = { id = 131348, Voice = '', Text = '唔，我也不能全认识，我只认识这一段…到这一段…这是“人类”的意思。', Speaker = 1067, Option = '', Action = _EmptyTable,}, 
	[131347] = { id = 131347, Voice = '', Text = '这个事上古时期的纸张，诸神记事所用的纸张，难得啊，居然保存到现在。', Speaker = 1067, Option = '', Action = _EmptyTable,}, 
	[131346] = { id = 131346, Voice = '', Text = '羊皮纸？我看看…', Speaker = 1067, Option = '', Action = _EmptyTable,}, 
	[131345] = { id = 131345, Voice = '', Text = '魔王的事情我已经清楚了，这次来是想让你看看这个羊皮纸。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131344] = { id = 131344, Voice = '', Text = '那么，你来找我有事吗？', Speaker = 1067, Option = '', Action = _EmptyTable,}, 
	[131343] = { id = 131343, Voice = '', Text = '啊，谢谢你冒险者，这些材料来得真是时候。', Speaker = 1067, Option = '', Action = _EmptyTable,}, 
	[131342] = { id = 131342, Voice = '', Text = '年轻的冒险者，你这次来找我是为了魔王的事情吗？', Speaker = 1067, Option = '', Action = _EmptyTable,}, 
	[131341] = { id = 131341, Voice = '', Text = '对了，带点亡者遗物给他，他最近好像急需这种材料。', Speaker = 1518, Option = '', Action = _EmptyTable,}, 
	[131340] = { id = 131340, Voice = '', Text = '也许他能从这张羊皮卷中看到不同的内容。', Speaker = 1518, Option = '', Action = _EmptyTable,}, 
	[131339] = { id = 131339, Voice = '', Text = '你可以试着找史芬斯，要知道他也是很有名的大学者呢。', Speaker = 1518, Option = '', Action = _EmptyTable,}, 
	[131338] = { id = 131338, Voice = '', Text = '不用谢我，我应该谢谢你才对，冒险者。如果我研究出来什么，会第一时间告诉你的。', Speaker = 1518, Option = '', Action = _EmptyTable,}, 
	[131337] = { id = 131337, Voice = '', Text = '多亏你啦，我会试着研究一下污染物与这个羊皮纸的联系的。', Speaker = 1518, Option = '', Action = _EmptyTable,}, 
	[131336] = { id = 131336, Voice = '', Text = '冒险者你回来啦，污染物呢？', Speaker = 1518, Option = '', Action = _EmptyTable,}, 
	[131335] = { id = 131335, Voice = '', Text = '这些玛勒盗虫身上总是充斥着污染物。可以的话带点回来，我可以试着研究一下。', Speaker = 1518, Option = '', Action = _EmptyTable,}, 
	[131334] = { id = 131334, Voice = '', Text = '唔，既然是污染的话，我觉得你可以去找找那些玛勒盗虫看看。', Speaker = 1518, Option = '', Action = _EmptyTable,}, 
	[131333] = { id = 131333, Voice = '', Text = '不过我大致推断了一下，也许是污染对魔物有什么影响也说不定。', Speaker = 1518, Option = '{前往下水道,40},{其他线索,50}', Action = _EmptyTable,}, 
	[131332] = { id = 131332, Voice = '', Text = '我也只认出了污染两个字。不愧是众神的语言，真是优美啊。', Speaker = 1518, Option = '', Action = _EmptyTable,}, 
	[131331] = { id = 131331, Voice = '', Text = '这….其实众神的语言我认识的也不多。', Speaker = 1518, Option = '', Action = _EmptyTable,}, 
	[131330] = { id = 131330, Voice = '', Text = '原来是这样，我来看看。', Speaker = 1518, Option = '', Action = _EmptyTable,}, 
	[131329] = { id = 131329, Voice = '', Text = '啊，好久不见冒险者…..你手中的羊皮纸是？', Speaker = 1518, Option = '', Action = _EmptyTable,}, 
	[131328] = { id = 131328, Voice = '', Text = '总之能解开这个谜团真是太好了。', Speaker = 1518, Option = '', Action = _EmptyTable,}, 
	[131327] = { id = 131327, Voice = '', Text = '这个羊皮纸难道是想告诉我们污染的危害么….这倒是很合情合理。', Speaker = 1518, Option = '', Action = _EmptyTable,}, 
	[131326] = { id = 131326, Voice = '', Text = '难道是污染是怪物发生异变？这倒是一个好的研究课题。。。。', Speaker = 1518, Option = '', Action = _EmptyTable,}, 
	[131325] = { id = 131325, Voice = '', Text = '食腐盗虫和食腐白鼠比一般的怪物更加强大嘛？', Speaker = 1518, Option = '', Action = _EmptyTable,}, 
	[131324] = { id = 131324, Voice = '', Text = '好像有什么魔物正在向你靠近……', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131323] = { id = 131323, Voice = '', Text = '我觉得你可以去下水道碰碰运气，也许会有什么线索。', Speaker = 1518, Option = '', Action = _EmptyTable,}, 
	[131322] = { id = 131322, Voice = '', Text = '普隆德拉的下水道是通往海里的吧？如果是污染的话，肯定与下水道脱不了干系。', Speaker = 1518, Option = '', Action = _EmptyTable,}, 
	[131321] = { id = 131321, Voice = '', Text = '众神的文字缺失太多，我也不能认全，不过，我倒是看出了“污染”两个字。', Speaker = 1518, Option = '', Action = _EmptyTable,}, 
	[131320] = { id = 131320, Voice = '', Text = '根据希盖伊兹的说法应该是海底了，但是我却解读出不同的东西。', Speaker = 1518, Option = '', Action = _EmptyTable,}, 
	[131319] = { id = 131319, Voice = '', Text = '羊皮纸？我看看…..', Speaker = 1518, Option = '', Action = _EmptyTable,}, 
	[131318] = { id = 131318, Voice = '', Text = '冒险者啊，是你啊，找我有事吗？', Speaker = 1518, Option = '', Action = _EmptyTable,}, 
	[131317] = { id = 131317, Voice = '', Text = '好的，如果有消息也请告诉我，我也很有兴趣呢。', Speaker = 1024, Option = '', Action = _EmptyTable,}, 
	[131316] = { id = 131316, Voice = '', Text = '我总觉得事情没有这么简单，我去问问史丹利吧，也许他有什么线索。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131315] = { id = 131315, Voice = '', Text = '总之，你能够安全回来真是太好了。', Speaker = 1024, Option = '', Action = _EmptyTable,}, 
	[131314] = { id = 131314, Voice = '', Text = '远古人鱼？唔，可能这卷羊皮纸就记载了这个远古人鱼的封印地点，不知道是谁制作的这张羊皮纸。', Speaker = 1024, Option = '', Action = _EmptyTable,}, 
	[131313] = { id = 131313, Voice = '', Text = '冒险者，你回来了？探查的结果怎么样？', Speaker = 1024, Option = '', Action = _EmptyTable,}, 
	[131312] = { id = 131312, Voice = '', Text = '是谁这么放肆，竟敢打扰我的沉眠？', Speaker = 2522, Option = '', Action = _EmptyTable,}, 
	[131311] = { id = 131311, Voice = '', Text = '既然这样，你就去海底看看吧。', Speaker = 1024, Option = '', Action = _EmptyTable,}, 
	[131310] = { id = 131310, Voice = '', Text = '人鱼？可能跟海底有关，我去海底探查一番吧。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131309] = { id = 131309, Voice = '', Text = '唔…这个大概是讲一个人鱼的故事吧。我只认识上面的“人鱼”两个字而已。', Speaker = 1024, Option = '{前往海底,20},{其他线索,30}', Action = _EmptyTable,}, 
	[131308] = { id = 131308, Voice = '', Text = '这种上古之物居然在你手中，冒险者你的运气真是好呢。', Speaker = 1024, Option = '', Action = _EmptyTable,}, 
	[131307] = { id = 131307, Voice = '', Text = '这羊皮卷上的文字是古老的众神的文字，我也认识的不是很多。', Speaker = 1024, Option = '', Action = _EmptyTable,}, 
	[131306] = { id = 131306, Voice = '', Text = '让我看看。', Speaker = 1024, Option = '', Action = _EmptyTable,}, 
	[131305] = { id = 131305, Voice = '', Text = '冒险者，你找我有什么事情吗？', Speaker = 1024, Option = '', Action = _EmptyTable,}, 
	[131304] = { id = 131304, Voice = '', Text = '现在你需要找一个能认识这种文字的人，据我所知，冒险者公会的高级导师希盖伊兹，学者史丹利，学者史芬斯以及魔法公会的雷藏大人都认识这种文字，你可以选择求助他们。', Speaker = 1391, Option = '{希盖伊兹,3},{史丹利,4}{史芬斯,5}{雷藏,6}', Action = _EmptyTable,}, 
	[131303] = { id = 131303, Voice = '', Text = '但是我看不懂这些文字，我觉得这些肯定关乎着一些秘密，说不定是藏宝图呢！', Speaker = 1391, Option = '', Action = _EmptyTable,}, 
	[131302] = { id = 131302, Voice = '', Text = '现在的年轻人真没耐心，好吧。这是我在海底洞穴里的一个石碑上发现的。', Speaker = 1391, Option = '', Action = _EmptyTable,}, 
	[131301] = { id = 131301, Voice = '', Text = '这么说，你哪儿会帮助我啊？别这么小气冒险者。要说起得到这件物品的由来，那可就是一个非常长的故事了……', Speaker = 1391, Option = '长话短说', Action = _EmptyTable,}, 
	[131300] = { id = 131300, Voice = '', Text = '你不知道什么东西你就说这是个秘密？', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131299] = { id = 131299, Voice = '', Text = '唔，冒险者，这是你奖励。老实说，我也不知道这是什么东西。', Speaker = 1391, Option = '', Action = _EmptyTable,}, 
	[131298] = { id = 131298, Voice = '', Text = '冒险者，你可帮了我大忙了，这下我能吃顿饱饭了。', Speaker = 1391, Option = '', Action = _EmptyTable,}, 
	[131297] = { id = 131297, Voice = '', Text = '这样啊，那好吧，如果你改变注意了记得来找我。', Speaker = 1391, Option = '', Action = _EmptyTable,}, 
	[131296] = { id = 131296, Voice = '', Text = '我希望你能帮助我消灭一些沙漠之狼，取得他们的皮毛，我想换点钱。', Speaker = 1391, Option = '{好的,2},{拒绝,999}', Action = _EmptyTable,}, 
	[131295] = { id = 131295, Voice = '', Text = '要知道，旅行可是很花钱的，导致我现在已经穷得叮当响了。', Speaker = 1391, Option = '', Action = _EmptyTable,}, 
	[131294] = { id = 131294, Voice = '', Text = '从普隆德拉到依斯鲁得，从海底洞穴到蚂蚁洞穴，我旅行过的地方太多太多了。', Speaker = 1391, Option = '', Action = _EmptyTable,}, 
	[131293] = { id = 131293, Voice = '', Text = '放心，我肯定不会让你陷入危险之中，我是说，这个忙并不复杂。', Speaker = 1391, Option = '', Action = _EmptyTable,}, 
	[131292] = { id = 131292, Voice = '', Text = '嘿，冒险者，愿意帮助我这个可怜的旅行者一个小忙吗？作为报酬，我会告诉你一个秘密~', Speaker = 1391, Option = '', Action = _EmptyTable,}, 
	[131274] = { id = 131274, Voice = '', Text = '由衷的感谢你救了我们，冒险者。', Speaker = 2507, Option = '', Action = _EmptyTable,}, 
	[131266] = { id = 131266, Voice = '', Text = '我现在就把你送过去。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131265] = { id = 131265, Voice = '', Text = '臣服我！你们就将得到不朽的生命！', Speaker = 2512, Option = '', Action = _EmptyTable,}, 
	[131182] = { id = 131182, Voice = '', Text = '什么！俄塞利斯？！我明白了，我这就向普隆德拉和梦罗克方面报告。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131181] = { id = 131181, Voice = '', Text = '传说中的俄塞利斯出现了。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131180] = { id = 131180, Voice = '', Text = '辛苦你啦，探查到什么了吗？', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131179] = { id = 131179, Voice = '', Text = '黑暗腐朽蔓延大地，而我将带着腐朽统治黑暗！', Speaker = 2520, Option = '', Action = _EmptyTable,}, 
	[131178] = { id = 131178, Voice = '', Text = '冒险者，你能帮我们探查一下吗？', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131177] = { id = 131177, Voice = '', Text = '王？难道二层还有我们不知道的秘密吗？', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131176] = { id = 131176, Voice = '', Text = '麻烦你了，冒险者。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131175] = { id = 131175, Voice = '', Text = '我王将醒来….你们的死期不远了！', Speaker = 2521, Option = '', Action = _EmptyTable,}, 
	[131174] = { id = 131174, Voice = '', Text = '麻烦你了，冒险者，它是一个非常危险的魔物，我希望你能够找一些朋友帮助你。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131173] = { id = 131173, Voice = '', Text = '首领就交给我吧！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131172] = { id = 131172, Voice = '', Text = '我们在二层深处发现了马尔杜克的头领，有它在，我们根本无法接近其他马尔杜克。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131171] = { id = 131171, Voice = '', Text = '为什么呢？', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131170] = { id = 131170, Voice = '', Text = '原来是这样，方法是很好，但是我们现在还没有把牧师带进去。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131169] = { id = 131169, Voice = '', Text = '怎么样？有什么办法可以解决马尔杜克吗？', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131168] = { id = 131168, Voice = '', Text = '并且金字塔那样的环境，你也一定需要带着能够治疗的牧师。', Speaker = 1313, Option = '', Action = _EmptyTable,}, 
	[131167] = { id = 131167, Voice = '', Text = '那么请你转告亚瑞将军，马尔杜克这种危险的魔物，带着牧师，会有很好的效果。', Speaker = 1313, Option = '', Action = _EmptyTable,}, 
	[131166] = { id = 131166, Voice = '', Text = '多亏了你啊，不然我得为这些材料愁白了头发。', Speaker = 1313, Option = '', Action = _EmptyTable,}, 
	[131165] = { id = 131165, Voice = '', Text = '这点可不够我研究呢……', Speaker = 1313, Option = '', Action = _EmptyTable,}, 
	[131164] = { id = 131164, Voice = '', Text = '啊，你回来了？把东西给我吧！', Speaker = 1313, Option = '', Action = _EmptyTable,}, 
	[131163] = { id = 131163, Voice = '', Text = '作为交换，我可以把马尔杜克的解决办法告诉你。', Speaker = 1313, Option = '', Action = _EmptyTable,}, 
	[131162] = { id = 131162, Voice = '', Text = '如果你愿意帮忙那就太好了，最近我需要一些魔物身上的不死心脏研究。', Speaker = 1313, Option = '', Action = _EmptyTable,}, 
	[131161] = { id = 131161, Voice = '', Text = '需要我做什么吗？', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131160] = { id = 131160, Voice = '', Text = '不过也不是没有办法克制，但是这个方法是我们魔物研究会的秘密….', Speaker = 1313, Option = '', Action = _EmptyTable,}, 
	[131159] = { id = 131159, Voice = '', Text = '马尔杜克？唔，这的确是一个非常强大的魔物。', Speaker = 1313, Option = '', Action = _EmptyTable,}, 
	[131158] = { id = 131158, Voice = '', Text = '嗯？是你啊，好久不见。', Speaker = 1313, Option = '', Action = _EmptyTable,}, 
	[131157] = { id = 131157, Voice = '', Text = '所以我想请你拜访一下魔物研究协会的魔物学者雷，问问他有什么方法。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131156] = { id = 131156, Voice = '', Text = '他们很强大，暂时我们还找不到对付它的办法。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131155] = { id = 131155, Voice = '', Text = '我们勇敢的士兵告诉我，二层除了邪骸弓箭手之外，还有一种名为马尔杜克的生物。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131154] = { id = 131154, Voice = '', Text = '干的很好，我们的伤亡明显降低了。谢谢你冒险者。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131153] = { id = 131153, Voice = '', Text = '冒险者，如果你没有事情的话，可以帮我们清理一些吗？', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131152] = { id = 131152, Voice = '', Text = '尤其是邪骸弓箭手，他们的远程攻击令我们非常头疼。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131151] = { id = 131151, Voice = '', Text = '里面的魔物出乎意料的强大，为此我们折损了不少士兵。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131150] = { id = 131150, Voice = '', Text = '哦，是你啊冒险者。我现在确实遇到了难题。我们城卫军发现了金字塔的第二层。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131149] = { id = 131149, Voice = '', Text = '亚瑞将军，出什么事情了吗？', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131148] = { id = 131148, Voice = '', Text = '唉….这可怎么办啊。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131147] = { id = 131147, Voice = '', Text = '这我就放心了！多谢你，冒险者。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131146] = { id = 131146, Voice = '', Text = '当然！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131145] = { id = 131145, Voice = '', Text = '事情我已经听那个士兵说了，怎么样？干掉那个牛头怪物了吗？', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131144] = { id = 131144, Voice = '', Text = '我先行离开这里。', Speaker = 2519, Option = '', Action = _EmptyTable,}, 
	[131143] = { id = 131143, Voice = '', Text = '那个恶魔非常强大，请你务必小心，最好再带上你的朋友！', Speaker = 2519, Option = '', Action = _EmptyTable,}, 
	[131142] = { id = 131142, Voice = '', Text = '勇士，我知道你非常强大，我请求你，消灭那头恶魔！为我死去的兄弟们报仇！', Speaker = 2519, Option = '', Action = _EmptyTable,}, 
	[131141] = { id = 131141, Voice = '', Text = '这头怪物太强大了，我们在短短的几分钟就已经全军覆没了，我被吓昏过去了！', Speaker = 2519, Option = '', Action = _EmptyTable,}, 
	[131140] = { id = 131140, Voice = '', Text = '我们…刚进入这里，被一个巨大无比的牛头怪物拦住了。', Speaker = 2519, Option = '', Action = _EmptyTable,}, 
	[131139] = { id = 131139, Voice = '', Text = '不用客气，我是受到亚瑞将军的托付，来寻找你们的，请问，这里发生了什么？', Speaker = 2519, Option = '', Action = _EmptyTable,}, 
	[131138] = { id = 131138, Voice = '', Text = '冒险者，你是唤醒了我吗？谢谢你！', Speaker = 2519, Option = '', Action = _EmptyTable,}, 
	[131137] = { id = 131137, Voice = '', Text = '我在做什么。。。', Speaker = 2519, Option = '', Action = _EmptyTable,}, 
	[131136] = { id = 131136, Voice = '', Text = '别伤害我！别伤害我……恶魔……啊！！', Speaker = 2518, Option = '', Action = _EmptyTable,}, 
	[131135] = { id = 131135, Voice = '', Text = '太好了太好了，事不宜迟，快去救好他，我们需要他完整的回来！', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131134] = { id = 131134, Voice = '', Text = '怎么样冒险者？', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131133] = { id = 131133, Voice = '', Text = '好了，这就是卷轴了。拿去吧。', Speaker = 1228, Option = '', Action = _EmptyTable,}, 
	[131132] = { id = 131132, Voice = '', Text = '这点材料恐怕不足以完成卷轴的制作…', Speaker = 1228, Option = '', Action = _EmptyTable,}, 
	[131131] = { id = 131131, Voice = '', Text = '好，我现在就为你制作！请稍等片刻！', Speaker = 1228, Option = '', Action = _EmptyTable,}, 
	[131130] = { id = 131130, Voice = '', Text = '你回来了？东西都收集齐了吗？', Speaker = 1228, Option = '', Action = _EmptyTable,}, 
	[131129] = { id = 131129, Voice = '', Text = '不过，我可以为他制作一个治疗的魔法卷轴，但是我缺少一些材料，你能帮我找到这些材料吗？', Speaker = 1228, Option = '', Action = _EmptyTable,}, 
	[131128] = { id = 131128, Voice = '', Text = '如果他在这里，我现在就可以为他治疗，我目前又不能离开普隆德拉。', Speaker = 1228, Option = '', Action = _EmptyTable,}, 
	[131127] = { id = 131127, Voice = '', Text = '是这样的，大主教。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131126] = { id = 131126, Voice = '', Text = '原来是这样，事情我明白了，所以你现在是需要治好那个士兵吧？', Speaker = 1228, Option = '', Action = _EmptyTable,}, 
	[131125] = { id = 131125, Voice = '', Text = '愿圣光永照！年轻人，你找我有什么事情吗？', Speaker = 1228, Option = '', Action = _EmptyTable,}, 
	[131124] = { id = 131124, Voice = '', Text = '他肯定知道一些什么！你去找找普隆德拉的汤玛斯大主教，或许他有办法。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131123] = { id = 131123, Voice = '', Text = '我在那里发现了一个已经疯了的士兵，但是我从他身上得不到任何情报。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131122] = { id = 131122, Voice = '', Text = '冒险者，你终于回来了？找到线索了吗？', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131121] = { id = 131121, Voice = '', Text = '看来只能找亚瑞说明情况了。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131120] = { id = 131120, Voice = '', Text = '(双手抱头，颤抖着)别伤害我！魔鬼……别碰我！', Speaker = 2518, Option = '', Action = _EmptyTable,}, 
	[131119] = { id = 131119, Voice = '', Text = '不要紧张，我是来救你们的冒险者，告诉我其他的人呢？', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[131118] = { id = 131118, Voice = '', Text = '别伤害我！别伤害我……恶魔……啊！！', Speaker = 2518, Option = '', Action = _EmptyTable,}, 
	[131117] = { id = 131117, Voice = '', Text = '别伤害我！别伤害我！', Speaker = 2518, Option = '', Action = _EmptyTable,}, 
	[131116] = { id = 131116, Voice = '', Text = '我们勇敢的士兵们给我报信后就进入了传送阵，至今没有回来。我想请你进去看一看。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131115] = { id = 131115, Voice = '', Text = '我们城卫军在探索金字塔时，发现了一个奇怪的传送阵。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131114] = { id = 131114, Voice = '', Text = '冒险者请等一等！我有件事情想请你帮忙。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131113] = { id = 131113, Voice = '', Text = '这倒是个好线索，我会如实向上报告的。再次感谢你，冒险者。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131112] = { id = 131112, Voice = '', Text = '邪骸军团长的王？这是他消失前说的吗？', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131111] = { id = 131111, Voice = '', Text = '解决了？我果然没看错人！', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131110] = { id = 131110, Voice = '', Text = '无耻的人类！我的王会为我报仇的！', Speaker = 2512, Option = '', Action = _EmptyTable,}, 
	[131109] = { id = 131109, Voice = '', Text = '这次行动非常危险，我建议你还是多找几个人吧。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131108] = { id = 131108, Voice = '', Text = '冒险者，你能帮我把邪骸军团长干掉吗？放心，不会让你白干的，我们会给你丰厚的奖励！', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131107] = { id = 131107, Voice = '', Text = '为了清理邪骸士兵，我们城卫军损失惨重，现在无力清理邪骸军团长了。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131106] = { id = 131106, Voice = '', Text = '看来邪骸士兵的问题就出在这个军团长身上了。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131105] = { id = 131105, Voice = '', Text = '邪骸军团长？消息准确吗？', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131104] = { id = 131104, Voice = '', Text = '只要找到他，那么我们就能解决这次的危机！', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131103] = { id = 131103, Voice = '', Text = '冒险者，据我们的情报显示，这邪骸士兵身后有人在操纵他们。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131102] = { id = 131102, Voice = '', Text = '士兵的恢复情况都很不错，多谢你，冒险者。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131101] = { id = 131101, Voice = '', Text = '请小心点，不要把沾染了诅咒！', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131100] = { id = 131100, Voice = '', Text = '既然血液到手了，请帮忙把血液涂抹在伤兵身上吧。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131099] = { id = 131099, Voice = '', Text = '你的效率真是惊人的高啊！可惜你不能加入我们城防军……', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131098] = { id = 131098, Voice = '', Text = '据说黑狐的血液有克制诅咒的效果，你能帮我取一点来吗？', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131097] = { id = 131097, Voice = '', Text = '要知道邪骸士兵的攻击中有着金字塔特有的诅咒，我们有不少士兵受伤了。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131096] = { id = 131096, Voice = '', Text = '冒险者，虽然有你的帮助情况好了一点，但是我们城防军也付出了相应的代价。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131095] = { id = 131095, Voice = '', Text = '事情迈克斯已经跟我说了，只是没想到你这么强大！', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131094] = { id = 131094, Voice = '', Text = '根据亚瑞将军的情报，目前金字塔的魔物以邪骸士兵最为猖獗，请你帮忙清理一些吧。', Speaker = 1554, Option = '', Action = _EmptyTable,}, 
	[131093] = { id = 131093, Voice = '', Text = '但是我们梦罗克大陆卫队现在也没有足够的人手，所以冒险者，我想请你帮助亚瑞将军。', Speaker = 1554, Option = '', Action = _EmptyTable,}, 
	[131092] = { id = 131092, Voice = '', Text = '亚瑞将军的困难我已经了解了，金字塔的魔物如果不能好好清理，总有一天要威胁到梦罗克城的民众们。', Speaker = 1554, Option = '', Action = _EmptyTable,}, 
	[131091] = { id = 131091, Voice = '', Text = '找我有什么事情吗？', Speaker = 1554, Option = '', Action = _EmptyTable,}, 
	[131090] = { id = 131090, Voice = '', Text = '现在我们的人手严重不足，我想请你前往梦罗克，寻找迈克斯事务官，并把这封急件交给他。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131089] = { id = 131089, Voice = '', Text = '如你所见，冒险者，现在金字塔中充斥着那些恶心肮脏的魔物，光是为了清理这些魔物，城防军已经疲于应付了。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[131088] = { id = 131088, Voice = '', Text = '冒险者，你来得正好，我现在需要你的帮助。', Speaker = 1575, Option = '', Action = _EmptyTable,}, 
	[120541] = { id = 120541, Voice = '', Text = '那真是太遗憾了。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120540] = { id = 120540, Voice = '', Text = '那真是太遗憾了。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120539] = { id = 120539, Voice = '', Text = '很好，谢谢你冒险者。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120538] = { id = 120538, Voice = '', Text = '很好，谢谢你冒险者。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120537] = { id = 120537, Voice = '', Text = '咦？这点[c][ffff00]加勒结晶[-][/c]可不够呢…', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120536] = { id = 120536, Voice = '', Text = '唔…这是我要的[c][ffff00]暖心料理[-][/c]吗？', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120535] = { id = 120535, Voice = '', Text = '这么快就收集来了吗？', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120534] = { id = 120534, Voice = '', Text = '唔，故事讲完了，作为报酬，我需要一些[c][ffff00]加勒结晶[-][/c]，你能帮我弄来吗？', Speaker = 1590, Option = '', Action = { num = 1, actionid = 201,},}, 
	[120533] = { id = 120533, Voice = '', Text = '这就是天才魔法师凯特丽娜的故事。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120532] = { id = 120532, Voice = '', Text = '但是她仍然希望有一天能够出去见见外面的世界。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120531] = { id = 120531, Voice = '', Text = '现在她在魔法公会中接手了一部分工作。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120530] = { id = 120530, Voice = '', Text = '她想出门游历，却被父母勒令呆在吉芬城。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120529] = { id = 120529, Voice = '', Text = '当吉芬城中所有的典籍不再满足她的要求后，她的力量提升终于缓慢了下来。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120528] = { id = 120528, Voice = '', Text = '她开始潜心学习魔法，努力的提升自己的力量。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120527] = { id = 120527, Voice = '', Text = '她为那股力量而痴迷不已，并且对于自己只能提供魔力而懊悔不已。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120526] = { id = 120526, Voice = '', Text = '而拉武雷从天空坠落昏迷不醒，凯特丽娜感受到了那股强大的力量。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120525] = { id = 120525, Voice = '', Text = '在一声巨响中，伊芙利特重伤逃遁。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120524] = { id = 120524, Voice = '', Text = '拉武雷念着咒语，手中的法杖开始聚集强大的魔力。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120523] = { id = 120523, Voice = '', Text = '凯特丽娜与玛莉亚的魔力源源不断地向拉武雷传输。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120522] = { id = 120522, Voice = '', Text = '拉武雷很看重她的体质，在玛莉亚的魔法阵的帮助下。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120521] = { id = 120521, Voice = '', Text = '干得不错冒险者，那么我就继续我的故事了。', Speaker = 1590, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120520] = { id = 120520, Voice = '', Text = '唔，我们协会也想为大陆出一份力，但是我们没有力量，你能帮我们消灭一些[c][ffff00]工蚁[-][/c]吗？', Speaker = 1590, Option = '', Action = { num = 1, actionid = 201,},}, 
	[120519] = { id = 120519, Voice = '', Text = '凯特丽娜甚至能够感受到拉武雷身体内的澎湃魔力。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120518] = { id = 120518, Voice = '', Text = '果然不出她的意料，拉武雷是古魔法师。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120517] = { id = 120517, Voice = '', Text = '但是凯特丽娜一直认为他是一个非常强大的魔法师。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120516] = { id = 120516, Voice = '', Text = '拉武雷的突然出现带给了她们信心，神秘的拉武雷从不展露他的实力。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120515] = { id = 120515, Voice = '', Text = '然而她们的实力无法伤到伊夫利特，魔法公会的高层又都不在城中，她们甚至感到绝望了。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120514] = { id = 120514, Voice = '', Text = '都让凯特丽娜感到恐惧，她看到玛莉亚在前方不停地攻击者魔物，她决定上去帮忙。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120513] = { id = 120513, Voice = '', Text = '伊芙利特身上散发的毁灭气息，以及轰击在魔法护罩上逸散的能量。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120512] = { id = 120512, Voice = '', Text = '没有经验的她立刻被伊夫利特震住了。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120511] = { id = 120511, Voice = '', Text = '突如其来的进攻打断了她的感悟，万分恼火的她来到城门。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120510] = { id = 120510, Voice = '', Text = '唔，你回来的真快。那么我就继续开始我的故事了。', Speaker = 1590, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120509] = { id = 120509, Voice = '', Text = '唔。。故事先告一段落，你能帮我买一个[c][ffff00]暖心料理[-][/c]吗？', Speaker = 1590, Option = '', Action = { num = 1, actionid = 201,},}, 
	[120508] = { id = 120508, Voice = '', Text = '她第一时间冲到吉芬塔下，细细的感受着这股力量。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120507] = { id = 120507, Voice = '', Text = '那丝波动他第一时间就感受到了，她从未感觉到如此特殊而又强大的力量。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120506] = { id = 120506, Voice = '', Text = '但是她的魔力容量能够与进入魔导士很久的玛莉亚相提并论。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120505] = { id = 120505, Voice = '', Text = '凯特丽娜的进步飞快，虽然她还没有能够成为魔导士。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120504] = { id = 120504, Voice = '', Text = '她的特殊体质也使得她被誉为有望超过雷藏的魔法师。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120503] = { id = 120503, Voice = '', Text = '她的身体可容的魔力是同等级的法师的五倍。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120502] = { id = 120502, Voice = '', Text = '在父母的熏陶下，凯特丽娜从小就展现出了与众不同的天赋。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120501] = { id = 120501, Voice = '', Text = '凯特丽娜是吉芬城中相当有名的魔法师，她的父母是魔法公会里的元老。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120500] = { id = 120500, Voice = '', Text = '今天我就来讲讲天才魔法师凯特丽娜的故事。', Speaker = 1590, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120499] = { id = 120499, Voice = '', Text = '咦？这点[c][ffff00]亡者遗物[-][/c]可不够呢…', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120498] = { id = 120498, Voice = '', Text = '唔…这是我要的唱片-[c][ffff00]我想念你[-][/c]吗？', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120497] = { id = 120497, Voice = '', Text = '这么快就收集来了吗？', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120496] = { id = 120496, Voice = '', Text = '唔，故事讲完了，作为报酬，我需要一些[c][ffff00]亡者遗物[-][/c]，你能帮我弄来吗？', Speaker = 1590, Option = '', Action = { num = 1, actionid = 201,},}, 
	[120495] = { id = 120495, Voice = '', Text = '这就是摩根大贤者的故事。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120494] = { id = 120494, Voice = '', Text = '摩根就此隐居在山脉中，潜心研究古魔法。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120493] = { id = 120493, Voice = '', Text = '人们都尊称他为大贤者，摩根也在妙勒尼山脉的民众中选择了三位拥有古魔法天赋的学生。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120492] = { id = 120492, Voice = '', Text = '在古魔法中受益匪浅，在试验的过程中，妙勒尼山脉的人们都被他魔法的威力所折服。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120491] = { id = 120491, Voice = '', Text = '在雷藏离开之后，摩根终于潜下心来学习古魔法。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120490] = { id = 120490, Voice = '', Text = '在离开遗迹后，摩根与雷藏探讨魔法的奥秘，他们成为了好朋友。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120489] = { id = 120489, Voice = '', Text = '雷藏也明白魔法体系之间的排斥，对自己将造成的苦果很后悔。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120488] = { id = 120488, Voice = '', Text = '如此浓厚的血腥气息！干得不错冒险者，那么我就继续我的故事了。', Speaker = 1590, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120487] = { id = 120487, Voice = '', Text = '唔，我们协会也想为大陆出一份力，但是我们没有力量，你能帮我们消灭一些[c][ffff00]兵蚁[-][/c]吗？', Speaker = 1590, Option = '', Action = { num = 1, actionid = 201,},}, 
	[120486] = { id = 120486, Voice = '', Text = '而摩根，拥有古魔法师的血脉，是修习古魔法的绝佳人选。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120485] = { id = 120485, Voice = '', Text = '雷藏的天赋在于吉芬魔法体系，强行学习古魔法会造成不可控的后果。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120484] = { id = 120484, Voice = '', Text = '直到被雷藏身上散发的毁灭力量惊醒，救走雷藏后，他告诉雷藏。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120483] = { id = 120483, Voice = '', Text = '这里发现的魔法甚至在他的家族史中都有记载，他开始潜心修行古魔法。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120482] = { id = 120482, Voice = '', Text = '令他万分高兴的是，这里竟然是古魔法的遗迹。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120481] = { id = 120481, Voice = '', Text = '摩根无奈之下答应了比试，前往魔法遗迹。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120480] = { id = 120480, Voice = '', Text = '但是雷藏根本不给他拒绝的机会，留下一句：我在魔法遗迹等你，就离开了。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120479] = { id = 120479, Voice = '', Text = '雷藏提出魔法比试的时候，摩根立刻就拒绝了。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120478] = { id = 120478, Voice = '', Text = '然而这一切都被雷藏看在眼里。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120477] = { id = 120477, Voice = '', Text = '唔，你回来的真快。那么我就继续开始我的故事了。', Speaker = 1590, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120476] = { id = 120476, Voice = '', Text = '唔。。故事先告一段落，你能帮我买一个唱片-[c][ffff00]我想念你[-][/c]吗？', Speaker = 1590, Option = '', Action = { num = 1, actionid = 201,},}, 
	[120475] = { id = 120475, Voice = '', Text = '很显然蜂后在他的手下毫无还手的能力。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120474] = { id = 120474, Voice = '', Text = '修习有成之后他来到了妙勒尼山脉，挑战强大的蜂后。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120473] = { id = 120473, Voice = '', Text = '索性他就放弃了学习魔法，前往大陆各地开始修习家族传承的魔法。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120472] = { id = 120472, Voice = '', Text = '摩根家族一脉相传的古魔法血脉，与吉芬的魔法体系格格不入。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120471] = { id = 120471, Voice = '', Text = '但是摩根本就是个散漫的性子，更不屑去辩解什么，他的臭名就此传遍吉芬。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120470] = { id = 120470, Voice = '', Text = '自己的天赋不输与任何人，摩根也自信不会输给他。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120469] = { id = 120469, Voice = '', Text = '他是吉芬魔法学校公认的“废柴”，但是他自己知道，自己的天赋不输与任何人。', Speaker = 1590, Option = '', Action = _EmptyTable,}, 
	[120468] = { id = 120468, Voice = '', Text = '今天我就来讲讲摩根的故事。', Speaker = 1590, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120467] = { id = 120467, Voice = '', Text = '咦？这点[c][ffff00]亡者遗物[-][/c]可不够呢…', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120466] = { id = 120466, Voice = '', Text = '唔…这是我要的[c][ffff00]伪装卷轴[-][/c]吗？', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120465] = { id = 120465, Voice = '', Text = '再也不轻易的离开吉芬，这就是雷藏的故事。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120464] = { id = 120464, Voice = '', Text = '吉芬似乎经历了一场大战，了解前因后果的雷藏，决定驻守吉芬塔。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120463] = { id = 120463, Voice = '', Text = '但是到了吉芬之后发现，吉芬城外的魔法护罩已经支离破碎。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120462] = { id = 120462, Voice = '', Text = '雷藏受益匪浅，决定离开妙勒尼，回到吉芬继续研究。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120461] = { id = 120461, Voice = '', Text = '这么快就收集来了吗？好好好。那我就继续我的故事了。', Speaker = 1589, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120460] = { id = 120460, Voice = '', Text = '唉，我也很想学习魔法，你能帮我找到一些[c][ffff00]亡者遗物[-][/c]吗?', Speaker = 1589, Option = '', Action = { num = 1, actionid = 201,},}, 
	[120459] = { id = 120459, Voice = '', Text = '他们成为了无话不谈的好朋友。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120458] = { id = 120458, Voice = '', Text = '带着雷藏回到了妙勒尼，在路途中他们探索魔法，切磋魔法。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120457] = { id = 120457, Voice = '', Text = '醒来的雷藏清楚的知道了自己做了什么，高傲如他也难免有些窘迫。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120456] = { id = 120456, Voice = '', Text = '摩根感受到身旁毁灭的气息，立刻击昏了雷藏，带着雷藏走出了遗迹。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120455] = { id = 120455, Voice = '', Text = '雷藏念着古魔法的咒语，而他的血液却从眼睛开始流淌而下。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120454] = { id = 120454, Voice = '', Text = '如此浓厚的血腥气息！干得不错冒险者，那么我就继续我的故事了。', Speaker = 1589, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120453] = { id = 120453, Voice = '', Text = '唉，哥布灵森林中的[c][ffff00]弓箭哥布灵[-][/c]又开始暴动了，你先把他们清理一下吧。', Speaker = 1589, Option = '', Action = { num = 1, actionid = 201,},}, 
	[120452] = { id = 120452, Voice = '', Text = '这让雷藏无法接受，决定强行释放古魔法。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120451] = { id = 120451, Voice = '', Text = '古魔法的奥义雷藏不能理解透彻，摩根对古魔法却有所领悟。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120450] = { id = 120450, Voice = '', Text = '雷藏持续的研究修炼，在冥想中感悟古魔法的力量。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120449] = { id = 120449, Voice = '', Text = '摩根无奈之下只能答应，古代的魔法奥妙精深。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120448] = { id = 120448, Voice = '', Text = '雷藏立刻表示希望用学习古魔法的速度，来一决胜负。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120447] = { id = 120447, Voice = '', Text = '在这里他们发现了古老的魔法文字，这是一种古魔法。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120446] = { id = 120446, Voice = '', Text = '在经历过一系列的考验后，他们终于来到遗迹的深处。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120445] = { id = 120445, Voice = '', Text = '他直接离开前往魔法遗迹，他们的比试就由此开始。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120444] = { id = 120444, Voice = '', Text = '摩根是拒绝的，但是高傲如雷藏根本不给他拒绝的机会。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120443] = { id = 120443, Voice = '', Text = '唔，你回来的真快。那么我就继续开始我的故事了。', Speaker = 1589, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120442] = { id = 120442, Voice = '', Text = '说到这里，冒险者，你能帮我找一些[c][ffff00]伪装卷轴[-][/c]来吗？', Speaker = 1589, Option = '', Action = { num = 1, actionid = 201,},}, 
	[120441] = { id = 120441, Voice = '', Text = '雷藏按捺不住，决定与摩根魔法比试一番。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120440] = { id = 120440, Voice = '', Text = '摩根简单精妙的魔法给蜂后造成了很大伤害，而蜂后对此却毫无办法。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120439] = { id = 120439, Voice = '', Text = '摩根与蜂后的战斗颠覆了雷藏看法。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120438] = { id = 120438, Voice = '', Text = '那是之前同学院的摩根，全魔法学院最差劲的学生。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120437] = { id = 120437, Voice = '', Text = '他为了修炼魔法来到了妙勒尼山脉，他居然看到了一个熟悉的人。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120436] = { id = 120436, Voice = '', Text = '魔法公会的人也认为他会是下一代魔法公会的领军人物，高傲自信的雷藏也是这样认为的。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120435] = { id = 120435, Voice = '', Text = '在吉芬城高等魔法学院里，每次考核他都是第一，他的光芒碾压了同级的所有人。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120434] = { id = 120434, Voice = '', Text = '他是天生的法师。老魔法公会会长这样评价他。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120433] = { id = 120433, Voice = '', Text = '今天我就来讲讲雷藏的故事。', Speaker = 1589, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120432] = { id = 120432, Voice = '', Text = '咦？这点[c][ffff00]柔毛[-][/c]可不够呢…', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120431] = { id = 120431, Voice = '', Text = '唔…这是我要的[c][ffff00]第六感药水[-][/c]吗？', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120430] = { id = 120430, Voice = '', Text = '所以她接手了魔法师的转职工作，这就是现今的大魔导士玛莉亚的故事。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120429] = { id = 120429, Voice = '', Text = '而现在，她发现了新人魔法师总有一些奇怪的发现，她对此很感兴趣。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120428] = { id = 120428, Voice = '', Text = '但是她仍然不满足，依旧沉浸在魔法的研究之中。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120427] = { id = 120427, Voice = '', Text = '在这种新型魔法阵的帮助下，吉芬城的大型防护魔法阵——吉芬之光，得到了空前的提升。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120426] = { id = 120426, Voice = '', Text = '她发现了三角魔法阵型，魔法元素的利用率上升并且能量传输的损耗变得极低。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120425] = { id = 120425, Voice = '', Text = '好吧。那我就继续刚刚的故事。', Speaker = 1589, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120424] = { id = 120424, Voice = '', Text = '回来的这么快？你真的收集到了？', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120423] = { id = 120423, Voice = '', Text = '唉，我现在缺少一些[c][ffff00]柔毛[-][/c]，我也想研究研究魔法，你能帮我弄过来吗？', Speaker = 1589, Option = '', Action = { num = 1, actionid = 201,},}, 
	[120422] = { id = 120422, Voice = '', Text = '但是如果对话的不是魔法师，她甚至连跟你说话的兴趣都没有。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120421] = { id = 120421, Voice = '', Text = '她的时间变得异常宝贵，谈到魔法她可以滔滔不绝的跟你谈上一天。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120420] = { id = 120420, Voice = '', Text = '但是她深深了解到自己的不足之处，把热情重新投入魔法试验之中。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120419] = { id = 120419, Voice = '', Text = '她的魔法阵在这场战斗中争取了很宝贵的时间。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120418] = { id = 120418, Voice = '', Text = '这位神秘的魔法师终于展现出强大的实力，在他的古魔法的帮助下，终于击退了伊夫利特。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120417] = { id = 120417, Voice = '', Text = '只要护罩破灭便会进入城中，拉武雷出现在他们的视野里。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120416] = { id = 120416, Voice = '', Text = '随着伊夫利特进攻的越来越猛烈，吉芬周围的魔物也逐渐向吉芬城聚集。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120415] = { id = 120415, Voice = '', Text = '干得不错！那么我就继续我的故事了。', Speaker = 1589, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120414] = { id = 120414, Voice = '', Text = '这次帮我教训一下[c][ffff00]喷射哥布灵[-][/c]吧。他们袭击过我，我可是很记仇的！', Speaker = 1589, Option = '', Action = { num = 1, actionid = 201,},}, 
	[120413] = { id = 120413, Voice = '', Text = '她的魔法阵为护罩提供了很长时间的能量。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120412] = { id = 120412, Voice = '', Text = '经过玛莉亚改良过的魔法传输法阵能够更好吸收魔法师们的力量。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120411] = { id = 120411, Voice = '', Text = '凯特丽娜来到她的身边，两人一起阻止伊夫利特进攻魔法护罩。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120410] = { id = 120410, Voice = '', Text = '伊夫利特疯狂的攻击着护罩，再想不出办法整个吉芬城就会有灭亡的危机。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120409] = { id = 120409, Voice = '', Text = '无奈只能躲入开启了吉芬历代法师维护的魔法护罩。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120408] = { id = 120408, Voice = '', Text = '但是伊夫利特的到来打破了她的自信，经过她简化的魔法咒语放出的魔法几乎伤不到伊夫利特分毫。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120407] = { id = 120407, Voice = '', Text = '她自信的认为自己能够解决那些魔物，并且尽快回到实验室继续她的试验。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120406] = { id = 120406, Voice = '', Text = '利用风行术直接飞翔到城门口，因为她知道，不久后魔物就会进攻吉芬。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120405] = { id = 120405, Voice = '', Text = '当那丝波动放出的时候，她停下了手中所有的试验。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120404] = { id = 120404, Voice = '', Text = '唔，你回来的真快。那么我就继续开始我的故事了。', Speaker = 1589, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120403] = { id = 120403, Voice = '', Text = '唔。。你懂我的规矩吧？帮我买点[c][ffff00]第六感药水[-][/c]吧。', Speaker = 1589, Option = '', Action = { num = 1, actionid = 201,},}, 
	[120402] = { id = 120402, Voice = '', Text = '她的实力也是毋庸置疑的强大，她是魔法公会百年来最快到达魔导士的魔法师。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120401] = { id = 120401, Voice = '', Text = '在大街上看到一只猫咪或者小狗，那很可能就是玛莉亚魔法实验失败的结果，将自己变成一个小动物。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120400] = { id = 120400, Voice = '', Text = '她都取得了不错的成就，当然她也会研究出一些搞笑的魔法。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120399] = { id = 120399, Voice = '', Text = '她的研究给吉芬人带来了无数的便利，吟唱咒语的改变以及魔法阵的控制。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120398] = { id = 120398, Voice = '', Text = '在魔法师公会中玛莉亚是以研究与实验而出名的魔法师。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120397] = { id = 120397, Voice = '', Text = '魔法的奥秘真是让人迷醉，在元素的海洋中沉沦。玛莉亚就是这样的一位魔法师。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120396] = { id = 120396, Voice = '', Text = '今天我就来讲讲大魔导士玛莉亚的故事。', Speaker = 1589, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120395] = { id = 120395, Voice = '', Text = '咦？这点[c][ffff00]粘稠液体[-][/c]可不够呢…', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120394] = { id = 120394, Voice = '', Text = '唔…这是我要的[c][ffff00]铁锤[-][/c]吗？', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120393] = { id = 120393, Voice = '', Text = '这就是迷之魔法师拉武雷的故事。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120392] = { id = 120392, Voice = '', Text = '并且在加入魔法公会负责魔法师的转职考核，他也希望通过各地的魔法师来寻找伊米儿之心的线索。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120391] = { id = 120391, Voice = '', Text = '拉武雷大失所望，想要离开吉芬，在吉芬人的挽留下，他还是留下来。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120390] = { id = 120390, Voice = '', Text = '目的就是引来强大的魔物进攻吉芬城，而现在，梦罗克的力量耗尽，暂时没有精力释放第二次了。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120389] = { id = 120389, Voice = '', Text = '但是吉芬根本没有伊米儿之心，先前的那丝波动实际上是吉芬塔下的封印中传出来的假象。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120388] = { id = 120388, Voice = '', Text = '拉武雷只有一个愿望，那就是得到伊米儿之心。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120387] = { id = 120387, Voice = '', Text = '好吧。那我就继续刚刚的故事。', Speaker = 1589, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120386] = { id = 120386, Voice = '', Text = '回来的这么快？你真的收集到了？', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120385] = { id = 120385, Voice = '', Text = '唔，好吧，我发誓，这是最后一个要求，请为我收集一些[c][ffff00]粘稠液体[-][/c]吧。', Speaker = 1589, Option = '', Action = { num = 1, actionid = 201,},}, 
	[120384] = { id = 120384, Voice = '', Text = '伊夫利特咆哮着退去，吉芬由此幸免于难，吉芬人无比感激。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120383] = { id = 120383, Voice = '', Text = '我祈求——禁·神灵之咒！', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120382] = { id = 120382, Voice = '', Text = '远古的诸神，永世照耀大地，邪恶，愚昧，讽刺，黑暗，一切罪恶在众神的注视下无所遁形！', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120381] = { id = 120381, Voice = '', Text = '感受身上澎湃的魔力，拉武雷轻声吟唱。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120380] = { id = 120380, Voice = '', Text = '准备好了么？拉武雷缓缓漂浮起来。玛莉亚，凯特丽娜开始把力量灌入法阵。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120379] = { id = 120379, Voice = '', Text = '吼——吉芬魔法护罩破碎，伊夫利特咆哮着飞入城中，直逼吉芬塔。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120378] = { id = 120378, Voice = '', Text = '你们两人需要建立魔力传输魔法阵，并且把魔力传输的终点指向我。拉武雷如此嘱咐。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120377] = { id = 120377, Voice = '', Text = '古魔法——禁·神灵之咒，施展这个魔法需要庞大的魔力。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120376] = { id = 120376, Voice = '', Text = '干得不错！那么我就继续我的故事了。', Speaker = 1589, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120375] = { id = 120375, Voice = '', Text = '唔，故事先讲到这里，你想帮我清理一些[c][ffff00]大斧哥布灵[-][/c]，我们协会经常有人被它们所伤。', Speaker = 1589, Option = '', Action = { num = 1, actionid = 201,},}, 
	[120374] = { id = 120374, Voice = '', Text = '吉芬不能就此毁于一旦，我需要你们的力量！两人连忙应承下来。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120373] = { id = 120373, Voice = '', Text = '他找到了现在城中最强的两位魔法师：玛莉亚和凯特丽娜。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120372] = { id = 120372, Voice = '', Text = '这样的下去，吉芬城怕是要毁灭了，拉武雷绝对不允许吉芬城毁灭！', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120371] = { id = 120371, Voice = '', Text = '伊夫利特？看来魔物们也受到了那丝气息的召唤了。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120370] = { id = 120370, Voice = '', Text = '吉芬现在根本没有能够抵抗他的力量啊！魔物学者雷在不远处大声嘶吼。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120369] = { id = 120369, Voice = '', Text = '门外逐渐变得喧哗起来：这是——这是伊夫利特啊，怎么办？', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120368] = { id = 120368, Voice = '', Text = '拉武雷开始着手准备材料，强有力的震动震散了他放置好的材料。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120367] = { id = 120367, Voice = '', Text = '拉武雷极为激动，在吉芬苦寻5年终于感受到了它的气息。那就是依米尔之心的气息。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120366] = { id = 120366, Voice = '', Text = '一丝奇异的波动悄然出现，惊醒了还在冰床上睡眠的拉武雷。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120365] = { id = 120365, Voice = '', Text = '唔，你回来的真快。那么我就继续开始我的故事了。', Speaker = 1589, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120364] = { id = 120364, Voice = '', Text = '吟游诗人的故事，可不是这么容易就能听到的。去帮我买个[c][ffff00]铁锤[-][/c]吧。', Speaker = 1589, Option = '', Action = { num = 1, actionid = 201,},}, 
	[120363] = { id = 120363, Voice = '', Text = '他依旧呆在自己的小屋中，随着时间的推移，吉芬人甚至遗忘了他的存在。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120362] = { id = 120362, Voice = '', Text = '用他的话说就是，巨龙需要在乎老鼠说了什么吗？', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120361] = { id = 120361, Voice = '', Text = '拉武雷成为了百年来吉芬最不受欢迎的人，他对此不闻不问，或者说他跟本不在乎。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120360] = { id = 120360, Voice = '', Text = '魔法公会的人很愤怒，他们发誓要将拉武雷赶出吉芬，却在魔法公会老会长的劝说下不了了之。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120359] = { id = 120359, Voice = '', Text = '没有人知道他在里面做什么，吉芬魔法公会尝试接触这位性格古怪的人，却遭到了拉武雷无情的嘲讽。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120358] = { id = 120358, Voice = '', Text = '八年前，吉芬迎来了一位陌生的法师，一个人在吉芬租下了一间小屋。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120357] = { id = 120357, Voice = '', Text = '魔法之光终将照耀大地——拉武雷。', Speaker = 1589, Option = '', Action = _EmptyTable,}, 
	[120356] = { id = 120356, Voice = '', Text = '今天我就来讲讲迷之魔法师拉武雷的故事。', Speaker = 1589, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120355] = { id = 120355, Voice = '', Text = '那真是太遗憾了。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120354] = { id = 120354, Voice = '', Text = '那真是太遗憾了。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120353] = { id = 120353, Voice = '', Text = '那真是太遗憾了。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120352] = { id = 120352, Voice = '', Text = '那真是太遗憾了。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120351] = { id = 120351, Voice = '', Text = '唔…这是我要的[c][ffff00]天地树叶子[-][/c]吗？', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120350] = { id = 120350, Voice = '', Text = '咦？这点[c][ffff00]不死心脏[-][/c]可不够呢…', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120349] = { id = 120349, Voice = '', Text = '谢谢你冒险者，你帮了我大忙了！', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120348] = { id = 120348, Voice = '', Text = '你回来啦？我的[c][ffff00]天地树叶子[-][/c]呢？', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120347] = { id = 120347, Voice = '', Text = '故事讲到这里，冒险者，我需要一些[c][ffff00]天地树叶子[-][/c]，你能帮我弄来吗？', Speaker = 1588, Option = '好的', Action = { num = 1, actionid = 201,},}, 
	[120346] = { id = 120346, Voice = '', Text = '这就是巴尔洛将军的故事。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120345] = { id = 120345, Voice = '', Text = '而巴尔洛也从未令梦罗克子民们失望。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120344] = { id = 120344, Voice = '', Text = '梦罗克之盾，这就是梦罗克子民给予巴尔洛的荣耀。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120343] = { id = 120343, Voice = '', Text = '在他们眼里巴尔洛就是他们安全最大的保障。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120342] = { id = 120342, Voice = '', Text = '梦罗克的子民们，敬仰着巴尔洛。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120341] = { id = 120341, Voice = '', Text = '干的不错，我问到了它们血液的酸臭味儿，那么我继续开始讲述故事了。', Speaker = 1588, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120340] = { id = 120340, Voice = '', Text = '冒险者，你没受伤吧？', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120339] = { id = 120339, Voice = '', Text = '想继续听下去吗？那就帮我消灭一些[c][ffff00]木乃伊[-][/c]吧。我身上的伤口就是拜他们所赐呢。', Speaker = 1588, Option = '如你所愿', Action = { num = 1, actionid = 201,},}, 
	[120338] = { id = 120338, Voice = '', Text = '随着魔物数量的增多，巴尔洛不得不与普隆德拉进行联系，邀请皇家骑士团入驻梦罗克，以确保位于梦罗克的封印不会受到魔物的侵扰。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120337] = { id = 120337, Voice = '', Text = '由他全权统领梦罗克大陆卫队。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120336] = { id = 120336, Voice = '', Text = '而创建大陆卫队的巴尔洛，也被王国议会授予了将军职位。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120335] = { id = 120335, Voice = '', Text = '愿意与大陆卫队信息共享，共同维护王国的利益。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120334] = { id = 120334, Voice = '', Text = '甚至于普隆德拉都承认了大陆卫队的地位。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120333] = { id = 120333, Voice = '', Text = '而梦罗克大陆卫队在这次战斗中一战成名。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120332] = { id = 120332, Voice = '', Text = '巴尔洛在这一场战斗中，奠定了大陆卫队首领的地位。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120331] = { id = 120331, Voice = '', Text = '宣告着这场战争的终结。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120330] = { id = 120330, Voice = '', Text = '大战持续了三天三夜，浑身鲜血的巴尔洛，一刀斩下了最后一头魔物的头颅。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120329] = { id = 120329, Voice = '', Text = '很快巴尔洛便受到了消息，双方在梦罗克城外决一死战。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120328] = { id = 120328, Voice = '', Text = '魔物们低估了巴尔洛手中的力量。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120327] = { id = 120327, Voice = '', Text = '干的不错，我就继续讲故事了。', Speaker = 1588, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120326] = { id = 120326, Voice = '', Text = '你终于回来了。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120325] = { id = 120325, Voice = '', Text = '想继续听下去吗？那就帮我弄一些[c][ffff00]不死心脏[-][/c]来吧。', Speaker = 1588, Option = '好的', Action = { num = 1, actionid = 201,},}, 
	[120324] = { id = 120324, Voice = '', Text = '魔物们似乎知道了大陆卫队的消息，乘着大陆卫队刚组建的时机，进攻梦罗克城。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120323] = { id = 120323, Voice = '', Text = '巴尔洛利用自己在皇家骑士团所学到的，努力增强着大陆卫队的实力。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120322] = { id = 120322, Voice = '', Text = '这是以防止梦罗克突破封印，维护梦罗克治安为最终目标的力量。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120321] = { id = 120321, Voice = '', Text = '在梦罗克城主的帮助下，组建了梦罗克大陆卫队。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120320] = { id = 120320, Voice = '', Text = '经过了十年的战斗与训练，他主动从皇家骑士团退役，回到梦罗克城。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120319] = { id = 120319, Voice = '', Text = '没忘就像个爷们一样，完成今天的训练任务！', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120318] = { id = 120318, Voice = '', Text = '挺着胸膛！巴尔洛！你忘了你的仇恨吗？', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120317] = { id = 120317, Voice = '', Text = '发下誓言，维护梦罗克子民的安全，让那些魔物和梦罗克都见鬼去吧！', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120316] = { id = 120316, Voice = '', Text = '如同临爆发的火山，巴尔洛压抑着心中汹涌的愤怒。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120315] = { id = 120315, Voice = '', Text = '今天我就来讲讲梦罗克之盾巴尔洛的故事。', Speaker = 1588, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120314] = { id = 120314, Voice = '', Text = '唔…这是我要的[c][ffff00]中和剂[-][/c]吗？', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120313] = { id = 120313, Voice = '', Text = '咦？这点[c][ffff00]粘稠液体[-][/c]可不够呢…', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120312] = { id = 120312, Voice = '', Text = '谢谢你，冒险者。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120311] = { id = 120311, Voice = '', Text = '你回来啦？我的[c][ffff00]中和剂[-][/c]呢？', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120310] = { id = 120310, Voice = '', Text = '故事就讲到这里。帮我买个[c][ffff00]中和剂[-][/c]吧。', Speaker = 1588, Option = '好的', Action = { num = 1, actionid = 201,},}, 
	[120309] = { id = 120309, Voice = '', Text = '这就是艾尔克的故事。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120308] = { id = 120308, Voice = '', Text = '如今，他还在继续寻找女神之泪。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120307] = { id = 120307, Voice = '', Text = '后来艾尔克用女神之泪救了你。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120306] = { id = 120306, Voice = '', Text = '而商旅团百年的夙愿，就将在艾尔克手中实现。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120305] = { id = 120305, Voice = '', Text = '女神之泪终于纳入了梦罗克商旅团的手中。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120304] = { id = 120304, Voice = '', Text = '干的不错，我问到了它们血液的酸臭味儿，那么我继续开始讲述故事了。', Speaker = 1588, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120303] = { id = 120303, Voice = '', Text = '冒险者，你没受伤吧？', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120302] = { id = 120302, Voice = '', Text = '想继续听下去吗？那就帮我消灭一些[c][ffff00]摩卡[-][/c]吧。我身上的伤口就是拜他们所赐呢。', Speaker = 1588, Option = '如你所愿', Action = { num = 1, actionid = 201,},}, 
	[120301] = { id = 120301, Voice = '', Text = '唔，女神会记住你的功绩，愿女神的光辉永照大地。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120300] = { id = 120300, Voice = '', Text = '在哥布灵的帮助下，完成了彩虹项链的铸造。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120299] = { id = 120299, Voice = '', Text = '艾尔克的努力之下，四种元素收集完成。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120298] = { id = 120298, Voice = '', Text = '精致元素、虹色元素、坚硬元素、魅惑元素，彩虹项链所需的四种稀有元素。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120297] = { id = 120297, Voice = '', Text = '艾尔克四处寻找彩虹项链的孕育方法。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120296] = { id = 120296, Voice = '', Text = '真正的彩虹项链，是要在妙勒尼山脉才能孕育而出。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120295] = { id = 120295, Voice = '', Text = '艾尔克啊，这不是我需要的彩虹项链。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120294] = { id = 120294, Voice = '', Text = '那条散发着七彩光芒的项链，摆在了女神使者面前。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120293] = { id = 120293, Voice = '', Text = '却也给予奥丁神殿的守护者重创。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120292] = { id = 120292, Voice = '', Text = '大战之后，艾尔克所带领的武力伤亡无数。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120291] = { id = 120291, Voice = '', Text = '奥丁神殿强大的守护者根本不会允许，这些外来的入侵者进入神殿的范围。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120290] = { id = 120290, Voice = '', Text = '干的不错，我就继续讲故事了。', Speaker = 1588, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120289] = { id = 120289, Voice = '', Text = '你终于回来了。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120288] = { id = 120288, Voice = '', Text = '想继续听下去吗？那就帮我弄一些[c][ffff00]粘稠液体[-][/c]来吧。', Speaker = 1588, Option = '好的', Action = { num = 1, actionid = 201,},}, 
	[120287] = { id = 120287, Voice = '', Text = '艾尔克带着商旅团绝大部分可以使用的武力，甚至于聘用了一批战斗经验丰富的冒险者，前往传说中彩虹项链的所在地，传说中的奥丁神殿。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120286] = { id = 120286, Voice = '', Text = '女神之使者在寻找着彩虹项链，也许可以凭借这个项链来换取传说中的女神之泪。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120285] = { id = 120285, Voice = '', Text = '我将尽我一生的力量来完成商会的夙愿！我一定会亲手把女神之泪带回来！艾尔克对着老去的父亲说。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120284] = { id = 120284, Voice = '', Text = '你必须找到女神之泪！那颗充满生命与希望的宝石，将会带给我们一个全新的梦罗克。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120283] = { id = 120283, Voice = '', Text = '艾尔克则是这一任商旅会的首领，艾尔克啊，商旅会的使命就交由你来掌控，一切为了梦罗克的未来。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120282] = { id = 120282, Voice = '', Text = '而生活在梦罗克的人们不堪忍受，人们自发组成了梦罗克商旅会，用来寻找解决梦罗克沙漠的方法。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120281] = { id = 120281, Voice = '', Text = '沙尘暴铺天盖地地撕扯着黑暗中的一切，如同梦罗克的不甘地怒吼，席卷了整个梦罗克。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120280] = { id = 120280, Voice = '', Text = '他诡异而又强大的力量，侵蚀了这原本充满生机的大地，可以媲美斐扬丛林的梦罗克，逐渐成为了荒无人烟的荒漠。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120279] = { id = 120279, Voice = '', Text = '梦罗克原本并不是一片荒芜的沙漠，魔王梦罗克的到来', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[120278] = { id = 120278, Voice = '', Text = '今天我就来讲讲商旅团之心艾尔克的故事。', Speaker = 1588, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120277] = { id = 120277, Voice = '', Text = '唔…这是我要的[c][ffff00]变傻光线枪[-][/c]吗？', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120276] = { id = 120276, Voice = '', Text = '咦？这点[c][ffff00]不死心脏[-][/c]可不够呢…', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120275] = { id = 120275, Voice = '', Text = '谢谢你，冒险者，你帮了我大忙了！', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120274] = { id = 120274, Voice = '', Text = '你回来啦？我的[c][ffff00]变傻光线枪[-][/c]呢？', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120273] = { id = 120273, Voice = '', Text = '故事讲到这里，冒险者，我需要一些[c][ffff00]变傻光线枪[-][/c]，你能帮我弄来吗？', Speaker = 1587, Option = '好的', Action = { num = 1, actionid = 201,},}, 
	[120272] = { id = 120272, Voice = '', Text = '这就是利刃艾勒梅斯的故事。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120271] = { id = 120271, Voice = '', Text = '他成为了首领麾下最锋利的一把剑，直至现在。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120270] = { id = 120270, Voice = '', Text = '艾勒梅斯没有回答，但是他的行动却证明了一切。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120269] = { id = 120269, Voice = '', Text = '艾勒梅斯啊，失去至亲的感受并不好，但是你要明白，我们刺客公会为何而存在。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120268] = { id = 120268, Voice = '', Text = '当他带着头颅回到刺客公会时，无名者早已在公会等候着他。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120267] = { id = 120267, Voice = '', Text = '干的不错，我问到了它们血液的酸臭味儿，那么我继续开始讲述故事了。', Speaker = 1587, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120266] = { id = 120266, Voice = '', Text = '冒险者，你没受伤吧？', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120265] = { id = 120265, Voice = '', Text = '想继续听下去吗？那就帮我消灭一些[c][ffff00]木乃伊[-][/c]吧。我身上的伤口就是拜他们所赐呢。', Speaker = 1587, Option = '如你所愿', Action = { num = 1, actionid = 201,},}, 
	[120264] = { id = 120264, Voice = '', Text = '终于在依努尔·盖尔放松下来的那一瞬间，突兀地出现在身后，一刀割下了他的头颅。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120263] = { id = 120263, Voice = '', Text = '隐入黑暗的艾勒梅斯也受到了冲击，但是他忍耐了下来。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120262] = { id = 120262, Voice = '', Text = '依努尔·盖尔终于露出了他狰狞的一面，开始释放它的力量，整座城堡都在他的力量下化为废墟', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120261] = { id = 120261, Voice = '', Text = '血的誓言回荡在耳边，艾勒梅斯终于下定决心，他的身体逐渐变得透明直至消失。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120260] = { id = 120260, Voice = '', Text = '我发誓用一生来维护世间力量的平衡，我发誓让刺客公会成为大陆上秩序的维护者，我发誓让那些平衡破坏者付出血的代价！', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120259] = { id = 120259, Voice = '', Text = '面前目中散发红光的父亲，再也不是那个意气风发的大贵族，沦为了迷幻之王的爪牙。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120258] = { id = 120258, Voice = '', Text = '艾勒梅斯接下这次任务，当他来到了父亲面前，他的父亲似乎都忘记了他曾经拥有过这样一位子嗣。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120257] = { id = 120257, Voice = '', Text = '这次任务是我特地留下给你的，虽然你沉默寡言，但是聪慧如你，应该明白我的意思。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120256] = { id = 120256, Voice = '', Text = '依努尔·盖尔，这是他这次任务的目标，这是他的父亲。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120255] = { id = 120255, Voice = '', Text = '刺客公会发布的任务，每次他都能够一丝不苟的完成。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120254] = { id = 120254, Voice = '', Text = '这几乎成为了艾勒梅斯的信条，从此他更加专注于刺杀时机的修炼。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120253] = { id = 120253, Voice = '', Text = '干的不错，我就继续讲故事了。', Speaker = 1587, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120252] = { id = 120252, Voice = '', Text = '你终于回来了。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120251] = { id = 120251, Voice = '', Text = '想继续听下去吗？那就帮我弄一些[c][ffff00]不死心脏[-][/c]来吧。', Speaker = 1587, Option = '好的', Action = { num = 1, actionid = 201,},}, 
	[120250] = { id = 120250, Voice = '', Text = '作为一名刺客，忍耐是比实力更重要的东西，无名者打败他时对他说的话。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120249] = { id = 120249, Voice = '', Text = '无名者的速度如同矫健的猎豹，无名者的力量堪比野兽的王者，在无名者面前他一败涂地。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120248] = { id = 120248, Voice = '', Text = '这成为了他骄傲的资本，然而，无名者出现了，据说是从刺客的圣地——刺客秘境中走出的。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120247] = { id = 120247, Voice = '', Text = '在刺客公会中，艾勒梅斯很快就脱颖而出，凭借过人的天赋，成为了刺客公会年轻一代第一人。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120246] = { id = 120246, Voice = '', Text = '终于在7岁那年他终于忍受不了家族的气氛偷偷溜出去，从此踏上了刺客之路。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120245] = { id = 120245, Voice = '', Text = '从此他父亲便再也没有过问他的生活，很显然，在他父亲眼中他已经毫无分量了。毕竟盖尔家族是王国有数的大家族，子孙繁荣。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120244] = { id = 120244, Voice = '', Text = '当他选择地上那不起眼的匕首时，他的父亲没有说话，但是眼中的失望暴露无遗。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120243] = { id = 120243, Voice = '', Text = '年幼的艾勒梅斯看不懂父亲的眼神，他不知道父亲是多希望他能够选择象征无上权力的权杖。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120242] = { id = 120242, Voice = '', Text = '选择吧，艾勒梅斯，选择你未来的路，慈祥的父亲带着期望地看着艾勒梅斯。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120241] = { id = 120241, Voice = '', Text = '今天我就来讲讲刺客利刃艾勒梅斯的故事。', Speaker = 1587, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120240] = { id = 120240, Voice = '', Text = '唔…这是我要的[c][ffff00]智力料理B[-][/c]吗？', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120239] = { id = 120239, Voice = '', Text = '咦？这点[c][ffff00]雪勒结晶[-][/c]可不够呢…', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120238] = { id = 120238, Voice = '', Text = '谢谢你，有了[c][ffff00]智力料理B[-][/c]我才能更好的游历啊。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120237] = { id = 120237, Voice = '', Text = '你回来啦？我的[c][ffff00]智力料理B[-][/c]呢？', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120236] = { id = 120236, Voice = '', Text = '故事就讲到这里。我需要一些[c][ffff00]智力料理B[-][/c]，你能帮我买回来嘛？', Speaker = 1587, Option = '好的', Action = { num = 1, actionid = 201,},}, 
	[120234] = { id = 120234, Voice = '', Text = '无需害怕失败，尽管挑战吧！哈沃德如此对忽克连说。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120233] = { id = 120233, Voice = '', Text = '希望有一天，能够打造出可以媲美达纳托斯之剑的武器。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120232] = { id = 120232, Voice = '', Text = '然而哈沃德并不满足于此，他认为他的技艺依然能够提升。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120231] = { id = 120231, Voice = '', Text = '他唯一与外界的联系就是收了一名徒弟，他的弟子是普隆德拉首席精炼师的忽克连。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120230] = { id = 120230, Voice = '', Text = '干的不错，我问到了它们血液的酸臭味儿，那么我继续开始讲述故事了。', Speaker = 1587, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120229] = { id = 120229, Voice = '', Text = '冒险者，你没受伤吧？', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120228] = { id = 120228, Voice = '', Text = '想继续听下去吗？那就帮我消灭一些[c][ffff00]巨石怪[-][/c]吧。我身上的伤口就是拜他们所赐呢。', Speaker = 1587, Option = '如你所愿', Action = { num = 1, actionid = 201,},}, 
	[120227] = { id = 120227, Voice = '', Text = '大陆锻造第一人哈沃德，带着无尽的荣耀隐居于梦罗克。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120226] = { id = 120226, Voice = '', Text = '不久后，衣衫褴褛的哈沃德回到了普隆德拉，他打造出的武器令人惊叹，众人终于认可了他的地位。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120225] = { id = 120225, Voice = '', Text = '传闻锻造进行的最后一天，风雷声大作，仿佛众神的怒吼，众神也不希望如此强大的武器问世。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120224] = { id = 120224, Voice = '', Text = '但是这足够他收集到足够的材料，进行他的锻造。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120223] = { id = 120223, Voice = '', Text = '他是一个天才，几年研究就完美研究出了添加能量晶石的方法，代价就是武器的寿命大大缩短。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120222] = { id = 120222, Voice = '', Text = '他想到一个绝妙的办法，就是试着在武器中加入强大的能量晶石，使得不会武技的人同样能够发出强大的攻击力。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120221] = { id = 120221, Voice = '', Text = '他没有力量，甚至连一只野狼都无法击败，他却倔强得不肯学习铁匠的战斗技巧。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120220] = { id = 120220, Voice = '', Text = '他离开了师傅，开始行走于大陆，同时锻炼着自己的打造技艺。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120219] = { id = 120219, Voice = '', Text = '哈沃德拒绝了师傅的好意，因为他知道只要打造出堪比神器的武器一样可以成为神工匠。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120218] = { id = 120218, Voice = '', Text = '年轻的哈沃德啊，放弃吧，没有铁匠的武力你是无法成为神工匠的，安心继承我的衣钵吧。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120217] = { id = 120217, Voice = '', Text = '从人人仰望的天才，变成了被众人的笑柄。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120216] = { id = 120216, Voice = '', Text = '干的不错，我就继续讲故事了。', Speaker = 1587, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120215] = { id = 120215, Voice = '', Text = '你终于回来了。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120214] = { id = 120214, Voice = '', Text = '想知道接下来的故事吗？那就帮我弄一些[c][ffff00]雪勒结晶[-][/c]来吧。', Speaker = 1587, Option = '好的', Action = { num = 1, actionid = 201,},}, 
	[120213] = { id = 120213, Voice = '', Text = '每年都参加职业考核，却每年都失望而归。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120212] = { id = 120212, Voice = '', Text = '但是他没有媲美神工匠的武力，一直得不到协会的认可。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120211] = { id = 120211, Voice = '', Text = '王国甚至希望他能够专职于为王室服务。而哈沃德拒绝了，他的目的只有一个，那就是成为神工匠。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120210] = { id = 120210, Voice = '', Text = '他早已能够打造出精品的武器，他的名气早已传遍了大陆。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120209] = { id = 120209, Voice = '', Text = '但是哈沃德只继承了一半的技艺，那就是锻造。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120208] = { id = 120208, Voice = '', Text = '哈沃德在师傅的眼中是一个不折不扣的天才，师傅希望他能够继承他全部的技艺。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120207] = { id = 120207, Voice = '', Text = '哈沃德是铁匠的异类，他放弃了铁匠强大的攻击能力，专精于锻造。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120206] = { id = 120206, Voice = '', Text = '铁匠闻名于大陆的原因不仅仅是能够锻造强大的武器，还在于铁匠强大的攻击能力。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120205] = { id = 120205, Voice = '', Text = '烈酒与锻造，这就是哈沃德全部的生命。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120204] = { id = 120204, Voice = '', Text = '今天我就来讲讲神工匠哈沃德的故事。', Speaker = 1587, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120203] = { id = 120203, Voice = '', Text = '唔…这是我要的[c][ffff00]幸运料理B[-][/c]吗？', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120202] = { id = 120202, Voice = '', Text = '咦？这点加勒结晶可不够呢…', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120201] = { id = 120201, Voice = '', Text = '谢谢你，冒险者。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120200] = { id = 120200, Voice = '', Text = '你回来啦？我的[c][ffff00]幸运料理B[-][/c]呢？', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120199] = { id = 120199, Voice = '', Text = '故事就讲到这里。帮我买一个[c][ffff00]幸运料理B[-][/c]吧。', Speaker = 1587, Option = '好的', Action = { num = 1, actionid = 201,},}, 
	[120198] = { id = 120198, Voice = '', Text = '而作为刺客公会的首领，他也赢得了“刺杀之王”的美名。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120197] = { id = 120197, Voice = '', Text = '在他的训练与培养下，刺客公会日益强大。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120196] = { id = 120196, Voice = '', Text = '而无名者早已回到公会，接受着刺客们的赞誉。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120195] = { id = 120195, Voice = '', Text = '没有了鹗枭男爵绝对力量的压制，古城又一次陷入了领土之争。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120194] = { id = 120194, Voice = '', Text = '不久，古城再次陷入暴乱。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120193] = { id = 120193, Voice = '', Text = '干的不错，我问到了它们血液的酸臭味儿，那么我继续开始讲述刺杀之王的故事了。', Speaker = 1587, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120192] = { id = 120192, Voice = '', Text = '冒险者，你没受伤吧？', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120191] = { id = 120191, Voice = '', Text = '想继续听下去吗？那就帮我消灭一些[c][ffff00]摩卡[-][/c]吧。我身上的伤口就是拜他们所赐呢。', Speaker = 1587, Option = '如你所愿', Action = { num = 1, actionid = 201,},}, 
	[120190] = { id = 120190, Voice = '', Text = '他感到他的力量在流逝，而刺杀者早已遁入黑暗，悄然离去。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120189] = { id = 120189, Voice = '', Text = '叮——声音如瓷器破碎般清脆明亮，惊醒地鹗枭男爵带着无比的愤怒，寻找着偷袭者。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120188] = { id = 120188, Voice = '', Text = '黑暗中闪过的刀光，如同星空中划过的流星，带着刺客千年不朽传承的决意，向着鹗枭男爵的心核悍然刺下。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120187] = { id = 120187, Voice = '', Text = '没有比这更加完美的刺杀时机了，臣服于暗影之中，接受均衡的裁决吧！', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120186] = { id = 120186, Voice = '', Text = '他心里已有判断，享用了美酒与美食，在无尽的膜拜中入梦。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120185] = { id = 120185, Voice = '', Text = '鹗枭男爵，一个不输于黑暗之王的存在，他的力量无比强大！', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120184] = { id = 120184, Voice = '', Text = '端坐于王座的恶魔，俯视着臣服于他的子民。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120183] = { id = 120183, Voice = '', Text = '这似曾相识的城市，如今已经成为魔物的天堂。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120182] = { id = 120182, Voice = '', Text = '拥抱暗影，或是死于黑暗，这就是我们刺客的宿命！', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120181] = { id = 120181, Voice = '', Text = '但是刺客公会的诸位，还请记住，没有人能够从暗影之中逃脱。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120180] = { id = 120180, Voice = '', Text = '维护秩序是我们的信仰，恶魔就交给我吧。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120179] = { id = 120179, Voice = '', Text = '干的不错，我就继续讲故事了。', Speaker = 1587, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120178] = { id = 120178, Voice = '', Text = '你终于回来了。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120177] = { id = 120177, Voice = '', Text = '想继续听下去吗？那就帮我弄一些[c][ffff00]加勒结晶[-][/c]来吧。', Speaker = 1587, Option = '好的', Action = { num = 1, actionid = 201,},}, 
	[120176] = { id = 120176, Voice = '', Text = '它无比强大，为了维护平衡我们已经损失了许多优秀的刺客，现在我们应该怎么做？', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120175] = { id = 120175, Voice = '', Text = '尊贵的无名者大人，先贤指引着我们前进的方向，古城的恶魔早已苏醒。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120174] = { id = 120174, Voice = '', Text = '遗忘的回忆就由自己的刀锋找出来，过去的羁绊就由自己的利刃斩断！', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120173] = { id = 120173, Voice = '', Text = '所有人都称呼他为无名者大人，他坦然接受。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120172] = { id = 120172, Voice = '', Text = '传奇刺客艾勒梅斯视他为一生追逐的目标。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120171] = { id = 120171, Voice = '', Text = '从秘境中走出，到成为地位尊崇的首领，他只用了三天时间。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120170] = { id = 120170, Voice = '', Text = '这便是他脑中仅存的记忆，忘却了本名，忘却了身世，却忘不掉所发的誓言。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120169] = { id = 120169, Voice = '', Text = '发誓让刺客公会成为大陆上秩序的维护者，发誓让那些平衡破坏者付出血的代价！', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120168] = { id = 120168, Voice = '', Text = '看着诸位先贤，用你的血液起誓，发誓用一生来维护世间力量的平衡。', Speaker = 1587, Option = '', Action = _EmptyTable,}, 
	[120167] = { id = 120167, Voice = '', Text = '今天我就来讲讲刺客公会无名者的故事。', Speaker = 1587, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120166] = { id = 120166, Voice = '', Text = '咦？这是我要的[c][ffff00]图纸[-][/c]吗？', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120165] = { id = 120165, Voice = '', Text = '咦？这点[c][ffff00]腐烂绷带[-][/c]可不够呢…', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120164] = { id = 120164, Voice = '', Text = '唔，有了这[c][ffff00]腐烂绷带[-][/c]，我想我的生活会变的更加宽裕，谢谢你，冒险者。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120163] = { id = 120163, Voice = '', Text = '冒险者你回来的可真快！[c][ffff00]腐烂绷带[-][/c]收集好了么？', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120162] = { id = 120162, Voice = '', Text = '好了冒险者，故事你也已经听完了。作为报酬，我希望你能够帮我取得一些[c][ffff00]腐烂绷带[-][/c]。', Speaker = 1583, Option = '交给我吧。', Action = { num = 1, actionid = 201,},}, 
	[120161] = { id = 120161, Voice = '', Text = '但是依斯鲁得的民众会永远记得这位勇猛不屈的骑士，王国之盾——休朗克。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120160] = { id = 120160, Voice = '', Text = '而现在，休朗克为了训练更多的骑士加入了冒险者工会，成为了一名骑士导师。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120159] = { id = 120159, Voice = '', Text = '亡灵终退，休朗克带着骄傲倒下——王国议会赐予王国之盾的称号，以纪念休朗克的功绩。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120158] = { id = 120158, Voice = '', Text = '啊——汤玛斯！总有一天，我会让你变成亡灵！让你的灵魂永受折磨！', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120157] = { id = 120157, Voice = '', Text = '亡灵之主啊！在我们的圣光下，你的阴谋都将粉碎！', Speaker = 1583, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120156] = { id = 120156, Voice = '', Text = '该死的罗达蛙！啊，原谅我的失态，我要继续讲故事了。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120155] = { id = 120155, Voice = '', Text = '依斯鲁得的沉船里充斥着亡灵与罗达蛙，对！尤其是[c][ffff00]罗达蛙[-][/c]，能不能帮我干掉这些肮脏的魔物？', Speaker = 1583, Option = '愿意。', Action = { num = 1, actionid = 201,},}, 
	[120154] = { id = 120154, Voice = '', Text = '恶魔无法引诱我，宣誓的那一天我便发誓成为王国民众的盾，不用多说，踏过我的尸体吧！亡灵之王无言，挥手下令进攻。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120153] = { id = 120153, Voice = '', Text = '你应该明白，以你这卑微的力量，是无法与我抗衡的！', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120152] = { id = 120152, Voice = '', Text = '卡德斯轻叹：勇敢的骑士啊，我敬重你，从过道让开，我可以让你安全的离开。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120151] = { id = 120151, Voice = '', Text = '又三天，孤高的骑士拄着断剑，破碎的铁甲遮不住不屈的身影。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120150] = { id = 120150, Voice = '', Text = '来吧！亡灵之守护者！骑士的精神永不磨灭！进攻依斯鲁得？踏过我的尸体！', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120149] = { id = 120149, Voice = '', Text = '休朗克看着遍地骑士的尸体，抬头止住快溢出的泪水，坚定地看着面前的亡灵之王。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120148] = { id = 120148, Voice = '', Text = '亡灵嘶吼着，夺取骑士们的生命，骑士越来越少，而亡灵却丝毫不减，大战持续了三天三夜。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120147] = { id = 120147, Voice = '', Text = '为了依斯鲁得所有手无寸铁的民众，战斗！为了我们皇家骑士团的荣誉，战斗！在圣光的照耀下，灭掉眼前的敌人！', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120146] = { id = 120146, Voice = '', Text = '英勇的骑士们啊！可曾记得在众神神像下的誓言？', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120145] = { id = 120145, Voice = '', Text = '休朗克带领骑士团死守依斯鲁得的甬道，不死的军团带着无尽的怨恨，疯狂冲击骑士团的阵地。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120144] = { id = 120144, Voice = '', Text = '皇家骑士团接到命令，务必守住依斯鲁得，等待增援。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120143] = { id = 120143, Voice = '', Text = '亡灵之守护者卡德斯自海上出现，亡灵的大军无声无息的包围了依斯鲁得。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120142] = { id = 120142, Voice = '', Text = '多谢你冒险家，那么我就继续开始讲述故事了。', Speaker = 1583, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120141] = { id = 120141, Voice = '', Text = '她看到我为她做的发箍该多开心啊！', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120140] = { id = 120140, Voice = '', Text = '对了，能帮我弄到一点[c][ffff00]发箍图纸[-][/c]吗？我想制作一个送给我心爱的女子。', Speaker = 1583, Option = '我这就去', Action = { num = 1, actionid = 201,},}, 
	[120139] = { id = 120139, Voice = '', Text = '微光的雕像，不朽的传承，单膝跪下的骑士，宣告着万古不变的誓言，米尔特家族终于出现了第二位骑士。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120138] = { id = 120138, Voice = '', Text = '我发誓帮助我的兄弟骑士，我发誓真诚地对待我的朋友，我发誓——将对所爱至死不渝！', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120137] = { id = 120137, Voice = '', Text = '我发誓帮助任何向我求助的人，我发誓不伤害任何妇人！', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120136] = { id = 120136, Voice = '', Text = '我发誓善待弱者，我发誓勇敢地对抗强暴，我发誓抗击一切错误，我发誓为手无寸铁的人战斗！', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120135] = { id = 120135, Voice = '', Text = '我，休朗克，在此宣誓：强敌当前，不畏不惧，果敢忠义，无愧众神，忠耿正直，宁死不屈，保护弱者，无违天理！', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120134] = { id = 120134, Voice = '', Text = '休朗克，在众神的神像面前，你可愿意，以谦卑、诚实、怜悯、英勇、公正、牺牲、荣誉、灵魂为信条，成为一位忠诚于王国的骑士？', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120133] = { id = 120133, Voice = '', Text = '那么，今天我们就来讲讲王国之盾——休朗克！', Speaker = 1583, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120132] = { id = 120132, Voice = '', Text = '咦？这是我要的图纸吗？', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120131] = { id = 120131, Voice = '', Text = '咦？这点[c][ffff00]杰勒比结晶[-][/c]可不够呢…', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120130] = { id = 120130, Voice = '', Text = '唔，有了这[c][ffff00]杰勒比结晶[-][/c]，我想我的生活会变的更加宽裕，谢谢你，冒险者。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120129] = { id = 120129, Voice = '', Text = '冒险者你回来的可真快！[c][ffff00]杰勒比结晶[-][/c]收集好了么？', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120128] = { id = 120128, Voice = '', Text = '好了冒险者，故事你也已经听完了。作为报酬，我希望你能够帮我取得一些[c][ffff00]杰勒比结晶[-][/c]。', Speaker = 1583, Option = '交给我吧。', Action = { num = 1, actionid = 201,},}, 
	[120127] = { id = 120127, Voice = '', Text = '所以她来到了冒险者工会，成为了一名牧师导师，这就是圣诗之歌——莱伊娜丝。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120126] = { id = 120126, Voice = '', Text = '而现在她又有了新的目标，为普隆德拉培养更多的牧师。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120125] = { id = 120125, Voice = '', Text = '一个纯洁如水晶之女子，救着一切因疾病，战斗而受伤之人，人们尊敬她之功绩，称为：圣诗之歌', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120124] = { id = 120124, Voice = '', Text = '普隆德拉的贫民区，亦或是危险的斐扬丛林边，王国皇家骑士团的生死训练场，都流传着一个美丽的故事。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120123] = { id = 120123, Voice = '', Text = '透过汝之双眼，诸神意志向我诉说，黑暗与亡灵的交战之时，便是汝之家族延续之时。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120122] = { id = 120122, Voice = '', Text = '老猎人叹息，家族之荣光又该如何维护？', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120121] = { id = 120121, Voice = '', Text = '诸神之意志不可违逆，如此就是后果，老猎人啊，把莱伊娜丝交由我来教导，可保圣女之平安。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120120] = { id = 120120, Voice = '', Text = '那冷漠如刀剑的眼神，终于让老猎人想起大主教的话，击昏精疲力尽的莱伊娜丝，神圣之女终是堕落了么？', Speaker = 1583, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120119] = { id = 120119, Voice = '', Text = '这么快就回来了？我老远就已经闻到你身上的血腥气息了。好吧，接着说故事。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120118] = { id = 120118, Voice = '', Text = '说起来，当初我在沉船受到了毒蘑菇的攻击，冒险者你现在愿意帮我教训教训那些可恶的[c][ffff00]毒蘑菇[-][/c]吗？', Speaker = 1583, Option = '愿意。', Action = { num = 1, actionid = 201,},}, 
	[120117] = { id = 120117, Voice = '', Text = '原本洁白无暇如同神女的莱伊娜丝，如今黑气旋绕，空洞的眼神注视着他', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120116] = { id = 120116, Voice = '', Text = '老猎人来到了丛林，眼前的景象令他浑身颤抖。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120115] = { id = 120115, Voice = '', Text = '感受着巨大的惊恐，莱伊娜丝彻底崩溃。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120114] = { id = 120114, Voice = '', Text = '群狼环绕，莱伊娜丝哭喊着，却激起了群狼兴奋的嚎叫。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120113] = { id = 120113, Voice = '', Text = '凄厉的惨叫，野兽的狂欢，莱伊娜丝很快就无法逃离。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120112] = { id = 120112, Voice = '', Text = '为了家族的荣耀，我必须这么做，老猎人叹息着离去。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120111] = { id = 120111, Voice = '', Text = '老猎人带着她来普隆德拉城外的丛林：年轻的莱伊娜丝啊！', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120110] = { id = 120110, Voice = '', Text = '她的心灵如同新生的幼儿般纯洁，她的善良如同清凉的微风般吹拂。我必须改变她的现状！', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120109] = { id = 120109, Voice = '', Text = '她无法背负家族的荣誉，老猎人看着全力依然无法拉动弓弦的莱伊娜丝。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120108] = { id = 120108, Voice = '', Text = '多谢你冒险家。那么我就继续开始讲述故事了。', Speaker = 1583, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120107] = { id = 120107, Voice = '', Text = '冒险家，你回来得真快。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120106] = { id = 120106, Voice = '', Text = '唔…能否帮我向微笑小姐买一个[c][ffff00]眼罩图纸[-][/c]？我带上眼罩一定很酷！', Speaker = 1583, Option = '我这就去', Action = { num = 1, actionid = 201,},}, 
	[120105] = { id = 120105, Voice = '', Text = '纯白的心灵终将堕落，邪恶与正义的执念彼此交合，到那时，可以来找我。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120104] = { id = 120104, Voice = '', Text = '赞美诸神，看在莱伊娜丝的份上，告诉你一个消息。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120103] = { id = 120103, Voice = '', Text = '尊敬的大主教啊，原谅我的自私，猎人之血不能断绝！', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120102] = { id = 120102, Voice = '', Text = '她如同瑰丽的白玉，她是上帝赐予我等最珍贵的礼物！', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120101] = { id = 120101, Voice = '', Text = '大主教轻语：这是仁慈之神的赐福啊，赞美诸神，莱伊娜丝之父啊，汝可愿把年幼的莱伊娜丝交由我来教导？', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120100] = { id = 120100, Voice = '', Text = '以诸神之名义，给予新生的莱伊娜丝众神之祝福，耀眼白光愈演愈烈。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[120099] = { id = 120099, Voice = '', Text = '那么，今天我们就来讲讲圣诗之歌——莱伊娜丝！', Speaker = 1583, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120098] = { id = 120098, Voice = '', Text = '咦？这是我要的[c][ffff00]苍蝇翅膀[-][/c]吗？', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120097] = { id = 120097, Voice = '', Text = '咦？这点[c][ffff00]杰勒比结晶[-][/c]可不够呢…', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120096] = { id = 120096, Voice = '', Text = '唔，有了这[c][ffff00]杰勒比结晶[-][/c]，我想我的生活会变的更加宽裕，谢谢你，冒险者。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120095] = { id = 120095, Voice = '', Text = '冒险者你回来的可真快！[c][ffff00]杰勒比结晶[-][/c]收集好了么？', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120094] = { id = 120094, Voice = '', Text = '好了冒险者，故事你也已经听完了。作为报酬，我希望你能够帮我取得一些[c][ffff00]杰勒比结晶[-][/c]。', Speaker = 1582, Option = '交给我吧。', Action = { num = 1, actionid = 201,},}, 
	[120093] = { id = 120093, Voice = '', Text = '这就是白银微风——卡巴克！', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120092] = { id = 120092, Voice = '', Text = '同时他又利用公会强大的情报能力，寻找着黑暗之王的踪迹，以期望有朝一日能够手刃仇敌。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120091] = { id = 120091, Voice = '', Text = '现如今，他来到了普隆德拉，听从莱伊娜丝的建议加入了冒险者公会，成为冒险者公会的猎人导师。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120090] = { id = 120090, Voice = '', Text = '各地流传着白银微风的美名。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120089] = { id = 120089, Voice = '', Text = '他的足迹踏遍了整个世界，如同学习翱翔的雏鹰，渐渐羽翼丰满。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120088] = { id = 120088, Voice = '', Text = '斐扬的密林，妙勒尼的山脉，梦罗克的沙漠，依斯鲁得的海底。', Speaker = 1582, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120087] = { id = 120087, Voice = '', Text = '该死的猴子！啊，原谅我的失态，我要继续讲故事了。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120086] = { id = 120086, Voice = '', Text = '该死，溜溜猴又偷走了我的帽子！冒险者你愿意帮我教训教训那些[c][ffff00]猴子[-][/c]吗？', Speaker = 1582, Option = '愿意。', Action = { num = 1, actionid = 201,},}, 
	[120085] = { id = 120085, Voice = '', Text = '你——可愿成为我的弟子？我愿意成为你的弟子。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120084] = { id = 120084, Voice = '', Text = '孩子，想手刃强大的黑暗之王吗？我在你身上看到了猎人崛起的契机', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120083] = { id = 120083, Voice = '', Text = '黑暗与亡灵的交战，我只是个被波及的弱者。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120082] = { id = 120082, Voice = '', Text = '叹道：孩子——我看到你眼中的仇恨，你到底有何仇怨深埋于心？', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120081] = { id = 120081, Voice = '', Text = '那个如同天使般圣洁的女子拯救了他的性命，女子身后的老者凝视着他。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120080] = { id = 120080, Voice = '', Text = '仁慈的神——我愿于此祈求，苦难与悲痛尽去，和平与光明到来——神圣救赎。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120079] = { id = 120079, Voice = '', Text = '可曾记得，百年之前的恩怨？如今我回来了，结束你我之仇怨！城堡的地板裂开，如同巨兽的口吞噬了他的身体。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120078] = { id = 120078, Voice = '', Text = '大地在震颤，眼中的焦距渐渐凝聚，黑暗之王啊——许久未见，可还记得我是谁？亡灵之守护者卡德斯，来此何干？', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120077] = { id = 120077, Voice = '', Text = '双眼失去了焦距，他感觉不到疼痛了。待吾归来，就是你命丧之时！', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120076] = { id = 120076, Voice = '', Text = '蝼蚁——再有一年，汝就要迎来生命的终结，汝还有什么遗言吗？', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120075] = { id = 120075, Voice = '', Text = '多谢你冒险家，那么我就继续开始讲述故事了。', Speaker = 1582, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120074] = { id = 120074, Voice = '', Text = '冒险家，你回来得真快。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120073] = { id = 120073, Voice = '', Text = '对了，能帮我弄到一点[c][ffff00]苍蝇翅膀[-][/c]吗？', Speaker = 1582, Option = '我这就去', Action = { num = 1, actionid = 201,},}, 
	[120072] = { id = 120072, Voice = '', Text = '心中怒吼：总有一天，我要亲手把你送入坟墓！让你在无尽的痛苦中忏悔所犯下的过错！', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120071] = { id = 120071, Voice = '', Text = '啊——痛苦的声音，黑暗囚笼中，他蜷缩着身体，忍受身上剧烈的疼痛。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120070] = { id = 120070, Voice = '', Text = '是谁，允许你直视本王！触犯本王的威严，吾必给予你世间最惨烈的折磨！', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120069] = { id = 120069, Voice = '', Text = '他被黑色烟雾包裹，离开了自幼生活的村落，仇恨的眼神，颤抖的双肩。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120068] = { id = 120068, Voice = '', Text = '黑暗之风吹袭而来，一个低沉的声音响彻村落，尔等的祭品吾已收下，下个十年吾会再次登门。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120067] = { id = 120067, Voice = '', Text = '伟大的黑暗之王啊！这是献上的祭品，求求您，庇佑我们村落吧！', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120066] = { id = 120066, Voice = '', Text = '那么，今天我们就来讲讲白银微风——卡巴克！', Speaker = 1582, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120065] = { id = 120065, Voice = '', Text = '咦？这是我要的[c][ffff00]天地树叶子[-][/c]吗？', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120064] = { id = 120064, Voice = '', Text = '咦？这点[c][ffff00]柔毛[-][/c]可不够呢…', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120063] = { id = 120063, Voice = '', Text = '唔，有了这[c][ffff00]柔毛[-][/c]，我想我的生活会变的更加宽裕，谢谢你，冒险者。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120062] = { id = 120062, Voice = '', Text = '冒险者你回来的可真快！[c][ffff00]柔毛[-][/c]收集好了么？', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120061] = { id = 120061, Voice = '', Text = '好了冒险者，故事你也已经听完了。作为报酬，我希望你能够帮我取得一些[c][ffff00]柔毛[-][/c]。', Speaker = 1582, Option = '交给我吧。', Action = { num = 1, actionid = 201,},}, 
	[120060] = { id = 120060, Voice = '', Text = '她加入了冒险者公会，成为了刺客职业的导师。这就是暗影之手——伊丝米儿的故事。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120059] = { id = 120059, Voice = '', Text = '而现在，冒险者公会的希盖伊兹找到了暗影之手，伊丝米儿决定为王国奉献自己的一点力量。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120058] = { id = 120058, Voice = '', Text = '民众为赐予他们新生的英雄取了一个响亮的称号，暗影之手·伊丝米儿！', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120057] = { id = 120057, Voice = '', Text = '，生活在古堡阴影下的民众跪拜着欢呼，而她早已带着解脱的笑容离开了。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120056] = { id = 120056, Voice = '', Text = '恶魔的惨嚎宣示着一场殊死搏斗的完结，熊熊火焰笼罩着整座古堡。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120055] = { id = 120055, Voice = '', Text = '愚蠢的恶魔啊，还记得你所犯下的罪孽？今天你罪恶的生命，就由我来终结！', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120054] = { id = 120054, Voice = '', Text = '恶魔愤怒地大吼：罪人！竟然打扰伟大的德古拉进食！', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120053] = { id = 120053, Voice = '', Text = '机会来了！她无声无息的向恶魔移动，碎裂的杯子，断掉地手臂。', Speaker = 1582, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120052] = { id = 120052, Voice = '', Text = '这么快就回来了？我老远就已经闻到你身上的血腥气息了。好吧，接着说故事。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120051] = { id = 120051, Voice = '', Text = '说起来，当初我也吃了不少魔物的苦，冒险者你愿意帮我教训教训那些魔物吗？', Speaker = 1582, Option = '愿意。', Action = { num = 1, actionid = 201,},}, 
	[120050] = { id = 120050, Voice = '', Text = '如同潜伏着毒蛇，等待着一击必杀的契机。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120049] = { id = 120049, Voice = '', Text = '冷冽的目光注视着面前这个状若疯狂的恶魔。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120048] = { id = 120048, Voice = '', Text = '感叹着一口喝掉杯中之物，她隐于墙角，不发出任何声音，一如年幼的她。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120047] = { id = 120047, Voice = '', Text = '美妙的鲜血，你真是始祖赐予我最完美的礼物！', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120046] = { id = 120046, Voice = '', Text = '摇晃着高脚杯，红色的血液在杯中不断地旋转着，伸长的舌头舔舐着杯边遗留的血液，嘴角露出残忍的笑意。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120045] = { id = 120045, Voice = '', Text = '嗅着空中弥散地血腥气息，充满罪恶的灵魂啊，接受均衡的制裁吧。她的身影逐渐变淡，直至消失。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120044] = { id = 120044, Voice = '', Text = '荒芜的坟场，萧条的树木，以及远处的古堡。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120043] = { id = 120043, Voice = '', Text = '事到如今，她终于有了足够的力量，去完成积压数十年的怨恨。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120042] = { id = 120042, Voice = '', Text = '多谢你冒险家。那么我就继续开始讲述故事了。', Speaker = 1582, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120041] = { id = 120041, Voice = '', Text = '冒险家，你回来得真快。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120040] = { id = 120040, Voice = '', Text = '故事讲到这里，冒险者，我需要一些[c][ffff00]天地树叶子[-][/c]，你能帮我弄来吗？', Speaker = 1582, Option = '我这就去', Action = { num = 1, actionid = 201,},}, 
	[120039] = { id = 120039, Voice = '', Text = '那个恶魔——德古拉男爵，放肆地屠戮着她的家人，她躲在床下，不敢发出声音，复仇的火焰在心头滋生。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120038] = { id = 120038, Voice = '', Text = '那天夜里，哭喊与恐惧交织，她恐惧地睁大双眼，看着面前的人一个个地死去。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120037] = { id = 120037, Voice = '', Text = '那个地狱来的恶魔，于血与火之中交织着放肆地嘲笑，弱者！没有生存的权利！', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120036] = { id = 120036, Voice = '', Text = '一个念头在她心中挥之不去，叫嚣着：铭记着的痛苦，现在到了宣泄的时候了！', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120035] = { id = 120035, Voice = '', Text = '继承了梦罗克刺客公会遗址中先贤的力量，现在的她无比强大。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120034] = { id = 120034, Voice = '', Text = '她缓缓地睁开双眼，从石床上走下，感受着身上澎湃的力量，如同脱缰之马，在血液中游荡。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120033] = { id = 120033, Voice = '', Text = '散发着耀眼光芒的符文悄然消散，石质祭坛散发着微弱的幽光。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120032] = { id = 120032, Voice = '', Text = '那么，今天我们就来讲讲暗影之手——伊丝米儿！', Speaker = 1582, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120031] = { id = 120031, Voice = '', Text = '咦？这点[c][ffff00]腐烂绷带[-][/c]可不够呢……', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120030] = { id = 120030, Voice = '', Text = '唔，卖了这腐烂绷带，我想我会更加光鲜，而不像是一个落魄的吟游诗人了呢。谢谢你，冒险者。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120029] = { id = 120029, Voice = '', Text = '冒险者你回来的可真快！腐烂绷带收集好了么？', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120028] = { id = 120028, Voice = '', Text = '好了冒险者，故事你也已经听完了。作为报酬，我希望你能够帮我取得一些[c][ffff00]腐烂绷带[-][/c]。', Speaker = 1582, Option = '交给我吧。', Action = { num = 1, actionid = 201,},}, 
	[120027] = { id = 120027, Voice = '', Text = '这就是现在最伟大的铁匠忽克连的故事。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120026] = { id = 120026, Voice = '', Text = '而现在，忽克连要把这武器精炼的技艺带给更多人，他来到了普隆德拉，成为了这里的铁匠。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120025] = { id = 120025, Voice = '', Text = '当他带着卡仑的头颅来到老师面前，哈沃德都为自己的弟子感到自豪。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120024] = { id = 120024, Voice = '', Text = '完美的武器深深的刺入卡仑的胸膛，野兽最后的哀嚎宣示着他的胜利。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120023] = { id = 120023, Voice = '', Text = '又一次，他带着他满意的作品来到卡仑面前，这次，他的武器无可匹敌，势不可挡。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120022] = { id = 120022, Voice = '', Text = '忽克连于地心熔岩中锤炼神之金属原石，他于极地冰川中去除原石杂质，又三年，他成功的提炼出了神之金属。', Speaker = 1582, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120021] = { id = 120021, Voice = '', Text = '这么快就回来了？我老远就已经闻到你身上的血腥气息了。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120020] = { id = 120020, Voice = '', Text = '说起来，忽克连在这几年里受到了不少魔物的伤害，他曾经拜托我，希望我找人替他复仇，冒险者你愿意接受这个活计吗？', Speaker = 1582, Option = '愿意。', Action = { num = 1, actionid = 201,},}, 
	[120019] = { id = 120019, Voice = '', Text = '忽克连再次逃离这里，他本可以带着绒毛回去，但他选择了继续试验，直到锻造出真正的完美的武器。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120018] = { id = 120018, Voice = '', Text = '与卡仑的激战中，他怒吼砍断了卡仑的绒毛，但是却不能够消灭卡仑。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120017] = { id = 120017, Voice = '', Text = '他再次带着修复好并且强大很多的完美武器，踏上了路途。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120016] = { id = 120016, Voice = '', Text = '三年间，他走遍了整个王国，最终在斐扬的某处洞穴中，找到了神之金属原石。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120015] = { id = 120015, Voice = '', Text = '他看到了一本古籍，上面写着：众神的武器皆是由神之金属锻造而成，神之金属使武器拥有了神性的光辉。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120014] = { id = 120014, Voice = '', Text = '可是完美的武器已经采用这世上最珍贵的材料，怎样才能使武器更加锋利呢？', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120013] = { id = 120013, Voice = '', Text = '忽克连无法相信，带着断裂的武器逃跑了，这是耻辱啊！忽克连！他对自己说道。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120012] = { id = 120012, Voice = '', Text = '令人心碎的声音响起，砰——完美的武器竟然断裂了！', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120011] = { id = 120011, Voice = '', Text = '他来到那片遥远的土地，忽克连一声怒吼，紧握手中的武器，向着沉睡的卡仑斩去。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120010] = { id = 120010, Voice = '', Text = '唔~多谢你冒险家。那么我就继续开始讲述故事了。', Speaker = 1582, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120009] = { id = 120009, Voice = '', Text = '冒险家，你回来得真快。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120008] = { id = 120008, Voice = '', Text = '先讲到这里，你能够帮我买一块[c][ffff00]蓝色魔力矿石[-][/c]吗？', Speaker = 1582, Option = '我这就去', Action = { num = 1, actionid = 201,},}, 
	[120007] = { id = 120007, Voice = '', Text = '他开始收集各种珍稀的材料，从天外陨铁到地心矿石，历时三年打造出了最完美的武器，忽克连迷醉地看着武器，信心满满。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120006] = { id = 120006, Voice = '', Text = '忽克连自信地拍着胸脯，没问题，我会亲手斩下它的头颅！', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120005] = { id = 120005, Voice = '', Text = '如果你铸造的武器能够带走他的绒毛，你就可以自豪地出师了。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120004] = { id = 120004, Voice = '', Text = '它的绒毛如同闪耀着的金刚石，它的骨头如同璀璨的结晶，它刀剑不入，它无比坚硬。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120003] = { id = 120003, Voice = '', Text = '哈沃德发现自己弟子内心的傲慢，他说：年轻的忽克连啊，你可知道有片遥远的土地，有着一种魔物，名为卡仑。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120002] = { id = 120002, Voice = '', Text = '忽克连认为自己的技艺已经赶上了老师，他变得愈发自大。', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[120001] = { id = 120001, Voice = '', Text = '那么，今天我们就来讲讲普隆德拉最伟大的铁匠——忽克连！', Speaker = 1582, Option = '', Action = { num = 1, actionid = 200,},}, 
	[120000] = { id = 120000, Voice = '', Text = '咦？这是我要的[c][ffff00]蓝色魔力矿石[-][/c]吗？', Speaker = 1582, Option = '', Action = _EmptyTable,}, 
	[110058] = { id = 110058, Voice = '', Text = '常年在外，我没有办法当面表明心意，只有在靠这种古老而俗气的方式了。冒险者，你会帮我的，对吧？', Speaker = 1632, Option = '交给我吧。', Action = _EmptyTable,}, 
	[110057] = { id = 110057, Voice = '', Text = '不过，我还有一个小小的请求，你能帮我把这封信交到我心仪的女孩——[c][ffff00]礼仪骑士艾米[-][/c]手中吗？', Speaker = 1632, Option = '', Emoji = 26, Action = _EmptyTable,}, 
	[110056] = { id = 110056, Voice = '', Text = '干的漂亮！冒险者，你的实力足以得到E级冒险家的称号！', Speaker = 1632, Option = '', Action = _EmptyTable,}, 
	[110055] = { id = 110055, Voice = '', Text = '现在证明你的实力的时候到了！我要你解锁冒险手册中的三个被广大冒险者称之为MINI级别的魔物！这是一个挑战，冒险者，快去吧！', Speaker = 1632, Option = '', Action = _EmptyTable,}, 
	[110054] = { id = 110054, Voice = '', Text = '上面我说过，冒险者除了要有欢笑，还要有磨练！这样才是一个合格的冒险者。', Speaker = 1632, Option = '', Action = _EmptyTable,}, 
	[110053] = { id = 110053, Voice = '', Text = '你成功的收集到了我要的这三个物品，很不错，这个考核就算你通过了！下面该发布下一阶段的考核了。', Speaker = 1632, Option = '', Action = _EmptyTable,}, 
	[110052] = { id = 110052, Voice = '', Text = '你的身上没有足够的[c][ffff00]变身叶子[-][/c]，你还是赶快去找吧。', Speaker = 1632, Option = '好的', Action = _EmptyTable,}, 
	[110051] = { id = 110051, Voice = '', Text = '你的身上没有我要的[c][ffff00]伪装卷轴[-][/c]，你还是赶快去找吧。', Speaker = 1632, Option = '好的', Action = _EmptyTable,}, 
	[110050] = { id = 110050, Voice = '', Text = '你的身上没有我要的[c][ffff00]恶作剧魔棒——冰波利[-][/c]，你还是赶快去找吧。', Speaker = 1632, Option = '好的', Action = _EmptyTable,}, 
	[110049] = { id = 110049, Voice = '', Text = '干的不错，冒险者。你果然没让我失望！', Speaker = 1632, Option = '', Emoji = 20, Action = _EmptyTable,}, 
	[110048] = { id = 110048, Voice = '', Text = '冒险者，东西拿到了吗？给我看看！', Speaker = 1632, Option = '', Action = _EmptyTable,}, 
	[110047] = { id = 110047, Voice = '', Text = '去收集一些[c][ffff00]变身叶子[-][/c]，听说南门的稀有魔物狸猫身上就有，快去收集吧。', Speaker = 1632, Option = '明白', Action = _EmptyTable,}, 
	[110046] = { id = 110046, Voice = '', Text = '去收集一些[c][ffff00]伪装卷轴[-][/c]，如果你怕麻烦的话，各大玩具商都有卖的。快去吧。', Speaker = 1632, Option = '明白', Action = _EmptyTable,}, 
	[110045] = { id = 110045, Voice = '', Text = '去收集一些[c][ffff00]恶作剧魔棒——冰波利[-][/c]，如果你怕麻烦的话，各大玩具商都有卖的。快去吧。', Speaker = 1632, Option = '明白', Action = _EmptyTable,}, 
	[110044] = { id = 110044, Voice = '', Text = '听着，我需要的是一些[c][ffff00]恶作剧魔棒——冰波利[-][/c]、一些[c][ffff00]伪装卷轴[-][/c]以及一些[c][ffff00]变身叶子[-][/c]。', Speaker = 1632, Option = '好的', Action = _EmptyTable,}, 
	[110043] = { id = 110043, Voice = '', Text = '我需要一些玩具…不许笑！冒险中要有欢笑和泪水，这是调节冒险者心态最好用的道具。', Speaker = 1632, Option = '', Action = _EmptyTable,}, 
	[110042] = { id = 110042, Voice = '', Text = '唔，总之，上一个考核你完成得不错，现在我要教给你下一个任务了。', Speaker = 1632, Option = '', Action = _EmptyTable,}, 
	[110041] = { id = 110041, Voice = '', Text = '你的冒险手册中并没有记录，所以冒险者，你还是赶紧去拍吧。', Speaker = 1632, Option = '', Action = _EmptyTable,}, 
	[110040] = { id = 110040, Voice = '', Text = '这么快就拍到给我了？挺不错的嘛！', Speaker = 1632, Option = '', Action = _EmptyTable,}, 
	[110039] = { id = 110039, Voice = '', Text = '用幽灵相机拍到那个魔物就可以了，拍到之后，你的冒险手册里会有记录的。', Speaker = 1632, Option = '', Action = _EmptyTable,}, 
	[110038] = { id = 110038, Voice = '', Text = '只要拍到照片就可以了，如果你已经拍到过那就再好不过了。记住，拍幽灵魔物需要学习冒险技能[c][ffff00]幽灵相机[-][/c]！', Speaker = 1632, Option = '', Action = _EmptyTable,}, 
	[110037] = { id = 110037, Voice = '', Text = '还记得你F级考核的沉没之船吗？据说沉船深处有一个幽灵波利，我想请你调查出他的原型。', Speaker = 1632, Option = '', Action = _EmptyTable,}, 
	[110036] = { id = 110036, Voice = '', Text = '很好！单是你的这份勇气，我觉得你就一定不会失败！冒险就应该勇往直前！', Speaker = 1632, Option = '', Action = _EmptyTable,}, 
	[110035] = { id = 110035, Voice = '', Text = '听着冒险者，作为你的考官，我必须提醒你的是，考核非常危险。尽管如此，你也要接受考核吗？', Speaker = 1632, Option = '是的', Action = _EmptyTable,}, 
	[110034] = { id = 110034, Voice = '', Text = '冒险者？你是来参加E级考核的冒险者吧？我收到了卡卡罗给我的消息。', Speaker = 1632, Option = '是的', Action = _EmptyTable,}, 
	[110033] = { id = 110033, Voice = '', Text = '洛萨达克是一名强大的冒险者，据说现在他去某处山脉中探险去了。', Speaker = 1501, Option = '', Action = _EmptyTable,}, 
	[110032] = { id = 110032, Voice = '', Text = '如今你可以进行E级冒险者的试炼了。这回的考官是E级冒险者洛萨达克。', Speaker = 1501, Option = '', Action = _EmptyTable,}, 
	[110031] = { id = 110031, Voice = '', Text = '唔，有一段时间没见了吧？看来你又要进行冒险等级考核了。', Speaker = 1501, Option = '', Action = _EmptyTable,}, 
	[110030] = { id = 110030, Voice = '', Text = '做的不错冒险者，恭喜你成为冒险者公会F级冒险者！', Speaker = 1501, Option = '', Action = _EmptyTable,}, 
	[110029] = { id = 110029, Voice = '', Text = '这是你的试炼证明。去找卡卡罗完成进阶吧！', Speaker = 1631, Option = '', Action = _EmptyTable,}, 
	[110028] = { id = 110028, Voice = '', Text = '干得漂亮！我给你的任务你都完成的很好。你可以得到F级冒险者的荣耀了！', Speaker = 1631, Option = '', Action = _EmptyTable,}, 
	[110027] = { id = 110027, Voice = '', Text = '他们的破坏力很强大，我希望你能够干掉他们。', Speaker = 1631, Option = '', Action = _EmptyTable,}, 
	[110026] = { id = 110026, Voice = '', Text = '据我们调查，邪骸海盗中会出现一些能力远强于一般海盗的[c][ffff00]邪骇战士[-][/c]。', Speaker = 1631, Option = '', Action = _EmptyTable,}, 
	[110025] = { id = 110025, Voice = '', Text = '嗯，干得不错，年轻人。冒险者公会有你们肯定会越来越强大。', Speaker = 1631, Option = '', Action = _EmptyTable,}, 
	[110024] = { id = 110024, Voice = '', Text = '这些邪骸海盗对我们造成很大的威胁。其中以我希望你能清理一下沉船的[c][ffff00]邪骸海盗[-][/c]。', Speaker = 1631, Option = '', Action = _EmptyTable,}, 
	[110023] = { id = 110023, Voice = '', Text = '在寻找白幽灵的时候你也发现了，沉船现在沦为海盗的天堂了。', Speaker = 1631, Option = '', Action = _EmptyTable,}, 
	[110022] = { id = 110022, Voice = '', Text = '但是这只是试炼的一部分而已，接下来你还有更加艰难的挑战等着你。', Speaker = 1631, Option = '', Action = _EmptyTable,}, 
	[110021] = { id = 110021, Voice = '', Text = '原来是白幽灵，难怪我无法看到他。你干得不错。', Speaker = 1631, Option = '', Action = _EmptyTable,}, 
	[110020] = { id = 110020, Voice = '', Text = '如果可能的话，找出它并且消灭它，免得他祸害其他冒险者。', Speaker = 1631, Option = '', Action = _EmptyTable,}, 
	[110019] = { id = 110019, Voice = '', Text = '帮我调查出伤我的生物是什么，据我猜测应该是一种[c][ffff00]幽灵生物[-][/c]。', Speaker = 1631, Option = '', Action = _EmptyTable,}, 
	[110018] = { id = 110018, Voice = '', Text = '如你所见，我是在依斯鲁得养伤的。前几天在沉船上被不明生物袭击了。', Speaker = 1631, Option = '', Action = _EmptyTable,}, 
	[110017] = { id = 110017, Voice = '', Text = '原来如此，F级冒险者的试炼吗？正好我这里有些事情需要你调查。', Speaker = 1631, Option = '', Action = _EmptyTable,}, 
	[110016] = { id = 110016, Voice = '', Text = '冒险者？找我有什么事情吗？', Speaker = 1631, Option = '', Action = _EmptyTable,}, 
	[110015] = { id = 110015, Voice = '', Text = '不过可以给你一个小提示，他在[c][ffff00]某一座对普隆德拉来说非常重要的岛屿[-][/c]上。快去寻找他吧', Speaker = 1501, Option = '', Action = _EmptyTable,}, 
	[110014] = { id = 110014, Voice = '', Text = '他在哪里？这可不能告诉你，寻人也是冒险者的必修技能呢。', Speaker = 1501, Option = '', Action = _EmptyTable,}, 
	[110013] = { id = 110013, Voice = '', Text = '我看看…唔，你的实力可以进行F级试炼了。去找史蒂文·莱茵进行试炼吧！', Speaker = 1501, Option = '', Action = _EmptyTable,}, 
	[110012] = { id = 110012, Voice = '', Text = '唔，冒险者，没想到你这么快又来进行试炼了。', Speaker = 1501, Option = '', Action = _EmptyTable,}, 
	[110011] = { id = 110011, Voice = '', Text = '做的不错冒险者，恭喜你成为冒险者公会勘察员！', Speaker = 1501, Option = '', Action = _EmptyTable,}, 
	[110010] = { id = 110010, Voice = '', Text = '这是你的试炼证明。回去找冒险者公会长官吧，他会帮助你完成进阶的。', Speaker = 1630, Option = '', Action = _EmptyTable,}, 
	[110009] = { id = 110009, Voice = '', Text = '我收到消息了。干得不错，我很满意！你的试炼完成了。', Speaker = 1630, Option = '', Action = _EmptyTable,}, 
	[110008] = { id = 110008, Voice = '', Text = '我收到消息，这附近最近[c][ffff00]蝗虫泛滥[-][/c]。如果你能够处理好这件事，你的试炼就完成了。', Speaker = 1630, Option = '', Action = _EmptyTable,}, 
	[110007] = { id = 110007, Voice = '', Text = '勘察员试炼完成了？不不不，哪有这么简单。', Speaker = 1630, Option = '', Action = _EmptyTable,}, 
	[110006] = { id = 110006, Voice = '', Text = '这些皮猴子哈哈哈，真是可爱呢。', Speaker = 1630, Option = '', Action = _EmptyTable,}, 
	[110005] = { id = 110005, Voice = '', Text = '调查结果怎么样？', Speaker = 1630, Option = '他们抢镜', Action = _EmptyTable,}, 
	[110004] = { id = 110004, Voice = '', Text = '看到那边的溜溜猴了吗？它就是我的研究对象，他似乎有种[c][ffff00]抢镜的特性[-][/c]，你去调查看看吧。', Speaker = 1630, Option = '', Action = _EmptyTable,}, 
	[110003] = { id = 110003, Voice = '', Text = '勘察员的责任之一，就是研究各种魔物的特性。', Speaker = 1630, Option = '', Action = _EmptyTable,}, 
	[110002] = { id = 110002, Voice = '', Text = '你要参加勘察员的试炼？好吧，这可不是一个轻松的活计。', Speaker = 1630, Option = '', Action = _EmptyTable,}, 
	[110001] = { id = 110001, Voice = '', Text = '去找勘察员吧，只要完成了他的考核，你就可以成为冒险勘察员了！', Speaker = 1501, Option = '', Action = _EmptyTable,}, 
	[110000] = { id = 110000, Voice = '', Text = '冒险者你成长的很快啊！现在的你已经拥有足够的实力拥有更高贵的冒险者称号了。', Speaker = 1501, Option = '', Action = _EmptyTable,}, 
	[102319] = { id = 102319, Voice = '', Text = '这，就是大贤者巴特蒙德的故事。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102318] = { id = 102318, Voice = '', Text = '大贤者巴特蒙德，终究离开了雷根贝勒，从此再也没人见过他。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102317] = { id = 102317, Voice = '', Text = '决裂，最终就这样发生了。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102316] = { id = 102316, Voice = '', Text = '理念上的冲突，让大贤者与宝勒世伏争吵了三天三夜，谁也不肯向对方妥协。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102315] = { id = 102315, Voice = '', Text = '雷根贝勒有了过于庞大的野心，而宝勒世伏就是这份野心最好的体现。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102314] = { id = 102314, Voice = '', Text = '大贤者不愿意自己的研究，演变成战争的导火索，他认为雷根贝勒已经变了。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102313] = { id = 102313, Voice = '', Text = '而最主要的一项，是关于一种拥有无限能源的武器——只属于雷根贝勒的武器。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102312] = { id = 102312, Voice = '', Text = '他一味的追求着更多的利润，强迫性的要求大贤者巴特蒙德去研发许多并不光彩的新项目。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102311] = { id = 102311, Voice = '', Text = '过快的成功让雷根贝勒的管理层心理极度膨胀，其中最为突出的，是一个叫宝勒世伏的男人。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102310] = { id = 102310, Voice = '', Text = '那时的雷根贝勒，由于30年间飞空艇所带来的利润，已经跻身一线企业，在整个大陆都拥有着惊人的影响力。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102309] = { id = 102309, Voice = '', Text = '那大约是在大贤者加入雷根贝勒的30年后，也就是距今120年前左右。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102308] = { id = 102308, Voice = '', Text = '可惜，美好的时代流逝的太快，大贤者和雷根贝勒的合作关系终究出现了严重问题……', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102307] = { id = 102307, Voice = '', Text = '他用智慧造福了大陆的交通问题，也解决了朱诺人民饱受地面污染的苦恼。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102306] = { id = 102306, Voice = '', Text = '于是双方达成合作，大贤者借助雷根贝勒的资源陆续开发出了飞空艇和朱诺的升空技术。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102305] = { id = 102305, Voice = '', Text = '直到150年前，大贤者找到了雷根贝勒公司，那时的雷根贝勒具有资源，而大贤者拥有伊米尔之心的技术和知识。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102304] = { id = 102304, Voice = '', Text = '此后，他用这份力量默默守护着人类世界，其中当然包括了艾尔帕兰钟塔。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102303] = { id = 102303, Voice = '', Text = '很久以前，大贤者巴特蒙徳借助伊米尔之心让自己拥有了永生且超乎人类的魔力。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102302] = { id = 102302, Voice = '', Text = '如果从这一角度出发，一切都说得通了。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102301] = { id = 102301, Voice = '', Text = '想一想，冒险者。雷根贝勒引以为豪的伊米尔之心技术恰恰就是从150年前，大贤者的加入而开始的。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102300] = { id = 102300, Voice = '', Text = '明白了么？没错，伊米尔之心可能就是永生的钥匙！', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102299] = { id = 102299, Voice = '', Text = '雷根贝勒对外宣称朱诺会永远悬浮在空中，换而言之，从能源角度来说，朱诺也是“永生”的。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102298] = { id = 102298, Voice = '', Text = '如今的朱诺能够成为一座天空之城，是基于雷根贝勒公司所拥有的，伊米尔之心碎片的应用技术。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102297] = { id = 102297, Voice = '', Text = '好吧，就让我来给你好好解释下。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102296] = { id = 102296, Voice = '', Text = '没错，这座令世人为之赞叹的朱诺，就是证明大贤者永生之谜的最有力证据。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102295] = { id = 102295, Voice = '', Text = '那个所谓关键性的证据，其实你我都不陌生，甚至此刻，它就在你的脚下。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102294] = { id = 102294, Voice = '', Text = '那么我就继续讲大贤者的故事了！', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102293] = { id = 102293, Voice = '', Text = '冒险者，我们马上开始我们的故事。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102292] = { id = 102292, Voice = '', Text = '冒险者，这[c][ffff00]哈比的羽毛[-][/c]的数量不够，没找齐我可是不会再说一个字，你还是再找一些来吧。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102291] = { id = 102291, Voice = '', Text = '我要的50个[c][ffff00]哈比的羽毛[-][/c]收集齐了吗？', Speaker = 1599, Option = '{上交哈比的羽毛,4},{还没准备好,5}', Action = _EmptyTable,}, 
	[102290] = { id = 102290, Voice = '', Text = '冒险者，想知道是什么证据么？帮我带些[c][ffff00]哈比的羽毛[-][/c]来吧。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102289] = { id = 102289, Voice = '', Text = '因为我有更可信的证据，能证明大贤者永生的秘密。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102288] = { id = 102288, Voice = '', Text = '不过就我看来，这些说法都还不够可信。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102287] = { id = 102287, Voice = '', Text = '有人猜测他拥有神族的血脉，也有人猜测他是天地树的化身。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102286] = { id = 102286, Voice = '', Text = '从痕迹的形态判断，那竟是至少500年前的痕迹了…大贤者，至少活了500年以上！', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102285] = { id = 102285, Voice = '', Text = '我曾经听雷根贝勒退休的老者说，他的考察队几年前发现过艾尔帕兰钟塔有过大贤者留下的痕迹。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102284] = { id = 102284, Voice = '', Text = '更震撼的是关于这一点，现在与其说是流言，倒不如说是事实了。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102283] = { id = 102283, Voice = '', Text = '大贤者巴特蒙徳拥有永恒的生命。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102282] = { id = 102282, Voice = '', Text = '但那都不算什么，真正令人惊愕的流言是…', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102281] = { id = 102281, Voice = '', Text = '关于大贤者的流言有很多，像是他能在空中行走，又或是在水中呼吸。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102280] = { id = 102280, Voice = '', Text = '那么我就继续讲大贤者的故事了！', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102279] = { id = 102279, Voice = '', Text = '朱诺就是需要你这样善良勇敢的人！', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102278] = { id = 102278, Voice = '', Text = '冒险者，想知道那个最神秘的流言吗，请你先帮我消灭些魔物吧。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102277] = { id = 102277, Voice = '', Text = '尤其是其中最为令人惊愕的一个……', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102276] = { id = 102276, Voice = '', Text = '这并不是他们毫无依据的判断，而是因为大陆早已流传过那些不可思议的，关于大贤者的流言。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102275] = { id = 102275, Voice = '', Text = '每个人都打从心里相信，大贤者将会带来改变和幸福。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102274] = { id = 102274, Voice = '', Text = '仿佛在沙漠里看到绿洲一般，人们心中的希望，被大贤者的到来点亮了。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102273] = { id = 102273, Voice = '', Text = '人们得知这个消息，是因为雷根贝勒公司毫无征兆地，宣布了大贤者巴特蒙徳将担任公司首席技术员。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102272] = { id = 102272, Voice = '', Text = '然而就在这个时候，大贤者巴特蒙徳出现了。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102271] = { id = 102271, Voice = '', Text = '依赖的，是它为共和国带来的财富；厌恶的，是它让共和国，尤其是朱诺，成为了苦不堪言的污染之地。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102270] = { id = 102270, Voice = '', Text = '雷根贝勒公司是这一切的源头，朱诺的人民对它既依赖又厌恶。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102269] = { id = 102269, Voice = '', Text = '那时候的共和国，到处都是因为矿业开采和工业开发造成的废土。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102268] = { id = 102268, Voice = '', Text = '说起大贤者巴特蒙徳，那不得不说起150年前的修发兹共和国了……', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102267] = { id = 102267, Voice = '', Text = '因为今天我要给你讲的，便是这位影响了修发兹共和国的，大贤者的故事……', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102266] = { id = 102266, Voice = '', Text = '好极了，冒险者！既然来到了朱诺，相信你一定听说过大贤者巴特蒙徳的名字。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[102265] = { id = 102265, Voice = '', Text = '美丽又神秘的天空之城朱诺也曾发生过很多神奇又有趣的故事，冒险者，你想听吗？', Speaker = 1599, Option = '{我想听,2},{我有空再听吧,3}', Action = _EmptyTable,}, 
	[102264] = { id = 102264, Voice = '', Text = '这，就是黑帽子贤者与白帽子贤者的故事。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102263] = { id = 102263, Voice = '', Text = '院长露出了欣慰的表情，终于同意了二人，正式接纳他们成为贤者学院的一员。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102262] = { id = 102262, Voice = '', Text = '当他俩再次找到院长的时候，看起来根本就不像是当初互相争执的样子，而是变成了亲密的老朋友，无话不谈的知己。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102261] = { id = 102261, Voice = '', Text = '黑帽子和白帽子惊喜的发现病痛全都消失了，自己的精神状态，甚至比曾经任何时候都要好。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102260] = { id = 102260, Voice = '', Text = '清晨，第一缕阳光洒进病房的窗户，咔嚓一声，仿佛什么东西破裂了。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102259] = { id = 102259, Voice = '', Text = '就这样，他们互相帮助，在欢笑和闲聊中度过了最后一晚。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102258] = { id = 102258, Voice = '', Text = '白帽子十分感动，也召唤出一层薄薄的冰霜覆盖在黑帽子的身上，让他不再感到炎热的压迫。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102257] = { id = 102257, Voice = '', Text = '于是黑帽子召唤出温热的火光点亮房间，为白帽子取暖驱寒。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102256] = { id = 102256, Voice = '', Text = '已经化敌为友的黑帽子和白帽子顿悟了一切。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102255] = { id = 102255, Voice = '', Text = '原来院长早就预想到了现在这个情况，他给两位贤者施下他们最棘手的咒语，就是为了教会他们互相合作，互相理解。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102254] = { id = 102254, Voice = '', Text = '冒险者，我们马上开始我们的故事。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102253] = { id = 102253, Voice = '', Text = '冒险者，这[c][ffff00]炙热的毛[-][/c]的数量不够，没找齐我可是不会再说一个字，你还是再找一些来吧。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102252] = { id = 102252, Voice = '', Text = '我要的50个[c][ffff00]炙热的毛[-][/c]收集齐了吗？', Speaker = 1598, Option = '{上交炙热的毛,4},{还没准备好,5}', Action = _EmptyTable,}, 
	[102251] = { id = 102251, Voice = '', Text = '冒险者，想知道解除咒语的秘密么？帮我带些[c][ffff00]炙热的毛[-][/c]来吧。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102250] = { id = 102250, Voice = '', Text = '终于，这天夜里，黑帽子和白帽子共同想到了解除咒语真正的关键。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102249] = { id = 102249, Voice = '', Text = '但院长下的咒语却依然没有找到办法解开，如果明天再找不到办法，他俩可能就要带着病痛被赶出贤者学院了。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102248] = { id = 102248, Voice = '', Text = '难受的病痛让两人暂时忘记了曾经的争执，反而在养病的过程中，逐渐成为互相理解的朋友。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102247] = { id = 102247, Voice = '', Text = '两位昔日的贤者成为了卧病在床的病号，他俩的床位就隔着胳膊的距离。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102246] = { id = 102246, Voice = '', Text = '直到白帽子也病了，他感冒了。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102245] = { id = 102245, Voice = '', Text = '而另一边，白帽子试图用最擅长的冰霜解除寒冷的咒语，可惜与黑帽子一样，他一用冰，身体就越是瑟瑟发抖。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102244] = { id = 102244, Voice = '', Text = '不久，黑帽子病了，他中暑了。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102243] = { id = 102243, Voice = '', Text = '黑帽子试图用引以为豪的火焰解除这炎热的咒语，但往往他越是用火，身体就越是热得无法忍受。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102242] = { id = 102242, Voice = '', Text = '白帽子被下的咒语是战栗的寒冷，他无时无刻不感觉在冰雪的覆盖下，天寒地冻，浑身颤抖。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102241] = { id = 102241, Voice = '', Text = '黑帽子被下的咒语是躁动的炎热，他无时无刻不感觉在正午的烈日下，口干舌燥，浑身冒汗。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102240] = { id = 102240, Voice = '', Text = '回到故事，咒语的内容是这样的…', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102239] = { id = 102239, Voice = '', Text = '朱诺就是需要你这样善良勇敢的人！', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102238] = { id = 102238, Voice = '', Text = '冒险者，想知道咒语的内容是什么吗，请你先帮我消灭些魔物吧。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102237] = { id = 102237, Voice = '', Text = '院长微笑着阻止了他们的争执，并对他们各自施下了一道独特的魔法咒语，随后沉声说：如果你们能解开自己身上的咒语，我就同意你们加入贤者学院。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102236] = { id = 102236, Voice = '', Text = '白帽子也不高兴了，抬手把黑帽子的鞋子冻在地上。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102235] = { id = 102235, Voice = '', Text = '黑帽子有些生气，召唤出火球融化了冰雕。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102234] = { id = 102234, Voice = '', Text = '然而还没等到掌声和赞扬，白帽子就立刻展示了他的冰霜魔法，用冰晶打造出高耸的雕塑，顿时令烟花黯然失色，沦为陪衬。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102233] = { id = 102233, Voice = '', Text = '黑帽子向众人展示了火焰魔法，用火焰塑成了灿烂的烟花，照亮了比朱诺更高的天空。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102232] = { id = 102232, Voice = '', Text = '他们俩加入贤者学院的第一天，院长让他们展示各自的本领。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102231] = { id = 102231, Voice = '', Text = '黑帽子是个急性子老人，白帽子是个慢性子老人。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102230] = { id = 102230, Voice = '', Text = '由于他们醒目的造型，人们一般称呼他们为黑帽子和白帽子。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102229] = { id = 102229, Voice = '', Text = '从前，在贤者学院有两位性格迥异的副院长。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102228] = { id = 102228, Voice = '', Text = '非常好，这个故事和贤者学院可有着密不可分的关系。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102227] = { id = 102227, Voice = '', Text = '今天我们要讲的故事和贤者学院有关，你能帮我带来一张贤者学院的照片吗？然后我们就可以开始我们的故事了。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[102226] = { id = 102226, Voice = '', Text = '美丽又神秘的天空之城朱诺也曾发生过很多神奇又有趣的故事，冒险者，你想听吗？', Speaker = 1598, Option = '{我想听,2},{我有空再听吧,3}', Action = _EmptyTable,}, 
	[101868] = { id = 101868, Voice = '', Text = '这份奖励便交给你吧，感谢你的帮助！', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101867] = { id = 101867, Voice = '', Text = '冒险者，你放心，我们魔物研究会一定会让所有人都重视起来！', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101866] = { id = 101866, Voice = '', Text = '真正能够解决问题的办法，只有人类好好保护环境，不再肆意破坏魔物们栖息的家园。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101865] = { id = 101865, Voice = '', Text = '在岩浆中投放药水，也只能暂时抑制魔物的狂暴。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101864] = { id = 101864, Voice = '', Text = '同时也让它们对所有人类都非常憎恶，以至于经常攻击无辜的冒险者。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101863] = { id = 101863, Voice = '', Text = '人类工业污染对环境造成的破坏，深深影响了熔岩洞窟的魔物们，使得原本生性活泼单纯的魔物变得暴躁易怒。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101862] = { id = 101862, Voice = '', Text = '熔岩洞窟发生的这些事真是让人感到惭愧。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101861] = { id = 101861, Voice = '', Text = '真是太感谢你了，冒险者。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101860] = { id = 101860, Voice = '', Text = '希望这样会有用吧……', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[101859] = { id = 101859, Voice = '', Text = '快去吧，再晚可就来不及了。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101858] = { id = 101858, Voice = '', Text = '或许把这药水投入在岩浆中，会暂时抑制魔物们身上的狂暴。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101857] = { id = 101857, Voice = '', Text = '我刚刚通过研究火焰结晶和燃烧的心脏，制作了一份抵御这种异变的药水。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101856] = { id = 101856, Voice = '', Text = '真让人想不到，魔物异变的根本原因竟然出自人类本身。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101855] = { id = 101855, Voice = '', Text = '竟然是因为这样的原因……', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101854] = { id = 101854, Voice = '', Text = '早晚有一天，你们要为自己的所作所为付出代价！', Speaker = 5783, Option = '', Action = _EmptyTable,}, 
	[101853] = { id = 101853, Voice = '', Text = '你不要装傻，要不是你们人类肆意破坏我们的生存环境，我们也不会变成这样！', Speaker = 5783, Option = '', Action = _EmptyTable,}, 
	[101852] = { id = 101852, Voice = '', Text = '人类，就算你击败了我，我也不会向你屈服的！', Speaker = 5783, Option = '发生了什么', Action = _EmptyTable,}, 
	[101851] = { id = 101851, Voice = '', Text = '人类，你还敢来这里！', Speaker = 5782, Option = '', Action = _EmptyTable,}, 
	[101850] = { id = 101850, Voice = '', Text = '这里似乎没有什么异常……', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[101849] = { id = 101849, Voice = '', Text = '冒险者，在这周围调查一下吧，一定会有新发现的。', Speaker = 5781, Option = '好的', Action = _EmptyTable,}, 
	[101848] = { id = 101848, Voice = '', Text = '至于这异常的来源……看来我们需要对周围的环境做更深一步的调查了。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101847] = { id = 101847, Voice = '', Text = '魔物的狂暴或许就是因为它们的身体出现了异常。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101846] = { id = 101846, Voice = '', Text = '果然……这燃烧的心脏也出现了异常，虽然这点变化非常细微，但还是被我发现了。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101845] = { id = 101845, Voice = '', Text = '很好，冒险者，让我来检查一下。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101844] = { id = 101844, Voice = '', Text = '仅仅这点燃烧的心脏，还不足以证实我的猜测。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101843] = { id = 101843, Voice = '', Text = '怎么样，冒险者，快把燃烧的心脏交给我吧。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101842] = { id = 101842, Voice = '', Text = '这一定能证实我的猜测！', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101841] = { id = 101841, Voice = '', Text = '冒险者，快！再去附近收集十个燃烧的心脏交给我！', Speaker = 5781, Option = '好的', Action = _EmptyTable,}, 
	[101840] = { id = 101840, Voice = '', Text = '这些火焰结晶与正常的相比颜色更深一些，一定是出了什么问题！', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101839] = { id = 101839, Voice = '', Text = '等等……似乎有些不一样！', Speaker = 5781, Option = '什么', Emoji = 7, Action = _EmptyTable,}, 
	[101838] = { id = 101838, Voice = '', Text = '这火焰结晶看上去很正常嘛……', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101837] = { id = 101837, Voice = '', Text = '这么多火焰结晶看上去足够了，让我仔细的研究一下。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101836] = { id = 101836, Voice = '', Text = '火焰结晶的数量似乎不够啊，收集齐了再来找我吧。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101835] = { id = 101835, Voice = '', Text = '你回来了，火焰结晶收集到了吧，赶快交给我吧。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101834] = { id = 101834, Voice = '', Text = '既然找不到原因，那么你就去他们的身上收集十个火焰结晶给我吧，说不定会有什么新发现。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101833] = { id = 101833, Voice = '', Text = '从他们的身上，我感觉到了对人类巨大的厌恶。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101832] = { id = 101832, Voice = '', Text = '根据我最近的研究，我发现熔岩洞窟2F的火焰精灵脾气似乎很暴躁。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101831] = { id = 101831, Voice = '', Text = '那好吧，如果你改变主意了，再来找我吧。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101830] = { id = 101830, Voice = '', Text = '这里对人类来说的确太过于危险，想要研究这里的魔物非常不容易，我需要你的帮助。', Speaker = 5781, Option = '{好的,2},{还是算了,3}', Action = _EmptyTable,}, 
	[101829] = { id = 101829, Voice = '', Text = '冒险者，相信你已经能够感受到这里的炙热了。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101828] = { id = 101828, Voice = '', Text = '不过为了尘世大陆，为了人类的未来，再苦再累我们也会坚持下去的。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101827] = { id = 101827, Voice = '', Text = '我们魔物学者要忍受魔物的侵扰以及长时间的风餐露宿，非常的不容易呢。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101826] = { id = 101826, Voice = '', Text = '但我们不会因此而自满，只要有我们力所能及的地方，我们一定会尽我们所能去给予人们帮助。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101825] = { id = 101825, Voice = '', Text = '直到现在为止，我们在魔物的研究上已经取得了重大的突破。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101824] = { id = 101824, Voice = '', Text = '我们魔物研究会是为了有效的解决层出不穷的魔物所衍生出来的问题而存在的。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101823] = { id = 101823, Voice = '', Text = '我们魔物研究协会绝对不会允许这种事情发生，我一定会竭尽全力研究它们，找到它们攻击人类的根本原因！', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101822] = { id = 101822, Voice = '', Text = '由于生长在岩浆旁，这些魔物本身的火焰力量非常强大，普通的冒险者根本无法抵御魔物的侵袭，这使得人们越来越畏惧熔岩洞窟，甚至成为了人类的禁地。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101821] = { id = 101821, Voice = '', Text = '洞窟中的原生魔物凭借着熔岩与火焰的力量迅速成长，它们原本生性活泼，而现在却对人类有着非常大的敌意，经常攻击无辜的冒险者。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101820] = { id = 101820, Voice = '', Text = '熔岩洞窟凭借恐怖的高温成为了冒险者们十分惧怕的地方。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101819] = { id = 101819, Voice = '', Text = '所以，魔物研究会特地派遣我来到这里，研究这些魔物突然狂暴的原因。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101818] = { id = 101818, Voice = '', Text = '但不知为什么，近些年，熔岩洞窟的魔物对人类的敌意越来越大，甚至开始猛烈地攻击冒险者，以前似乎从来没有发生过这样的事。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101817] = { id = 101817, Voice = '', Text = '在这样的环境下，一些拥有着火焰力量的魔物便诞生了，这些魔物原本生性活泼调皮，对人类充满了好奇。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101816] = { id = 101816, Voice = '', Text = '通往朱诺的路上，或许大家都会注意到附近的熔岩洞窟，炙热的温度让很多冒险者都不敢靠近。', Speaker = 5781, Option = '', Action = _EmptyTable,}, 
	[101813] = { id = 101813, Voice = '', Text = '这就是大总统卡尔的故事。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101812] = { id = 101812, Voice = '', Text = '只是卡尔心里明白，距离驱逐雷根贝勒的目标还有着遥远的距离。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101811] = { id = 101811, Voice = '', Text = '如今秘密之翼还在各地活跃着，而大总统也在朱诺进行着自己的计划。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101810] = { id = 101810, Voice = '', Text = '而秘密之翼的目的，就是阻止修发兹发德被单一集团控制，并使得修发兹发德独立，进而使雷根贝勒这个企业垮台。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101809] = { id = 101809, Voice = '', Text = '他认为雷根贝勒的野心不止是赚钱，而是想控制整个国家，乃至整个大陆。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101808] = { id = 101808, Voice = '', Text = '他与卡普拉服务组织联手在修发兹发德运作着一个叫做秘密之翼的组织。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101807] = { id = 101807, Voice = '', Text = '卡尔忍无可忍，处处掣肘的政治环境让卡尔萌生了对抗雷根贝勒的念头。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101806] = { id = 101806, Voice = '', Text = '而卡尔宣扬的清除祸害政府的腐败因子的承诺也因此仍未实现分毫。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101805] = { id = 101805, Voice = '', Text = '雷根贝勒不断地阻扰着卡尔的政策推行，很多对国民有益的政策都无法实施。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101804] = { id = 101804, Voice = '', Text = '那么，我来为故事作一个完美的收尾。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101803] = { id = 101803, Voice = '', Text = '冒险者，感谢你的慷慨大方！', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101802] = { id = 101802, Voice = '', Text = '冒险者，这[c][ffff00]火焰结晶[-][/c]的数量不对啊，作为报酬可是不太够呢，你还是再找一些来吧。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101801] = { id = 101801, Voice = '', Text = '我要的50个[c][ffff00]火焰结晶[-][/c]收集齐了吗？', Speaker = 1599, Option = '{上交火焰结晶,4},{还没准备好,5}', Action = _EmptyTable,}, 
	[101800] = { id = 101800, Voice = '', Text = '讲到这里，冒险者，是收取报酬的时候了。我需要一些[c][ffff00]火焰结晶[-][/c]，你能帮我找来吗？', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101799] = { id = 101799, Voice = '', Text = '但是大总统深知，雷根贝勒绝对不会善罢甘休。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101798] = { id = 101798, Voice = '', Text = '尽管有雷根贝勒从中作祟，但是卡尔还是以微弱的优势成功当选为修发兹发德共和国的大总统。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101797] = { id = 101797, Voice = '', Text = '他提出的清除祸害政府的腐败因子理念受到民众的大力支持。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101796] = { id = 101796, Voice = '', Text = '这点困难击倒不了卡尔，卡尔的演讲会很快开遍了国内。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101795] = { id = 101795, Voice = '', Text = '卡尔愤怒地拒绝了雷根贝勒的提议，雷根贝勒表示卡尔将永远不能出任总统。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101794] = { id = 101794, Voice = '', Text = '同时他也明白了，原来在这个国家雷根贝勒才是实际意义上的政治掌控者。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101793] = { id = 101793, Voice = '', Text = '他们目中无人的态度深深地激怒了这个由平民一步步走上来的官员。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101792] = { id = 101792, Voice = '', Text = '雷根贝勒明确的表示，只要卡尔愿意付出一点点代价，就可以顺利成为总统。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101791] = { id = 101791, Voice = '', Text = '那我就继续我的故事了。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101790] = { id = 101790, Voice = '', Text = '干得不错，就是有你这样的冒险者世界才会安定。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101789] = { id = 101789, Voice = '', Text = '想知道是发生了什么转变吗？那就教训一下那些讨厌的邪恶向日葵吧。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101788] = { id = 101788, Voice = '', Text = '雷根贝勒找到了他，这也让他的目标发生了转变。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101787] = { id = 101787, Voice = '', Text = '从一个小小的政务处理员到距离权力巅峰的时刻仅有一步之遥，他花了数十年的时间。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101786] = { id = 101786, Voice = '', Text = '卡尔开始为拯救这个掌控在一群充满铜臭的商人手中的国家而做着长足的准备。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101785] = { id = 101785, Voice = '', Text = '卡尔认为充斥在国家内部的腐败份子才是这个国家的病因所在。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101784] = { id = 101784, Voice = '', Text = '尽管名义上修发兹发德是一个共和国，可是其立法机关至今仍不为人所知。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101783] = { id = 101783, Voice = '', Text = '他自幼便生活在朱诺城下的小村庄之中，很早之前他就意识到，这个国家是病态的。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101782] = { id = 101782, Voice = '', Text = '大总统卡尔，全名卡尔·希欧多尔·维尔史特拉斯。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101781] = { id = 101781, Voice = '', Text = '既然你拍到了总统府的照片，那么今天，我就讲讲现任大总统卡尔的故事吧。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101780] = { id = 101780, Voice = '', Text = '真是完美的照片！', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101779] = { id = 101779, Voice = '', Text = '在听故事之前，我想请你去总统府拍张照片。', Speaker = 1599, Option = '交给我吧', Action = _EmptyTable,}, 
	[101778] = { id = 101778, Voice = '', Text = '矗立在云雾之中的天空之城，拥有无数动人的故事，冒险者，你想听吗？', Speaker = 1599, Option = '{我想听,2},{我有空再听吧,3}', Action = _EmptyTable,}, 
	[101777] = { id = 101777, Voice = '', Text = '这就是天空之城朱诺的由来。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101776] = { id = 101776, Voice = '', Text = '朱诺就这样，矗立在紫色的云雾之中，成为了世人所知的“天空之城”。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101775] = { id = 101775, Voice = '', Text = '加速运作的螺旋机桨轰鸣着撕裂了大地，巨大的狂风压垮了地面。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101774] = { id = 101774, Voice = '', Text = '他把伊米尔之心带回了朱诺，将之镶嵌在能源器的核心之中。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101773] = { id = 101773, Voice = '', Text = '科鲁特惊呼这是神的力量，只有神，才有这种无尽的伟力。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101772] = { id = 101772, Voice = '', Text = '科鲁特发现，它的体积虽然小，但是仿佛蕴含着无穷无尽的能量。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101771] = { id = 101771, Voice = '', Text = '在「尤配擂斯」这地方的中心，有一个叫「伊米尔之心」的不明物体。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101770] = { id = 101770, Voice = '', Text = '「尤配擂斯」，这个初古代文明被科鲁特这样命名着。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101769] = { id = 101769, Voice = '', Text = '那么，我来为故事作一个完美的收尾。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101768] = { id = 101768, Voice = '', Text = '冒险者，感谢你的慷慨大方！', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101767] = { id = 101767, Voice = '', Text = '冒险者，这[c][ffff00]燃烧的心脏[-][/c]的数量不对啊，作为报酬可是不太够呢，你还是再找一些来吧。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101766] = { id = 101766, Voice = '', Text = '我要的50个[c][ffff00]燃烧的心脏[-][/c]收集齐了吗？', Speaker = 1599, Option = '{上交燃烧的心脏,10},{还没准备好,11}', Action = _EmptyTable,}, 
	[101765] = { id = 101765, Voice = '', Text = '讲到这里，冒险者，是收取报酬的时候了。我需要一些[c][ffff00]燃烧的心脏[-][/c]，你能帮我找来吗？', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101764] = { id = 101764, Voice = '', Text = '岁月的痕迹在这里清晰可见，科鲁特在这片初古代文明中，找到了改变朱诺的利器。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101763] = { id = 101763, Voice = '', Text = '直到某天，饱经风霜的科鲁特来到了一个神秘的地方。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101762] = { id = 101762, Voice = '', Text = '炙热的熔岩洞窟里蓬勃的岩浆让科鲁特惊叹不已，可一旦离开了地面，就无法继续利用这个能源了。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101761] = { id = 101761, Voice = '', Text = '他旅行至艾因布洛克原野，尝试寻找稀有的能源矿物，却被凶猛的魔物追得仓皇而逃。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101760] = { id = 101760, Voice = '', Text = '他走过国境线，尝试利用强大的风力，但风的力量无形无质无法保存，他很快便放弃了。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101759] = { id = 101759, Voice = '', Text = '政府反应非常迅速，科鲁特被授予寻找全新能源的使命。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101758] = { id = 101758, Voice = '', Text = '众所周知，一旦没有了能源，那些巨大无比的螺旋机桨根本不会有任何作用。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101757] = { id = 101757, Voice = '', Text = '原来朱诺人赖以生存的能源已经接近枯竭，这让科鲁特十分苦恼。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101756] = { id = 101756, Voice = '', Text = '不过，承诺就是承诺，我就继续我的故事了。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101755] = { id = 101755, Voice = '', Text = '真是令人惊叹的效率啊！我原以为你会很慢的。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101754] = { id = 101754, Voice = '', Text = '想知道是什么消息吗？那就教训一下那些讨厌的鸟人哈比吧。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101753] = { id = 101753, Voice = '', Text = '一切都向着美好的方向发展着，直到某个消息打乱了科鲁特的部署。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101752] = { id = 101752, Voice = '', Text = '他观察鸟类生物，发明了螺旋机浆，这种巨大的机械能够产生强大的推进力。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101751] = { id = 101751, Voice = '', Text = '从此以后，科鲁特走遍了整个朱诺，搜集能够使整座朱诺城升上天空的办法。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101750] = { id = 101750, Voice = '', Text = '他的建议得到了当时朱诺政府的支持——政府早已厌倦这样的环境。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101749] = { id = 101749, Voice = '', Text = '也许蔚蓝的天空，才是勇敢而又辛劳的朱诺人最终的归宿。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101748] = { id = 101748, Voice = '', Text = '在一次魔物袭击后，看着满目疮痍的城市，学者科鲁特愤然说道：这，不该是朱诺人的命运！', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101747] = { id = 101747, Voice = '', Text = '这里荒芜且充满了危险，仿佛无穷无尽的魔物时刻威胁着朱诺人的安全。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101746] = { id = 101746, Voice = '', Text = '作为修发兹发德共和国的首都，朱诺原本位于一片贫瘠的荒地之上。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101745] = { id = 101745, Voice = '', Text = '那么今天，就让我们来讲讲天空之城朱诺的故事吧！', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101744] = { id = 101744, Voice = '', Text = '看来你对我们吟游诗人传唱的故事并不是很了解呢，你还是了解了解再来找我吧。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101743] = { id = 101743, Voice = '', Text = '看来你已经对我们吟游诗人传唱的故事了若指掌了呢！', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101742] = { id = 101742, Voice = '', Text = '那么请听第三题，我的同僚，姜饼城的默德温·卡普讲了哪个故事？', Speaker = 1599, Option = '{月狼传说,8},{固特伊,8},{圣诞传说,9}', Action = _EmptyTable,}, 
	[101741] = { id = 101741, Voice = '', Text = '第二题，创立姜饼城玩具工厂的是谁？', Speaker = 1599, Option = '{小丑加尔,6},{飞利浦,7},{圣诞老人,6}', Action = _EmptyTable,}, 
	[101740] = { id = 101740, Voice = '', Text = '第一个问题，在月狼传说的故事中，耶梦加得的哥哥是谁？', Speaker = 1599, Option = '{海拉,4},{提尔,4},{芬里尔,5}', Action = _EmptyTable,}, 
	[101739] = { id = 101739, Voice = '', Text = '冒险者，你自信的样子真是帅气呢。', Speaker = 1599, Option = '', Action = _EmptyTable,}, 
	[101738] = { id = 101738, Voice = '', Text = '在听故事之前，我想问你几个问题，很简单的小问题哦~', Speaker = 1599, Option = '问吧', Action = _EmptyTable,}, 
	[101737] = { id = 101737, Voice = '', Text = '矗立在云雾之中的天空之城，拥有无数动人的故事，冒险者，你想听吗？', Speaker = 1599, Option = '{我想听,2},{我有空再听吧,3}', Action = _EmptyTable,}, 
	[101541] = { id = 101541, Voice = '', Text = '只有失去光泽的海姆达水晶，似乎在悄悄地向人们诉说曾经发生的那些过往。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101540] = { id = 101540, Voice = '', Text = '伴随着天边渐渐泛起的鱼肚白，这个夜晚发生的故事也被深深埋葬在每个人心中。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101539] = { id = 101539, Voice = '', Text = '而这之后，海姆达水晶耗尽了所有的能量，陷入了深深的沉睡。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101538] = { id = 101538, Voice = '', Text = '那光芒瞬间抵挡了波迪的所有的攻击，驱散了魔物们的强大力量，人们也因此得救。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101537] = { id = 101537, Voice = '', Text = '那一瞬间，人们只看到，菲洛·瑟夫苍老的脸上流下了欣慰的泪水，与此同时，海姆达水晶散发出了无比耀眼的光芒。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101536] = { id = 101536, Voice = '', Text = '“我愿意。”', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101535] = { id = 101535, Voice = '', Text = '“你是否愿意奉献出自己的生命，与我一同永远守护着朱诺的子民。”', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101534] = { id = 101534, Voice = '', Text = '这次，他清楚的听到了，是海姆达水晶发出的声音！', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101533] = { id = 101533, Voice = '', Text = '就在这时，菲洛·瑟夫的耳畔似乎又响起了曾经听到过的那轻轻呢喃。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101532] = { id = 101532, Voice = '', Text = '失去水晶的守护，人们几乎没有任何力量可以反抗波迪的攻击。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101531] = { id = 101531, Voice = '', Text = '波迪的力量实在太强大了，海姆达水晶所散发的光芒越来越微弱。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101530] = { id = 101530, Voice = '', Text = '邪恶的波迪利用飞行的优势盘旋在朱诺的上空中，对海姆达水晶形成的守护光罩进行攻击。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101529] = { id = 101529, Voice = '', Text = '那么我便继续讲下去了。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101528] = { id = 101528, Voice = '', Text = '你愿意把它交给我吗？', Speaker = 1598, Option = '{我愿意,6},{不愿意,4}', Action = _EmptyTable,}, 
	[101527] = { id = 101527, Voice = '', Text = '这似乎不够啊，快去准备足够的材料交给我吧。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101526] = { id = 101526, Voice = '', Text = '你拿到我要的东西了吗？', Speaker = 1598, Option = '{拿到了,3},{还没有,4}', Action = _EmptyTable,}, 
	[101525] = { id = 101525, Voice = '', Text = '想知道接下来发生了什么，就快去准备50份炙热的毛给我吧。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101524] = { id = 101524, Voice = '', Text = '一声长啸刺破了夜空，惊醒了沉睡中的人们，大家纷纷惊慌地拿出武器，但是毫无准备的人类又怎能敌过这一波袭击呢。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101523] = { id = 101523, Voice = '', Text = '当朱诺的子民还沉浸在美妙的梦乡当中时，波迪与其他魔物们已经潜伏在城边。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101522] = { id = 101522, Voice = '', Text = '邪恶的鸟人哈比首领——波迪为了夺取海姆达水晶，带着他的部下和诸多魔物来到了朱诺。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101521] = { id = 101521, Voice = '', Text = '在所有人意料不到的时刻，灾难突然降临。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101520] = { id = 101520, Voice = '', Text = '他不明白这个梦究竟意味着什么，他只是来到了海姆达水晶的周围，静静地在寒风中站了一整夜。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101519] = { id = 101519, Voice = '', Text = '突然有一天，菲洛·瑟夫从睡梦中惊醒，在梦中，他仿佛看到了漫天的羽毛和满地的鲜血。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101518] = { id = 101518, Voice = '', Text = '很多年过去了，菲洛·瑟夫已经逐渐苍老。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101517] = { id = 101517, Voice = '', Text = '很好，冒险者，那么我便继续讲下去了。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101516] = { id = 101516, Voice = '', Text = '冒险者，你想知道发生了什么吗？去消灭300只鸟人哈比吧，之后我会告诉你。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101515] = { id = 101515, Voice = '', Text = '直到发生了一件事，让美丽的海姆达水晶失去了它的光泽。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101514] = { id = 101514, Voice = '', Text = '从此以后，这颗蕴含着巨大能量的华美水晶便成为了朱诺的标志性建筑物，守护着一代又一代朱诺的子民。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101513] = { id = 101513, Voice = '', Text = '最终，在雷根贝勒公司的帮助下，菲洛·瑟夫建造了一座巨塔，也开启了海姆达水晶的守护之力。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101512] = { id = 101512, Voice = '', Text = '于是，菲洛·瑟夫毫不犹豫地投身在了海达姆水晶的研究当中。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101511] = { id = 101511, Voice = '', Text = '当菲洛·瑟夫第一眼看到海姆达水晶的时候，他仿佛感受到了水晶在他耳边轻轻的呢喃。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101510] = { id = 101510, Voice = '', Text = '他们找来了智慧的贤者学院院长菲洛·瑟夫，想与他一同研究。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101509] = { id = 101509, Voice = '', Text = '大约在300年前，雷根贝勒公司在神的遗迹中发现了海姆达水晶。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101508] = { id = 101508, Voice = '', Text = '今天，让我为你诉说一段故事，歌颂这座一直以来默默守护着朱诺的海姆达水晶。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101507] = { id = 101507, Voice = '', Text = '很好，看来，我要从很久很久以前说起了……', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101506] = { id = 101506, Voice = '', Text = '相信你一定注意到城里那颗巨大的水晶了，去帮我拍一张它的照片吧，今天我便要给你讲它的故事。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101505] = { id = 101505, Voice = '', Text = '美丽又神秘的天空城朱诺也曾发生过很多神奇又有趣的故事，冒险者，你想听吗？', Speaker = 1598, Option = '{我想听,2},{我有空再听吧,3}', Action = _EmptyTable,}, 
	[101317] = { id = 101317, Voice = '', Text = '也是塞壬作为传奇战士开端的第一句话。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101316] = { id = 101316, Voice = '', Text = '这是塞壬离开贤者学院前的最后一句话。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101315] = { id = 101315, Voice = '', Text = '“我想成为英雄。”', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101314] = { id = 101314, Voice = '', Text = '塞壬的心中默念道，对不起，母亲。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101313] = { id = 101313, Voice = '', Text = '西芙深叹了一口气，明白自己的反对无法阻止塞壬的决心。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101312] = { id = 101312, Voice = '', Text = '“对不起，老师”塞壬始终没抬起自己的头“我心中早已有了答案。”', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101311] = { id = 101311, Voice = '', Text = '塞壬决定加入大总统直辖的新作战小队——曙光队。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101310] = { id = 101310, Voice = '', Text = '在自己的成人礼上，塞壬一身戎装跪在西弗面前，贤者学院一片寂静。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101309] = { id = 101309, Voice = '', Text = '不错啊伙计，那我就接着讲了。', Speaker = 1598, Option = '好的', Action = _EmptyTable,}, 
	[101308] = { id = 101308, Voice = '', Text = '等你把材料交给我后，我会告诉你接下去的故事。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101307] = { id = 101307, Voice = '', Text = '你带来我想要的东西了么？', Speaker = 1598, Option = '{带来了,8},{没带来,9}', Action = _EmptyTable,}, 
	[101306] = { id = 101306, Voice = '', Text = '你想知道塞壬做出的选择么，请为我收集30个神之手吧。', Speaker = 1598, Option = '好吧', Action = _EmptyTable,}, 
	[101305] = { id = 101305, Voice = '', Text = '终于，在他成人礼那天，他做出了对自己命运的选择。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101304] = { id = 101304, Voice = '', Text = '想把塞壬培养成学者的西弗坚决反对他走上战斗的道路。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101303] = { id = 101303, Voice = '', Text = '一有机会，塞壬就溜出学院向各地来的冒险家们学习战斗经验。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101302] = { id = 101302, Voice = '', Text = '在贤者学院的时光里，塞壬背着西芙偷偷挥舞树枝练习剑技。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101301] = { id = 101301, Voice = '', Text = '以及自己心中深处的答案。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101300] = { id = 101300, Voice = '', Text = '这项链无时无刻不在提醒他探查父亲失踪的真相。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101299] = { id = 101299, Voice = '', Text = '塞壬在西芙的养育下成长，但一刻都不曾摘下脖子上的父亲留下的项链。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101298] = { id = 101298, Voice = '', Text = '塞壬陷入了沉默，西弗点着他的太阳穴说道，脑海之外万物皆为虚幻。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101297] = { id = 101297, Voice = '', Text = '孩子，你想成为一个怎样的人？西弗问道。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101296] = { id = 101296, Voice = '', Text = '成为孤儿的塞壬被带到了贤者学院院长西弗的面前。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101295] = { id = 101295, Voice = '', Text = '还没等到塞壬的回复，母亲便永远的睡去了。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101294] = { id = 101294, Voice = '', Text = '这是我唯一的愿望，答应我。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101293] = { id = 101293, Voice = '', Text = '塞壬的母亲说：“塞壬，别再像你父亲一样想着成为英雄，作为一个普通人好好生活下去。”', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101292] = { id = 101292, Voice = '', Text = '很好，这下以后就不怕土坡鼠了。之前说到塞壬母亲的愿望了对吧？', Speaker = 1598, Option = '是的', Action = _EmptyTable,}, 
	[101291] = { id = 101291, Voice = '', Text = '想知道塞壬母亲的愿望嘛？请你帮我消灭一些曾经袭击过我的土拨鼠吧！', Speaker = 1598, Option = '好吧', Action = _EmptyTable,}, 
	[101290] = { id = 101290, Voice = '', Text = '塞壬，我只有一个愿望......', Speaker = 1598, Option = '是什么', Action = _EmptyTable,}, 
	[101289] = { id = 101289, Voice = '', Text = '塞壬的母亲用尽力气挤出微笑，用微弱地声音说。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101288] = { id = 101288, Voice = '', Text = '年幼的塞壬趴在病危的母亲床前，嚎啕大哭。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101287] = { id = 101287, Voice = '', Text = '最终被污染和操劳击倒，患了重病。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101286] = { id = 101286, Voice = '', Text = '塞壬的母亲独自撑起家庭，在朱诺与艾音布罗克往返奔波工作。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101285] = { id = 101285, Voice = '', Text = '年幼的塞壬不曾想到父亲会从此音信全无，只留下一根项链。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101284] = { id = 101284, Voice = '', Text = '一边的母亲微笑着望着这对父子，笑容中藏着一丝忧虑。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101283] = { id = 101283, Voice = '', Text = '塞壬，你是大人了，爸爸不在的时候，你要照顾好妈妈。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101282] = { id = 101282, Voice = '', Text = '父亲这次不会又是在吹牛吧？小塞壬半信半疑，但心底却抑制不住的期待着。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101281] = { id = 101281, Voice = '', Text = '只要变强就能加入军队，成为塞壬最喜欢的英雄！', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101280] = { id = 101280, Voice = '', Text = '爸爸答应去参加一个训练，去了以后就会变得很厉害！', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101279] = { id = 101279, Voice = '', Text = '有一天，父亲兴奋得对塞壬说。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101278] = { id = 101278, Voice = '', Text = '不错，那我就继续为你讲述故事吧！', Speaker = 1598, Option = '好的', Action = _EmptyTable,}, 
	[101277] = { id = 101277, Voice = '', Text = '冒险家，照片拍好了么？', Speaker = 1598, Option = '拍好了', Action = _EmptyTable,}, 
	[101275] = { id = 101275, Voice = '', Text = '父亲为了成为儿子的英雄，做了一个重大决定。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101274] = { id = 101274, Voice = '', Text = '但平静的生活在某一天突然结束了。', Speaker = 1598, Option = '发生了什么', Action = _EmptyTable,}, 
	[101273] = { id = 101273, Voice = '', Text = '咳咳，父亲尴尬的咳了几声，旁边的母亲轻轻笑了出来。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101272] = { id = 101272, Voice = '', Text = '吹牛！妈妈说你以前就是个书呆子，几乎没出过朱诺城！', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101271] = { id = 101271, Voice = '', Text = '我年轻的时候可是战胜过火焰鸟的冒险家哦，够厉害吧！', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101270] = { id = 101270, Voice = '', Text = '塞壬，别小看爸爸哦！男人想了一会儿说道。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101269] = { id = 101269, Voice = '', Text = '父亲推了推眼镜，有些尴尬。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101268] = { id = 101268, Voice = '', Text = '爸爸，你为什么不能像卡尔大总统和他的军队一样和魔物战斗，做大英雄呢？', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101267] = { id = 101267, Voice = '', Text = '年幼的塞壬对热衷于研究的爸爸多少有些失望。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101266] = { id = 101266, Voice = '', Text = '爸爸，你为什么整天看书，你又不是贤者学院里的那些怪人。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101265] = { id = 101265, Voice = '', Text = '还有一个从小怀着英雄梦的儿子。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101264] = { id = 101264, Voice = '', Text = '从前在朱诺有一个幸福的三口之家，研究员父亲与贤惠美丽的母亲。', Speaker = 1598, Option = '', Action = _EmptyTable,}, 
	[101263] = { id = 101263, Voice = '', Text = '美丽又神秘的天空城朱诺也曾发生过很多神奇又有趣的故事，冒险者，你想听吗？', Speaker = 1598, Option = '{我想听,2},{我没兴趣,3}', Action = _EmptyTable,}, 
