	[88815] = { id = 88815, Voice = '', Text = '你能击败它吗？如果你能击败它的话，我才能肯定你有实力帮助我。来让我看看你的冒险手册，如果你有能力击败[c][ffff00]大本钟[-][/c]的话，你的冒险手册应该解锁了才对。', Speaker = 4614, Option = '', Action = _EmptyTable,}, 
