	[88806] = { id = 88806, Voice = '', Text = '我想请你帮我拍一张大本钟的照片。', Speaker = 4614, Option = '{没问题,2},{没兴趣,1}', Action = _EmptyTable,}, 
	[88805] = { id = 88805, Voice = '', Text = '我很想研究这种魔物，但是我无法获得它的资料。', Speaker = 4614, Option = '', Action = _EmptyTable,}, 
	[88804] = { id = 88804, Voice = '', Text = '我听说钟楼里有一种魔物，名叫大本钟。', Speaker = 4614, Option = '', Action = _EmptyTable,}, 
	[88803] = { id = 88803, Voice = '', Text = '…啊！冒险者，我刚刚在思考…', Speaker = 4614, Option = '思考什么', Action = _EmptyTable,}, 
	[88418] = { id = 88418, Voice = '', Text = '我们也在抓紧时间制作，争取早点把这金光四射的精制山羊头盔带给大家！', Speaker = 6517, Option = '', Action = _EmptyTable,}, 
	[88417] = { id = 88417, Voice = '', Text = '由于“黄金虫”的数量稀少，我们无法进行大规模生产。如果你想要的话，只能抢购了哦，毕竟数量有限呢！', Speaker = 6517, Option = '', Action = _EmptyTable,}, 
	[88416] = { id = 88416, Voice = '', Text = '这种物质可以使得我们的装扮镀上一层金色，在阳光下会散发出迷人的光芒。', Speaker = 6517, Option = '', Action = _EmptyTable,}, 
	[88415] = { id = 88415, Voice = '', Text = '我们家族从下水道的“黄金虫”身上提取到一种神秘物质。', Speaker = 6517, Option = '', Action = _EmptyTable,}, 
	[88414] = { id = 88414, Voice = '', Text = '啊...你说这个！这可是我们闪金家族的诚意之作！', Speaker = 6517, Option = '', Action = _EmptyTable,}, 
	[88233] = { id = 88233, Voice = '', Text = '也许我该去找一次克里斯蒂安，克里斯蒂安那里应该会有什么消息…这次多谢你的帮助了，冒险者。', Speaker = 6541, Option = '', Action = _EmptyTable,}, 
	[88232] = { id = 88232, Voice = '', Text = '如果能弄清楚它们身上的能量为什么会变成这样，也许我们的问题就能迎刃而解了。', Speaker = 6541, Option = '', Action = _EmptyTable,}, 
	[88231] = { id = 88231, Voice = '', Text = '之前森灵身上的能量是纯净的，但是现在它们身上的能量明显是受到污染的。', Speaker = 6541, Option = '', Action = _EmptyTable,}, 
	[88230] = { id = 88230, Voice = '', Text = '这种不纯净的能量影响了它们的思维，使它们变得更加暴躁，我想重金属蝗虫与猎蝇也是因为这股能量的原因。', Speaker = 6541, Option = '', Action = _EmptyTable,}, 
	[88229] = { id = 88229, Voice = '', Text = '没错了！就是它们身体内的这股能量，不纯净的能量充斥着它们全身。', Speaker = 6541, Option = '', Action = _EmptyTable,}, 
	[88228] = { id = 88228, Voice = '', Text = '从那些森灵身上收集一些森灵的粘液，它们可比波利强大太多了，冒险者你要小心。', Speaker = 6541, Option = '', Action = _EmptyTable,}, 
	[88227] = { id = 88227, Voice = '', Text = '但是这股能量我曾经看到过，是一种叫做“森灵”的魔物身上特有的能量。', Speaker = 6541, Option = '', Action = _EmptyTable,}, 
	[88226] = { id = 88226, Voice = '', Text = '这股能量非常少，以至于从外表看波利完全没有什么改变。', Speaker = 6541, Option = '', Action = _EmptyTable,}, 
	[88225] = { id = 88225, Voice = '', Text = '唔，我仔细的研究了波利的体液，发现它们的体液中掺杂着不纯净的能量。', Speaker = 6541, Option = '', Action = _EmptyTable,}, 
	[88224] = { id = 88224, Voice = '', Text = '你能帮我从波利的身上收集一些波利的体液给我研究吗？', Speaker = 6541, Option = '我很乐意', Action = _EmptyTable,}, 
	[88223] = { id = 88223, Voice = '', Text = '而波利和绿棉虫却没有受到影响，我猜测，是不是波利和绿棉虫身上有什么特殊的地方…', Speaker = 6541, Option = '', Action = _EmptyTable,}, 
	[88222] = { id = 88222, Voice = '', Text = '根据我的研究发现，受到影响的魔物只有那些蝗虫和苍蝇，它们现在演变为强大的重金属蝗虫和猎蝇了。', Speaker = 6541, Option = '', Action = _EmptyTable,}, 
	[88221] = { id = 88221, Voice = '', Text = '我就知道你一定不会袖手旁观的！', Speaker = 6541, Option = '', Action = _EmptyTable,}, 
	[88220] = { id = 88220, Voice = '', Text = '所以冒险者，我需要你的帮助。', Speaker = 6541, Option = '{我帮你,2},{没兴趣,1}', Action = _EmptyTable,}, 
	[88219] = { id = 88219, Voice = '', Text = '为了来往行人的安全，我一定会把这里的魔物狂暴原因研究透彻的。', Speaker = 6541, Option = '', Action = _EmptyTable,}, 
	[88218] = { id = 88218, Voice = '', Text = '从这些魔物身上，我获得不少资料，现在已经到了验证成果的时候了。', Speaker = 6541, Option = '', Action = _EmptyTable,}, 
	[88217] = { id = 88217, Voice = '', Text = '冒险者，我想我对这里的魔物已经有了一定程度的了解。', Speaker = 6541, Option = '', Action = _EmptyTable,}, 
	[88216] = { id = 88216, Voice = '', Text = '不过为了尘世大陆，为了国王，也为了王国的民众们，再苦再累我们也会坚持下去的。', Speaker = 6541, Option = '', Action = _EmptyTable,}, 
	[88215] = { id = 88215, Voice = '', Text = '我们魔物学者要忍受魔物的侵扰以及长时间的风餐露宿，非常的不容易呢。', Speaker = 6541, Option = '', Action = _EmptyTable,}, 
	[88214] = { id = 88214, Voice = '', Text = '我的同僚在克雷斯特汉姆的魔物研究上取得了重大的突破。而我作为魔物研究会的一员，也应该加把劲儿！', Speaker = 6541, Option = '', Action = _EmptyTable,}, 
	[88213] = { id = 88213, Voice = '', Text = '我们魔物研究会是为了有效的解决层出不穷的魔物所衍生出来的问题而存在的。', Speaker = 6541, Option = '', Action = _EmptyTable,}, 
	[88212] = { id = 88212, Voice = '', Text = '它们的异变对来往的人们构成严重的威胁，为了行人们的安全，国王陛下派遣我们来到这里研究魔物，希望有一天能够找出它们的弱点。', Speaker = 6541, Option = '', Action = _EmptyTable,}, 
	[88211] = { id = 88211, Voice = '', Text = '它们的异变直接威胁了北门人们的安全，北门的人烟越来越稀少。国王陛下决不允许国都附近出现这种危险的地域。', Speaker = 6541, Option = '', Action = _EmptyTable,}, 
	[88210] = { id = 88210, Voice = '', Text = '而现在因为某种原因，魔物们发生了某种异变，除了更具攻击性之外，它们的样子也有了很大的变化。', Speaker = 6541, Option = '', Action = _EmptyTable,}, 
	[88209] = { id = 88209, Voice = '', Text = '在很久之前，普隆德拉北门是一片和谐美好的土地，爱捣蛋的波利与绿棉虫与人们相处融洽，安然生活。', Speaker = 6541, Option = '', Action = _EmptyTable,}, 
	[88208] = { id = 88208, Voice = '', Text = '所以我们魔物研究会派遣我来这里研究这里的魔物，以及这些魔物带来的影响。', Speaker = 6541, Option = '', Action = _EmptyTable,}, 
	[88207] = { id = 88207, Voice = '', Text = '但是它们现在发生了显而易见的变化，我甚至觉得它们与南门的生物不再是同一个物种。', Speaker = 6541, Option = '', Action = _EmptyTable,}, 
	[88206] = { id = 88206, Voice = '', Text = '它们虽然偶尔会给北门的人们带来一些麻烦，但是绝对不会引起大的骚动。', Speaker = 6541, Option = '', Action = _EmptyTable,}, 
	[88205] = { id = 88205, Voice = '', Text = '普隆德拉北门原本也是和南门一样，生活着人畜无害的波利和绿棉虫。', Speaker = 6541, Option = '', Action = _EmptyTable,}, 
	[87980] = { id = 87980, Voice = '', Text = '我可以把你传送进普隆德拉皇家料理协会总部哦？你想去吗？', Speaker = 6454, Option = '{我想,2},{我不想,1}', Action = _EmptyTable,}, 
	[87947] = { id = 87947, Voice = '', Text = '好了，已经登记完毕了！恭喜你美食家等级晋升成功！', Speaker = 6445, Option = '', Action = _EmptyTable,}, 
	[87946] = { id = 87946, Voice = '', Text = '不过你要记得，美食是一门很深的学问，你要好好研究才行。', Speaker = 6445, Option = '', Action = _EmptyTable,}, 
	[87945] = { id = 87945, Voice = '', Text = '看来你已经达到晋升的条件了，没想到你居然这么快就走到了现在这一步。', Speaker = 6445, Option = '', Action = _EmptyTable,}, 
