	[87918] = { id = 87918, Voice = '', Text = '已经登记结束了，恭喜你，你的料理师等级已经更进一步了！', Speaker = 6444, Option = '', Action = _EmptyTable,}, 
	[87917] = { id = 87917, Voice = '', Text = '我要的手续费10000zeny以及料理书，你带来了吗？', Speaker = 6444, Option = '{带来了,4},{没有,3}', Action = _EmptyTable,}, 
	[87916] = { id = 87916, Voice = '', Text = '带上手续费10000zeny与料理书再来找我吧！', Speaker = 6444, Option = '', Action = _EmptyTable,}, 
	[87915] = { id = 87915, Voice = '', Text = '现在，你想晋升料理师等级吗？', Speaker = 6444, Option = '{我想,2},{我不想,1}', Action = _EmptyTable,}, 
	[87914] = { id = 87914, Voice = '', Text = '新的料理书意味着更多的菜谱，我想每个料理师都不会错过。', Speaker = 6444, Option = '', Action = _EmptyTable,}, 
	[87913] = { id = 87913, Voice = '', Text = '我把你的料理师等级进阶。同时，我也会为你更换料理书。', Speaker = 6444, Option = '', Action = _EmptyTable,}, 
	[87912] = { id = 87912, Voice = '', Text = '我看到你已经把料理书的记载运用得无比娴熟，现在的你，有资格也有能力选择在料理的道路上更进一步。', Speaker = 6444, Option = '', Action = _EmptyTable,}, 
	[87911] = { id = 87911, Voice = '', Text = '我曾经听闻你的名字，在普隆德拉料理界冉冉升起的新星。', Speaker = 6444, Option = '', Action = _EmptyTable,}, 
	[87906] = { id = 87906, Voice = '', Text = '你的进步太快了，也许很快我就教不了你了呢！', Speaker = 6446, Option = '', Action = _EmptyTable,}, 
	[87905] = { id = 87905, Voice = '', Text = '嗯嗯…真不错，海鲜的鲜味适中，与米饭完美结合。做的不错呀！', Speaker = 6446, Option = '', Action = _EmptyTable,}, 
	[87904] = { id = 87904, Voice = '', Text = '叫做海鲜焗饭，这是3级料理哦，做出来给我尝尝吧！', Speaker = 6446, Option = '', Action = _EmptyTable,}, 
	[87903] = { id = 87903, Voice = '', Text = '但是一旦成功，你就会得到很珍贵的经验。今天，就请你做一份不算太难的料理。', Speaker = 6446, Option = '', Action = _EmptyTable,}, 
	[87902] = { id = 87902, Voice = '', Text = '当我们的料理等级不够，这时候如果我们想烹饪一些高级的菜谱，就有很大几率失败。', Speaker = 6446, Option = '', Action = _EmptyTable,}, 
	[87901] = { id = 87901, Voice = '', Text = '这次，可能对你是一个考验。不会那么简单哦。', Speaker = 6446, Option = '', Action = _EmptyTable,}, 
	[87900] = { id = 87900, Voice = '', Text = '你的进步很快哦~期待你下一次的表现。', Speaker = 6446, Option = '', Action = _EmptyTable,}, 
	[87899] = { id = 87899, Voice = '', Text = '不过你还是要主要肉串烧烤时的火候，另外翻转的时候要烤均匀。', Speaker = 6446, Option = '', Action = _EmptyTable,}, 
	[87898] = { id = 87898, Voice = '', Text = '肉质鲜美，外酥里嫩，不错的大众肉串呀。', Speaker = 6446, Option = '', Action = _EmptyTable,}, 
	[87897] = { id = 87897, Voice = '', Text = '为了看看你现在的烹饪技术，做三份大众肉串给我尝尝吧！', Speaker = 6446, Option = '', Action = _EmptyTable,}, 
	[87896] = { id = 87896, Voice = '', Text = '唔，经过锻炼，你的手艺一定会越来越好的！', Speaker = 6446, Option = '', Action = _EmptyTable,}, 
	[87895] = { id = 87895, Voice = '', Text = '以后还要多多练习哦。我很期待你成为料理大师的那一天！', Speaker = 6446, Option = '', Action = _EmptyTable,}, 
	[87894] = { id = 87894, Voice = '', Text = '嗯！我闻到初心小肉串的香气了！看来你已经成功制作出来了。', Speaker = 6446, Option = '', Action = _EmptyTable,}, 
	[87893] = { id = 87893, Voice = '', Text = '首先，你得从最基础的学起，先尝试制作两份初心小肉串吧。', Speaker = 6446, Option = '', Action = _EmptyTable,}, 
	[87892] = { id = 87892, Voice = '', Text = '想成为一个真正的料理大师，只有通过不停地烹饪，才能提升烹饪技巧。', Speaker = 6446, Option = '', Action = _EmptyTable,}, 
	[87890] = { id = 87890, Voice = '', Text = '多多品尝美食会提升自己的美食等级，有机会你还是要多多尝试才行。', Speaker = 6445, Option = '', Action = _EmptyTable,}, 
	[87889] = { id = 87889, Voice = '', Text = '在加上葱花与奶油，才形成了现在的美味。你能说出其中一个特点已经很不错了。', Speaker = 6445, Option = '', Action = _EmptyTable,}, 
	[87888] = { id = 87888, Voice = '', Text = '初心小肉排是用普通的肉烤制而成，辅以佐料，不断翻烤。', Speaker = 6445, Option = '', Action = _EmptyTable,}, 
	[87887] = { id = 87887, Voice = '', Text = '吃了这个初心小肉排，感觉怎么样？', Speaker = 6445, Option = '{色泽明亮,3},{香气十足,3},{肉质鲜美,3},{口感爽滑,3}', Action = _EmptyTable,}, 
	[87886] = { id = 87886, Voice = '', Text = '这是刚刚做好的料理，你品尝一下，把感受告诉我。', Speaker = 6445, Option = '', Action = _EmptyTable,}, 
	[87885] = { id = 87885, Voice = '', Text = '既然感兴趣，那就让我来看看你的天赋究竟如何吧！', Speaker = 6445, Option = '', Action = _EmptyTable,}, 
	[87884] = { id = 87884, Voice = '', Text = '所以品尝美食也是一门课程，你对这个课程感兴趣吗？', Speaker = 6445, Option = '{感兴趣,2},{没兴趣,1}', Action = _EmptyTable,}, 
	[87883] = { id = 87883, Voice = '', Text = '当美味的料理递上来，你却不懂美食的滋味所在，那才是对美食的亵渎。', Speaker = 6445, Option = '', Action = _EmptyTable,}, 
	[87882] = { id = 87882, Voice = '', Text = '所以你还是再接再厉，争取早日成为真正的料理大师吧！', Speaker = 6447, Option = '', Action = _EmptyTable,}, 
	[87881] = { id = 87881, Voice = '', Text = '这可是你从一个菜鸟厨师进阶为料理大师的必经步骤，一旦你得到我们协会的认可，我们就会向你开放更多的料理菜单。', Speaker = 6447, Option = '', Action = _EmptyTable,}, 
	[87880] = { id = 87880, Voice = '', Text = '看样子你在料理上的天赋还算不错，多尝试制作料理，可以提升熟练度。', Speaker = 6447, Option = '', Action = _EmptyTable,}, 
	[87879] = { id = 87879, Voice = '', Text = '嗯！外酥内嫩，口感不错！看来你还有点天赋嘛。', Speaker = 6447, Option = '', Action = _EmptyTable,}, 
	[87878] = { id = 87878, Voice = '', Text = '我也不为难你，你就做个最简单的料理：初心小肉排，这可不需要什么技术，只是需要一点耐心而已。', Speaker = 6447, Option = '我试试', Action = _EmptyTable,}, 
	[87877] = { id = 87877, Voice = '', Text = '现在你先做一个料理，让我看看你在料理的天赋究竟有多高！', Speaker = 6447, Option = '', Action = _EmptyTable,}, 
	[87876] = { id = 87876, Voice = '', Text = '嘿，还不赖嘛，那么从现在开始，我就是你的料理导师了。', Speaker = 6447, Option = '', Action = _EmptyTable,}, 
	[87875] = { id = 87875, Voice = '', Text = '嗯？新面孔啊…年轻人，你通过碧莎的入门考核了？', Speaker = 6447, Option = '是的', Action = _EmptyTable,}, 
	[87874] = { id = 87874, Voice = '', Text = '你可以从中学到一些烹饪的基础操作哦！', Speaker = 6446, Option = '', Action = _EmptyTable,}, 
	[87873] = { id = 87873, Voice = '', Text = '完成得不错哦，那么现在我就教你学习烹饪！', Speaker = 6446, Option = '', Action = _EmptyTable,}, 
	[87872] = { id = 87872, Voice = '', Text = '唔，你身上的疯兔肉数量可不够哦，你还是在疯兔身上好好辨认，收集到指定数量后，再来找我吧！', Speaker = 6446, Option = '', Action = _EmptyTable,}, 
	[87871] = { id = 87871, Voice = '', Text = '看来你辨认食材的天赋还不错呢！', Speaker = 6446, Option = '', Action = _EmptyTable,}, 
	[87870] = { id = 87870, Voice = '', Text = '看来你的疯兔肉已经收集完毕了呢，现在你要把疯兔肉交给我吗？', Speaker = 6446, Option = '{交,6},{不交,5}', Action = _EmptyTable,}, 
	[87869] = { id = 87869, Voice = '', Text = '接下来要考另一个食材的辨认了！从疯兔身上收集疯兔肉带给我吧！喏，给你厨神的鹰眼。', Speaker = 6446, Option = '', Action = _EmptyTable,}, 
	[87868] = { id = 87868, Voice = '', Text = '刚才我说过了哦，辨认食材是作为料理师来说最基础的能力。', Speaker = 6446, Option = '', Action = _EmptyTable,}, 
	[87867] = { id = 87867, Voice = '', Text = '唔，你身上的蝗虫腿肉数量可不够哦，你还是在蝗虫身上好好辨认，收集到指定数量后，再来找我吧！', Speaker = 6446, Option = '', Action = _EmptyTable,}, 
	[87866] = { id = 87866, Voice = '', Text = '看来你辨认食材的天赋还不错呢！', Speaker = 6446, Option = '', Action = _EmptyTable,}, 
	[87865] = { id = 87865, Voice = '', Text = '看来你的蝗虫腿肉已经收集完毕了呢，现在你要把蝗虫腿肉交给我吗？', Speaker = 6446, Option = '{交,7},{不交,6}', Action = _EmptyTable,}, 
	[87864] = { id = 87864, Voice = '', Text = '只要你能从蝗虫身上把食材蝗虫腿肉带给我，就算你第一波考核通过了呢。喏，给你厨神的鹰眼。', Speaker = 6446, Option = '交给我吧。', Action = _EmptyTable,}, 
	[87863] = { id = 87863, Voice = '', Text = '嘻嘻，其实考核也很简单啦。料理最基础的不是怎么翻炒，而是食材的辨认。', Speaker = 6446, Option = '', Action = _EmptyTable,}, 
	[87862] = { id = 87862, Voice = '', Text = '我们普隆德拉皇家料理协会最近正在寻找有天赋的料理学员，如果你有兴趣的话，可以来参加我的考核哦！', Speaker = 6446, Option = '参加', Action = _EmptyTable,}, 
	[87861] = { id = 87861, Voice = '', Text = '我想也是！没有人能够拒绝美食的诱惑！', Speaker = 6446, Option = '', Action = _EmptyTable,}, 
	[87860] = { id = 87860, Voice = '', Text = '啊，冒险者，你对那些美味可口的料理感兴趣吗？', Speaker = 6446, Option = '{感兴趣,2},{没兴趣,1}', Action = _EmptyTable,}, 
	[87859] = { id = 87859, Voice = '', Text = '要说这世上最令人幸福的事情，就是在普隆德拉的夕阳下，美美品上一碗令人心醉的料理了！', Speaker = 6446, Option = '料理？', Action = _EmptyTable,}, 
	[87858] = { id = 87858, Voice = '', Text = '每年这时，各地的美食家与厨师都会汇集到普隆德拉，参加这场饕餮盛宴。', Speaker = 6448, Option = '', Action = _EmptyTable,}, 
	[87857] = { id = 87857, Voice = '', Text = '为了王国的荣耀，也为了研究新的料理，培养新的料理人才，特里斯坦三世宣布每年都会举行一次“普隆德拉美食料理大赛”。', Speaker = 6448, Option = '', Action = _EmptyTable,}, 
	[87856] = { id = 87856, Voice = '', Text = '但是朱诺诸神食谱研究院与里希塔乐食品贸易公司的建立，使得皇家协会产生了强烈的危机感。', Speaker = 6448, Option = '', Action = _EmptyTable,}, 
	[87855] = { id = 87855, Voice = '', Text = '一直以来，普隆德拉皇家料理协会在料理上有着至高的地位，没有人能够在料理上与之争锋。', Speaker = 6448, Option = '', Action = _EmptyTable,}, 
	[87854] = { id = 87854, Voice = '', Text = '这对想成为料理师，并且愿意在料理上付出心血的人，是一种肯定，也是一种荣耀。', Speaker = 6448, Option = '', Action = _EmptyTable,}, 
	[87853] = { id = 87853, Voice = '', Text = '当然最重要的还是美味的升级，如果能够成为料理大师，甚至会受到皇室的邀请，成为宫廷料理师。', Speaker = 6448, Option = '', Action = _EmptyTable,}, 
	[87852] = { id = 87852, Voice = '', Text = '这意味着，你可以见识到能多的菜谱，拥有更高的技艺来烹饪食材，使之拥有不同的效果。', Speaker = 6448, Option = '', Action = _EmptyTable,}, 
	[87851] = { id = 87851, Voice = '', Text = '协会建立了料理师等级制度。料理师等级共分为十级，每一级的提升，都代表这烹饪能力的提升。', Speaker = 6448, Option = '', Action = _EmptyTable,}, 
	[87850] = { id = 87850, Voice = '', Text = '将无数新鲜的食材变为可口的料理，施展这种魔术的人被称之为料理师。', Speaker = 6448, Option = '', Action = _EmptyTable,}, 
	[87849] = { id = 87849, Voice = '', Text = '而出于对料理的深刻认识，受到料理的效果也会提升，这也是大部分人想成为美食家的原因。', Speaker = 6448, Option = '', Action = _EmptyTable,}, 
	[87848] = { id = 87848, Voice = '', Text = '美食家等级共分为十级，每一级的提升，都代表这美食鉴赏能力的提升。', Speaker = 6448, Option = '', Action = _EmptyTable,}, 
	[87847] = { id = 87847, Voice = '', Text = '鉴于美食家在料理的重要作用，协会决定建立美食家等级制度。', Speaker = 6448, Option = '', Action = _EmptyTable,}, 
	[87846] = { id = 87846, Voice = '', Text = '美食家们不断品尝料理，他们指出料理的优缺点，使料理师能够对自己的料理有直观的认识，便于改进。', Speaker = 6448, Option = '', Action = _EmptyTable,}, 
	[87845] = { id = 87845, Voice = '', Text = '美味的料理总要有懂得的人欣赏，而美食家就是这种人。', Speaker = 6448, Option = '', Action = _EmptyTable,}, 
	[87844] = { id = 87844, Voice = '', Text = '因为食材的原因，各种料理有时还会对冒险家有意想不到的作用：治疗伤势、解除中毒等等，这使得料理行业在米德加尔特王国备受欢迎，这也是普隆德拉皇家料理协会能够得到皇室支持迅速发展的重要原因。', Speaker = 6448, Option = '', Action = _EmptyTable,}, 
	[87843] = { id = 87843, Voice = '', Text = '经过料理大师的烹饪，各种食材能够最大的发挥自己的作用，成为一道道可口的佳肴。', Speaker = 6448, Option = '', Action = _EmptyTable,}, 
	[87842] = { id = 87842, Voice = '', Text = '旗下分设依斯鲁德料理协会、吉芬魔法料理学院、梦罗克料理协会、斐扬膳食坊、艾尔贝塔水产协会以及艾尔帕兰美食鉴定委员会，研究不同地域不同的特色美食。', Speaker = 6448, Option = '', Action = _EmptyTable,}, 
	[87841] = { id = 87841, Voice = '', Text = '首位协会会长由当时的皇家宫廷料理大师库索担任，经过数百年的发展，皇家料理协会逐渐壮大。', Speaker = 6448, Option = '', Action = _EmptyTable,}, 
	[87840] = { id = 87840, Voice = '', Text = '致力于研究对身体有益且更加美味的料理和培养大批善于烹饪的厨师的普隆德拉皇家料理协会，在特里斯坦大帝的批准下建立。', Speaker = 6448, Option = '', Action = _EmptyTable,}, 
	[87839] = { id = 87839, Voice = '', Text = '料理协会，全称普隆德拉皇家料理协会。', Speaker = 6448, Option = '', Action = _EmptyTable,}, 
	[87536] = { id = 87536, Voice = '', Text = '这就是默斯曼·岁月的故事。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87535] = { id = 87535, Voice = '', Text = '在众人期盼的眼光下，他从首领手中接过【时钟感应器】，正式成为了新的教团领袖。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87534] = { id = 87534, Voice = '', Text = '而默斯曼守护了【黄金时钟】，甚至引动了【黄金时钟】的力量，得到了教团的认可。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87533] = { id = 87533, Voice = '', Text = '教团卫队的到来，代表着这场战斗已经进入尾声。劳伦斯被封印关押在钟楼地下。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87532] = { id = 87532, Voice = '', Text = '劳伦斯的结界彻底爆碎，连带这魔物也被一扫而光。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87531] = { id = 87531, Voice = '', Text = '叮——【黄金时钟】突然响起，一股熟悉的魔力与默斯曼的力量结合，轰然爆开。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87530] = { id = 87530, Voice = '', Text = '短暂的延迟后，巨大的魔力在默斯曼手杖尖端爆开，魔力甚至影响到了【黄金时钟】。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87529] = { id = 87529, Voice = '', Text = '默斯曼在劳伦斯与魔物的袭击下节节败退，默斯曼心一横，决定使用那个最危险的魔法。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87528] = { id = 87528, Voice = '', Text = '劳伦斯的吟唱速度在时间魔法加速的情况下，要比默斯曼快好几倍。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87527] = { id = 87527, Voice = '', Text = '两人开始了华美的魔法对决，时间魔法的诡异在这一刻彻底展现。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87526] = { id = 87526, Voice = '', Text = '尽力阻止劳伦斯夺走【黄金时钟】才是他应该做的事情。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87525] = { id = 87525, Voice = '', Text = '“劳伦斯--”默斯曼用力的握住自己的手杖，他明白现在再说太多已经毫无意义。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87524] = { id = 87524, Voice = '', Text = '真是可惜啊，默斯曼，如果你刚才让我进去拿走【黄金时钟】该多好。他掀开了自己的兜帽，露出了默斯曼无比熟悉的面貌。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87523] = { id = 87523, Voice = '', Text = '那么，我来解开最后的疑问。', Speaker = 1596, Option = '', Action = { num = 1, actionid = 200,},}, 
	[87522] = { id = 87522, Voice = '', Text = '冒险者，这水晶骨的数量不对啊，作为报酬可是不太够呢，你还是再找一些来吧。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87521] = { id = 87521, Voice = '', Text = '冒险者，感谢你的慷慨大方！', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87520] = { id = 87520, Voice = '', Text = '我要的水晶骨收集齐了吗？', Speaker = 1596, Option = '{带来了,2},{还没有,1}', Action = _EmptyTable,}, 
	[87519] = { id = 87519, Voice = '', Text = '讲到这里，冒险者，是收取报酬的时候了。我需要一些水晶骨，你能帮我找来吗？', Speaker = 1596, Option = '', Action = { num = 1, actionid = 201,},}, 
	[87518] = { id = 87518, Voice = '', Text = '默斯曼有些难以置信，冰冷的眼光对视着，默斯曼才嘶哑得开口：果然是你。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87517] = { id = 87517, Voice = '', Text = '一袭黑衣快速的靠近，妄图穿过默斯曼的防线，却被默斯曼的古魔法拦截住。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87516] = { id = 87516, Voice = '', Text = '这是魔法，默斯曼心中明白，只是没有想到，原来是他。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87515] = { id = 87515, Voice = '', Text = '面前的魔物们的行动迟缓起来，他松了一口气，但是紧接着却发现，不仅仅是魔物，他自己的行动也一样迟缓。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87514] = { id = 87514, Voice = '', Text = '他明白，恐怕早已经有人封锁了钟楼的入口，掩盖住了钟楼内的波动。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87513] = { id = 87513, Voice = '', Text = '默斯曼渐渐感觉到怪异，激战了许久，都没有任何人能够发现钟楼的异样。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87512] = { id = 87512, Voice = '', Text = '魔物的暴动来得极其迅速，默斯曼不得已，用家族流传下来的古魔法，成片的击退这些疯狂的魔物。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87511] = { id = 87511, Voice = '', Text = '果然，在黑暗的钟楼里，无数魔物在不知名的力量引诱之下，成群得向默斯曼扑来。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87510] = { id = 87510, Voice = '', Text = '这恐怕是钟楼防御力量最薄弱的一天了，默斯曼有种预感，今晚恐怕幕后黑手会浮出水面。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87509] = { id = 87509, Voice = '', Text = '轮到默斯曼进入钟楼看守了，本来与他一同看守的劳伦斯却因为身体不适没有能够过来。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87508] = { id = 87508, Voice = '', Text = '那我就继续我的故事了。', Speaker = 1596, Option = '', Action = { num = 1, actionid = 200,},}, 
	[87507] = { id = 87507, Voice = '', Text = '干得不错，就是有你这样的冒险者世界才会安定。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87506] = { id = 87506, Voice = '', Text = '我非常担心钟楼内的魔物会影响到楼中的【黄金时钟】，你去消灭一些古钟魔吧。', Speaker = 1596, Option = '', Action = { num = 1, actionid = 201,},}, 
	[87505] = { id = 87505, Voice = '', Text = '而现在，他要尽快查出首领口中的“不安定因素”究竟是谁。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87504] = { id = 87504, Voice = '', Text = '默斯曼转身离开了，对于这个正直的年轻人来说，【黄金时钟】才是他一生需要守护的对象。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87503] = { id = 87503, Voice = '', Text = '明白！默斯曼坚定的回答令老首领十分满意：很好，默斯曼，我没有白培养你。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87502] = { id = 87502, Voice = '', Text = '必定是有人泄漏了换岗布防的时间。如果不能把他找出来，总有一天，我们会守不住这【黄金时钟】的。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87501] = { id = 87501, Voice = '', Text = '这个月【黄金时钟】已经迎来了第三波进攻，每次进攻的时机，都是教团卫队换岗的时期。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87500] = { id = 87500, Voice = '', Text = '教团中，有着不安定的因素，找出来，清理出去，你就可以继承我的位置。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87499] = { id = 87499, Voice = '', Text = '老首领轻轻抚摸着默斯曼的脸颊，笑着说：这是我们的使命，即使你不是教团的领袖，你也要为【黄金时钟】流干自己的血液。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87498] = { id = 87498, Voice = '', Text = '毋庸置疑，我的父亲！默斯曼·岁月坚定地回答。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87497] = { id = 87497, Voice = '', Text = '你愿意承担起大贤者给予我们教团的责任吗？年迈的老首领问。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87496] = { id = 87496, Voice = '', Text = '今天，我们就来讲讲默斯曼·岁月的故事。', Speaker = 1596, Option = '', Action = { num = 1, actionid = 200,},}, 
	[87495] = { id = 87495, Voice = '', Text = '看来你对我们吟游诗人传唱的故事并不是很了解呢，你还是了解了解再来找我吧。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87494] = { id = 87494, Voice = '', Text = '看来你已经对我们传唱的故事了若指掌了呢。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87493] = { id = 87493, Voice = '', Text = '第三题，在库卡·剑齿的故事中，库卡·剑齿希望鲁德去哪里求助？', Speaker = 1596, Option = '{梦罗克,5},{普隆德拉,5},{吉芬,0}', Action = _EmptyTable,}, 
	[87492] = { id = 87492, Voice = '', Text = '那么，请听第二题，在施密特王的故事中，施密特王手下最精锐的军队是什么？', Speaker = 1596, Option = '{皇家卫队,4},{皇家守卫,0},{皇家护卫,4}', Action = _EmptyTable,}, 
	[87491] = { id = 87491, Voice = '', Text = '那么请回答我的第一个问题，在无名者的故事中，谁视无名者为一生追逐的目标？', Speaker = 1596, Option = '{艾勒梅斯,0},{洛斯·拉比尔,3},{伊斯米儿,3}', Action = _EmptyTable,}, 
	[87490] = { id = 87490, Voice = '', Text = '冒险者，你自信的样子真是帅气呢。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87489] = { id = 87489, Voice = '', Text = '再听故事之前，我想问你几个问题，很简单的小问题哦~', Speaker = 1596, Option = '问吧', Action = { num = 1, actionid = 201,},}, 
	[87488] = { id = 87488, Voice = '', Text = '运河之都艾尔帕兰蕴含无数动人的故事，冒险者，你想听吗？', Speaker = 1596, Option = '{我仍然想听,2},{我有空再听吧,1}', Action = _EmptyTable,}, 
	[87487] = { id = 87487, Voice = '', Text = '这就是月狼的传说。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87486] = { id = 87486, Voice = '', Text = '他的弟弟也失去了踪迹，只剩下他孤寂的困锁在月亮之上。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87485] = { id = 87485, Voice = '', Text = '直到某天诸神的黄昏来临，芬里尔因为躲在月球而逃过一劫，月亮的主人月神却再也没有回来。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87484] = { id = 87484, Voice = '', Text = '他的弟弟耶梦加得看到遍体麟伤的芬里尔，决定一定要把他从月亮上救出来。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87483] = { id = 87483, Voice = '', Text = '芬里尔不断的挣扎，却无法挣脱出锁链。月神用仅剩下的一只手，持着魔法鞭子，用力抽打，发泄着失去手臂的愤恨。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87482] = { id = 87482, Voice = '', Text = '月神痛呼一声，拎着锁链，把巨狼带入了月亮，锁链深深埋入月亮之中。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87481] = { id = 87481, Voice = '', Text = '于是，绝望的芬里尔用那比剑还锐利的牙齿咬断了月神的手腕。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87480] = { id = 87480, Voice = '', Text = '芙蕾雅冷笑着：“到了你付出代价的时候了！”', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87479] = { id = 87479, Voice = '', Text = '魔狼凶暴的眼神中，露出哀求的神情，求诸神打开锁链，但诸神一个个都目光移开。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87478] = { id = 87478, Voice = '', Text = '芬里尔也就被这细柔的锁链永远束缚住，他恐惧极了，用尽全身的力量挣扎、跳跃，但无论如何都不能把这条锁链挣断，这是魔法的锁链。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87477] = { id = 87477, Voice = '', Text = '那么，我来为神话作一个完美的收尾。', Speaker = 1596, Option = '', Action = { num = 1, actionid = 200,},}, 
	[87476] = { id = 87476, Voice = '', Text = '冒险者，这魔女星沙的数量不对啊，作为报酬可是不太够呢，你还是再找一些来吧。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87475] = { id = 87475, Voice = '', Text = '冒险者，感谢你的慷慨大方！', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87474] = { id = 87474, Voice = '', Text = '我要的魔女星沙收集齐了吗？', Speaker = 1596, Option = '{带来了,2},{还没有,1}', Action = _EmptyTable,}, 
	[87473] = { id = 87473, Voice = '', Text = '讲到这里，冒险者，是收取报酬的时候了。我需要一些魔女星沙，你能帮我找来吗？', Speaker = 1596, Option = '', Action = { num = 1, actionid = 201,},}, 
	[87472] = { id = 87472, Voice = '', Text = '最后，还是月神向前跨出一步，把他那结实的手腕放进芬里尔的口中。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87471] = { id = 87471, Voice = '', Text = '诸神看着芬里尔口中那两尖锐如刃的利齿，大家都沉默不语。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87470] = { id = 87470, Voice = '', Text = '经过再三劝说激励之后，芬里尔终于答应受缚，可是它提出一个条件：“必需有一位神把手放入我的口中，来保证我的安全，否则，我不愿受缚。”', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87469] = { id = 87469, Voice = '', Text = '为了要煽起狼的傲慢的心理，故意说：“为什么对这根细锁链这么害怕？”', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87468] = { id = 87468, Voice = '', Text = '诸神尽力哄骗芬里尔，对他说：“没什么，我们只不过想用根细锁链试试你那无敌怪力。”', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87467] = { id = 87467, Voice = '', Text = '这锁链看起来很美丽，质地像丝绢一样柔软平滑，但似乎有一种咒力。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87466] = { id = 87466, Voice = '', Text = '当诸神拿着这柔软的锁链来到魔狼芬里尔的身边时，狼对这锁链十分疑惧，拒绝受缚。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87465] = { id = 87465, Voice = '', Text = '秉承着奥丁的命令，侏儒们研究出一种锁链，上面镌刻着密密麻麻的卢恩符文。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87464] = { id = 87464, Voice = '', Text = '诸神已经无计可施，只得向聪慧的侏儒们求助。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87463] = { id = 87463, Voice = '', Text = '那我就继续我的故事了。', Speaker = 1596, Option = '', Action = { num = 1, actionid = 200,},}, 
	[87462] = { id = 87462, Voice = '', Text = '干得不错，就是有你这样的冒险者世界才会安定。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87461] = { id = 87461, Voice = '', Text = '灾祸到处都有，艾尔帕兰也不例外，去钟楼内消灭一些黑魔女吧。', Speaker = 1596, Option = '', Action = { num = 1, actionid = 201,},}, 
	[87460] = { id = 87460, Voice = '', Text = '但是这匹狼只是奋力一跳，就轻轻松松地挣脱出来。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87459] = { id = 87459, Voice = '', Text = '诸神用坚固的铁锻造了九天九夜，锁住了芬里尔，这铁链非常沉重，诸神抬起来的时候都会左右晃动。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87458] = { id = 87458, Voice = '', Text = '诸神都觉得这个办法好，于是他们决定实行芙蕾雅的方法。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87457] = { id = 87457, Voice = '', Text = '芙蕾雅却建议，用坚固的锁链束缚住巨狼，把他封印在荒芜的月亮上。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87456] = { id = 87456, Voice = '', Text = '诸神表示巨狼太过强大，不付出生命的代价，无法杀死这头巨狼。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87455] = { id = 87455, Voice = '', Text = '芙蕾雅暗恨在心，她跑到奥丁面前哭诉：伟大的奥丁，你看——那头邪恶的巨狼给我们带来了多少灾祸！', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87454] = { id = 87454, Voice = '', Text = '大怒的芙蕾雅要芬里尔付出代价，芬里尔——这头桀骜不驯的狼表示芙蕾雅根本没有能力让他付出代价。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87453] = { id = 87453, Voice = '', Text = '某天，芬里尔来路过芙蕾雅的花园，芙蕾雅的花园在芬里尔的灾祸中逐渐凋零。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87452] = { id = 87452, Voice = '', Text = '芬里尔生来伴随着无数灾祸，无论出现在哪里，芬里尔身边的灾祸总会给人们带来死亡。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87451] = { id = 87451, Voice = '', Text = '今天，我们就来讲述一个流传于艾尔帕兰的神话——月狼。', Speaker = 1596, Option = '', Action = { num = 1, actionid = 200,},}, 
	[87450] = { id = 87450, Voice = '', Text = '看来你对我们吟游诗人传唱的故事并不是很了解呢，你还是了解了解再来找我吧。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87449] = { id = 87449, Voice = '', Text = '看来你已经对我们传唱的故事了若指掌了呢。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87448] = { id = 87448, Voice = '', Text = '第三题，在星痕的故事中，星痕曾经想用短刀杀死谁？', Speaker = 1596, Option = '{年幼的大运,5},{年幼的统帅,0},{年幼的金胜,5}', Action = _EmptyTable,}, 
	[87447] = { id = 87447, Voice = '', Text = '那么，请听第二题，在索罗·图克的故事中，索罗·图克的称号是什么？', Speaker = 1596, Option = '{狂怒者,0},{屠戮者,4},{猎杀者,4}', Action = _EmptyTable,}, 
	[87446] = { id = 87446, Voice = '', Text = '那么请回答我的第一个问题，在沙西莉的故事中，沙西莉心爱的男人西蒙特是什么职业？', Speaker = 1596, Option = '{骑士,0},{刺客,3},{魔法师,3}', Action = _EmptyTable,}, 
	[87445] = { id = 87445, Voice = '', Text = '冒险者，你自信的样子真是帅气呢。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87444] = { id = 87444, Voice = '', Text = '再听故事之前，我想问你几个问题，很简单的小问题哦~', Speaker = 1596, Option = '问吧', Action = { num = 1, actionid = 201,},}, 
	[87443] = { id = 87443, Voice = '', Text = '运河之都艾尔帕兰蕴含无数动人的故事，冒险者，你想听吗？', Speaker = 1596, Option = '{我仍然想听,2},{我有空再听吧,1}', Action = _EmptyTable,}, 
	[87441] = { id = 87441, Voice = '', Text = '这就是时钟守护者---摩加达·岁月的故事。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87440] = { id = 87440, Voice = '', Text = '教团步入了正轨，摩加达·岁月再次步入了钟楼，守护在【黄金时钟】身边，只是这一次，他再也没能从钟楼中走出来。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87439] = { id = 87439, Voice = '', Text = '任何企图夺走【黄金时钟】的人，都是【时钟守护者】的敌人。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87438] = { id = 87438, Voice = '', Text = '他组建了一个名为【时钟守护者】的教团，他们信奉【黄金时钟】，坚信黄金时钟能给艾尔帕兰的人们带来幸福，并且以守护【黄金时钟】为教团第一教规。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87437] = { id = 87437, Voice = '', Text = '巴特蒙德远去了，摩加达信守诺言，看守在钟楼深处，钟楼的异变越来越剧烈，强大的魔法战士摩加达也感到疲累。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87436] = { id = 87436, Voice = '', Text = '摩加达看着这个被自己视为父亲的男人，郑重地许下誓言：我必将视它为生命！钟在人在！', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87435] = { id = 87435, Voice = '', Text = '巴特蒙德笑着：我即将远行，以后守护黄金时钟的重任就交给你了。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87434] = { id = 87434, Voice = '', Text = '进过几年的研究，巴特蒙德走出实验室，亲手把一个金黄色的时钟放入了摩加达·岁月的手中。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87433] = { id = 87433, Voice = '', Text = '巴特蒙德来到了运河之都，高耸的钟塔吸引着这位睿智的大贤者。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87432] = { id = 87432, Voice = '', Text = '大贤者巴特蒙德收养了摩加达·岁月，摩加达·岁月在以后的日子里跟随巴特蒙德游历大陆。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87431] = { id = 87431, Voice = '', Text = '那么，我来为故事作一个完美的收尾。', Speaker = 1596, Option = '', Action = { num = 1, actionid = 200,},}, 
	[87430] = { id = 87430, Voice = '', Text = '冒险者，这时间结晶的数量不对啊，作为报酬可是不太够呢，你还是再找一些来吧。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87429] = { id = 87429, Voice = '', Text = '冒险者，感谢你的慷慨大方！', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87428] = { id = 87428, Voice = '', Text = '我要的时间结晶收集齐了吗？', Speaker = 1596, Option = '{带来了,2},{还没有,1}', Action = _EmptyTable,}, 
	[87427] = { id = 87427, Voice = '', Text = '讲到这里，冒险者，是收取报酬的时候了。我需要一些时间结晶，你能帮我找来吗？', Speaker = 1596, Option = '', Action = { num = 1, actionid = 201,},}, 
	[87426] = { id = 87426, Voice = '', Text = '从今天起，你就叫做——摩加达·岁月，但愿岁月能够抚平你的创伤。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87425] = { id = 87425, Voice = '', Text = '白袍男人轻轻地拍动他的身体：忘记这个悲惨的夜晚吧，你应该拥有新的生活。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87424] = { id = 87424, Voice = '', Text = '白袍男人走到他身边，轻轻抱起了他，问道：“没事吧，孩子？”他的眼泪再也抑制不住。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87423] = { id = 87423, Voice = '', Text = '耳边微风传来轻轻一声叹息，炽烈的白色光芒闪烁，魔物被这突如其来的一击贯穿身体！', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87422] = { id = 87422, Voice = '', Text = '魔物咆哮着，向着他奔来。他只是个孩子，他被吓住了。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87421] = { id = 87421, Voice = '', Text = '魔物回过头，静静盯着后面那片灌木丛。风静静抚过，他的冷汗缓缓顺着脸颊淌下，他不敢有动作。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87420] = { id = 87420, Voice = '', Text = '咔擦——他压断了一小节灌木，在这片寂静的森林中，声音显得极其刺耳。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87419] = { id = 87419, Voice = '', Text = '魔物心满意足，缓步离开。他身上被冷汗浸透，魔物刚走不远，他就坐倒在地面上。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87418] = { id = 87418, Voice = '', Text = '这是一种莫大的折磨，他亲眼看着自己的亲人逝去却无能为力。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87417] = { id = 87417, Voice = '', Text = '那我就继续我的故事了。', Speaker = 1596, Option = '', Action = { num = 1, actionid = 200,},}, 
	[87416] = { id = 87416, Voice = '', Text = '干得不错，就是有你这样的冒险者世界才会安定。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87415] = { id = 87415, Voice = '', Text = '魔物的危害由此可见一般，冒险者，消灭他们的重任都在你身上呐。', Speaker = 1596, Option = '', Action = { num = 1, actionid = 201,},}, 
	[87414] = { id = 87414, Voice = '', Text = '那一瞬间，他看到了他的母亲的笑脸，他安静下来，眼睁睁地看着他的母亲被吞噬殆尽。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87413] = { id = 87413, Voice = '', Text = '魔物的利爪撕碎了她的身体，孩子惊恐地看着这一些，忍不住想要大声叫喊。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87412] = { id = 87412, Voice = '', Text = '她不再留恋，转身来到向着那个巨大的魔物奔去。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87411] = { id = 87411, Voice = '', Text = '温柔地笑着，抚摸着他的额头，说：无论发生了什么都不要发出任何声音哦~孩子懵懂地点着头。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87410] = { id = 87410, Voice = '', Text = '孩子……女人不舍地看着自己的孩子，轻轻地把孩子放到灌木丛中。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87409] = { id = 87409, Voice = '', Text = '女人剧烈得喘息着，后面巨大的脚步声越来越近，而她已经没有力气再往前奔走了。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87408] = { id = 87408, Voice = '', Text = '她的怀中还抱着一个怯生生的孩子，他瞪大着自己的双眼，无比惊恐得看着他的妈妈。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87407] = { id = 87407, Voice = '', Text = '火光映照，整个夜空都染成了红色，在树林中，一个美丽的女子奔跑着。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87406] = { id = 87406, Voice = '', Text = '今天，我们就来讲讲摩加达·岁月的故事吧。', Speaker = 1596, Option = '', Action = { num = 1, actionid = 200,},}, 
	[87405] = { id = 87405, Voice = '', Text = '看来你对我们吟游诗人传唱的故事并不是很了解呢，你还是了解了解再来找我吧。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87404] = { id = 87404, Voice = '', Text = '看来你已经对我们传唱的故事了若指掌了呢。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87403] = { id = 87403, Voice = '', Text = '第三题，在盖因·诺斯的故事中，盖因·诺斯心爱的女子是谁？', Speaker = 1596, Option = '{莱伊娜丝,5},{医者诺雅,5},{澈,0}', Action = _EmptyTable,}, 
	[87402] = { id = 87402, Voice = '', Text = '那么，请听第二题，在洛斯·拉比尔的故事中，洛斯·拉比尔后来加入了哪个组织？', Speaker = 1596, Option = '{冒险者公会,4},{女神之手,0},{刺客公会,4}', Action = _EmptyTable,}, 
	[87401] = { id = 87401, Voice = '', Text = '那么请回答我的第一个问题，在伊丝米儿的故事中，暗影之手消灭了哪个魔物？', Speaker = 1596, Option = '{卡仑,3},{死灵,3},{德古拉,0}', Action = _EmptyTable,}, 
	[87400] = { id = 87400, Voice = '', Text = '冒险者，你自信的样子真是帅气呢。', Speaker = 1596, Option = '', Action = _EmptyTable,}, 
	[87399] = { id = 87399, Voice = '', Text = '再听故事之前，我想问你几个问题，很简单的小问题哦~', Speaker = 1596, Option = '问吧', Action = { num = 1, actionid = 201,},}, 
	[87398] = { id = 87398, Voice = '', Text = '运河之都艾尔帕兰蕴含无数动人的故事，冒险者，你想听吗？', Speaker = 1596, Option = '{我仍然想听,2},{我有空再听吧,1}', Action = _EmptyTable,}, 
	[87331] = { id = 87331, Voice = '', Text = '在这世界上并不缺少创造者，区别在于，一个创造出真正的幸福，而另一个只是幻想罢了。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87330] = { id = 87330, Voice = '', Text = '于是，每晚十二点过后的，总有一双阴森的双眼注视着钟楼二层。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87329] = { id = 87329, Voice = '', Text = '同时他又是勇敢的，他为了救阿奇索伦永陷黑暗，即使在这个时候，他也没有忘记让蒙妮卡接替了他的工作。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87328] = { id = 87328, Voice = '', Text = '他决定扮成鬼影，驱逐那些误入这里的人们。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87327] = { id = 87327, Voice = '', Text = '德斯黎便是创造者，他在创造一个通向安全的方向牌。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87326] = { id = 87326, Voice = '', Text = '时间是世界上一切成就的土壤。时间给空想者痛苦，给创造者幸福。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87325] = { id = 87325, Voice = '', Text = '你还没有看穿吗？再好好想想吧！', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87324] = { id = 87324, Voice = '', Text = '现在，你知道钟楼魅影一开始是谁扮演的吗？', Speaker = 6561, Option = '{蒙妮卡,2},{德斯黎,3},{阿奇索伦,2},{斯科特,2}', Action = _EmptyTable,}, 
	[87323] = { id = 87323, Voice = '', Text = '这就是[c][ffff00]【时间魔石·现在】[-][/c]想要告诉我的内容。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87322] = { id = 87322, Voice = '', Text = '于是，每晚十二点过后的，总有一双阴森的双眼注视着钟楼二层。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87321] = { id = 87321, Voice = '', Text = '当德斯黎为了救阿奇索伦深陷黑暗，他临死前的最后一句话，让蒙妮卡接替了他的工作。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87320] = { id = 87320, Voice = '', Text = '研究很失败，他对黑洞毫无办法，他决定扮成鬼影，驱逐那些误入这里的人们。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87319] = { id = 87319, Voice = '', Text = '在斯科特于黑洞中消失之后，德斯黎开始研究黑洞。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87318] = { id = 87318, Voice = '', Text = '时间是世界上一切成就的土壤。时间给空想者痛苦，给创造者幸福。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87317] = { id = 87317, Voice = '', Text = '[c][ffff00]【时间魔石·现在】[-][/c]已解锁，斯特里尔会告诉你它的内容。', Speaker = 6411, Option = '', Action = _EmptyTable,}, 
	[87306] = { id = 87306, Voice = '', Text = '时间魔石记录着的现在，在眼前悄然浮现……', Speaker = 6411, Option = '', Action = _EmptyTable,}, 
	[87305] = { id = 87305, Voice = '', Text = '陈旧的魔石散发着腐朽的力量，那是时间的力量。', Speaker = 6411, Option = '{捡起,3},{没兴趣,2}', Action = _EmptyTable,}, 
	[87304] = { id = 87304, Voice = '', Text = '出现[c][ffff00]【时间魔石·现在】[-][/c]关键道具。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87303] = { id = 87303, Voice = '', Text = '一番激烈的战斗后阿奇索伦摘下了鬼影的面具，却发现鬼影竟然是蒙妮卡！', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87302] = { id = 87302, Voice = '', Text = '经过许久的等待，他终于等到了那个鬼影。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87301] = { id = 87301, Voice = '', Text = '蒙妮卡劝他不要再去那个诡异的地方了，但是阿奇索伦却毫不在意。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87300] = { id = 87300, Voice = '', Text = '时间魔石却给我们带来了消息。阿奇索伦坚持认为德斯黎与斯科特的逝去与神秘的鬼影有关。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87299] = { id = 87299, Voice = '', Text = '时间，捉摸不透，人们无法看到未来究竟发生了什么。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87298] = { id = 87298, Voice = '', Text = '[c][ffff00]【时间魔石·未来】[-][/c]已解锁，斯特里尔会告诉你它的内容。', Speaker = 6410, Option = '', Action = _EmptyTable,}, 
	[87290] = { id = 87290, Voice = '', Text = '时间魔石承载着的未来，在眼前悄然浮现……', Speaker = 6410, Option = '', Action = _EmptyTable,}, 
	[87289] = { id = 87289, Voice = '', Text = '陈旧的魔石散发着腐朽的力量，那是时间的力量。', Speaker = 6410, Option = '{捡起,2},{没兴趣,1}', Action = _EmptyTable,}, 
	[87288] = { id = 87288, Voice = '', Text = '但是他没有想到，在时间黑洞结束之后，一颗魔石坠落到地面之上。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87286] = { id = 87286, Voice = '', Text = '斯科特狠狠的推开德斯黎，自己却因为反作用力被黑洞吞噬。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87285] = { id = 87285, Voice = '', Text = '在这场拉锯战中，人最终是失败者，但是斯科特选择了保全朋友，牺牲自己。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87284] = { id = 87284, Voice = '', Text = '他想抓住斯科特，时间黑洞吞噬着周围的一切。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87283] = { id = 87283, Voice = '', Text = '斯科特与德斯黎显然是一对好朋友。在黑洞出现的那刻，德斯黎拼命向前。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87282] = { id = 87282, Voice = '', Text = '[c][ffff00]【时间魔石·过往】[-][/c]已解锁，斯特里尔会告诉你它的内容。', Speaker = 6409, Option = '', Action = _EmptyTable,}, 
	[87273] = { id = 87273, Voice = '', Text = '时间魔石承载着的过往，在眼前悄然浮现……', Speaker = 6409, Option = '', Action = _EmptyTable,}, 
	[87272] = { id = 87272, Voice = '', Text = '陈旧的魔石散发着腐朽的力量，那是时间的力量。', Speaker = 6409, Option = '{捡起,2},{没兴趣,1}', Action = _EmptyTable,}, 
	[87271] = { id = 87271, Voice = '', Text = '出现[c][ffff00]【时间魔石】[-][/c]关键道具。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87270] = { id = 87270, Voice = '', Text = '鬼魅的真面目究竟是什么呢？我对此很感兴趣。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87269] = { id = 87269, Voice = '', Text = '三人还是决定前往钟楼一探究竟，结果他们却遭遇了魔物的袭击…', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87268] = { id = 87268, Voice = '', Text = '但是蒙妮卡和德斯黎却不置可否，但是耐不住阿奇索伦的软磨硬泡，同时也是担心阿奇索伦的安稳。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87267] = { id = 87267, Voice = '', Text = '只不过因为某种原因失去了力量与记忆，才演变成现在这幅模样。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87266] = { id = 87266, Voice = '', Text = '而没过几天，钟楼就传出了关于鬼魅的传说，阿奇索伦认为，那个鬼魅就是斯科特。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87265] = { id = 87265, Voice = '', Text = '他们三人的至交好友斯科特突然在这钟楼二层失踪。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87264] = { id = 87264, Voice = '', Text = '时间的力量变幻莫测，时间黑洞却告知了过往。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87263] = { id = 87263, Voice = '', Text = '德斯黎的指环已解锁，斯特里尔会告诉你它的内容。', Speaker = 6408, Option = '', Action = _EmptyTable,}, 
	[87254] = { id = 87254, Voice = '', Text = '不知名的魔力爆发，周围形成了一个小型的时间黑洞…', Speaker = 6408, Option = '', Action = _EmptyTable,}, 
	[87253] = { id = 87253, Voice = '', Text = '银白色的指环泛着微光，散发着一股不知名的魔力……', Speaker = 6408, Option = '{捡起,2},{没兴趣,1}', Action = _EmptyTable,}, 
	[87252] = { id = 87252, Voice = '', Text = '某天夜里，阿奇索伦带着自己的朋友德斯黎与蒙妮卡，进入了钟楼二层……', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87251] = { id = 87251, Voice = '', Text = '但是阿奇索伦却是对于这个传说嗤之以鼻，他觉得这个幽魂应该是他某个熟悉的友人……', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
	[87250] = { id = 87250, Voice = '', Text = '胆子小的冒险者们听完了这个传说，对钟楼隐藏着幽魂的消息深信不疑。', Speaker = 6561, Option = '', Action = _EmptyTable,}, 
