	[86862] = { id = 86862, Voice = '', Text = '恭喜你冒险者，你开启了神碑之力，希望你能够好好利用阿萨神碑的力量！', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86861] = { id = 86861, Voice = '', Text = '不过现在，还是先让我我送你离开这里吧。', Speaker = 6282, Option = '', Action = _EmptyTable,}, 
	[86860] = { id = 86860, Voice = '', Text = '好了冒险者，神碑之力已经开启，出去之后你可以细细体会神碑给你带来的力量。', Speaker = 6282, Option = '', Action = _EmptyTable,}, 
	[86859] = { id = 86859, Voice = '', Text = '开始吧！', Speaker = 6282, Option = '', Action = _EmptyTable,}, 
	[86858] = { id = 86858, Voice = '', Text = '如果你干得足够好，神是不会吝啬奖励的。好了，现在我就帮你开启神碑之力。', Speaker = 6270, Option = '好的', Action = _EmptyTable,}, 
	[86857] = { id = 86857, Voice = '', Text = '赐予你们阿萨神碑碎片的本意就是希望人类冒险者能够帮助我们办一些事。', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86856] = { id = 86856, Voice = '', Text = '你已经完成了我的试炼，很简单的试炼，不是吗？', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86855] = { id = 86855, Voice = '', Text = '你这是在欺骗一个神吗？你根本没有足够的尼伯龙根碎片！', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86854] = { id = 86854, Voice = '', Text = '你果然拥有足够的贡献！', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86853] = { id = 86853, Voice = '', Text = '你已经拥有足够的尼伯龙根碎片！', Speaker = 6270, Option = '{是的,3},{没有,2}', Action = _EmptyTable,}, 
	[86852] = { id = 86852, Voice = '', Text = '带上尼伯龙根碎片之后再来找我吧！', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86851] = { id = 86851, Voice = '', Text = '如果你拥有尼伯龙根碎片，才能证明你是一个有所贡献的冒险者。', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86850] = { id = 86850, Voice = '', Text = '拥有足够的实力只是其中之一，你还要有所贡献。', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86849] = { id = 86849, Voice = '', Text = '干的不错，你用魔物的鲜血证明了自己的力量！', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86848] = { id = 86848, Voice = '', Text = '去兽人村消灭你们人类的敌人来证明自己的力量吧。', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86847] = { id = 86847, Voice = '', Text = '强大的力量是驾驭神碑的基础条件，没有足够的力量，你只能从神碑上看到一团迷雾。', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86846] = { id = 86846, Voice = '', Text = '首先，我必须得确认你是否拥有足够的力量。现在你想开始进行试炼吗？', Speaker = 6270, Option = '{是的,3},{没兴趣,2}', Action = _EmptyTable,}, 
	[86845] = { id = 86845, Voice = '', Text = '当然，只要你达到了我的要求，我可以帮助你开启神碑之力。', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86844] = { id = 86844, Voice = '', Text = '阿萨神碑的力量凡人不可能一开始就学习到，越是高深的知识，人类就越难以触及。', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86843] = { id = 86843, Voice = '', Text = '完成进阶二转的试炼之后，再来找我吧！', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86842] = { id = 86842, Voice = '', Text = '阿萨神碑蕴含着无尽的智慧与知识，只要你拥有足够的力量我就可以帮助你开启神碑被封存的力量。', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86302] = { id = 86302, Voice = '', Text = '所以，冒险者，你想得到阿萨神碑的力量吗？', Speaker = 6271, Option = '', Action = _EmptyTable,}, 
	[86301] = { id = 86301, Voice = '', Text = '这是为了取得尼伯龙根碎片，为了防止下一次诸神的黄昏，也是为了这个世界的未来。', Speaker = 6271, Option = '', Action = _EmptyTable,}, 
	[86300] = { id = 86300, Voice = '', Text = '我们决定帮助一部分拥有潜力的人类，赐予他们阿萨神碑的碎片，让他们获得匹敌英灵的力量。', Speaker = 6271, Option = '', Action = _EmptyTable,}, 
	[86299] = { id = 86299, Voice = '', Text = '为了取得尼伯龙根的碎片，我们决定借助冒险者们的力量，但是冒险者的力量在我们看来过于弱小。', Speaker = 6271, Option = '', Action = _EmptyTable,}, 
	[86298] = { id = 86298, Voice = '', Text = '终于再次建立起了新的世界，而这块石板也帮助众神获得了无与伦比的智慧与知识，它也有了新的名字-阿萨神碑。', Speaker = 6271, Option = '', Action = _EmptyTable,}, 
	[86297] = { id = 86297, Voice = '', Text = '第一次诸神黄昏，为了世界免受灭顶之灾，侏儒的领袖将这块石板的一部分献给了奥丁，弥留之际奥丁将石板托付给了女武神——亚尔薇特。', Speaker = 6271, Option = '', Action = _EmptyTable,}, 
	[86296] = { id = 86296, Voice = '', Text = '侏儒们会围绕着它们的智慧源泉，一块充满神奇魔力的石板的周围，休息，跳舞，讲故事，这是一块印满卢恩符文的石板，它赐予侏儒无限的智慧与深邃的知识。', Speaker = 6271, Option = '', Action = _EmptyTable,}, 
	[86295] = { id = 86295, Voice = '', Text = '在世界之树支撑的九个国度中，有着一些阿斯嘉德所未曾注意到的地方，其中有一个叫做瓦特阿尔海姆的美丽森林。', Speaker = 6271, Option = '', Action = _EmptyTable,}, 
	[86294] = { id = 86294, Voice = '', Text = '所以我们决定派遣一些优秀的冒险者进入神弃之地，帮助我们取得尼伯龙根碎片。', Speaker = 6271, Option = '', Action = _EmptyTable,}, 
	[86293] = { id = 86293, Voice = '', Text = '我们决定从神弃之地取得尼伯龙根碎片，但是英灵殿的英灵数量本就不多，而神弃之地又禁止神的进入。', Speaker = 6271, Option = '', Action = _EmptyTable,}, 
	[86292] = { id = 86292, Voice = '', Text = '尼伯龙根的稀少程度很让我们烦恼，直到我们的英灵从神弃之地，发现了这种金属。', Speaker = 6271, Option = '', Action = _EmptyTable,}, 
	[86291] = { id = 86291, Voice = '', Text = '他们派遣我们来收集这种尼伯龙根碎片，用以制造更多的神器。', Speaker = 6271, Option = '', Action = _EmptyTable,}, 
	[86290] = { id = 86290, Voice = '', Text = '而尼伯龙根是诸神用以制作神器的材料之一，残存的神认为为了维护世界的安定需要更多的神器。', Speaker = 6271, Option = '', Action = _EmptyTable,}, 
	[86289] = { id = 86289, Voice = '', Text = '在神弃之力的侵蚀下，这些珍贵的金属矿石逐渐被改造，成为另外一种金属——拥有魔金之称的尼伯龙根。', Speaker = 6271, Option = '', Action = _EmptyTable,}, 
	[86288] = { id = 86288, Voice = '', Text = '诸神在瓦尔哈拉囤积着许多珍贵的金属矿石，诸神黄昏爆发后，这里被神弃之力所笼罩。', Speaker = 6271, Option = '', Action = _EmptyTable,}, 
	[86287] = { id = 86287, Voice = '', Text = '神把它称之为“神弃之力”，这就是神弃之地瓦尔哈拉废墟的由来。', Speaker = 6271, Option = '', Action = _EmptyTable,}, 
	[86286] = { id = 86286, Voice = '', Text = '巨人们在这里也会受到死去的诸神血肉的排斥。大战死去的诸神与巨人的力量在这里交织，形成了一股新的力量。', Speaker = 6271, Option = '', Action = _EmptyTable,}, 
	[86285] = { id = 86285, Voice = '', Text = '这股力量来源于诸神与巨人的血肉，他们在这里不断交战，诸神想要靠近这里，就会遭到巨人血肉的排斥。', Speaker = 6271, Option = '', Action = _EmptyTable,}, 
	[86284] = { id = 86284, Voice = '', Text = '战争结束后，残存下来的神想要回到这片曾经带给他们欢乐的花园，但是却被一股诡异的力量阻止了。', Speaker = 6271, Option = '', Action = _EmptyTable,}, 
	[86283] = { id = 86283, Voice = '', Text = '他们来势汹汹，诸神率先撤离了瓦尔哈拉，巨人们也紧随其后离开了这片废墟。', Speaker = 6271, Option = '', Action = _EmptyTable,}, 
	[86282] = { id = 86282, Voice = '', Text = '这里率先成为诸神黄昏的主战场，巨人与神在这里大打出手，将整座天空花园打得支离破碎。', Speaker = 6271, Option = '', Action = _EmptyTable,}, 
	[86281] = { id = 86281, Voice = '', Text = '诸神与巨人族的战争开始了。巨人们从一开始就对这美丽富饶的瓦尔哈拉充满敌意。', Speaker = 6271, Option = '', Action = _EmptyTable,}, 
	[86280] = { id = 86280, Voice = '', Text = '天空花园瓦尔哈拉，是诸神们曾经享乐的花园。诸神们在这里度过无数欢乐的岁月。', Speaker = 6271, Option = '', Action = _EmptyTable,}, 
	[86279] = { id = 86279, Voice = '', Text = '好了，我现在送你离开这里！', Speaker = 6282, Option = '', Action = _EmptyTable,}, 
	[86278] = { id = 86278, Voice = '', Text = '现在，接下这块神碑碎片，用所学到的知识以及力量守护大陆吧！', Speaker = 6282, Option = '', Action = _EmptyTable,}, 
	[86277] = { id = 86277, Voice = '', Text = '现在，我将赐予你阿萨神碑的碎片。对于你们冒险者来说即便是手掌大小的一块碎片都需要大量时间研究才能真正的解开石板中的全部知识。', Speaker = 6282, Option = '', Action = _EmptyTable,}, 
	[86276] = { id = 86276, Voice = '', Text = '你也用自己的实力，向我们，向阿萨神碑证明了你能够帮助到我们。', Speaker = 6282, Option = '', Action = _EmptyTable,}, 
	[86275] = { id = 86275, Voice = '', Text = '经过了贪婪、嫉妒、冷漠、暴怒四狱的考验，你用行动向我们证明你是一个正直善良的冒险者。', Speaker = 6282, Option = '', Action = _EmptyTable,}, 
	[86274] = { id = 86274, Voice = '', Text = '接下来，准备迎接阿萨神碑的考验吧！', Speaker = 6282, Option = '', Action = _EmptyTable,}, 
	[86273] = { id = 86273, Voice = '', Text = '你已经获得了阿萨神碑的碎片，希望你能尽快强大起来！', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86272] = { id = 86272, Voice = '', Text = '现在你想要开始阿萨神碑的考验吗？', Speaker = 6270, Option = '{准备好了,2},{再等等,1}', Action = _EmptyTable,}, 
	[86271] = { id = 86271, Voice = '', Text = '恭喜你冒险者，你通过了我们的考验，现在只需要去获得阿萨神碑的认可就可以了。', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86270] = { id = 86270, Voice = '', Text = '你已经被冷漠支配了！', Speaker = 6280, Option = '', Action = _EmptyTable,}, 
	[86269] = { id = 86269, Voice = '', Text = '看呐，一个小女孩被饿狼盯上了，如果上去救了她，你可能会死去，现在你会怎么选择呢？', Speaker = 6280, Option = '{冷漠对待,2},{尽力而为,3}', Action = _EmptyTable,}, 
	[86268] = { id = 86268, Voice = '', Text = '开始你的考验吧！这次你将迎来冷漠梦魇的考问！', Speaker = 6282, Option = '', Action = _EmptyTable,}, 
	[86267] = { id = 86267, Voice = '', Text = '你是一个热情而又正直的冒险者，恭喜你完成了冷漠之狱考验。', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86266] = { id = 86266, Voice = '', Text = '准备开始进行考验吧！你准备好了吗？', Speaker = 6270, Option = '{准备好了,2},{再等等,1}', Action = _EmptyTable,}, 
	[86265] = { id = 86265, Voice = '', Text = '如果人人都冷漠不堪，那么世界将会变成什么样子？', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86264] = { id = 86264, Voice = '', Text = '人类的强大之处，在于他们团结有爱，在冒险时，总有同伴愿意为你伸出援助的双手。', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86263] = { id = 86263, Voice = '', Text = '你已经被嫉妒支配了！', Speaker = 6275, Option = '', Action = _EmptyTable,}, 
	[86262] = { id = 86262, Voice = '', Text = '一个是赫赫有名的天才魔法师，一个是没什么天赋但却努力的小法师，你觉得庞德应该嫉妒凯特莉娜吗？', Speaker = 6275, Option = '{嫉妒,2},{自己努力,3}', Action = _EmptyTable,}, 
	[86261] = { id = 86261, Voice = '', Text = '恭喜你做出了正确的选择，完成了嫉妒之狱考验。', Speaker = 6282, Option = '', Action = _EmptyTable,}, 
	[86259] = { id = 86259, Voice = '', Text = '开始你的考验吧！你将迎来嫉妒梦魇的诘问！', Speaker = 6282, Option = '', Action = _EmptyTable,}, 
	[86258] = { id = 86258, Voice = '', Text = '那么准备开始进行考验吧！你准备好了吗？', Speaker = 6270, Option = '{准备好了,2},{再等等,1}', Action = _EmptyTable,}, 
	[86257] = { id = 86257, Voice = '', Text = '想要得到强大的力量，必须战胜你心中的嫉妒。', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86256] = { id = 86256, Voice = '', Text = '嫉妒往往是引导人类走入歧途的罪魁祸首。一颗充满嫉妒的心，是没有资格得到阿萨神碑的认可，神也不会认可！', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86255] = { id = 86255, Voice = '', Text = '你已经被贪婪支配了！', Speaker = 6273, Option = '', Action = _EmptyTable,}, 
	[86253] = { id = 86253, Voice = '', Text = '这里有两个木箱，里面装着无数宝藏，你想打开哪个？还是…全部都占为己有呢？', Speaker = 6273, Option = '{全部打开,2},{打开一个,3}', Action = _EmptyTable,}, 
	[86252] = { id = 86252, Voice = '', Text = '开始你的考验吧！你将迎来贪婪梦魇的诱惑！', Speaker = 6282, Option = '', Action = _EmptyTable,}, 
	[86251] = { id = 86251, Voice = '', Text = '恭喜你做出了正确的选择，完成了贪婪之狱考验。', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86250] = { id = 86250, Voice = '', Text = '那么准备开始进行考验吧！你准备好了吗？', Speaker = 6270, Option = '{准备好了,2},{再等等,1}', Action = _EmptyTable,}, 
	[86249] = { id = 86249, Voice = '', Text = '搜集尼伯龙根是我们最重要的任务，我绝对不会允许贪婪之徒混入其中！', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86248] = { id = 86248, Voice = '', Text = '贪婪无疑是最令人讨厌的，但是很遗憾，贪婪的强大甚至连神都无法避免。', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86247] = { id = 86247, Voice = '', Text = '很遗憾你没有通过考验，不过我愿意再给你一次机会。你要再考核一次吗？', Speaker = 6282, Option = '愿意', Action = _EmptyTable,}, 
	[86246] = { id = 86246, Voice = '', Text = '你已经被愤怒支配了！', Speaker = 6272, Option = '', Action = _EmptyTable,}, 
	[86245] = { id = 86245, Voice = '', Text = '我就是你前进路上的梦魇，来让我见识一下你愤怒的力量吧！', Speaker = 6272, Option = '{愤怒迎战,2},{冷静思考,3}', Action = _EmptyTable,}, 
	[86244] = { id = 86244, Voice = '', Text = '无法控制自己的愤怒了？那就不要控制，释放出你的怒火！', Speaker = 6272, Option = '', Action = _EmptyTable,}, 
	[86243] = { id = 86243, Voice = '', Text = '愤怒吧！杀光这些阻挠你的魔物！', Speaker = 6272, Option = '', Action = _EmptyTable,}, 
	[86242] = { id = 86242, Voice = '', Text = '开始你的考验吧！你将迎来狂怒梦魇的诘问！', Speaker = 6282, Option = '', Action = _EmptyTable,}, 
	[86241] = { id = 86241, Voice = '', Text = '恭喜你成功控制了自己的情绪，完成了狂怒之狱考验。', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86240] = { id = 86240, Voice = '', Text = '那么准备开始进行考验吧！你准备好了吗？', Speaker = 6270, Option = '{准备好了,2},{再等等,1}', Action = _EmptyTable,}, 
	[86239] = { id = 86239, Voice = '', Text = '所以能否控制好这些负面情绪，并且使之成为你们强大的助力，才是我们所需要的强者。', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86238] = { id = 86238, Voice = '', Text = '暴怒就是其中一种，但是有些时候这些负面情绪是可以给你们带来力量的。', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86237] = { id = 86237, Voice = '', Text = '人类是一种复杂的生物，你们拥有诸多负面情绪，也有着善良稳重的一面。', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86236] = { id = 86236, Voice = '', Text = '只要通过了神与阿萨神碑的考验，你就能得到强大的力量。你想得到可以媲美英灵的力量吗？', Speaker = 6270, Option = '{我想,0},{没兴趣…,1}', Action = _EmptyTable,}, 
	[86235] = { id = 86235, Voice = '', Text = '但是并不是所有人类都有资格得到神的赐予，只有当中小部分拥有天赋的冒险者才有这等殊荣。', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[86234] = { id = 86234, Voice = '', Text = '为了取得尼伯龙根的碎片，我们需要人类的支持。相对得，我们也会赐予人类强大的力量，那就是阿萨神碑的碎片。', Speaker = 6270, Option = '', Action = _EmptyTable,}, 
	[85025] = { id = 85025, Voice = '', Text = '干的不错，冒险者！感谢你的帮助，你可以去微笑小姐那里购买职业专属头饰啦。', Speaker = 1106, Option = '', Action = _EmptyTable,}, 
	[85024] = { id = 85024, Voice = '', Text = '你完成得太出色了，接下来，你只需要帮忙清理一些榔头犬妖就可以了！', Speaker = 1106, Option = '好吧。', Action = _EmptyTable,}, 
	[85023] = { id = 85023, Voice = '', Text = '咦？这是我要的两颗极效精准魔石吗？', Speaker = 1106, Option = '', Action = _EmptyTable,}, 
	[85022] = { id = 85022, Voice = '', Text = '太好了，这就是我要的极效精准魔石！', Speaker = 1106, Option = '', Action = _EmptyTable,}, 
	[85021] = { id = 85021, Voice = '', Text = '我要的两颗极效精准魔石带来了吗？', Speaker = 1106, Option = '{带来了,3},{没有,2}', Action = _EmptyTable,}, 
	[85020] = { id = 85020, Voice = '', Text = '所以我希望你能提供两颗极效精准魔石，用来应付日益危险的魔物。', Speaker = 1106, Option = '明白', Action = _EmptyTable,}, 
	[85019] = { id = 85019, Voice = '', Text = '在与魔物的战斗中，我们神工匠做制作的道具卖得越来越好，我们的库存已经严重不足了。', Speaker = 1106, Option = '', Action = _EmptyTable,}, 
	[85018] = { id = 85018, Voice = '', Text = '这点深渊花可不太足够呢。', Speaker = 1106, Option = '', Action = _EmptyTable,}, 
	[85017] = { id = 85017, Voice = '', Text = '太棒了冒险者！这真是雪中送炭啊！', Speaker = 1106, Option = '', Action = _EmptyTable,}, 
	[85016] = { id = 85016, Voice = '', Text = '100份深渊花准备好了吗？', Speaker = 1106, Option = '{带来了,3},{没有,2}', Action = _EmptyTable,}, 
	[85015] = { id = 85015, Voice = '', Text = '所以我想请你带回来一些深渊花，补充仓库的库存。', Speaker = 1106, Option = '交给我吧', Action = _EmptyTable,}, 
	[85014] = { id = 85014, Voice = '', Text = '最近各地的魔物都呈现出不安分的状态，我们冒险者公会为了对抗魔物的暴动，消耗了大量的材料。', Speaker = 1106, Option = '', Action = _EmptyTable,}, 
	[85013] = { id = 85013, Voice = '', Text = '你只需要为我们冒险者公会做一些事情。', Speaker = 1106, Option = '', Action = _EmptyTable,}, 
	[85012] = { id = 85012, Voice = '', Text = '当然，这需要一点小小的代价……', Speaker = 1106, Option = '什么代价', Action = _EmptyTable,}, 
	[85011] = { id = 85011, Voice = '', Text = '我们冒险者公会为每一位通过考核的神工匠准备了相应的福利哦~', Speaker = 1106, Option = '', Action = _EmptyTable,}, 
	[85010] = { id = 85010, Voice = '', Text = '作为你的导师，我为你有今天这样的成就而感到骄傲。', Speaker = 1106, Option = '', Action = _EmptyTable,}, 
	[85009] = { id = 85009, Voice = '', Text = '衷心的祝福你，冒险者，你成功通过了考验，正式成为了一名神工匠。', Speaker = 1106, Option = '', Action = _EmptyTable,}, 
	[84943] = { id = 84943, Voice = '', Text = '咦？这是我要的铝吗？', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84942] = { id = 84942, Voice = '', Text = '干得不错~这就是我要的铝。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84941] = { id = 84941, Voice = '', Text = '冒险者，我要的铝带来了吗？', Speaker = 1592, Option = '{带来了,3},{没有,2}', Action = { num = 1, actionid = 201,},}, 
	[84940] = { id = 84940, Voice = '', Text = '哦~冒险者，故事讲完了，我该收取我的报酬了~请带给我一些铝吧！', Speaker = 1592, Option = '', Action = { num = 1, actionid = 201,},}, 
	[84939] = { id = 84939, Voice = '', Text = '拯救她，也拯救这些无辜的灵魂。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84938] = { id = 84938, Voice = '', Text = '同时，她也在期待着，有朝一日，月神能够降临此地', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84937] = { id = 84937, Voice = '', Text = '她现在唯一能做的就是避免自己陷入狂暴', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84936] = { id = 84936, Voice = '', Text = '令她感到恐惧的是，伊米尔之心碎片的力量正在逐渐侵蚀着自己的灵魂', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84935] = { id = 84935, Voice = '', Text = '用身体中大部分力量，封印住着伊米尔之心，使事态不再进一步扩大', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84934] = { id = 84934, Voice = '', Text = '这一切都是因为她的疏忽，这让她自责不已。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84933] = { id = 84933, Voice = '', Text = '而他们在伊米尔之心的影响下，逐渐变得极具攻击性', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84932] = { id = 84932, Voice = '', Text = '这种力量是如此的不详，吸引来了土人与九尾狐的窥视', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84931] = { id = 84931, Voice = '', Text = '在她沉睡不久，石头就开始若有若无的散发出一种奇特的力量', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84930] = { id = 84930, Voice = '', Text = '它持续释放着自己诡异的力量', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84929] = { id = 84929, Voice = '', Text = '然而伊米尔之心的碎片并没有消失', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84928] = { id = 84928, Voice = '', Text = '安抚住信徒后，她便陷入城市地底沉睡', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84927] = { id = 84927, Voice = '', Text = '催动她的铃铛，使人们心中的不安渐渐平息', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84926] = { id = 84926, Voice = '', Text = '信徒们惊恐地抱作一团，月夜猫凝聚仅存的神力', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84925] = { id = 84925, Voice = '', Text = '城中的信徒死伤无数，而她承受住了大部分冲击', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84924] = { id = 84924, Voice = '', Text = '地底深处的城市受到了伊米尔之心巨大的冲击，往日繁荣的城市毁于一旦', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84923] = { id = 84923, Voice = '', Text = '然而，始祖巨人何其强大，哪怕是巨人心脏中一块小小的碎片', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84922] = { id = 84922, Voice = '', Text = '干的不错冒险者~我可以继续我的故事了~', Speaker = 1592, Option = '', Action = { num = 1, actionid = 200,},}, 
	[84921] = { id = 84921, Voice = '', Text = '我曾经前往斐扬洞穴寻找失落的城市，但是我被里面的那些恐怖的魔物吓到了。你能帮我清理一些转转蛋吗？', Speaker = 1592, Option = '好的', Action = { num = 1, actionid = 201,},}, 
	[84920] = { id = 84920, Voice = '', Text = '避免与伊米尔之心发生正面的冲撞', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84919] = { id = 84919, Voice = '', Text = '在剧烈的颤抖中，整座城市缓缓地沉入地下', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84918] = { id = 84918, Voice = '', Text = '城中的信徒们纷纷跪地祈祷，大声呼喊月神之名', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84917] = { id = 84917, Voice = '', Text = '一道土黄色的光幕划破天际', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84916] = { id = 84916, Voice = '', Text = '召唤出土元素领域——由于神力的加持，土元素领域直接覆盖了全程', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84915] = { id = 84915, Voice = '', Text = '她瞬间凝聚起厚重的土元素', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84914] = { id = 84914, Voice = '', Text = '神赋予她的力量，在此刻终于觉醒了', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84913] = { id = 84913, Voice = '', Text = '但是为了完成月神的命令，她只能选择这样做', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84912] = { id = 84912, Voice = '', Text = '尽管她知道这样会给她带来不可治愈的创伤', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84911] = { id = 84911, Voice = '', Text = '情急之下，她选择转移这里的人们', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84910] = { id = 84910, Voice = '', Text = '她清楚地知道，仅凭自己的力量是无法正面与这股力量相抗衡', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84909] = { id = 84909, Voice = '', Text = '她第一时间就感受到了那股毁灭的力量', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84908] = { id = 84908, Voice = '', Text = '其中，承载着始祖巨人力量的心脏碎片，落在了这片安宁的城市当中。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84907] = { id = 84907, Voice = '', Text = '诸神之战以始祖巨人伊米尔战败而告终，他的身体四分五裂，散落在世界的各个地方。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84906] = { id = 84906, Voice = '', Text = '她在全力守护这个国度的同时，也祈求着月神平安，祈求着诸神之战早日结束。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84905] = { id = 84905, Voice = '', Text = '很快，诸神之战的爆发，使得整个世界都陷入了混乱', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84904] = { id = 84904, Voice = '', Text = '月神把她送回了高塔，她却什么话也说不出来——神并没有给她询问的机会', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84903] = { id = 84903, Voice = '', Text = '她从未见过如此惊慌失措的月神，月神告诉她，大战将起，请她保护好她的子民', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84902] = { id = 84902, Voice = '', Text = '某天，月神召见了她', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84901] = { id = 84901, Voice = '', Text = '至此之后，每当圆月到来之时，她会准时出现在城市中心高大的祭坛顶，对着月亮默默祈祷', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84900] = { id = 84900, Voice = '', Text = '信徒们再次俯下身子，祈祷着，赞美着伟大的月神', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84899] = { id = 84899, Voice = '', Text = '声音虽轻，却响在信徒们的耳边', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84898] = { id = 84898, Voice = '', Text = '轻声说道：吾回应汝等之祈祷，谨遵神谕——守护此地', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84897] = { id = 84897, Voice = '', Text = '叮叮——她轻轻地摇动着身后的叮当', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84896] = { id = 84896, Voice = '', Text = '他们看到了一个背负着巨大铃铛的猫站在高塔之上', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84895] = { id = 84895, Voice = '', Text = '当这些虔诚的信徒感受不到月光的照耀，疑惑地抬起头', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84894] = { id = 84894, Voice = '', Text = '圆月照耀下，她的影子无限放大，笼罩着跪伏的人们', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84893] = { id = 84893, Voice = '', Text = '那么，我就继续讲述月夜传说的故事了。', Speaker = 1592, Option = '', Action = { num = 1, actionid = 200,},}, 
	[84892] = { id = 84892, Voice = '', Text = '咦？你身上没有我要的普通精准魔石啊。魔石只有铁匠能够制作，你可以去交易所购买哦。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84891] = { id = 84891, Voice = '', Text = '干得不错~这就是我要的普通精准魔石。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84890] = { id = 84890, Voice = '', Text = '你把普通精准魔石带来了吗？', Speaker = 1592, Option = '{带来了,3},{没有,2}', Action = { num = 1, actionid = 201,},}, 
	[84889] = { id = 84889, Voice = '', Text = '啊，我听闻这世上有着普通精准魔石这种神奇的东西呢，你可以帮我弄来吗？', Speaker = 1592, Option = '可以', Action = { num = 1, actionid = 201,},}, 
	[84888] = { id = 84888, Voice = '', Text = '她带着微笑俯视着这些虔诚的人们——他们就是她要守护的人们。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84887] = { id = 84887, Voice = '', Text = '叮叮——一声清脆的铃声响起，一个优雅的身影悄然落在了高塔之上。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84886] = { id = 84886, Voice = '', Text = '我要以诗歌赞美神的名，赞美月神。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84885] = { id = 84885, Voice = '', Text = '感谢神，歌颂你至高者的名，用风琴和瑟，早晨传扬您的慈爱，每夜传扬您的信实。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84884] = { id = 84884, Voice = '', Text = '他们缓缓地伏跪于地，低声轻轻的祷告着。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84883] = { id = 84883, Voice = '', Text = '虔诚地仰望着高塔祭坛之上那一轮圆月。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84882] = { id = 84882, Voice = '', Text = '圆月当空，无数民众聚集到城中宽大的广场。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84881] = { id = 84881, Voice = '', Text = '她带着满脸不甘，离开了这个生活了千百年的华美宫殿。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84880] = { id = 84880, Voice = '', Text = '才传出一句声音：“此为神谕！退下吧！”', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84879] = { id = 84879, Voice = '', Text = '孤傲地神座之上的身影沉默片刻。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84878] = { id = 84878, Voice = '', Text = '她急切地说：“吾神！请让我留下守护你！这是我诞生的使命！”', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84877] = { id = 84877, Voice = '', Text = '声音说着：“吾之眷族栖身于尘世之东南，去吧，把吾之辉光散步于众！”', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84876] = { id = 84876, Voice = '', Text = '凝视着大殿中央的神座，神座之上传来威严且动听的声音。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[84875] = { id = 84875, Voice = '', Text = '今天我们就来讲讲流传于斐扬的“月夜传说”吧！', Speaker = 1592, Option = '', Action = { num = 1, actionid = 200,},}, 
	[84709] = { id = 84709, Voice = '', Text = '谢谢你的帮助，冒险者！', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[84708] = { id = 84708, Voice = '', Text = '很抱歉，你的身上并没有足够的材料呢！', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[84707] = { id = 84707, Voice = '', Text = '冒险者，你带来了足够的[c][ffff00]慧眼[-][/c]吗？', Speaker = 3292, Option = '{我这就去收集,3},{给你慧眼,4}', Action = _EmptyTable,}, 
	[84706] = { id = 84706, Voice = '', Text = '真是令人信赖的冒险者，很高兴有你的帮助呢！', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[84705] = { id = 84705, Voice = '', Text = '冒险者，你带来了足够的[c][ffff00]龙鳞[-][/c]吗？', Speaker = 3292, Option = '{我这就去收集,3},{给你龙鳞,4}', Action = _EmptyTable,}, 
	[84704] = { id = 84704, Voice = '', Text = '冒险者，你带来了足够的[c][ffff00]触角[-][/c]吗？', Speaker = 3292, Option = '{我这就去收集,3},{给你触角,4}', Action = _EmptyTable,}, 
	[84703] = { id = 84703, Voice = '', Text = '冒险者，你能帮我们取来一些吗？', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[84700] = { id = 84700, Voice = '', Text = '你的表现真是太棒了，恭喜你通过我的考核，这是你的考核证明。去找索罗·图克吧，告诉他我很满意你的表现，我还会在这里修养一段时间。', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[84699] = { id = 84699, Voice = '', Text = '最后请为我带来一些[c][ffff00]慧眼[-][/c]吧，因为所需的数量较多，我希望冒险者你能够耐心地进行收集，因为这也是考验冒险者是否具有持之以恒的奋斗精神呢。', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[84698] = { id = 84698, Voice = '', Text = '那么作为回报，我必须要恭喜你，距离C级冒险者你就只差最后一步了。', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[84697] = { id = 84697, Voice = '', Text = '真是没办法呢！冒险者你实在是太优秀了，这么简单就收集到这些材料。', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[84696] = { id = 84696, Voice = '', Text = '你如果要去寻找这种材料，我建议你多请一些帮手。', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[84695] = { id = 84695, Voice = '', Text = '那么这次我需要的材料是[c][ffff00]龙鳞[-][/c]，这种材料我听说只有在古城郊外那种危险区域的的半龙人和迪塔勒泰晤勒身上才有。', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[84694] = { id = 84694, Voice = '', Text = '冒险者，我很满意你之前的表现，但你必须要明白，越是危险的挑战越会激发我们昂扬向上的斗志，越是困难的考验越会磨炼我们坚韧不拔的精神，而这也是作为一个C级冒险者的必备素质。', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[84693] = { id = 84693, Voice = '', Text = '很抱歉，你的身上并没有足够的材料呢！', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[84692] = { id = 84692, Voice = '', Text = '你完成得真出色，简直令我大吃一惊呢！', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[84691] = { id = 84691, Voice = '', Text = '接下来我需要一些[c][ffff00]触角[-][/c]，这种材料也是相当稀少，因而可以算是我们储备的必要物资之一。', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[84690] = { id = 84690, Voice = '', Text = '不错的冒险者，你的能力和荣誉也达到了令人满意的程度。', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[84689] = { id = 84689, Voice = '', Text = '如果你还没有达到这项要求，那么很抱歉我也不能让你继续进行考核，这也是C级冒险者考核的严苛所在。', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[84688] = { id = 84688, Voice = '', Text = '接下来请确保你已经获得并成功在冒险手册里激活[c][ffff00]导师资格证[-][/c]，这可是象征着冒险者责任和荣誉的重要物品呢！', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[84687] = { id = 84687, Voice = '', Text = '很有效率的冒险者，但只是这样还不能证明你拥有进阶C级冒险者的资格哦。', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[84686] = { id = 84686, Voice = '', Text = '在考核开始之前，我需要你身上至少拥有[c][ffff00]100万Zeny[-][/c]，有时候充裕的财富也是冒险者实力的象征呢。冒险者，请确保你拥有足够的Zeny再过来找我吧！', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[84685] = { id = 84685, Voice = '', Text = '真是很有斗志的冒险者，不过C级冒险者的考核与以往的考核可是完全不同呢！', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[84645] = { id = 84645, Voice = '', Text = '咦？这点数量的“我中意你”可不够啊…', Speaker = 4558, Option = '', Action = _EmptyTable,}, 
	[84644] = { id = 84644, Voice = '', Text = '嗯，数量都对了呢！恭喜您，您完成了妾身的考验呢！', Speaker = 4558, Option = '', Action = _EmptyTable,}, 
	[84643] = { id = 84643, Voice = '', Text = '尊贵的客人，您收集到妾身要的40个“我中意你”了吗？', Speaker = 4558, Option = '{没有,3},{带来了,4}', Action = _EmptyTable,}, 
	[84642] = { id = 84642, Voice = '', Text = '您收集到40个“我中意你”再来找妾身吧~', Speaker = 4558, Option = '', Action = _EmptyTable,}, 
	[84641] = { id = 84641, Voice = '', Text = '那么就帮妾身买40个“我中意你”吧！', Speaker = 4558, Option = '', Action = _EmptyTable,}, 
	[84640] = { id = 84640, Voice = '', Text = '尊贵的客人，看来您已经知道妾身喜欢的是什么了呢。', Speaker = 4558, Option = '我中意你', Action = _EmptyTable,}, 
	[84639] = { id = 84639, Voice = '', Text = '你可要多买一点哟。', Speaker = 3043, Option = '好的', Action = _EmptyTable,}, 
	[84638] = { id = 84638, Voice = '', Text = '唔，就是“我中意你”，不过因为美莎很喜欢这个，所以她对这件物品的需求可是非常大的。', Speaker = 3043, Option = '', Action = _EmptyTable,}, 
	[84637] = { id = 84637, Voice = '', Text = '美莎喜欢的东西？哈哈哈哈，她喜欢的东西是玩具啦。', Speaker = 3043, Option = '什么玩具？', Action = _EmptyTable,}, 
	[84636] = { id = 84636, Voice = '', Text = '您可以去问问派姬哦~妾身与她合作甚久呢~', Speaker = 4558, Option = '', Action = _EmptyTable,}, 
	[84635] = { id = 84635, Voice = '', Text = '想要雇佣妾身的话，就得找到妾身喜欢的东西~', Speaker = 4558, Option = '', Action = _EmptyTable,}, 
	[84634] = { id = 84634, Voice = '', Text = '咦？尊重的客人，您是第一次雇佣我吗？', Speaker = 4558, Option = '…', Action = _EmptyTable,}, 
	[84633] = { id = 84633, Voice = '', Text = '啊…尊贵的客人…您是想要雇佣妾身嘛？', Speaker = 4558, Option = '{什么东西,2},{不想雇佣,1}', Action = _EmptyTable,}, 
	[84511] = { id = 84511, Voice = '', Text = '冒险者，你带来了足够的[c][ffff00]兽人指甲[-][/c]吗？', Speaker = 3292, Option = '{我这就去收集,3},{给你兽人指甲,4}', Action = _EmptyTable,}, 
	[84501] = { id = 84501, Voice = '', Text = '冒险者，你带来了足够的[c][ffff00]恶魔角[-][/c]吗？', Speaker = 1228, Option = '{我这就去收集,3},{给你恶魔角,4}', Action = _EmptyTable,}, 
	[84499] = { id = 84499, Voice = '', Text = '咦？这点数量的腐烂绷带可不够啊…', Speaker = 4556, Option = '', Action = _EmptyTable,}, 
	[84498] = { id = 84498, Voice = '', Text = '嗯，数量都对了呢！恭喜你，你完成了我的考验了！', Speaker = 4556, Option = '', Action = _EmptyTable,}, 
	[84497] = { id = 84497, Voice = '', Text = '冒险者，你收集到我要的60个腐烂绷带了吗？', Speaker = 4556, Option = '{没有,3},{带来了,4}', Action = _EmptyTable,}, 
	[84496] = { id = 84496, Voice = '', Text = '你收集到60个腐烂绷带再来找我完成考验吧！', Speaker = 4556, Option = '', Action = _EmptyTable,}, 
	[84495] = { id = 84495, Voice = '', Text = '我只需要60个腐烂绷带就可以啦。是不是很便宜呢？', Speaker = 4556, Option = '', Action = _EmptyTable,}, 
	[84494] = { id = 84494, Voice = '', Text = '…什么钱不钱的！我是哪种人吗？庞德那家伙，这都说了…下次雇佣我的价格翻倍！', Speaker = 4556, Option = '…', Action = _EmptyTable,}, 
	[84493] = { id = 84493, Voice = '', Text = '嘿，我山葵从来不骗人！怎么样？想雇佣我的话可要先完成我的考验哦~', Speaker = 4556, Option = '多少钱？', Action = _EmptyTable,}, 
	[84492] = { id = 84492, Voice = '', Text = '虽然他的嘴欠了点…只要能忍受这一点，你将会得到一个坚韧无比的盾！', Speaker = 1340, Option = '', Action = _EmptyTable,}, 
	[84491] = { id = 84491, Voice = '', Text = '不过他的实力真的是很强，有他在我可以安心念咒语不用担心被怪打扰了。', Speaker = 1340, Option = '', Action = _EmptyTable,}, 
	[84490] = { id = 84490, Voice = '', Text = '我当初雇佣他的时候可是花了不少钱呢。现在物价上涨，肯定是更贵了…', Speaker = 1340, Option = '', Action = _EmptyTable,}, 
	[84489] = { id = 84489, Voice = '', Text = '嘿，原来是他…如果你想要完成他的考验，怕是要出点血了…', Speaker = 1340, Option = '', Action = _EmptyTable,}, 
	[84488] = { id = 84488, Voice = '', Text = '山葵？就是那个嘴欠又贪财的佣兵猫吗？', Speaker = 1340, Option = '是的', Action = _EmptyTable,}, 
	[84487] = { id = 84487, Voice = '', Text = '不信可以去问问他！我的实力是毋庸置疑的！', Speaker = 4556, Option = '', Action = _EmptyTable,}, 
	[84486] = { id = 84486, Voice = '', Text = '嗯？你不信？庞德你认识吧！这小子刚来吉芬的时候我可帮了他不少忙呢！', Speaker = 4556, Option = '', Action = _EmptyTable,}, 
	[84485] = { id = 84485, Voice = '', Text = '不过，你运气真好，你碰上了我，只要雇佣了我，什么危险都不够看！', Speaker = 4556, Option = '你？', Action = _EmptyTable,}, 
	[84484] = { id = 84484, Voice = '', Text = '你这么脆弱的身躯恐怕那些魔物轻轻一碰，你就会受伤吧？', Speaker = 4556, Option = '…', Action = _EmptyTable,}, 
	[84483] = { id = 84483, Voice = '', Text = '嘿…冒险者，你的身板可真脆弱…', Speaker = 4556, Option = '{…,2},{关你什么事,1}', Action = _EmptyTable,}, 
	[83880] = { id = 83880, Voice = '', Text = '是时候告别了，永别了冒险者。', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83879] = { id = 83879, Voice = '', Text = '美好的总是短暂的，谢谢你冒险者。', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83878] = { id = 83878, Voice = '', Text = '能在帮我播放一次留影水晶吗？最后一次。', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83877] = { id = 83877, Voice = '', Text = '我是灵魂状态，无法开启水晶。', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83876] = { id = 83876, Voice = '', Text = '那就好…', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83875] = { id = 83875, Voice = '', Text = '能在帮我播放一次留影水晶吗？最后一次。', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83874] = { id = 83874, Voice = '', Text = '我是灵魂状态，无法开启水晶。', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83873] = { id = 83873, Voice = '', Text = '只是不想让他知道我的消息而已。你很快就会变回来的。', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83872] = { id = 83872, Voice = '', Text = '我在你走之前给你施加了变形诅咒，放心，这对你没有危害。', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83871] = { id = 83871, Voice = '', Text = '变形诅咒？冒险者，你为什么要撒谎呢？', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83870] = { id = 83870, Voice = '', Text = '冒险者，我不想打扰他的生活，你应该没有把我的事情告诉他吧？', Speaker = 4448, Option = '{没有,102},{说了,101}', Action = _EmptyTable,}, 
	[83869] = { id = 83869, Voice = '', Text = '哈…他有了妻子，生活很圆满…', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83868] = { id = 83868, Voice = '', Text = '冒险者，他现在过得怎么样？', Speaker = 4448, Option = '讲述经过', Action = _EmptyTable,}, 
	[83867] = { id = 83867, Voice = '', Text = '谢谢你的陪伴，冒险者。', Speaker = 4449, Option = '不用客气', Action = _EmptyTable,}, 
	[83866] = { id = 83866, Voice = '', Text = '好了，莫亚，该回家了。', Speaker = 4449, Option = '', Action = _EmptyTable,}, 
	[83865] = { id = 83865, Voice = '', Text = '哼…你还挺厉害的吗，学的这么快，我已经没东西好给你了，记得以后来找我玩哦！一定！', Speaker = 4450, Option = '一定…', Action = _EmptyTable,}, 
	[83864] = { id = 83864, Voice = '', Text = '不错嘛…', Speaker = 4450, Option = '{再玩一次,30},{不玩啦,28}', Action = _EmptyTable,}, 
