	[83862] = { id = 83862, Voice = '', Text = '哈哈我可是很厉害的哦！', Speaker = 4450, Option = '{再玩一次,30},{不玩啦,28}', Action = _EmptyTable,}, 
	[83861] = { id = 83861, Voice = '', Text = '猜猜我会出什么？', Speaker = 4450, Option = '{拳头,40},{剪刀,50},{布,60}', Action = _EmptyTable,}, 
	[83860] = { id = 83860, Voice = '', Text = '一种简单的游戏，共有剪刀、石头、布三个手势。二人同时用手做出相应形状而出，输赢判断规则为：剪刀赢布，布赢石头，石头赢剪刀。', Speaker = 4450, Option = '{我也要玩,30},{不玩啦,28}', Action = _EmptyTable,}, 
	[83859] = { id = 83859, Voice = '', Text = '你知道怎么玩嘛？我可是很厉害的来较量一下吧！', Speaker = 4450, Option = '{怎么玩呢？,29},{来吧！,30}', Action = _EmptyTable,}, 
	[83858] = { id = 83858, Voice = '', Text = '快跟我玩猜拳啦！', Speaker = 4450, Option = '', Action = _EmptyTable,}, 
	[83857] = { id = 83857, Voice = '', Text = '莫亚…爷爷已经玩不动了…冒险者，你能陪他玩一会吗？', Speaker = 4449, Option = '好吧。', Action = _EmptyTable,}, 
	[83856] = { id = 83856, Voice = '', Text = '爷爷，快陪我玩猜拳嘛！', Speaker = 4450, Option = '', Action = _EmptyTable,}, 
	[83855] = { id = 83855, Voice = '', Text = '老头子的时日不多了。', Speaker = 4449, Option = '', Action = _EmptyTable,}, 
	[83854] = { id = 83854, Voice = '', Text = '一切都已经圆满，距离我和她相见的日子，已经不远了。', Speaker = 4449, Option = '你？', Action = _EmptyTable,}, 
	[83853] = { id = 83853, Voice = '', Text = '如今我有了妻子，有了孩子，我的孩子也已经结婚生子。', Speaker = 4449, Option = '', Action = _EmptyTable,}, 
	[83852] = { id = 83852, Voice = '', Text = '但是我再也没敢踏入那个地方，如今我只是个名为骑士实为懦夫的糟老头子罢了。', Speaker = 4449, Option = '…', Action = _EmptyTable,}, 
	[83851] = { id = 83851, Voice = '', Text = '在她离去那几年，我无时无刻不想着为他复仇。', Speaker = 4449, Option = '', Action = _EmptyTable,}, 
	[83850] = { id = 83850, Voice = '', Text = '是啊，几十年过去了。', Speaker = 4449, Option = '', Action = _EmptyTable,}, 
	[83849] = { id = 83849, Voice = '', Text = '送给你是最好的选择。再次感谢你，冒险者。', Speaker = 4449, Option = '', Action = _EmptyTable,}, 
	[83848] = { id = 83848, Voice = '', Text = '虽然不值钱，但是很有收藏价值…我不想每天看着水晶以泪洗面。', Speaker = 4449, Option = '', Action = _EmptyTable,}, 
	[83847] = { id = 83847, Voice = '', Text = '这块水晶残片就送给你作为纪念。', Speaker = 4449, Option = '可是…', Action = _EmptyTable,}, 
	[83846] = { id = 83846, Voice = '', Text = '我明白…冒险者，谢谢你让我见到了盖妮最后一面。', Speaker = 4449, Option = '', Action = _EmptyTable,}, 
	[83845] = { id = 83845, Voice = '', Text = '盖妮…就这么走了…', Speaker = 4449, Option = '节哀', Action = _EmptyTable,}, 
	[83844] = { id = 83844, Voice = '', Text = '我该走了…请好好生活，记住，我一直陪伴着你。', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83843] = { id = 83843, Voice = '', Text = '…嗯…', Speaker = 4448, Option = '', Emoji = 2, Action = _EmptyTable,}, 
	[83842] = { id = 83842, Voice = '', Text = '没有可是…就让我陪你走完最后的路。', Speaker = 4449, Option = '', Action = _EmptyTable,}, 
	[83841] = { id = 83841, Voice = '', Text = '可是…', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83840] = { id = 83840, Voice = '', Text = '在我心中，你还是原来的样子，那么懵懂纯洁。', Speaker = 4449, Option = '', Action = _EmptyTable,}, 
	[83839] = { id = 83839, Voice = '', Text = '不！不要过来！我不想让你看到我现在的样子！', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83838] = { id = 83838, Voice = '', Text = '亲爱的盖妮…我来见你最后一面…', Speaker = 4449, Option = '', Action = _EmptyTable,}, 
	[83837] = { id = 83837, Voice = '', Text = '嗯…我等着那一天…', Speaker = 4454, Option = '', Action = _EmptyTable,}, 
	[83836] = { id = 83836, Voice = '', Text = '盖妮，等我成为了骑士，我就向你求婚！', Speaker = 4455, Option = '', Action = _EmptyTable,}, 
	[83835] = { id = 83835, Voice = '', Text = '嘻嘻，盖妮，你永远也不会离开我的身边。', Speaker = 4453, Option = '', Action = _EmptyTable,}, 
	[83834] = { id = 83834, Voice = '', Text = '嘻嘻嘻…莫里森快来追我啊！', Speaker = 4452, Option = '', Action = _EmptyTable,}, 
	[83833] = { id = 83833, Voice = '', Text = '我来让他冷静下来吧。', Speaker = 4449, Option = '', Action = _EmptyTable,}, 
	[83831] = { id = 83831, Voice = '', Text = '你答应过我的！', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83830] = { id = 83830, Voice = '', Text = '可是你！竟然告诉了他我的消息！', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83829] = { id = 83829, Voice = '', Text = '我不想让他知道我的消息，我不想让他知道，他曾经的爱人如今变成了这样。', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83828] = { id = 83828, Voice = '', Text = '你答应过我的！', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83827] = { id = 83827, Voice = '', Text = '可你竟然告诉了他我的消息！', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83826] = { id = 83826, Voice = '', Text = '我只是不想让他知道我的消息而已。你很快就会变回来的。', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83825] = { id = 83825, Voice = '', Text = '我在你走之前给你施加了变形诅咒，放心，这对你没有危害。', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83824] = { id = 83824, Voice = '', Text = '变形诅咒？冒险者，你为什么要撒谎呢？', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83823] = { id = 83823, Voice = '', Text = '冒险者，我不想打扰他的生活，你应该没有把我的事情告诉他吧？', Speaker = 4448, Option = '{没有,3},{说了,4}', Action = _EmptyTable,}, 
	[83822] = { id = 83822, Voice = '', Text = '哈…他有了妻子，生活很圆满…', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83821] = { id = 83821, Voice = '', Text = '冒险者，他现在过得怎么样？', Speaker = 4448, Option = '讲述经过', Action = _EmptyTable,}, 
	[83820] = { id = 83820, Voice = '', Text = '她没多少时间了，冒险者，你先去古城，我收拾一下就会过去！', Speaker = 4449, Option = '好的', Action = _EmptyTable,}, 
	[83819] = { id = 83819, Voice = '', Text = '那块留影水晶她还留着…', Speaker = 4449, Option = '', Action = _EmptyTable,}, 
	[83818] = { id = 83818, Voice = '', Text = '太好了！她没死！我要去找他！', Speaker = 4449, Option = '讲述经过', Action = _EmptyTable,}, 
	[83817] = { id = 83817, Voice = '', Text = '你在哪里看到她的！', Speaker = 4449, Option = '古城', Emoji = 1, Action = _EmptyTable,}, 
	[83816] = { id = 83816, Voice = '', Text = '等等！你说盖妮！你知道盖妮！', Speaker = 4449, Option = '是的', Action = _EmptyTable,}, 
	[83815] = { id = 83815, Voice = '', Text = '一切都已经圆满，距离我和她相见的日子，已经不远了。', Speaker = 4449, Option = '', Action = _EmptyTable,}, 
	[83814] = { id = 83814, Voice = '', Text = '如今我有了妻子，有了孩子，我的孩子也已经结婚生子。', Speaker = 4449, Option = '', Action = _EmptyTable,}, 
	[83813] = { id = 83813, Voice = '', Text = '啊…我在想某一个人…也不知道她在天国过得好不好…', Speaker = 4449, Option = '{盖妮？,2},{请节哀,27}', Action = _EmptyTable,}, 
	[83812] = { id = 83812, Voice = '', Text = '……', Speaker = 4449, Option = '你在想什么', Action = _EmptyTable,}, 
	[83811] = { id = 83811, Voice = '', Text = '他就在普隆德拉，请一定不要告诉他我的事。', Speaker = 4448, Option = '明白了', Action = _EmptyTable,}, 
	[83810] = { id = 83810, Voice = '', Text = '冒险者，我的时间不多了，你能帮我看看他现在过得怎么样吗？', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83809] = { id = 83809, Voice = '', Text = '算了算日子，他应该已经老了…', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83808] = { id = 83808, Voice = '', Text = '直到我找到了这个水晶。看到这个，我的心情就会平静下来。', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83807] = { id = 83807, Voice = '', Text = '起初，我不能接受自己死去的事实。', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83806] = { id = 83806, Voice = '', Text = '这里的区域似乎有所异常，我的灵魂被囚禁在这里。', Speaker = 4448, Option = '然后呢？', Action = _EmptyTable,}, 
	[83805] = { id = 83805, Voice = '', Text = '莫里森被其他队友救走了，而我却慢了一步。来救我的都死去了。', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83804] = { id = 83804, Voice = '', Text = '我们当初来到这里冒险，却被恶魔袭击了。', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83803] = { id = 83803, Voice = '', Text = '他是我的挚爱。', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83802] = { id = 83802, Voice = '', Text = '是的，这是一颗留影水晶，我和莫里森的回忆都在里面。', Speaker = 4448, Option = '莫里森？', Action = _EmptyTable,}, 
	[83801] = { id = 83801, Voice = '', Text = '这是我和莫里森的回忆。', Speaker = 4448, Option = '回忆？', Action = _EmptyTable,}, 
	[83800] = { id = 83800, Voice = '', Text = '冒险者…那是我最宝贵的东西…可以还给我了吗？', Speaker = 4448, Option = '递上水晶', Action = _EmptyTable,}, 
	[83799] = { id = 83799, Voice = '', Text = '我又魔女化了么…谢谢你让我清醒了，冒险者。', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83795] = { id = 83795, Voice = '', Text = '魔力蓬勃而出…周围突然一片漆黑…', Speaker = 4447, Option = '', Action = _EmptyTable,}, 
	[83794] = { id = 83794, Voice = '', Text = '魔力在涌动…', Speaker = 4447, Option = '', Action = _EmptyTable,}, 
	[83793] = { id = 83793, Voice = '', Text = '你会后悔的！冒险者！你会死在这里！', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83792] = { id = 83792, Voice = '', Text = '够了！不要碰它！', Speaker = 4448, Option = '抚摸水晶', Action = _EmptyTable,}, 
	[83791] = { id = 83791, Voice = '', Text = '请求你，不要触碰它。', Speaker = 4448, Option = '坚持触碰', Action = _EmptyTable,}, 
	[83790] = { id = 83790, Voice = '', Text = '这样最好…', Speaker = 4448, Option = '', Action = _EmptyTable,}, 
	[83789] = { id = 83789, Voice = '', Text = '冒险者…不要触碰这个水晶…', Speaker = 4448, Option = '{再次触碰,3},{放下水晶,4}', Action = _EmptyTable,}, 
	[83788] = { id = 83788, Voice = '', Text = '剔透的水晶…半截都埋在土地之中。', Speaker = 4447, Option = '{捡起来看看,2},{不感兴趣,1}', Action = _EmptyTable,}, 
	[83452] = { id = 83452, Voice = '', Text = '恭喜你冒险者！你的收藏又丰富了！', Speaker = 4266, Option = '', Action = _EmptyTable,}, 
	[83451] = { id = 83451, Voice = '', Text = '冒险者，你并没有收集齐王冠的部件…很遗憾我无法为你修复王冠了。', Speaker = 4266, Option = '', Action = _EmptyTable,}, 
	[83449] = { id = 83449, Voice = '', Text = '你想用破旧王冠、碧蓝宝石和赤红晶钻恢复以往王冠的风采吗？', Speaker = 4266, Option = '{是的,3},{我再想想,1}', Action = _EmptyTable,}, 
	[83448] = { id = 83448, Voice = '', Text = '那么作为一名历史研究学者，我可以帮你修复这件王冠。', Speaker = 4266, Option = '', Action = _EmptyTable,}, 
	[83447] = { id = 83447, Voice = '', Text = '冒险者，你收集齐了王冠的部件了吗？', Speaker = 4266, Option = '{真的,2},{还没有,1}', Action = _EmptyTable,}, 
	[83446] = { id = 83446, Voice = '', Text = '如果你有更多关于王冠的消息，记得回来找我！', Speaker = 4403, Option = '好的', Action = _EmptyTable,}, 
	[83445] = { id = 83445, Voice = '', Text = '虽然它没有什么魔力，但是还是极具价值！', Speaker = 4403, Option = '', Action = _EmptyTable,}, 
	[83444] = { id = 83444, Voice = '', Text = '历史真是让人着迷…我想如果你能够收集到全部的部件，那么一定能还原出一个象征休密兹王朝终结的完整的王冠！', Speaker = 4403, Option = '', Action = _EmptyTable,}, 
	[83443] = { id = 83443, Voice = '', Text = '也许就是受到这个晶钻的影响…', Speaker = 4403, Option = '', Action = _EmptyTable,}, 
	[83442] = { id = 83442, Voice = '', Text = '它竟然有这样的魔力？我参考史书，休密兹晚年曾经痴迷于炼金以及魔幻仪式。', Speaker = 4403, Option = '', Action = _EmptyTable,}, 
	[83441] = { id = 83441, Voice = '', Text = '哇哦！这就是赤红晶钻！', Speaker = 4403, Option = '讲述来历', Action = _EmptyTable,}, 
	[83440] = { id = 83440, Voice = '', Text = '冒险者，我相信我们以后会再见面的！', Speaker = 4403, Option = '再见。', Action = _EmptyTable,}, 
	[83439] = { id = 83439, Voice = '', Text = '重获自由之身，我应该在大陆上好好走走。', Speaker = 4403, Option = '', Action = _EmptyTable,}, 
	[83438] = { id = 83438, Voice = '', Text = '也许你能拥有一个完整的王冠。哈哈哈哈哈。', Speaker = 4403, Option = '', Action = _EmptyTable,}, 
	[83437] = { id = 83437, Voice = '', Text = '相信你能换到不少钱，当然，如果你能收集到另外两件物品。', Speaker = 4403, Option = '', Action = _EmptyTable,}, 
	[83436] = { id = 83436, Voice = '', Text = '既然这样，这块晶钻就送给你了。', Speaker = 4403, Option = '', Action = _EmptyTable,}, 
	[83435] = { id = 83435, Voice = '', Text = '这块晶钻是我从休密兹的王冠中取下来的。', Speaker = 4403, Option = '', Action = _EmptyTable,}, 
	[83434] = { id = 83434, Voice = '', Text = '好吧，这样也好，起码再也不会有人变成我那副样子了。', Speaker = 4403, Option = '', Action = _EmptyTable,}, 
	[83433] = { id = 83433, Voice = '', Text = '这块赤红晶钻害得我…咦？晶钻的魔力竟然已经不见了？', Speaker = 4403, Option = '是的', Action = _EmptyTable,}, 
	[83432] = { id = 83432, Voice = '', Text = '冒险者，我真的不知道该如何感谢你！', Speaker = 4403, Option = '', Action = _EmptyTable,}, 
	[83431] = { id = 83431, Voice = '', Text = '我终于从那个鬼地方出来了！', Speaker = 4403, Option = '', Action = _EmptyTable,}, 
	[83430] = { id = 83430, Voice = '', Text = '我已经把他制作成了符文，你只需要对着那片晶钻使用就可以破除上面的魔力了。', Speaker = 4271, Option = '明白了', Action = _EmptyTable,}, 
	[83429] = { id = 83429, Voice = '', Text = '很顺利的提取出了伊米尔之心的力量。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[83428] = { id = 83428, Voice = '', Text = '冒险者，请你稍等一会。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[83427] = { id = 83427, Voice = '', Text = '唔，这点血液应该足够提取了。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[83426] = { id = 83426, Voice = '', Text = '这过程中会有一点点危险性，冒险者你还是小心一点吧！', Speaker = 4271, Option = '好吧。', Action = _EmptyTable,}, 
	[83425] = { id = 83425, Voice = '', Text = '很好，现在请你用采血器抽取血液吧。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[83424] = { id = 83424, Voice = '', Text = '你需要采血器是吗？你先拿去用！不过你可要记得还给我！', Speaker = 4322, Option = '没问题！', Action = _EmptyTable,}, 
	[83423] = { id = 83423, Voice = '', Text = '这下我的研究进度不会放慢了。', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[83422] = { id = 83422, Voice = '', Text = '哇！你竟然找到了！', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[83421] = { id = 83421, Voice = '', Text = '只要你能从游荡的半龙人身上夺回采血器，那我就把采血器借给你！', Speaker = 4322, Option = '好吧', Action = _EmptyTable,}, 
	[83420] = { id = 83420, Voice = '', Text = '我的采血器在那个时候弄丢了，所以我身边也没有采血器了。', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[83419] = { id = 83419, Voice = '', Text = '真是不凑巧！我在古城研究魔物的时候，遇到了一个游荡的半龙人！', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[83418] = { id = 83418, Voice = '', Text = '你去找魔物学者看看吧。他现在就在古城中做研究。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[83417] = { id = 83417, Voice = '', Text = '我听说，魔物学者们经常会用到这种采血器。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[83416] = { id = 83416, Voice = '', Text = '没错，就是你的。为了安全起见，我们需要一个采血器。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[83415] = { id = 83415, Voice = '', Text = '接下来，我需要拥有伊米尔之心碎片的血液了。', Speaker = 4271, Option = '我的？', Action = _EmptyTable,}, 
	[83414] = { id = 83414, Voice = '', Text = '你还是一如既往的高效率！', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[83413] = { id = 83413, Voice = '', Text = '用这个来引导伊米尔之心碎片的力量再合适不过了！', Speaker = 4271, Option = '好的', Action = _EmptyTable,}, 
	[83412] = { id = 83412, Voice = '', Text = '恶魔来自异世界，它的气息可以引起伊米尔之心碎片强烈的抵抗。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[83411] = { id = 83411, Voice = '', Text = '你可以去收集一些恶魔犄角。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[83410] = { id = 83410, Voice = '', Text = '现在去找伊米尔之心碎片肯定不现实…', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[83409] = { id = 83409, Voice = '', Text = '伊米尔之心碎片的力量残存在你的体内，那些魔法作用到你身上的时候就会被削弱。', Speaker = 4271, Option = '我该怎么救', Action = _EmptyTable,}, 
	[83408] = { id = 83408, Voice = '', Text = '你能够这么快挣脱变形，离开那个诡异的梦境，应该是你曾经使用过伊米尔之心碎片的缘故。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[83407] = { id = 83407, Voice = '', Text = '触摸就会变形，进入幻境的晶钻？', Speaker = 4271, Option = '是的', Action = _EmptyTable,}, 
	[83373] = { id = 83373, Voice = '', Text = '发出一道柔和的亮光，一股莫名的力量进入了你的身体…', Speaker = 4402, Option = '', Action = _EmptyTable,}, 
	[83372] = { id = 83372, Voice = '', Text = '火红却又剔透的钻石，藏在草丛之中…', Speaker = 4402, Option = '{仔细看看,2},{不感兴趣,1}', Action = _EmptyTable,}, 
	[83371] = { id = 83371, Voice = '', Text = '如果你有更多关于王冠的消息，记得回来找我！', Speaker = 4266, Option = '好的', Action = _EmptyTable,}, 
	[83370] = { id = 83370, Voice = '', Text = '如果能得到其他两块宝石，那我们就能亲眼目睹这个华贵的王冠了！', Speaker = 4266, Option = '', Action = _EmptyTable,}, 
	[83369] = { id = 83369, Voice = '', Text = '搞不好这真的是休密兹的王冠呢！', Speaker = 4266, Option = '', Action = _EmptyTable,}, 
	[83368] = { id = 83368, Voice = '', Text = '做工精密，材质昂贵，上面还铭刻着休密兹时代的符文！这确实是休密兹时代的产物！', Speaker = 4266, Option = '', Action = _EmptyTable,}, 
	[83367] = { id = 83367, Voice = '', Text = '你找到休密兹的王冠了？快让我看看！', Speaker = 4266, Option = '交上王冠', Action = _EmptyTable,}, 
	[83366] = { id = 83366, Voice = '', Text = '永别了，冒险者…', Speaker = 4400, Option = '安息吧。', Action = _EmptyTable,}, 
	[83365] = { id = 83365, Voice = '', Text = '希望有一天你能够把王冠再次还原出来。', Speaker = 4400, Option = '', Action = _EmptyTable,}, 
	[83364] = { id = 83364, Voice = '', Text = '这是曾经休密兹的王冠，上面的孔是另外两件宝石。', Speaker = 4400, Option = '', Action = _EmptyTable,}, 
	[83363] = { id = 83363, Voice = '', Text = '灵魂状态是不可能在世间停留太久的，我终究会消散的…', Speaker = 4400, Option = '', Action = _EmptyTable,}, 
	[83362] = { id = 83362, Voice = '', Text = '我也该去找他们了……', Speaker = 4400, Option = '你？', Action = _EmptyTable,}, 
	[83361] = { id = 83361, Voice = '', Text = '不用担心，几百年的囚禁，我什么都看开了。', Speaker = 4400, Option = '', Action = _EmptyTable,}, 
	[83360] = { id = 83360, Voice = '', Text = '看来他们都没有能够成功的逃出去。', Speaker = 4400, Option = '不要难过', Action = _EmptyTable,}, 
	[83359] = { id = 83359, Voice = '', Text = '没有任何魔法印记…', Speaker = 4400, Option = '', Action = _EmptyTable,}, 
	[83358] = { id = 83358, Voice = '', Text = '…没有什么特殊的魔法印记出现…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[83357] = { id = 83357, Voice = '', Text = '灵魂结晶微微发亮…似乎就是这里了！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[83356] = { id = 83356, Voice = '', Text = '灵魂结晶没有任何反应…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[83355] = { id = 83355, Voice = '', Text = '似乎不在这里……', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[83354] = { id = 83354, Voice = '', Text = '它会帮助你看到那些特殊的魔法印记。捏碎他就可以了。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[83353] = { id = 83353, Voice = '', Text = '冒险者，这是我灵魂的力量凝成的结晶。', Speaker = 4400, Option = '', Action = _EmptyTable,}, 
	[83352] = { id = 83352, Voice = '', Text = '哦？！拍照是什么，不过听上去很棒，我期待着你给我惊喜。', Speaker = 4400, Option = '等着！', Action = _EmptyTable,}, 
	[83351] = { id = 83351, Voice = '', Text = '我想看看他们有没有成功逃出去，冒险者，你有办法能帮助我了解这些吗？', Speaker = 4400, Option = '拍照', Action = _EmptyTable,}, 
	[83350] = { id = 83350, Voice = '', Text = '我们曾经约好，谁到了约定的地点，就用特殊的魔法印记标记。', Speaker = 4400, Option = '', Action = _EmptyTable,}, 
	[83349] = { id = 83349, Voice = '', Text = '如果真有什么值得留念的，那大概就是他们的安危了。', Speaker = 4400, Option = '', Action = _EmptyTable,}, 
	[83348] = { id = 83348, Voice = '', Text = '我的心愿也算是了结了…这世间已经没有什么值得我留念的了。', Speaker = 4400, Option = '', Action = _EmptyTable,}, 
	[83347] = { id = 83347, Voice = '', Text = '你解决他了？那真是太棒了！', Speaker = 4400, Option = '', Action = _EmptyTable,}, 
	[83346] = { id = 83346, Voice = '', Text = '他现在应该在就在克雷斯特汉姆的墓地之中。', Speaker = 4400, Option = '好吧', Action = _EmptyTable,}, 
	[83345] = { id = 83345, Voice = '', Text = '那个巫师叫做风之巫师，看守我的蓝鼠也是他召唤出来的。', Speaker = 4400, Option = '', Action = _EmptyTable,}, 
	[83344] = { id = 83344, Voice = '', Text = '我相信你绝不会拒绝的。', Speaker = 4400, Option = '好吧', Action = _EmptyTable,}, 
	[83343] = { id = 83343, Voice = '', Text = '如果你愿意帮助我，我就给你一件珍贵的物品！', Speaker = 4400, Option = '', Action = _EmptyTable,}, 
	[83342] = { id = 83342, Voice = '', Text = '虽然我没有力量，但是冒险者，你有啊！', Speaker = 4400, Option = '…', Action = _EmptyTable,}, 
	[83341] = { id = 83341, Voice = '', Text = '真想找那个家伙报仇啊！', Speaker = 4400, Option = '那个巫师？', Action = _EmptyTable,}, 
	[83340] = { id = 83340, Voice = '', Text = '谁说不是呢！可惜，被巫阵封印在那里，我所有力量都流逝了。', Speaker = 4400, Option = '', Action = _EmptyTable,}, 
	[83339] = { id = 83339, Voice = '', Text = '但是我却被一个巫师抓起来，被关在匣子里几百年之久。', Speaker = 4400, Option = '真惨…', Action = _EmptyTable,}, 
	[83338] = { id = 83338, Voice = '', Text = '我们把王冠分成了三份，并且约好在城外见面。', Speaker = 4400, Option = '', Action = _EmptyTable,}, 
	[83337] = { id = 83337, Voice = '', Text = '但是黑暗魔族降临，我们三个不得不联手对抗魔族。', Speaker = 4400, Option = '', Action = _EmptyTable,}, 
	[83336] = { id = 83336, Voice = '', Text = '默德雷格偷走了休密兹的王冠，我和西亚顿追捕他。', Speaker = 4400, Option = '', Action = _EmptyTable,}, 
	[83335] = { id = 83335, Voice = '', Text = '西亚顿和默德雷格，我的战友。', Speaker = 4400, Option = '', Action = _EmptyTable,}, 
	[83334] = { id = 83334, Voice = '', Text = '几百年了…就算他们活着出去了，也已经不在世间了吧。', Speaker = 4400, Option = '他们？', Action = _EmptyTable,}, 
	[83316] = { id = 83316, Voice = '', Text = '一股不可抗拒的吸力突然涌现…', Speaker = 4399, Option = '', Action = _EmptyTable,}, 
	[83315] = { id = 83315, Voice = '', Text = '秘匣内似乎有一些不一样的声音…', Speaker = 4399, Option = '', Action = _EmptyTable,}, 
	[83314] = { id = 83314, Voice = '', Text = '深深地埋在土地中的金属盒子，露出地面的只有一点点尖锐的棱角。', Speaker = 4399, Option = '{仔细看看,2},{不感兴趣,1}', Action = _EmptyTable,}, 
	[83313] = { id = 83313, Voice = '', Text = '如果你有更多关于王冠的消息，记得回来找我！', Speaker = 4266, Option = '好的。', Action = _EmptyTable,}, 
	[83312] = { id = 83312, Voice = '', Text = '真是符合赛依连的性格啊，哈哈哈哈，总之，能够找到主人真是太好了。', Speaker = 4266, Option = '', Action = _EmptyTable,}, 
	[83311] = { id = 83311, Voice = '', Text = '盖因·洛斯…我听说过他…据说是剑圣家族的传人呢…', Speaker = 4266, Option = '', Action = _EmptyTable,}, 
	[83310] = { id = 83310, Voice = '', Text = '我会找到你的…澈…', Speaker = 4397, Option = '喜欢就好', Action = _EmptyTable,}, 
	[83309] = { id = 83309, Voice = '', Text = '有了这把剑后，相信我很快就能深入古城。', Speaker = 4397, Option = '', Action = _EmptyTable,}, 
	[83308] = { id = 83308, Voice = '', Text = '多谢你的守护，冒险者。', Speaker = 4397, Option = '', Action = _EmptyTable,}, 
	[83298] = { id = 83298, Voice = '', Text = '这把剑…魔力韵律流动…铭刻魔力符文…你怎么弄到的？', Speaker = 4397, Option = '试试吧', Action = _EmptyTable,}, 
	[83297] = { id = 83297, Voice = '', Text = '哈哈，难为他还记得，我这把已经破旧不堪了，长时间在古城中战斗，它早已不堪负荷了。', Speaker = 4397, Option = '', Action = _EmptyTable,}, 
	[83296] = { id = 83296, Voice = '', Text = '嗯？赛依连让你过来找我做什么？', Speaker = 4397, Option = '讲述事情', Action = _EmptyTable,}, 
	[83295] = { id = 83295, Voice = '', Text = '什么人！', Speaker = 4397, Option = '是我', Action = _EmptyTable,}, 
	[83294] = { id = 83294, Voice = '', Text = '他在古城里寻找着什么，你可以把这把剑赠送给他，我相信以他的能力，一定能驾驭这把剑的。', Speaker = 4275, Option = '好的', Action = _EmptyTable,}, 
	[83293] = { id = 83293, Voice = '', Text = '他叫做盖因·洛斯，也许你听过他的名字。', Speaker = 4275, Option = '', Action = _EmptyTable,}, 
	[83292] = { id = 83292, Voice = '', Text = '他的实力相当强劲，从深渊骑士手中救出了陷入苦战的我。', Speaker = 4275, Option = '', Action = _EmptyTable,}, 
	[83291] = { id = 83291, Voice = '', Text = '我倒是有个合适的人选，我在探索古城的时候遇到的。', Speaker = 4275, Option = '', Action = _EmptyTable,}, 
	[83290] = { id = 83290, Voice = '', Text = '我早已视它为自己的亲人，我怎么可以放弃它呢？', Speaker = 4275, Option = '我明白了。', Action = _EmptyTable,}, 
	[83289] = { id = 83289, Voice = '', Text = '我的剑没有这把剑锋利，但是它陪伴了我这么多年。', Speaker = 4275, Option = '', Action = _EmptyTable,}, 
	[83288] = { id = 83288, Voice = '', Text = '骑士宣誓的时候，我曾经立下誓言：对国家，对人民，对我手中之剑绝对的忠诚。', Speaker = 4275, Option = '', Action = _EmptyTable,}, 
	[83287] = { id = 83287, Voice = '', Text = '不过冒险者，请恕我拒绝你的好意。', Speaker = 4275, Option = '为什么', Action = _EmptyTable,}, 
	[83286] = { id = 83286, Voice = '', Text = '嘶…好锋利的剑！其中涌动的魔力异常澎湃…', Speaker = 4275, Option = '', Action = _EmptyTable,}, 
	[83285] = { id = 83285, Voice = '', Text = '嗯？要送我一把武器？', Speaker = 4275, Option = '是的', Action = _EmptyTable,}, 
	[83284] = { id = 83284, Voice = '', Text = '没错，英勇善战，这把剑送给他再合适不过了！', Speaker = 4266, Option = '确实…', Action = _EmptyTable,}, 
	[83283] = { id = 83283, Voice = '', Text = '可惜我不是剑士…说起这个…我向你推荐一位，喏，就是那位火堆旁的骑士！', Speaker = 4266, Option = '赛依连?', Action = _EmptyTable,}, 
	[83282] = { id = 83282, Voice = '', Text = '不过现在你还是完成他最后的心愿吧…唔…真是一把锋利的剑！', Speaker = 4266, Option = '', Action = _EmptyTable,}, 
	[83281] = { id = 83281, Voice = '', Text = '不过如果我们能收集到所有的王冠部件，才能确认这段历史是否属实。', Speaker = 4266, Option = '…', Action = _EmptyTable,}, 
	[83280] = { id = 83280, Voice = '', Text = '原来是一开始就失落在外啊！这样就很合理了…我得记下来…', Speaker = 4266, Option = '', Action = _EmptyTable,}, 
	[83279] = { id = 83279, Voice = '', Text = '难怪…根据我找到的史书，确实没有提到象征着王权的王冠。', Speaker = 4266, Option = '', Action = _EmptyTable,}, 
	[83278] = { id = 83278, Voice = '', Text = '怎么样？你肯定为我带来了好消息吧？', Speaker = 4266, Option = '讲述经过', Action = _EmptyTable,}, 
	[83277] = { id = 83277, Voice = '', Text = '不用客气…不要忘记我们之间的约定！', Speaker = 4396, Option = '我会的', Action = _EmptyTable,}, 
	[83276] = { id = 83276, Voice = '', Text = '他们的下落我也不太清楚…我能告诉你的就只有这么多了。', Speaker = 4396, Option = '谢谢你', Action = _EmptyTable,}, 
	[83275] = { id = 83275, Voice = '', Text = '可是我们分开不久我就遇到大队魔族，我被击败了。但是我却以灵魂状态苟活了下来。', Speaker = 4396, Option = '', Action = _EmptyTable,}, 
	[83274] = { id = 83274, Voice = '', Text = '周围的魔族太多了，我们只能分开逃走，走之前，我们把王冠拆开，分成了三件。', Speaker = 4396, Option = '', Action = _EmptyTable,}, 
	[83273] = { id = 83273, Voice = '', Text = '嘿，暴君早就不得人心了，我们的背叛合情合理，我可不想为休密兹陪葬。', Speaker = 4396, Option = '', Action = _EmptyTable,}, 
	[83272] = { id = 83272, Voice = '', Text = '我们答应了。', Speaker = 4396, Option = '答应了？', Action = _EmptyTable,}, 
	[83271] = { id = 83271, Voice = '', Text = '在联手打退了他们后默德雷格那个小子，劝我们一起逃出克雷斯特汉姆。', Speaker = 4396, Option = '', Action = _EmptyTable,}, 
	[83270] = { id = 83270, Voice = '', Text = '那些魔族可不分什么敌我，我们三个人受到了魔族小队的进攻。', Speaker = 4396, Option = '', Action = _EmptyTable,}, 
	[83269] = { id = 83269, Voice = '', Text = '可是这个时候叛军与休密兹召唤出来的黑暗魔族已经开始交战。', Speaker = 4396, Option = '', Action = _EmptyTable,}, 
	[83268] = { id = 83268, Voice = '', Text = '他很狡猾，我和我的同僚卡洛姆追查很久才找到他。', Speaker = 4396, Option = '', Action = _EmptyTable,}, 
	[83267] = { id = 83267, Voice = '', Text = '从王宫中偷走王冠的那个盗贼，名叫默德雷格。', Speaker = 4396, Option = '', Action = _EmptyTable,}, 
	[83266] = { id = 83266, Voice = '', Text = '好！那么我就告诉你，那天晚上所发生的事情。', Speaker = 4396, Option = '', Action = _EmptyTable,}, 
	[83265] = { id = 83265, Voice = '', Text = '人拥有灵魂，剑也不例外，辉光之剑会自己选择主人的。', Speaker = 4396, Option = '小事一桩！', Action = _EmptyTable,}, 
	[83264] = { id = 83264, Voice = '', Text = '现在我也快离开这个世界了，走之前，我希望你能为他找到一个合适的主人。', Speaker = 4396, Option = '', Action = _EmptyTable,}, 
	[83263] = { id = 83263, Voice = '', Text = '这是我的武器…辉光之剑，从我成为一名剑士的时候就陪伴在我身边。', Speaker = 4396, Option = '', Action = _EmptyTable,}, 
	[83262] = { id = 83262, Voice = '', Text = '不过，在我消散之前，我想请你帮一个忙。', Speaker = 4396, Option = '什么', Action = _EmptyTable,}, 
	[83261] = { id = 83261, Voice = '', Text = '嘿嘿…你想知道这个…也好，历史本来就不应该被深藏。', Speaker = 4396, Option = '', Action = _EmptyTable,}, 
	[83260] = { id = 83260, Voice = '', Text = '你是来看我最后狼狈的样子吗？', Speaker = 4396, Option = '关于王冠', Action = _EmptyTable,}, 
	[83258] = { id = 83258, Voice = '', Text = '这对我研究古城的历史有非常巨大的帮助。', Speaker = 4266, Option = '', Action = _EmptyTable,}, 
	[83257] = { id = 83257, Voice = '', Text = '冒险者，我需要更多的线索来揭开这块宝石的秘密。', Speaker = 4266, Option = '', Action = _EmptyTable,}, 
	[83256] = { id = 83256, Voice = '', Text = '这种价值不菲的宝石，名至实归，冒险者，你倒是运气不错。', Speaker = 4266, Option = '', Action = _EmptyTable,}, 
	[83255] = { id = 83255, Voice = '', Text = '从年份上来看…确实是休密兹时代的物品…很可能就是王冠上的宝石呢。', Speaker = 4266, Option = '', Action = _EmptyTable,}, 
	[83254] = { id = 83254, Voice = '', Text = '嗯？休密兹王冠上的宝石？快！快给我看看！', Speaker = 4266, Option = '', Action = _EmptyTable,}, 
	[83253] = { id = 83253, Voice = '', Text = '冒险者，对于一个学者来说，这样打招呼可不是很礼貌。', Speaker = 4266, Option = '讲述故事', Action = _EmptyTable,}, 
	[83252] = { id = 83252, Voice = '', Text = '如果你能找到其他的物件，你就可以得到休密兹的王冠了。', Speaker = 4396, Option = '好吧。', Action = _EmptyTable,}, 
	[83251] = { id = 83251, Voice = '', Text = '这块碧蓝宝石，你带走吧。', Speaker = 4396, Option = '', Action = _EmptyTable,}, 
	[83250] = { id = 83250, Voice = '', Text = '如今我也快消散了…算是我作恶多年应有的惩罚。', Speaker = 4396, Option = '…', Action = _EmptyTable,}, 
	[83249] = { id = 83249, Voice = '', Text = '依靠夺取路过的冒险者的灵魂，才勉强存活下来…', Speaker = 4396, Option = '', Action = _EmptyTable,}, 
	[83248] = { id = 83248, Voice = '', Text = '没想到，最后我还是沦落到现在这个地步。', Speaker = 4396, Option = '', Action = _EmptyTable,}, 
	[83247] = { id = 83247, Voice = '', Text = '这王冠足以让我一辈子吃穿不愁！', Speaker = 4396, Option = '…', Action = _EmptyTable,}, 
	[83246] = { id = 83246, Voice = '', Text = '我想如果我能夺回这王冠，我就离开克雷斯特汉姆，一辈子都不会回来了。', Speaker = 4396, Option = '', Action = _EmptyTable,}, 
	[83245] = { id = 83245, Voice = '', Text = '而这个时候，国王的王冠丢失了！我自告奋勇接了这个任务，离开了王宫。', Speaker = 4396, Option = '', Action = _EmptyTable,}, 
	[83244] = { id = 83244, Voice = '', Text = '召唤异世界的黑暗大军…嘿嘿…自取灭亡，怪不了别人。', Speaker = 4396, Option = '', Action = _EmptyTable,}, 
	[83243] = { id = 83243, Voice = '', Text = '嘿…说到底，我并不想给这位国王陛下陪葬。', Speaker = 4396, Option = '陪葬？', Action = _EmptyTable,}, 
	[83242] = { id = 83242, Voice = '', Text = '而我，是他的近卫队队长。', Speaker = 4396, Option = '', Action = _EmptyTable,}, 
	[83241] = { id = 83241, Voice = '', Text = '是啊，卢恩·米德加尔特的国王陛下。', Speaker = 4396, Option = '', Action = _EmptyTable,}, 
	[83240] = { id = 83240, Voice = '', Text = '这里竟然已经变成了这副模样…', Speaker = 4396, Option = '休密兹？', Action = _EmptyTable,}, 
	[83239] = { id = 83239, Voice = '', Text = '休密兹皇家近卫队，你没有听说过吗？', Speaker = 4396, Option = '', Action = _EmptyTable,}, 
	[83238] = { id = 83238, Voice = '', Text = '当初的近卫队长如今连一位冒险者都无法击败…终究是老了。', Speaker = 4396, Option = '近卫队？', Action = _EmptyTable,}, 
	[83232] = { id = 83232, Voice = '', Text = '难道你不想亲手触碰这块价值不菲的宝石吗？', Speaker = 4396, Option = '取出宝石', Action = _EmptyTable,}, 
	[83231] = { id = 83231, Voice = '', Text = '天啊，你竟然能找到这么稀有的宝石！', Speaker = 4396, Option = '', Action = _EmptyTable,}, 
	[83230] = { id = 83230, Voice = '', Text = '我？我叫西亚顿！', Speaker = 4396, Option = '', Action = _EmptyTable,}, 
	[83229] = { id = 83229, Voice = '', Text = '嘿…冒险者！这可是一个非常罕见的宝石！', Speaker = 4396, Option = '你是谁？', Action = _EmptyTable,}, 
	[83228] = { id = 83228, Voice = '', Text = '这是一块流光满溢的宝石。', Speaker = 4395, Option = '{仔细看看,2},{不感兴趣,1}', Action = _EmptyTable,}, 
	[83227] = { id = 83227, Voice = '', Text = '如果没有牧师的话，也可以通过坐下休息来快速恢复佣兵们的伤势哦~', Speaker = 4301, Option = '', Action = _EmptyTable,}, 
	[83226] = { id = 83226, Voice = '', Text = '但是可能因为体质特殊，艾莉儿小姐的治愈术对我们并没有作用呢。', Speaker = 4301, Option = '', Action = _EmptyTable,}, 
	[83225] = { id = 83225, Voice = '', Text = '我们的佣兵们战斗之后也是需要治疗的，牧师们可以快速治愈我们的伤势。', Speaker = 4301, Option = '', Action = _EmptyTable,}, 
	[83004] = { id = 83004, Voice = '', Text = '嘿，动作不慢嘛老板！算你通过考验啦！', Speaker = 4368, Option = '', Action = _EmptyTable,}, 
	[83003] = { id = 83003, Voice = '', Text = '完成这个我才会承认你，你也就能得到我的雇佣资格证了。', Speaker = 4368, Option = '', Action = _EmptyTable,}, 
	[83002] = { id = 83002, Voice = '', Text = '这样我就可以天天用人偶来练手啦。', Speaker = 4368, Option = '', Action = _EmptyTable,}, 
	[83001] = { id = 83001, Voice = '', Text = '从那些凶恶的树精身上取得一些树皮，我想用这些树皮制作一个人偶。', Speaker = 4368, Option = '', Action = _EmptyTable,}, 
	[83000] = { id = 83000, Voice = '', Text = '所以，能让我感到佩服的，只有那些勇敢的冒险者。', Speaker = 4368, Option = '', Action = _EmptyTable,}, 
	[82999] = { id = 82999, Voice = '', Text = '阿宝最喜欢的就是比武了，可惜这是普隆德拉，不允许动手打斗。', Speaker = 4368, Option = '', Action = _EmptyTable,}, 
	[82998] = { id = 82998, Voice = '', Text = '根据佣兵团的规矩，考核的题目都是由我们自己决定的。', Speaker = 4368, Option = '', Action = _EmptyTable,}, 
	[82997] = { id = 82997, Voice = '', Text = '嘿嘿，我就知道，阿宝可是最受欢迎的佣兵之一了！', Speaker = 4368, Option = '', Action = _EmptyTable,}, 
	[82996] = { id = 82996, Voice = '', Text = '怎么样老板？想得到雇佣资格证吗？', Speaker = 4368, Option = '{确定,2},{还是算了,1}', Action = _EmptyTable,}, 
	[82995] = { id = 82995, Voice = '', Text = '我们猫猫佣兵团的可不是什么人都能雇佣的！', Speaker = 4368, Option = '', Action = _EmptyTable,}, 
	[82994] = { id = 82994, Voice = '', Text = '专属于我的雇佣资格证。只有持有雇佣资格证，才能雇佣我。', Speaker = 4368, Option = '这么麻烦', Action = _EmptyTable,}, 
	[82993] = { id = 82993, Voice = '', Text = '你没有雇佣资格证。我无法接受你的雇佣。', Speaker = 4368, Option = '什么证？', Action = _EmptyTable,}, 
	[82992] = { id = 82992, Voice = '', Text = '老板你是故意来消遣我的吗？', Speaker = 4368, Option = '', Action = _EmptyTable,}, 
	[82991] = { id = 82991, Voice = '', Text = '老板想雇佣我么？', Speaker = 4368, Option = '是的', Action = _EmptyTable,}, 
	[82432] = { id = 82432, Voice = '', Text = '不管怎么样，这次多谢你了，冒险者！', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82431] = { id = 82431, Voice = '', Text = '等我们同盟军打进古城，我一定要鹗枭男爵好看！', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82430] = { id = 82430, Voice = '', Text = '我试着研究一下普通不死魔物的种子，也许会发现一些什么。', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82429] = { id = 82429, Voice = '', Text = '还好…不过比起我的发现，这点惊吓不算什么！', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82428] = { id = 82428, Voice = '', Text = '它！！它竟然亲自赶过来了！我觉得我发现了一个了不得的大秘密！！', Speaker = 4322, Option = '你没事吧', Action = _EmptyTable,}, 
	[82427] = { id = 82427, Voice = '', Text = '要不是…你们今天都活不了！哼！', Speaker = 4323, Option = '', Action = _EmptyTable,}, 
	[82426] = { id = 82426, Voice = '', Text = '不好！结晶被他抢走了！', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82425] = { id = 82425, Voice = '', Text = '就凭你们，也想窥视我们力量源泉的秘密？真是不可救药！', Speaker = 4323, Option = '', Action = _EmptyTable,}, 
	[82424] = { id = 82424, Voice = '', Text = '我需要更多的资料，如果能拿到鹗枭男爵身上的种子就好了…', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82423] = { id = 82423, Voice = '', Text = '再比如说，甚至黑暗种子进化的尽头就是黑暗之王！', Speaker = 4322, Option = '大胆的猜想', Action = _EmptyTable,}, 
	[82422] = { id = 82422, Voice = '', Text = '比如说控制体内拥有种子和结晶的魔物，可以远远控制他们进攻或者逃离！', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82421] = { id = 82421, Voice = '', Text = '不妨我们猜想一下，当结晶吸收足够的能量后，他会产生什么样的变化？', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82420] = { id = 82420, Voice = '', Text = '比起种子，他更像是种子吞噬足够能量后接近饱和的状态，对…就是这种结晶体！', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82419] = { id = 82419, Voice = '', Text = '不敢相信…这个竟然是…姑且称之为黑暗结晶…', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82418] = { id = 82418, Voice = '', Text = '竟然这么快就回来了，奇特的鹗枭首领那么强大！你是怎么做到的！', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82417] = { id = 82417, Voice = '', Text = '不如你帮我从鹗枭首领身上找找看有没有黑暗种子吧！', Speaker = 4322, Option = '交给我吧', Action = _EmptyTable,}, 
	[82416] = { id = 82416, Voice = '', Text = '令我奇怪的是，这个黑暗种子似乎…还不是最终形态，我想…在支配这些低级不死魔物的奇特的鹗枭首领身上会不有有不同的东西……', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82415] = { id = 82415, Voice = '', Text = '我给他命名为黑暗种子！他就像种子一样在魔物身上生根发芽，直到最后支配整个魔物。', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82414] = { id = 82414, Voice = '', Text = '它如同黑洞一般深邃，如果看得时间久了，我想我的灵魂可能也会被一起吸进去。', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82413] = { id = 82413, Voice = '', Text = '瞧！我从他们心脏中那些驳杂的黑暗之力中提取了这样一种物质。', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82412] = { id = 82412, Voice = '', Text = '真是太有效率了！有了这些恶魔之心，我就可以进行我下面的研究了！', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82411] = { id = 82411, Voice = '', Text = '现在，我需要一些恶魔侍者身上的恶魔之心，唔，听起来很恶心，不过，为了让我们的人少流血，我也只能这么做。', Speaker = 4322, Option = '交给我吧', Action = _EmptyTable,}, 
	[82410] = { id = 82410, Voice = '', Text = '如果我能找出那些魔物的弱点，那么将对克雷斯特同盟军进驻古城减少很多阻力。', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82409] = { id = 82409, Voice = '', Text = '他们懂得围攻，如果势单力薄，他们会选择撤退！有智慧的不死魔物比那些只懂得进攻的魔物难对付一万倍！', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82408] = { id = 82408, Voice = '', Text = '是这样，我的研究已经进行到了最关键的时刻。与斐扬的不死魔物不同，这里的不死魔物极具智慧！', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82407] = { id = 82407, Voice = '', Text = '真的？那真是太棒了！', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82406] = { id = 82406, Voice = '', Text = '研究就要停止了，该怎么办…我肩负国王陛下的委托，我怎么能停止研究！', Speaker = 4322, Option = '我来帮你', Action = _EmptyTable,}, 
	[82405] = { id = 82405, Voice = '', Text = '现在他们的人手已经不够了，没有人能够保护我进入古城研究魔物了。', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82404] = { id = 82404, Voice = '', Text = '但是最近克雷斯特同盟军也遇到了困难，他们遭受到了无数不死魔物的袭击。', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82403] = { id = 82403, Voice = '', Text = '在这里进行魔物研究可真是危险呐！还好有克雷斯特同盟军的保护，否则我真的不知道该怎么办！', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82402] = { id = 82402, Voice = '', Text = '不过为了尘世大陆，为了国王，也为了王国的民众们，再苦再累我们也会坚持下去的。', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82401] = { id = 82401, Voice = '', Text = '我们魔物学者要忍受魔物的侵扰以及长时间的风餐露宿，非常的不容易呢。', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82400] = { id = 82400, Voice = '', Text = '而现在我们将眼光聚焦到古城这片古老的土地上，只有未知才能吸引我们魔物学者！', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82399] = { id = 82399, Voice = '', Text = '值得高兴的是，我们在犬妖森林魔物的研究上取得了重大的突破。', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82398] = { id = 82398, Voice = '', Text = '我们魔物研究会是为了有效的解决层出不穷的魔物所衍生出来的问题而存在的。', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82397] = { id = 82397, Voice = '', Text = '我要研究出不死魔物的秘密，让同盟军们尽快进入古城解开这片地区掩盖的秘密。', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82396] = { id = 82396, Voice = '', Text = '他们长期占据着古城，而我也被国王陛下派遣，跟随克雷斯特同盟军驻扎在这里。', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82395] = { id = 82395, Voice = '', Text = '我只知道，这里出现的不死魔物与之前斐扬出现的不死魔物完全不同，甚至可以说是……更为高级！', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82394] = { id = 82394, Voice = '', Text = '而现在，这里也只剩下残桓断壁，在这些废墟究竟掩盖着怎样的历史，我们也无从得知。', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82393] = { id = 82393, Voice = '', Text = '很久以前，克雷斯特汉姆无比繁荣，这里甚至被人们称作为尘世大陆的心脏！', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82392] = { id = 82392, Voice = '', Text = '正因为如此，我们魔物研究会才会派遣我来这里研究这里的魔物，以及这些魔物带来的影响。', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82391] = { id = 82391, Voice = '', Text = '根据我们之前在斐扬对于不死魔物的结论来看，这里的不死魔物又感觉很不一样，他们似乎是拥有智慧的。', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82390] = { id = 82390, Voice = '', Text = '在古城中随处可以看见那些充满阴暗气息的不死系魔物。', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82389] = { id = 82389, Voice = '', Text = '很久以前，这里被称之为克雷斯特汉姆，而现在，这里被世人所知，人们更愿意称呼这里为古城。', Speaker = 4322, Option = '', Action = _EmptyTable,}, 
	[82388] = { id = 82388, Voice = '', Text = '这就是百年智家的故事。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82387] = { id = 82387, Voice = '', Text = '而统帅府一直与智家有着联系，智家人甚至发出了”妖君不死，永世不出“誓言。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82386] = { id = 82386, Voice = '', Text = '智家人毫不犹豫地答应下来。从此智家人逐渐淡出斐扬城。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82385] = { id = 82385, Voice = '', Text = '火焰燃尽，那个神秘的声音告诉智家人，这里充斥这黑暗的力量，妖君很快就会因为这里的黑暗力量而复活，这里需要人守护。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82384] = { id = 82384, Voice = '', Text = '火焰越燃越烈，妖君痛苦地嚎叫：我！在这里就是不死之身！我会回来的！我会将你们一个个变为我的奴隶！', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82383] = { id = 82383, Voice = '', Text = '火焰燃烧下，妖君痛苦的嘶吼，一个慵懒的声音传来：看在星辰的面上喵，我救你们一次啊喵~', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82382] = { id = 82382, Voice = '', Text = '那么我就告诉你，事情的结果吧！', Speaker = 1595, Option = '', Action = { num = 1, actionid = 200,},}, 
	[82381] = { id = 82381, Voice = '', Text = '啊…就是这本诗集！谢谢你冒险者。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82380] = { id = 82380, Voice = '', Text = '啊…还有件事想请你帮忙，我在进入古城的时候，曾经遇到一个邪恶使者，惊慌失措之下，我遗失了自己的诗集，你能帮我找回来吗？', Speaker = 1595, Option = '', Action = { num = 1, actionid = 201,},}, 
	[82379] = { id = 82379, Voice = '', Text = '在智家人近乎绝望的时候，一声清脆的铃铛响起，一簇火苗落入了妖君的身上。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82378] = { id = 82378, Voice = '', Text = '而此时的妖君正是全盛时期，强大的妖君肆意散发着黑暗的力量。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82377] = { id = 82377, Voice = '', Text = '智家人全力消灭了这里所有的”不死人“，却也因为星辰力量的消耗而衰弱下来。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82376] = { id = 82376, Voice = '', Text = '他们智家终于走到了妖君面前，此时的妖君无比的强大，更是凭借洞窟中的黑暗力量以及刚刚死去的斐扬人，组成了一支不死军团。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82375] = { id = 82375, Voice = '', Text = '入了斐扬洞窟后，普通人接受不了那些黑暗力量的侵蚀，变成了不死人。只有智家人撑了下来。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82374] = { id = 82374, Voice = '', Text = '在与斐扬的统帅，占星师以及城卫队的商讨下决定征讨”不死人“，于是以智家为首的远征军出发了。', Speaker = 1595, Option = '', Action = { num = 1, actionid = 200,},}, 
	[82373] = { id = 82373, Voice = '', Text = '冒险者，这赛依伐的数量不对啊，作为报酬可是不太够呢，你还是再找一些来吧。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82372] = { id = 82372, Voice = '', Text = '冒险者，感谢你的慷慨大方！', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82371] = { id = 82371, Voice = '', Text = '东西收集齐了吗？', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82370] = { id = 82370, Voice = '', Text = '好了冒险者，是收取报酬的时候了。我需要一些赛依伐，你能帮我找来吗？', Speaker = 1595, Option = '', Action = { num = 1, actionid = 201,},}, 
	[82369] = { id = 82369, Voice = '', Text = '虽然第一位智家族长智晨已经去世，但是智家不会忘记”不死人“的威胁。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82368] = { id = 82368, Voice = '', Text = '过了几十年，曾经人丁稀少的智家逐渐繁衍强盛，成为了斐扬的第一家族。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82367] = { id = 82367, Voice = '', Text = '在一番挑选之后，智晨一家接受了这股力量。智家的星辰力量配合猎人的箭矢很快就打退了”不死人“，年轻的妖君放言以后还会回来。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82366] = { id = 82366, Voice = '', Text = '巴罗娜将耗尽自己的生命接引天上星辰的力量，改善人们的体质，使他们拥有星辰的力量，而这种力量对那些”不死人“有着强烈的克制作用。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82365] = { id = 82365, Voice = '', Text = '坚固的城墙帮助斐扬人们撑了下来，但是那些”不死人“不眠不休的进攻，令斐扬人们心力交瘁，直到那一任占星师巴罗娜提出了一个新的办法。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82364] = { id = 82364, Voice = '', Text = '初得强大力量的年轻人需要更加强大的力量，他希望把整个城的人都变成他的手下，他自称妖君开始进攻斐扬城。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82363] = { id = 82363, Voice = '', Text = '着那些曾经朝气蓬勃，现在没有了神志只有狰狞面孔的斐扬人。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82362] = { id = 82362, Voice = '', Text = '结果所有一同前往的斐扬人都没有回来，直到有一天，顺利偷取到洞窟中黑暗力量的古怪的外来者回来了。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82361] = { id = 82361, Voice = '', Text = '那接下来，我就继续讲述智家的故事了。', Speaker = 1595, Option = '', Action = { num = 1, actionid = 200,},}, 
	[82360] = { id = 82360, Voice = '', Text = '嗯！坚韧，更可贵的是弹性极佳！非常感谢，冒险者。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82359] = { id = 82359, Voice = '', Text = '对了，我可以用弓箭的弦代替！冒险者，请帮我从蝙蝠弓箭手身上收集一些弓弦吧！', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82358] = { id = 82358, Voice = '', Text = '呀，糟糕，我的琴弦断裂了，有什么合适的材料可以制作琴弦呢？', Speaker = 1595, Option = '', Action = { num = 1, actionid = 201,},}, 
	[82357] = { id = 82357, Voice = '', Text = '这位年轻人对洞窟非常感兴趣，他开口希望他刚刚结识的朋友们能带他去那个洞窟，而斐扬活力满满的年轻人早就按捺不住了，双方一拍即合前往了那片洞窟。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82356] = { id = 82356, Voice = '', Text = '某天，远方迎来了一位奇怪的年轻人，他身上没有任何旅行用具，却徒步走到了斐扬，斐扬人对此并没有起疑，相反，那时热情好客的斐扬人邀请他住了下来。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82355] = { id = 82355, Voice = '', Text = '但是年轻人渴望探索，一群年轻人商量后，决定下次偷偷前往洞窟。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82354] = { id = 82354, Voice = '', Text = '城内欢呼雀跃，其中一个年轻人告诉其他没去的伙伴，他们发现了一个黝黑的洞窟，有经验的老猎人阻止了想要进去探一探的年轻人。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82353] = { id = 82353, Voice = '', Text = '很快，远征小队就回到了斐扬，带着不少从未见过的猎物。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82352] = { id = 82352, Voice = '', Text = '终于有一次，在激烈的讨论后，以年轻人为主的激进派终于说服了以中老年猎人为主的保守派，斐扬的人们决定派遣一只小队探索斐扬外未知的地区，并且绘制地图，为下一次探索做准备。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82351] = { id = 82351, Voice = '', Text = '斐扬的人们，尤其是年轻的猎人们，他们需要更加激烈的争斗，希望猎取更加凶猛的野兽，他们渴望离开城墙附近，探索外面的天地。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82350] = { id = 82350, Voice = '', Text = '他们用坚固的城墙抵御了无数野兽的袭击，在与野兽搏斗中斐扬人们的狩猎技术不断成长，终于，斐扬人们靠着自己的力量也能不费吹灰之力猎到野兽了。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82349] = { id = 82349, Voice = '', Text = '勤劳团结的斐扬人为了更好的抵御斐扬南部以及斐扬树林野兽的侵扰，建造了斐扬城。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82348] = { id = 82348, Voice = '', Text = '那么接下来，我就讲讲百年智家的建立吧！', Speaker = 1595, Option = '', Action = { num = 1, actionid = 200,},}, 
	[82347] = { id = 82347, Voice = '', Text = '看来你对我们吟游诗人传唱的故事并不是很了解呢，你还是了解了解再来找我吧。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82346] = { id = 82346, Voice = '', Text = '看来你已经对我们传唱的故事了若指掌了呢。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82345] = { id = 82345, Voice = '', Text = '第三题，在艾尔克的故事中，艾尔克极力寻找女神项链是为了换取什么物品？', Speaker = 1595, Option = '{轮回之石,5},{女神之泪,0},{占卜宝珠,5}', Action = _EmptyTable,}, 
	[82344] = { id = 82344, Voice = '', Text = '那么，请听第二题，在拉武雷的故事中，拉武雷所使用的是什么魔法？', Speaker = 1595, Option = '{古魔法,0},{火魔法,4},{冰魔法,4}', Action = _EmptyTable,}, 
	[82343] = { id = 82343, Voice = '', Text = '那么请回答我的第一个问题，在西比盖的故事中，西比盖是什么职业？', Speaker = 1595, Option = '{摄影师,3},{占卜师,3},{傀儡师,0}', Action = _EmptyTable,}, 
	[82342] = { id = 82342, Voice = '', Text = '冒险者，你自信的样子真是帅气呢。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82341] = { id = 82341, Voice = '', Text = '再听故事之前，我想问你几个问题，很简单的小问题哦~', Speaker = 1595, Option = '你问吧', Action = { num = 1, actionid = 201,},}, 
	[82340] = { id = 82340, Voice = '', Text = '克雷斯特汉姆这片古老的土地蕴含无数动人的故事，冒险者，你想听吗？', Speaker = 1595, Option = '{我仍然想听,2},{我有空再听吧,1}', Action = _EmptyTable,}, 
	[82256] = { id = 82256, Voice = '', Text = '当然，如果队伍有空闲位置的时候，冒险者们仍然可以召唤回这些已经雇佣的佣兵，让他们再次进入队伍。', Speaker = 4301, Option = '', Action = _EmptyTable,}, 
	[82255] = { id = 82255, Voice = '', Text = '特别需要说明的是，一位佣兵会占用一个组队位置，当冒险者们是组队状态的时候，如果冒险者加佣兵的数量大于队伍上限的时候，佣兵会自己脱离组队。', Speaker = 4301, Option = '', Action = _EmptyTable,}, 
	[82254] = { id = 82254, Voice = '', Text = '当佣兵受到致命伤害的时候，他们会回来进入休整期，疗养好伤势后便会再次投入战斗！', Speaker = 4301, Option = '', Action = _EmptyTable,}, 
	[82253] = { id = 82253, Voice = '', Text = '为了配合雇主的战斗需求，冒险者的等级越高，我们派遣的佣兵等级也就越高。', Speaker = 4301, Option = '', Action = _EmptyTable,}, 
	[82252] = { id = 82252, Voice = '', Text = '我对我们佣兵团佣兵的实力非常自信，所以，每位冒险者最多只能雇佣两位佣兵，当你等级15级时，就可以在我们佣兵团雇佣一名佣兵，如果你想雇佣第二名佣兵，就请先学习冒险技能吧！', Speaker = 4301, Option = '', Action = _EmptyTable,}, 
	[82251] = { id = 82251, Voice = '', Text = '猫猫佣兵团是在冒险者公会高级导师希盖伊兹的主导下建立的，目的是帮助维持我们猫猫岛不擅长经商的伙伴的生活，同时也可以帮助冒险者们解决各种棘手难题的佣兵团~~', Speaker = 4301, Option = '', Action = _EmptyTable,}, 
	[82249] = { id = 82249, Voice = '', Text = '好了，冒险者，该说再见了。如果想听故事，记得回来找我！', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82248] = { id = 82248, Voice = '', Text = '至此克雷斯特汉姆又重新回到了卢恩王国的手中。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82247] = { id = 82247, Voice = '', Text = '黑暗之王被封印后，施密特王开始着手重建克雷斯特汉姆，他从普隆德拉调来了大量物资，用以重建古城。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82246] = { id = 82246, Voice = '', Text = '但是黑暗之王是无法被杀死的，施密特王只能把黑暗之王永封墓地。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82245] = { id = 82245, Voice = '', Text = '施密特王高举象征王权的利刃，重重一击斩在黑暗之王身上，经过教会洗礼的利刃直接令黑暗之王重创。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82244] = { id = 82244, Voice = '', Text = '黑暗之王没想带施密特王的武力也如此出众，竟然这么快就杀到了面前。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82243] = { id = 82243, Voice = '', Text = '他命令皇家守卫拖出黑暗之王召唤出来的魔物，自己和副官冲上去对抗正在进行召唤的黑暗之王。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82242] = { id = 82242, Voice = '', Text = '尽管皇家守卫在黑暗之王的伏击下死伤惨重，但是施密特王也明白，此时是封印黑暗之王最好的时机。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82241] = { id = 82241, Voice = '', Text = '那么我就告诉你，事情的结果吧！', Speaker = 1595, Option = '', Action = { num = 1, actionid = 200,},}, 
	[82240] = { id = 82240, Voice = '', Text = '那个麦克利特被你狠狠地教训了吧！这真是…太痛快了！', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82239] = { id = 82239, Voice = '', Text = '嗯？贝尔蒙德找来的冒险者，想让我跟贝尔蒙德道歉吗？可以，打赢我！', Speaker = 4313, Option = '试试就试试', Action = _EmptyTable,}, 
	[82238] = { id = 82238, Voice = '', Text = '如果你愿意挑战一下这个狂傲的家伙，那么我会很愉快的告诉你施密特王是如何制裁黑暗之王的！', Speaker = 1595, Option = '愿意', Action = _EmptyTable,}, 
	[82237] = { id = 82237, Voice = '', Text = '可是那该死的麦克利特竟然说我不够资格！他就在龙区四处挑战冒险者呢。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82236] = { id = 82236, Voice = '', Text = '冒险者，听说过终极决斗俱乐部吗？曾经我很想成为其中的一员。', Speaker = 1595, Option = '', Action = { num = 1, actionid = 201,},}, 
	[82235] = { id = 82235, Voice = '', Text = '在古城墓地，黑暗之王召唤出无数魔物埋伏并重创了皇家守卫。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82234] = { id = 82234, Voice = '', Text = '数次的伏击让黑暗之王苦不堪言，这位占据古城长达百年的魔物决定给予施密特王一个教训。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82233] = { id = 82233, Voice = '', Text = '他需要这里大量的灵魂来恢复伤势。施密特王也抓住他恢复伤势的机会，一次又一次的伏击黑暗之王。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82232] = { id = 82232, Voice = '', Text = '在这次作战中，黑暗之王被重创逃遁，但是黑暗之王没有离开古城。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82231] = { id = 82231, Voice = '', Text = '黑暗之王竟然潜藏在古城的墓地之中，施密特王立刻制定了作战的方案。', Speaker = 1595, Option = '', Action = { num = 1, actionid = 200,},}, 
	[82230] = { id = 82230, Voice = '', Text = '冒险者，这泯灭之心的数量不对啊，作为报酬可是不太够呢，你还是再找一些来吧。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82229] = { id = 82229, Voice = '', Text = '冒险者，感谢你的慷慨大方！', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82228] = { id = 82228, Voice = '', Text = '东西都收集齐了吗？', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82227] = { id = 82227, Voice = '', Text = '好了冒险者，是收取报酬的时候了。我需要一些泯灭之心，你能帮我找来吗？', Speaker = 1595, Option = '', Action = { num = 1, actionid = 201,},}, 
	[82226] = { id = 82226, Voice = '', Text = '终于，他的得力助手哈因里希为他带来了一个振奋人心的消息！', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82225] = { id = 82225, Voice = '', Text = '施密特王一边派出盗贼去刺探情报，一边思考如何找到黑暗之王。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82224] = { id = 82224, Voice = '', Text = '皇家守卫边战边退，终于在古城的一角建立了营地。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82223] = { id = 82223, Voice = '', Text = '施密特王很快就意识到神圣魔法的作用，他命令随军牧师撑起光之壁障，挡住了大波恶魔的进攻。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82222] = { id = 82222, Voice = '', Text = '他们可以成片的召唤出小型恶魔，这让猝不及防的皇家守卫一遭遇便死伤惨重。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82221] = { id = 82221, Voice = '', Text = '其中盘旋的魔物，是他们从未见过恶魔。这些恶魔每个都会强大的魔法。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82220] = { id = 82220, Voice = '', Text = '眼前的一切让他们不敢相信，以往恢宏大气的克雷斯特汉姆变成了现在的废墟。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82219] = { id = 82219, Voice = '', Text = '他们一路没有遇到任何龙族，很顺利的进入到古城内了。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82218] = { id = 82218, Voice = '', Text = '当所有的龙族魔物都在寻找龙蛋的时候，潜伏在密林中的皇家守卫终于行动了。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82217] = { id = 82217, Voice = '', Text = '并且把所有的龙蛋都藏在了古城大门的相反方向。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82216] = { id = 82216, Voice = '', Text = '聪明的施密特王，派遣手下的盗贼偷走了龙巢中的龙蛋。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82215] = { id = 82215, Voice = '', Text = '那我就继续我的故事了。', Speaker = 1595, Option = '', Action = { num = 1, actionid = 200,},}, 
	[82214] = { id = 82214, Voice = '', Text = '上面似乎是有着模糊的文字…但是却又不太像卢恩文字…', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82213] = { id = 82213, Voice = '', Text = '我想答案应该可以从那些深渊骑士的铠甲中得到。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82212] = { id = 82212, Voice = '', Text = '说起来，我有一个大胆的猜想！古城中的深渊骑士是否就是当初施密特王的皇家守卫呢？', Speaker = 1595, Option = '', Action = { num = 1, actionid = 201,},}, 
	[82211] = { id = 82211, Voice = '', Text = '聪明的施密特王想出了一个办法，这让他们的军队顺利突破龙族的封锁，进入了古城。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82210] = { id = 82210, Voice = '', Text = '施密特王深知一旦被龙族拖在这里，士气一定会低落，以至于到最后全军覆没都有可能。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82209] = { id = 82209, Voice = '', Text = '尽管指挥有度，但是这些强力的龙族魔物还是带给皇家守卫不小的麻烦。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82208] = { id = 82208, Voice = '', Text = '而当天夜晚，皇家守卫便受到了古城郊外龙族魔物的袭击。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82207] = { id = 82207, Voice = '', Text = '皇家守卫从普隆德拉一路前行，仅仅只用三天时间便赶到了古城郊外。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82206] = { id = 82206, Voice = '', Text = '英明的施密特认为时机成熟了，是时候去收复被黑暗之王占领近两百年的古城了。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82205] = { id = 82205, Voice = '', Text = '这支军队从出师那天起就攻无不克战无不胜，无论是什么样的魔物，在这支铁骑面前都不够看。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82204] = { id = 82204, Voice = '', Text = '挑选皇家守卫的职责是他的副官——哈因里希承担的，哈因里希会交给有资质的人一个盾，对他试炼，如果通过了，那人就能成为皇家守卫。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82203] = { id = 82203, Voice = '', Text = '为此他精心训练了一支军队，他称之为皇家守卫。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82202] = { id = 82202, Voice = '', Text = '在施密特登位之后，便立下誓言，绝对会从黑暗之王手中夺回古城。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82201] = { id = 82201, Voice = '', Text = '施密特国王毫无疑问，是一个伟大而又英明的君主。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82200] = { id = 82200, Voice = '', Text = '那么接下来，我就讲诉在黑暗之王纵横的年代，施密特王是如何重建古城的吧。', Speaker = 1595, Option = '', Action = { num = 1, actionid = 200,},}, 
	[82199] = { id = 82199, Voice = '', Text = '看来你对我们吟游诗人传唱的故事并不是很了解呢，你还是了解了解再来找我吧。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82198] = { id = 82198, Voice = '', Text = '看来你已经对我们传唱的故事了若指掌了呢。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82197] = { id = 82197, Voice = '', Text = '第三题，洛斯·拉比尔在进入女神之手之前隶属于哪个公会？', Speaker = 1595, Option = '{刺客公会,0},{魔法公会,5},{猎人公会,5}', Action = _EmptyTable,}, 
	[82196] = { id = 82196, Voice = '', Text = '那么，请听第二题，雷藏、摩根和玛莉亚有着一位共同点的导师。她的名字叫什么？', Speaker = 1595, Option = '{瑟西,0},{瑟雷雅,4},{沙西莉,4}', Action = _EmptyTable,}, 
	[82195] = { id = 82195, Voice = '', Text = '那么请回答我的第一个问题，在亚苏哈卡·剑齿的故事中，亚苏哈卡·剑齿是犬妖氏族的第几任先知？', Speaker = 1595, Option = '{第三任,3},{第一任,0},{第六任,3}', Action = _EmptyTable,}, 
	[82194] = { id = 82194, Voice = '', Text = '冒险者，你自信的样子真是帅气呢。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82193] = { id = 82193, Voice = '', Text = '再听故事之前，我想问你几个问题，很简单的小问题哦~', Speaker = 1595, Option = '你问吧', Action = { num = 1, actionid = 201,},}, 
	[82192] = { id = 82192, Voice = '', Text = '克雷斯特汉姆这片古老的土地蕴含无数动人的故事，冒险者，你想听吗？', Speaker = 1595, Option = '{我仍然想听,2},{我有空再听吧,1}', Action = _EmptyTable,}, 
	[82176] = { id = 82176, Voice = '', Text = '咦？你买的礼物在哪里呢？', Speaker = 4302, Option = '', Action = _EmptyTable,}, 
	[82175] = { id = 82175, Voice = '', Text = '咦，你买的东西还不错，中规中矩的，勉强算你考核过关啦！', Speaker = 4302, Option = '', Action = _EmptyTable,}, 
	[82174] = { id = 82174, Voice = '', Text = '喵~礼物买到了吗？', Speaker = 4302, Option = '{带来了,22},{没有,21}', Action = _EmptyTable,}, 
	[82173] = { id = 82173, Voice = '', Text = '咦？你买的礼物在哪里呢？', Speaker = 4302, Option = '', Action = _EmptyTable,}, 
	[82172] = { id = 82172, Voice = '', Text = '哇！你果然很合我的胃口呢！老板，你通过考验啦！', Speaker = 4302, Option = '', Action = _EmptyTable,}, 
	[82171] = { id = 82171, Voice = '', Text = '喵~礼物买到了吗？', Speaker = 4302, Option = '{带来了,12},{没有,11}', Action = _EmptyTable,}, 
	[82170] = { id = 82170, Voice = '', Text = '我很好理解吧~根据我的描述买一份玩具给我吧！', Speaker = 4302, Option = '{我中意你,11},{波利魔棒,21}', Action = _EmptyTable,}, 
	[82169] = { id = 82169, Voice = '', Text = '我是一只外卖猫~最喜欢的是心~最讨厌的就是黏糊糊的波利了！', Speaker = 4302, Option = '', Action = _EmptyTable,}, 
	[82168] = { id = 82168, Voice = '', Text = '现在我就给你讲讲我自己吧！', Speaker = 4302, Option = '', Action = _EmptyTable,}, 
	[82167] = { id = 82167, Voice = '', Text = '如果雇主不了解，一旦发生不愉快的事情，这就很难办了。', Speaker = 4302, Option = '', Action = _EmptyTable,}, 
	[82166] = { id = 82166, Voice = '', Text = '接下来开始第二项考核，我们为雇主工作的时候，希望雇主能够了解我们。', Speaker = 4302, Option = '', Action = _EmptyTable,}, 
	[82165] = { id = 82165, Voice = '', Text = '哇！这么简单就完成了啊！冒险者你真是太棒了！', Speaker = 4302, Option = '', Action = _EmptyTable,}, 
	[82164] = { id = 82164, Voice = '', Text = '这点材料根本不是我预定的数量，你还是核对一下贸易单再来找我吧。', Speaker = 2632, Option = '', Action = _EmptyTable,}, 
	[82163] = { id = 82163, Voice = '', Text = '哼，效率还可以，我们家族会考虑加深与猫猫岛商团的合作的。', Speaker = 2632, Option = '', Action = _EmptyTable,}, 
	[82162] = { id = 82162, Voice = '', Text = '我要的柔毛带过来了吗？', Speaker = 2632, Option = '{带来了,2},{没有,1}', Action = _EmptyTable,}, 
	[82161] = { id = 82161, Voice = '', Text = '这点材料根本不是我预定的数量，你还是核对一下贸易单再来找我吧。', Speaker = 1044, Option = '', Action = _EmptyTable,}, 
	[82160] = { id = 82160, Voice = '', Text = '啊！谢谢你，没想到猫猫岛商团的效率真是高啊！', Speaker = 1044, Option = '', Action = _EmptyTable,}, 
	[82159] = { id = 82159, Voice = '', Text = '我要的杰勒比结晶带过来了吗？', Speaker = 1044, Option = '{带来了,2},{没有,1}', Action = _EmptyTable,}, 
	[82158] = { id = 82158, Voice = '', Text = '这是我们猫猫岛的一份贸易单，请你根据贸易单的描述完成考核吧！', Speaker = 4302, Option = '', Action = _EmptyTable,}, 
	[82157] = { id = 82157, Voice = '', Text = '众所周知，我们猫猫岛以经商闻名，我们很佩服那些拥有经商能力的人。', Speaker = 4302, Option = '', Action = _EmptyTable,}, 
	[82156] = { id = 82156, Voice = '', Text = '那先预祝我未来的老板考核成功！第一项考核来啦！', Speaker = 4302, Option = '', Action = _EmptyTable,}, 
	[82155] = { id = 82155, Voice = '', Text = '老板要不要参加考核呢？在我这里就可以直接参加考核哦~', Speaker = 4302, Option = '{参加,2},{还是算了,1}', Action = _EmptyTable,}, 
	[82154] = { id = 82154, Voice = '', Text = '虽然麻烦，但是可不是谁都能得到我们的服务呢。', Speaker = 4302, Option = '', Action = _EmptyTable,}, 
	[82153] = { id = 82153, Voice = '', Text = '雇佣资格证。简而言之，就是通过了我们猫猫岛成员的考核，得到了我们认可的人，我们才会为他提供服务。', Speaker = 4302, Option = '这么麻烦', Action = _EmptyTable,}, 
	[82152] = { id = 82152, Voice = '', Text = '咦，你好像并没有获得雇佣资格证呢。恐怕我无法为您提供服务了。', Speaker = 4302, Option = '什么证？', Action = _EmptyTable,}, 
	[82151] = { id = 82151, Voice = '', Text = '老板！你要雇佣我吗？价格很优惠哦~', Speaker = 4302, Option = '', Action = _EmptyTable,}, 
	[82148] = { id = 82148, Voice = '', Text = '好了，冒险者，该说再见了。如果想听故事，记得回来找我！', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82147] = { id = 82147, Voice = '', Text = '至于历史的真相如何，那我就无能为力了。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82146] = { id = 82146, Voice = '', Text = '我曾有幸看到过卢恩王国的史书，加上我道听途说的传说，编织成这样一个故事。希望来听故事的冒险者能够满意。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82145] = { id = 82145, Voice = '', Text = '施密特王在古城旧址上，再次新建古城，当然，这就是另外一个故事了。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82144] = { id = 82144, Voice = '', Text = '但是他们都铩羽而归，直到施密特国王横空出世，几经战斗击败了黑暗之王。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82143] = { id = 82143, Voice = '', Text = '无数冒险者想深入其中寻找黑暗之王的踪迹，想净化古城中的暗黑。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82142] = { id = 82142, Voice = '', Text = '古城已经沦入了黑暗之王的领地，在黑暗之王的统治之下，古城成为了有名的险地。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82141] = { id = 82141, Voice = '', Text = '好吧，既然你赢了，我就讲完这个故事吧。', Speaker = 1595, Option = '', Action = { num = 1, actionid = 200,},}, 
	[82140] = { id = 82140, Voice = '', Text = '平局啊…不行，怎么说你也得赢我一把才行…', Speaker = 1595, Option = '{再玩一次,30},{不玩啦,1}', Action = _EmptyTable,}, 
	[82139] = { id = 82139, Voice = '', Text = '哎呀！真是不走运，让你赢了一局，不过，下次你就没这么好的运气了。好吧，冒险者，和你猜拳很愉快。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82138] = { id = 82138, Voice = '', Text = '嘿，我就说嘛，在这里没有人能在猜拳上胜我！', Speaker = 1595, Option = '{再玩一次,30},{不玩啦,1}', Action = _EmptyTable,}, 
	[82137] = { id = 82137, Voice = '', Text = '那么，现在开始吧！三！二！一！出拳！', Speaker = 1595, Option = '{拳头,40},{剪刀,50},{布,60}', Action = _EmptyTable,}, 
	[82136] = { id = 82136, Voice = '', Text = '试试你的手气~，赢了我你可是能得到不少奖励的哦~', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82135] = { id = 82135, Voice = '', Text = '好了冒险者，故事也快进入尾声了，如果不介意，来猜个拳吧！', Speaker = 1595, Option = '', Action = { num = 1, actionid = 201,},}, 
	[82134] = { id = 82134, Voice = '', Text = '普隆德拉再也收不到古城一丝一毫的消息，派去的探索人员也都毫无声息的消失了。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82133] = { id = 82133, Voice = '', Text = '黑暗之王袭击了看守古城的警卫队，并且封锁了古城。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82132] = { id = 82132, Voice = '', Text = '那些肮脏的魔物组成了一个新的魔物——黑暗之王！', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82131] = { id = 82131, Voice = '', Text = '离开了古城的可雷霍尔伦却不知道，他所消灭的黑暗魔物，在一股不知名的力量下汇聚在了一起。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82130] = { id = 82130, Voice = '', Text = '冒险者，这深渊花的数量不对啊，作为报酬可是不太够呢，你还是再找一些来吧。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82129] = { id = 82129, Voice = '', Text = '冒险者，感谢你的慷慨大方！', Speaker = 1595, Option = '', Action = { num = 1, actionid = 200,},}, 
	[82128] = { id = 82128, Voice = '', Text = '东西都收集齐了吗？', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82127] = { id = 82127, Voice = '', Text = '好了冒险者，是收取报酬的时候了。我需要一些深渊花，你能帮我找来吗？', Speaker = 1595, Option = '', Action = { num = 1, actionid = 201,},}, 
	[82126] = { id = 82126, Voice = '', Text = '经过上上下下缜密的搜查之后，终于是藉由某种无法言喻的魔之力量躲进了软禁房中的镜子里面去了。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82125] = { id = 82125, Voice = '', Text = '在休密兹被监进小房间几个月之后的某天，负责监视休密兹的警卫队长在巡房之际居然发现，休密兹凭空消失了。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82124] = { id = 82124, Voice = '', Text = '可雷霍尔伦搬离了克雷斯特汉姆新城，重返普隆德拉王宫，同时重新宣告普隆德拉为卢恩—米德加尔特王国的首都。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82123] = { id = 82123, Voice = '', Text = '直到古城再也见不到一只黑暗生物为止，休密兹被囚禁在克雷斯特汉姆新城的塔顶。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82122] = { id = 82122, Voice = '', Text = '可雷霍尔伦对休密兹所在的古城深恶痛绝，他清理了一波又一波黑暗魔物。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82121] = { id = 82121, Voice = '', Text = '当休密兹被叛军逮捕时已经不成人形了。这就是休密兹多年来为了追求幻魔仪式与长生不老的肉体，经过多种实验换来的代价。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82120] = { id = 82120, Voice = '', Text = '虽然牺牲惨烈，可雷霍尔伦领导的叛军还是攻占了克雷斯特汉姆新城。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82119] = { id = 82119, Voice = '', Text = '休密兹招来的一群由魔族们所组成的魔军，向反叛军发起反击。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82118] = { id = 82118, Voice = '', Text = '在反叛军濒临城下之时，惊慌的休密兹通过仪式联系到了魔族。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82117] = { id = 82117, Voice = '', Text = '然而灾难的征兆已经逐渐呈现，休密兹多年来为了追求幻魔仪式与长生不老的肉体，使他达成了与魔族的契约。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82116] = { id = 82116, Voice = '', Text = '嘿，干得好冒险者，不过现在不是研究的时候，我得讲完我的故事。', Speaker = 1595, Option = '', Action = { num = 1, actionid = 200,},}, 
	[82115] = { id = 82115, Voice = '', Text = '如果能证实与休密兹有关，嘿嘿，那么我们吟游诗人又有新的故事可以传唱了。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82114] = { id = 82114, Voice = '', Text = '所以我希望你能够从他们身上收集一些恶魔之心。', Speaker = 1595, Option = '', Action = { num = 1, actionid = 201,},}, 
	[82113] = { id = 82113, Voice = '', Text = '说到这里，我怀疑古城中的恶魔女仆和恶魔侍者就是休密兹招来的恶魔。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82112] = { id = 82112, Voice = '', Text = '可雷霍尔伦·冯·吉奥布罗克打著驱逐暴君的口号集结了武装叛军。于各地势如破竹击败王室军队，最后包围了克雷斯特汉姆新城。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82111] = { id = 82111, Voice = '', Text = '他的所作所为引起了他的表弟可雷霍尔伦·冯·吉奥布罗克的强烈不满。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82110] = { id = 82110, Voice = '', Text = '休密兹在晚年甚至借助魔族的力量想要长生不老，大肆绑架人民以供祭祀。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82109] = { id = 82109, Voice = '', Text = '休密兹在新城中完全和外界断绝联络，每天只顾著享乐与游戏，花费大笔的税金，这些税金全由人民与冒险家来缴纳。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82108] = { id = 82108, Voice = '', Text = '休密兹为了恢复与中兴巴特家族的名声，不顾他人眼光调整了人事，甚至还不够，还改变了接受王位的继承权的条件。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82107] = { id = 82107, Voice = '', Text = '历代都会产生国王的名门王族，赖贝布勒克家族，因为被休密兹的肃清行动而牺牲或逃亡国外，或已在国外的人们全部驱逐殆尽。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82106] = { id = 82106, Voice = '', Text = '在得知了自己的身世的情况下，休密兹对欺骗他们的人们展开血腥复仇。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82105] = { id = 82105, Voice = '', Text = '休密兹的威望越来越高最后掌握了无所不为的大权。随著王宫改建的开始，他花税金如流水，一连串的派对与活动让王宫财政开始吃紧。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82104] = { id = 82104, Voice = '', Text = '他在首都附近盖了一作名为‘竞技场’的综合斗技场 将冒险家们虐杀魔族们的画面公开呈现以搏取国民的欢心。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82103] = { id = 82103, Voice = '', Text = '他开始拓展国外版图 并将王宫内许多人士赶出宫外，把自己身边马屁精们奉为自己的心腹。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82102] = { id = 82102, Voice = '', Text = '暴君休密兹在曾经被巨人族与诸神大战中毁灭的遗址上建立了克雷斯特汉姆。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82101] = { id = 82101, Voice = '', Text = '那么接下来，我就好好讲述一下黑暗降临的故事。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82100] = { id = 82100, Voice = '', Text = '看来你对我们吟游诗人传唱的故事并不是很了解呢，你还是了解了解再来找我吧。', Speaker = 1595, Option = '', Action = _EmptyTable,}, 
	[82099] = { id = 82099, Voice = '', Text = '看来你已经对我们传唱的故事了若指掌了呢。', Speaker = 1595, Option = '', Action = { num = 1, actionid = 200,},}, 
	[82098] = { id = 82098, Voice = '', Text = '最后一个问题，在索罗·图克的故事中，索罗·图克的妻子叫什么名字？', Speaker = 1595, Option = '{柯古娜,5},{柯尔娜,5},{柯丽娜,0}', Action = _EmptyTable,}, 
	[82097] = { id = 82097, Voice = '', Text = '嘿嘿，那么回答我第二个问答吧！在艾勒梅斯的故事中，艾勒梅斯的父亲陷入了哪个魔物的控制？', Speaker = 1595, Option = '{迷幻之王,0},{黑暗之王,4},{虎王,4}', Action = _EmptyTable,}, 
	[82096] = { id = 82096, Voice = '', Text = '普隆德拉的铁匠忽克连的师傅是谁呢？', Speaker = 1595, Option = '{无名者,3},{哈沃德,0},{固特伊,3}', Action = _EmptyTable,}, 
	[82095] = { id = 82095, Voice = '', Text = '听起来你很自信。那么请回答我第一个问题！', Speaker = 1595, Option = '', Action = { num = 1, actionid = 201,},}, 
	[82094] = { id = 82094, Voice = '', Text = '那么请先回答我几个小小的问题吧！我可不想我们诗人讲述的故事那么快被世人遗忘。', Speaker = 1595, Option = '你问吧', Action = _EmptyTable,}, 
	[82093] = { id = 82093, Voice = '', Text = '你想听听古城的往事吗？作为吟游诗人协会的高级理事，我的要价可不低哦~', Speaker = 1595, Option = '{我仍然想听,2},{我有空再听吧,1}', Action = _EmptyTable,}, 
	[82052] = { id = 82052, Voice = '', Text = '喏，就是这把匕首。我一定会好好珍惜这把匕首的。', Speaker = 4295, Option = '', Action = _EmptyTable,}, 
	[82051] = { id = 82051, Voice = '', Text = '不过在好心的冒险者的帮助下，金恩总算找回了他的儿子，而我，也得到了金恩的馈赠。', Speaker = 4295, Option = '', Action = _EmptyTable,}, 
	[82050] = { id = 82050, Voice = '', Text = '跳动的火焰，欢笑着，游荡在空寂的残城之中，放佛在寻找下一个迷失的人类。', Speaker = 4295, Option = '', Action = _EmptyTable,}, 
	[82049] = { id = 82049, Voice = '', Text = '最终，他自己变成了一团幽蓝的火焰，看上去壮大了一些。', Speaker = 4295, Option = '', Action = _EmptyTable,}, 
	[82048] = { id = 82048, Voice = '', Text = '小金尚好奇地看着这团火焰，他忘记了自己还在等待归来的母亲和父亲，忘记了饥饿，他迷失在跳动的火焰中，火焰燃烧着他的身躯，但是他感觉不任何疼痛。', Speaker = 4295, Option = '', Action = _EmptyTable,}, 
	[82047] = { id = 82047, Voice = '', Text = '而她的儿子，小金尚在家等候父母的时候，看到了一团幽蓝的火焰。', Speaker = 4295, Option = '', Action = _EmptyTable,}, 
	[82046] = { id = 82046, Voice = '', Text = '她成为鬼女整日游荡，寻找着她失踪的孩子。', Speaker = 4295, Option = '', Action = _EmptyTable,}, 
	[82045] = { id = 82045, Voice = '', Text = '金恩回家后看到小金尚不见了，四处寻找未果后，食用了蘑菇，而这蘑菇竟然是有毒的鬼母菇！', Speaker = 4295, Option = '', Action = _EmptyTable,}, 
	[82044] = { id = 82044, Voice = '', Text = '在他身上，她找到了一把精钢匕首。想到小金尚还在家等着她煮美味的蘑菇汤，她忍痛埋葬了那具尸体，向着家的方向，离去。', Speaker = 4295, Option = '', Action = _EmptyTable,}, 
	[82043] = { id = 82043, Voice = '', Text = '不久后，她依稀见到远处躺着一个熟悉的人，她奔跑过去，只看到了一张被利爪破坏得面目全非的人类。', Speaker = 4295, Option = '', Action = _EmptyTable,}, 
	[82042] = { id = 82042, Voice = '', Text = '金恩穿过中心的祭台，往城外走去。运气不错，她找到了一些阴凉的蘑菇，他的儿子小金尚应该能美美地喝上一顿蘑菇汤。', Speaker = 4295, Option = '', Action = _EmptyTable,}, 
	[82041] = { id = 82041, Voice = '', Text = '可是几天过去了，金恩苦等，却始终不能等到丈夫回来，眼看着存粮渐渐减少，无奈之下，金恩只能出门寻找丈夫，同时也看看这地下空间有没有什么能够果腹的食物。', Speaker = 4295, Option = '', Action = _EmptyTable,}, 
	[82040] = { id = 82040, Voice = '', Text = '金恩把家中唯一还算是值钱的精钢匕首给了卡勒，希望这个经过月神洗礼的匕首能够带给他好运。', Speaker = 4295, Option = '', Action = _EmptyTable,}, 
	[82039] = { id = 82039, Voice = '', Text = '金恩原本就是月之国的子民，在月之国遭逢大变，进入地下之后，金恩的丈夫不得不为了自己挚爱的家人出门寻找食物。', Speaker = 4295, Option = '', Action = _EmptyTable,}, 
	[82038] = { id = 82038, Voice = '', Text = '我曾经在这里遇到一个叫做金恩的鬼女，她给予了对我来说最为珍贵的礼物，那就是精钢匕首。我很想讲述一下金恩的故事。你想听吗？', Speaker = 4295, Option = '当然', Action = _EmptyTable,}, 
	[82037] = { id = 82037, Voice = '', Text = '非常感谢你，冒险者。', Speaker = 4290, Option = '', Action = _EmptyTable,}, 
	[82036] = { id = 82036, Voice = '', Text = '这真是…太棒了！冒险者，我一定要把你的故事写到国家地理杂志上！', Speaker = 4290, Option = '', Action = _EmptyTable,}, 
	[82035] = { id = 82035, Voice = '', Text = '你赶走那些鬼女了吗？', Speaker = 4290, Option = '当然', Action = _EmptyTable,}, 
	[82034] = { id = 82034, Voice = '', Text = '如果冒险者你学了幽灵相机就会很容易地找到他们。', Speaker = 4290, Option = '我试试', Action = _EmptyTable,}, 
	[82033] = { id = 82033, Voice = '', Text = '我的意思是…她只出现在了我照相机镜头内，可能跟我来之前学的幽灵相机有关…', Speaker = 4290, Option = '', Action = _EmptyTable,}, 
	[82032] = { id = 82032, Voice = '', Text = '说起来…这种鬼女还有一个奇怪的地方…我的双眼无法看到他们。', Speaker = 4290, Option = '…', Action = _EmptyTable,}, 
	[82031] = { id = 82031, Voice = '', Text = '这些凶悍的鬼影害得我不能安心工作了，妻子和孩子可就指着我的工资活呢！', Speaker = 4290, Option = '{帮帮他,2},{我很忙,1}', Action = _EmptyTable,}, 
	[82030] = { id = 82030, Voice = '', Text = '她注意到了我镜头，直接向我扑过来…如果不是我跑的快…', Speaker = 4290, Option = '', Action = _EmptyTable,}, 
	[82029] = { id = 82029, Voice = '', Text = '可是！你绝对想不到，在我拍摄古旧的建筑时，发现了更加凶猛的鬼女！', Speaker = 4290, Option = '', Action = _EmptyTable,}, 
	[82028] = { id = 82028, Voice = '', Text = '…还不是那些鬼东西！我在这里呆了也算不短的时间了。该见识的，我也见识过了。', Speaker = 4290, Option = '', Action = _EmptyTable,}, 
	[82027] = { id = 82027, Voice = '', Text = '救…救命！', Speaker = 4290, Option = '怎么了？', Action = _EmptyTable,}, 
	[82026] = { id = 82026, Voice = '', Text = '不过我打算留着它，算是邂逅他们的纪念吧！', Speaker = 4295, Option = '', Action = _EmptyTable,}, 
	[82025] = { id = 82025, Voice = '', Text = '原来是这样，能有个完美结局真是太好了…嘿嘿，这精钢匕首能值不少钱呢！', Speaker = 4295, Option = '', Action = _EmptyTable,}, 
	[82024] = { id = 82024, Voice = '', Text = '哈，你回来了？怎么样？找到金恩了吗？', Speaker = 4295, Option = '找到了', Action = _EmptyTable,}, 
	[82023] = { id = 82023, Voice = '', Text = '能见到你就好…跟我离开吧…谢谢…这精钢匕首就作为谢礼送给贝鲁特吧。', Speaker = 4298, Option = '', Action = _EmptyTable,}, 
	[82022] = { id = 82022, Voice = '', Text = '当我清醒的时候，我就成了一团鬼火的模样。', Speaker = 4296, Option = '', Action = _EmptyTable,}, 
	[82021] = { id = 82021, Voice = '', Text = '我也忘记了饥饿，迷失在跳动的火焰中，火焰燃烧着我的身躯，但是我感觉不任何疼痛。', Speaker = 4296, Option = '', Action = _EmptyTable,}, 
	[82020] = { id = 82020, Voice = '', Text = '我遇到了一簇鬼火，在这火焰中，我忘记了自己还在等待归来的母亲和父亲。', Speaker = 4296, Option = '', Action = _EmptyTable,}, 
	[82019] = { id = 82019, Voice = '', Text = '金尚？你怎么变成这个样子？', Speaker = 4298, Option = '', Action = _EmptyTable,}, 
	[82018] = { id = 82018, Voice = '', Text = '妈妈……', Speaker = 4296, Option = '', Action = _EmptyTable,}, 
	[82017] = { id = 82017, Voice = '', Text = '快带我看看那个无名鬼火…也许他就是我失踪的儿子小金尚！', Speaker = 4298, Option = '', Action = _EmptyTable,}, 
	[82016] = { id = 82016, Voice = '', Text = '这是…这是我丈夫卡勒的东西…这不是被我送给了贝鲁特吗？', Speaker = 4298, Option = '讲述经过', Action = _EmptyTable,}, 
	[82015] = { id = 82015, Voice = '', Text = '金尚…我的儿子…你到底在哪里？', Speaker = 4298, Option = '看看这个', Action = _EmptyTable,}, 
	[82014] = { id = 82014, Voice = '', Text = '冒险者，你现在去找她应该还来得及…也许他知道些什么…', Speaker = 4295, Option = '', Action = _EmptyTable,}, 
	[82013] = { id = 82013, Voice = '', Text = '我记得…好像是叫金恩…的吧？就在不久前，我在中央的祭坛出遇到了她。', Speaker = 4295, Option = '', Action = _EmptyTable,}, 
	[82012] = { id = 82012, Voice = '', Text = '这把匕首是这个鬼火生前父亲的？可是…我是遇到的一个鬼女才拿到的啊。', Speaker = 4295, Option = '', Action = _EmptyTable,}, 
	[82011] = { id = 82011, Voice = '', Text = '冒险者，找到我的匕首了吗？…是那簇鬼火！冒险者！快赶走他！', Speaker = 4295, Option = '讲述经过', Action = _EmptyTable,}, 
	[82010] = { id = 82010, Voice = '', Text = '父亲的身躯就在这里…一定是你杀了父亲！', Speaker = 4296, Option = '我没有', Action = _EmptyTable,}, 
	[82009] = { id = 82009, Voice = '', Text = '精钢匕首…是我父亲的物品…那天我看着父亲离开了家门，就带着这把匕首！', Speaker = 4296, Option = '', Action = _EmptyTable,}, 
	[82008] = { id = 82008, Voice = '', Text = '…你…杀了…父亲…你杀了父亲！', Speaker = 4296, Option = '', Action = _EmptyTable,}, 
	[82007] = { id = 82007, Voice = '', Text = '……', Speaker = 4296, Option = '', Action = _EmptyTable,}, 
	[82006] = { id = 82006, Voice = '', Text = '…哈！找到了…', Speaker = 1307, Option = '', Action = _EmptyTable,}, 
	[82005] = { id = 82005, Voice = '', Text = '…奇怪的鬼火…奇怪的墓碑…不过这里也没有匕首…', Speaker = 1307, Option = '', Action = _EmptyTable,}, 
	[82004] = { id = 82004, Voice = '', Text = '这里什么都没有……', Speaker = 1307, Option = '', Action = _EmptyTable,}, 
	[82003] = { id = 82003, Voice = '', Text = '那是一把精钢匕首！我敢说，它至少拥有五百年历史！你能帮我回去找找看吗？', Speaker = 4295, Option = '{帮帮他,2},{我很忙,1}', Action = _EmptyTable,}, 
	[82002] = { id = 82002, Voice = '', Text = '我再也不敢去那里了…可是刚刚我发现，我曾经在这里发现了一个古物丢失了！', Speaker = 4295, Option = '什么古物？', Action = _EmptyTable,}, 
	[82001] = { id = 82001, Voice = '', Text = '我感觉到不对劲，立马撒腿狂奔，一直跑到这里，那鬼火也没跟来。', Speaker = 4295, Option = '', Action = _EmptyTable,}, 
	[82000] = { id = 82000, Voice = '', Text = '反而围着我的身体旋转，但是我感觉我的身体在发烫，像是要燃烧了一样。', Speaker = 4295, Option = '', Action = _EmptyTable,}, 
	[81999] = { id = 81999, Voice = '', Text = '看到鬼火的那一瞬间，我吓坏了！但是这簇鬼火并没有袭击我。', Speaker = 4295, Option = '', Action = _EmptyTable,}, 
	[81998] = { id = 81998, Voice = '', Text = '你应该知道这里有着鬼怪的传说，就在刚刚我遇到了一簇奇怪的鬼火。', Speaker = 4295, Option = '', Action = _EmptyTable,}, 
	[81997] = { id = 81997, Voice = '', Text = '哦，天啊！该死的！一定是奔跑时弄丢了…', Speaker = 4295, Option = '你怎么了？', Action = _EmptyTable,}, 
	[81996] = { id = 81996, Voice = '', Text = '所以我才来到这里。现在的我只想尽快找到那位神秘的人物，为我智家报仇！', Speaker = 4294, Option = '', Action = _EmptyTable,}, 
	[81995] = { id = 81995, Voice = '', Text = '妖君也被他封印，进攻我们智家的，只是那妖君小小的分身而已。', Speaker = 4294, Option = '', Action = _EmptyTable,}, 
	[81994] = { id = 81994, Voice = '', Text = '我听家里老人说过，这位神秘人物与妖君是老对头了，两人曾经数次交手，结果当然是神秘人赢了。', Speaker = 4294, Option = '', Action = _EmptyTable,}, 
	[81993] = { id = 81993, Voice = '', Text = '我们智家曾经流传着这样一个传说，妖君第一次入侵之时，曾经有位神秘任务帮了我们，消灭了妖君。', Speaker = 4294, Option = '', Action = _EmptyTable,}, 
	[81992] = { id = 81992, Voice = '', Text = '可是我在这里呆了很久，也没有找到，我想返回智家，却发现智家已经…', Speaker = 4294, Option = '神秘人物？', Action = _EmptyTable,}, 
	[81991] = { id = 81991, Voice = '', Text = '在妖君入侵之时，我就担心智家会抵挡不住，所以我早一步来到了这里，希望能找到昔日帮助过我们智家的那位神秘人物。', Speaker = 4294, Option = '', Action = _EmptyTable,}, 
	[81990] = { id = 81990, Voice = '', Text = '当然奇怪！自从我们智家被那些不死人淹没之后，我已经许久没见过外界的人啦！', Speaker = 4294, Option = '那你…', Action = _EmptyTable,}, 
	[81989] = { id = 81989, Voice = '', Text = '冒险者？天啊！冒险者竟然可以深入到这种地方来了吗？', Speaker = 4294, Option = '很奇怪吗', Action = _EmptyTable,}, 
	[81988] = { id = 81988, Voice = '', Text = '我只有有感而发…这凄凉美丽的传说，我一定要写一篇诗章，赞美月之守护者！', Speaker = 4293, Option = '', Action = _EmptyTable,}, 
	[81987] = { id = 81987, Voice = '', Text = '这里也就变成了现在这番模样。哭泣的鬼魂啊！你为何不肯离去？这里还有什么留恋吗？', Speaker = 4293, Option = '…', Action = _EmptyTable,}, 
	[81986] = { id = 81986, Voice = '', Text = '据说在不知名的力量影响下，月之守护者也逐渐迷失了自己。', Speaker = 4293, Option = '', Action = _EmptyTable,}, 
	[81985] = { id = 81985, Voice = '', Text = '也许是保护这里的代价太过高昂，月之守护者一直处于一种沉眠的状态。', Speaker = 4293, Option = '', Action = _EmptyTable,}, 
	[81984] = { id = 81984, Voice = '', Text = '九尾狐与土人的出现打破了这里人们的美好期望。无数月之国民众被袭击，月之守护者不能保护他们。', Speaker = 4293, Option = '为什么？', Action = _EmptyTable,}, 
	[81983] = { id = 81983, Voice = '', Text = '无论是谁，终究有守护不了的东西，月之守护者也一样。', Speaker = 4293, Option = '', Action = _EmptyTable,}, 
	[81982] = { id = 81982, Voice = '', Text = '没错，就是曾经月神的守护者，她保护着这里不被破坏。', Speaker = 4293, Option = '', Action = _EmptyTable,}, 
	[81981] = { id = 81981, Voice = '', Text = '这座城市还保存着月之国当初的建筑风格，如果不是当初的月之守护者，恐怕这里什么都不会剩下。', Speaker = 4293, Option = '', Action = _EmptyTable,}, 
	[81980] = { id = 81980, Voice = '', Text = '曾经繁荣之极的月之国沦落到这种地步，难道不是可悲的事情吗？', Speaker = 4293, Option = '', Action = _EmptyTable,}, 
	[81979] = { id = 81979, Voice = '', Text = '可悲！可悲！', Speaker = 4293, Option = '可悲什么', Action = _EmptyTable,}, 
	[81978] = { id = 81978, Voice = '', Text = '我还在这里寻找伊米尔之心的踪迹，如果被心术不正的人找到，那不知道就会引起什么样的灾难了。', Speaker = 4292, Option = '', Action = _EmptyTable,}, 
	[81977] = { id = 81977, Voice = '', Text = '我不知道发生了什么，这里似乎没有被伊米尔之心正面冲撞到，否则这里早就化为焦土了。', Speaker = 4292, Option = '', Action = _EmptyTable,}, 
	[81976] = { id = 81976, Voice = '', Text = '根据我的猜测，承载着始祖巨人力量的心脏碎片，落在了这片安宁的城市当中。', Speaker = 4292, Option = '', Action = _EmptyTable,}, 
	[81975] = { id = 81975, Voice = '', Text = '只有伊米尔之心才能让魔力紊乱到这种程度，毕竟巨人的力量区别于诸神魔法的力量。', Speaker = 4292, Option = '', Action = _EmptyTable,}, 
	[81974] = { id = 81974, Voice = '', Text = '嗯？你是在怀疑我么？这里紊乱的魔力还不能说明一切吗？', Speaker = 4292, Option = '', Action = _EmptyTable,}, 
	[81973] = { id = 81973, Voice = '', Text = '而这里恰恰是伊米尔之心坠落的地点！', Speaker = 4292, Option = '你确定？', Action = _EmptyTable,}, 
	[81972] = { id = 81972, Voice = '', Text = '吉芬图书馆有一本古籍中曾经提到，诸神之战以始祖巨人伊米尔战败而告终，他的身体四分五裂，散落在世界的各个地方。', Speaker = 4292, Option = '', Action = _EmptyTable,}, 
	[81971] = { id = 81971, Voice = '', Text = '可能无法感受到，但是对于一个魔法师来说，这里紊乱的魔力流动实在令人太不愉快了！', Speaker = 4292, Option = '', Action = _EmptyTable,}, 
	[81970] = { id = 81970, Voice = '', Text = '我不知道，但是终有一天，我会把这里的历史完完整整的还原出来！', Speaker = 4291, Option = '', Action = _EmptyTable,}, 
	[81969] = { id = 81969, Voice = '', Text = '这里充斥这九尾狐以及土人这两种奇怪的魔物，加上传闻的鬼怪之谈，这里一定隐藏着巨大的秘密！', Speaker = 4291, Option = '什么秘密', Action = _EmptyTable,}, 
	[81968] = { id = 81968, Voice = '', Text = '很惊人，不是吗？这是天灾吗？还是人祸？如果是人祸…想要把整个城市移近地底，这样的人是有多强大！甚至将它称之为神也不为过！', Speaker = 4291, Option = '', Action = _EmptyTable,}, 
	[81967] = { id = 81967, Voice = '', Text = '而根据我对这里找到的古物年份的观察，这座城市至少被埋入地下至少五百年以上！', Speaker = 4291, Option = '', Action = _EmptyTable,}, 
	[81966] = { id = 81966, Voice = '', Text = '破坏的城墙，死去的树木，以及正在修建的祭台，种种迹象都在告诉我们一件事！这座城市几乎是一夜之间沉入地底的！', Speaker = 4291, Option = '', Action = _EmptyTable,}, 
	[81965] = { id = 81965, Voice = '', Text = '作为历史研究员，我对这些非常感兴趣。', Speaker = 4291, Option = '', Action = _EmptyTable,}, 
	[81964] = { id = 81964, Voice = '', Text = '这种建筑，你在其他地方看的到吗？', Speaker = 4291, Option = '这倒是', Action = _EmptyTable,}, 
	[81963] = { id = 81963, Voice = '', Text = '当然古怪！你看这斑驳的时钟，错落有致的石质台阶，当然还有木头门柱！', Speaker = 4291, Option = '', Action = _EmptyTable,}, 
	[81962] = { id = 81962, Voice = '', Text = '这种风格的建筑…似乎从来没见过呢…', Speaker = 4291, Option = '古怪吗？', Action = _EmptyTable,}, 
	[81961] = { id = 81961, Voice = '', Text = '再拍两组照片我就走！这种鬼地方我再也不想来了！', Speaker = 4290, Option = '祝你好运', Action = _EmptyTable,}, 
	[81960] = { id = 81960, Voice = '', Text = '如果不是我机灵，用闪光灯照了一下他，恐怕我…', Speaker = 4290, Option = '', Action = _EmptyTable,}, 
	[81959] = { id = 81959, Voice = '', Text = '当然是那些鬼怪告诉我的！我本以为遇到一个善良的鬼魂呢，谁知道刚说完她就要吃了我。', Speaker = 4290, Option = '', Action = _EmptyTable,}, 
	[81958] = { id = 81958, Voice = '', Text = '听说每当夜到来之时，灵猫会准时出现在城市中心高大的祭坛顶，对着月亮默默祈祷。', Speaker = 4290, Option = '你怎么知道的', Action = _EmptyTable,}, 
	[81957] = { id = 81957, Voice = '', Text = '我们称之为月夜猫的传说中的魔物，就曾是这个国家的守护者！', Speaker = 4290, Option = '', Action = _EmptyTable,}, 
	[81956] = { id = 81956, Voice = '', Text = '而伟大的月神也对这个国度关爱有加，她甚至派出了自己的守护者——一只带着巨大铃铛的猫，守护着这个国度。', Speaker = 4290, Option = '', Action = _EmptyTable,}, 
	[81955] = { id = 81955, Voice = '', Text = '没听过吧？嘿…他们侍奉月之女神，月神是他们唯一的信仰。', Speaker = 4290, Option = '', Action = _EmptyTable,}, 
	[81954] = { id = 81954, Voice = '', Text = '哈！你一定猜不到！这里可是传说中的月之国！', Speaker = 4290, Option = '', Action = _EmptyTable,}, 
	[81953] = { id = 81953, Voice = '', Text = '猜猜，这里为什么会有建筑？这里为什么会有鬼怪？', Speaker = 4290, Option = '我不知道', Action = _EmptyTable,}, 
	[81952] = { id = 81952, Voice = '', Text = '当…当然！如果不是因为杂志社丰厚的报酬，我早就离开这个鬼地方了！', Speaker = 4290, Option = '', Action = _EmptyTable,}, 
	[81951] = { id = 81951, Voice = '', Text = '胆小？嘿，在这种地方，谁的胆子不小？传闻这里有着鬼怪出现，如果你曾经遇到过…', Speaker = 4290, Option = '你遇到过？', Action = _EmptyTable,}, 
	[81950] = { id = 81950, Voice = '', Text = '啊！你吓死我了！', Speaker = 4290, Option = '这么胆小', Action = _EmptyTable,}, 
	[81862] = { id = 81862, Voice = '', Text = '果然难不倒你，那么现在还是必须恭喜你真正的完成了E级考核。', Speaker = 1501, Option = '', Action = _EmptyTable,}, 
	[81861] = { id = 81861, Voice = '', Text = '恩？我没有发现你身上有2片[c][ffff00]变身叶子[-][/c]，等你找到的时候再来和我谈吧！', Speaker = 1501, Option = '', Action = _EmptyTable,}, 
	[81860] = { id = 81860, Voice = '', Text = '很遗憾，这样的话我无法向你介绍接下去的考核！', Speaker = 1501, Option = '', Action = _EmptyTable,}, 
	[81859] = { id = 81859, Voice = '', Text = '那么你愿意补交E级考核时漏交的2个[c][ffff00]变身叶子[-][/c]吗？', Speaker = 1501, Option = '{愿意,4},{不愿意,3}', Action = _EmptyTable,}, 
	[81858] = { id = 81858, Voice = '', Text = '本着对所有冒险者负责的态度，在你进行接下来的考核之前我必须向你补收E级考核时少收的2个[c][ffff00]变身叶子[-][/c]。', Speaker = 1501, Option = '', Action = _EmptyTable,}, 
	[81857] = { id = 81857, Voice = '', Text = '我也因为这件事被国王点名批评了一番。', Speaker = 1501, Option = '', Action = _EmptyTable,}, 
	[81856] = { id = 81856, Voice = '', Text = '说来也是我们的失误，你的E级冒险者考核出了一些纰漏。', Speaker = 1501, Option = '', Action = _EmptyTable,}, 
	[81855] = { id = 81855, Voice = '', Text = '欧~我终于等到你了冒险者！', Speaker = 1501, Option = '怎么了', Action = _EmptyTable,}, 
	[81580] = { id = 81580, Voice = '', Text = '这点[c][ffff00]恶魔角[-][/c]可不够呢。。。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81579] = { id = 81579, Voice = '', Text = '唔，有了这[c][ffff00]恶魔角[-][/c]，我想我的生活会变的更加宽裕，谢谢你，冒险者。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81578] = { id = 81578, Voice = '', Text = '冒险者你回来的可真快！[c][ffff00]恶魔角[-][/c]收集好了么？', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81577] = { id = 81577, Voice = '', Text = '好了冒险者，故事你也已经听完了。作为报酬，我希望你能够帮我取得一些[c][ffff00]恶魔角[-][/c]。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81576] = { id = 81576, Voice = '', Text = '这就是提尔·剑齿的故事。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81575] = { id = 81575, Voice = '', Text = '希望能够在犬妖首领酿成大错之前，阻止首领自取灭亡的计划。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81574] = { id = 81574, Voice = '', Text = '而现在，提尔·剑齿出没在克特森林之中，一边躲避着首领对他的追捕，一边试图联系吉芬的魔法师。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81573] = { id = 81573, Voice = '', Text = '提尔决定遵从先祖的遗命，自己前往吉芬寻求支援，彻底粉碎犬妖首领不切实际的幻想。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81572] = { id = 81572, Voice = '', Text = '提尔·剑齿趁上来囚禁他的犬妖士兵没有防备冲出了包围。提尔决定遵从先祖的遗命，自己前往吉芬寻求支援。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81571] = { id = 81571, Voice = '', Text = '发誓要让他们看到犬妖在他的带领下欣欣向荣的局面。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81570] = { id = 81570, Voice = '', Text = '这句话使得犬妖首领瞬间怒火中烧，下令把这几人软禁。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81569] = { id = 81569, Voice = '', Text = '堪布·剑齿奚落地嘲讽犬妖首领：犬妖一族的命运就葬送在你的手上！', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81568] = { id = 81568, Voice = '', Text = '那接下来，我就继续讲诉这段故事了。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81567] = { id = 81567, Voice = '', Text = '干的不错，冒险者。你果然没让我失望！', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81566] = { id = 81566, Voice = '', Text = '想听接下来发生了什么吗？冒险者消灭一些[c][ffff00]狂暴野猫[-][/c]，我就告诉你。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81565] = { id = 81565, Voice = '', Text = '首领认为在数百年前去吉芬求助，却没有收到想要的帮助，这使得他对吉芬的那群高傲的魔法师充满厌恶。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81564] = { id = 81564, Voice = '', Text = '犬妖首领反问还有其他可行的办法，提尔提出前往吉芬求助遭到了首领的反对。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81563] = { id = 81563, Voice = '', Text = '笼罩在诅咒中的先知陵墓已经变成了一片不祥之地，一旦死去的先知在诅咒中复活，很有可能会为犬妖带来灭族之灾。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81562] = { id = 81562, Voice = '', Text = '这种亵渎先知的做法令提尔四人不能接受，陵寝的看守者旺达·剑齿甚至明明白白地告诉首领。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81561] = { id = 81561, Voice = '', Text = '结合犬妖一族被禁用的巫术复活曾经的犬妖先知们，由此来召唤大犬神希望得到神明最后的庇护。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81560] = { id = 81560, Voice = '', Text = '原来，犬妖首领竟然想利用祭座附近残存的“亚苏哈卡”的神奇力量。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81559] = { id = 81559, Voice = '', Text = '他把自己的计划告诉了四人，却没想到遭到了四个人的极力反对。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81558] = { id = 81558, Voice = '', Text = '犬妖首领看过这封书信，却告诉四人，他心里已经有了完整的计划。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81557] = { id = 81557, Voice = '', Text = '那接下来，我就继续讲诉提尔·剑齿的这段故事了。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81556] = { id = 81556, Voice = '', Text = '咦？这点[c][ffff00]智力料理B[-][/c]可不够呢…', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81555] = { id = 81555, Voice = '', Text = '这么快就收集好了！冒险者你真是太棒了！', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81554] = { id = 81554, Voice = '', Text = '这么快就回来了？快让我看看料理吧！', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81553] = { id = 81553, Voice = '', Text = '想知道他是怎么做的吗？那就帮我收集一些[c][ffff00]智力料理B[-][/c]来吧！', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81552] = { id = 81552, Voice = '', Text = '他们决定联合去找到犬妖首领，告诉他来自过去先知的警告。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81551] = { id = 81551, Voice = '', Text = '三人看过这封书信，一致认为以先知们的能力，一定不会危言耸听。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81550] = { id = 81550, Voice = '', Text = '提尔·剑齿无比崇拜他的先祖，对先祖说的话坚信不移，提尔立刻找到了他的三位友人：旺达·剑齿、堪布·剑齿以及格洛·剑齿。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81549] = { id = 81549, Voice = '', Text = '提尔·剑齿在整理先祖的遗物时，在古老的羊皮卷中发现了这份书信，也看明白了信中所蕴含的感情与警告。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81548] = { id = 81548, Voice = '', Text = '为了犬妖也为了克特之森！阻止他们！', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81547] = { id = 81547, Voice = '', Text = '如果有人还想依靠女神之手，到那个时候，我的子孙们，必须要有人站出来！你们必须站出来！', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81546] = { id = 81546, Voice = '', Text = '终有一日，我们犬妖要脱离女神之手的掌控，终有一日，犬妖一族将获得新生！', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81545] = { id = 81545, Voice = '', Text = '如果我们一昧地依靠女神之手，那么我们犬妖一族的灭亡之日不再遥远。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81544] = { id = 81544, Voice = '', Text = '女神之手不是如我们眼见的那么和善，这个组织充斥着私欲、争斗以及混乱。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81543] = { id = 81543, Voice = '', Text = '我并未受诅咒影响，在此——我，第七先知孟达·剑齿告诫我的子孙们。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81542] = { id = 81542, Voice = '', Text = '或者我已经被诅咒侵蚀，成为了一个不折不扣的恶魔，但是，现在写这封信的时候。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81541] = { id = 81541, Voice = '', Text = '我亲爱的子孙，在你看到这封书信的时候，也许我早已进入陵寝中安息。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81540] = { id = 81540, Voice = '', Text = '今天我们就来讲讲提尔·剑齿的故事吧！', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81539] = { id = 81539, Voice = '', Text = '这点[c][ffff00]玻璃珠[-][/c]可不够呢。。。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81538] = { id = 81538, Voice = '', Text = '唔，有了这[c][ffff00]玻璃珠[-][/c]，我想我的生活会变的更加宽裕，谢谢你，冒险者。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81537] = { id = 81537, Voice = '', Text = '冒险者你回来的可真快！[c][ffff00]玻璃珠[-][/c]收集好了么？', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81536] = { id = 81536, Voice = '', Text = '好了冒险者，故事你也已经听完了。作为报酬，我希望你能够帮我取得一些[c][ffff00]玻璃珠[-][/c]。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81535] = { id = 81535, Voice = '', Text = '“鲁德，接下来就看你的了”，这就是库卡·剑齿的故事。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81534] = { id = 81534, Voice = '', Text = '他走出了巫术阵，空中无处不在的诅咒发现这个弱小的身躯，失去了宝石的庇佑，库卡·剑齿瞬间就被诅咒侵蚀。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81533] = { id = 81533, Voice = '', Text = '库卡·剑齿欣慰地笑着，力量的流失对他来说是那么微不足道。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81532] = { id = 81532, Voice = '', Text = '宝石的光亮越来越盛，这下宝石应该还能支撑一段时间。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81531] = { id = 81531, Voice = '', Text = '力量从他身上开始向手中的宝石聚集，宝石的光辉开始闪亮，整个巫术阵再次充满了能量，库卡·剑齿的力量逐渐消失。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81530] = { id = 81530, Voice = '', Text = '双手捧着“亚苏哈卡”，库卡念着古老的巫咒，他正在献祭自己的力量。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81529] = { id = 81529, Voice = '', Text = '而库卡·剑齿，深深吸了一口气，慢慢进入了之前建立的巫术阵中。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81528] = { id = 81528, Voice = '', Text = '库卡像是交代后事般，把所有事情都交代给鲁德，鲁德带着敬意，离开了犬妖森林，去吉芬寻求帮助。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81527] = { id = 81527, Voice = '', Text = '不用担心这里，我会在这里布置好一切，你只需要做好自己的事情，但是切记不管发生任何事情，一定要先保证族群的安全！', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81526] = { id = 81526, Voice = '', Text = '据说南方的吉芬塔中，住着一位伟大的魔法师，鲁德，你可以派人前往求助，我会在这里守护“亚苏哈卡”。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81525] = { id = 81525, Voice = '', Text = '不能让整个犬妖森林就此灭亡，他对鲁德这样说着。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81524] = { id = 81524, Voice = '', Text = '库卡·剑齿摘下了自己象征先知身份的头冠，把它交给了自己的继承者——鲁德·剑齿。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81523] = { id = 81523, Voice = '', Text = '那么，我就继续开始讲诉故事了。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81522] = { id = 81522, Voice = '', Text = '唔，完成得不错，冒险者。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81521] = { id = 81521, Voice = '', Text = '就如同现在的那些榔头犬妖一样，充满危险，冒险者，你能消灭一些[c][ffff00]榔头犬妖[-][/c]吗？', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81520] = { id = 81520, Voice = '', Text = '虽然克特森林表面上仍旧枝繁叶茂，可是那些植物随时都可能让任何一个踏入这里的人感受到生命的威胁。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81519] = { id = 81519, Voice = '', Text = '荆棘比从前更疯狂的肆虐土地，饲养的猫变的狂暴。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81518] = { id = 81518, Voice = '', Text = '直到变成了只有指甲大小时，诅咒气息又一次笼罩了克特森林，克特森林里的犬妖在这段时间里失去了往日的神采，有一些甚至丧失了理智。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81517] = { id = 81517, Voice = '', Text = '他感受到“亚苏哈卡”的力量正在逐渐衰弱，宝石由最初的拳头大小在几年时间内变为手指大小。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81516] = { id = 81516, Voice = '', Text = '魔王邪恶的诅咒并没有被彻底的根除，随着时间的流逝，祭座上的“亚苏哈卡” 慢慢的失去的光芒。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81515] = { id = 81515, Voice = '', Text = '克特森林慢慢的恢复了往日的繁茂，但是库卡·剑齿深知现在并不是安心下来的时候。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81514] = { id = 81514, Voice = '', Text = '把那块宝石放入了祭座中心的巫术阵中，“亚苏哈卡” 与恶魔诅咒的力量瞬间产生冲突，慢慢驱散了恶魔的诅咒。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81513] = { id = 81513, Voice = '', Text = '他以大犬神的指引为由，命令犬妖们在祭坛中心建造了巨大的犬神祭座。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81512] = { id = 81512, Voice = '', Text = '那接下来，我就继续讲诉库卡·剑齿的这段故事了。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81511] = { id = 81511, Voice = '', Text = '咦？这点[c][ffff00]幸运料理B[-][/c]可不够呢…', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81510] = { id = 81510, Voice = '', Text = '这么快就收集好了！冒险者你真是太棒了！', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81509] = { id = 81509, Voice = '', Text = '这么快就回来了？快让我看看料理吧！', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81508] = { id = 81508, Voice = '', Text = '想知道他是怎么做的吗？那就帮我收集一些[c][ffff00]幸运料理B[-][/c]来吧！', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81507] = { id = 81507, Voice = '', Text = '思虑许久，他才决定使用被犬妖先知奉如珍宝的“亚苏哈卡”，他坚信这块散发着神奇力量的宝石，一定能够与魔王的诅咒对抗，为犬妖们争取更多时间。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81506] = { id = 81506, Voice = '', Text = '他也清楚的知道，如果没有任何外力介入的情况下，它们的家园将在不久的将来彻底毁灭。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81505] = { id = 81505, Voice = '', Text = '而作为先知们的继承者，库卡·剑齿明白，所谓的神明只是为了更好的主导犬妖的未来而编写的谎言。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81504] = { id = 81504, Voice = '', Text = '更多的犬妖纷纷来到犬神祭坛下，祈祷着期盼着那位并不存在的神明的庇护。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81503] = { id = 81503, Voice = '', Text = '巨大的灾难严重影响着犬妖们的生活，越来越多的犬妖在绝望中癫狂，死去。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81502] = { id = 81502, Voice = '', Text = '郁郁葱葱的森林逐渐凋零，充满尖刺的荆棘疯狂生长，整个森林在诅咒下整个森林面目全非。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81501] = { id = 81501, Voice = '', Text = '当梦罗克在这片生机盎然的土地上撒下了最恶毒的诅咒，库卡·剑齿就知道亚苏哈卡先知所看见的未来的一角已经悄然来临。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81500] = { id = 81500, Voice = '', Text = '今天我们就来讲讲库卡·剑齿的故事吧！', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81499] = { id = 81499, Voice = '', Text = '这点[c][ffff00]刹勒空结晶[-][/c]可不够呢。。。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81498] = { id = 81498, Voice = '', Text = '唔，有了这[c][ffff00]刹勒空结晶[-][/c]，我想我的生活会变的更加宽裕，谢谢你，冒险者。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81497] = { id = 81497, Voice = '', Text = '冒险者你回来的可真快！[c][ffff00]刹勒空结晶[-][/c]收集好了么？', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81496] = { id = 81496, Voice = '', Text = '好了冒险者，故事你也已经听完了。作为报酬，我希望你能够帮我取得一些[c][ffff00]刹勒空结晶[-][/c]。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81495] = { id = 81495, Voice = '', Text = '这就是犬妖们的首任先知亚苏哈卡·剑齿的故事。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81494] = { id = 81494, Voice = '', Text = '先知陵寝成为了他的安息之所，先知遗迹成为了他隐藏先知秘密的地方，犬神祭坛成为了这块被他命名为“亚苏哈卡”的宝石的祭祀之地。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81493] = { id = 81493, Voice = '', Text = '亚苏哈卡知道危机并没有解除，他命令犬妖们修建三座建筑，', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81492] = { id = 81492, Voice = '', Text = '大战持续近一个昼夜，巨兽终于在不甘中死去，而亚苏哈卡则在万千犬妖的呼喊中成为犬妖的领袖。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81491] = { id = 81491, Voice = '', Text = '他发现他能控制更多的“光点”并且他的操控更加精准。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81490] = { id = 81490, Voice = '', Text = '但他别无选择，为了族群只能这么做，终于他激活了宝石中的力量，浑身沐浴在金色光芒之下。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81489] = { id = 81489, Voice = '', Text = '亚苏卡哈决定爆发宝石中的力量，尽管他知道这会对他的身体造成不可磨灭的伤害。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81488] = { id = 81488, Voice = '', Text = '他决定用自己的力量，用宝石的力量给予巨兽致命的一击，可惜他得到宝石的时间不长，无法对巨兽造成伤害。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81487] = { id = 81487, Voice = '', Text = '亚苏哈卡却清楚，“大犬神”根本不存在，看着流离失所的同族们，亚苏哈卡思量许久，终于决定站出来。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81486] = { id = 81486, Voice = '', Text = '那天巨兽来袭，数以千计的犬妖死于巨兽口中，活下来的犬妖们跪地祈求亚苏卡哈召唤伟大的“大犬神”，用神罚惩戒这可恶的巨兽。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81485] = { id = 81485, Voice = '', Text = '暗中窥视的巨兽垂涎宝石的力量，而身怀宝藏却毫无所觉的亚苏哈卡还在过着奢华的生活。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81484] = { id = 81484, Voice = '', Text = '那么，我就继续开始讲诉故事了。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81483] = { id = 81483, Voice = '', Text = '唔，果然是充满诅咒恶臭的气味呢，干得好，冒险者！', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81482] = { id = 81482, Voice = '', Text = '究竟是什么灾难呢~~嘿嘿，想知道的话，就杀一些[c][ffff00]大斧犬妖[-][/c]吧！', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81481] = { id = 81481, Voice = '', Text = '然而享受着这一切的亚苏哈卡还不知道，灾难即将来临。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81480] = { id = 81480, Voice = '', Text = '所有食物尽情享用，亚苏哈卡所要的一切，犬妖们都尽量满足，他被犬妖们尊称为“蒙轲雷瑟”，意为“神之先知”。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81479] = { id = 81479, Voice = '', Text = '谎称自己是神明降入大陆的侍从，亚苏哈卡受到了前所未有最高规格的对待。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81478] = { id = 81478, Voice = '', Text = '亚苏哈卡的恶作剧念头又开始活跃，他利用那些“光点”令自己的声音放大无数倍。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81477] = { id = 81477, Voice = '', Text = '光点所造成的破坏力让亚苏哈卡·剑齿大为满意，亚苏哈卡伸了伸懒腰，却看到山丘下跪伏一片的犬妖们。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81476] = { id = 81476, Voice = '', Text = '光点渐渐密集，亚苏哈卡这一刻在犬妖们眼里宛若神明，就连犬妖首领都无法理解亚苏哈卡身上发生的一切。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81475] = { id = 81475, Voice = '', Text = '终于，他们在山丘上看到了亚苏哈卡，亚苏哈卡正在研究新的“光点”排列方式。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81474] = { id = 81474, Voice = '', Text = '失踪地亚苏哈卡·剑齿还是引起了犬妖们的注意，村落的犬妖们开始寻找起亚苏哈卡。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81473] = { id = 81473, Voice = '', Text = '他第一次清晰地认识到现在的自己掌控着多大的力量。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81472] = { id = 81472, Voice = '', Text = '终于，一个排列完整的“光点”失控飞向远处，巨大轰鸣声吓住了亚苏哈卡，他愣愣的看着之前还绿意盎然的树林，如今却变成一片荒地焦土。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81471] = { id = 81471, Voice = '', Text = '他愉快与这些愉快的光点玩耍，他让这些光点排列成他想要的形状，在过度操纵之后，他的精神逐渐疲劳，对“光点”的掌控也渐渐力不从心。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81470] = { id = 81470, Voice = '', Text = '令他感到奇怪的是，四周有一些五颜六色的光亮围绕着他。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81469] = { id = 81469, Voice = '', Text = '一股暖洋洋的触感从手中传来，他的身体也在悄然发生着变化，亚苏哈卡舒服着闭着眼睛。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81468] = { id = 81468, Voice = '', Text = '当好奇心压倒对未知事物的恐惧，亚苏哈卡迎来了他的新生，他捧着这颗宝石，宝石的光亮愈发闪亮。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81467] = { id = 81467, Voice = '', Text = '当他走到山丘之上，他发现在山丘上出现了一道深坑，他也终于看到了流星的全貌——那是一颗熠熠生辉的宝石。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81466] = { id = 81466, Voice = '', Text = '那接下来，我就继续告诉你，那道光芒是什么。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81465] = { id = 81465, Voice = '', Text = '咦？这点[c][ffff00]暖心料理[-][/c]可不够呢…', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81464] = { id = 81464, Voice = '', Text = '这么快就收集好了！冒险者你真是太棒了！', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81463] = { id = 81463, Voice = '', Text = '这么快就回来了？快让我看看料理吧！', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81462] = { id = 81462, Voice = '', Text = '你也想知道那个光亮是什么吗？想知道的话，就帮我买点[c][ffff00]暖心料理[-][/c]吧！', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81461] = { id = 81461, Voice = '', Text = '那颗流星重重地坠落在不远处——那是亚苏哈卡常去玩乐的小山丘，天性充满好奇的亚苏哈卡决定去看看那道光芒究竟是什么。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81460] = { id = 81460, Voice = '', Text = '早已习惯的亚苏哈卡对此轻车熟路，他愉快地于村落外嬉闹，一道巨大而光亮的流星吸引了亚苏哈卡的身心。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81459] = { id = 81459, Voice = '', Text = '在某次亚苏哈卡又一次恶作剧惹怒了犬妖们后，愤怒的犬妖们把亚苏哈卡赶出了村落。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81458] = { id = 81458, Voice = '', Text = '去邻居家的猫窝中，吓唬那些被豢养的野猫，或是去偷取其他犬妖打猎的成果用以果腹，为此，犬妖们对亚苏哈卡很不友好。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81457] = { id = 81457, Voice = '', Text = '亚苏哈卡·剑齿从小就与其他犬妖不同，犬妖们老实忠厚，而他却充满了与众不同的鬼点子。', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
	[81456] = { id = 81456, Voice = '', Text = '今天我们就来讲讲亚苏哈卡·剑齿的故事吧！', Speaker = 1594, Option = '', Action = _EmptyTable,}, 
