	[81059] = { id = 81059, Voice = '', Text = '这次多谢你了，冒险者。这是你应得的奖励。请收好。', Speaker = 2682, Option = '', Action = _EmptyTable,}, 
	[81058] = { id = 81058, Voice = '', Text = '总算完成了这个差事了，我要在这里等待魔法公会的人员来接我。', Speaker = 2682, Option = '', Action = _EmptyTable,}, 
	[81057] = { id = 81057, Voice = '', Text = '现在说这些已经没用了，你还是老老实实地让我封印能力吧。', Speaker = 2682, Option = '', Action = _EmptyTable,}, 
	[81056] = { id = 81056, Voice = '', Text = '如果有机会，我肯定不会再这样做了…', Speaker = 2683, Option = '', Action = _EmptyTable,}, 
	[81055] = { id = 81055, Voice = '', Text = '看来只能跟他们走一趟了…可惜了我花费的那么多的钱…', Speaker = 2683, Option = '', Action = _EmptyTable,}, 
	[81054] = { id = 81054, Voice = '', Text = '原来是这样…但是我自己也不清楚为什么会发生这种事情。', Speaker = 2683, Option = '', Action = _EmptyTable,}, 
	[81053] = { id = 81053, Voice = '', Text = '冒险者？？你和这个魔法师准备对我做什么？', Speaker = 2683, Option = '述说危害', Action = _EmptyTable,}, 
	[81052] = { id = 81052, Voice = '', Text = '嘿，我看他的样子还有些不服气，冒险者，去听听他说了什么，我现在要准备魔法封印了，可没空去管他！', Speaker = 2682, Option = '', Action = _EmptyTable,}, 
	[81051] = { id = 81051, Voice = '', Text = '所以这一次，我们必须抓住他，不能再让他为所欲为了！', Speaker = 2682, Option = '', Action = _EmptyTable,}, 
	[81050] = { id = 81050, Voice = '', Text = '[c][ffff00]大陆上的冒险者能力忽强忽弱的罪魁祸首不是别人，就是他！[-][/c]', Speaker = 2682, Option = '', Action = _EmptyTable,}, 
	[81049] = { id = 81049, Voice = '', Text = '当这一次传送到我们所在的位面时，发生了超大的魔法震荡，这个世界的所有人多多少少能力都发生了很奇怪的变化。', Speaker = 2682, Option = '', Action = _EmptyTable,}, 
	[81048] = { id = 81048, Voice = '', Text = '在进行位面传送的时候，冒险者与魔法师之间会产生非常精密的联系，而这一次发生了更可怕的事。', Speaker = 2682, Option = '', Action = _EmptyTable,}, 
	[81047] = { id = 81047, Voice = '', Text = '这点数量不太够呢，你还是再找一些神之手来吧。', Speaker = 2682, Option = '', Action = _EmptyTable,}, 
	[81046] = { id = 81046, Voice = '', Text = '太棒了冒险者，有了这些材料，我就能顺利的限制他的能力了！', Speaker = 2682, Option = '', Action = _EmptyTable,}, 
	[81045] = { id = 81045, Voice = '', Text = '已经收集好了么？你愿意现在就把30个神之手交给我吗？', Speaker = 2682, Option = '{愿意,5},{不愿意,4}', Action = _EmptyTable,}, 
	[81044] = { id = 81044, Voice = '', Text = '你能够帮我从周围的绿贝勒斯和毕帝特飞龙身上收集一些吗？我相信，充满正义感的冒险者一定不会让他为所欲为的。', Speaker = 2682, Option = '{交给我,4},{不愿意,3}', Action = _EmptyTable,}, 
	[81043] = { id = 81043, Voice = '', Text = '所以我必须限制住他的能力，只是我现在的魔力很不稳定，需要大量神之手来稳固我的魔力，这样我才能更好的限制住他。', Speaker = 2682, Option = '', Action = _EmptyTable,}, 
	[81042] = { id = 81042, Voice = '', Text = '好不容易抓住了他，我得时刻盯着他才行。否则一不小心他又会逃跑的。', Speaker = 2682, Option = '', Action = _EmptyTable,}, 
	[81041] = { id = 81041, Voice = '', Text = '正因为如此，有些位面魔法师会突然获得魔法能力的大幅提升，有些则突然魔力丧失。', Speaker = 2682, Option = '这么危险', Action = _EmptyTable,}, 
	[81040] = { id = 81040, Voice = '', Text = '而就从目前来看，他的每一次行动都会导致我们的魔法师体内的魔力出现大幅度的动荡。', Speaker = 2682, Option = '', Action = _EmptyTable,}, 
	[81039] = { id = 81039, Voice = '', Text = '由于我们目前的研究还没有完全的了解清楚时空与位面的秘密，我们无法预估他成功之后的后果。', Speaker = 2682, Option = '', Action = _EmptyTable,}, 
	[81038] = { id = 81038, Voice = '', Text = '每次在进行位面传送时，他不断在途中尝试使用隐匿来让自己停留在位面与位面之间。', Speaker = 2682, Option = '', Action = _EmptyTable,}, 
	[81037] = { id = 81037, Voice = '', Text = '你别看他现在可怜兮兮的样子，其实这个刺客是个隐匿高手，而他在尝试一个很可怕的事情。', Speaker = 2682, Option = '', Action = _EmptyTable,}, 
	[81036] = { id = 81036, Voice = '', Text = '是的，正如你所见，是我袭击了他。', Speaker = 2682, Option = '为什么？', Action = _EmptyTable,}, 
	[81035] = { id = 81035, Voice = '', Text = '你的同情心在哪里！？', Speaker = 2683, Option = '', Action = _EmptyTable,}, 
	[81034] = { id = 81034, Voice = '', Text = '冒险者，你愿意帮我赶走那些可恶的魔法师吗？', Speaker = 2683, Option = '{我愿意,3},{不愿意,2}', Action = _EmptyTable,}, 
	[81033] = { id = 81033, Voice = '', Text = '可是突然就有一些时空法师，让我回到我原本的位面，当然，我可是花费了相当高昂的旅行费啊，怎么可能说走就走呢？', Speaker = 2683, Option = '', Action = _EmptyTable,}, 
	[81032] = { id = 81032, Voice = '', Text = '我本想进行一场轰轰烈烈的时空旅行，那就是去到无数个不同的位面，来发现这些世界中到底有着什么区别。', Speaker = 2683, Option = '', Action = _EmptyTable,}, 
	[81031] = { id = 81031, Voice = '', Text = '冒险者！好可怕，我被袭击了！', Speaker = 2683, Option = '怎么回事', Action = _EmptyTable,}, 
	[81030] = { id = 81030, Voice = '', Text = '你一会儿去河边找我吧。我要好好谢谢你。', Speaker = 2668, Option = '', Action = _EmptyTable,}, 
	[81029] = { id = 81029, Voice = '', Text = '冒险者，我先去安葬我的父亲了…', Speaker = 2668, Option = '', Action = _EmptyTable,}, 
	[81028] = { id = 81028, Voice = '', Text = '我不能让我的父亲就这么睡在冰冷的祭坛上。', Speaker = 2668, Option = '', Action = _EmptyTable,}, 
	[81020] = { id = 81020, Voice = '', Text = '不管怎么样，研究总算有所突破了，多谢你的帮助，冒险者。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[81019] = { id = 81019, Voice = '', Text = '很可怕的魔力，我想这股魔力，应该与古城内部的那股令人不安的力量有所联系。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[81018] = { id = 81018, Voice = '', Text = '他们的角中的那股魔力才是真正的罪魁祸首，甚至于神之手也是在这种魔力的作用下才出现在魔物身上的。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[81017] = { id = 81017, Voice = '', Text = '在研究了这些魔物的角之后，我可以很清楚的认识到，不是因为神之手魔物才狂暴的。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[81016] = { id = 81016, Voice = '', Text = '但是实际上，在毕帝特地龙身上，我并没有发现神之手，这使得我推翻了之前的推断。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[81015] = { id = 81015, Voice = '', Text = '果然不出我的意料，曾经的我一直以为，在神之手这种材料的作用下，这些魔物才会变得狂暴不已。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[81014] = { id = 81014, Voice = '', Text = '让我好好研究一番……', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[81013] = { id = 81013, Voice = '', Text = '你回来了，看你的样子应该成功的拿到角了吧。快给我吧。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[81012] = { id = 81012, Voice = '', Text = '吼——', Speaker = 2684, Option = '', Action = _EmptyTable,}, 
	[81011] = { id = 81011, Voice = '', Text = '它的角上恐怕拥有着更加强大的魔力！冒险者，我需要它的角来继续我的研究。请帮我收集过来吧。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[81010] = { id = 81010, Voice = '', Text = '毫无疑问，它的强大就像龙族的王者一样，起码我没有见过其他的龙族像它一样强大。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[81009] = { id = 81009, Voice = '', Text = '但是在毕帝特龙族中，还有一个更加强大的存在，我称它为毕帝特地龙王。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[81008] = { id = 81008, Voice = '', Text = '这些魔力都聚集在这些角上，我想这就是为什么这里的魔物变狂暴且更加具有攻击性了。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[81007] = { id = 81007, Voice = '', Text = '这种魔力对我们人族没有任何影响，但是对这些魔物却影响巨大。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[81006] = { id = 81006, Voice = '', Text = '经过我的研究，我发现这些角里面存在这一种纯粹的魔力。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[81005] = { id = 81005, Voice = '', Text = '我的天！我想我从未见过像你这样高效率的冒险者！太感谢你了。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[81004] = { id = 81004, Voice = '', Text = '我需要绿贝勒斯的角来进行我的下一步研究，冒险者你帮我收集一些来吧。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[81003] = { id = 81003, Voice = '', Text = '我想绿贝勒斯们也是靠着这么强大的角才能在毕帝特龙族手下挣得生存的空间。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[81002] = { id = 81002, Voice = '', Text = '绿贝勒斯的角是最不寻常的，他们的角显得更加强大而富有攻击性。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[81001] = { id = 81001, Voice = '', Text = '干得非常不错，冒险者，你的帮助太重要了！', Speaker = 2675, Option = '', Action = _EmptyTable,}, 
	[81000] = { id = 81000, Voice = '', Text = '可见飞龙的角与地龙的角是有所不同的，所以冒险者，我需要你收集一些飞龙角，方便我进行研究。', Speaker = 2675, Option = '', Action = _EmptyTable,}, 
	[80999] = { id = 80999, Voice = '', Text = '因为根据我的观察，他们的角就算受伤也会很快的恢复。', Speaker = 2675, Option = '', Action = _EmptyTable,}, 
	[80998] = { id = 80998, Voice = '', Text = '比起毕帝特地龙，毕帝特飞龙有些不同，他们并不在意自己的角是否受伤。', Speaker = 2675, Option = '', Action = _EmptyTable,}, 
	[80997] = { id = 80997, Voice = '', Text = '竟然这么快就回来了！太棒了冒险者！', Speaker = 2675, Option = '', Action = _EmptyTable,}, 
	[80996] = { id = 80996, Voice = '', Text = '所以我需要你收集一些毕帝特地龙角给我，我可以仔细的研究一下地龙角的成分，以便做出正确的判断。', Speaker = 2675, Option = '', Action = _EmptyTable,}, 
	[80995] = { id = 80995, Voice = '', Text = '我觉得这种现象表示龙角在他们心中的分量相当重要。', Speaker = 2675, Option = '', Action = _EmptyTable,}, 
	[80994] = { id = 80994, Voice = '', Text = '根据我的观察，毕帝特地龙在战斗的时候总是尽量避免自己的角受伤。', Speaker = 2675, Option = '', Action = _EmptyTable,}, 
	[80993] = { id = 80993, Voice = '', Text = '我和我的护卫贝妮会给你一些信息，我们很真诚的希望你能够加入我们的研究。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[80992] = { id = 80992, Voice = '', Text = '而是因为其他的原因，根据我的猜测，恐怕与这些魔物身上的角有关系。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[80991] = { id = 80991, Voice = '', Text = '根据我的研究发现，影响毕帝特龙族和绿贝勒斯的原因，并不是因为他们身体里的那些神之手。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[80990] = { id = 80990, Voice = '', Text = '我就知道你一定不会袖手旁观的！', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[80989] = { id = 80989, Voice = '', Text = '所以冒险者，我需要你的帮助。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[80988] = { id = 80988, Voice = '', Text = '为了来往行人的安全，我一定会把这里的魔物狂暴原因研究透彻的。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[80987] = { id = 80987, Voice = '', Text = '从这些魔物身上，我获得不少资料，现在已经到了验证成果的时候了。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[80985] = { id = 80985, Voice = '', Text = '不过为了尘世大陆，为了国王，也为了王国的民众们，再苦再累我们也会坚持下去的。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[80984] = { id = 80984, Voice = '', Text = '我们魔物学者要忍受魔物的侵扰以及长时间的风餐露宿，非常的不容易呢。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[80983] = { id = 80983, Voice = '', Text = '我的同僚在犬妖森林的魔物研究上取得了重大的突破。而我作为魔物研究会的一员，也应该加把劲儿！', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[80982] = { id = 80982, Voice = '', Text = '我们魔物研究会是为了有效的解决层出不穷的魔物所衍生出来的问题而存在的。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[80981] = { id = 80981, Voice = '', Text = '他们的狂暴化对来往的人们构成严重的威胁，为了行人们的安全，国王陛下派遣我们来到这里研究魔物，希望有一天能够找出他们的弱点。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[80980] = { id = 80980, Voice = '', Text = '这片生机盎然的土地最终演变成了一块危机四伏的土地，过往的行人们对这里畏惧不已。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[80979] = { id = 80979, Voice = '', Text = '而现在古城荒废之后，毕帝特龙族以及绿贝勒斯在某种原因的影响下逐渐变的狂暴。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[80978] = { id = 80978, Voice = '', Text = '在很久之前，毕帝特龙族以及绿贝勒斯是非常和善的魔物，他们与古城的人们和谐相处。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[80977] = { id = 80977, Voice = '', Text = '所以我们魔物研究会派遣我来这里研究这里的魔物，以及这些魔物带来的影响。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[80976] = { id = 80976, Voice = '', Text = '它们袭击着商队以及前来冒险的人们，造成了很多人受伤甚至死亡。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[80975] = { id = 80975, Voice = '', Text = '生活在城外原本和善的毕帝特龙族也逐渐变成了凶恶的魔物。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[80974] = { id = 80974, Voice = '', Text = '克雷斯特汉姆的城外曾是一片生机盎然的土地，而在克雷斯特汉姆荒废以后，这片土地也渐渐的失去了生机。', Speaker = 2674, Option = '', Action = _EmptyTable,}, 
	[80795] = { id = 80795, Voice = '', Text = '我还要去感谢所有帮助过我的人，亲爱的冒险者，再次感谢你。再见。', Speaker = 2661, Option = '再见', Action = _EmptyTable,}, 
	[80794] = { id = 80794, Voice = '', Text = '太感谢你了冒险者！你不但救了我的命，还治好了我女儿的病！我一定要好好谢谢你，这是我的一点心意，请你务必收下！', Speaker = 2661, Option = '', Action = _EmptyTable,}, 
	[80793] = { id = 80793, Voice = '', Text = '爸爸回来了！谢谢你冒险者，最喜欢你了！', Speaker = 2659, Option = '乖，喝药', Action = _EmptyTable,}, 
	[80792] = { id = 80792, Voice = '', Text = '还好我有先见之明，才能这么快的制作完成，你只需要把药剂给小诺娃喝下就可以了。', Speaker = 2535, Option = '好的。', Action = _EmptyTable,}, 
	[80791] = { id = 80791, Voice = '', Text = '稍等片刻，我现在就开始制作。', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[80790] = { id = 80790, Voice = '', Text = '你之前收集的材料可就派上用场咯！', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[80789] = { id = 80789, Voice = '', Text = '看来你已经把永生花带回来了，给我吧。', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[80788] = { id = 80788, Voice = '', Text = '你既然拿到了永生花，就去找医者诺雅，把药做好带回吉芬吧。', Speaker = 2660, Option = '好的', Action = _EmptyTable,}, 
	[80787] = { id = 80787, Voice = '', Text = '接下来，我把他的父亲送回吉芬，他似乎中了梦魇王的诅咒，我想请雷藏大人帮忙看看。', Speaker = 2660, Option = '', Action = _EmptyTable,}, 
	[80786] = { id = 80786, Voice = '', Text = '你消灭了梦魇王，还取到了永生花？太棒了！冒险者！', Speaker = 2660, Option = '', Action = _EmptyTable,}, 
	[80785] = { id = 80785, Voice = '', Text = '终于消灭了…接下来去采集永生花吧！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[80784] = { id = 80784, Voice = '', Text = '逃不掉了！只能杀死它了！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[80783] = { id = 80783, Voice = '', Text = '梦魇王似乎发怒了…不好！它挣脱封印了！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[80782] = { id = 80782, Voice = '', Text = '就是现在！冒险者，我带走他了！你自己小心！', Speaker = 2660, Option = '', Action = _EmptyTable,}, 
	[80781] = { id = 80781, Voice = '', Text = '真的是梦魇王…悄悄地靠近！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[80780] = { id = 80780, Voice = '', Text = '那就这么说定了！你现在就去封印它吧，我准备一下，随后就会赶到！', Speaker = 2660, Option = '', Action = _EmptyTable,}, 
	[80779] = { id = 80779, Voice = '', Text = '你觉得我的计划怎么样？', Speaker = 2660, Option = '可以。', Action = _EmptyTable,}, 
	[80778] = { id = 80778, Voice = '', Text = '一会你去封印住梦魇王，之后，我偷偷地去绕到梦魇王背后，趁他不注意，带走小诺娃的父亲！', Speaker = 2660, Option = '', Action = _EmptyTable,}, 
	[80777] = { id = 80777, Voice = '', Text = '我还看到了一个人倒在地上，看来那就是小诺娃的父亲了。', Speaker = 2660, Option = '', Action = _EmptyTable,}, 
	[80776] = { id = 80776, Voice = '', Text = '太好了。我刚刚已经去过了那里。那里果然有一个奇特的魔物在看守着。', Speaker = 2660, Option = '', Action = _EmptyTable,}, 
	[80775] = { id = 80775, Voice = '', Text = '拿到卷轴了？', Speaker = 2660, Option = '拿到了', Action = _EmptyTable,}, 
	[80774] = { id = 80774, Voice = '', Text = '制作好了冒险者。你现在可以拿去找梦魇王了。我在吉芬等你的消息！', Speaker = 1321, Option = '收下卷轴', Action = _EmptyTable,}, 
	[80773] = { id = 80773, Voice = '', Text = '东西到齐了，事不宜迟，接下来，我就要制作封印卷轴了。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[80772] = { id = 80772, Voice = '', Text = '这澎湃的魔力…干得不错冒险者！', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[80771] = { id = 80771, Voice = '', Text = '我需要一些泉水，请帮我打一点过来吧！', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[80770] = { id = 80770, Voice = '', Text = '吉芬里有一座魔力喷泉，在玛娜神力的影响下，泉水有着不可思议的力量。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[80769] = { id = 80769, Voice = '', Text = '好了，这些犬妖之牙足够完成刻画了。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[80768] = { id = 80768, Voice = '', Text = '你去犬妖森林的犬妖弓箭手身上收集一些犬妖之牙来吧。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[80767] = { id = 80767, Voice = '', Text = '犬妖弓箭手身上的犬妖之牙拥有优秀的魔导能力，是不可多得的魔法材料。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[80766] = { id = 80766, Voice = '', Text = '干得不错冒险者，效率很高嘛！', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[80765] = { id = 80765, Voice = '', Text = '用它来作为封印的底料最合适不过了！你去收集一些来吧！', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[80764] = { id = 80764, Voice = '', Text = '犬妖森林的狂暴野猫的皮毛拥有良好的魔导性。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[80763] = { id = 80763, Voice = '', Text = '这样吧，你帮我收集三件材料，我来帮你制作封印卷轴。', Speaker = 1321, Option = '好的', Action = _EmptyTable,}, 
	[80762] = { id = 80762, Voice = '', Text = '所以我们只能在梦魇王诅咒之前封印它的力量。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[80761] = { id = 80761, Voice = '', Text = '对付梦魇王最重要的就是梦魇王的诅咒，一旦中了诅咒，你有什么手段都难以施展。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[80760] = { id = 80760, Voice = '', Text = '梦魇王？这种魔物倒是少见，不过，曾经遇到过，那时候还是瑟西老师带我前去遗迹试炼的时候…', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[80759] = { id = 80759, Voice = '', Text = '雷藏大人见多识广，相信雷藏大人会给你一个满意的消息的。', Speaker = 2660, Option = '', Action = _EmptyTable,}, 
	[80758] = { id = 80758, Voice = '', Text = '我从来没有见过这种魔物，恐怕你还得回去询问一下雷藏大人。', Speaker = 2660, Option = '', Action = _EmptyTable,}, 
	[80757] = { id = 80757, Voice = '', Text = '但是我们现在要提前做好准备，我们要想办法对付梦魇王。', Speaker = 2660, Option = '', Action = _EmptyTable,}, 
	[80756] = { id = 80756, Voice = '', Text = '原来是这样，辛苦你了冒险者。关于永生花生长的地方，我已经有了一个比较符合的地点了。', Speaker = 2660, Option = '', Action = _EmptyTable,}, 
	[80755] = { id = 80755, Voice = '', Text = '现在你可以把消息带回去了，找到永生花之后再来找我。我来帮你配置药物！', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[80754] = { id = 80754, Voice = '', Text = '她的父亲才失踪两天，恐怕他现在才刚刚进入梦魇状态。我们的时间是足够的。', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[80753] = { id = 80753, Voice = '', Text = '至于她父亲…梦魇王对觊觎永生花的人采取的措施一般是让他在永恒的梦魇中痛苦的死去。', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[80752] = { id = 80752, Voice = '', Text = '不然永生花直接喂服恐怕小诺娃会直接死亡。', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[80751] = { id = 80751, Voice = '', Text = '我之所以让你帮我采集那些物品，是因为病人是一个小女孩。我需要这些物品中和永生花强大的药性。', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[80750] = { id = 80750, Voice = '', Text = '越是年份长久的永生花，它身边的梦魇王就越加强大。', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[80749] = { id = 80749, Voice = '', Text = '怎么样才能让永生花的诅咒消除呢？第三个条件，永生花的周围有梦魇王的守护。梦魇王会汲取永生花的诅咒生存。', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[80748] = { id = 80748, Voice = '', Text = '生长在这样的土壤里的永生花势必会附带诅咒，因为上天是公平的，永生花的具有强大药效的同时，身上也带着无法驱逐的诅咒！', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[80747] = { id = 80747, Voice = '', Text = '其次，它所生长的土壤必须是受到诅咒的土壤。', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[80746] = { id = 80746, Voice = '', Text = '但是永生花的生长条件极其苛刻。首先，它要生长在纯净的泉水边，方便汲取泉水。', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[80745] = { id = 80745, Voice = '', Text = '永生花毫无疑问，是药材中最上级的药材之一，它所拥有的药性甚至可以让人延长寿命！', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[80744] = { id = 80744, Voice = '', Text = '既然东西都拿到了，那么我就告诉你永生花的一些消息吧。', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[80743] = { id = 80743, Voice = '', Text = '干得很好冒险者，这些血液足够了。', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[80742] = { id = 80742, Voice = '', Text = '从那些堕落的大斧犬妖身上收集一些诅咒之血给我吧。', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[80741] = { id = 80741, Voice = '', Text = '虽然我身处斐扬南部，但是我也知道克特森林受到诅咒侵袭的事情。', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[80740] = { id = 80740, Voice = '', Text = '谢谢你冒险者，你完成得很棒！', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[80739] = { id = 80739, Voice = '', Text = '狂暴野猫最喜欢这种受到诅咒的荆棘了。你从狂暴野猫身上也许就能得到。', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[80738] = { id = 80738, Voice = '', Text = '腐朽荆棘是克特森林的特产，它通常出现在狂暴野猫的聚集地。', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[80737] = { id = 80737, Voice = '', Text = '为了不浪费时间，我就直说了，我需要你帮我收集一些材料。', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[80736] = { id = 80736, Voice = '', Text = '放心，小诺娃的父亲应该不会有事情。而且你迟早会为我办这两件事的。', Speaker = 2535, Option = '好吧。', Action = _EmptyTable,}, 
	[80735] = { id = 80735, Voice = '', Text = '原来是这样，我理解你的急切心情。我可以告诉你，但是你得帮助我做两件事情。', Speaker = 2535, Option = '我还要找人', Action = _EmptyTable,}, 
	[80734] = { id = 80734, Voice = '', Text = '永生花？你怎么会问起这种药材的信息。', Speaker = 2535, Option = '述说经过', Action = _EmptyTable,}, 
	[80733] = { id = 80733, Voice = '', Text = '冒险者，永生花的信息就交给你了，我在这里等你的好消息！', Speaker = 2660, Option = '', Action = _EmptyTable,}, 
	[80732] = { id = 80732, Voice = '', Text = '诺雅！她就算是在吉芬也是赫赫有名呢！如果能得到她的帮助那真是太好了。', Speaker = 2660, Option = '', Action = _EmptyTable,}, 
	[80731] = { id = 80731, Voice = '', Text = '可是我并不认识药剂大师，冒险者你有什么好主意吗？', Speaker = 2660, Option = '医者诺雅', Action = _EmptyTable,}, 
	[80730] = { id = 80730, Voice = '', Text = '如果能知道永生花生长在那些地方的话，我们寻找起来就会更加容易。', Speaker = 2660, Option = '', Action = _EmptyTable,}, 
	[80729] = { id = 80729, Voice = '', Text = '不过有你来帮助我就太好了！我们现在对诺娃父亲要找的永生花一无所知。', Speaker = 2660, Option = '', Action = _EmptyTable,}, 
	[80728] = { id = 80728, Voice = '', Text = '我需要一边寻找诺娃父亲的踪迹，一边提防犬妖和野猫的袭击。', Speaker = 2660, Option = '', Action = _EmptyTable,}, 
	[80727] = { id = 80727, Voice = '', Text = '这可真是个苦差事啊！克特森林这么大，更何况这里危险至极。', Speaker = 2660, Option = '', Action = _EmptyTable,}, 
	[80726] = { id = 80726, Voice = '', Text = '雷藏大人让你来找我的吗？', Speaker = 2660, Option = '是的', Action = _EmptyTable,}, 
	[80725] = { id = 80725, Voice = '', Text = '那位魔法师叫做柯瑞。冒险者你可以去克特森林帮帮他一起寻找。', Speaker = 1321, Option = '{不感兴趣,3},{义不容辞,4}', Action = _EmptyTable,}, 
	[80724] = { id = 80724, Voice = '', Text = '要知道，克特森林可是很大的，他一个人恐怕很难寻找到小诺娃的父亲。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[80723] = { id = 80723, Voice = '', Text = '那位魔法师昨天还传了一封魔法信件给我，说是还在搜寻当中。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[80722] = { id = 80722, Voice = '', Text = '我的确派遣了一位魔法师进入克特森林寻找小诺娃的父亲。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[80721] = { id = 80721, Voice = '', Text = '也不知道有没有消息传过来，不如你去问一问雷藏大人吧。', Speaker = 1340, Option = '', Action = _EmptyTable,}, 
	[80720] = { id = 80720, Voice = '', Text = '为了小诺娃的事情，雷藏大人曾经派遣一位魔法师进入克特森林。', Speaker = 1340, Option = '', Action = _EmptyTable,}, 
	[80719] = { id = 80719, Voice = '', Text = '现在的克特森林充斥着狂暴野猫和发疯的犬妖，这时候进去找药材，恐怕小诺娃的父亲已经…唉！', Speaker = 1340, Option = '', Action = _EmptyTable,}, 
	[80718] = { id = 80718, Voice = '', Text = '我听说克特森林在诅咒的侵蚀下，早就不是原来的克特森林了。', Speaker = 1340, Option = '', Action = _EmptyTable,}, 
	[80717] = { id = 80717, Voice = '', Text = '你说小诺娃啊！小诺娃真的挺可怜的。', Speaker = 1340, Option = '', Action = _EmptyTable,}, 
	[80716] = { id = 80716, Voice = '', Text = '可是爸爸到现在还没有回来…爸爸不要诺娃了…诺娃是一个没人要的小孩了…', Speaker = 2659, Option = '去找庞德问问', Action = _EmptyTable,}, 
	[80715] = { id = 80715, Voice = '', Text = '庞德叔叔把我接到学院里，让我安心等待爸爸回来…', Speaker = 2659, Option = '', Action = _EmptyTable,}, 
	[80714] = { id = 80714, Voice = '', Text = '爸爸曾经说他太阳下山之前一定会回来的，可是小诺娃已经在这里等了两天了。', Speaker = 2659, Option = '', Action = _EmptyTable,}, 
	[80713] = { id = 80713, Voice = '', Text = '爸爸为了给小诺娃治病，去克特森林里寻找一种叫做“永生花”的稀有药材。', Speaker = 2659, Option = '', Action = _EmptyTable,}, 
	[80712] = { id = 80712, Voice = '', Text = '爸爸不要诺娃了……谁都不要诺娃了……', Speaker = 2659, Option = '发生了什么', Action = _EmptyTable,}, 
	[80711] = { id = 80711, Voice = '', Text = '呜呜呜呜……', Speaker = 2659, Option = '怎么了小妹妹', Action = _EmptyTable,}, 
	[80710] = { id = 80710, Voice = '', Text = '一点小小的心意，请收下吧。', Speaker = 2668, Option = '', Action = _EmptyTable,}, 
	[80709] = { id = 80709, Voice = '', Text = '他走的时候很安详，这还要谢谢你，冒险者。如果不是你，我和我的父亲恐怕永远不能再见了。', Speaker = 2668, Option = '', Action = _EmptyTable,}, 
	[80708] = { id = 80708, Voice = '', Text = '我父亲这么多年来一直拼命压制诅咒，生命已经走到了尽头。', Speaker = 2668, Option = '', Action = _EmptyTable,}, 
	[80705] = { id = 80705, Voice = '', Text = '基…基恩…快…快走！我…控制…不住…诅咒的力量了！走！', Speaker = 2672, Option = '', Action = _EmptyTable,}, 
	[80704] = { id = 80704, Voice = '', Text = '父亲！', Speaker = 2668, Option = '', Action = _EmptyTable,}, 
	[80703] = { id = 80703, Voice = '', Text = '铃…铃声…在哪儿…', Speaker = 2672, Option = '', Action = _EmptyTable,}, 
	[80702] = { id = 80702, Voice = '', Text = '我来放上铜铃，你来引动魔法阵。', Speaker = 2668, Option = '', Action = _EmptyTable,}, 
	[80700] = { id = 80700, Voice = '', Text = '冒险者，你先去摆放魔法阵，我跟看守者大人说一下我们的行动，看守者大人一定不会阻止我们的。', Speaker = 2668, Option = '', Action = _EmptyTable,}, 
	[80699] = { id = 80699, Voice = '', Text = '我想想…放在哪里合适呢…要不就放在祭坛吧！哪里地势比较高，声音应该能传的更远。', Speaker = 2668, Option = '', Action = _EmptyTable,}, 
	[80698] = { id = 80698, Voice = '', Text = '我现在就要找地方使用这个魔法阵，如果我父亲还活着，一定会回来找我的！', Speaker = 2668, Option = '', Action = _EmptyTable,}, 
	[80697] = { id = 80697, Voice = '', Text = '这就是可以扩音的魔法阵？太谢谢你了，冒险者。', Speaker = 2668, Option = '', Action = _EmptyTable,}, 
	[80696] = { id = 80696, Voice = '', Text = '喏，拿去吧，这就是那个扩音魔法阵，使用的时候小心一点，你可不要被震坏了耳朵！', Speaker = 3371, Option = '收下魔法阵', Action = _EmptyTable,}, 
	[80695] = { id = 80695, Voice = '', Text = '东西都齐了，那么我可以开始刻画了，稍等片刻，冒险者。', Speaker = 3371, Option = '', Action = _EmptyTable,}, 
	[80694] = { id = 80694, Voice = '', Text = '这点慧眼恐怕不足以支撑我画完整个魔法阵，你还是再找一些来吧。', Speaker = 3371, Option = '', Action = _EmptyTable,}, 
	[80693] = { id = 80693, Voice = '', Text = '数量都足够了，辛苦了冒险者。', Speaker = 3371, Option = '', Action = _EmptyTable,}, 
	[80692] = { id = 80692, Voice = '', Text = '看来你已经收集到足够的慧眼了，给我看看吧。', Speaker = 3371, Option = '', Action = _EmptyTable,}, 
	[80691] = { id = 80691, Voice = '', Text = '周围的大斧犬妖身上有慧眼，你可以从他们那里得到，当然，你有钱的话，也可以去拍卖行买。', Speaker = 3371, Option = '', Action = _EmptyTable,}, 
	[80690] = { id = 80690, Voice = '', Text = '足够了足够了，辛苦你了冒险者。', Speaker = 3371, Option = '', Action = _EmptyTable,}, 
	[80689] = { id = 80689, Voice = '', Text = '在克特之森的犬妖遗迹边，有一潭清澈的泉水，你可以去那里弄一些来。', Speaker = 3371, Option = '', Action = _EmptyTable,}, 
	[80688] = { id = 80688, Voice = '', Text = '干的不错冒险者，这些血液足够使用了。', Speaker = 3371, Option = '', Action = _EmptyTable,}, 
	[80687] = { id = 80687, Voice = '', Text = '狂暴野猫之血，顾名思义，你可以从野猫身上得到，所以快行动吧！', Speaker = 3371, Option = '', Action = _EmptyTable,}, 
	[80686] = { id = 80686, Voice = '', Text = '听好了，我需要的材料分别是狂暴野猫之血、清澈的泉水和提供魔力的慧眼。', Speaker = 3371, Option = '', Action = _EmptyTable,}, 
	[80685] = { id = 80685, Voice = '', Text = '材料倒是不少见，就是需要一点时间收集而已。', Speaker = 3371, Option = '', Action = _EmptyTable,}, 
	[80684] = { id = 80684, Voice = '', Text = '只是我身边没有这些材料…恐怕没有办法制作魔法阵给你。', Speaker = 3371, Option = '什么材料', Action = _EmptyTable,}, 
	[80683] = { id = 80683, Voice = '', Text = '能够放大声音的魔法阵？这种初级的魔法阵怎么会难倒我？', Speaker = 3371, Option = '', Action = _EmptyTable,}, 
	[80682] = { id = 80682, Voice = '', Text = '冒险者，我听说吉芬有种魔法阵能够帮我实现这个愿望，你能帮我弄到吗？', Speaker = 2668, Option = '我试试吧', Action = _EmptyTable,}, 
	[80681] = { id = 80681, Voice = '', Text = '但是，我知道我的父亲一定在森林的某处，如果…我能把我的叮当的声音传遍森林，我父亲有意识的话，一定会来找我的！', Speaker = 2668, Option = '', Action = _EmptyTable,}, 
	[80680] = { id = 80680, Voice = '', Text = '父亲离开之后，没过几天，森林中诅咒的浓度果然减少了。父亲再也没有回来。', Speaker = 2668, Option = '', Action = _EmptyTable,}, 
	[80679] = { id = 80679, Voice = '', Text = '吞噬只能缓解诅咒的蔓延，却不能根治，父亲临走前希望首领能够找到解决的办法，因为他能做的，只有这么多了。', Speaker = 2668, Option = '', Action = _EmptyTable,}, 
	[80678] = { id = 80678, Voice = '', Text = '所以我的父亲选择用先知自古留下的魔法，吞噬这无尽的诅咒。', Speaker = 2668, Option = '', Action = _EmptyTable,}, 
	[80677] = { id = 80677, Voice = '', Text = '但是也是族群中数一数二的强者，但是在诅咒的侵蚀下，我的父亲用尽全力，也不能拯救整个族群。', Speaker = 2668, Option = '', Action = _EmptyTable,}, 
	[80676] = { id = 80676, Voice = '', Text = '第九先知，是最后一任先知，我父亲不如前八位先知那么强大。', Speaker = 2668, Option = '', Action = _EmptyTable,}, 
	[80675] = { id = 80675, Voice = '', Text = '直到某天，我用力挥动铃铛，父亲却再也没有回到我身边。', Speaker = 2668, Option = '发生了什么', Action = _EmptyTable,}, 
	[80674] = { id = 80674, Voice = '', Text = '父亲赠送我这个铜铃，只要我用力一挥动，当铃铛发出叮叮当当的声响后，我父亲很快就会出现在我面前。', Speaker = 2668, Option = '', Action = _EmptyTable,}, 
	[80673] = { id = 80673, Voice = '', Text = '我的父亲是族群中的第九先知，名为巴诺·剑齿！', Speaker = 2668, Option = '', Action = _EmptyTable,}, 
	[80672] = { id = 80672, Voice = '', Text = '冒险者，愿意听听我跟父亲的事情吗？事情过去很久了，总想找个人倾诉一番。', Speaker = 2668, Option = '{不感兴趣,1},{愿闻其详,2}', Action = _EmptyTable,}, 
	[80671] = { id = 80671, Voice = '', Text = '太棒了，这就是我父亲送给我的铜铃，谢谢你冒险者。', Speaker = 2668, Option = '', Action = _EmptyTable,}, 
	[80669] = { id = 80669, Voice = '', Text = '杀…杀光你们！', Speaker = 2671, Option = '', Action = _EmptyTable,}, 
	[80668] = { id = 80668, Voice = '', Text = '找了半天，这里也没有铜铃呢…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[80667] = { id = 80667, Voice = '', Text = '不好，好像被魔物包围了！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[80666] = { id = 80666, Voice = '', Text = '看来这里没有铜铃呢…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[80665] = { id = 80665, Voice = '', Text = '祭坛重地，擅闯者死！', Speaker = 2669, Option = '', Action = _EmptyTable,}, 
	[80664] = { id = 80664, Voice = '', Text = '冒险者你能帮我去这三个地方找一找吗？', Speaker = 2668, Option = '', Action = _EmptyTable,}, 
	[80663] = { id = 80663, Voice = '', Text = '也许我的铜铃就是那个时候丢失的。', Speaker = 2668, Option = '', Action = _EmptyTable,}, 
	[80662] = { id = 80662, Voice = '', Text = '我曾经去先知陵寝、先知遗迹、以及犬神祭坛拜访三位看守者大人。', Speaker = 2668, Option = '', Action = _EmptyTable,}, 
	[80661] = { id = 80661, Voice = '', Text = '前几天还在的，大概就是这几天把铜铃弄丢了，我要仔细想想我这几天曾经去过的地方。', Speaker = 2668, Option = '', Action = _EmptyTable,}, 
	[80660] = { id = 80660, Voice = '', Text = '这是我父亲留给我的唯一礼物，是当时他去吉芬的时候给我带回来的。虽然不是什么贵重的东西，但是对我来说是无价的。', Speaker = 2668, Option = '', Action = _EmptyTable,}, 
	[80659] = { id = 80659, Voice = '', Text = '我的父亲赠送给我的铜铃不见了，这可怎么办啊…', Speaker = 2668, Option = '别着急', Action = _EmptyTable,}, 
	[80658] = { id = 80658, Voice = '', Text = '在哪儿…在哪儿…铜铃啊铜铃，你到底去哪儿了？', Speaker = 2668, Option = '需要帮忙吗？', Action = _EmptyTable,}, 
	[80657] = { id = 80657, Voice = '', Text = '这也是我出现在这里的职责，你所遇到的事情，我会如实向吉芬方面报告。辛苦你了，冒险者！', Speaker = 2664, Option = '', Action = _EmptyTable,}, 
	[80656] = { id = 80656, Voice = '', Text = '原来是这样…我们吉芬的魔法虽然能够封印住大部分诅咒，但是还有一些诅咒不能净化。', Speaker = 2664, Option = '', Action = _EmptyTable,}, 
	[80655] = { id = 80655, Voice = '', Text = '你回来了？那个令人恐惧的身影究竟是什么？', Speaker = 2664, Option = '讲述经过', Action = _EmptyTable,}, 
	[80654] = { id = 80654, Voice = '', Text = '这次多谢你了，冒险者，否则我还不知道什么时候能够找到他呢！', Speaker = 2665, Option = '不用谢', Action = _EmptyTable,}, 
	[80653] = { id = 80653, Voice = '', Text = '第二先知的身体已经由我的小队接手了，我们小队会负责把身体送回陵寝。', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80652] = { id = 80652, Voice = '', Text = '这…这太不可思议了！我还想着去帮忙呢！', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80651] = { id = 80651, Voice = '', Text = '什么？你已经解决了第二先知？', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80649] = { id = 80649, Voice = '', Text = '果然出现了！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[80648] = { id = 80648, Voice = '', Text = '吼——', Speaker = 2667, Option = '', Action = _EmptyTable,}, 
	[80647] = { id = 80647, Voice = '', Text = '地面的震动越来越强烈了…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[80646] = { id = 80646, Voice = '', Text = '地面开始轻微震动…似乎有什么东西要来了。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[80645] = { id = 80645, Voice = '', Text = '冒险者，你快去放吧，我和我的小队成员会在周围警戒的。', Speaker = 2665, Option = '好的', Action = _EmptyTable,}, 
	[80644] = { id = 80644, Voice = '', Text = '我们就放在中间的广场上吧，那里地域宽敞，万一发生战斗，我们也能迅速分散减小伤亡。', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80643] = { id = 80643, Voice = '', Text = '接下来，我们应该把这些东西放在一个显眼的位置。', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80642] = { id = 80642, Voice = '', Text = '多谢你冒险者，我想我们离成功又接近了一步！', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80641] = { id = 80641, Voice = '', Text = '冒险者，你能收集一些皮毛过来吗？', Speaker = 2665, Option = '好的', Action = _EmptyTable,}, 
	[80640] = { id = 80640, Voice = '', Text = '既然是这样的话，恐怕我们就需要一些狂暴野猫的皮毛了。', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80639] = { id = 80639, Voice = '', Text = '没想到第二先知居然还有这种嗜好…', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80638] = { id = 80638, Voice = '', Text = '如果想引它出来的话，那么野猫的皮毛是不可缺少的。', Speaker = 3375, Option = '谢谢', Action = _EmptyTable,}, 
	[80637] = { id = 80637, Voice = '', Text = '甚至于当时他的房屋里布满了野猫的皮毛制成的物品，被子，枕头…如果一天没有摸到野猫的皮毛。卡隆就会变得无比暴躁。', Speaker = 3375, Option = '', Action = _EmptyTable,}, 
	[80636] = { id = 80636, Voice = '', Text = '卡隆当时是族群中数一数二的战士，不过谁会知道，这位强大的战士会喜欢摩挲野猫的皮毛呢！', Speaker = 3375, Option = '', Action = _EmptyTable,}, 
	[80635] = { id = 80635, Voice = '', Text = '那么你是想找寻第二先知的过往，然后把他引出来对吗？我可以告诉你。', Speaker = 3375, Option = '', Action = _EmptyTable,}, 
	[80634] = { id = 80634, Voice = '', Text = '第二先知？我听说卡隆的安息之所已经被打开了。这是真的吗？', Speaker = 3375, Option = '真的', Action = _EmptyTable,}, 
	[80633] = { id = 80633, Voice = '', Text = '如果你能找到他的话，那么我们一定能够得到关于第二先知的消息！', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80632] = { id = 80632, Voice = '', Text = '格洛·剑齿大人负责我们最为重要的犬神祭坛，他对先知们的事情很是了解。', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80631] = { id = 80631, Voice = '', Text = '干的漂亮冒险者，这些斧子我应该足够了。', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80630] = { id = 80630, Voice = '', Text = '冒险者，你能帮我从周围那些大斧犬妖身上收集一些斧子吗？', Speaker = 2665, Option = '交给我吧', Action = _EmptyTable,}, 
	[80629] = { id = 80629, Voice = '', Text = '既然一定会去找战斧，那不如我们先去把战斧取过来吧。', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80628] = { id = 80628, Voice = '', Text = '堪布大人居然是第二先知的后人？这可是个大秘密呢…不对，现在不是想这个的时候。', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80627] = { id = 80627, Voice = '', Text = '嘿，如果卡隆醒来的第一件事，应该是去找他的战斧吧。我知道的只有这么多了。', Speaker = 3374, Option = '谢谢', Action = _EmptyTable,}, 
	[80626] = { id = 80626, Voice = '', Text = '你在我身后的壁画上也可以看到第二先知手持巨斧的身影。', Speaker = 3374, Option = '', Action = _EmptyTable,}, 
	[80625] = { id = 80625, Voice = '', Text = '卡隆先祖是历代先知中力量最为强大的一个，卡隆的战斧就是最普通的大斧，但是这大斧在他身上有着惊人的威力。', Speaker = 3374, Option = '', Action = _EmptyTable,}, 
	[80624] = { id = 80624, Voice = '', Text = '第二先知么？嘿，说起来第二先知还算是我的先祖，不过，我知道的事情也并不多。', Speaker = 3374, Option = '', Action = _EmptyTable,}, 
	[80623] = { id = 80623, Voice = '', Text = '人类？嘿，这里可不是你应该来的地方，赶紧离开！', Speaker = 3374, Option = '讲述经过', Action = _EmptyTable,}, 
	[80622] = { id = 80622, Voice = '', Text = '不过冒险者，堪布大人的脾气古怪，你不要触怒他啊。', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80621] = { id = 80621, Voice = '', Text = '遗迹看守者——堪布·剑齿大人就在森林中的一座小桥后。你去那里应该能找到他。', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80620] = { id = 80620, Voice = '', Text = '不可思议…你回来的也太快了吧。感谢你，冒险者。', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80619] = { id = 80619, Voice = '', Text = '你怎么回来的这么快？难道是路上遇到了危险?', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80618] = { id = 80618, Voice = '', Text = '冒险者，你能帮忙采集一些荆棘条来吗？', Speaker = 2665, Option = '没问题', Action = _EmptyTable,}, 
	[80617] = { id = 80617, Voice = '', Text = '既然得到了这么可靠的消息，那么我们应该着手准备一下了。', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80616] = { id = 80616, Voice = '', Text = '旺达大人知道的东西真多！谢谢你冒险者。', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80615] = { id = 80615, Voice = '', Text = '据说这跟卡隆小时候曾被荆棘刺伤有关系，嘿嘿，强大如第二先知也有这样的一面呢。', Speaker = 3373, Option = '谢谢告知', Action = _EmptyTable,}, 
	[80614] = { id = 80614, Voice = '', Text = '卡隆非常讨厌荆棘，在卡隆还是第二先知的时候曾经用魔法把森林中的荆棘都清理了一遍。', Speaker = 3373, Option = '', Action = _EmptyTable,}, 
	[80613] = { id = 80613, Voice = '', Text = '第二先知的事情数不胜数，随便哪个犬妖都能说出一大堆，但是，我会告诉你一个其他人都不知道的。', Speaker = 3373, Option = '', Action = _EmptyTable,}, 
	[80612] = { id = 80612, Voice = '', Text = '第二先知卡隆·剑齿啊…那可是非常久远的事情了。', Speaker = 3373, Option = '', Action = _EmptyTable,}, 
	[80611] = { id = 80611, Voice = '', Text = '陵寝看守者——旺达·剑齿大人，就在森林的西北方。你去那里找旺达·剑齿大人吧。', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80610] = { id = 80610, Voice = '', Text = '所以冒险者，我想请你去找我们尊敬的看守者大人。也许他们能给你一些建议。', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80609] = { id = 80609, Voice = '', Text = '我们还会在这附近继续搜查，因为年代久远，我们对第二先知一无所知。', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80608] = { id = 80608, Voice = '', Text = '冒险者，既然我们有着相同的目的，我们可以联合在一起。', Speaker = 2665, Option = '该怎么做', Action = _EmptyTable,}, 
	[80607] = { id = 80607, Voice = '', Text = '我们根据陵寝留下的线索一路追查到这里，那个诡异的身影就是受到诅咒影响而复活的第二先知卡隆。', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80606] = { id = 80606, Voice = '', Text = '就在几天前，陵寝看守者旺达·剑齿大人发现第二先知卡隆·剑齿安息之所竟然被打开了！', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80605] = { id = 80605, Voice = '', Text = '先知并不是神，在先知们死后，我们为先知们建立了一座陵寝，让先知们死后得以安息。', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80604] = { id = 80604, Voice = '', Text = '是的，曾经侍奉于大犬神的先知们一直是我们犬妖所敬仰的人。', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80603] = { id = 80603, Voice = '', Text = '根据我们三位看守者大人的推断，那个恐怖的阴影应该是我们曾经的先知。', Speaker = 2665, Option = '先知？', Action = _EmptyTable,}, 
	[80602] = { id = 80602, Voice = '', Text = '其实我也是来调查这附近出现的神秘阴影的。', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80601] = { id = 80601, Voice = '', Text = '你也是来调查那个神秘的阴影的？我为我的莽撞感到抱歉，冒险者。', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80600] = { id = 80600, Voice = '', Text = '你…你是谁？为什么出现在这里！', Speaker = 2665, Option = '讲述经过', Action = _EmptyTable,}, 
	[80599] = { id = 80599, Voice = '', Text = '有敌人！小队警戒！', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80598] = { id = 80598, Voice = '', Text = '这里的气息…果然是“他”的气息！…什么人？', Speaker = 2665, Option = '', Action = _EmptyTable,}, 
	[80597] = { id = 80597, Voice = '', Text = '所以冒险者，我想请你去那边的森林探查一下，确保我接下来的日子能够安稳的度过。', Speaker = 2664, Option = '交给我吧', Action = _EmptyTable,}, 
	[80596] = { id = 80596, Voice = '', Text = '我很想搬离这里，但是这里确确实实是一个观测诅咒的好地方。', Speaker = 2664, Option = '', Action = _EmptyTable,}, 
	[80595] = { id = 80595, Voice = '', Text = '那种感觉…就像死神的镰刀从我的脖颈划过，这种面临死亡的恐惧，我再也不想尝试第二次了。', Speaker = 2664, Option = '', Action = _EmptyTable,}, 
	[80594] = { id = 80594, Voice = '', Text = '就在这时，我发现密林的深处一个黑影渐渐浮现，他的眼睛散发这红色的光芒，我可以感觉到，他是在盯着我！', Speaker = 2664, Option = '', Action = _EmptyTable,}, 
	[80593] = { id = 80593, Voice = '', Text = '就在前几天，我和往常一样提取了空气中的诅咒浓度后，就准备休息了。', Speaker = 2664, Option = '', Action = _EmptyTable,}, 
	[80592] = { id = 80592, Voice = '', Text = '我是遵从魔法公会的命令来克特森林调查和研究森林中诅咒的研究员。', Speaker = 2664, Option = '', Action = _EmptyTable,}, 
	[80591] = { id = 80591, Voice = '', Text = '天啊，我该怎么形容我看到的那个…哦，我的天，我实在想不出什么词来形容我看到的东西。', Speaker = 2664, Option = '发生了什么', Action = _EmptyTable,}, 
	[56069] = { id = 56069, Voice = '', Text = '在今后的冒险中，冒险者公会与你同在！', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[56068] = { id = 56068, Voice = '', Text = '那么恭喜你，冒险者，你成为了光荣的B级冒险者的一员！', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[56067] = { id = 56067, Voice = '', Text = '你的时间扭曲键呢？是丢失了吗？', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[56066] = { id = 56066, Voice = '', Text = '你还是一如既往的迅速啊，冒险者，时间扭曲键我就收下了。', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[56065] = { id = 56065, Voice = '', Text = '冒险者，你还在等待什么？', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[56064] = { id = 56064, Voice = '', Text = '既然你已经带来了时间扭曲键，那就快把它给我吧！', Speaker = 2540, Option = '{上交时间扭曲键,2},{不交,3}', Action = _EmptyTable,}, 
	[56063] = { id = 56063, Voice = '', Text = '你身上没有时间扭曲键，你还是赶紧去寻找吧！', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[56062] = { id = 56062, Voice = '', Text = '嗯，确实是时间扭曲键没错。', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[56061] = { id = 56061, Voice = '', Text = '这么快？我看看…', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[56060] = { id = 56060, Voice = '', Text = '只要把它交给我，你的进阶考核就算结束了！', Speaker = 2540, Option = '交给我！', Action = _EmptyTable,}, 
	[56059] = { id = 56059, Voice = '', Text = '那么只有最后一个考核了，冒险者公会现在需要一个稀有材料，叫做时间扭曲键！', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[56058] = { id = 56058, Voice = '', Text = '你也确实拥有B级冒险者的实力与经验！', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[56057] = { id = 56057, Voice = '', Text = '冒险者，凯特·索玛斯很是夸赞你的能力呢。', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[56056] = { id = 56056, Voice = '', Text = '索罗·图克这家伙眼光还真不错！我对你的表现非常满意。你去找索罗·图克接着进行考核吧！', Speaker = 1022, Option = '好的', Action = _EmptyTable,}, 
	[56055] = { id = 56055, Voice = '', Text = '谢天谢地，图纸终于回来了，非常感谢你冒险者。', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
	[56054] = { id = 56054, Voice = '', Text = '我得去追这些家伙了！再见，冒险者，你这家伙还真不赖！', Speaker = 5107, Option = '', Action = _EmptyTable,}, 
	[56053] = { id = 56053, Voice = '', Text = '是他们…冒险者，把图纸带回普隆德拉。', Speaker = 5107, Option = '', Action = _EmptyTable,}, 
	[56052] = { id = 56052, Voice = '', Text = '等到雷根贝勒…哼，要他们好看！', Speaker = 5112, Option = '', Action = _EmptyTable,}, 
	[56051] = { id = 56051, Voice = '', Text = '哼！没用的东西，回去！有些事情要好好计划一下了！', Speaker = 5111, Option = '', Action = _EmptyTable,}, 
	[56050] = { id = 56050, Voice = '', Text = '崔斯蒂任务失败，死去了！', Speaker = 5110, Option = '', Action = _EmptyTable,}, 
	[56049] = { id = 56049, Voice = '', Text = '等等！躲起来！', Speaker = 5107, Option = '', Action = _EmptyTable,}, 
	[56048] = { id = 56048, Voice = '', Text = '干得不错冒险者，我们准备回去复命吧！', Speaker = 5107, Option = '', Action = _EmptyTable,}, 
	[56041] = { id = 56041, Voice = '', Text = '未知的魔法阵闪烁着充盈的魔力。', Speaker = 5109, Option = '{进入,3},{不进入,4}', Action = _EmptyTable,}, 
	[56040] = { id = 56040, Voice = '', Text = '[c][ffff00]我建议，你还是找一些同伴再进入这里寻找崔斯蒂吧！[-][/c]', Speaker = 5107, Option = '', Action = _EmptyTable,}, 
	[56039] = { id = 56039, Voice = '', Text = '看来就在里面了，冒险者，我感到传送门里有一股非常危险的气息。', Speaker = 5107, Option = '', Action = _EmptyTable,}, 
	[56037] = { id = 56037, Voice = '', Text = '安全屋就是这里了吧，接下来只要等他们来…', Speaker = 5108, Option = '', Action = _EmptyTable,}, 
	[56029] = { id = 56029, Voice = '', Text = '那现在你可以动身了，我会潜行跟随在你身边。', Speaker = 5107, Option = '', Action = _EmptyTable,}, 
	[56028] = { id = 56028, Voice = '', Text = '具体效果未知，总之，一切小心为上。听明白了吗？', Speaker = 5107, Option = '明白', Action = _EmptyTable,}, 
	[56027] = { id = 56027, Voice = '', Text = '另外，我曾经看到崔斯蒂拿着一个红色瓶子，这个应该就是雷根贝勒的新型药剂。', Speaker = 5107, Option = '', Action = _EmptyTable,}, 
	[56026] = { id = 56026, Voice = '', Text = '但是这种地方往往伴随着陷阱与暗箭，你可别死在里面！', Speaker = 5107, Option = '', Action = _EmptyTable,}, 
	[56025] = { id = 56025, Voice = '', Text = '她带着图纸进入了钟楼地下，地下应该有让她觉得安全的地方。', Speaker = 5107, Option = '', Action = _EmptyTable,}, 
	[56024] = { id = 56024, Voice = '', Text = '目标人物名叫崔斯蒂，是一名十字刺客。', Speaker = 5107, Option = '', Action = _EmptyTable,}, 
	[56023] = { id = 56023, Voice = '', Text = '哼，算了，听着冒险者，情报我只说一遍。', Speaker = 5107, Option = '', Action = _EmptyTable,}, 
	[56022] = { id = 56022, Voice = '', Text = '你就是冒险者公会派来参加考核的冒险者吗？哼，看上去不怎么样啊！', Speaker = 5107, Option = '{……,2},{你这人…,2},{是我,2}', Action = _EmptyTable,}, 
	[56021] = { id = 56021, Voice = '', Text = '我们的情报人员名叫法拉姆，到了钟楼后你可以找到他，他会告诉你奸细的消息！', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
	[56020] = { id = 56020, Voice = '', Text = '她身上似乎有着雷根贝勒的某样研究品，战斗力不能以常理推断。', Speaker = 1022, Option = '我明白', Action = _EmptyTable,}, 
	[56019] = { id = 56019, Voice = '', Text = '看来她是朱诺那边派来的人了…冒险者请小心，她很强大！', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
	[56018] = { id = 56018, Voice = '', Text = '我们的情报人员传来消息，她现在已经躲进了艾尔帕兰的钟楼！', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
	[56017] = { id = 56017, Voice = '', Text = '我们的情报人员已经搜集到关于她的相关情报了，而你的考核就是夺回图纸！', Speaker = 1022, Option = '交给我', Action = _EmptyTable,}, 
	[56016] = { id = 56016, Voice = '', Text = '索罗·图克应该跟你说过魔灵怒吼的设计图被奸细夺走了。', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
	[56015] = { id = 56015, Voice = '', Text = '接下来的事情可能对你来说是个考核，但是对我们来说却是一件紧急事件。', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
	[56014] = { id = 56014, Voice = '', Text = '谢谢你带来的材料，我们可以进行修复工作了。', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
	[56013] = { id = 56013, Voice = '', Text = '你的珍珠呢？是丢失了吗？', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
	[56012] = { id = 56012, Voice = '', Text = '珍珠我收下了，你的速度还不错。', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
	[56011] = { id = 56011, Voice = '', Text = '冒险者，你还在等待什么？', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
	[56010] = { id = 56010, Voice = '', Text = '既然你已经带来了珍珠，那就快把它给我吧！', Speaker = 1022, Option = '{上交1个珍珠,2},{不交,3}', Action = _EmptyTable,}, 
	[56009] = { id = 56009, Voice = '', Text = '你身上没有珍珠，你还是赶紧去寻找吧！', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
	[56008] = { id = 56008, Voice = '', Text = '嗯，确实是珍珠没错。', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
	[56007] = { id = 56007, Voice = '', Text = '这么快？我看看…', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
	[56006] = { id = 56006, Voice = '', Text = '我期待着你能带给我惊喜，如果你成功完成了这小小的考验，那么才有资格挑战接下来的困难！', Speaker = 1022, Option = '我会的', Action = _EmptyTable,}, 
	[56005] = { id = 56005, Voice = '', Text = '另一种材料叫做珍珠，它拥有良好的魔导性，在海神、蜂后这些强大魔物身上可以找到。', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
	[56004] = { id = 56004, Voice = '', Text = '用你的实力来获得我的信任吧。', Speaker = 1022, Option = '好的', Action = _EmptyTable,}, 
	[56003] = { id = 56003, Voice = '', Text = '参加考核？好吧，虽然我听说过你的名字，但是我还不是很信任你。', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
	[56002] = { id = 56002, Voice = '', Text = '要知道沟通能力也是一个冒险者必备的素质，去取得凯特·索玛斯的信任吧！', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[56001] = { id = 56001, Voice = '', Text = '那么现在，你可以去寻找王国内务官凯特·索玛斯，问问他另一种材料是什么了。', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[56000] = { id = 56000, Voice = '', Text = '还不错，恭喜你完成第一项考核。', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[55999] = { id = 55999, Voice = '', Text = '你的黑云母呢？是丢失了吗？', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[55998] = { id = 55998, Voice = '', Text = '总算收集到黑云母了，还不错。', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[55997] = { id = 55997, Voice = '', Text = '冒险者，你还在等待什么？', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[55996] = { id = 55996, Voice = '', Text = '既然你已经带来了黑云母，那就快把它给我吧！', Speaker = 2540, Option = '{上交1个黑云母,7},{不交,8}', Action = _EmptyTable,}, 
	[55995] = { id = 55995, Voice = '', Text = '你身上没有黑云母，你还是赶紧去寻找吧！', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[55994] = { id = 55994, Voice = '', Text = '嗯，确实是黑云母没错。', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[55993] = { id = 55993, Voice = '', Text = '这么快？我看看…', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[55992] = { id = 55992, Voice = '', Text = '有信心是好事，不过考虑到考核的危险性，你还是小心一点吧！', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[55991] = { id = 55991, Voice = '', Text = '有信心完成考核吗？', Speaker = 2540, Option = '当然！', Action = _EmptyTable,}, 
	[55990] = { id = 55990, Voice = '', Text = '而你的考核，就是从这些强大的魔物身上，收集到黑云母并且带回来！', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[55989] = { id = 55989, Voice = '', Text = '首先是黑云母，这种珍稀的材料只会出现在古城的强大魔物弑神者以及黑暗之王身上。', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[55988] = { id = 55988, Voice = '', Text = '损坏一台都是很沉重的损失，王国近年来财政吃紧，现在还缺少两件材料才能修复。', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[55987] = { id = 55987, Voice = '', Text = '魔灵怒吼因为制作材料过于昂贵，因此数量稀少。', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[55986] = { id = 55986, Voice = '', Text = '那个奸细在王国卧底长达十年，并且成功盗走了魔灵怒吼的设计图。', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[55985] = { id = 55985, Voice = '', Text = '米德加尔特王国广为人知的战争兵器——[c][ffff00]魔灵怒吼[-][/c]被敌国奸细破坏了！', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[55984] = { id = 55984, Voice = '', Text = '不过现在用来进行B级冒险者的考核正合适！接下来，我来告诉你详细的考核内容。', Speaker = 2540, Option = '好的', Action = _EmptyTable,}, 
	[55983] = { id = 55983, Voice = '', Text = '这里有一份来自王国的委托，本来这是B级冒险者才有资格接取的任务。', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[55982] = { id = 55982, Voice = '', Text = '嘿，你的心还不够坚定啊，想清楚之后再来找我吧！', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[55981] = { id = 55981, Voice = '', Text = '而B级考核更加困难，你还要继续参加考核吗？', Speaker = 2540, Option = '{参加,5},{不参加,6}', Action = _EmptyTable,}, 
	[55980] = { id = 55980, Voice = '', Text = '参加C级考核的时候我就跟你说过，考核的过程往往伴随着危险。', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[55979] = { id = 55979, Voice = '', Text = '所以你的第一个考核就是考验你的收集能力。', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[55978] = { id = 55978, Voice = '', Text = '生存、沟通、收集，三种能力缺一不可。', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[55977] = { id = 55977, Voice = '', Text = '告诉我，一个优秀的冒险者应该具备哪些优秀的技能？', Speaker = 2540, Option = '{生存能力,4},{沟通能力,4},{收集能力,4}', Action = _EmptyTable,}, 
	[55976] = { id = 55976, Voice = '', Text = '嘿，我们果然没看错你，冒险者就该勇往直前！', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[55975] = { id = 55975, Voice = '', Text = '好吧，别让我等太久！B级冒险者的诞生可是难得一见呢！', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[55974] = { id = 55974, Voice = '', Text = '如果你想进行考核，那么现在就可以开始了！', Speaker = 2540, Option = '{现在开始吧,3},{再等等,2}', Action = _EmptyTable,}, 
	[55973] = { id = 55973, Voice = '', Text = '你的能力毋庸置疑！你有资格进行B级冒险者的进阶考核！', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[55972] = { id = 55972, Voice = '', Text = '咳咳，还不错，整个米德加尔特都流传着你的名字，那些吟游诗人也争相传诵着你英勇的事迹。', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[55971] = { id = 55971, Voice = '', Text = '让我看看你的资料…C级冒险者[c][ffff00][PlayerName][-][/c]…唔…', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[55970] = { id = 55970, Voice = '', Text = '好久不见，[c][ffff00][PlayerName][-][/c]，相比于过去，你现在更加成熟强大了呢。', Speaker = 2540, Option = '是的', Action = _EmptyTable,}, 
	[55497] = { id = 55497, Voice = '', Text = '谢谢你为贝莎做得一切，冒险者。', Speaker = 6991, Option = '', Action = _EmptyTable,}, 
	[55496] = { id = 55496, Voice = '', Text = '这件事对贝莎的打击一定很大，我得回去安慰安慰她。', Speaker = 6991, Option = '', Action = _EmptyTable,}, 
	[55495] = { id = 55495, Voice = '', Text = '是吗？她已经回去了吗？', Speaker = 6991, Option = '', Action = _EmptyTable,}, 
	[55494] = { id = 55494, Voice = '', Text = '告诉我的姐姐，我已经回家了，请她不用担心。', Speaker = 5620, Option = '', Action = _EmptyTable,}, 
	[55493] = { id = 55493, Voice = '', Text = '谢谢你，冒险者，我想我应该回去好好静一静。', Speaker = 5620, Option = '', Action = _EmptyTable,}, 
	[55492] = { id = 55492, Voice = '', Text = '我不会感到恐惧,因为你的灵魂与我同在…', Speaker = 5620, Option = '', Action = _EmptyTable,}, 
	[55491] = { id = 55491, Voice = '', Text = '将此泪水献给你，这是崭新的爱语，我们将感谢你给予我的梦想与幸福的日子。', Speaker = 5620, Option = '', Action = _EmptyTable,}, 
	[55490] = { id = 55490, Voice = '', Text = '你的诞生与你的生存只是为了传递那希望的诗篇，直至永远。', Speaker = 5620, Option = '', Action = _EmptyTable,}, 
	[55489] = { id = 55489, Voice = '', Text = '安息吧，我的维奇，你的灵魂，将会延续。', Speaker = 5620, Option = '', Action = _EmptyTable,}, 
	[55488] = { id = 55488, Voice = '', Text = '静静流逝的所有一切，这个世界没有终结。', Speaker = 5620, Option = '', Action = _EmptyTable,}, 
	[55487] = { id = 55487, Voice = '', Text = '我要为维奇祈祷，为我以前的做法赎罪…', Speaker = 5620, Option = '', Action = _EmptyTable,}, 
	[55486] = { id = 55486, Voice = '', Text = '我先回姜饼城了，我会在教堂等你。', Speaker = 5620, Option = '', Action = _EmptyTable,}, 
	[55485] = { id = 55485, Voice = '', Text = '我明白，我想为维奇再做最后一件事情…', Speaker = 5620, Option = '', Action = _EmptyTable,}, 
	[55484] = { id = 55484, Voice = '', Text = '我对维奇做的事，让它十分伤心吧…', Speaker = 5620, Option = '好好对待', Action = _EmptyTable,}, 
	[55483] = { id = 55483, Voice = '', Text = '我从来没有想过玩具也会有自己的感情。', Speaker = 5620, Option = '', Action = _EmptyTable,}, 
	[55482] = { id = 55482, Voice = '', Text = '我以前一直把玩具当做一个工具，他们的存在就是逗我开心。', Speaker = 5620, Option = '', Action = _EmptyTable,}, 
	[55467] = { id = 55467, Voice = '', Text = '我记得应该是在“微笑”的标识附近，也就是smile附近！', Speaker = 6991, Option = '我去看看', Action = _EmptyTable,}, 
	[55466] = { id = 55466, Voice = '', Text = '那时候的维奇浑身脏兮兮的躺在地上，贝莎很喜欢它，便把它带回来了。', Speaker = 6991, Option = '在哪里', Action = _EmptyTable,}, 
	[55465] = { id = 55465, Voice = '', Text = '在玩具工厂魔物还没有像现在横行的时候，我们在玩具工厂找到了它。', Speaker = 6991, Option = '', Action = _EmptyTable,}, 
	[55464] = { id = 55464, Voice = '', Text = '它怎么会变成这样…冒险者你一定要救救贝莎！', Speaker = 6991, Option = '我会的', Action = _EmptyTable,}, 
	[55463] = { id = 55463, Voice = '', Text = '维奇最终被贝莎不小心弄坏了，扔在了垃圾堆里。', Speaker = 6991, Option = '', Action = _EmptyTable,}, 
	[55462] = { id = 55462, Voice = '', Text = '自从贝莎有了玩具士兵后，就再也没碰过维奇。', Speaker = 6991, Option = '', Action = _EmptyTable,}, 
	[55461] = { id = 55461, Voice = '', Text = '它曾经是贝莎最喜欢的玩具，他们俩在一起度过了很快乐的时光。', Speaker = 6991, Option = '', Action = _EmptyTable,}, 
	[55460] = { id = 55460, Voice = '', Text = '维奇！原来如此，我说为什么那只熊这么眼熟。', Speaker = 6991, Option = '它是？', Action = _EmptyTable,}, 
	[55459] = { id = 55459, Voice = '', Text = '贝————莎————', Speaker = 5621, Option = '', Emoji = 12, Action = _EmptyTable,}, 
	[55458] = { id = 55458, Voice = '', Text = '维奇…是你吗？', Speaker = 5620, Option = '', Action = _EmptyTable,}, 
	[55457] = { id = 55457, Voice = '', Text = '赶——走——他——', Speaker = 5621, Option = '', Action = _EmptyTable,}, 
	[55456] = { id = 55456, Voice = '', Text = '贝————莎————', Speaker = 5621, Option = '放开她', Action = _EmptyTable,}, 
	[55455] = { id = 55455, Voice = '', Text = '只有那里才可能是玩偶熊的藏身之地！', Speaker = 6991, Option = '', Action = _EmptyTable,}, 
	[55454] = { id = 55454, Voice = '', Text = '那个熊…有种曾经在哪里看过的感觉…冒险者你先去玩具工厂看看！', Speaker = 6991, Option = '', Action = _EmptyTable,}, 
	[55453] = { id = 55453, Voice = '', Text = '冒险者，你一定要救救贝莎——', Speaker = 6991, Option = '我会的', Action = _EmptyTable,}, 
	[55452] = { id = 55452, Voice = '', Text = '天啊，我的贝莎，贝莎去哪里了啊！', Speaker = 6991, Option = '别着急', Action = _EmptyTable,}, 
	[55451] = { id = 55451, Voice = '', Text = '贝————莎————', Speaker = 5621, Option = '', Action = _EmptyTable,}, 
	[55450] = { id = 55450, Voice = '', Text = '贝莎！天啊！', Speaker = 6991, Option = '', Action = _EmptyTable,}, 
	[55449] = { id = 55449, Voice = '', Text = '贝————莎————', Speaker = 5621, Option = '谁！', Action = _EmptyTable,}, 
	[55448] = { id = 55448, Voice = '', Text = '不介意的话，请留下来吃顿晚饭吧！', Speaker = 6991, Option = '不用客气', Action = _EmptyTable,}, 
	[55447] = { id = 55447, Voice = '', Text = '太感谢你了冒险者，这小家伙整天只会到处乱跑…', Speaker = 6991, Option = '', Action = _EmptyTable,}, 
	[55446] = { id = 55446, Voice = '', Text = '呜呜呜…我要让姐姐给我买新的玩具，呜呜呜呜…', Speaker = 5620, Option = '', Action = _EmptyTable,}, 
	[55445] = { id = 55445, Voice = '', Text = '姐姐在找我…呜呜…我要埋葬好我的玩具士兵…', Speaker = 5620, Option = '我来帮你', Action = _EmptyTable,}, 
	[55444] = { id = 55444, Voice = '', Text = '呜呜呜呜呜…我的玩具士兵不知道被谁给弄坏了…呜呜呜…', Speaker = 5620, Option = '', Action = _EmptyTable,}, 
	[55443] = { id = 55443, Voice = '', Text = '呜呜呜呜呜呜……', Speaker = 5620, Option = '贝莎？', Action = _EmptyTable,}, 
	[55442] = { id = 55442, Voice = '', Text = '刚才我看到她哭得很伤心，向着教堂的方向走过去呢。', Speaker = 6964, Option = '谢谢', Action = _EmptyTable,}, 
	[55441] = { id = 55441, Voice = '', Text = '贝莎，哈，我知道，下午我还跟她一起捉迷藏呢！', Speaker = 6964, Option = '', Action = _EmptyTable,}, 
	[55440] = { id = 55440, Voice = '', Text = '这里没有小女孩的踪迹。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[55439] = { id = 55439, Voice = '', Text = '你愿意帮我找找吗？贝莎还小，肯定走不远的！', Speaker = 6991, Option = '{交给我,2},{我很忙,1}', Action = _EmptyTable,}, 
	[55438] = { id = 55438, Voice = '', Text = '怎么能不着急？这天都快黑了，一家人还在等她吃饭呢！', Speaker = 6991, Option = '', Action = _EmptyTable,}, 
	[55437] = { id = 55437, Voice = '', Text = '贝莎是我的妹妹！这孩子不知道去哪儿了，可真是急死我了。', Speaker = 6991, Option = '别着急', Action = _EmptyTable,}, 
	[55436] = { id = 55436, Voice = '', Text = '贝莎…贝莎到底去哪儿了？', Speaker = 6991, Option = '怎么了', Action = _EmptyTable,}, 
	[55135] = { id = 55135, Voice = '', Text = '你选错啦！！重新再选一次吧', Speaker = 5604, Option = '', Action = _EmptyTable,}, 
	[55134] = { id = 55134, Voice = '', Text = '你给的东西并没有让吉娜释怀，吉娜发出了一声难听的嘶吼，痛苦向你发动攻击。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[55133] = { id = 55133, Voice = '', Text = '明白了，这就给她。', Speaker = 5619, Option = '', Action = _EmptyTable,}, 
	[55132] = { id = 55132, Voice = '', Text = '我明白了自己的不成熟与错误…我要重新回到圣诞老人那里，给孩子们带去关心与爱…', Speaker = 5617, Option = '加油！', Action = _EmptyTable,}, 
	[55131] = { id = 55131, Voice = '', Text = '谢谢你冒险者…你是真正的冒险者…', Speaker = 5617, Option = '', Action = _EmptyTable,}, 
	[55130] = { id = 55130, Voice = '', Text = '并且给了我一颗纯洁无暇的心…', Speaker = 5617, Option = '这没什么', Action = _EmptyTable,}, 
	[55129] = { id = 55129, Voice = '', Text = '你…居然愿意，为了我许愿…', Speaker = 5617, Option = '', Action = _EmptyTable,}, 
	[55128] = { id = 55128, Voice = '', Text = '吉娜得到了一颗白雪一般纯洁无暇的心。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[55127] = { id = 55127, Voice = '', Text = '那么你要的这样东西，你希望它是什么样的。', Speaker = 5619, Option = '{贪婪讨厌,6},{肮脏丑陋,6},{纯白无暇,6}', Action = _EmptyTable,}, 
	[55126] = { id = 55126, Voice = '', Text = '那么你要的这样东西，你希望它是什么样的。', Speaker = 5619, Option = '{贪婪讨厌,6},{肮脏丑陋,6},{纯白无暇,4}', Action = _EmptyTable,}, 
	[55125] = { id = 55125, Voice = '', Text = '你是要帮助黑彼得吗？我这里有几样东西可以给她，你看是什么？', Speaker = 5619, Option = '{心脏,3},{头发,5},{眼睛,5}', Action = _EmptyTable,}, 
	[55124] = { id = 55124, Voice = '', Text = '主人啊主人，你要许什么愿望我都能满足你！', Speaker = 5619, Option = '帮助吉娜', Action = _EmptyTable,}, 
	[55123] = { id = 55123, Voice = '', Text = '你说什么！？你要为我许一个愿？？', Speaker = 5617, Option = '是的', Action = _EmptyTable,}, 
	[55122] = { id = 55122, Voice = '', Text = '你还来做什么！！！你这个贪婪丑陋的贼！！！', Speaker = 5617, Option = '', Action = _EmptyTable,}, 
	[55121] = { id = 55121, Voice = '', Text = '带着怀表去找黑彼得，还给她一颗纯洁无暇的心。', Speaker = 5618, Option = '明白了', Action = _EmptyTable,}, 
	[55120] = { id = 55120, Voice = '', Text = '太好了，我知道你的内心充满善良与喜乐！', Speaker = 5618, Option = '如何做', Action = _EmptyTable,}, 
	[55119] = { id = 55119, Voice = '', Text = '因为你制服了黑彼得，所以你得到了一次宝贵的许愿机会。冒险者，这次的机会来之不易，你愿意拯救黑彼得的内心吗？', Speaker = 5618, Option = '愿意', Action = _EmptyTable,}, 
	[55118] = { id = 55118, Voice = '', Text = '看到了吧！这样的怀表要是落入黑彼得手中，后果不堪设想啊！孩子们的命运会怎么样！？想想都可怕，噢，不！', Speaker = 5618, Option = '是啊', Action = _EmptyTable,}, 
	[55117] = { id = 55117, Voice = '', Text = '这是圣诞老人的宝物，也是万能的许愿怀表，召唤出来的精灵可以实现你的任何愿望。', Speaker = 5618, Option = '', Action = _EmptyTable,}, 
	[55116] = { id = 55116, Voice = '', Text = '主人啊主人，你要许什么愿望我都能够满足你！', Speaker = 5619, Option = '', Action = _EmptyTable,}, 
	[55115] = { id = 55115, Voice = '', Text = '一只圣诞精灵首领出现在玩家眼前。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[55114] = { id = 55114, Voice = '', Text = '随着“嘭”的一声！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[55113] = { id = 55113, Voice = '', Text = '怀表竟发出了奇幻的光线！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[55112] = { id = 55112, Voice = '', Text = '你试着擦拭这只怀表看看。', Speaker = 5618, Option = '', Action = _EmptyTable,}, 
	[55111] = { id = 55111, Voice = '', Text = '她的目标是那只旧怀表对吗？', Speaker = 5618, Option = '是的', Action = _EmptyTable,}, 
	[55110] = { id = 55110, Voice = '', Text = '黑彼得失去了她的本职，对姜饼城的仇恨和不满逐渐腐蚀着她的内心，这让她变得又老又丑，常常伪装自己，回到姜饼城欺骗路过的冒险者。', Speaker = 5618, Option = '欺骗什么', Action = _EmptyTable,}, 
	[55109] = { id = 55109, Voice = '', Text = '黑彼得并没有这份耐心，她厌倦了孩子们的哭闹，失手打了其中一个小男孩。因为这件事，被圣诞老人驱逐出了姜饼城。', Speaker = 5618, Option = '原来如此', Action = _EmptyTable,}, 
	[55108] = { id = 55108, Voice = '', Text = '没错，她以前主要的任务是取悦孩子们，但你知道孩子们可都是需要耐心和关爱的。', Speaker = 5618, Option = '', Action = _EmptyTable,}, 
	[55107] = { id = 55107, Voice = '', Text = '是关于吉娜的事，吉娜的原名是黑彼得。曾经是圣诞老人的助手之一…', Speaker = 5618, Option = '黑彼得？', Action = _EmptyTable,}, 
	[55106] = { id = 55106, Voice = '', Text = '哦对了，我看你穿的这么少都忘了该告诉你的正事了呢…哈哈哈！', Speaker = 5618, Option = '', Action = _EmptyTable,}, 
	[55105] = { id = 55105, Voice = '', Text = '冒险者~你来啦！天气很冷要多加衣服哦，你穿这么点不怕感冒？！', Speaker = 5618, Option = '', Action = _EmptyTable,}, 
	[55104] = { id = 55104, Voice = '', Text = '这里危险，来姜饼城找我吧！', Speaker = 5618, Option = '', Action = _EmptyTable,}, 
	[55103] = { id = 55103, Voice = '', Text = '冒险者，这里可不是每一个人都像童话故事里那样友善的，和一些人结识可要格外小心。', Speaker = 5618, Option = '怎么回事', Action = _EmptyTable,}, 
	[55102] = { id = 55102, Voice = '', Text = '不好是雪橇…真是不走运！', Speaker = 5617, Option = '', Action = _EmptyTable,}, 
	[55101] = { id = 55101, Voice = '', Text = '你这害人的东西啊！又跑来迷惑谁！？', Speaker = 5618, Option = '', Action = _EmptyTable,}, 
	[55099] = { id = 55099, Voice = '', Text = '你不给我，我现在就咬去你的脑袋！！！', Speaker = 5616, Option = '', Emoji = 12, Action = _EmptyTable,}, 
	[55098] = { id = 55098, Voice = '', Text = '你问这个做什么！？与你有何相干！！你给不给我！？', Speaker = 5616, Option = '', Action = _EmptyTable,}, 
	[55097] = { id = 55097, Voice = '', Text = '吉娜的眼里射出不友好的目光，瞪大的眼睛里充满了红血丝，仿佛要把你生吞活剥一般。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[55096] = { id = 55096, Voice = '', Text = '你！？不会是想占为己有吧！！！！', Speaker = 5616, Option = '', Emoji = 12, Action = _EmptyTable,}, 
	[55095] = { id = 55095, Voice = '', Text = '快！！快把它给我！！', Speaker = 5616, Option = '你急什么', Action = _EmptyTable,}, 
	[55094] = { id = 55094, Voice = '', Text = '这么说你拿到那枚旧怀表了！', Speaker = 5616, Option = '', Action = _EmptyTable,}, 
	[55088] = { id = 55088, Voice = '', Text = '青草~香氛~小饼干~将这位冒险者带进礼物盒的下面！', Speaker = 5616, Option = '', Action = _EmptyTable,}, 
	[55087] = { id = 55087, Voice = '', Text = '那你准备好了哦~', Speaker = 5616, Option = '', Action = _EmptyTable,}, 
	[55086] = { id = 55086, Voice = '', Text = '红帽子的精灵有个首领全身闪着黑色的光芒，一眼就能看出来，除掉它其他的红帽精灵也会消失。绿帽子的精灵，喂给他们甜的东西，他们愿意乖乖走开。听明白了吗？', Speaker = 5616, Option = '{明白了,3},{再听一遍,2}', Action = _EmptyTable,}, 
	[55085] = { id = 55085, Voice = '', Text = '绿帽子的精灵，喂给他们甜的东西，他们愿意乖乖走开。听明白了吗？', Speaker = 5616, Option = '{明白了,3},{再听一遍,2}', Action = _EmptyTable,}, 
	[55084] = { id = 55084, Voice = '', Text = '红帽子的精灵有个首领全身闪着黑色的光芒，一眼就能看出来，除掉它其他的红帽精灵也会消失。', Speaker = 5616, Option = '', Action = _EmptyTable,}, 
	[55083] = { id = 55083, Voice = '', Text = '那你听好了，进去之后，会有个闪闪发亮的宝箱，但是看守宝箱的是一群惹人厌的宝箱精灵。', Speaker = 5616, Option = '', Action = _EmptyTable,}, 
	[55082] = { id = 55082, Voice = '', Text = '那是我祖母上次忘掉在那里面的。', Speaker = 5616, Option = '', Action = _EmptyTable,}, 
	[55081] = { id = 55081, Voice = '', Text = '嘿嘿嘿，我一个铜板也不要。我只要你替我把那个旧怀表取出来。', Speaker = 5616, Option = '旧怀表？', Action = _EmptyTable,}, 
	[55080] = { id = 55080, Voice = '', Text = '你说要答谢我？', Speaker = 5616, Option = '', Action = _EmptyTable,}, 
	[55079] = { id = 55079, Voice = '', Text = '嘿嘿嘿，真是年轻又勇敢啊~你是我见过最厉害的冒险者！', Speaker = 5616, Option = '谢谢', Action = _EmptyTable,}, 
	[55078] = { id = 55078, Voice = '', Text = '不过这次可是要冒点险的，你有没有这份觉悟？', Speaker = 5616, Option = '有', Action = _EmptyTable,}, 
	[55077] = { id = 55077, Voice = '', Text = '是的，我可以用魔法将你传送进去。', Speaker = 5616, Option = '', Action = _EmptyTable,}, 
	[55076] = { id = 55076, Voice = '', Text = '就在这一大堆礼物下面~', Speaker = 5616, Option = '礼物下面？', Action = _EmptyTable,}, 
	[55075] = { id = 55075, Voice = '', Text = '跟我来~', Speaker = 5616, Option = '', Action = _EmptyTable,}, 
	[55074] = { id = 55074, Voice = '', Text = '说实话，这里还有最后一个宝箱。', Speaker = 5616, Option = '在哪儿？', Action = _EmptyTable,}, 
	[55073] = { id = 55073, Voice = '', Text = '哎哟，我真是有点羡慕你年轻的才华呢！', Speaker = 5616, Option = '', Action = _EmptyTable,}, 
