	[55055] = { id = 55055, Voice = '', Text = '如果你想要更多的财富和礼物，这儿还有第二个宝箱！~', Speaker = 5616, Option = '在哪里？', Action = _EmptyTable,}, 
	[55054] = { id = 55054, Voice = '', Text = '哦~我的孩子，谢什么呀！这里还有好东西呢！', Speaker = 5616, Option = '', Action = _EmptyTable,}, 
	[55053] = { id = 55053, Voice = '', Text = '纽扣从盒子里滚了出来，什么事都没发生。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[55052] = { id = 55052, Voice = '', Text = '要投入纽扣吗？', Speaker = 5603, Option = '{投入,43},{不投入,42}', Action = _EmptyTable,}, 
	[55051] = { id = 55051, Voice = '', Text = '顺利的拿到了宝箱里的东西。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[55050] = { id = 55050, Voice = '', Text = '盒子闪烁着金灿灿的光，变的沉重起来。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[55049] = { id = 55049, Voice = '', Text = '要投入纽扣吗？', Speaker = 5602, Option = '{投入,33},{不投入,31}', Action = _EmptyTable,}, 
	[55048] = { id = 55048, Voice = '', Text = '纽扣从盒子里滚了出来，什么事都没发生。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[55047] = { id = 55047, Voice = '', Text = '要投入纽扣吗？', Speaker = 5601, Option = '{投入,23},{不投入,22}', Action = _EmptyTable,}, 
	[55046] = { id = 55046, Voice = '', Text = '祝你好运了，年轻人。拿到好东西就来告诉我吧！', Speaker = 5616, Option = '', Action = _EmptyTable,}, 
	[55045] = { id = 55045, Voice = '', Text = '把纽扣投进最小的那个盒子里！盒子里就会有宝藏掉出来了！', Speaker = 5616, Option = '', Action = _EmptyTable,}, 
	[55044] = { id = 55044, Voice = '', Text = '你听懂了吗？', Speaker = 5616, Option = '{听懂了,4},{再说一遍,3}', Action = _EmptyTable,}, 
	[55043] = { id = 55043, Voice = '', Text = '把纽扣投进最小的那个盒子里！盒子里就会有宝藏掉出来了！', Speaker = 5616, Option = '', Action = _EmptyTable,}, 
	[55042] = { id = 55042, Voice = '', Text = '一会儿会从传送带上转出三个礼盒。', Speaker = 5616, Option = '', Action = _EmptyTable,}, 
	[55041] = { id = 55041, Voice = '', Text = '看到我身后的传送带了吗？', Speaker = 5616, Option = '看到了', Action = _EmptyTable,}, 
	[55040] = { id = 55040, Voice = '', Text = '做的好，冒险者，现在。', Speaker = 5616, Option = '', Action = _EmptyTable,}, 
	[55039] = { id = 55039, Voice = '', Text = '非常好！！', Speaker = 5616, Option = '', Action = _EmptyTable,}, 
	[55038] = { id = 55038, Voice = '', Text = '别问我为什么！想要拿到宝藏需要那个，我会在玩具工厂等着你。', Speaker = 5600, Option = '', Action = _EmptyTable,}, 
	[55037] = { id = 55037, Voice = '', Text = '玩具工厂有一只巨型玩具熊，它的眼睛是一颗大大的纽扣，我需要你把那颗纽扣扯下来。', Speaker = 5600, Option = '', Action = _EmptyTable,}, 
	[55036] = { id = 55036, Voice = '', Text = '我可是不会骗人的，只不过…', Speaker = 5600, Option = '什么', Action = _EmptyTable,}, 
	[55035] = { id = 55035, Voice = '', Text = '我知道玩具工厂里有三个大礼物堆，你可以跑进去，里面可是有宝藏的。', Speaker = 5600, Option = '真的吗', Action = _EmptyTable,}, 
	[55034] = { id = 55034, Voice = '', Text = '恭喜你，你马上就要走好运了！', Speaker = 5600, Option = '{相信,0},{不相信,1}', Action = _EmptyTable,}, 
	[55033] = { id = 55033, Voice = '', Text = '晚安，冒险者！你的武器真漂亮，你真是一个不折不扣的冒险者！', Speaker = 5600, Option = '谢谢', Action = _EmptyTable,}, 
	[54702] = { id = 54702, Voice = '', Text = '好吧，那么等你有时间我再念给你听吧～', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54492] = { id = 54492, Voice = '', Text = '惊鸿一瞥注定千年的悲伤……', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54491] = { id = 54491, Voice = '', Text = '耀眼的太阳爱上冰冷的极光。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54490] = { id = 54490, Voice = '', Text = '偶见金子般的美人吉尔达，', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54489] = { id = 54489, Voice = '', Text = '象征金色阳光和温暖夏雨的弗雷，', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54488] = { id = 54488, Voice = '', Text = '阿斯加德迎来了太阳神弗雷，', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54487] = { id = 54487, Voice = '', Text = '那一天，月亮和星星都黯然失辉，', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54486] = { id = 54486, Voice = '', Text = '也为了连接过去与未来。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54485] = { id = 54485, Voice = '', Text = '为了现在人类所处的黄金年代，', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54484] = { id = 54484, Voice = '', Text = '献给路过的命运之人，', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54483] = { id = 54483, Voice = '', Text = '试着用韵文和音律将其叙述。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54482] = { id = 54482, Voice = '', Text = '关于爱与遗憾，我们这些无名的吟游诗人，', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54481] = { id = 54481, Voice = '', Text = '时值公元以前，人间还存留着通往神域之路。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54480] = { id = 54480, Voice = '', Text = '这个故事发生于美丽的亚尔夫海姆，', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54479] = { id = 54479, Voice = '', Text = '好了，就是这样，你走吧，我不想见到你了……', Speaker = 6988, Option = '', Action = _EmptyTable,}, 
	[54478] = { id = 54478, Voice = '', Text = '她守护着夏宫，也就是姜饼城，用自己微弱的神力让这里变成如今这个样子。', Speaker = 6988, Option = '', Action = _EmptyTable,}, 
	[54477] = { id = 54477, Voice = '', Text = '吉尔达看见自己的丈夫被火焰巨人杀死，不愿意在这个世界上独活。', Speaker = 6988, Option = '', Action = _EmptyTable,}, 
	[54476] = { id = 54476, Voice = '', Text = '我只是一个孤独的灵魂……', Speaker = 6988, Option = '', Action = _EmptyTable,}, 
	[54475] = { id = 54475, Voice = '', Text = '那个时候，夏宫还不是这个样子，没有风雪、也没有玩具工厂。只有从来不会凝结的泉水和永恒的夏天。', Speaker = 6988, Option = '你是谁？', Action = _EmptyTable,}, 
	[54474] = { id = 54474, Voice = '', Text = '偶尔弗雷也会邀请芬里尔、耶梦加得和海拉前往夏宫游玩。', Speaker = 6988, Option = '', Action = _EmptyTable,}, 
	[54473] = { id = 54473, Voice = '', Text = '他的三个孩子在神域受尽了屈辱……只有同样身为巨人后裔的吉尔达对他们温柔以待。', Speaker = 6988, Option = '', Action = _EmptyTable,}, 
	[54472] = { id = 54472, Voice = '', Text = '那个时候，阿萨神族对巨人的后裔都很排斥……洛基，即使是主神之一的洛基也受到神族的冷落。', Speaker = 6988, Option = '', Action = _EmptyTable,}, 
	[54471] = { id = 54471, Voice = '', Text = '你是这样认为的吗？冒险者，你很有趣！', Speaker = 6988, Option = '', Action = _EmptyTable,}, 
	[54470] = { id = 54470, Voice = '', Text = '你想知道吉尔达怎么样了？你是一个人类，会关心一个巨人的死活吗？', Speaker = 6988, Option = '我很关心', Action = _EmptyTable,}, 
	[54469] = { id = 54469, Voice = '', Text = '如果你能永远记得有这样一段往事，我会永远的感激你，命运之人。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54468] = { id = 54468, Voice = '', Text = '好了，我的故事已经到此为止了，这就是太阳神弗雷和他妻子吉尔达的爱情。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54467] = { id = 54467, Voice = '', Text = '太眼神陨落了，仅留下了一小片没有意识的神力，永远保护着夏宫……', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54466] = { id = 54466, Voice = '', Text = '失去了弗雷神力支持的夏宫，急速从神域向人间坠落，弗雷为了保护还在夏宫里的吉尔达，打破自己微弱的神格，让这片土地安全的落入人间。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54465] = { id = 54465, Voice = '', Text = '失去了胜利之剑的弗雷只能用鹿角与巨人战斗，最后，他击败了巨人，自己也耗尽了神力……', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54464] = { id = 54464, Voice = '', Text = '弗雷为了保护自己的宫殿，与一个火焰巨人发生了战斗。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54463] = { id = 54463, Voice = '', Text = '然而战火依然蔓延到这一片祥和的土地，诸神黄昏来临之时，整个阿斯加德都天崩地裂。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54462] = { id = 54462, Voice = '', Text = '他把这所宫殿称为夏宫，弗雷和吉尔达幸福的生活在夏宫里，不参与任何神族和巨人的战争，也不过问神族内部的世事。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54461] = { id = 54461, Voice = '', Text = '他又用自己的神力为这所宫殿赋予了生命，吸引了各种珍奇灵兽，让宫殿的时间凝固，停留在生命力最旺盛的夏天。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54460] = { id = 54460, Voice = '', Text = '他在神域的边缘选址，将亚尔夫海姆的鲜花与树木移植到宫殿里，引来了天上的河流，从宫殿的中央穿过……', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54459] = { id = 54459, Voice = '', Text = '太阳神弗雷害怕自己的妻子吉尔达因为巨人后裔的身份被阿斯加德的众神冷落，决定要建造一座美丽的宫殿，让吉尔达和自己有一个家。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54458] = { id = 54458, Voice = '', Text = '唉，这是一个悲伤的故事，也许我不应该跟你说这么多。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54457] = { id = 54457, Voice = '', Text = '你准备好了要听这个故事了吗？', Speaker = 6987, Option = '{准备听,6},{没兴趣,5}', Action = _EmptyTable,}, 
	[54456] = { id = 54456, Voice = '', Text = '这又是另外一段故事，让我平复一下心情……', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54455] = { id = 54455, Voice = '', Text = '是鹿角的碎片吗？果然！', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54454] = { id = 54454, Voice = '', Text = '去找吟游诗人吧，我只是时间的过客……', Speaker = 6988, Option = '', Action = _EmptyTable,}, 
	[54453] = { id = 54453, Voice = '', Text = '当然只是故事，别想太多，一切只是我的一段梦而已。', Speaker = 6988, Option = '', Action = _EmptyTable,}, 
	[54452] = { id = 54452, Voice = '', Text = '这个碎片是……是鹿角的碎片，传说中太阳神弗雷和火焰巨人战斗时用的鹿角。', Speaker = 6988, Option = '只是故事', Action = _EmptyTable,}, 
	[54451] = { id = 54451, Voice = '', Text = '哦，你听那个吟游诗人讲了故事吗？他说是我讲给他的……也许吧，故事只是个故事而已。', Speaker = 6988, Option = '', Action = _EmptyTable,}, 
	[54450] = { id = 54450, Voice = '', Text = '唔，不好意思，冒险者，我认错人了……', Speaker = 6988, Option = '', Action = _EmptyTable,}, 
	[54449] = { id = 54449, Voice = '', Text = '是你！', Speaker = 6988, Option = '你认识我？', Action = _EmptyTable,}, 
	[54448] = { id = 54448, Voice = '', Text = '你不妨可以去问问他。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54447] = { id = 54447, Voice = '', Text = '我不确定这个碎片是不是我心中所想的东西。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54446] = { id = 54446, Voice = '', Text = '不瞒你说，我所讲的故事，是听我旁边的这位蒙面人跟我说的。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54445] = { id = 54445, Voice = '', Text = '这个材质是鹿角？！这个莫非是……', Speaker = 6987, Option = '是什么', Action = _EmptyTable,}, 
	[54444] = { id = 54444, Voice = '', Text = '让我仔细看一看，到底是什么能发出如此巨大的力量。这个材质……', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54443] = { id = 54443, Voice = '', Text = '就是这一个小小的碎片吗？', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54442] = { id = 54442, Voice = '', Text = '魔物都像疯了一样！你找到了什么？', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54441] = { id = 54441, Voice = '', Text = '我感受到了刚刚的震动，是你做的吗？', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54440] = { id = 54440, Voice = '', Text = '碎片上的力量持续减弱，变成了一枚普通的碎片。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54439] = { id = 54439, Voice = '', Text = '这股力量吸引了旁边的魔物！阻止他们!', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54438] = { id = 54438, Voice = '', Text = '一股强大的力量从碎片中传出。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54437] = { id = 54437, Voice = '', Text = '神秘碎片，看上去很锋利！', Speaker = 5450, Option = '{拾取,3},{没兴趣,2}', Action = _EmptyTable,}, 
	[54436] = { id = 54436, Voice = '', Text = '请给我带来更多的线索。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54435] = { id = 54435, Voice = '', Text = '真想去神域看一看亚尔夫海姆，还有那里生长的各种珍奇异草啊。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54434] = { id = 54434, Voice = '', Text = '今天的故事讲完了，你喜欢吗？', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54433] = { id = 54433, Voice = '', Text = '吉尔达看着这棵纤细的植物，内心渐渐平静下来，她看着英俊温柔的太阳神，动了心……', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54432] = { id = 54432, Voice = '', Text = '他对吉尔达说，听说吉尔达生长的北国非常寒冷，没有植物，这是他掌管的精灵之国的花朵，希望她喜欢，他会像呵护这朵花一样呵护吉尔达。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54431] = { id = 54431, Voice = '', Text = '此时，弗雷拿出了他的礼物，那是一朵亚尔夫海姆里才会生长的花，弗雷利用神力把花朵的时间停滞，用水晶瓶保存下来。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54430] = { id = 54430, Voice = '', Text = '她说，她和弗雷一人是华纳的神族，一人是巨人的后裔，他们的爱情注定没有结局。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54429] = { id = 54429, Voice = '', Text = '也许是温柔的太阳神打动了吉尔达女神的心，她终于开口说话了。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54428] = { id = 54428, Voice = '', Text = '看着吉尔达对自己哭，弗雷的心都快碎了，他不知所措的看着吉尔达，一句话都不敢说。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54427] = { id = 54427, Voice = '', Text = '可是，吉尔达一点也不开心，她的眉头紧锁，双颊苍白，泪珠从她哀怨的双眼里滚落……', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54426] = { id = 54426, Voice = '', Text = '他们约定9天后在绿地蒲利见面。当天，弗雷如约来到蒲利，见到了冰雪美人吉尔达。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54425] = { id = 54425, Voice = '', Text = '经过史基尔尼的撮合，吉尔达女神同意与太阳神弗雷见面。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54424] = { id = 54424, Voice = '', Text = '我已经迫不及待想跟你讲这个故事了！', Speaker = 6987, Option = '{准备听,6},{没兴趣,5}', Action = _EmptyTable,}, 
	[54423] = { id = 54423, Voice = '', Text = '这是太阳神弗雷第一次与吉尔达女神见面时送给吉尔达女神的花呢……', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54422] = { id = 54422, Voice = '', Text = '好美啊，谢谢你，命运之人，让我见识到神域的花朵。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54421] = { id = 54421, Voice = '', Text = '你给我带来了什么，让我看看……永恒之花！', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54420] = { id = 54420, Voice = '', Text = '清洗后的瓶子闪闪发光，中间有一朵凝固的花。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54419] = { id = 54419, Voice = '', Text = '清洁剂借给你了。', Speaker = 6989, Option = '', Emoji = 2, Action = _EmptyTable,}, 
	[54418] = { id = 54418, Voice = '', Text = '冒险者你可真厉害，那些深处的魔物让我心惊胆颤。', Speaker = 6989, Option = '', Action = _EmptyTable,}, 
	[54417] = { id = 54417, Voice = '', Text = '照片拍到了吗？看我仔细瞧瞧～', Speaker = 6989, Option = '', Action = _EmptyTable,}, 
	[54416] = { id = 54416, Voice = '', Text = '给我玩具工厂的照片，我就可以借给你清洁剂！', Speaker = 6989, Option = '', Action = _EmptyTable,}, 
	[54415] = { id = 54415, Voice = '', Text = '这里的魔物太多了，我没办法深入调查。', Speaker = 6989, Option = '', Action = _EmptyTable,}, 
	[54414] = { id = 54414, Voice = '', Text = '但是我为什么要借给你呢？除非，你帮我拍到玩具工厂深处的照片！', Speaker = 6989, Option = '', Action = _EmptyTable,}, 
	[54413] = { id = 54413, Voice = '', Text = '请我帮你清理脏瓶子？我确实有清洁剂可以清理一些脏东西。', Speaker = 6989, Option = '', Action = _EmptyTable,}, 
	[54412] = { id = 54412, Voice = '', Text = '这是什么？全都是泥，抱歉，这么脏的东西让我一点灵感都没有……', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54411] = { id = 54411, Voice = '', Text = '泥土中埋着一个小瓶子，脏兮兮的……', Speaker = 5448, Option = '{拾取,3},{没兴趣,2}', Action = _EmptyTable,}, 
	[54410] = { id = 54410, Voice = '', Text = '呼，讲故事可真累啊，今天就到此为止吧，请给我带来更多的线索。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54409] = { id = 54409, Voice = '', Text = '很可惜是不是？失去了主人的兵器，也失去了灵魂。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54408] = { id = 54408, Voice = '', Text = '然而胜利之剑离开主人弗雷后，不愿再为任何人战斗，从此这把剑彻底失去了魔力，变成了一把普通的剑。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54407] = { id = 54407, Voice = '', Text = '为了报答史基尔尼的恩情，弗雷把胜利之剑作为谢礼送给了史基尔尼。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54406] = { id = 54406, Voice = '', Text = '在史基尔尼顺利的撮合他跟吉尔达女神以后，弗雷非常高兴。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54405] = { id = 54405, Voice = '', Text = '弗雷本身的神力就很强大，有了这把神剑，他与敌人战斗的时候如虎添翼。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54404] = { id = 54404, Voice = '', Text = '这把无敌的神剑能自行飞行取敌首级，无论谁掌握了这把剑，这剑便会随着持剑者的希望，独自在战场上飞舞杀戮敌人。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54403] = { id = 54403, Voice = '', Text = '弗雷刚到阿斯加德，阿斯加德的众神为了欢迎他，送了他一把神剑，叫做胜利之剑。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54402] = { id = 54402, Voice = '', Text = '拒绝的真干脆，我还能说些什么呢？', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54401] = { id = 54401, Voice = '', Text = '你知道胜利之剑是什么吗？', Speaker = 6987, Option = '{准备听,10},{没兴趣,9}', Action = _EmptyTable,}, 
	[54400] = { id = 54400, Voice = '', Text = '一个守护的精灵？还活着？！跨越了千年的精灵啊，她一定是个重视承诺的灵魂……', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54399] = { id = 54399, Voice = '', Text = '这是胜利之剑！太阳神弗雷的佩剑，命运之人，你果然不同凡响。', Speaker = 6987, Option = '一位精灵', Action = _EmptyTable,}, 
	[54398] = { id = 54398, Voice = '', Text = '什么？这柄剑，居然被你找到了！', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54397] = { id = 54397, Voice = '', Text = '击退了聚集过来的甜饼人，将那柄充满了寒气的剑从地上拔了出来！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54396] = { id = 54396, Voice = '', Text = '精灵在你面前凝结成一块冰，破碎了，化为点点的碎片。吸引了一些魔物聚集过来！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54395] = { id = 54395, Voice = '', Text = '好冷……弗雷大人，我无法完成你的任务了……呜呜', Speaker = 5451, Option = '', Action = _EmptyTable,}, 
	[54394] = { id = 54394, Voice = '', Text = '谢谢你，虽然不知道你是谁，但是我感受到了你的善意。我没有力气再守护这柄剑了，可以请你帮保管这柄神剑吗？直到我苏醒……', Speaker = 5451, Option = '', Action = _EmptyTable,}, 
	[54393] = { id = 54393, Voice = '', Text = '好冷……好冷……为什么这里会这么冷？弗雷大人呢？', Speaker = 5451, Option = '{保护精灵,5},{置之不理,6}', Action = _EmptyTable,}, 
	[54392] = { id = 54392, Voice = '', Text = '请你速速离去！', Speaker = 5451, Option = '', Action = _EmptyTable,}, 
	[54391] = { id = 54391, Voice = '', Text = '我是夏宫里的守护精灵，负责保护弗雷大人的胜利之剑。', Speaker = 5451, Option = '', Action = _EmptyTable,}, 
	[54390] = { id = 54390, Voice = '', Text = '什么人？胆敢闯入夏宫？', Speaker = 5451, Option = '', Action = _EmptyTable,}, 
	[54389] = { id = 54389, Voice = '', Text = '一柄锋利的剑，寒光笼罩在它的周围……', Speaker = 5449, Option = '{拾取,3},{没兴趣,2}', Action = _EmptyTable,}, 
	[54388] = { id = 54388, Voice = '', Text = '不对，再想一想吧。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54387] = { id = 54387, Voice = '', Text = '不愿意吗？没有灵感，我也无法讲故事呀，请给我带来更多的线索吧！', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54386] = { id = 54386, Voice = '', Text = '天色已晚，剩下的故事我们改天再讲吧。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54385] = { id = 54385, Voice = '', Text = '女神被威胁后只能答应了9天以后在绿地蒲利与弗雷相会……', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54384] = { id = 54384, Voice = '', Text = '没想到吉尔达女神直接拒绝了弗雷的求爱，史基尔尼情急之下，对女神下了咒语，诅咒她如果不嫁给太阳神，就只能独守空闺一生。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54383] = { id = 54383, Voice = '', Text = '弗雷的父亲派史基尔尼去帮助弗雷，史基尔尼受命为吉尔达女神带去了弗雷的定情信物，是一枚聚金戒指。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54382] = { id = 54382, Voice = '', Text = '弗雷的憔悴让所有倾慕他的神族都感到心痛，他的父亲也看在眼里。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54381] = { id = 54381, Voice = '', Text = '神族和巨人之间的仇恨让他没有自信追求吉尔达，那一天，亚尔夫海姆的第一次下起了雨……', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54380] = { id = 54380, Voice = '', Text = '但是，当他知道这位美丽的女神是霜巨人盖密尔的女儿时，感到了深深的绝望……', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54379] = { id = 54379, Voice = '', Text = '虽然只是惊鸿一瞥，冰天雪地中，女神绝美的容颜让弗雷倾倒。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54378] = { id = 54378, Voice = '', Text = '弗雷利用奥丁的宝座看见了冰封之地的吉尔达女神。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54377] = { id = 54377, Voice = '', Text = '不不不，当然没有，他们可没这么容易相爱，让我喝一口水，请听我慢慢的讲……', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54376] = { id = 54376, Voice = '', Text = '不错，这枚戒指叫做聚金戒指，是弗雷送给吉尔达的定情信物。', Speaker = 6987, Option = '恋爱了吗？', Action = _EmptyTable,}, 
	[54375] = { id = 54375, Voice = '', Text = '让我看看，啊，是一枚戒指。太阳神弗雷送给吉尔达的戒指，你知道这枚戒指象征着什么吗？', Speaker = 6987, Option = '{太阳神的爱意,6},{太阳神的战书,5},{太阳神的魔力,5}', Action = _EmptyTable,}, 
	[54374] = { id = 54374, Voice = '', Text = '你带来了重要的线索！', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54373] = { id = 54373, Voice = '', Text = '炼金尖刺已经清理干净，找到有一枚闪闪发光的戒指。', Speaker = 6989, Option = '', Action = _EmptyTable,}, 
	[54372] = { id = 54372, Voice = '', Text = '但是炼金尖刺非常害怕火！只要用火烧就可以清理干净。', Speaker = 6989, Option = '', Action = _EmptyTable,}, 
	[54371] = { id = 54371, Voice = '', Text = '那一定是玩具工厂特有的炼金尖刺，炼金尖刺非常坚固，无法用刀刃破坏。', Speaker = 6989, Option = '', Action = _EmptyTable,}, 
	[54370] = { id = 54370, Voice = '', Text = '什么？你被尖刺划伤了？', Speaker = 6989, Option = '', Action = _EmptyTable,}, 
	[54369] = { id = 54369, Voice = '', Text = '卑鄙的小丑已被击退。清理尖刺可能会被刺伤，寻求其他人的帮助吧。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54368] = { id = 54368, Voice = '', Text = '啊！手指不小心被尖刺划伤，吸引了周围的魔物！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54367] = { id = 54367, Voice = '', Text = '一道金光在闪动，仔细看看？', Speaker = 5447, Option = '{拾取,3},{没兴趣,2}', Action = _EmptyTable,}, 
	[54366] = { id = 54366, Voice = '', Text = '剩下的故事等你找到更多的线索吧。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54365] = { id = 54365, Voice = '', Text = '啊……讲了这么多，我的口渴了……', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54364] = { id = 54364, Voice = '', Text = '女神白金色的卷发，忽闪的睫毛，妖娆的身段，像一朵轻盈的流云，悠悠飘进太阳神的心底。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54363] = { id = 54363, Voice = '', Text = '湖面上倒映着一位优雅的女神，她提着自己的裙摆，款款的走进冰湖旁的小屋里……', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54362] = { id = 54362, Voice = '', Text = '那是一大片冰原，暴雪伴着飓风在空气里呼啸，风雪漩涡的中央，是一片宁静的湖，安静的落雪消失在如镜子一般的湖面。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54361] = { id = 54361, Voice = '', Text = '那一天，弗雷偷偷坐在奥丁的宝座上眺望远方，看见了一块不同于亚尔夫海姆的地方。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54360] = { id = 54360, Voice = '', Text = '小精灵在繁花中穿梭，留下点点的金辉闪动，吸引蝴蝶和蜜蜂跟随飞舞。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54359] = { id = 54359, Voice = '', Text = '百花不分季节绽放，水果压断了树枝滚落在地上任人随意拾取，美酒从石缝里涌出，汇集成泉在山林间流淌。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54358] = { id = 54358, Voice = '', Text = '弗雷利用自己的神力养育精灵之国，源源不断的生命力在亚尔夫海姆的土地里渗透……', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54357] = { id = 54357, Voice = '', Text = '阿萨神请求弗雷担任太阳神的职位，邀请他掌管精灵之国——亚尔夫海姆。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54356] = { id = 54356, Voice = '', Text = '华纳神向阿萨神族送去了弗雷和芙蕾雅，这对兄妹来到阿斯加德之时，他们的智慧与美貌征服了所有的阿萨神。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54355] = { id = 54355, Voice = '', Text = '双方争斗了无数年，造成了不可挽回的损失，那一年，两大神族决定为了双方的繁荣和兴盛而休战，他们签订了互不侵犯的协议，向对方派送了人质。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54354] = { id = 54354, Voice = '', Text = '阿萨神族与华纳神族发生了一场旷日持久的战争。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54353] = { id = 54353, Voice = '', Text = '天地刚刚诞生，秩序刚刚建立，因果刚刚轮回……', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54352] = { id = 54352, Voice = '', Text = '悉听尊便。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54351] = { id = 54351, Voice = '', Text = '吉尔达是象征着极光的女神，这可是一个漫长的故事，请你耐心的听我讲述……', Speaker = 6987, Option = '{准备听,6},{没兴趣,5}', Action = _EmptyTable,}, 
	[54350] = { id = 54350, Voice = '', Text = '一团卷发，哦，多美的头发啊！这是女神吉尔达的秀发。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54349] = { id = 54349, Voice = '', Text = '让我看看你找到了什么，命运之人。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54348] = { id = 54348, Voice = '', Text = '侍女的灵魂失去力量，化成点点碎片消失了……得到一缕头发。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54347] = { id = 54347, Voice = '', Text = '这里……这是什么地方？！我明明在弗雷大人的夏宫里……吉尔达大人在哪里？', Speaker = 5452, Option = '', Action = _EmptyTable,}, 
	[54346] = { id = 54346, Voice = '', Text = '玩具工厂又是什么？', Speaker = 5452, Option = '', Action = _EmptyTable,}, 
	[54345] = { id = 54345, Voice = '', Text = '你是什么人？为什么会在夏宫出现？', Speaker = 5452, Option = '玩具工厂', Action = _EmptyTable,}, 
	[54344] = { id = 54344, Voice = '', Text = '住手！不许碰这个……', Speaker = 5452, Option = '', Action = _EmptyTable,}, 
	[54343] = { id = 54343, Voice = '', Text = '地板的夹缝里，有什么东西在闪烁……', Speaker = 5446, Option = '{拾取,3},{没兴趣,2}', Action = _EmptyTable,}, 
	[54342] = { id = 54342, Voice = '', Text = '帮助我吧，命运之人……', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54341] = { id = 54341, Voice = '', Text = '可是这个玩具工厂太危险了，我无法独自收集证据，你能来到这里，真是命运的选择。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54340] = { id = 54340, Voice = '', Text = '我需要一些线索来丰富我的故事，激发我的灵感。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54339] = { id = 54339, Voice = '', Text = '这段时间，我在玩具工厂里发现一些遗迹，有关于几千年前的一段往事。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54338] = { id = 54338, Voice = '', Text = '我一直在大陆上找寻有关于神族的事迹，希望为那些神话谱写诗篇。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54337] = { id = 54337, Voice = '', Text = '请允许我做一个简短的自我介绍，我叫做克里斯，是一名四处游历的吟游诗人。', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54336] = { id = 54336, Voice = '', Text = '日安，冒险者！', Speaker = 6987, Option = '', Action = _EmptyTable,}, 
	[54230] = { id = 54230, Voice = '', Text = '我已经收到你的50个冰之心，让我开始为你讲故事吧！', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54229] = { id = 54229, Voice = '', Text = '你身上没有50个冰之心，请找到50个冰之心再来找我。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54228] = { id = 54228, Voice = '', Text = '但据说马特当年收养的那个婴儿学习了马特所有的本领，重开了一家马戏团，名叫红鼻子马戏团。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54227] = { id = 54227, Voice = '', Text = '因为人们都不喜欢这个丑陋的小老头，欢乐马戏团的名气也就渐渐在米德加尔特大陆消失匿迹。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54226] = { id = 54226, Voice = '', Text = '从那天起，英俊潇洒的马特消失了，马戏团多了一位驼背的小丑表演者。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54225] = { id = 54225, Voice = '', Text = '老头的嘴中却发出了自己合作多年的伙伴马特的声音。“是我！唐！”', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54224] = { id = 54224, Voice = '', Text = '第二天清晨，唐被同伴的尖叫声唤醒，站在自己面前的是一个丑陋无比的驼背老人。唐惊恐的询问眼前的人是谁。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54223] = { id = 54223, Voice = '', Text = '唐想起了拉链熊的告诫，劝马特听从预言，但马特本来就不相信那个愚蠢的预言。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54222] = { id = 54222, Voice = '', Text = '年轻好面子的马特夸下海口，称自己将负责把这个幸存的孤儿抚养成人，并教会他自己的所有本领。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54221] = { id = 54221, Voice = '', Text = '人们唏嘘，惊呼，少女们同情舞者的经历，簇拥着马特，问马特愿不愿意收养这个可怜的婴儿。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54220] = { id = 54220, Voice = '', Text = '因为舞者的死，让马戏团一时名声大噪，来看演出的人比往年更多了。这个幸存的孩子却让唐有些不知所措。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54219] = { id = 54219, Voice = '', Text = '从舞者的身上搜出了一封遗书，原来舞者怀了贵族孩子却遭到了抛弃，绝望的她选择在聚光灯下结束自己年轻的生命。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54218] = { id = 54218, Voice = '', Text = '惊愕的团长唐慌忙的上前查看，舞者早已断了气息。但她怀里的婴儿轻声啼哭着，似乎毫发无伤！', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54217] = { id = 54217, Voice = '', Text = '她轻轻的从十五米的高空纵身一跃而起，轻盈的像一只燕子，一瞬间，舞台被鲜血染成了红色。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54216] = { id = 54216, Voice = '', Text = '冒险者，我们马上开始我们的故事。舞者的手上居然怀抱着一个婴儿，在聚光灯下舞者的蓝眼睛里满是绝望。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54215] = { id = 54215, Voice = '', Text = '冒险者，这冰之心的数量不够，没找齐我可是不会再说一个字，你还是再找一些来吧。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54214] = { id = 54214, Voice = '', Text = '你愿意把50个冰之心交给我吗？', Speaker = 1597, Option = '{我愿意,7},{我不愿意,8}', Action = _EmptyTable,}, 
	[54213] = { id = 54213, Voice = '', Text = '让我看看你收集到的冰之心。', Speaker = 1597, Option = '好的', Action = _EmptyTable,}, 
	[54212] = { id = 54212, Voice = '', Text = '冒险者，想知道舞者之后做了什么就帮我带些冰之心来吧。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54211] = { id = 54211, Voice = '', Text = '但这位舞者的登场，却让在场的所有观众惊奇的瞪大了双眼。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54210] = { id = 54210, Voice = '', Text = '华丽的马戏之夜在一串欢乐急促的鼓声中开始了，妙曼的舞者从十五米高的舞台上闪亮登场。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54209] = { id = 54209, Voice = '', Text = '唐看中舞者的才华，用高价聘请了舞者，并将其排在第一位登场表演。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54208] = { id = 54208, Voice = '', Text = '几年后的这天，马戏团里来了一位才华横溢的舞者，银发碧眼，眼里还带着少许忧郁。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54207] = { id = 54207, Voice = '', Text = '唐将自己的奇遇告诉了马特，马特一脸不屑的表示不相信自己好朋友说的话。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54206] = { id = 54206, Voice = '', Text = '说罢拉链熊便扭着它笨重的屁股一摇一摆的出了帐篷，等唐掀开帐帘一看，拉链熊竟消失了。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54205] = { id = 54205, Voice = '', Text = '“这将只是一个开始，我劝你们不要收养这个孩子，不然将会卷进诅咒的深渊。”', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54204] = { id = 54204, Voice = '', Text = '“这孩子命中注定将会非比寻常，他会带给你们马戏团名誉和荣耀！”', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54203] = { id = 54203, Voice = '', Text = '“在不久的将来，将会有个孤儿流落到你们马戏团。”拉链熊长长的叹了口气。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54202] = { id = 54202, Voice = '', Text = '拉链熊为了表达自己的谢意，愿意送给唐一条能够影响马戏团未来的告诫。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54201] = { id = 54201, Voice = '', Text = '心地善良的唐将拉链熊从笼子里放了出来。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54199] = { id = 54199, Voice = '', Text = '拉链熊抓住唐的手，眼泪从它忽闪忽闪的大眼睛中流了出来。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54198] = { id = 54198, Voice = '', Text = '那么我就继续讲欢乐马戏团的故事了！', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54197] = { id = 54197, Voice = '', Text = '圣诞村就是需要你这样善良勇敢的人！', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54196] = { id = 54196, Voice = '', Text = '冒险者，想知道拉链熊说了什么吗，那么勇敢的你先帮我消灭些魔物吧。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54195] = { id = 54195, Voice = '', Text = '但没想到的是，那只拉链熊忽然一把抓住唐的手！竟然开口说起了话！！', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54194] = { id = 54194, Voice = '', Text = '唐以为是马特新抓进来的，很兴奋地跑过去，还试图摸了摸它的毛。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54193] = { id = 54193, Voice = '', Text = '在后台唐看到了一只被关在笼子里的拉链熊，可他却从未见过这只拉链熊。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54192] = { id = 54192, Voice = '', Text = '演出结束后，英俊的马特左拥右抱着几个姑娘在小酒馆里畅饮，低调的唐则像往常一样，来到后台收拾演出道具。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54191] = { id = 54191, Voice = '', Text = '那是一年一度的圣诞节，两人的演出在姜饼城得到了观众热烈的掌声。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54190] = { id = 54190, Voice = '', Text = '四面八方的人们都慕名而来只为两人精彩绝伦的马戏表演。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54189] = { id = 54189, Voice = '', Text = '欢乐马戏团在米德加尔特的各个城市巡回，成功的表演让两人走遍了米德加尔特的各个角落。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54188] = { id = 54188, Voice = '', Text = '两个年轻人一身的本领，都希望做出一番事业来。便一起创办了欢乐马戏团。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54187] = { id = 54187, Voice = '', Text = '唐的性格内向但为人却十分稳重，马特英俊潇洒玩世不恭总有许多鬼点子。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54186] = { id = 54186, Voice = '', Text = '马特和唐是一对亲如手足的好朋友。两人都是被抛弃的孤儿，互相照顾在同一条街上长大。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54185] = { id = 54185, Voice = '', Text = '今天，我们就来说说这个闻名圣诞村的马戏团——欢乐马戏团。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54184] = { id = 54184, Voice = '', Text = '非常好，这个故事和马戏团可有着密不可分的关系。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54183] = { id = 54183, Voice = '', Text = '今天我们要讲的故事和城里的马戏团有关，你能帮我带来一张马戏团的照片吗？然后我们就可以开始我们的故事了。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[54182] = { id = 54182, Voice = '', Text = '童话般的冰雪世界圣诞村有着许多奇幻而神奇的故事，冒险者，你想听吗？', Speaker = 1597, Option = '{我想听,2},{我有空再听吧,1}', Action = _EmptyTable,}, 
	[54147] = { id = 54147, Voice = '', Text = '姜饼城真的好美啊……', Speaker = 6985, Option = '', Action = _EmptyTable,}, 
	[54146] = { id = 54146, Voice = '', Text = '啊，小幸运和小勇气，两位炼金术士的守护是他们幸运和勇气的来源。', Speaker = 6985, Option = '', Action = _EmptyTable,}, 
	[54145] = { id = 54145, Voice = '', Text = '而他的徒弟也继承了他的善良，收养了小幸运和小勇气，并对他们倾注了自己毕生的爱意。', Speaker = 6985, Option = '', Action = _EmptyTable,}, 
	[54144] = { id = 54144, Voice = '', Text = '飞利浦大师不仅在炼金术上造诣深厚，还拥有金子一般的心。他牺牲了自己的生命拯救了两个可怜的生命。', Speaker = 6985, Option = '', Action = _EmptyTable,}, 
	[54143] = { id = 54143, Voice = '', Text = '这本日记就作为谢礼送给你了，希望你好好保存哦。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54142] = { id = 54142, Voice = '', Text = '感谢你帮我收集了这么多线索，让我了解到这里的历史。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54141] = { id = 54141, Voice = '', Text = '真令人感动，原来姜饼城还隐藏了这样一段往事啊，看着他们一家人幸福的生活我就放心了。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54140] = { id = 54140, Voice = '', Text = '没想到时间过得这么快，一眨眼十几年就过去了，我有点开始明白老师拼了命也要救孩子的心理了，姜饼城今晚的月色真美啊……', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[54139] = { id = 54139, Voice = '', Text = '我要自己把香水送到小幸运手上吗？如果中途哭出来会不会很丢脸，要不然做一个留声器，到时候我就装病让小勇气帮我送礼物。', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[54138] = { id = 54138, Voice = '', Text = '东方的茉莉什么时候能送到，提炼精油的时间要三个月，不知道能不能赶上婚礼。', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[54137] = { id = 54137, Voice = '', Text = '（日记本丢失几页）', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[54136] = { id = 54136, Voice = '', Text = '恩，也许可以买一副好用的手套，当初我刚成为炼金术士的时候，因为没有经验导致了手部被灼伤，现在还经常疼痛。', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[54135] = { id = 54135, Voice = '', Text = '我在瞎担心些什么，小勇气一直都很努力，肯定能通过的！让我想想给他送一份什么礼物好呢？', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[54134] = { id = 54134, Voice = '', Text = '今天是小勇气第一次离开家呢，艾尔帕兰的气候他还习惯吗？不知道炼金术士的考试对他来说是不是太难了。', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[54133] = { id = 54133, Voice = '', Text = '（日记本丢失几页）', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[54132] = { id = 54132, Voice = '', Text = '不如在城中做一个大篝火吧，这样小幸运生病了，也有个温暖的地方可以让她舒服一点，希望孩子们喜欢。', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[54131] = { id = 54131, Voice = '', Text = '还好小勇气一直都很懂事，一直在照顾妹妹。我是不是应该辞职呢？', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[54130] = { id = 54130, Voice = '', Text = '是不是小时候的灾难让她的身体不好？炼金协会的工作太忙了，我实在没时间照顾她。', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[54129] = { id = 54129, Voice = '', Text = '唉，最近都没有好好睡觉，小幸运又生病了，都已经十岁了，还是这么瘦弱。', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[54128] = { id = 54128, Voice = '', Text = '让我为你读一读日记本最后的内容。', Speaker = 6986, Option = '好的', Action = _EmptyTable,}, 
	[54127] = { id = 54127, Voice = '', Text = '之后的日记都是不连贯的，时间太久远，遗失了十几页。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54126] = { id = 54126, Voice = '', Text = '原来小幸运和小勇气是阿克米为两个小孩起得名字，有了你的线索我才能分析日记内容。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54125] = { id = 54125, Voice = '', Text = '当初我操作炼金道具不熟练的时候，双手上可都是药水腐蚀的痕迹呢！', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54124] = { id = 54124, Voice = '', Text = '啊，阿克米对儿子可真好，这幅手套的价格对于炼金术士来说非常昂贵。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54123] = { id = 54123, Voice = '', Text = '我很想念你。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54122] = { id = 54122, Voice = '', Text = '哈哈，爸爸开玩笑的，望你一切顺利，有时间多回家。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54121] = { id = 54121, Voice = '', Text = '地龙皮可以阻挡强酸和强碱的腐蚀，希望你好好使用，不要让炼金药水灼伤你的双手。好好保管，儿子！这可是很贵呢！', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54120] = { id = 54120, Voice = '', Text = '前段时间爸爸的朋友做了一副手套，我想到你考过了炼金术士可能会需要，就买下来了。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54119] = { id = 54119, Voice = '', Text = '如果不能适应就早点回家，虽然学习炼金术很重要，但是你的身体健康比炼金术更重要。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54118] = { id = 54118, Voice = '', Text = '跟姜饼城不同，艾尔帕兰的气候湿润多雨，温度变化无常，你要注意加减衣物。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54117] = { id = 54117, Voice = '', Text = '艾尔帕兰的生活还习惯吗？那里是爸爸出生的地方，围绕整个城市的运河伴随了我的成长。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54116] = { id = 54116, Voice = '', Text = '亲爱的小勇气，听说你考上了炼金术士，恭喜你，你是爸爸的骄傲！', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54115] = { id = 54115, Voice = '', Text = '是一封信“赠给我的小勇气”。看来是送给别人的，让我读一读信吧？', Speaker = 6986, Option = '{读吧,6},{不想听,5}', Action = _EmptyTable,}, 
	[54114] = { id = 54114, Voice = '', Text = '这个可能是阿克米的手套吧，咦，里面还有折叠好的纸张。让我看看……', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54113] = { id = 54113, Voice = '', Text = '地龙皮最适合做炼金术士的工作手套，因为，它非常的柔韧，并且不受强酸和强碱的腐蚀。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54112] = { id = 54112, Voice = '', Text = '什么?地龙当然不是巨龙，但是也是一种非常危险的生物。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54111] = { id = 54111, Voice = '', Text = '让我看看你找到了些什么！一副地龙皮手套，冒险者你知道这个多么稀有吗？', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54110] = { id = 54110, Voice = '', Text = '获得了一双质地古怪的手套。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54109] = { id = 54109, Voice = '', Text = '糟糕，触碰了盒子的机关！吸引了一些绿饼人聚过来！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54108] = { id = 54108, Voice = '', Text = '礼品盒的缝隙中闪动着光芒，要打开它吗？', Speaker = 0, Option = '{打开,3},{没兴趣,2}', Action = _EmptyTable,}, 
	[54107] = { id = 54107, Voice = '', Text = '真是伟大的父亲！', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54106] = { id = 54106, Voice = '', Text = '时间改变了所有的事情，曾经阿克米还因为他老师原因怨恨两个小孩，随着朝夕的相处，他已经全心全意的照顾小女儿了。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54105] = { id = 54105, Voice = '', Text = '爸爸爱你。', Speaker = 5444, Option = '', Action = _EmptyTable,}, 
	[54104] = { id = 54104, Voice = '', Text = '成为一名优秀的妻子，可以和丈夫一起支撑起自己的家庭。', Speaker = 5444, Option = '', Action = _EmptyTable,}, 
	[54103] = { id = 54103, Voice = '', Text = '爸爸把它送给你，希望以后没有我的日子，你可以平静的面对生活中的挫折', Speaker = 5444, Option = '', Action = _EmptyTable,}, 
	[54102] = { id = 54102, Voice = '', Text = '这瓶茉莉香水是爸爸送给你的结婚礼物，茉莉的香味可以使人心情宁静。', Speaker = 5444, Option = '', Action = _EmptyTable,}, 
	[54101] = { id = 54101, Voice = '', Text = '爸爸对你好的日子不多了，以后会有另外一个男人照顾你的一生。', Speaker = 5444, Option = '', Action = _EmptyTable,}, 
	[54100] = { id = 54100, Voice = '', Text = '想当初，我还曾经拿假的饼干人欺骗你和你哥哥，现在想一想有点为这件事情后悔。', Speaker = 5444, Option = '', Action = _EmptyTable,}, 
	[54099] = { id = 54099, Voice = '', Text = '没想到转眼间，你就已经可以嫁人了，真希望此时此刻时间可以停滞，你可以永远是我的宝贝公主。', Speaker = 5444, Option = '', Action = _EmptyTable,}, 
	[54098] = { id = 54098, Voice = '', Text = '时光飞逝，第一次见到你的时候，你还是个只会哭哭啼啼的小姑娘。', Speaker = 5444, Option = '', Action = _EmptyTable,}, 
	[54097] = { id = 54097, Voice = '', Text = '亲爱的小幸运，能看见你穿婚纱的这天，爸爸很欣慰。', Speaker = 5444, Option = '', Action = _EmptyTable,}, 
	[54096] = { id = 54096, Voice = '', Text = '你怎么一点好奇心都没有……', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54095] = { id = 54095, Voice = '', Text = '让我们听听这个留声器的内容吧？', Speaker = 6986, Option = '{好的,6},{不想听,5}', Action = _EmptyTable,}, 
	[54094] = { id = 54094, Voice = '', Text = '一个按钮，这……天呐，莫非这个茉莉瓶子是留声器吗？这么小的瓶子是怎么装下炼金法阵的？令人震惊！', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54093] = { id = 54093, Voice = '', Text = '咦，我摸到了什么？', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54092] = { id = 54092, Voice = '', Text = '无数朵茉莉花才能提取一滴精油，看这瓶茉莉香水的纯度，一定是经验丰富的炼金术士努力数月的成果。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54091] = { id = 54091, Voice = '', Text = '你当然不可能知道，这是遥远的东方大陆上生长的一种气味芬芳的植物。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54090] = { id = 54090, Voice = '', Text = '让我看看你找到了什么！一瓶茉莉香水，冒险者你知道茉莉这种植物吗？', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54089] = { id = 54089, Voice = '', Text = '获得了一个精美的瓶子，散发出阵阵的香味。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54088] = { id = 54088, Voice = '', Text = '糟糕，触碰了某些机关！吸引了一些魔物！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54087] = { id = 54087, Voice = '', Text = '礼品盒传来阵阵茉莉清香，要打开它吗？', Speaker = 0, Option = '{打开,3},{没兴趣,2}', Action = _EmptyTable,}, 
	[54086] = { id = 54086, Voice = '', Text = '出现解锁【阿克米的爱】的关键道具。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54085] = { id = 54085, Voice = '', Text = '你可以帮我收集更多的线索吗？', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54084] = { id = 54084, Voice = '', Text = '后面的日记被撕去了好几页，让我更难以理解。而且他还提到了小幸运和小勇气，那是什么？', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54083] = { id = 54083, Voice = '', Text = '谢谢你冒险者，你的线索让我理清了思路。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54082] = { id = 54082, Voice = '', Text = '之前我都没有看懂他提到饼干和暴脾气的玩具是什么。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54081] = { id = 54081, Voice = '', Text = '等发工资了，买点真正的糖果吧。', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[54080] = { id = 54080, Voice = '', Text = '他们也挺可怜的吧，这么小就失去了所有的亲人，老师也离开了。在这么寒冷的地方，没有我他们估计活不久。', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[54079] = { id = 54079, Voice = '', Text = '居然就这样轻易的接受了这么暴躁的玩具，还相处的挺好的，小鬼头和玩具真有共同语言……', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[54078] = { id = 54078, Voice = '', Text = '（翻开后几页）', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[54077] = { id = 54077, Voice = '', Text = '不管了，还是送出去吧，我也没有时间和精力再做点别的了，老师离开后炼金公会的工作太多了。', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[54076] = { id = 54076, Voice = '', Text = '真是失败，新做的玩具脾气太暴躁了，连我都敢骂，不好意思送给他们了。', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[54075] = { id = 54075, Voice = '', Text = '要不要再做一个盒子装起来了，反正也不是什么麻烦事情……唔，再加点音乐吧。', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[54074] = { id = 54074, Voice = '', Text = '做个小玩意来陪你们说话吧，这样我也可以专心工作了。', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[54073] = { id = 54073, Voice = '', Text = '（翻开后几页）', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[54072] = { id = 54072, Voice = '', Text = '怎么又天天哭了，烦死了，让我想想还可以做点什么。', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[54071] = { id = 54071, Voice = '', Text = '果然被当成饼干了，这下没有哭了，小鬼头挺好骗的。', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[54070] = { id = 54070, Voice = '', Text = '突然有了个好点子。哈哈哈哈，感觉自己好坏啊……', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[54069] = { id = 54069, Voice = '', Text = '（翻开后几页）', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[54068] = { id = 54068, Voice = '', Text = '整天哭哭哭，别对着我哭，我也没办法，我小时候比他们乖多了吧，真生气。', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[54067] = { id = 54067, Voice = '', Text = '不如，把他们送到艾尔帕兰找合适的人收养吧……可是老师专门嘱咐过要好好教导他们炼金术，真头痛啊。', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[54066] = { id = 54066, Voice = '', Text = '真头痛，我只是一个炼金术士，连女朋友都没有的炼金术士。我哪里知道怎么照顾小孩呢？', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[54065] = { id = 54065, Voice = '', Text = '这种感觉真是奇妙啊，让我们接着读一读日记吧。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54064] = { id = 54064, Voice = '', Text = '看着日记，让我感觉300年前的传奇人物，在我眼前活过来了一样……', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54063] = { id = 54063, Voice = '', Text = '做了一个假的糖果，真的合适吗？哈哈哈，没有想到阿克米大师年轻的时候是这样的人呢。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54062] = { id = 54062, Voice = '', Text = '虽然心中有些怨言，阿克米还是按照老师的吩咐收养了两个小孩，而且认真照顾他们了。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54061] = { id = 54061, Voice = '', Text = '秘密还沉睡于历史之中，等待我们发掘。', Speaker = 6985, Option = '', Action = _EmptyTable,}, 
	[54060] = { id = 54060, Voice = '', Text = '难道说是阿克米为两个小孩起的名字吗？', Speaker = 6985, Option = '', Action = _EmptyTable,}, 
	[54059] = { id = 54059, Voice = '', Text = '日记里还提到了小幸运和小勇气，这两个名字代表的是什么含义呢？', Speaker = 6985, Option = '', Action = _EmptyTable,}, 
	[54058] = { id = 54058, Voice = '', Text = '虽然，他只是一个年轻的单身男人，没有什么照顾小孩子的经验，但是看上去他的工作还算顺利。', Speaker = 6985, Option = '', Action = _EmptyTable,}, 
	[54057] = { id = 54057, Voice = '', Text = '啊，我听波特瑞说了，你找到了四个重要线索，现在我们已经知道了阿克米接受了养子和养女。', Speaker = 6985, Option = '', Action = _EmptyTable,}, 
	[54056] = { id = 54056, Voice = '', Text = '我还需要更多的线索才能分析日记内容。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54055] = { id = 54055, Voice = '', Text = '看来阿克米在认真的履行他的职责，这样我也能稍稍安心。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54054] = { id = 54054, Voice = '', Text = '让我仔细瞧瞧，打开还有音乐播放，这是阿克米为两个小孩制作的礼物。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54053] = { id = 54053, Voice = '', Text = '一个装礼品的盒子啊，看上去挺精美的呢！', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54052] = { id = 54052, Voice = '', Text = '动作真快啊冒险者，让我看看你找到了些什么……唔！', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54051] = { id = 54051, Voice = '', Text = '话尚未说完，礼品盒就不再发出声音，静静的躺在地上。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54050] = { id = 54050, Voice = '', Text = '那可真是一……一段……美好的……时……', Speaker = 5443, Option = '', Action = _EmptyTable,}, 
	[54049] = { id = 54049, Voice = '', Text = '除了我，阿克米还炼制了饼干人和玩具士兵，他们也在这个工厂里呢，如果你见到他们，请代我问好。', Speaker = 5443, Option = '', Action = _EmptyTable,}, 
	[54048] = { id = 54048, Voice = '', Text = '哦，别听他在日记里胡说，他是一个刀子嘴豆腐心的烂好人。如果不爱他们，他何必炼制我作为礼物送给小孩呢？', Speaker = 5443, Option = '', Action = _EmptyTable,}, 
	[54047] = { id = 54047, Voice = '', Text = '阿克米是一位心地善良的炼金术士。', Speaker = 5443, Option = '这样啊', Action = _EmptyTable,}, 
	[54046] = { id = 54046, Voice = '', Text = '在弥留之际，可以跟比我小300岁的冒险者说说话，真令人开心。', Speaker = 5443, Option = '', Action = _EmptyTable,}, 
	[54045] = { id = 54045, Voice = '', Text = '我体内的炼金法阵马上就要失效了，我将会变成一个普通的礼品盒。', Speaker = 5443, Option = '', Action = _EmptyTable,}, 
	[54044] = { id = 54044, Voice = '', Text = '啊……阿克米，我的创造者，我很想念他。好久没跟人这么愉快的交谈了。', Speaker = 5443, Option = '', Action = _EmptyTable,}, 
	[54043] = { id = 54043, Voice = '', Text = '看来你没有认真在跟我聊天，请好好听清我说的话，再来找我吧。', Speaker = 5443, Option = '', Action = _EmptyTable,}, 
	[54042] = { id = 54042, Voice = '', Text = '有时候，身为一个敏感忧郁的灵魂，我会觉得自己思考的太多了，你会这样认为吗？', Speaker = 5443, Option = '{你会思考？,5},{难以置信！,5},{需要思考！,6}', Action = _EmptyTable,}, 
	[54041] = { id = 54041, Voice = '', Text = '年轻人，你看，我这个样子，知道我经常被用来做什么吗？', Speaker = 5443, Option = '{盛放礼物,0},{唱歌跳舞,5},{吃饭睡觉,5}', Action = _EmptyTable,}, 
	[54040] = { id = 54040, Voice = '', Text = '要聊一聊？那就聊一聊吧，我可没什么好话想跟你说。', Speaker = 5443, Option = '', Action = _EmptyTable,}, 
	[54039] = { id = 54039, Voice = '', Text = '没见过你这么罗嗦的人，好吧，我确实知道一些事情，可是我为什么要告诉你？', Speaker = 5443, Option = '', Action = _EmptyTable,}, 
	[54038] = { id = 54038, Voice = '', Text = '会说话的礼品盒怎么了……现在的冒险者都是这么孤陋寡闻吗？', Speaker = 5443, Option = '了解玩具工厂吗', Action = _EmptyTable,}, 
	[54037] = { id = 54037, Voice = '', Text = '啊啊啊啊……痛痛痛痛痛！', Speaker = 5443, Option = '你会说话？', Action = _EmptyTable,}, 
	[54036] = { id = 54036, Voice = '', Text = '玩具堆里卡住了一个礼品盒，要把它拿出来吗？', Speaker = 0, Option = '{拿出来,3},{没兴趣,2}', Action = _EmptyTable,}, 
	[54035] = { id = 54035, Voice = '', Text = '我还需要更多的线索才能分析日记内容。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54034] = { id = 54034, Voice = '', Text = '阿克米遵守了他老师的话，收养了两个小孩并照顾他们，只是好像不是很情愿，日记本里也是这样记载的。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54033] = { id = 54033, Voice = '', Text = '这是阿克米为两个小孩制作的玩具吗？看起来做工很粗糙啊，这个饼干人身上还有牙印……要知道炼金术士可以制作出利刃都无法破坏的物品。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54032] = { id = 54032, Voice = '', Text = '动作真快啊冒险者，让我看看你找到了些什么……唔！', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54031] = { id = 54031, Voice = '', Text = '饼干人停止运转了，在你手中渐渐冰冷。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54030] = { id = 54030, Voice = '', Text = '不用安慰我，冒险者……可以把我的开关关掉吗？如果没有人愿意找我玩，我也不想一个人这么孤独的活着。', Speaker = 5442, Option = '', Action = _EmptyTable,}, 
	[54029] = { id = 54029, Voice = '', Text = '可是最近都没有人来玩具工厂玩了，我很孤独，时常会怀念过去的日子。', Speaker = 5442, Option = '别难过', Action = _EmptyTable,}, 
	[54028] = { id = 54028, Voice = '', Text = '姜饼城的小孩都是我看着长大的，每个人第一次见到我都会张开嘴巴，唉，都怪阿克米那个混蛋，让我看上去那么像饼干。', Speaker = 5442, Option = '', Action = _EmptyTable,}, 
	[54027] = { id = 54027, Voice = '', Text = '你看，我身上的伤……都是那个时候被咬出来的，气死了……', Speaker = 5442, Option = '', Action = _EmptyTable,}, 
	[54026] = { id = 54026, Voice = '', Text = '我是第一个被炼制出来的玩具，也不知道阿克米那个家伙在想些什么，居然做个假的饼干，害得我被两个小孩咬了好几口。', Speaker = 5442, Option = '', Action = _EmptyTable,}, 
	[54025] = { id = 54025, Voice = '', Text = '既然你想知道300年前的事情，我就跟你讲一讲吧。那时候阿克米看两个小孩经常哭，心烦意乱之下就随便用炼金术给他们制作玩具。', Speaker = 5442, Option = '', Action = _EmptyTable,}, 
	[54024] = { id = 54024, Voice = '', Text = '我也知道自己的性格有点别扭，感谢你体谅我，冒险者。', Speaker = 5442, Option = '', Action = _EmptyTable,}, 
	[54023] = { id = 54023, Voice = '', Text = '我跟你没什么好说的，你根本没有好好听我说话！', Speaker = 5442, Option = '', Action = _EmptyTable,}, 
	[54022] = { id = 54022, Voice = '', Text = '姜饼城非常美对不对，这里只有一个季节，你知道是什么季节吗？', Speaker = 5442, Option = '{春天,5},{夏天,5},{冬天,6}', Action = _EmptyTable,}, 
	[54021] = { id = 54021, Voice = '', Text = '这个玩具工厂是不是很华丽，它可是一位炼金大师的杰作！你知道是谁建立的吗？', Speaker = 5442, Option = '{太阳神弗雷,5},{炼金师飞利浦,0},{梦罗克,5}', Action = _EmptyTable,}, 
	[54020] = { id = 54020, Voice = '', Text = '还算有点礼貌。既然你这么诚恳，那陪我聊一聊吧。', Speaker = 5442, Option = '', Action = _EmptyTable,}, 
	[54019] = { id = 54019, Voice = '', Text = '300年前关你什么事请，都已经打算吃我了，我什么都不会跟你说的。', Speaker = 5442, Option = '我错了', Action = _EmptyTable,}, 
	[54018] = { id = 54018, Voice = '', Text = '当然不是饼干，你见过会说话的饼干吗？真是的300年前的人把我当成了饼干，300年后的人又把我当成了饼干！', Speaker = 5442, Option = '300年前？', Action = _EmptyTable,}, 
	[54017] = { id = 54017, Voice = '', Text = '啊，居然对我张嘴巴，你想做些什么！', Speaker = 5442, Option = '', Action = _EmptyTable,}, 
	[54016] = { id = 54016, Voice = '', Text = '礼品盒里有一个看上去很美味的小饼干，要吃下去吗？', Speaker = 0, Option = '{吃下去,3},{没兴趣,2}', Action = _EmptyTable,}, 
	[54015] = { id = 54015, Voice = '', Text = '我还需要更多的线索才能分析日记内容。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54014] = { id = 54014, Voice = '', Text = '虽然说着不愿意照顾两个孩子，阿克米还是很负责的嘛，也不辜负飞利浦大师对他的期望。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54013] = { id = 54013, Voice = '', Text = '看来这个玩具士兵是阿克米为两个小孩制作的玩具了。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54012] = { id = 54012, Voice = '', Text = '啊，抱歉，我又岔开话题了，我就是有这个毛病，老师都说过我好多次了。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54011] = { id = 54011, Voice = '', Text = '哦？你也有同感吗？哈哈，看来你的父母也很严厉呀……', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54010] = { id = 54010, Voice = '', Text = '这种想法真是令人沮丧，无趣的童年啊……我的同事小时候都有很多玩具呢！', Speaker = 6986, Option = '是的', Action = _EmptyTable,}, 
	[54009] = { id = 54009, Voice = '', Text = '一个玩具士兵！太棒了，我小时候最喜欢玩具士兵了，可是爸爸妈妈怕影响我学习炼金术，不愿意买给我。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54008] = { id = 54008, Voice = '', Text = '动作真快啊冒险者，让我看看你找到了些什么……唔！', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[54007] = { id = 54007, Voice = '', Text = '玩具士兵话音完毕就闭上了双眼。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54006] = { id = 54006, Voice = '', Text = '再见，冒险者……', Speaker = 5441, Option = '', Action = _EmptyTable,}, 
	[54005] = { id = 54005, Voice = '', Text = '你想知道我的职责啊？我可是个守护者，要保护两个可爱的孩子呢！', Speaker = 5441, Option = '', Action = _EmptyTable,}, 
	[54004] = { id = 54004, Voice = '', Text = '……冒险者，既然爸爸已经去世了，那我的职责也终止。我不想待在人类的世界了，很久没人来找我玩了。', Speaker = 5441, Option = '你的职责？', Action = _EmptyTable,}, 
	[54003] = { id = 54003, Voice = '', Text = '……已经去世了吗？原来人类的寿命这么短暂啊，我只是炼金术创造的玩具，我不懂人类。', Speaker = 5441, Option = '', Action = _EmptyTable,}, 
	[54002] = { id = 54002, Voice = '', Text = '这些玩具都是阿克米爸爸制作的！爸爸好久没有来看我了。你知道他去哪里了吗？', Speaker = 5441, Option = '他离世了', Emoji = 2, Action = _EmptyTable,}, 
	[54001] = { id = 54001, Voice = '', Text = '原来如此，你是来调查的啊，调查什么呢？', Speaker = 5441, Option = '玩具工厂', Action = _EmptyTable,}, 
	[54000] = { id = 54000, Voice = '', Text = '没想到你还有两下子。但是也不能抢我的眼睛啊！', Speaker = 5441, Option = '我只是调查', Action = _EmptyTable,}, 
	[53999] = { id = 53999, Voice = '', Text = '道歉？道歉是没有用的，留下你的遗言就出手吧！', Speaker = 5441, Option = '', Action = _EmptyTable,}, 
	[53998] = { id = 53998, Voice = '', Text = '住手！无礼之徒，你想对我的眼睛做什么？', Speaker = 5441, Option = '', Action = _EmptyTable,}, 
	[53997] = { id = 53997, Voice = '', Text = '地板缝中间有一颗闪闪发光的东西……要捡起来吗？', Speaker = 0, Option = '{拾取,3},{没兴趣,2}', Action = _EmptyTable,}, 
	[53996] = { id = 53996, Voice = '', Text = '哈哈，谢谢你的夸奖。说回正事，没有线索后面的日记变得难以理解，我需要更多的线索。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[53995] = { id = 53995, Voice = '', Text = '你会觉得他很伟大吗？冒险者，炼金术士的工作繁琐而精细，不能出一点差错。但是想到能一手打造这样的传奇，我们就心甘情愿，这样的心情你可以理解吗？', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[53994] = { id = 53994, Voice = '', Text = '看日记的内容，原来是为了给收养的小孩提供一个家园，还为他们建造了玩具工厂。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[53993] = { id = 53993, Voice = '', Text = '但是却因为某些不知名的原因，关闭了自己的法阵，重新开放了姜饼城。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[53992] = { id = 53992, Voice = '', Text = '炼金协会有个传说，姜饼城是飞利浦大师无意之中发现的一块神奇的土地。因为这片土地的力量太强大，他制作了炼金法阵把整个城市藏了起来。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[53991] = { id = 53991, Voice = '', Text = '原来传说是真的……', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[53990] = { id = 53990, Voice = '', Text = '又是为了这两个小鬼头，做出这样的牺牲真的值得吗？居然还恳请我收养他们，我恨这两个小鬼，如果不是他们，我的老师也不会离世。', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[53989] = { id = 53989, Voice = '', Text = '前几个月的封印已经让他的生命快走到尽头，他也没有料想到这片土地的力量如此强大，所以利用最后的时间建造玩具工厂……', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[53988] = { id = 53988, Voice = '', Text = '老师居然拖着这样的身体又建立了一个玩具工厂！那些复杂的炼金结构会要了他的命！', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[53987] = { id = 53987, Voice = '', Text = '（翻开后几页）', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[53986] = { id = 53986, Voice = '', Text = '这几天老师一直背着我在做些什么，我有一点担心老师的身体，那两个小鬼头也是挺麻烦的。', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[53985] = { id = 53985, Voice = '', Text = '以后的姜饼城会永远停留在冬天，满天飘着飞雪，看上去还挺浪漫的。', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[53984] = { id = 53984, Voice = '', Text = '原来这几个月老师花费大量的心血把姜饼城的秘密封印起来了，难怪变得如此苍老。', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[53983] = { id = 53983, Voice = '', Text = '（翻开后几页）', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[53982] = { id = 53982, Voice = '', Text = '天呐，那这块土地的秘密不会被人发现吗？', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[53981] = { id = 53981, Voice = '', Text = '为了这两个孩子，他竟然决定打开姜饼城的通道，要让外界的人进入姜饼城照顾他们。', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[53980] = { id = 53980, Voice = '', Text = '老师对我说，他在北方的村庄里救了两个小孩，想把他们安顿在姜饼城。', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[53979] = { id = 53979, Voice = '', Text = '简直令人难以置信！前几个月我看见老师的时候，他还是神采奕奕的模样。今天在姜饼城看见他，竟然头发都白了。', Speaker = 5440, Option = '', Action = _EmptyTable,}, 
	[53978] = { id = 53978, Voice = '', Text = '这本日记是飞利浦的学生阿克米·曼的日记本，上面提到了这段往事，你自己看吧……', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[53977] = { id = 53977, Voice = '', Text = '一件麻烦的事情，原来是这样……', Speaker = 6986, Option = '发生了什么？', Action = _EmptyTable,}, 
	[53975] = { id = 53975, Voice = '', Text = '老师现在碰见了一件麻烦的事情需要你帮助，希望你可以尽快来一趟姜饼城，越快越好。', Speaker = 5439, Option = '', Action = _EmptyTable,}, 
	[53974] = { id = 53974, Voice = '', Text = '亲爱的阿克米，近来无恙？', Speaker = 5439, Option = '', Action = _EmptyTable,}, 
	[53973] = { id = 53973, Voice = '', Text = '（按下红色按钮）', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[53972] = { id = 53972, Voice = '', Text = '我们来试试看吧！', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[53971] = { id = 53971, Voice = '', Text = '只要按下中间的按钮就会记录周围的声音，再次按下按钮就可以播放声音，是不是很有趣？', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[53970] = { id = 53970, Voice = '', Text = '飞利浦大师是300多年前的炼金天才，这个小玩意是他众多发明的一个，叫做【波利留声器】。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[53969] = { id = 53969, Voice = '', Text = '抱歉，请原谅我这么失控，作为一个炼金术士看见了飞利浦大师创造的炼金玩具，我难免有点激动。', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[53968] = { id = 53968, Voice = '', Text = '啊！居然被你找到了这个，哈哈哈！', Speaker = 6986, Option = '', Action = _EmptyTable,}, 
	[53967] = { id = 53967, Voice = '', Text = '出现解锁【炼金术士的日记本】的关键道具，请把线索拿给波特瑞分析。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[53966] = { id = 53966, Voice = '', Text = '糟糕!触发了机关!', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[53965] = { id = 53965, Voice = '', Text = '看上去只是普通的波利玩具，中间有一个红色的按钮。', Speaker = 0, Option = '{拾取,3},{没兴趣,2}', Action = _EmptyTable,}, 
	[53964] = { id = 53964, Voice = '', Text = '我们需要更多线索来研究日记本里的故事，[c][ffff00]日记本在波特瑞手上[-][/c]，找到线索就可以对照日记内容分析了。', Speaker = 6985, Option = '', Action = _EmptyTable,}, 
	[53963] = { id = 53963, Voice = '', Text = '真头痛……日记本的内容记载的太混乱了，还丢失了好多页。', Speaker = 6985, Option = '', Action = _EmptyTable,}, 
	[53962] = { id = 53962, Voice = '', Text = '这件事关系到[c][ffff00]300多年前姜饼城[-][/c]的诞生，还有[c][ffff00]炼金大师飞利浦[-][/c]和他的徒弟[c][ffff00]阿克米·曼[-][/c]。', Speaker = 6985, Option = '', Action = _EmptyTable,}, 
	[53961] = { id = 53961, Voice = '', Text = '虽然看上去很不起眼，但是我们仔细阅读以后发现了日记本记载着一件惊人的事情。', Speaker = 6985, Option = '', Action = _EmptyTable,}, 
	[53960] = { id = 53960, Voice = '', Text = '哦，这本日记啊……前段时间，我和[c][ffff00]波特瑞[-][/c]在整理图书架的时候，这本日记正好掉在我们面前，我就随手翻了翻。', Speaker = 6985, Option = '', Action = _EmptyTable,}, 
	[53959] = { id = 53959, Voice = '', Text = '哎呀，不敢当，我只是一名普通的小角色啦，我也很荣幸认识你。', Speaker = 6985, Option = '', Action = _EmptyTable,}, 
	[53958] = { id = 53958, Voice = '', Text = '我们可不是普通的图书馆管理员哦，我是炼金协会内部图书馆的管理员，也是一名炼金术士。', Speaker = 6985, Option = '', Action = _EmptyTable,}, 
	[53957] = { id = 53957, Voice = '', Text = '还没有做自我介绍，我叫瑞斯奇，是一名图书馆管理员，旁边是[c][ffff00]我的同事波特瑞[-][/c]。', Speaker = 6985, Option = '', Action = _EmptyTable,}, 
	[53956] = { id = 53956, Voice = '', Text = '抱歉抱歉，我没看见你走过来，我正在研究这本日记呢。', Speaker = 6985, Option = '', Action = _EmptyTable,}, 
	[53955] = { id = 53955, Voice = '', Text = '啊！吓了我一大跳，你是从哪里冒出来的，冒险者？', Speaker = 6985, Option = '', Action = _EmptyTable,}, 
	[53608] = { id = 53608, Voice = '', Text = '我在你眼里看到了健康的光环！你的旅程才刚刚开始，愿你带着这份健康不变初心的走下去！', Speaker = 1381, Option = '', Action = _EmptyTable,}, 
	[53607] = { id = 53607, Voice = '', Text = '我在你眼里看到了爱情的甜蜜！你的旅程才刚刚开始，愿你带着这份爱情不变初心的走下去！', Speaker = 1381, Option = '', Action = _EmptyTable,}, 
	[53606] = { id = 53606, Voice = '', Text = '我在你眼里看到了财富的光辉！你的旅程才刚刚开始，愿你带着这份财富不变初心的走下去！', Speaker = 1381, Option = '', Action = _EmptyTable,}, 
	[53605] = { id = 53605, Voice = '', Text = '我在你眼里看到了荣耀的光芒！你的旅程才刚刚开始，愿你带着这份荣耀不变初心的走下去！', Speaker = 1381, Option = '', Action = _EmptyTable,}, 
	[53604] = { id = 53604, Voice = '', Text = '冒险者，一路走来，你最终收获了什么？', Speaker = 1381, Option = '{荣耀,2},{财富,3},{爱情,4},{健康,5}', Action = _EmptyTable,}, 
	[53603] = { id = 53603, Voice = '', Text = '看着你一路成长，我真的很欣慰。', Speaker = 1381, Option = '', Action = _EmptyTable,}, 
	[53602] = { id = 53602, Voice = '', Text = '信念是一种无坚不摧的力量，当你坚信自己能成功时，就必定能收获成功！', Speaker = 1381, Option = '没错', Action = _EmptyTable,}, 
	[53601] = { id = 53601, Voice = '', Text = '传说中的四叶草并不能帮人完全实现他的愿望，但能给予人信念。', Speaker = 1381, Option = '', Action = _EmptyTable,}, 
	[53600] = { id = 53600, Voice = '', Text = '孩子，你很善良，帮助了在困境中的人们。', Speaker = 1381, Option = '', Action = _EmptyTable,}, 
	[53599] = { id = 53599, Voice = '', Text = '谢谢你，是你给了我希望，总有一天姐姐和我都会收获我们想要的健康！', Speaker = 4257, Option = '', Action = _EmptyTable,}, 
	[53598] = { id = 53598, Voice = '', Text = '我长大了会努力赚钱给姐姐治病，姐姐看着长青的叶子说不定又会燃起对生命的希望！', Speaker = 4257, Option = '没错', Action = _EmptyTable,}, 
	[53597] = { id = 53597, Voice = '', Text = '你说的没错冒险者，我不该放弃。', Speaker = 4257, Option = '', Action = _EmptyTable,}, 
	[53596] = { id = 53596, Voice = '', Text = '这不是我想要的。', Speaker = 4257, Option = '', Action = _EmptyTable,}, 
	[53595] = { id = 53595, Voice = '', Text = '梅里亚想收获什么呢？', Speaker = 0, Option = '{荣耀,3},{财富,3},{爱情,3},{健康,0}', Action = _EmptyTable,}, 
	[53594] = { id = 53594, Voice = '', Text = '这片长青的树叶，将作为梅里亚姐姐的生命希望永远悬挂在枝头。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[53593] = { id = 53593, Voice = '', Text = '这样姐姐就永远看不到最后一片树叶的落下！', Speaker = 4257, Option = '交给我', Action = _EmptyTable,}, 
	[53592] = { id = 53592, Voice = '', Text = '请你帮我画一片叶脉青翠的树叶，挂在我家旁边的树枝上！', Speaker = 4257, Option = '', Action = _EmptyTable,}, 
	[53591] = { id = 53591, Voice = '', Text = '我不想姐姐走！她还有好多梦想没有完成。', Speaker = 4257, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[53590] = { id = 53590, Voice = '', Text = '秋天到了，她的身体也一天不如一天，有一天她甚至对我说“当窗口的树叶全部掉光时，就是我离开的时候。”', Speaker = 4257, Option = '真糟糕', Action = _EmptyTable,}, 
	[53589] = { id = 53589, Voice = '', Text = '姐姐得了一种非常难治愈的疾病，看了好多医生都无法治愈…', Speaker = 4257, Option = '', Action = _EmptyTable,}, 
	[53588] = { id = 53588, Voice = '', Text = '冒险者...姐姐最近病了，病的很重。', Speaker = 4257, Option = '姐姐怎么了', Emoji = 24, Action = _EmptyTable,}, 
	[53587] = { id = 53587, Voice = '', Text = '谢谢你，是你给了我提点，总有一天我会收获我想要的爱情！', Speaker = 1097, Option = '', Action = _EmptyTable,}, 
	[53586] = { id = 53586, Voice = '', Text = '我该放下自己的架子，主动给庞德写一封充满思念的信！', Speaker = 1097, Option = '没错', Action = _EmptyTable,}, 
	[53585] = { id = 53585, Voice = '', Text = '你说的没错冒险者，我不该在这里胡乱猜疑。', Speaker = 1097, Option = '', Action = _EmptyTable,}, 
	[53584] = { id = 53584, Voice = '', Text = '这不是我想要的。', Speaker = 1097, Option = '', Action = _EmptyTable,}, 
	[53583] = { id = 53583, Voice = '', Text = '爱丽丝想收获什么呢？', Speaker = 0, Option = '{荣耀,7},{财富,7},{爱情,0},{健康,7}', Action = _EmptyTable,}, 
	[53582] = { id = 53582, Voice = '', Text = '这是传说中那朵幸运的四叶草？', Speaker = 1097, Option = '', Action = _EmptyTable,}, 
	[53581] = { id = 53581, Voice = '', Text = '我为什么要相信你…', Speaker = 1097, Option = '', Action = _EmptyTable,}, 
	[53580] = { id = 53580, Voice = '', Text = '噢不，告诉我这不是真的。', Speaker = 1097, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[53578] = { id = 53578, Voice = '', Text = '实际上，我已经很久没有收到我的恋人，庞德的书信了。', Speaker = 1097, Option = '', Action = _EmptyTable,}, 
	[53577] = { id = 53577, Voice = '', Text = '冒险者…', Speaker = 1097, Option = '你怎么了？', Emoji = 12, Action = _EmptyTable,}, 
	[53576] = { id = 53576, Voice = '', Text = '谢谢你，是你给了我信心，总有一天我会收获我想要的财富！', Speaker = 1197, Option = '', Action = _EmptyTable,}, 
	[53575] = { id = 53575, Voice = '', Text = '我该改变自己的经验策略，抓住灵活的商机。', Speaker = 1197, Option = '没错', Action = _EmptyTable,}, 
	[53574] = { id = 53574, Voice = '', Text = '你说的没错冒险者，我不该放弃。', Speaker = 1197, Option = '', Action = _EmptyTable,}, 
	[53573] = { id = 53573, Voice = '', Text = '这不是我想要的。', Speaker = 1197, Option = '', Action = _EmptyTable,}, 
	[53572] = { id = 53572, Voice = '', Text = '水果商人想收获什么呢？', Speaker = 0, Option = '{荣耀,3},{财富,0},{爱情,3},{健康,3}', Action = _EmptyTable,}, 
	[53571] = { id = 53571, Voice = '', Text = '这是传说中那朵幸运的四叶草？', Speaker = 1197, Option = '', Emoji = 7, Action = _EmptyTable,}, 
	[53570] = { id = 53570, Voice = '', Text = '有了这些我可能可以熬过今天，但明天又怎么办呢。', Speaker = 1197, Option = '', Emoji = 15, Action = _EmptyTable,}, 
	[53569] = { id = 53569, Voice = '', Text = '今年西门的苹果和生梨都结的格外的好，你能帮我采一些回来吗…', Speaker = 1197, Option = '没问题', Action = _EmptyTable,}, 
	[53568] = { id = 53568, Voice = '', Text = '如果我这小商铺关了门，妻子一定会带着孩子离我而去的。', Speaker = 1197, Option = '别灰心', Action = _EmptyTable,}, 
	[53567] = { id = 53567, Voice = '', Text = '最近的货源真是越来越差了…', Speaker = 1197, Option = '', Emoji = 15, Action = _EmptyTable,}, 
	[53566] = { id = 53566, Voice = '', Text = '谢谢你，是你给了我勇气，总有一天我会收获我想要的荣耀！', Speaker = 5420, Option = '', Action = _EmptyTable,}, 
	[53565] = { id = 53565, Voice = '', Text = '只要不断磨练自己，我也会成为像你一样厉害的冒险者。', Speaker = 5420, Option = '没错', Action = _EmptyTable,}, 
	[53564] = { id = 53564, Voice = '', Text = '你说的没错冒险者，我不该放弃。', Speaker = 5420, Option = '', Action = _EmptyTable,}, 
	[53563] = { id = 53563, Voice = '', Text = '这不是我想要的。', Speaker = 5420, Option = '', Action = _EmptyTable,}, 
	[53562] = { id = 53562, Voice = '', Text = '艾斯想收获什么呢？', Speaker = 0, Option = '{荣耀,0},{财富,3},{爱情,3},{健康,3}', Action = _EmptyTable,}, 
	[53561] = { id = 53561, Voice = '', Text = '这是传说中那朵幸运的四叶草？', Speaker = 5420, Option = '', Emoji = 7, Action = _EmptyTable,}, 
	[53560] = { id = 53560, Voice = '', Text = '海上真是太危险了，看来我一辈子也做不了海上勇士了…', Speaker = 5420, Option = '', Action = _EmptyTable,}, 
	[53559] = { id = 53559, Voice = '', Text = '从哪里来的海怪！？！', Speaker = 5420, Option = '', Emoji = 18, Action = _EmptyTable,}, 
	[53558] = { id = 53558, Voice = '', Text = '我的天呐！！', Speaker = 5420, Option = '', Emoji = 18, Action = _EmptyTable,}, 
	[53557] = { id = 53557, Voice = '', Text = '可其实我生性胆小，你能陪我去依斯鲁得的港口看看吗…', Speaker = 1119, Option = '没问题', Emoji = 10, Action = _EmptyTable,}, 
	[53556] = { id = 53556, Voice = '', Text = '冒险者，我的梦想是去大海远航，做个英勇的海上勇士。', Speaker = 1119, Option = '支持你', Action = _EmptyTable,}, 
	[53555] = { id = 53555, Voice = '', Text = '帮助他们收获荣誉，财富，爱情和健康吧！', Speaker = 1381, Option = '', Action = _EmptyTable,}, 
	[53554] = { id = 53554, Voice = '', Text = '听到你这么说真是欣慰，我这里有一朵四叶草，请将这四片分别交给需要它们的人。', Speaker = 1381, Option = '', Action = _EmptyTable,}, 
	[53553] = { id = 53553, Voice = '', Text = '孩子，能力越大，责任越大啊！', Speaker = 1381, Option = '', Action = _EmptyTable,}, 
	[53552] = { id = 53552, Voice = '', Text = '但这个世界还有更多的人需要像你这样的冒险者帮助他们找到属于他们的幸运，你愿意帮忙吗？', Speaker = 1381, Option = '{愿意,0},{不愿意,3}', Action = _EmptyTable,}, 
	[53551] = { id = 53551, Voice = '', Text = '看到你已成长为一位强大的冒险者，我为你感到高兴。', Speaker = 1381, Option = '', Action = _EmptyTable,}, 
	[53550] = { id = 53550, Voice = '', Text = '我们又见面了！没想到许久不见，你竟成长了这么多，真是让我惊讶。', Speaker = 1381, Option = '谢谢', Action = _EmptyTable,}, 
	[53549] = { id = 53549, Voice = '', Text = '那实在是很让人伤心。', Speaker = 1381, Option = '', Action = _EmptyTable,}, 
	[53548] = { id = 53548, Voice = '', Text = '孩子，还记得我吗？还记得那片给人带来幸运的四叶草吗？', Speaker = 1381, Option = '{记得,0},{不记得,2}', Action = _EmptyTable,}, 
	[53543] = { id = 53543, Voice = '', Text = '现在你愿意把这些材料交给我吗？', Speaker = 1597, Option = '{交,7},{不交,6}', Action = _EmptyTable,}, 
	[53542] = { id = 53542, Voice = '', Text = '现在你愿意把这些材料交给我吗？', Speaker = 1597, Option = '{交,7},{不交,6}', Action = _EmptyTable,}, 
	[53541] = { id = 53541, Voice = '', Text = '这，就是飞利浦·巴力士兹的故事。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53540] = { id = 53540, Voice = '', Text = '后来他来到姜饼城，不仅收养了两个无人陪伴的孤儿，还用炼金术为姜饼城造了一座华丽的玩具工厂。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53539] = { id = 53539, Voice = '', Text = '飞利浦平生第一次感到了真正的快乐。“他的心在笑”，那一刻他发誓要做个乐善好施的炼金术士。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53538] = { id = 53538, Voice = '', Text = '可是相反，飞利浦对他说，“我的好学生，祝你圣诞快乐！”接着他又买了一只特别大的火鸡叫人给阿克米家送去。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53537] = { id = 53537, Voice = '', Text = '在圣诞精灵拜访过的第二天早上，也就是圣诞节的早晨，学生阿克米迟到了，他原以为飞利浦会生气的朝他大吼。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53536] = { id = 53536, Voice = '', Text = '他开始重新思考生活的意义，才发现原来施与比接受更快乐。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53535] = { id = 53535, Voice = '', Text = '老年的飞利浦在痛苦和挣扎中孤独的闭上了眼睛，这让飞利浦从噩梦一般的景象中惊醒。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53534] = { id = 53534, Voice = '', Text = '老年的飞利浦在病痛中呼喊着家人的名字，但没有一个人陪伴在他左右。就连他的学生阿克米也没有出现。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53533] = { id = 53533, Voice = '', Text = '躺在床上的老人不是别人，正是飞利浦自己。窗外飘着大雪，日历上的日期显示那天就是圣诞节。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53532] = { id = 53532, Voice = '', Text = '那么我就继续讲飞利浦的故事了！', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53530] = { id = 53530, Voice = '', Text = '冒险者，这冰块之角的数量不够，没找齐我可是不会再说一个字，你还是再找一些来吧。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53529] = { id = 53529, Voice = '', Text = '我要的50个冰块之角收集齐了吗？', Speaker = 1597, Option = '{齐了,4},{没有,3}', Action = _EmptyTable,}, 
	[53528] = { id = 53528, Voice = '', Text = '冒险者，想知道飞利浦的未来，就帮我带些冰块之角来吧。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53527] = { id = 53527, Voice = '', Text = '飞利浦仔细的朝老人端详，老人的面貌让飞利浦倒吸一口冷气。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53526] = { id = 53526, Voice = '', Text = '飞利浦看到一座富丽堂皇的房子，气派的屋子中却十分冷清，华丽的卧室里躺着一个重病卧床的老人。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53525] = { id = 53525, Voice = '', Text = '这一次飞利浦并没有想把“未来之灵”赶走，因为他也好奇，等待他的将会是一个怎样的未来。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53524] = { id = 53524, Voice = '', Text = '“现在之灵”将飞利浦拉回了现实后也消失了。只剩下“未来之灵”低喃着：“飞利浦，你就不想知道你的未来吗？”', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53523] = { id = 53523, Voice = '', Text = '飞利浦不懂，阿克米家的火炉边没有圣诞礼物，餐桌上没有火鸡，可为什么每个人脸上还是洋溢着幸福的微笑。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53522] = { id = 53522, Voice = '', Text = '阿克米兴高采烈的向母亲叙述着在飞利浦这里学到的新知识，丝毫没有埋怨飞利浦的意思。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53521] = { id = 53521, Voice = '', Text = '所以飞利浦觉得，这儿肯定不欢迎自己，便急忙掩了掩自己的身体。一旁的“现在之灵”说他们看不见飞利浦，他这才放下心来。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53520] = { id = 53520, Voice = '', Text = '飞利浦对学徒阿克米十分不负责任，不仅不怎么教授阿克米炼金术的知识，还时常把阿克米当做自己的免费劳力。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53519] = { id = 53519, Voice = '', Text = '飞利浦此时正置身于自己的学徒阿克米·曼的家中。阿克米·曼正坐在餐桌前与他的家人一起吃圣诞晚餐。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53518] = { id = 53518, Voice = '', Text = '那么我就继续讲飞利浦的故事了！', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53517] = { id = 53517, Voice = '', Text = '圣诞村就是需要你这样善良勇敢的人！', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53516] = { id = 53516, Voice = '', Text = '冒险者，想知道飞利浦去了哪里吗，那么勇敢的你先帮我消灭些魔物吧。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53515] = { id = 53515, Voice = '', Text = '他发现自己正置身于一个让他十分尴尬的地方！', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53514] = { id = 53514, Voice = '', Text = '刺眼的白光让飞利浦睁不开眼，当光点渐渐消失，他困难的向眼前看去。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53513] = { id = 53513, Voice = '', Text = '“过去之灵”笑着消失了，“现在之灵”似乎显得不慌不忙，他轻巧的躲过了攻击，在空中划出一道白光。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53512] = { id = 53512, Voice = '', Text = '但他的嘴上始终不饶人，叫喊着不愿再看这些，抄起身边的瓶子向三只圣诞精灵扔去。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53511] = { id = 53511, Voice = '', Text = '飞利浦的心开始有些软化了，他有些后悔自己对待世人的态度。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53510] = { id = 53510, Voice = '', Text = '以及他当学徒时，仁慈善良的炼金术老师在圣诞之夜和大家一起开心跳舞，款待学生的情形。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53509] = { id = 53509, Voice = '', Text = '飞利浦的记忆一下将他带回了孤单寂寞的童年生活中，他的弟弟虽比他年幼，却对他倍加关爱。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53508] = { id = 53508, Voice = '', Text = '一位叫“过去之灵”的圣诞精灵说道：“我能让你看到你的过去”，说罢便点了点飞利浦的额头。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53507] = { id = 53507, Voice = '', Text = '“看啊，那就是自私鬼飞利浦”他们窃窃私语道。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53506] = { id = 53506, Voice = '', Text = '出现在飞利浦眼前的，是三个扑朔着翅膀的圣诞精灵，愉悦的嘲笑着飞利浦。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53505] = { id = 53505, Voice = '', Text = '那么我就继续讲飞利浦的故事了！', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53504] = { id = 53504, Voice = '', Text = '非常好，那我们就来继续听故事。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53503] = { id = 53503, Voice = '', Text = '请你为我带一张独特又充满童趣的玩具工厂的照片来。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53502] = { id = 53502, Voice = '', Text = '冒险者，想知道后面发生了什么。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53501] = { id = 53501, Voice = '', Text = '他紧握武器，紧张的将门奋力一推，却被眼前的景象给惊呆了。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53500] = { id = 53500, Voice = '', Text = '他疑惑的下了床，朝水房走去，水房的门半虚掩着，里面透出微微的亮光。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53499] = { id = 53499, Voice = '', Text = '愤怒的飞利浦以为房里进了小偷，大声的呵斥着问谁在那里，可却没有人回应。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53498] = { id = 53498, Voice = '', Text = '那一夜，飞利浦躺上床刚准备入睡，便听见水房有谁在窃窃私语。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53497] = { id = 53497, Voice = '', Text = '飞利浦却觉得圣诞传统十分虚伪，也不愿费时远行，将信揉了揉扔进了垃圾箱。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53496] = { id = 53496, Voice = '', Text = '圣诞节的前一晚，飞利浦的弟弟从遥远的姜饼城寄了一封信给他，邀请他参加自己的圣诞晚餐。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53495] = { id = 53495, Voice = '', Text = '他冷漠无情, 是个连乞丐都不愿向他乞讨的吝啬鬼。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53494] = { id = 53494, Voice = '', Text = '飞利浦是个年轻有为的炼金术师，但他学习炼金术只为一己私欲，炼制的所有物品都占为己有。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53493] = { id = 53493, Voice = '', Text = '今天我们故事的主人公，正是姜饼城玩具工厂的创办人——飞利浦·巴力士兹。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53492] = { id = 53492, Voice = '', Text = '那么就让我们来讲讲姜饼城著名又奇幻的建筑——玩具工厂。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53491] = { id = 53491, Voice = '', Text = '童话般的冰雪世界圣诞村有着许多奇幻而神奇的故事，冒险者，你想听吗？', Speaker = 1597, Option = '{我想听,2},{我有空再听吧,1}', Action = _EmptyTable,}, 
	[53490] = { id = 53490, Voice = '', Text = '这棵树就成为圣诞树的来源！', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53489] = { id = 53489, Voice = '', Text = '每年圣诞，他都会与城民一起，为树上装饰各种灯烛、彩花、玩具、星星。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53488] = { id = 53488, Voice = '', Text = '农夫明白，自己接待的是一位圣诞使者，便将树上的礼物分给姜饼城的每一个城民。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53487] = { id = 53487, Voice = '', Text = '杉树茁壮成长，在姜饼城中越长越大，每年的十二月，树上便会礼物满枝。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53486] = { id = 53486, Voice = '', Text = '少年走后，农民便发现那树枝竟渐渐的长成了一棵小树。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53485] = { id = 53485, Voice = '', Text = '第二天清晨，少年便匆匆启程告别了农夫，并折了一根杉树枝插在地上。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53484] = { id = 53484, Voice = '', Text = '他们一起颂歌、跳舞，欢乐的烛火一直闪烁到午夜。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53483] = { id = 53483, Voice = '', Text = '农夫盛情的接待了少年，并让少年与自己的家人吃了一顿丰盛的圣诞大餐。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53482] = { id = 53482, Voice = '', Text = '善良的农夫，看着在风雨中瑟瑟发抖的少年，便赶忙将他引进房中。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53481] = { id = 53481, Voice = '', Text = '那是姜饼城一个风雨交加的圣诞夜，一位饥寒落魄的少年敲响了一位农夫家的门。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53480] = { id = 53480, Voice = '', Text = '那么就让我来告诉你圣诞树的来源吧！', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53479] = { id = 53479, Voice = '', Text = '冒险者，我们马上开始我们的故事。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53478] = { id = 53478, Voice = '', Text = '冒险者，这礼盒纸的数量不够，没找齐我可是不会再说一个字，你还是再找一些来吧。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53477] = { id = 53477, Voice = '', Text = '我要的礼盒纸收集齐了吗？', Speaker = 1597, Option = '{齐了,4},{没有,3}', Action = _EmptyTable,}, 
	[53476] = { id = 53476, Voice = '', Text = '冒险者，我的故事可不是免费的。我需要一些礼盒纸，你能帮我找来吗？', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53475] = { id = 53475, Voice = '', Text = '这…就是圣诞袜的故事。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53474] = { id = 53474, Voice = '', Text = '一家人终于又团聚在一起时，他们虽然已一无所有，但脸上都洋溢着幸福的笑容。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53473] = { id = 53473, Voice = '', Text = '坏脾气的贵族终于抑制不住，留下了悔恨的泪水。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53472] = { id = 53472, Voice = '', Text = '三个女儿醒来时发现了袜子里的金子，惊喜又意外。但并没有拿金子当嫁妆，而是用金子将自己的父亲赎回了家。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53471] = { id = 53471, Voice = '', Text = '圣诞老人被贵族妻子的善行打动，在他们家的烟囱里撒下了许多金子，落进姑娘们烤在火炉旁的长筒袜。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53470] = { id = 53470, Voice = '', Text = '接受过贵族妻子恩赐的城民们请求圣诞老人帮助他们。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53469] = { id = 53469, Voice = '', Text = '喜庆的圣诞夜，三个姑娘早早地蜷在炉子边进入了梦乡，剩下妻子在窗边长吁短叹。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53468] = { id = 53468, Voice = '', Text = '贵族的三个女儿快要出嫁了，妻子为没有钱给她们买嫁妆而难过。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53467] = { id = 53467, Voice = '', Text = '贵族因为待人不善，也因家中的财富遭人妒忌，最终被人陷害，关进了冰冷的监狱中。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53466] = { id = 53466, Voice = '', Text = '姜饼城有个坏脾气的贵族，但他的妻子却心地善良，对所有人都十分慷慨大方。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53465] = { id = 53465, Voice = '', Text = '那么我们就来讲讲圣诞袜的由来吧！', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53464] = { id = 53464, Voice = '', Text = '真高兴圣诞村能有你这样的勇士，我赞美你！', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53463] = { id = 53463, Voice = '', Text = '冒险者，看似宁静的圣诞村，也在饱受魔物的危害，我相信你能帮助这里善良的人们消灭它们。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53462] = { id = 53462, Voice = '', Text = '姜饼城的孩子们都知道，那就是当年的那个孤儿。他们叫他：圣诞老人。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53461] = { id = 53461, Voice = '', Text = '在午夜的钟声敲响，他由烟囱进入各家，把圣诞礼物装在袜子中挂在孩子们的床头或是火炉前。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53460] = { id = 53460, Voice = '', Text = '那是位身着红袍，头戴红帽的白胡子老头，他的脸上永远洋溢着幸福的笑容，仿佛从来都不会被烦恼叨扰。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53459] = { id = 53459, Voice = '', Text = '之后每年的十二月，当姜饼城孩子们的笑声都凝成片片雪花，一个慈祥的身影将驾着雪橇从天而降。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53458] = { id = 53458, Voice = '', Text = '那年的平安夜，老人在孩子们的簇拥下，安静的去世了。虽然他走了，但他活在每个姜饼城民的心中。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53457] = { id = 53457, Voice = '', Text = '相信他的人越来越多，帮助他的人越来越多，随着时间的流逝，满脸的白胡子也爬上了他的脸庞…', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53456] = { id = 53456, Voice = '', Text = '为了表达他的感谢，每年他要离开的晚上，他就会把用木头雕刻的小礼物送给抚养过他的家庭中的孩子们。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53455] = { id = 53455, Voice = '', Text = '但善良的姜饼城民们收留了他，轮流将他照顾抚养长大，每年的圣诞，他就要从一户人家搬到另一户人家。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53454] = { id = 53454, Voice = '', Text = '那是一个雪夜，一个孤儿被遗弃在姜饼城的教堂前。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53453] = { id = 53453, Voice = '', Text = '那么我们就开始讲讲圣诞老人的诞生吧！', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53452] = { id = 53452, Voice = '', Text = '看来你对圣诞的传统已经有了一定的了解。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53451] = { id = 53451, Voice = '', Text = '看来你对圣诞的渊源并不是很了解，请你稍作了解之后再来吧。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53450] = { id = 53450, Voice = '', Text = '第三个问题，圣诞节惩罚坏孩子的怪兽叫什么名字？', Speaker = 1597, Option = '{芬里尔,5},{坎卜斯,0},{嘉尔姆,5}', Action = _EmptyTable,}, 
	[53449] = { id = 53449, Voice = '', Text = '第二个问题，圣诞节的头牌主菜是什么呢？', Speaker = 1597, Option = '{珍珠鸡,4},{火鸡,0},{洛克鸡,4}', Action = _EmptyTable,}, 
	[53448] = { id = 53448, Voice = '', Text = '那么第一个问题，圣诞夜时孩子们会把什么挂在床头呢？', Speaker = 1597, Option = '{袜子,0},{帽子,3},{裤子,3}', Action = _EmptyTable,}, 
	[53447] = { id = 53447, Voice = '', Text = '看来你对自己很有自信啊~冒险者。', Speaker = 1597, Option = '', Action = _EmptyTable,}, 
	[53446] = { id = 53446, Voice = '', Text = '在听故事之前，我想问你几个问题，看看你对圣诞的渊源了解多少~', Speaker = 1597, Option = '好的', Action = _EmptyTable,}, 
	[53445] = { id = 53445, Voice = '', Text = '童话般的冰雪世界圣诞村有着许多奇幻而神奇的故事，冒险者，你想听吗？', Speaker = 1597, Option = '{我想听,2},{我有空再听吧,1}', Action = _EmptyTable,}, 
	[53165] = { id = 53165, Voice = '', Text = '另外可别忘了，还有玩具工厂的每日任务等你去完成呀~~', Speaker = 5384, Option = '', Emoji = 27, Action = _EmptyTable,}, 
