	[53156] = { id = 53156, Voice = '', Text = '完成三个任务后请回来告诉我~我将继续教你宝箱的领取方法噢~', Speaker = 5384, Option = '{我这就去,5},{还没有,4}', Action = _EmptyTable,}, 
	[53155] = { id = 53155, Voice = '', Text = '需要帮助的姜饼城城民每天都会发出六个每日任务，只需要完成其中三个，就能领取宝箱呢！', Speaker = 5384, Option = '', Action = _EmptyTable,}, 
	[53154] = { id = 53154, Voice = '', Text = '姜饼城和玩具工厂都有这样的每日任务。', Speaker = 5384, Option = '如何接取', Action = _EmptyTable,}, 
	[53153] = { id = 53153, Voice = '', Text = '我想你已经注意到了地图上的小图标，那就是每日任务。', Speaker = 5384, Option = '哪里有', Action = _EmptyTable,}, 
