	[53151] = { id = 53151, Voice = '', Text = '那看来你对区域任务已经有些了解了~让我来告诉你如何完成区域任务吧？', Speaker = 5384, Option = '', Action = _EmptyTable,}, 
	[53150] = { id = 53150, Voice = '', Text = '嘿嘿，冒险者你了解姜饼城的区域任务吗？', Speaker = 5384, Option = '{了解,2},{不了解,3}', Action = _EmptyTable,}, 
	[53148] = { id = 53148, Voice = '', Text = '看来是我小瞧你了呢！你将得到我代表姜饼城诚挚的感谢和赠与你的宝箱~！', Speaker = 5383, Option = '', Action = _EmptyTable,}, 
	[53147] = { id = 53147, Voice = '', Text = '害怕了？助人为乐可是姜饼城的优良品德~！', Speaker = 5383, Option = '', Action = _EmptyTable,}, 
	[53146] = { id = 53146, Voice = '', Text = '不过如果你有胆量去那儿冒险，并且帮助路途上三个遇到困难的人，我就承认你的勇气，给你些奖励。', Speaker = 5383, Option = '{我这就去,3},{还没有,4}', Action = _EmptyTable,}, 
	[53145] = { id = 53145, Voice = '', Text = '玩具工厂二层危险重重，我不觉得你能活着回来，哈哈！ ', Speaker = 5383, Option = '', Action = _EmptyTable,}, 
