	[53142] = { id = 53142, Voice = '', Text = '干的不错！你将得到我代表姜饼城诚挚的感谢和赠与你的宝箱~', Speaker = 5382, Option = '', Action = _EmptyTable,}, 
	[53141] = { id = 53141, Voice = '', Text = '加把劲，助人为乐可是姜饼城的优良品德~！', Speaker = 5382, Option = '', Action = _EmptyTable,}, 
	[53140] = { id = 53140, Voice = '', Text = '你何不也试试，如果你已经帮助了他们完成了三个他们的请求，请来找我，会有好事发生的！', Speaker = 5382, Option = '{我这就去,3},{还没有,4}', Action = _EmptyTable,}, 
	[53139] = { id = 53139, Voice = '', Text = '每天都有冒险者去玩具工厂进行愉快的探险，他们也会帮助路途上遇到困难的人。', Speaker = 5382, Option = '', Action = _EmptyTable,}, 
	[53138] = { id = 53138, Voice = '', Text = '亲爱的冒险者~不尝试去玩具工厂一层探险一下吗？', Speaker = 5382, Option = '{我很乐意,2},{不愿意,1}', Action = _EmptyTable,}, 
	[53136] = { id = 53136, Voice = '', Text = '干的好伙计！你将得到我代表姜饼城诚挚的感谢和赠与你的宝箱~！', Speaker = 5381, Option = '', Action = _EmptyTable,}, 
