	[53134] = { id = 53134, Voice = '', Text = '如果你已经帮助了其中三个人，那么请来找我，我带给你的惊喜绝不会让你失望~！', Speaker = 5381, Option = '{我这就去,3},{还没有,4}', Action = _EmptyTable,}, 
	[53133] = { id = 53133, Voice = '', Text = '帮助姜饼城中可爱的人们，你会收获良多。', Speaker = 5381, Option = '', Action = _EmptyTable,}, 
