	[20098] = { id = 20098, Voice = '', Text = '咦？这点[c][ffff00]恶魔角[-][/c]可不够呢…', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20097] = { id = 20097, Voice = '', Text = '这么快就收集来了吗？我来看看数量……', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20096] = { id = 20096, Voice = '', Text = '唔，故事讲完了，作为报酬，我需要一些[c][ffff00]恶魔角[-][/c]，你能帮我弄来吗？', Speaker = 1593, Option = '', Action = { num = 1, actionid = 201,},}, 
	[20095] = { id = 20095, Voice = '', Text = '这就是邪教女巫瑟西的故事。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20094] = { id = 20094, Voice = '', Text = '她在地下创建了黑巫教会，希望多吸收一些信徒，而这些信徒就是她对抗吉芬的最大资本。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20093] = { id = 20093, Voice = '', Text = '瑟西来到了吉芬地下，黑暗的力量可以缓慢治疗她受到的致命伤势，总有一天，她要吉芬付出代价。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20092] = { id = 20092, Voice = '', Text = '在他们犹豫的时候，瑟西突然挣脱魔法阵，逃离出去，雷藏第一个反应过来，将手中的魔力轰了出来。但是瑟西还是成功逃离了。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20091] = { id = 20091, Voice = '', Text = '雷藏三人看着导师熟悉的面孔，想到导师对他们的教导之恩，有些犹豫。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20090] = { id = 20090, Voice = '', Text = '摩根与玛莉亚结出了强大的魔法阵封印瑟西，而雷藏则是给出致命一击，瑟西突然说道：你们，要杀了你自己的老师吗？', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20089] = { id = 20089, Voice = '', Text = '这场追逐战持续了九天九夜，在第十天的黄昏，瑟西在苏克拉特沙漠被击败。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20088] = { id = 20088, Voice = '', Text = '黑暗种子控制的瑟西嘿嘿一笑，使用魔法离开了吉芬塔，往城外逃去，雷藏三人紧随其后追了出去。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20087] = { id = 20087, Voice = '', Text = '雷藏三人感受到瑟西身上突然爆发的黑暗之力，终于明白了瑟西的意思，三人立刻联手，希望以强大的力量压制爆发的瑟西。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20086] = { id = 20086, Voice = '', Text = '瑟西痛苦的大叫，她能感觉到自己的灵魂正在被黑暗种子吞噬，瑟西嚎叫：杀我！杀了我！', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20085] = { id = 20085, Voice = '', Text = '雷藏三人对这个要求显得不知所措，这一犹豫，黑暗种子突然爆发瞬间挣脱了瑟西的控制。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20084] = { id = 20084, Voice = '', Text = '直到某天，瑟西意识到黑暗种子的力量已经压制不住了，瑟西召来了自己最得意的三位学生，祈求他们杀了自己。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20083] = { id = 20083, Voice = '', Text = '在接下来的五十年，瑟西一直呆在吉芬塔，与逐渐壮大的黑暗种子抗争。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20082] = { id = 20082, Voice = '', Text = '一开始很成功，黑暗种子几乎就要被完美封印了，但是黑暗种子在她的压制下勉强支撑下来。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20081] = { id = 20081, Voice = '', Text = '这股怨念逐渐改变着瑟西的性格，当瑟西明白这一点之后，她开始尝试使用自己的魔力来压制黑暗种子。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20080] = { id = 20080, Voice = '', Text = '一个上古时代的邪恶魔法师种下了黑暗种子，这颗种子在她身上不断吸收她的魔力，而种子中蕴含着那位邪恶魔法师的怨念。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20079] = { id = 20079, Voice = '', Text = '很快她就查到了问题所在，她的身体不知什么时候被遗迹魔法漩涡的始作俑者。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20078] = { id = 20078, Voice = '', Text = '这根本不是她！她决定进入吉芬塔，依靠吉芬塔内充足的魔力检查自己的身体。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20077] = { id = 20077, Voice = '', Text = '随着日子的平静，瑟西渐渐发现了自己的异常，她意识到自己逐渐变得好战，对待敌人冷酷无情。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20076] = { id = 20076, Voice = '', Text = '这么快就回来了？好好好。那我就继续我的故事了。', Speaker = 1593, Option = '', Action = { num = 1, actionid = 200,},}, 
	[20075] = { id = 20075, Voice = '', Text = '嘿嘿，想知道接下来发生了什么吗？想知道的话就帮我消灭一些[c][ffff00]邪骸兽人[-][/c]吧！', Speaker = 1593, Option = '', Action = { num = 1, actionid = 201,},}, 
	[20074] = { id = 20074, Voice = '', Text = '这次探查遗迹给魔法公会带来了沉重的打击，魔法公会渐渐沉寂，修生养息。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20073] = { id = 20073, Voice = '', Text = '不久后瑟西一个人身受重伤回到了吉芬，所有前往遗迹的人员全部遇难，只有她靠着魔法的掌控离开了遗迹最后爆发的魔法漩涡。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20072] = { id = 20072, Voice = '', Text = '瑟西在给三人上了最后一次魔法元素理论课后，便跟随魔法公会前往上古遗迹去寻找失落的魔法。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20071] = { id = 20071, Voice = '', Text = '雷藏、摩根和玛莉亚信服不已，三人立志成为像瑟西一样强大的魔法师。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20070] = { id = 20070, Voice = '', Text = '她曾经这样教导学生，在大义面前，一切个人感情都要压制，绝不能让个人感情左右自己的思绪。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20069] = { id = 20069, Voice = '', Text = '而令她最自豪的并不是她强大的魔力，而是她教出了三个出类拔萃的学生，他们就是闻名大陆的雷藏、摩根和玛莉亚。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20068] = { id = 20068, Voice = '', Text = '瑟西无疑是近百年来魔法学院最出彩的超魔导师之一，她超强的魔法掌控力使得她在整个大陆都赫赫有名。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20067] = { id = 20067, Voice = '', Text = '今天，我们就来讲讲瑟西的故事吧。', Speaker = 1593, Option = '', Action = { num = 1, actionid = 200,},}, 
	[20066] = { id = 20066, Voice = '', Text = '咦？这点[c][ffff00]玻璃珠[-][/c]可不够呢…', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20065] = { id = 20065, Voice = '', Text = '这么快就收集来了吗？我来看看数量……', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20064] = { id = 20064, Voice = '', Text = '唔，故事讲完了，作为报酬，我需要一些[c][ffff00]玻璃珠[-][/c]，你能帮我弄来吗？', Speaker = 1593, Option = '', Action = { num = 1, actionid = 201,},}, 
	[20063] = { id = 20063, Voice = '', Text = '西比盖一直希望有个人来拯救他，结束他的罪恶。这就是傀儡师西比盖的故事。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20062] = { id = 20062, Voice = '', Text = '西比盖无奈，为了那些孩子们的安全，他只能屈服，西比盖不断地制作傀儡，心中的内疚也越来越甚。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20061] = { id = 20061, Voice = '', Text = '他终于明白他犯了多大的错误，他的挚友——女神之手的佐伊威胁他，如果不能在这里继续制作傀儡娃娃，那么原本村里的那些天真的笑容，他只能在死后的世界才能看到了。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20060] = { id = 20060, Voice = '', Text = '西比盖心中一片悲凉，他看到傀儡娃娃的身影渐渐消失在空气中，不久后，远处传来冒险者们惊恐的惨叫。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20059] = { id = 20059, Voice = '', Text = '他的挚友告诉他，其实他效力于一个神秘的组织，这次来这里是为了给吉芬制造一些麻烦，而西比盖只是被利用的对象而已。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20058] = { id = 20058, Voice = '', Text = '看到傀儡活了，他的心中没有喜悦，只有惊恐，西比盖质问他的挚友，到底做了什么。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20057] = { id = 20057, Voice = '', Text = '他看到了自己满含心血的傀儡们慢慢变大，傀儡们的心核发出幽黑的光芒，宝石制作的眼睛爆发出妖异的红光。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20056] = { id = 20056, Voice = '', Text = '终于他大喊，希望他的挚友住手，他的挚友不闻不问，继续着他的咒语。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20055] = { id = 20055, Voice = '', Text = '强大的魔力开始汇聚，周围那些不详的气息也开始聚集，他心中的不安越来越强烈。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20054] = { id = 20054, Voice = '', Text = '他的挚友要他把傀儡娃娃放下，紧接着，他的挚友开始念动咒语。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20053] = { id = 20053, Voice = '', Text = '挚友把他带到了吉芬地下，他感受到了这里充斥这不详的气息。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20052] = { id = 20052, Voice = '', Text = '在几天之后，他带着他的所有完成的傀儡和挚友一起踏上了去吉芬的路途。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20051] = { id = 20051, Voice = '', Text = '西比盖激动得不能自已，他开始制造更多的傀儡娃娃。他甚至梦里有一大堆活着的傀儡娃娃，围着他，叫着爸爸。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20050] = { id = 20050, Voice = '', Text = '西比盖兴奋极了，他迫切的询问，他的挚友告诉他，就在魔法之都吉芬，那里肯定能实现他的梦想。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20049] = { id = 20049, Voice = '', Text = '他的挚友眼神一亮，告诉他，有一处地方能够让他的傀儡活过来。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20048] = { id = 20048, Voice = '', Text = '正当他无从下手的时候，他少时的挚友回到了他所在的村子，在酒后，他向他的挚友诉说了自己的痛楚。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20047] = { id = 20047, Voice = '', Text = '这么快就回来了？好好好。那我就继续我的故事了。', Speaker = 1593, Option = '', Action = { num = 1, actionid = 200,},}, 
	[20046] = { id = 20046, Voice = '', Text = '嘿嘿，想知道接下来发生了什么吗？想知道的话就帮我消灭一些[c][ffff00]九尾狐[-][/c]吧！', Speaker = 1593, Option = '', Action = { num = 1, actionid = 201,},}, 
	[20045] = { id = 20045, Voice = '', Text = '但是凭借他所学，根本制造不出拥有灵魂的傀儡，这使他非常苦恼。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20044] = { id = 20044, Voice = '', Text = '她能唱能跳，能哭能闹，她会依偎在他怀里撒娇——就像自己想念的那些笑容一样。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20043] = { id = 20043, Voice = '', Text = '然而这还不是西比盖想要的，西比盖希望能制造出一个傀儡娃娃。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20042] = { id = 20042, Voice = '', Text = '他开始尝试把魔法融入到傀儡身上，他建造了一个魔力心核，由魔力心核供给魔力，傀儡们变得更加生动。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20041] = { id = 20041, Voice = '', Text = '西比盖开始渴望拥有自己的孩子，西比盖制作了一个又一个精致的傀儡。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20040] = { id = 20040, Voice = '', Text = '但是他明白，那些孩子们的笑容并不属于自己，而是属于孩子们，属于孩子们的父母。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20039] = { id = 20039, Voice = '', Text = '他开始想念孩子们的笑容——尽管他明白第二天他就能见到那些笑容。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20038] = { id = 20038, Voice = '', Text = '他错了，每当夜晚到来，躺在床上的西比盖就感到一阵空虚。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20037] = { id = 20037, Voice = '', Text = '每当看到孩子们的笑脸，西比盖就觉得无比的满足，西比盖觉得就这样给孩子们制造一辈子的傀儡，他的生活就无比富足。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20036] = { id = 20036, Voice = '', Text = '西比盖是有名的傀儡师，由他制造出来的傀儡娃娃活灵活现，深受人们的喜爱，尤其受到孩子们的喜欢。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20035] = { id = 20035, Voice = '', Text = '今天，我们就来讲讲西比盖的故事吧。', Speaker = 1593, Option = '', Action = { num = 1, actionid = 200,},}, 
	[20034] = { id = 20034, Voice = '', Text = '咦？这点[c][ffff00]剎勒空结晶[-][/c]可不够呢…', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20033] = { id = 20033, Voice = '', Text = '这么快就收集来了吗？我来看看数量……', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20032] = { id = 20032, Voice = '', Text = '唔，故事讲完了，作为报酬，我需要一些[c][ffff00]剎勒空结晶[-][/c]，你能帮我弄来吗？', Speaker = 1593, Option = '', Action = { num = 1, actionid = 201,},}, 
	[20031] = { id = 20031, Voice = '', Text = '这就是吸血鬼女孩——沙西莉的故事。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20030] = { id = 20030, Voice = '', Text = '沙西莉决定在这里隐居下来，等待德古拉回来，等待亲手将德古拉送葬。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20029] = { id = 20029, Voice = '', Text = '追着男爵而去，但是男爵早已不知所踪，沙西莉知道德古拉总有一天会回到这里。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20028] = { id = 20028, Voice = '', Text = '某天，沙西莉看到城堡中的德古拉惨叫着逃离了城堡，一个诡魅的身影幽然浮现，沙西莉奋力的挣脱了魔力锁链。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20027] = { id = 20027, Voice = '', Text = '她不断的尝试挣脱德古拉加附在身上的魔力锁链，不断的尝试后她终于能够解开魔力锁链，但她没有挣脱，她在等待，等待一个报仇的机会。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20026] = { id = 20026, Voice = '', Text = '这让她愈加恨德古拉，她恨德古拉夺走了他的一切。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20025] = { id = 20025, Voice = '', Text = '沙西莉只能隐忍，在德古拉男爵的城堡住下，在这期间她收到了自己的未婚夫西蒙特的死讯。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20024] = { id = 20024, Voice = '', Text = '德古拉不屑一笑：尽管恨我吧，沙西莉，你一辈子都不可能离开这个地方，你一辈子都属于我！', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20023] = { id = 20023, Voice = '', Text = '沙西莉睁开她的血瞳，向德古拉发动了攻击，但是刚刚初拥完毕的沙西莉被轻易的击败了。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20022] = { id = 20022, Voice = '', Text = '一对血色蝠翼张开——沙西莉完成了初拥仪式。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20021] = { id = 20021, Voice = '', Text = '一头淡蓝的卷发在血液的力量下渐渐变成如血液般鲜红，她的后背隆起，一声轻响，象征魔法师地位的魔法袍撕裂了。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20020] = { id = 20020, Voice = '', Text = '吸血鬼的獠牙深深刺入沙西莉的脖颈，充满黑暗魔力的血液在她的体内不断吞噬同化。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20019] = { id = 20019, Voice = '', Text = '沙西莉的惊恐愈甚，在男爵黑暗魔力的压制下，沙西莉的魔力甚至驱使元素都做不到。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20018] = { id = 20018, Voice = '', Text = '男爵低下头靠近沙西莉的耳边，低声诉说：沙西莉，我决定赐予伴随我身边的殊荣，我将赐予你不老的容颜以及不朽的身躯。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20017] = { id = 20017, Voice = '', Text = '醒来的沙西莉发现自己的躺在一个精美的餐桌上，男爵狰狞的面孔映照在沙西莉美丽的眼瞳中，沙西莉惊骇地说不出话。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20016] = { id = 20016, Voice = '', Text = '他凭借强大的实力瞬间击倒了奉命护送沙西莉的骑士，而沙西莉在德古拉男爵的咆哮下失去了意识。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20015] = { id = 20015, Voice = '', Text = '他仿佛已经闻到了沙西莉身上血液的芳香，这让他死寂已久的心兴奋不已。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20014] = { id = 20014, Voice = '', Text = '德古拉男爵惊讶这世上竟然有如此纯净充满魔力的血液，男爵的贪婪再也无法遏制。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20013] = { id = 20013, Voice = '', Text = '然而就在沙西莉返回吉芬的途中，被外出寻觅新鲜血液的德古拉男爵遇到了。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20012] = { id = 20012, Voice = '', Text = '这么快就回来了？好好好。那我就继续我的故事了。', Speaker = 1593, Option = '', Action = { num = 1, actionid = 200,},}, 
	[20011] = { id = 20011, Voice = '', Text = '嘿嘿，想知道接下来发生了什么吗？想知道的话就帮我消灭一些烦人的[c][ffff00]梦魇[-][/c]吧！', Speaker = 1593, Option = '', Action = { num = 1, actionid = 201,},}, 
	[20010] = { id = 20010, Voice = '', Text = '完成仪式的沙西莉启程返回吉芬，而西蒙特与她约定，在这次任务完成后他就会前往吉芬正式把沙西莉带回来。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20009] = { id = 20009, Voice = '', Text = '在汤玛斯大主教的主持下，两人在普隆德拉教堂签订了订婚仪式，成为了人人羡慕的一对。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20008] = { id = 20008, Voice = '', Text = '在一路历练之下，两人的感情与日俱增，终于，西蒙特在妙勒尼山脉的山顶单膝下跪，向着心爱的女子许下了一生最为真诚的诺言。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20007] = { id = 20007, Voice = '', Text = '西蒙特强大的实力以及英俊的外表很快就吸引了沙西莉，而沙西莉的美貌与智慧也深深地吸引着西蒙特。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20006] = { id = 20006, Voice = '', Text = '那几年她是吉芬魔法学院真正的公主，直到某一天，沙西莉在一次外出的历练中，结识了来自普隆德拉的骑士西蒙特。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20005] = { id = 20005, Voice = '', Text = '吉芬魔法学院的男学生们都被她的美丽所吸引，而她也有着令魔法学院的导师们惊艳不已的魔法天赋。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20004] = { id = 20004, Voice = '', Text = '沙西莉毫无疑问是吉芬近几年来最为美丽的女孩，她拥有着宛如星辰般晶莹的眼眸和无数女孩们羡慕不已的淡蓝色卷发。', Speaker = 1593, Option = '', Action = _EmptyTable,}, 
	[20003] = { id = 20003, Voice = '', Text = '今天，我们就来讲讲沙西莉的故事吧。', Speaker = 1593, Option = '', Action = { num = 1, actionid = 200,},}, 
	[9618] = { id = 9618, Voice = '', Text = '真是遗憾，你的身上并没有我想要的东西呢！', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[9617] = { id = 9617, Voice = '', Text = '干得真不错，你果然已经理解了冒险者的生活。', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[9616] = { id = 9616, Voice = '', Text = '回来了吗？快让我看看这有趣的东西！', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[9615] = { id = 9615, Voice = '', Text = '请带给我一些[c][ffff00]枯树枝[-][/c]，听说树精身上就有这种东西，当然你也可以去交易所购买，快去吧。', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[9614] = { id = 9614, Voice = '', Text = '请带给我一个[c][ffff00]艾娃[-][/c]，如果你怕麻烦的话，可以去各大玩具商那里逛逛哦，那儿就有我想要的东西。', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[9613] = { id = 9613, Voice = '', Text = '我听说新型玩具[c][ffff00]艾娃[-][/c]和物品[c][ffff00]枯树枝[-][/c]非常受欢迎呢，那么请你为我把它们带来吧！', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[9612] = { id = 9612, Voice = '', Text = '你知道，冒险者经常要面对孤独的长途旅程，这时候如果有一些酷炫有趣的玩具物品陪伴，无聊寂寞的旅程也可以多姿多彩哦。', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[9611] = { id = 9611, Voice = '', Text = '我很满意你之前的表现，现在我有一个额外的要求，你可以把它当作第五项考核。', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[9610] = { id = 9610, Voice = '', Text = '“可以成长的武器防具”这才是乌加特家族铸造工艺的代表。', Speaker = 3343, Option = '', Action = _EmptyTable,}, 
	[9609] = { id = 9609, Voice = '', Text = '精炼大师忽克连曾说过：乌加特家族的武器是最适合精炼的武器。', Speaker = 3343, Option = '', Action = _EmptyTable,}, 
	[9608] = { id = 9608, Voice = '', Text = '他们用普通的材料铸造出的武器锋利无比，不易损坏，并且通过他们家族工艺铸造的武器可以进行附魔，精炼等强化武器的行为。', Speaker = 3343, Option = '', Action = _EmptyTable,}, 
	[9607] = { id = 9607, Voice = '', Text = '乌加特人经过一代又一代的研究，终于研究出用普通的铁，钢等金属铸造出精良的武器。', Speaker = 3343, Option = '', Action = _EmptyTable,}, 
	[9606] = { id = 9606, Voice = '', Text = '加上侏儒特有的卢恩文铭刻魔力，才造就了威力强大的神器，人类显然无法找到如此珍贵的材料。', Speaker = 3343, Option = '', Action = _EmptyTable,}, 
	[9605] = { id = 9605, Voice = '', Text = '我们家族的工艺是根据侏儒的铸造书卷，经过一代代乌加特人的苦心研究演变而来。侏儒的铸造书卷中，武器与防具的制造材料珍惜无比，比如世界树的枝干，永不腐朽的星辰铁等。', Speaker = 3343, Option = '', Action = _EmptyTable,}, 
	[9604] = { id = 9604, Voice = '', Text = '我们家族在铸造这一领域的成就让哈沃德也盛赞不已，称我们家族是大陆第一的铸造家族。', Speaker = 3343, Option = '', Action = _EmptyTable,}, 
	[9603] = { id = 9603, Voice = '', Text = '而哈沃德通过稀有的魔力材料以附魔的形式铸造，简单来说就是铸造与附魔同时进行的一种铸造术。我们称之为“附魔铸造术”。', Speaker = 3343, Option = '', Action = _EmptyTable,}, 
	[9602] = { id = 9602, Voice = '', Text = '乌加特家族是使用天生不能导入魔力的普通材料制造武器防具，经由乌加特家族技术制造的武器防具，完美解决了普通材料不能导入魔力的问题，可以用普通材料制造的装备进行附魔。', Speaker = 3343, Option = '', Action = _EmptyTable,}, 
	[9601] = { id = 9601, Voice = '', Text = '而现在，我们家族的生意遍布整个大陆，几乎每个城市都有着家族的族人存在，家族的名望到达顶峰，是当之无愧的大陆最强大的家族之一。', Speaker = 3343, Option = '', Action = _EmptyTable,}, 
	[9600] = { id = 9600, Voice = '', Text = '家族千年来积累了大量的财富，而我们也为大陆上的冒险者们提供优良的武器。', Speaker = 3343, Option = '', Action = _EmptyTable,}, 
	[9599] = { id = 9599, Voice = '', Text = '为大陆做出了巨大贡献的乌加特家族的名声也在大陆之上广为流传，各国对我们家族的技术垂涎不已，但没有人胆敢挑衅达纳托斯的威严，加上我们家族在大陆上崇高的名望使得家族安全的生存下来。', Speaker = 3343, Option = '', Action = _EmptyTable,}, 
	[9598] = { id = 9598, Voice = '', Text = '达纳托斯凭借这身铠甲承受住了魔王的致命一击，最终成功封印了魔王。', Speaker = 3343, Option = '', Action = _EmptyTable,}, 
	[9597] = { id = 9597, Voice = '', Text = '梦罗克魔王入侵大陆之时，我们家族拿出了祖先留下的世界树枝干，请所有家族的铸造师，为魔剑士达纳托斯铸造了一身坚固无匹的铠甲——名为达纳托斯的守护。', Speaker = 3343, Option = '', Action = _EmptyTable,}, 
	[9596] = { id = 9596, Voice = '', Text = '作为大陆上首席的铸造家族，我们乌加特家族也为大陆的危机贡献出了自己的力量。', Speaker = 3343, Option = '', Action = _EmptyTable,}, 
	[9595] = { id = 9595, Voice = '', Text = '灾难之后新的秩序建立，他也来到了幸存者的聚集地，凭借他强大的铸造技术站稳了脚跟，创立了屹立大陆千年不倒的强盛家族——乌加特家族。', Speaker = 3342, Option = '', Action = _EmptyTable,}, 
	[9594] = { id = 9594, Voice = '', Text = '在地下这几年，他把羊皮卷上的铸造技术研究了大半，他所铸造的武器与装备坚固无比，魔力流转，蕴含了强大的力量。', Speaker = 3342, Option = '', Action = _EmptyTable,}, 
	[9593] = { id = 9593, Voice = '', Text = '不久后，正如侏儒所料，灾难开始了，而早有准备的先祖准备了充足的食物躲避了战火的洗礼。', Speaker = 3342, Option = '', Action = _EmptyTable,}, 
	[9592] = { id = 9592, Voice = '', Text = '他感念先祖的照料，留下了由卢恩文写成的铸造书卷以及一小块世界树的枝干，并且告诉先祖，称不久后大乱即将来临，希望他好好传承这门技艺，使之在大陆上永远流传。', Speaker = 3342, Option = '', Action = _EmptyTable,}, 
	[9591] = { id = 9591, Voice = '', Text = '先祖醒来后发现客人不见了，他阅读着客人留下的信件，他才明白，原来那位客人是侍奉于诸神的侏儒一族，他们曾经为诸神们铸造诸多强大的神器，其中甚至包括主神奥丁的“永恒之枪”！', Speaker = 3342, Option = '', Action = _EmptyTable,}, 
	[9590] = { id = 9590, Voice = '', Text = '这位客人的伤势好了之后，却乘着先祖熟睡之际，悄悄离开了他。临行前客人感念这段时间乌加特对他的悉心照料，留下了厚厚一卷羊皮卷，以及一封信件。', Speaker = 3342, Option = '', Action = _EmptyTable,}, 
	[9589] = { id = 9589, Voice = '', Text = '善良的乌加特把他带出了危险的沼泽，花了很长时间为这位客人治疗魔物造成的伤势。', Speaker = 3342, Option = '', Action = _EmptyTable,}, 
	[9588] = { id = 9588, Voice = '', Text = '传闻诸神的黄昏还未发生之前，我们勇敢而年轻的家族先祖——麦克·乌加特，在魔物丛生的沼泽中磨练自己的战斗技巧时，从魔物的口中救下了一位皮肤苍白如尸体，头发及胡须为黑色，身形扭曲，腿短而手长，长着大脑袋和一张饱经风霜的脸孔的“人”。', Speaker = 3342, Option = '', Action = _EmptyTable,}, 
	[9587] = { id = 9587, Voice = '', Text = '我们乌加特家族是大陆上历史最为悠久的家族之一。', Speaker = 3342, Option = '', Action = _EmptyTable,}, 
	[9283] = { id = 9283, Voice = '', Text = '唔…这点[c][ffff00]力量料理B[-][/c]可不够呢…', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9282] = { id = 9282, Voice = '', Text = '咦？这[c][ffff00]坚硬外皮[-][/c]数量可不太对，你还是再找一些来吧…', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9281] = { id = 9281, Voice = '', Text = '谢谢你冒险者，你帮了我大忙了！', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9280] = { id = 9280, Voice = '', Text = '你回来啦？我的[c][ffff00]力量料理B[-][/c]呢？', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9279] = { id = 9279, Voice = '', Text = '好了朋友，故事也已经讲完了，该是你付报酬的时间了。我需要一些[c][ffff00]力量料理B[-][/c]。', Speaker = 1588, Option = '好的', Action = { num = 1, actionid = 201,},}, 
	[9278] = { id = 9278, Voice = '', Text = '这就是洛斯·拉比尔的故事。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9277] = { id = 9277, Voice = '', Text = '最终决定加入女神之手，成为女神的教徒，他希望有朝一日能够借助女神的力量复活他的姐姐。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9276] = { id = 9276, Voice = '', Text = '洛斯·拉比尔逃离了梦罗克，见到了女神之手的执事，想到自己的信仰的颠覆，他沉思许久。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9275] = { id = 9275, Voice = '', Text = '看着毙于他手的导师，洛斯·拉比尔大笑着说出：黑暗中的正义？刺客公会的信条？可笑！', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9274] = { id = 9274, Voice = '', Text = '洛斯·拉比尔手中尖锐的拳刃一击又一击不断地挥出，用得全是他的导师教他的技巧。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9273] = { id = 9273, Voice = '', Text = '洛斯乘着艾勒梅斯和无名者出门执行任务的时机，运用导师曾教给他的隐匿伏击连续技重创了他的导师。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9272] = { id = 9272, Voice = '', Text = '沉寂许久，他绝定离开刺客公会，在走之前他决定复仇。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9271] = { id = 9271, Voice = '', Text = '却不知被执行任务提前回来的洛斯听到了，所有信仰一朝崩溃，洛斯捏紧拳头，指甲刺入手心血流不止，但洛斯已经感觉不到疼痛。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9270] = { id = 9270, Voice = '', Text = '他甚至大笑着说：他的姐姐被我逼死，他还尊敬地叫我老师，这感觉棒极了！', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9269] = { id = 9269, Voice = '', Text = '直到有一天，喝醉酒的导师在于友人的交谈中，说出了这过去他曾做过的往事。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9268] = { id = 9268, Voice = '', Text = '冒险者你真是太棒了，那么我也可以继续我的故事了。', Speaker = 1588, Option = '', Action = { num = 1, actionid = 200,},}, 
	[9267] = { id = 9267, Voice = '', Text = '他讨厌沙盗，正如我讨厌沙漠之狼一样，冒险者，帮我消灭一些[c][ffff00]沙漠之狼[-][/c]吧！', Speaker = 1588, Option = '好吧', Action = { num = 1, actionid = 201,},}, 
	[9266] = { id = 9266, Voice = '', Text = '他花了几年时间，一己之力屠灭了近乎所有的沙盗，但他对他姐姐的死亡仍然不能释怀，只能苦苦的压抑住自己内心的悲苦。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9265] = { id = 9265, Voice = '', Text = '年轻的洛斯相信了他的导师，在这之后，他更是刻苦学习，练习着刺客公会各种刺杀技巧。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9264] = { id = 9264, Voice = '', Text = '他的导师惨然一笑：沙盗的数量太多，他没有能力救下村子。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9263] = { id = 9263, Voice = '', Text = '在艾勒梅斯的帮助下，洛斯振作了起来，他回到刺客公会后带着愤怒质问他的导师，为什么没有保护住村落？', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9262] = { id = 9262, Voice = '', Text = '他狂奔到了家，看到他的姐姐满身鲜血地倒在了和一片狼藉的家中，伤心欲绝的他甚至忘记了如何哭泣', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9261] = { id = 9261, Voice = '', Text = '几天后，从刺客公会返回的洛斯带着艾勒梅斯回到了村落，却发现整个村落灭绝了生机。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9260] = { id = 9260, Voice = '', Text = '说完艾丽莎横刀一抹，结束了自己年轻的生命。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9259] = { id = 9259, Voice = '', Text = '你永远得不到我，就算用我的弟弟威胁我也一样，因为我永远不可能成为我弟弟负担。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9258] = { id = 9258, Voice = '', Text = '艾丽莎惊愕，但是随即反应了过来，她拿出一把沙漠女子装饰用的匕首，轻声笑着。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9257] = { id = 9257, Voice = '', Text = '他愤怒之下狠狠地威胁她：如果你想你的弟弟能够好好的活下去，那么就乖乖的跟我走！', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9256] = { id = 9256, Voice = '', Text = '终于有一天，导师忍不住对这位美丽的女孩下手，艾丽莎拼死反抗，导师害怕伤了这位女孩，一时竟然不能得手。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9255] = { id = 9255, Voice = '', Text = '洛斯的导师也被这位女孩吸引——他想霸占这颗美丽的明珠，在导师的眼里，只要拥有力量，没有什么是得不到的——一如当初死在他手下的刺客公会荣誉执事。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9254] = { id = 9254, Voice = '', Text = '洛斯的姐姐是沙漠中远近闻名的美人，温柔体贴，能歌善舞，村落里的人们都称赞她是沙漠中的一颗明珠。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9253] = { id = 9253, Voice = '', Text = '导师是一个很严厉的人，起码在他眼里是这样的，但是他的导师却还有着不为人知的一面。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9252] = { id = 9252, Voice = '', Text = '那么我就开始继续讲述我的故事了。', Speaker = 1588, Option = '', Action = { num = 1, actionid = 200,},}, 
	[9251] = { id = 9251, Voice = '', Text = '这么快就收集来了？看来你很喜欢我的故事啊。让我点点材料。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9250] = { id = 9250, Voice = '', Text = '嘿嘿，讲到这里，你应该知道我想要点好处了吧~~想继续听下去，就带一些[c][ffff00]坚硬外皮[-][/c]来吧。', Speaker = 1588, Option = '好的', Action = { num = 1, actionid = 201,},}, 
	[9249] = { id = 9249, Voice = '', Text = '然而，对于洛斯来说，他的灾难才刚刚开始。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9248] = { id = 9248, Voice = '', Text = '而洛斯·拉比尔以他无与伦比的天赋，成为了众多孩子们眼中当中的“幸运儿”。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9247] = { id = 9247, Voice = '', Text = '沙漠的孩子们深受刺客公会的影响，他们都希望能成为一个真正的刺客。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9246] = { id = 9246, Voice = '', Text = '他的导师不仅是是大陆上成名许久的刺客，还有刺客公会荣誉执事。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9245] = { id = 9245, Voice = '', Text = '十四岁那年，洛斯·拉比尔遇到了他的导师——那个造就与毁灭了他一生的人。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9244] = { id = 9244, Voice = '', Text = '洛斯·拉比尔和他的姐姐艾丽莎·拉比尔，相依为命地在沙漠中的一个小村落。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9243] = { id = 9243, Voice = '', Text = '他有一个无比光辉的未来，以及一个爱他的姐姐。他就是洛斯·拉比尔。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9242] = { id = 9242, Voice = '', Text = '他曾经是刺客公会的骄傲，无名者也称他与艾勒梅斯是刺客公会的未来。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9241] = { id = 9241, Voice = '', Text = '他曾是刺客公会最为杰出的弟子之一，他曾经与艾勒梅斯并称刺客双星。', Speaker = 1588, Option = '', Action = _EmptyTable,}, 
	[9240] = { id = 9240, Voice = '', Text = '今天，我们就来讲讲洛斯·拉比尔的故事吧。', Speaker = 1588, Option = '', Action = { num = 1, actionid = 200,},}, 
	[9200] = { id = 9200, Voice = '', Text = '既然你通过了考核，那么，我就正式授予你E级冒险者称号。你要再接再厉，冒险者！', Speaker = 1501, Option = '', Action = _EmptyTable,}, 
	[9199] = { id = 9199, Voice = '', Text = '你回来了。洛萨达克对你的评价可是很高的呢！', Speaker = 1501, Option = '', Action = _EmptyTable,}, 
	[9198] = { id = 9198, Voice = '', Text = '谢谢你冒险者，如果有天我们进行婚礼，我希望你能来~~~', Speaker = 1223, Option = '一定！', Action = _EmptyTable,}, 
	[9197] = { id = 9197, Voice = '', Text = '我还在想这个坏人要让我等多久呢！总算让我等到了。', Speaker = 1223, Option = '', Action = _EmptyTable,}, 
	[9196] = { id = 9196, Voice = '', Text = '这是什么…啊！', Speaker = 1223, Option = '', Emoji = 2, Action = _EmptyTable,}, 
	[9195] = { id = 9195, Voice = '', Text = '就算是有急事，你也应该保持礼仪，这才是对人应有的态度哟~', Speaker = 1223, Option = '', Action = _EmptyTable,}, 
	[9158] = { id = 9158, Voice = '', Text = '你带来了足够的[c][ffff00]幸运四叶草[-][/c]吗？', Speaker = 3288, Option = '{我这就去收集,11},{给你幸运四叶草,12}', Action = _EmptyTable,}, 
	[9157] = { id = 9157, Voice = '', Text = '你带来了足够的[c][ffff00]透明之布[-][/c]吗？', Speaker = 3288, Option = '{我这就去收集,8},{给你透明之布,18}', Action = _EmptyTable,}, 
	[9156] = { id = 9156, Voice = '', Text = '你带来了足够的[c][ffff00]华丽金属[-][/c]吗？', Speaker = 3288, Option = '{我这就去收集,3},{给你华丽金属,4}', Action = _EmptyTable,}, 
	[9155] = { id = 9155, Voice = '', Text = '不过下一种材料获得起来可比华丽金属危险得多。', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[9154] = { id = 9154, Voice = '', Text = '当然，首先还是要先恭喜你成为一名D级冒险者！', Speaker = 1501, Option = '', Action = _EmptyTable,}, 
	[9153] = { id = 9153, Voice = '', Text = '这种材料只有在幽灵船的幽灵波利身上比较常见。如果你能为我带来3个[c][ffff00]透明之布[-][/c]，我想我会对你刮目相看。', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[9152] = { id = 9152, Voice = '', Text = '现在你先给我找到一些[c][ffff00]华丽金属[-][/c]吧，我只需要1个。', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[9151] = { id = 9151, Voice = '', Text = '真是令人兴奋，你完成得非常出色，冒险者！', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[9150] = { id = 9150, Voice = '', Text = '众所周知，拥有丰富收藏的冒险手册，是一个冒险者成功的象征。', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[9147] = { id = 9147, Voice = '', Text = '那么还是等你想好之后再来找我吧！', Speaker = 1501, Option = '', Action = _EmptyTable,}, 
	[9146] = { id = 9146, Voice = '', Text = '盖尔诺顿已经被我，汤玛斯大主教以圣光的名义就地处决，你回去就这么向公会报告吧！', Speaker = 1228, Option = '', Action = _EmptyTable,}, 
	[9121] = { id = 9121, Voice = '', Text = '希望你能够发扬冒险者精神，给冒险者公会带来新的活力！', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[9120] = { id = 9120, Voice = '', Text = '不过付出就有回报！恭喜你冒险者，你完成了C级冒险者晋级考核。我代表冒险者公会正式给予你C级冒险者称号！', Speaker = 2540, Option = '', Action = _EmptyTable,}, 
	[9119] = { id = 9119, Voice = '', Text = '哦呀~冒险者，感觉怎么样？这次的考核不容易吧？', Speaker = 2540, Option = '是的', Action = _EmptyTable,}, 
	[9116] = { id = 9116, Voice = '', Text = '这点材料太少了吧，远远不够呢！你还是再找一些来吧！', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[9115] = { id = 9115, Voice = '', Text = '干的不错冒险者！你做得非常好！', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[9113] = { id = 9113, Voice = '', Text = '首先我需要一些[c][ffff00]兽人指甲[-][/c]，你能帮我弄来吗？', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[9112] = { id = 9112, Voice = '', Text = '冒险者，现在女神之手频繁活动，我们需要储备一些稀缺的物资以应对将要到来的混乱。储备的过程会有些繁琐，但我们需要你的帮助，毕竟这也是C级冒险者考核的内容。', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[9111] = { id = 9111, Voice = '', Text = '很不错，冒险者，你的阅历也达到了C级冒险者应有的素质。', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[9110] = { id = 9110, Voice = '', Text = '我需要你成功战胜过无限塔第70层，如果没有，那么很抱歉，规矩所在，我也不能让你通过考核。', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[9109] = { id = 9109, Voice = '', Text = '不过，作为你的考官，必要的流程还是要走的，那么，丰富的阅历是一个冒险者的象征。', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[9108] = { id = 9108, Voice = '', Text = '你前面的表现已经足以让你成为一个C级冒险者了，毕竟这是一个C级的机密任务。', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[9107] = { id = 9107, Voice = '', Text = '被汤玛斯大主教就地处决了吗...这样也好，我的任务就算完成了，多谢你了冒险者。', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[9106] = { id = 9106, Voice = '', Text = '冒险者你回来了？情况怎么样了？', Speaker = 3292, Option = '叙述经过', Action = _EmptyTable,}, 
	[9105] = { id = 9105, Voice = '', Text = '我不放心你，就过来看看，没想到却看到这样的一幕...', Speaker = 1228, Option = '', Action = _EmptyTable,}, 
	[9104] = { id = 9104, Voice = '', Text = '啊……', Speaker = 3293, Option = '', Action = _EmptyTable,}, 
	[9103] = { id = 9103, Voice = '', Text = '早晚有一天...早晚有一天！！！你们会成为大人的囚徒，受尽折磨！！！哈哈哈哈！！！', Speaker = 3293, Option = '', Action = _EmptyTable,}, 
	[9102] = { id = 9102, Voice = '', Text = '啊！！！哈哈哈哈！！！除了我...女神之手还有其他人隐藏在你们中间！！！', Speaker = 3293, Option = '', Action = _EmptyTable,}, 
	[9101] = { id = 9101, Voice = '', Text = '啊！！！！汤玛斯！！！我诅咒你！！我诅咒你的灵魂将化为一滩血水供魔鬼们吸食！！', Speaker = 3293, Option = '', Action = _EmptyTable,}, 
	[9100] = { id = 9100, Voice = '', Text = '还想跑！光耀审判！', Speaker = 1228, Option = '', Action = _EmptyTable,}, 
	[9099] = { id = 9099, Voice = '', Text = '汤...汤玛斯！可恶！冒险者，我不会放过你的！你给我等着！', Speaker = 3293, Option = '', Action = _EmptyTable,}, 
	[9098] = { id = 9098, Voice = '', Text = '放肆！堕落的你，也敢呼喊圣光的名字！今天我就代表神赐予你永远的沉眠！你的灵魂将会在圣光中得到净化！', Speaker = 1228, Option = '', Action = _EmptyTable,}, 
	[9097] = { id = 9097, Voice = '', Text = '可恶，你竟敢！你竟敢破坏我的计划！我一定要用锁链把你钉在十字架上，永世接受圣光的问拷！', Speaker = 3293, Option = '', Action = _EmptyTable,}, 
	[9096] = { id = 9096, Voice = '', Text = '所以，在死灵暴走前消灭掉它，是最好的办法。', Speaker = 1228, Option = '交给我吧', Action = _EmptyTable,}, 
	[9095] = { id = 9095, Voice = '', Text = '至于他的目的，应该是吉芬塔下的那片未知的国度吧...总之，不能让他们在吉芬地下制造混乱。', Speaker = 1228, Option = '', Action = _EmptyTable,}, 
	[9094] = { id = 9094, Voice = '', Text = '根据你所说，我认为他出现在吉芬地下是为了合理利用他修习多年的圣光力量，来刺激死灵暴走。', Speaker = 1228, Option = '', Action = _EmptyTable,}, 
	[9093] = { id = 9093, Voice = '', Text = '盖尔诺顿啊...我过去很看好这个有潜力的小伙子，没想到他会堕落...神不会宽恕他的！', Speaker = 1228, Option = '', Action = _EmptyTable,}, 
	[9092] = { id = 9092, Voice = '', Text = '那就好，那么你现在来找我是？', Speaker = 1228, Option = '盖尔诺顿', Action = _EmptyTable,}, 
	[9091] = { id = 9091, Voice = '', Text = '他还在休养当中，多谢你，大主教。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[9090] = { id = 9090, Voice = '', Text = '冒险者？你又来了，怎么样，你的朋友治好了吗？', Speaker = 1228, Option = '', Action = _EmptyTable,}, 
	[9089] = { id = 9089, Voice = '', Text = '可恶！竟然跑了！牧师...大主教应该知道些什么。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[9088] = { id = 9088, Voice = '', Text = '不要走！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[9087] = { id = 9087, Voice = '', Text = '你有能力，你就留下我啊！', Speaker = 3293, Option = '', Action = _EmptyTable,}, 
	[9086] = { id = 9086, Voice = '', Text = '桀桀桀桀...就凭你？别让我发笑了，要不是死灵...嘿，我早就送你去见你们伟大的奥丁了！', Speaker = 3293, Option = '', Action = _EmptyTable,}, 
	[9085] = { id = 9085, Voice = '', Text = '你竟然背叛教会！背叛王国！我一定会阻止你！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[9084] = { id = 9084, Voice = '', Text = '是你？那个站在卡兹洛斯身边的D级冒险者？卡兹洛斯脑子坏掉了吗？竟然让你来阻止我？！', Speaker = 3293, Option = '', Action = _EmptyTable,}, 
	[9083] = { id = 9083, Voice = '', Text = '谁？！', Speaker = 3293, Option = '', Action = _EmptyTable,}, 
	[9082] = { id = 9082, Voice = '', Text = '对了，他的名字是盖尔诺顿。记住他的名字！', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[9081] = { id = 9081, Voice = '', Text = '我收到冒险者公会发来的消息，这位队长出现在吉芬地下，你可以去那里找找线索。', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[9080] = { id = 9080, Voice = '', Text = '啊！那真是太感谢你了，这次事情结束，我一定马上准备你的考核。', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[9079] = { id = 9079, Voice = '', Text = '只是没想到，他竟然伤了我！我很想接着调查下去，但是...诺雅应该不会放任我这么乱来。', Speaker = 3292, Option = '交给我吧', Action = _EmptyTable,}, 
	[9078] = { id = 9078, Voice = '', Text = '我奉命调查这位队长，没想到，他发现我的追踪后竟然不顾一切的逃走了，我一直追踪他来到斐扬。', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[9077] = { id = 9077, Voice = '', Text = '我们冒险者公会收到消息，教会裁判所的一名队长级人物与女神之手接触，为女神之手提供情报，这无疑是背叛了整个王国。', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[9076] = { id = 9076, Voice = '', Text = '这种牧师的力量体现在他们无与伦比的破坏力，与之相对的，他们的治疗能力就比较弱。', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[9075] = { id = 9075, Voice = '', Text = '普隆德拉教会麾下有一个由牧师组成的势力，名叫教会裁判所。不过他们不是普通的牧师，而是战斗型牧师。', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[9074] = { id = 9074, Voice = '', Text = '这本来是机密，不过你也是冒险者公会的一员，也不算是外人，我就跟你说说吧。', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[9073] = { id = 9073, Voice = '', Text = '啊...好多了，多谢你冒险者。', Speaker = 3292, Option = '发生了什么？', Action = _EmptyTable,}, 
	[9072] = { id = 9072, Voice = '', Text = '啊！抱歉，冒险者，我陷入思考就会这样...你直接对准病患部位使用吧。我会看着，没事的。', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[9071] = { id = 9071, Voice = '', Text = '我的天，不愧是汤玛斯大主教，这种方式真的是从来没见过呢！我的草药学是不是也能借鉴这用法呢...', Speaker = 2535, Option = '咳咳', Action = _EmptyTable,}, 
	[9070] = { id = 9070, Voice = '', Text = '黑暗卷轴？汤玛斯把黑暗力量囚禁在卷轴中，使用卷轴后，封印的黑暗力量会瞬间喷涌而出，对准病患部位直接冲垮圣光力量？', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[9069] = { id = 9069, Voice = '', Text = '你回来了？汤玛斯大主教有办法吗？', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[9068] = { id = 9068, Voice = '', Text = '好了！拿去吧，冒险者。圣光的伤害还是很少见的，如果与我们教会有关系，我希望你能通知我一声。', Speaker = 1228, Option = '没问题', Action = _EmptyTable,}, 
	[9066] = { id = 9066, Voice = '', Text = '不过，毕竟是黑暗力量，对病人的身体会造成一定程度的损伤，之后的话，还是需要诺雅的治疗才能完全康复。', Speaker = 1228, Option = '', Action = _EmptyTable,}, 
	[9065] = { id = 9065, Voice = '', Text = '接下来我来帮你处理一下材料，我会把恶魔角制作成一个卷轴，你到时候只要对着病患部位直接施放就好了。', Speaker = 1228, Option = '', Action = _EmptyTable,}, 
	[9064] = { id = 9064, Voice = '', Text = '这点材料恐怕不够救你的朋友呢，你还是再找一些来吧。', Speaker = 1228, Option = '', Action = _EmptyTable,}, 
	[9063] = { id = 9063, Voice = '', Text = '干的不错冒险者，你的实力在王国中也算一把好手了。', Speaker = 1228, Option = '', Action = _EmptyTable,}, 
	[9061] = { id = 9061, Voice = '', Text = '但是这种方法所需要的[c][ffff00]恶魔角[-][/c]数量将会相当庞大，冒险者，如果你能为我带来[c][ffff00]1000份恶魔角[-][/c]，那么我想我可以完全治愈卡兹洛斯。', Speaker = 1228, Option = '', Action = _EmptyTable,}, 
	[9060] = { id = 9060, Voice = '', Text = '[c][ffff00]恶魔角[-][/c]，如果能够取得这种材料再加上独特的处理方法，我就可以从中提取出纯净的黑暗力量，来克制圣光力量的伤害。', Speaker = 1228, Option = '', Action = _EmptyTable,}, 
	[9059] = { id = 9059, Voice = '', Text = '治疗圣光属性的伤害？不是不可以，只是对所需要的材料有非常严格的要求，想要治愈的话，难度很大啊。', Speaker = 1228, Option = '什么材料？', Action = _EmptyTable,}, 
	[9058] = { id = 9058, Voice = '', Text = '啊，是你啊冒险者，真的是好久不见了。', Speaker = 1228, Option = '', Action = _EmptyTable,}, 
	[9057] = { id = 9057, Voice = '', Text = '也许，只有最了解圣光力量的汤玛斯大主教才能解决这种问题吧。', Speaker = 2535, Option = '明白了', Action = _EmptyTable,}, 
	[9056] = { id = 9056, Voice = '', Text = '如果想尽快康复，只有驱逐那些破坏性的圣光力量。很遗憾，我没有这个能力。', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[9055] = { id = 9055, Voice = '', Text = '卡兹洛斯就是这种情况，外伤我治好了，但是他体内残留的圣光力量，我却没有办法驱逐干净。', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[9054] = { id = 9054, Voice = '', Text = '比如汤玛斯大主教的实力就和他的治愈能力一样出名，如果是神圣属性造成的伤，就不能用圣光属性治疗，否则会加重病情。', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[9053] = { id = 9053, Voice = '', Text = '众所周知，神圣属性的力量可以治愈一切伤痛，但是人们似乎忘记了，神圣属性其实也拥有可怕的杀伤力。', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[9052] = { id = 9052, Voice = '', Text = '他表面上是康复了，但是实际上他的情况还是很糟糕。我还是简单的跟你说吧。', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[9051] = { id = 9051, Voice = '', Text = '哈？他才刚刚完成了外伤治愈就想出去执行任务了？他当我是汤玛斯大主教吗？！', Speaker = 2535, Option = '', Action = _EmptyTable,}, 
	[9050] = { id = 9050, Voice = '', Text = '真是麻烦啊...请帮我问问诺雅我什么时候能离开这里，我手里还有机密任务呢！', Speaker = 3292, Option = '好的', Action = _EmptyTable,}, 
	[9049] = { id = 9049, Voice = '', Text = '真是抱歉冒险者，耽误了你的考核。', Speaker = 3292, Option = '没事', Action = _EmptyTable,}, 
	[9048] = { id = 9048, Voice = '', Text = '呼...将我传送走后...你去医者诺雅那里找我...', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[9047] = { id = 9047, Voice = '', Text = '呼...它会把我传送到斐扬南部的医者诺雅身边...我现在急需治疗...', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[9046] = { id = 9046, Voice = '', Text = '...呼...冒险者...我的口袋里...有传送卷轴...帮我使用它...', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[9045] = { id = 9045, Voice = '', Text = '桀桀...', Speaker = 3293, Option = '', Action = _EmptyTable,}, 
	[9044] = { id = 9044, Voice = '', Text = '啊！好痛！呼...该死的...', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[9043] = { id = 9043, Voice = '', Text = '在走之前，我要解决掉你这个麻烦！', Speaker = 3293, Option = '', Action = _EmptyTable,}, 
	[9042] = { id = 9042, Voice = '', Text = '我最讨厌的就是有人追踪我，一直以来只有我追踪别人的份！你已经惹怒我了！', Speaker = 3293, Option = '', Action = _EmptyTable,}, 
	[9041] = { id = 9041, Voice = '', Text = '桀桀...前几天紧紧追着我不放，现在就不知道我是谁了？你还真是健忘，C级冒险者就这点水平吗？', Speaker = 3293, Option = '', Action = _EmptyTable,}, 
	[9040] = { id = 9040, Voice = '', Text = '什么人？！', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[9039] = { id = 9039, Voice = '', Text = '听着冒险者，我现在很忙，大概没有办法为你进行考核了...', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[9038] = { id = 9038, Voice = '', Text = '啊啊啊啊，真是麻烦！索罗·图克可真会给我找事做，不知道我在执行任务吗？', Speaker = 3292, Option = '', Action = _EmptyTable,}, 
	[9037] = { id = 9037, Voice = '', Text = '冒险者？看样子是专程来找我的...说吧，找我有什么事情吗？', Speaker = 3292, Option = '冒险考核', Action = _EmptyTable,}, 
