	[8977] = { id = 8977, Voice = '', Text = '上架的物品，我们交易行会收取一定的手续费，交易成功后会收取一定数额的交易税，如果上架的物品24小时内没有售出就是视为过期，那么会退回物品。', Speaker = 2159, Option = '明白了', Action = _EmptyTable,}, 
	[8976] = { id = 8976, Voice = '', Text = '那么，我就简单的为您介绍一下交易所。我们交易所是使用银币进行交易，交易的价格是由我们交易所的鉴宝师确定，当然，我们也会根据交易情况上下浮动的。', Speaker = 2159, Option = '', Action = _EmptyTable,}, 
	[8900] = { id = 8900, Voice = '', Text = '哼，记住了！我叫美羽，是一个超！级！美！少！女！！我这里往往有一些你意想不到的活动哦~~~', Speaker = 2160, Option = '明白了', Action = _EmptyTable,}, 
	[8899] = { id = 8899, Voice = '', Text = '哼…连我是谁都不知道…不想理你！', Speaker = 2160, Option = '别啊…', Emoji = 12, Action = _EmptyTable,}, 
	[8817] = { id = 8817, Voice = '', Text = '真是不简单的冒险者，你的勇气改变了我对你的认知，希望你能在冒险的路上越走越远，未来的你将无可限量。', Speaker = 1501, Option = '', Emoji = 20, Action = _EmptyTable,}, 
	[8816] = { id = 8816, Voice = '', Text = '考核回来了？嗯，看样子是完成了考核呢。快把你的考核证明拿给我吧。', Speaker = 1501, Option = '', Action = _EmptyTable,}, 
	[8815] = { id = 8815, Voice = '', Text = '这是你的考核证明，拿去给卡卡罗吧，他会帮你完成进阶的。', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[8814] = { id = 8814, Voice = '', Text = '你居然成功了！你知道吗？我还没有见过哪个冒险者能像你这样，这么从容地通过D级冒险者的考核。荣耀非你莫属！我很荣幸能够成为你的考官。', Speaker = 3288, Option = '', Emoji = 1, Action = _EmptyTable,}, 
	[8813] = { id = 8813, Voice = '', Text = '如果你对自己充满了信心，那么请你消灭金字塔中的俄塞里斯吧！这个不可一世的古老霸主，正在急切地寻找实力相当的对手。', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[8812] = { id = 8812, Voice = '', Text = '那么，开始最后一项试炼！', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[8811] = { id = 8811, Voice = '', Text = '前几项考核你都完成得非常顺利，冒险者，你很有天赋，只要完成最后一项考核，你就正式成为D级冒险者了！', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[8810] = { id = 8810, Voice = '', Text = '厉害厉害！不愧是敢于参加D级冒险考核的冒险者！', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[8809] = { id = 8809, Voice = '', Text = '这么快？好吧，现在，就让我看看你的冒险手册吧！', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[8808] = { id = 8808, Voice = '', Text = '如果数量不能让我满意的话，那么就证明你的冒险经历还不够哦~~', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[8807] = { id = 8807, Voice = '', Text = '你有没有见过一些变异且强大的魔物吗？我听闻这些魔物被人称作MINI魔物，我想看看你冒险手册中有多少这种魔物的收藏。', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[8806] = { id = 8806, Voice = '', Text = '接下来进行第四项考核吧。', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[8804] = { id = 8804, Voice = '', Text = '不过我听说蓝疯兔很是稀少，非常难遇到，所以冒险者，这将是一个非常困难的考验。', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[8803] = { id = 8803, Voice = '', Text = '好吧，我需要3个[c][ffff00]幸运四叶草[-][/c]，这种材料非常罕见，我听说南门的蓝疯兔身上就有。', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[8802] = { id = 8802, Voice = '', Text = '最后这个材料，我只能说非常稀有，恐怕不那么容易能找到，你有信心接下这个考核吗？', Speaker = 3288, Option = '交给我吧', Action = _EmptyTable,}, 
	[8800] = { id = 8800, Voice = '', Text = '没想到你这么快就能找到华丽金属，很棒的冒险者！', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[8799] = { id = 8799, Voice = '', Text = '这点材料太少了吧，要知道材料仓库缺很多呢！', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[8798] = { id = 8798, Voice = '', Text = '干的不错冒险者！你做得非常好！', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[8796] = { id = 8796, Voice = '', Text = '根据仓库目前的情况，需要补充三种极其罕见的珍贵物品，想要找到它们可能会有一点难度。', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[8795] = { id = 8795, Voice = '', Text = '为冒险者公会的仓库补充必要的物资是每一个冒险者必须去做的事情。', Speaker = 3288, Option = '好的', Action = _EmptyTable,}, 
	[8794] = { id = 8794, Voice = '', Text = '那么接下来，进行第三项考核，冒险者，这次你可要有些心理准备。', Speaker = 3288, Option = '没问题', Action = _EmptyTable,}, 
	[8793] = { id = 8793, Voice = '', Text = '这么快就回来了，看来这个考验还真是太简单了点呢。', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[8792] = { id = 8792, Voice = '', Text = '听着，现在梦罗克城外的沙漠中魔物横行，以沙漠之狼的数量最多，你的第二个考验就是清理一些城外的沙漠之狼。', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[8791] = { id = 8791, Voice = '', Text = '不错不错，勇气可嘉！那么现在就开始吧。', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[8790] = { id = 8790, Voice = '', Text = '那么第一项温和的考核已经通过了，接下来才是真正的试炼，你已经准备好了吗？', Speaker = 3288, Option = '是的', Action = _EmptyTable,}, 
	[8789] = { id = 8789, Voice = '', Text = '远道而来的冒险者，能找到这儿，足够说明你的坚韧和睿智。', Speaker = 3288, Option = '', Action = _EmptyTable,}, 
	[8788] = { id = 8788, Voice = '', Text = '他现在正在梦罗克城外，可他的脚下并不是沙漠，那么，你的第一项考核，就是根据我的提示去找到他！', Speaker = 1501, Option = '明白', Action = _EmptyTable,}, 
	[8787] = { id = 8787, Voice = '', Text = 'D级冒险者的考官是公会中颇有资历的D级冒险者摩根·哈里森。', Speaker = 1501, Option = '', Action = _EmptyTable,}, 
	[8786] = { id = 8786, Voice = '', Text = '我高兴你有这样的信心，我也希望你能够通过考核，成为D级冒险者！', Speaker = 1501, Option = '', Action = _EmptyTable,}, 
	[8785] = { id = 8785, Voice = '', Text = '那么，现在要参加D级冒险者的考验了是吗？需要提醒你的是，考核一旦开始就不能终止了哦，确定参加考核吗？', Speaker = 1501, Option = '{确定参加,2},{我还没想好,15}', Action = _EmptyTable,}, 
	[8784] = { id = 8784, Voice = '', Text = '好久不见，[c][ffff00][PlayerName][-][/c]，没想到你这么快就走到了这一步，冒险者公会就需要你这样的人才！', Speaker = 1501, Option = '', Action = _EmptyTable,}, 
	[8641] = { id = 8641, Voice = '', Text = '咦？这点[c][ffff00]玻璃珠[-][/c]可不够呢…', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8640] = { id = 8640, Voice = '', Text = '这么快就收集来了吗？我来看看数量……', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8639] = { id = 8639, Voice = '', Text = '唔，故事讲完了，作为报酬，我需要一些[c][ffff00]玻璃珠[-][/c]，你能帮我弄来吗？', Speaker = 1592, Option = '', Action = { num = 1, actionid = 201,},}, 
	[8638] = { id = 8638, Voice = '', Text = '这就是索罗·图克的故事。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8637] = { id = 8637, Voice = '', Text = '但是总有一天，索罗·图克的暴君狂怒之名，会再次响彻整个王国。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8636] = { id = 8636, Voice = '', Text = '索罗·图克从此性格大变，成为了拥有和善面具的人。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8635] = { id = 8635, Voice = '', Text = '柯丽娜逝去了，逝去前，她劝索罗·图克放弃狂怒者的称号，好好带着诺娜生活。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8634] = { id = 8634, Voice = '', Text = '而沙盗也在索罗最后的一击中死去。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8633] = { id = 8633, Voice = '', Text = '索罗的箭一次又一次的封死了他的退路，沙盗心一狠，拼命把自己的匕首送入了受伤的柯丽娜身上。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8632] = { id = 8632, Voice = '', Text = '索罗·图克怒火中烧，大吼一声向沙盗冲去，沙盗见到索罗·图克回来了，立刻选择逃跑，但是在索罗的弓箭下无所遁形。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8631] = { id = 8631, Voice = '', Text = '却发现沙盗首领偷袭了柯丽娜，而柯丽娜已经快撑不住了。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8630] = { id = 8630, Voice = '', Text = '索罗·图客瞬间愤怒，不顾妻子的劝阻前往追击蜂后，在经历了一番大战，索罗·图克带着蜂后的尸体回到了营地。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8629] = { id = 8629, Voice = '', Text = '沙盗用特殊的药剂在妙勒尼山脉中吸引了蜂后，蜂后惊吓到了索罗·图克的小女儿诺娜。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8628] = { id = 8628, Voice = '', Text = '就是他的性格太暴躁太容易愤怒了，沙盗头领设下了一个圈套。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8627] = { id = 8627, Voice = '', Text = '但是好景不长，一个残留的沙盗头领深深知道索罗·图客的弱点。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8626] = { id = 8626, Voice = '', Text = '在旅途中，索罗·图克的妻子为她生下了一个女儿，一家三口幸福美满。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8625] = { id = 8625, Voice = '', Text = '梦罗克的安全已经在小队的清理下渐渐稳定下来，他谢绝了巴尔洛的挽留，索罗·图克带着妻子远行。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8624] = { id = 8624, Voice = '', Text = '游侠小队早出晚归，沙盗，魔物都是他们的目标，而索罗·图客也与小队成员之一柯丽娜结为夫妻。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8623] = { id = 8623, Voice = '', Text = '索罗·图克在巴尔洛的默许下，建立了梦罗克游侠小队，旨在建立梦罗克的正常秩序。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8622] = { id = 8622, Voice = '', Text = '这么快就回来了？好好好。那我就继续我的故事了。', Speaker = 1592, Option = '', Action = { num = 1, actionid = 200,},}, 
	[8621] = { id = 8621, Voice = '', Text = '想知道接下来的故事就帮我清理一些[c][ffff00]妖道[-][/c]吧。', Speaker = 1592, Option = '', Action = { num = 1, actionid = 201,},}, 
	[8620] = { id = 8620, Voice = '', Text = '索罗·图克带来得这个礼物令巴尔洛将军很是开心，对于索罗·图克接下来的要求并没有为难，痛快应允了。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8619] = { id = 8619, Voice = '', Text = '梦罗克大陆卫队甚至经常没见到流浪之狼的样子，就已经死伤惨重。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8618] = { id = 8618, Voice = '', Text = '巴尔洛惊叹不已，流浪之狼是为祸梦罗克很久的魔物了，因为对沙漠熟悉而且实力无比强大。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8617] = { id = 8617, Voice = '', Text = '这时索罗·图克出现了，带着流浪之狼的狼头出现在巴尔洛将军的面前。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8616] = { id = 8616, Voice = '', Text = '梦罗克的安全问题深深地困扰着巴尔洛将军，沙盗与城外的魔物时刻的在提醒巴尔洛将军，不能掉以轻心，否则梦罗克城将毁于一旦。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8615] = { id = 8615, Voice = '', Text = '梦罗克城刚刚从战火中脱离出来，梦罗克大陆卫队建立刚刚不足三年。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8614] = { id = 8614, Voice = '', Text = '而在来斐扬之前，索罗·图克却是带着狂怒者称号的暴君。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8613] = { id = 8613, Voice = '', Text = '同时，他强大的实力也赢得了斐扬年轻人的尊重，在人们眼里索罗·图克是一个稳重的前辈。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8612] = { id = 8612, Voice = '', Text = '他拥有无比丰富的冒险经验，能够给予冒险者们经验的指导。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8611] = { id = 8611, Voice = '', Text = '作为冒险者公会的元老之一，对他来说，没有什么困难能够难倒他。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8610] = { id = 8610, Voice = '', Text = '在这个外表和蔼从不与人争斗的面具后，隐藏着地是一颗强大且火热的心。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8609] = { id = 8609, Voice = '', Text = '狂怒者—索罗·图克是冒险者公会派遣到斐扬的特派员。', Speaker = 1592, Option = '', Action = _EmptyTable,}, 
	[8608] = { id = 8608, Voice = '', Text = '今天就讲讲狂怒者—索罗·图克的故事吧。', Speaker = 1592, Option = '', Action = { num = 1, actionid = 200,},}, 
	[8607] = { id = 8607, Voice = '', Text = '咦？这是我要的[c][ffff00]暖身料理[-][/c]吗？', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8606] = { id = 8606, Voice = '', Text = '咦？这点[c][ffff00]亡者遗物[-][/c]可不够呢…', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8605] = { id = 8605, Voice = '', Text = '唔，有了这[c][ffff00]亡者遗物[-][/c]，我想我的生活会变的更加宽裕，谢谢你，冒险者。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8604] = { id = 8604, Voice = '', Text = '冒险者你回来的可真快！[c][ffff00]亡者遗物[-][/c]收集好了么？', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8603] = { id = 8603, Voice = '', Text = '好了冒险者，故事你也已经听完了。作为报酬，我希望你能够帮我取得一些[c][ffff00]亡者遗物[-][/c]。', Speaker = 1583, Option = '', Action = { num = 1, actionid = 201,},}, 
	[8602] = { id = 8602, Voice = '', Text = '这就是创造者——固特伊的故事。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8601] = { id = 8601, Voice = '', Text = '现在，固特伊按照老师的命令来到了普隆德拉，训练更多强力的商人与铁匠。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8600] = { id = 8600, Voice = '', Text = '人们尊敬于固特伊的贡献，称他为创造者，意为创造幸福的人。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8599] = { id = 8599, Voice = '', Text = '他的每一次出现都放佛在创造着和平美满的景象。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8598] = { id = 8598, Voice = '', Text = '时而亲自参与魔物的围剿，减少人员的伤亡。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8597] = { id = 8597, Voice = '', Text = '他时而为村民们打造武器与铠甲，使他们免受魔物的侵扰。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8596] = { id = 8596, Voice = '', Text = '不久后大陆上逐渐流传着这样一个名字：创造者——固特伊。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8595] = { id = 8595, Voice = '', Text = '片刻后他带着满身鲜血从巢穴中出现，离开了这里。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8594] = { id = 8594, Voice = '', Text = '他的目标，就是为死去的妻儿复仇，他一人就冲进了沙盗的巢穴，在里面大肆杀戮。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8593] = { id = 8593, Voice = '', Text = '就这样过去了三年，固特伊再一次出现在苏克拉特沙漠。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8592] = { id = 8592, Voice = '', Text = '这么快就回来了？你还真是喜欢听我的故事呢！那我就继续我的故事了！', Speaker = 1583, Option = '', Action = { num = 1, actionid = 200,},}, 
	[8591] = { id = 8591, Voice = '', Text = '休息一下，想接着听的话，就请帮我消灭一些[c][ffff00]邪骸战士[-][/c]吧！', Speaker = 1583, Option = '', Action = { num = 1, actionid = 201,},}, 
	[8590] = { id = 8590, Voice = '', Text = '固特伊接受了冒险者为他安排的一切，就这样过去了三年。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8589] = { id = 8589, Voice = '', Text = '他带着固特伊离开了沙漠，前往传说中的商人之都艾尔贝塔。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8588] = { id = 8588, Voice = '', Text = '冒险者看着目光中带着坚定与仇恨的固特伊，决定收下了这个徒弟。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8587] = { id = 8587, Voice = '', Text = '固特伊拉住了冒险者，希望能够成为冒险者的学生，为了强大，也为了复仇。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8586] = { id = 8586, Voice = '', Text = '冒险者看着失魂落魄的固特伊，无奈的叹了口气，正要离去。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8585] = { id = 8585, Voice = '', Text = '固特伊下定决定，希望能够成为向这位冒险者一样的强者。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8584] = { id = 8584, Voice = '', Text = '这位冒险者的英姿在固特伊眼中不断闪耀，看着沙盗们在冒险者的力量下四散奔走。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8583] = { id = 8583, Voice = '', Text = '固特伊满腔的哀伤化为无尽的怒火，正在此时，一个路过的冒险者救了固特伊。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8582] = { id = 8582, Voice = '', Text = '但是丧心病狂的沙盗丝毫不顾及固特伊，残忍地伤害了他的亲人。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8581] = { id = 8581, Voice = '', Text = '固特伊哀求，拿出了自己所有的钱财，只为换自己妻子孩子一命。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8580] = { id = 8580, Voice = '', Text = '途经苏克拉特沙漠，他们遇到了传闻中的沙盗，固特伊雇佣的护卫根本不足以对抗穷凶恶极的沙盗。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8579] = { id = 8579, Voice = '', Text = '妻子带着女儿前往梦罗克探亲，乘坐着固特伊的运货车队出发。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8578] = { id = 8578, Voice = '', Text = '能够每天看着他们的笑容，固特伊觉得十分满足，直到那天灾难的降临——', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8577] = { id = 8577, Voice = '', Text = '在很久之前，固特伊还是一个老实本分的商人，每天为了妻子和孩子而忙碌。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8576] = { id = 8576, Voice = '', Text = '多谢你冒险家，那么我就继续开始讲述故事了。', Speaker = 1583, Option = '', Action = { num = 1, actionid = 200,},}, 
	[8575] = { id = 8575, Voice = '', Text = '东西买来了吗？', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8574] = { id = 8574, Voice = '', Text = '想继续听下去，就帮我弄到一个[c][ffff00]暖身料理[-][/c]吧！', Speaker = 1583, Option = '', Action = { num = 1, actionid = 201,},}, 
	[8573] = { id = 8573, Voice = '', Text = '固特伊成为了冒险者公会不可或缺的一部分。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8572] = { id = 8572, Voice = '', Text = '所以固特伊成为了这样一个铁匠，他制作的武器锋利无比的同时，他的破坏力甚至能够与魔法导师杜赫塔魔法的破坏力相提并论。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8571] = { id = 8571, Voice = '', Text = '锻造出最好的武器与盔甲，没有强大的实力怎么能够驾驭这些力量？', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8570] = { id = 8570, Voice = '', Text = '他对哈沃德那种醉心于锻造的铁匠嗤之以鼻。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8569] = { id = 8569, Voice = '', Text = '与哈沃德不同，固特伊坚持认为，这世界还是以实力为尊。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8568] = { id = 8568, Voice = '', Text = '小瞧固特伊，敌人们将会付出惨痛的代价，希盖伊兹曾经这么说过。', Speaker = 1583, Option = '', Action = _EmptyTable,}, 
	[8567] = { id = 8567, Voice = '', Text = '那我们今天就讲讲固特伊的故事。', Speaker = 1583, Option = '', Action = { num = 1, actionid = 200,},}, 
	[8480] = { id = 8480, Voice = '', Text = '冒险者你还没能收集到材料啊？你还是先把材料收集好了再来找我吧！', Speaker = 2646, Option = '', Action = _EmptyTable,}, 
	[8479] = { id = 8479, Voice = '', Text = '真是太棒啦！谢谢你帮助了我，作为奖励，你现在可以去斐扬的微笑小姐那里购买我珍藏的头饰图纸——[c][ffff00]狐狸假面[-][/c]的图纸了！快去找她吧！', Speaker = 2646, Option = '好的', Action = _EmptyTable,}, 
	[8478] = { id = 8478, Voice = '', Text = '你这么快击败[c][ffff00]月夜猫[-][/c]拿到[c][ffff00]铃铛[-][/c]了？愿意给我[c][ffff00]一个铃铛[-][/c]吗？', Speaker = 2646, Option = '{不愿意,13}{好的,14}', Action = _EmptyTable,}, 
	[8477] = { id = 8477, Voice = '', Text = '冒险者，你的冒险手册里没有解锁[c][ffff00]月夜猫[-][/c]呢。如果你真的想帮助我的话先解锁冒险手册里的[c][ffff00]月夜猫[-][/c]吧！', Speaker = 2646, Option = '好吧', Action = _EmptyTable,}, 
	[8476] = { id = 8476, Voice = '', Text = '冒险者，请你帮助我，我需要[c][ffff00]月夜猫[-][/c]身上的一个稀有材料，叫做[c][ffff00]铃铛[-][/c]，我只需要一个就够了，你能帮我弄来吗？', Speaker = 2646, Option = '没问题', Action = _EmptyTable,}, 
	[8475] = { id = 8475, Voice = '', Text = '我看了你的冒险手册，你果然解锁了[c][ffff00]月夜猫[-][/c]呢！你没让我失望！', Speaker = 2646, Option = '', Action = _EmptyTable,}, 
	[8474] = { id = 8474, Voice = '', Text = '你能帮我？来让我看看你的冒险手册，如果你有能力击败[c][ffff00]月夜猫[-][/c]的话，你的冒险手册应该解锁了才对。', Speaker = 2646, Option = '好的', Action = _EmptyTable,}, 
	[8473] = { id = 8473, Voice = '', Text = '你还没能拍到[c][ffff00]月夜猫[-][/c]的照片？哼，等你拍到了再来找我吧。', Speaker = 2646, Option = '好吧', Action = _EmptyTable,}, 
	[8472] = { id = 8472, Voice = '', Text = '没想到你这么快就拍到了，挺不错嘛！', Speaker = 2646, Option = '', Action = _EmptyTable,}, 
	[8471] = { id = 8471, Voice = '', Text = '你回来了？让我看看[c][ffff00]月夜猫[-][/c]的照片，你可别拍错了！', Speaker = 2646, Option = '', Action = _EmptyTable,}, 
	[8470] = { id = 8470, Voice = '', Text = '你竟然没听过？这…好吧，冒险者，我建议你还是去了解一下月夜猫吧！', Speaker = 2646, Option = '', Action = _EmptyTable,}, 
	[8469] = { id = 8469, Voice = '', Text = '听说过就好。你能帮我拍张[c][ffff00]月夜猫[-][/c]的照片吗？', Speaker = 2646, Option = '', Action = _EmptyTable,}, 
