	[5013] = { id = 5013, Voice = '', Text = '现在手册只有一些基本的内容，随着以后的冒险，不断地丰富，这是重要的资料，也是属于你的珍贵的回忆。', Speaker = 1501, Option = '', Action = _EmptyTable,}, 
