	[5011] = { id = 5011, Voice = '', Text = '冒险家等级提升后，除了皇家颁发的奖励之外，公会也开始提供更多的资源和信息，支持你进行更加深入的探险，逐步揭开大陆的秘密，走向冒险家的顶端。', Speaker = 1501, Option = '厉害', Action = _EmptyTable,}, 
	[5010] = { id = 5010, Voice = '', Text = '领取手册之后，冒险者需要定期向公会进行汇报。根据冒险资料的价值，公会会为每个冒险者进行等级评定，这就是所谓的[c][ffff00]冒险家等级[-][/c]。', Speaker = 1501, Option = '', Action = _EmptyTable,}, 
