	[5008] = { id = 5008, Voice = '', Text = '我已收到通知，[c][ffff00][PlayerName][-][/c]前来领取冒险手册。', Speaker = 1501, Option = '是的', Action = _EmptyTable,}, 
	[5007] = { id = 5007, Voice = '', Text = '现在，你可以向冒险者公会的卡卡罗索取属于你的冒险手册了，我会帮你介绍的。', Speaker = 1118, Option = '谢谢', Action = _EmptyTable,}, 
	[5006] = { id = 5006, Voice = '', Text = '探索米德加尔特是冒险家协会最重要的使命，而冒险手册就是我们完成使命的方式。你明白了吗？', Speaker = 1118, Option = '{明白了,0}{再听一遍,2}', Action = _EmptyTable,}, 
	[5005] = { id = 5005, Voice = '', Text = '协会收集这些信息，形成正式的官方文献，作为皇家学者们和魔法研究机构的参考资料。当然这些信息对军队也是非常有价值的。', Speaker = 1118, Option = '下面', Action = _EmptyTable,}, 
	[5004] = { id = 5004, Voice = '', Text = '每个冒险家都有自己的冒险手册，用来记录冒险中遇到的事件和见闻，定期向协会提交冒险报告。', Speaker = 1118, Option = '下面', Action = _EmptyTable,}, 
	[5003] = { id = 5003, Voice = '', Text = '什么！你还没有冒险手册？', Speaker = 1118, Option = '我是新人', Action = _EmptyTable,}, 
