	[3920] = { id = 3920, Voice = '', Text = '看来你已经开通了[c][ffff00]普隆德拉[-][/c]的传送功能了，很棒呢~', Speaker = 1015, Option = '谢谢', Emoji = 20, Action = _EmptyTable,}, 
	[3896] = { id = 3896, Voice = '', Text = '不过即使如此，[c][ffff00]杰勒比结晶[-][/c]一定是生成魔物固有性向的原因之一。现在我打算通过一个实验，证实这个推测。冒险者，你可以帮我收集一些 [c][ffff00]实验用杰勒比结晶[-][/c] 吗？', Speaker = 1313, Option = '{好的,0}{没兴趣,9}', Action = _EmptyTable,}, 
	[3895] = { id = 3895, Voice = '', Text = '我们到目前为止一直想找出能应付[c][ffff00]杰勒比结晶[-][/c]的对策，但是现在看来就算找到了，也无法完全将魔物铲除。', Speaker = 1313, Option = '', Action = _EmptyTable,}, 
	[3894] = { id = 3894, Voice = '', Text = '不是每个冒险家都热心帮助人啊，我还是自己动手吧，再见。', Speaker = 1313, Option = '', Emoji = 25, Action = _EmptyTable,}, 
	[3893] = { id = 3893, Voice = '', Text = '真是令人伤心啊。不过我会继续研究，如果可以找到结晶的来源，不仅人类，连魔物也可以挽救。谢谢你的帮助，下次再见。', Speaker = 1313, Option = '再见', Action = _EmptyTable,}, 
	[3892] = { id = 3892, Voice = '', Text = '它们都是与人类不同种的动物或是与人类共生的动物突变后变成的。虽然现在的人可能不知道，原本魔物其实都是很温顺的动物。', Speaker = 1313, Option = '', Action = _EmptyTable,}, 
	[3891] = { id = 3891, Voice = '', Text = '实际上，魔物们是生存在古代的生命体。他们与人类以及神斗争失败，从此就灭亡了。但是因为大自然生态平衡的力量，魔物们又再度充斥出现。', Speaker = 1313, Option = '', Action = _EmptyTable,}, 
	[3890] = { id = 3890, Voice = '', Text = '刚刚太危险了还好有你在，冒险者。我现在差不多可以得出一些结论了，想听听看吗？', Speaker = 1313, Option = '有兴趣', Action = _EmptyTable,}, 
	[3888] = { id = 3888, Voice = '', Text = '普通的疯兔变成了巨大的[c][ffff00]狂暴蓝疯兔[-][/c]危险！', Speaker = 1313, Option = '', Action = _EmptyTable,}, 
