	[3885] = { id = 3885, Voice = '', Text = '呼！~太危险了，不过结果符合我的推测。如果把[c][ffff00]杰勒比结晶[-][/c]调和一下的话，说不定还可以生成更强大的魔物。', Speaker = 1313, Option = '', Action = _EmptyTable,}, 
