Table_Dialog_Quest9 =
{ 
	[1500051] = { id = 1500051, Voice = '', Text = '很感谢你帮助了我，但是如果你没有卡普拉MVP券，那么我也帮不了你啦。不过随时欢迎你再来找我！', Speaker = 2700, Option = '', Action = _EmptyTable,}, 
	[1500048] = { id = 1500048, Voice = '', Text = '拍到以上BOSS中的[c][ffff00]任意6个[-][/c]可以领取[c][ffff00]开服庆典福袋[-][/c]2个！！', Speaker = 650000, Option = '{我拍到了Boss,0},{我这就去,1}', Action = _EmptyTable,}, 
	[1500047] = { id = 1500047, Voice = '', Text = '拍到以上BOSS中的[c][ffff00]任意4个[-][/c]可以领取[c][ffff00]开服庆典福袋[-][/c]2个！！拍到[c][ffff00]任意6个[-][/c]的话，还可以[c][ffff00]再次领取2个[-][/c]！！拍得越多，奖励越多！', Speaker = 650000, Option = '{我拍到了Boss,0},{我这就去,1}', Action = _EmptyTable,}, 
	[1500046] = { id = 1500046, Voice = '', Text = '拍到以上BOSS中的[c][ffff00]任意2个[-][/c]可以领取[c][ffff00]开服庆典福袋[-][/c]2个！！拍到[c][ffff00]任意4个[-][/c]还可以[c][ffff00]再次领取2个[-][/c]！！！初心者，赶快拿起相机上路吧～～', Speaker = 650000, Option = '{我拍到了Boss,0},{我这就去,1}', Action = _EmptyTable,}, 
	[1500045] = { id = 1500045, Voice = '', Text = '让我来看一下你的特典卡片激活数量吧～～', Speaker = 650000, Option = '', Action = _EmptyTable,}, 
	[1500044] = { id = 1500044, Voice = '', Text = '你可太有一手了。', Speaker = 2700, Option = '', Action = _EmptyTable,}, 
	[1500043] = { id = 1500043, Voice = '', Text = '好吧好吧，看你这嘴甜的，不就是为了我那颗猫眼石嘛～回去告诉金币商人，我可以考虑考虑，但价格不能太低～！', Speaker = 1045, Option = '', Action = _EmptyTable,}, 
	[1500042] = { id = 1500042, Voice = '', Text = '我都说了我不卖那颗猫眼石！！永远不卖！！', Speaker = 1045, Option = '', Action = _EmptyTable,}, 
	[1500041] = { id = 1500041, Voice = '', Text = '哼，说话还蛮好听的嘛。原来是金币商人派你来的。', Speaker = 1045, Option = '{直接说来意,5},{夸赞艾丽米,0}', Action = _EmptyTable,}, 
	[1500040] = { id = 1500040, Voice = '', Text = '去去去，一边儿去！！', Speaker = 1045, Option = '', Action = _EmptyTable,}, 
	[1500039] = { id = 1500039, Voice = '', Text = '嘻嘻，你是哪儿来的，看你这不上档次的衣服就知道你并不富裕。', Speaker = 1045, Option = '{表现出愤怒,4},{表现出谦卑,0}', Action = _EmptyTable,}, 
	[1500038] = { id = 1500038, Voice = '', Text = '我想向她收购她店里的一颗猫眼石，可她小气得很，都不愿意出手。希望你能帮我多说些好话，劝她将那颗猫眼石卖给我。', Speaker = 2700, Option = '{没问题,2}', Action = _EmptyTable,}, 
	[1500037] = { id = 1500037, Voice = '', Text = '那你可得帮帮我，你知道珠宝商人艾丽米吗？她可是我认识最美丽又难搞的女人！', Speaker = 2700, Option = '然后呢', Action = _EmptyTable,}, 
	[1500036] = { id = 1500036, Voice = '', Text = '机智聪慧的冒险者啊，告诉你件好事，如果你拥有足够多的卡普拉MVP券Ⅳ，就可以与我兑换许多好东西。你要与我兑换吗？', Speaker = 2700, Option = '{需要,0},{不需要,1}', Action = _EmptyTable,}, 
	[1500035] = { id = 1500035, Voice = '', Text = '哈哈，这小无赖也该有人给他上一课了。', Speaker = 2700, Option = '', Action = _EmptyTable,}, 
	[1500034] = { id = 1500034, Voice = '', Text = '唔…饶命，我知道了知道了，明天我就把那些金币一枚不落的还给金币商人…', Speaker = 1119, Option = '', Action = _EmptyTable,}, 
	[1500033] = { id = 1500033, Voice = '', Text = '你掏出武器，恶狠狠的盯着艾斯。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[1500032] = { id = 1500032, Voice = '', Text = '什么金币～我可不知道，啦啦啦啦啦！', Speaker = 1119, Option = '', Action = _EmptyTable,}, 
	[1500031] = { id = 1500031, Voice = '', Text = '什么！？你是金币商人派来的？', Speaker = 1119, Option = '', Action = _EmptyTable,}, 
	[1500030] = { id = 1500030, Voice = '', Text = '希望你帮我传个信儿！让他尽快还清自己的债务。我不建议带些威逼恐吓～', Speaker = 2700, Option = '没问题', Emoji = 5, Action = _EmptyTable,}, 
	[1500029] = { id = 1500029, Voice = '', Text = '那你可得替我做件事！是这样的，艾斯那个小混球，去年问我借了一袋金币到现在都没有还给我。', Speaker = 2700, Option = '然后呢', Action = _EmptyTable,}, 
	[1500028] = { id = 1500028, Voice = '', Text = '能说会道的冒险者啊，告诉你件好事，如果你拥有足够多的卡普拉MVP券Ⅲ，就可以与我兑换许多好东西。你要与我兑换吗？', Speaker = 2700, Option = '{需要,0},{不需要,1}', Action = _EmptyTable,}, 
	[1500027] = { id = 1500027, Voice = '', Text = '原来如此，溜溜猴也不过就像是普通的小猴子一样嘛！', Speaker = 2700, Option = '', Action = _EmptyTable,}, 
	[1500026] = { id = 1500026, Voice = '', Text = '快回去告诉这位三不知先生吧～', Speaker = 2506, Option = '', Action = _EmptyTable,}, 
	[1500025] = { id = 1500025, Voice = '', Text = '这个问题太简单了。溜溜猴喜欢的不就是香蕉嘛！', Speaker = 2506, Option = '', Action = _EmptyTable,}, 
	[1500024] = { id = 1500024, Voice = '', Text = '希望你帮我问一问魔物学者艾德里安·洛克，关于魔物，他可以算得上是精通了啊。', Speaker = 2700, Option = '没问题', Action = _EmptyTable,}, 
	[1500023] = { id = 1500023, Voice = '', Text = '可实际上天知道那些蹦蹦跳跳的猴子喜欢什么呀！', Speaker = 2700, Option = '', Action = _EmptyTable,}, 
	[1500022] = { id = 1500022, Voice = '', Text = '那你可得祝我一臂之力，说起来很惭愧，前些日子，我和朋友们吹牛说自己知道溜溜猴最喜欢的食物是什么。', Speaker = 2700, Option = '然后呢', Action = _EmptyTable,}, 
	[1500021] = { id = 1500021, Voice = '', Text = '热心的冒险者啊，告诉你件好事，如果你拥有足够多的卡普拉MVP券Ⅱ，就可以与我兑换许多好东西。你要与我兑换吗？', Speaker = 2700, Option = '{需要,0},{不需要,1}', Action = _EmptyTable,}, 
	[1500020] = { id = 1500020, Voice = '', Text = '效率真是高得惊人，都说时间就是金钱，你很富有，冒险者。', Speaker = 2700, Option = '谢谢', Action = _EmptyTable,}, 
	[1500019] = { id = 1500019, Voice = '', Text = '太好了，晚上可以吃奶酪饼了呢！', Speaker = 1217, Option = '', Action = _EmptyTable,}, 
	[1500018] = { id = 1500018, Voice = '', Text = '这是要给我父亲的吗？', Speaker = 1217, Option = '是的', Action = _EmptyTable,}, 
	[1500017] = { id = 1500017, Voice = '', Text = '她父亲做的菠萝派非常好吃，作为回礼，我准备了一些干酪，请你将这些转交给她。', Speaker = 2700, Option = '没问题', Action = _EmptyTable,}, 
	[1500016] = { id = 1500016, Voice = '', Text = '那你可得帮我个小忙，你知道赛斯贝雪吗？就是水果商人旁那个乖巧的小姑娘。', Speaker = 2700, Option = '知道', Action = _EmptyTable,}, 
	[1500015] = { id = 1500015, Voice = '', Text = '很可惜啊冒险者，如果你没有卡普拉MVP券，那么我也帮不了你啦。不过随时欢迎你再来找我！', Speaker = 2700, Option = '', Action = _EmptyTable,}, 
	[1500014] = { id = 1500014, Voice = '', Text = '行色匆匆的冒险者啊!告诉你件好事,如果你拥有足够多的卡普拉MVP券Ⅰ,就可以与我兑换许多好东西。你要与我兑换吗?', Speaker = 2700, Option = '{需要,0},{不需要,1}', Action = _EmptyTable,}, 
	[1500013] = { id = 1500013, Voice = '', Text = '虽然我知道拍到6个Boss是件很难的事，但是做不到的话，我是不会给奖励的，哼！', Speaker = 650000, Option = '我这就去', Action = _EmptyTable,}, 
	[1500012] = { id = 1500012, Voice = '', Text = '什～～～么！！你竟然竟然能拍到这么多！真是太～厉～害了！！！请务必收下这份奖励！', Speaker = 650000, Option = '', Action = _EmptyTable,}, 
	[1500011] = { id = 1500011, Voice = '', Text = '活动期间，找到[c][ffff00]狸猫[-][/c]、[c][ffff00]蓝疯兔[-][/c]、[c][ffff00]摇滚蝗虫[-][/c]、[c][ffff00]蛙王[-][/c]、[c][ffff00]幽灵波利[-][/c]、[c][ffff00]波利之王[-][/c]、[c][ffff00]直升机哥布灵[-][/c]、[c][ffff00]流浪之狼[-][/c]、[c][ffff00]天使波利[-][/c]、[c][ffff00]黄金虫[-][/c]、[c][ffff00]恶魔波利[-][/c]进行拍照后，解琐冒险手册相应魔物图鉴后即可到我这里领取对应奖励哦。', Speaker = 650000, Option = '什么奖励？', Action = _EmptyTable,}, 
	[1500010] = { id = 1500010, Voice = '', Text = '什么嘛，[c][ffff00]没拍够4个[-][/c]BOSS，我可是不会发奖励给你的哦！', Speaker = 650000, Option = '我这就去', Action = _EmptyTable,}, 
	[1500009] = { id = 1500009, Voice = '', Text = '竟然又拍到了两只！真是太厉害了！请收下相应奖励吧。如果你能[c][ffff00]再拍摄两只[-][/c]的话，我会[c][ffff00]再给你2个开服庆典福袋[-][/c]！！', Speaker = 650000, Option = '', Action = _EmptyTable,}, 
	[1500008] = { id = 1500008, Voice = '', Text = '活动期间，找到[c][ffff00]狸猫[-][/c]、[c][ffff00]蓝疯兔[-][/c]、[c][ffff00]摇滚蝗虫[-][/c]、[c][ffff00]蛙王[-][/c]、[c][ffff00]幽灵波利[-][/c]、[c][ffff00]波利之王[-][/c]、[c][ffff00]直升机哥布灵[-][/c]、[c][ffff00]流浪之狼[-][/c]、[c][ffff00]天使波利[-][/c]、[c][ffff00]黄金虫[-][/c]、[c][ffff00]恶魔波利[-][/c]进行拍照后，解琐冒险手册相应魔物图鉴后即可到我这里领取对应奖励哦。', Speaker = 650000, Option = '什么奖励？', Action = _EmptyTable,}, 
	[1500007] = { id = 1500007, Voice = '', Text = '什么嘛，[c][ffff00]没拍够2个[-][/c]BOSS，我可是不会发奖励给你的哦！', Speaker = 650000, Option = '我这就去', Action = _EmptyTable,}, 
	[1500006] = { id = 1500006, Voice = '', Text = '好厉害，这么快就拍到了BOSS啊!请收下相应奖励吧。如果能[c][ffff00]拍到4只[-][/c]的话，还能[c][ffff00]再领取2个开服庆典福袋[-][/c]哦～', Speaker = 650000, Option = '', Action = _EmptyTable,}, 
	[1500005] = { id = 1500005, Voice = '', Text = '活动期间，找到[c][ffff00]狸猫[-][/c]、[c][ffff00]蓝疯兔[-][/c]、[c][ffff00]摇滚蝗虫[-][/c]、[c][ffff00]蛙王[-][/c]、[c][ffff00]幽灵波利[-][/c]、[c][ffff00]波利之王[-][/c]、[c][ffff00]直升机哥布灵[-][/c]、[c][ffff00]流浪之狼[-][/c]、[c][ffff00]天使波利[-][/c]、[c][ffff00]黄金虫[-][/c]、[c][ffff00]恶魔波利[-][/c]进行拍照后，解琐冒险手册相应魔物图鉴后即可到我这里领取对应奖励哦。', Speaker = 650000, Option = '什么奖励？', Action = _EmptyTable,}, 
	[1500004] = { id = 1500004, Voice = '', Text = '唔呼呼~你身上的特典卡片被激活的数量不够呢。这样可没法给你卡普拉小姐姐们的特别馈赠哦…', Speaker = 650000, Option = '', Action = _EmptyTable,}, 
	[1500003] = { id = 1500003, Voice = '', Text = '嘻嘻，这可是卡普拉小姐姐们爱的馈赠哦~拿好了哦~', Speaker = 650000, Option = '', Action = _EmptyTable,}, 
	[1500001] = { id = 1500001, Voice = '', Text = '这个[c][ffff00]开服庆典福袋[-][/c]给你，请不要告诉别人我在这里哦～', Speaker = 650001, Option = '', Action = _EmptyTable,}, 
	[1000796] = { id = 1000796, Voice = '', Text = '我可要好好感谢你，这是我们敲萌家族的谢礼，请收好~', Speaker = 6153, Option = '', Action = _EmptyTable,}, 
	[1000795] = { id = 1000795, Voice = '', Text = '我们敲萌家族的那些调皮的成员都被你一一找到了呢。', Speaker = 6153, Option = '', Action = _EmptyTable,}, 
	[1000794] = { id = 1000794, Voice = '', Text = '喏，这是你帮我寻找我们敲萌家族成员的奖励，请收好~', Speaker = 6153, Option = '', Action = _EmptyTable,}, 
	[1000793] = { id = 1000793, Voice = '', Text = '哈哈，我就猜到，他们一定不会逃出冒险者的搜索的！', Speaker = 6153, Option = '', Action = _EmptyTable,}, 
	[1000792] = { id = 1000792, Voice = '', Text = '敲萌家族·玛雅去了苏克拉特沙漠,你能拍照告诉我他现在的情况吗？我会给你准备丰厚的礼物。', Speaker = 6153, Option = '', Action = _EmptyTable,}, 
	[1000791] = { id = 1000791, Voice = '', Text = '敲萌家族·艾西去了西门,你能拍照告诉我他现在的情况吗？我会给你准备丰厚的礼物。', Speaker = 6153, Option = '', Action = _EmptyTable,}, 
	[1000790] = { id = 1000790, Voice = '', Text = '敲萌家族·凯瑞去了兽人村,你能拍照告诉我他现在的情况吗？我会给你准备丰厚的礼物。', Speaker = 6153, Option = '', Action = _EmptyTable,}, 
	[1000789] = { id = 1000789, Voice = '', Text = '敲萌家族·基诺去了斐扬洞穴二层,你能拍照告诉我他现在的情况吗？我会给你准备丰厚的礼物。', Speaker = 6153, Option = '', Action = _EmptyTable,}, 
	[1000788] = { id = 1000788, Voice = '', Text = '敲萌家族·卡隆去了迷藏森林,你能拍照告诉我他现在的情况吗？我会给你准备丰厚的礼物。', Speaker = 6153, Option = '', Action = _EmptyTable,}, 
	[1000787] = { id = 1000787, Voice = '', Text = '敲萌家族·贝蒂去了吉芬地下,你能拍照告诉我他现在的情况吗？我会给你准备丰厚的礼物。', Speaker = 6153, Option = '', Action = _EmptyTable,}, 
	[1000786] = { id = 1000786, Voice = '', Text = '敲萌家族·匹格去了斐扬,你能拍照告诉我他现在的情况吗？我会给你准备丰厚的礼物。', Speaker = 6153, Option = '', Action = _EmptyTable,}, 
	[1000785] = { id = 1000785, Voice = '', Text = '但是有些调皮的敲萌家族成员竟然在这几天不见了，他们都说要在米德加尔特大陆好好玩一玩。', Speaker = 6153, Option = '', Action = _EmptyTable,}, 
	[1000784] = { id = 1000784, Voice = '', Text = '为了参加这次庆典，我们敲萌家族的成员很早就打扮好，准备在庆典中好好玩一玩呢。', Speaker = 6153, Option = '', Action = _EmptyTable,}, 
	[1000783] = { id = 1000783, Voice = '', Text = '只是我没想到会这么快…呜呜呜…我会想念它的。', Speaker = 2160, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[1000782] = { id = 1000782, Voice = '', Text = '咿维离开了吗…在它兴高采烈地跟我说它找到了冒险的意义之后，我就知道会有这么一天。', Speaker = 2160, Option = '', Action = _EmptyTable,}, 
	[1000781] = { id = 1000781, Voice = '', Text = '记得帮我跟美羽打声招呼，告诉她，我走了。哼哧…那有机会再见咯…', Speaker = 600091, Option = '', Action = _EmptyTable,}, 
	[1000780] = { id = 1000780, Voice = '', Text = '到了那个时候，我会回来找你的！', Speaker = 600091, Option = '我等你', Action = _EmptyTable,}, 
	[1000779] = { id = 1000779, Voice = '', Text = '冒险者，你真是一个非常好的冒险者…我一定会成为你这样的冒险家。', Speaker = 600091, Option = '', Action = _EmptyTable,}, 
	[1000778] = { id = 1000778, Voice = '', Text = '我想，我会一辈子记住你的帮助的。', Speaker = 600091, Option = '好吧。', Action = _EmptyTable,}, 
	[1000777] = { id = 1000777, Voice = '', Text = '走之前，我想跟你合一次影，就在普隆德拉的喷泉。', Speaker = 600091, Option = '', Action = _EmptyTable,}, 
	[1000776] = { id = 1000776, Voice = '', Text = '哼哧…不用担心啦，我有机会会回来看你的。', Speaker = 600091, Option = '好吧', Action = _EmptyTable,}, 
	[1000775] = { id = 1000775, Voice = '', Text = '我也想去找到可靠的同伴呢！我想我的冒险之旅一定非常精彩。', Speaker = 600091, Option = '', Action = _EmptyTable,}, 
	[1000774] = { id = 1000774, Voice = '', Text = '去冒险！托你的福，我找到了冒险的意义。', Speaker = 600091, Option = '', Action = _EmptyTable,}, 
	[1000773] = { id = 1000773, Voice = '', Text = '感谢有你的陪伴，冒险者，但我得离开这里了。', Speaker = 600091, Option = '去哪儿', Action = _EmptyTable,}, 
	[1000772] = { id = 1000772, Voice = '', Text = '出来这么久，美羽一定很担心，我先回普隆德拉了，你也快来哦~', Speaker = 600091, Option = '好的', Action = _EmptyTable,}, 
	[1000771] = { id = 1000771, Voice = '', Text = '嘻嘻…没想到真的有效果…', Speaker = 600091, Option = '', Action = _EmptyTable,}, 
	[1000770] = { id = 1000770, Voice = '', Text = '它交给我一个特殊的魔法药剂，可以驱散这些讨厌的蜂兵。', Speaker = 600091, Option = '', Action = _EmptyTable,}, 
	[1000769] = { id = 1000769, Voice = '', Text = '是不是很惊讶？哼唧…我在妙勒尼山脉碰到一位魔法师。', Speaker = 600091, Option = '', Action = _EmptyTable,}, 
	[1000768] = { id = 1000768, Voice = '', Text = '这讨厌的气味…可恶！！该死的摩根！', Speaker = 600089, Option = '', Action = _EmptyTable,}, 
	[1000767] = { id = 1000767, Voice = '', Text = '交给我来解决吧！', Speaker = 600091, Option = '', Action = _EmptyTable,}, 
	[1000766] = { id = 1000766, Voice = '', Text = '哼哧…别着急！我来了！', Speaker = 600091, Option = '', Action = _EmptyTable,}, 
	[1000765] = { id = 1000765, Voice = '', Text = '不好，被魔物包围了！得赶紧找到咿维！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[1000764] = { id = 1000764, Voice = '', Text = '我看你得赶紧找到它，毕竟妙勒尼的那些魔物可不是吃素的！', Speaker = 1322, Option = '明白', Action = _EmptyTable,}, 
	[1000763] = { id = 1000763, Voice = '', Text = '呵…那头摇摇晃晃的猪吗？我在妙勒尼山脉中看到它了…不过它似乎迷路了…', Speaker = 1322, Option = '', Action = _EmptyTable,}, 
	[1000762] = { id = 1000762, Voice = '', Text = '哈哈哈，那头小野猪么，它在吉芬中可是很显眼呢，但是它好像去哥布灵森林找人了呢。', Speaker = 1340, Option = '', Action = _EmptyTable,}, 
	[1000761] = { id = 1000761, Voice = '', Text = '啊…我采集样本的时候倒是见到过…如果我没看错它去的方向应该是吉芬。', Speaker = 1067, Option = '多谢', Action = _EmptyTable,}, 
	[1000760] = { id = 1000760, Voice = '', Text = '我听它嘴里念叨着西门风车什么的…', Speaker = 1054, Option = '谢谢你', Action = _EmptyTable,}, 
	[1000759] = { id = 1000759, Voice = '', Text = '那头可爱的小野猪么？今天还一蹦一跳得从我面前经过呢…', Speaker = 1054, Option = '', Action = _EmptyTable,}, 
	[1000758] = { id = 1000758, Voice = '', Text = '那我在普隆德拉等你的消息！', Speaker = 600088, Option = '', Action = _EmptyTable,}, 
	[1000757] = { id = 1000757, Voice = '', Text = '我也不知道它去哪里了…', Speaker = 600088, Option = '去找它', Action = _EmptyTable,}, 
	[1000756] = { id = 1000756, Voice = '', Text = '看得出来，今天的它非常开心，告诉我一声它就直接走开了…', Speaker = 600088, Option = '', Action = _EmptyTable,}, 
	[1000755] = { id = 1000755, Voice = '', Text = '它说它迫不及待地想要见到你了，就去找你了。', Speaker = 600088, Option = '去哪儿了', Action = _EmptyTable,}, 
	[1000754] = { id = 1000754, Voice = '', Text = '你终于来了！咿维已经去找你去了。', Speaker = 600088, Option = '找我？', Action = _EmptyTable,}, 
	[1000752] = { id = 1000752, Voice = '', Text = '我要好好整休一下，明天见~哼哧…', Speaker = 600087, Option = '', Action = _EmptyTable,}, 
	[1000751] = { id = 1000751, Voice = '', Text = '我决定了！哼哧…我一定要成为像你这样的，充满正义的冒险家！', Speaker = 600087, Option = '', Action = _EmptyTable,}, 
	[1000750] = { id = 1000750, Voice = '', Text = '谢谢夸奖', Speaker = 600087, Option = '', Action = _EmptyTable,}, 
	[1000749] = { id = 1000749, Voice = '', Text = '谢谢你的开导…你一定是位非常出色的冒险者！', Speaker = 600087, Option = '', Action = _EmptyTable,}, 
	[1000748] = { id = 1000748, Voice = '', Text = '所以，不用害怕冒险中的艰难险阻，你要知道，你的伙伴们在前方等待着你！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[1000747] = { id = 1000747, Voice = '', Text = '他们的存在才是冒险中最最珍贵的财富。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[1000746] = { id = 1000746, Voice = '', Text = '在你难过的时候，他们会来安慰你；在你开心的时候，他们会和你一起大笑，分享你的喜悦。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[1000745] = { id = 1000745, Voice = '', Text = '在你被魔物围攻的时候，他们会来帮助你；在你深陷古代遗迹的时候，他们会来解救你；在你被人欺负的时候，他们会来保护你。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[1000744] = { id = 1000744, Voice = '', Text = '当然，最重要的一点。那就是我们可以在冒险的旅程中结识许许多多的小伙伴。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[1000743] = { id = 1000743, Voice = '', Text = '去深入那些广袤的无人区，寻找珍贵的宝藏，得到数之不尽的财富。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[1000742] = { id = 1000742, Voice = '', Text = '当然，冒险也不仅仅是为了保护他人。我们可以去探索世界的秘密。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[1000741] = { id = 1000741, Voice = '', Text = '我们肩负着这样的责任，保护弱者，崇尚正义，如果失去了冒险的初心，那么你的冒险将变得毫无意义。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[1000740] = { id = 1000740, Voice = '', Text = '冒险，不是为了寻求刺激，不是为了显示个人超凡的能力，而是出于对世界的奉献，出于造福世界的责任感。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[1000738] = { id = 1000738, Voice = '', Text = '哼哧…我曾经把冒险视为娱乐，现在看来，冒险根本不是我想想中的那般美好…', Speaker = 600087, Option = '', Action = _EmptyTable,}, 
	[1000737] = { id = 1000737, Voice = '', Text = '这就是战斗么…太残酷了…不是你死就是我亡…', Speaker = 600087, Option = '', Action = _EmptyTable,}, 
	[1000736] = { id = 1000736, Voice = '', Text = '这就是战斗…哼哧…我有些累了…', Speaker = 600087, Option = '', Action = _EmptyTable,}, 
	[1000735] = { id = 1000735, Voice = '', Text = '你能带我见识一下冒险者的战斗吗？', Speaker = 600087, Option = '好吧', Action = _EmptyTable,}, 
	[1000734] = { id = 1000734, Voice = '', Text = '我将来去冒险，这些也是要经历的过程呢。', Speaker = 600087, Option = '', Action = _EmptyTable,}, 
	[1000733] = { id = 1000733, Voice = '', Text = '学会在魔物的袭击下生存下来才是一个冒险者该做的事呢。', Speaker = 600087, Option = '', Action = _EmptyTable,}, 
	[1000732] = { id = 1000732, Voice = '', Text = '接下来啊…哼哧…《冒险指南》里说，大陆上有着各种各样的魔物。', Speaker = 600087, Option = '', Action = _EmptyTable,}, 
	[1000731] = { id = 1000731, Voice = '', Text = '哼哧…不好玩不好玩…总是输给你…', Speaker = 600087, Option = '运气好', Action = _EmptyTable,}, 
	[1000730] = { id = 1000730, Voice = '', Text = '哼哧…不行啊，怎么说你也得赢我一把才行…', Speaker = 600087, Option = '', Action = _EmptyTable,}, 
	[1000729] = { id = 1000729, Voice = '', Text = '哼哧…现在开始吧！三！二！一！出拳！', Speaker = 600087, Option = '{拳头,4},{剪刀,3},{布,3}', Action = _EmptyTable,}, 
	[1000728] = { id = 1000728, Voice = '', Text = '就陪我玩玩嘛。就一小会~~哼哧…', Speaker = 600087, Option = '好吧', Action = _EmptyTable,}, 
	[1000727] = { id = 1000727, Voice = '', Text = '哼哼，那就是猜拳！你来陪我玩猜拳嘛！', Speaker = 600087, Option = '这…', Action = _EmptyTable,}, 
	[1000726] = { id = 1000726, Voice = '', Text = '哼哧…你来啦！你一定想不到，刚刚美羽教会了我一个很有趣的东西。', Speaker = 600087, Option = '什么', Action = _EmptyTable,}, 
	[1000725] = { id = 1000725, Voice = '', Text = '那就明天不见不散啦~哼哧…再见。', Speaker = 600086, Option = '', Action = _EmptyTable,}, 
	[1000724] = { id = 1000724, Voice = '', Text = '哼哧…玩得真开心啊~明天我还要玩。', Speaker = 600086, Option = '好啊好啊', Action = _EmptyTable,}, 
	[1000723] = { id = 1000723, Voice = '', Text = '不玩了不玩了…哼哧…咿维累了呢…我先回南门了…', Speaker = 600086, Option = '', Action = _EmptyTable,}, 
	[1000722] = { id = 1000722, Voice = '', Text = '我想多拍一些照片。', Speaker = 600086, Option = '当然愿意', Action = _EmptyTable,}, 
	[1000721] = { id = 1000721, Voice = '', Text = '冒险者，今天你可以带我去西门玩吗？', Speaker = 600086, Option = '', Action = _EmptyTable,}, 
	[1000720] = { id = 1000720, Voice = '', Text = '嗝~~咿维吃饱了…该消化消化食物了…', Speaker = 600086, Option = '', Action = _EmptyTable,}, 
	[1000719] = { id = 1000719, Voice = '', Text = '哼哧…这点苹果汁太少了…', Speaker = 600086, Option = '', Action = _EmptyTable,}, 
	[1000718] = { id = 1000718, Voice = '', Text = '哇…这就是苹果汁啊…哼哧…', Speaker = 600086, Option = '', Action = _EmptyTable,}, 
	[1000717] = { id = 1000717, Voice = '', Text = '你带来了我想要的苹果汁了吗？', Speaker = 600086, Option = '{没有,3},{带来了,4}', Action = _EmptyTable,}, 
	[1000716] = { id = 1000716, Voice = '', Text = '哼哧…我、我这是享受生活！', Speaker = 600086, Option = '我去弄吧', Action = _EmptyTable,}, 
	[1000715] = { id = 1000715, Voice = '', Text = '哼哧…是的…我很想尝尝苹果汁的滋味…', Speaker = 600086, Option = '真贪吃', Action = _EmptyTable,}, 
	[1000714] = { id = 1000714, Voice = '', Text = '对啊！也许咿维也想尝尝苹果汁呢！可惜我的苹果汁都喝光了…', Speaker = 1050, Option = '原来是这样', Action = _EmptyTable,}, 
	[1000713] = { id = 1000713, Voice = '', Text = '我当时口渴，去喝了两杯苹果汁，回来之后它就闷闷不乐了。', Speaker = 1050, Option = '苹果汁？', Action = _EmptyTable,}, 
	[1000712] = { id = 1000712, Voice = '', Text = '咿维？是啊，一开始它来找我的时候还开开心心的呢。', Speaker = 1050, Option = '', Action = _EmptyTable,}, 
	[1000711] = { id = 1000711, Voice = '', Text = '塞尼亚她…唉…还是不要麻烦你了…哼哧…每次都麻烦你…', Speaker = 600086, Option = '塞尼亚？', Action = _EmptyTable,}, 
	[1000710] = { id = 1000710, Voice = '', Text = '郁闷…哼哧…咦…是你啊…今天也是令人烦恼的一天呢。', Speaker = 600086, Option = '你怎么了？', Action = _EmptyTable,}, 
	[1000709] = { id = 1000709, Voice = '', Text = '我要炫耀给赛利亚看看去~我们明天见哦~', Speaker = 600086, Option = '', Action = _EmptyTable,}, 
	[1000708] = { id = 1000708, Voice = '', Text = '我终于是一个天使猪啦~你真是太好了！哼哧…', Speaker = 600086, Option = '', Action = _EmptyTable,}, 
	[1000707] = { id = 1000707, Voice = '', Text = '真的是…天使发圈…哼哧…这种感觉真是太棒了！', Speaker = 600086, Option = '', Action = _EmptyTable,}, 
	[1000706] = { id = 1000706, Voice = '', Text = '喏，拿去吧，这是小野猪定制版天使发圈。', Speaker = 1054, Option = '谢谢', Action = _EmptyTable,}, 
	[1000705] = { id = 1000705, Voice = '', Text = '数量不多不少，刚刚好~', Speaker = 1054, Option = '', Action = _EmptyTable,}, 
	[1000704] = { id = 1000704, Voice = '', Text = '这点材料也想打发我？', Speaker = 1054, Option = '', Action = _EmptyTable,}, 
	[1000703] = { id = 1000703, Voice = '', Text = '你把我需要的材料带过来了吗？', Speaker = 1054, Option = '{没有,7},{带来了,8}', Action = _EmptyTable,}, 
	[1000702] = { id = 1000702, Voice = '', Text = '你帮我收集5个柔毛来，我就帮你制作~', Speaker = 1054, Option = '好的', Action = _EmptyTable,}, 
	[1000701] = { id = 1000701, Voice = '', Text = '但是这世上可没有免费的午餐，你明白的~', Speaker = 1054, Option = '', Action = _EmptyTable,}, 
	[1000700] = { id = 1000700, Voice = '', Text = '小野猪吗？恩，确实需要订做，不过这难不倒我。', Speaker = 1054, Option = '', Action = _EmptyTable,}, 
	[1000699] = { id = 1000699, Voice = '', Text = '天使发圈啊~或许我可以帮你解决。', Speaker = 1054, Option = '', Action = _EmptyTable,}, 
	[1000698] = { id = 1000698, Voice = '', Text = '好办？你可能不理解…传说中的天使波利可是想见一面都难啊…', Speaker = 600085, Option = '交给我吧。', Action = _EmptyTable,}, 
	[1000697] = { id = 1000697, Voice = '', Text = '是啊，我想要那个天使波利身上的发圈，但是我完全不明白，应该怎么得到它…', Speaker = 600085, Option = '这好办', Action = _EmptyTable,}, 
	[1000696] = { id = 1000696, Voice = '', Text = '我也要做一只酷猪，一只带着天使翅膀的天使猪！', Speaker = 600085, Option = '', Action = _EmptyTable,}, 
	[1000695] = { id = 1000695, Voice = '', Text = '其实今天在等食物的时候，我发现了一个超酷的波利，他居然有天使的翅膀！', Speaker = 600085, Option = '', Action = _EmptyTable,}, 
	[1000694] = { id = 1000694, Voice = '', Text = '波利，白色，头饰，看来你已经明白我想要什么了。', Speaker = 600085, Option = '', Action = _EmptyTable,}, 
	[1000693] = { id = 1000693, Voice = '', Text = '唉，看来你还没有懂我的心思。', Speaker = 600085, Option = '', Action = _EmptyTable,}, 
	[1000692] = { id = 1000692, Voice = '', Text = '第三个问题，什么颜色象征这完美无缺的纯洁？', Speaker = 600085, Option = '{白色,5},{粉色,6},{黄色,6},{红色,6}', Action = _EmptyTable,}, 
	[1000691] = { id = 1000691, Voice = '', Text = '第二个问题，什么才是这片大陆上时尚的象征？', Speaker = 600085, Option = '{闪亮的武器,6},{华美的衣服,6},{酷炫的首饰,6},{精美的头饰,4}', Action = _EmptyTable,}, 
	[1000690] = { id = 1000690, Voice = '', Text = '第一个问题，谁是这片大陆上可爱的象征？虽然我不喜欢它…', Speaker = 600085, Option = '{溜溜猴,6},{绿棉虫,6},{波利,3},{疯兔,6}', Action = _EmptyTable,}, 
	[1000689] = { id = 1000689, Voice = '', Text = '唔…看在你这么想帮我的份上，就给你个机会猜一猜。', Speaker = 600085, Option = '好的', Action = _EmptyTable,}, 
	[1000688] = { id = 1000688, Voice = '', Text = '你怎么又回来了？让我自己呆着不好吗？哼哧…', Speaker = 600085, Option = '到底怎么了', Action = _EmptyTable,}, 
	[1000687] = { id = 1000687, Voice = '', Text = '它兴奋了没多久就消沉下去了呢。', Speaker = 1054, Option = '好吧', Action = _EmptyTable,}, 
	[1000686] = { id = 1000686, Voice = '', Text = '它回来的时候看到了天使波利呢。我告诉它这是难得一见的魔物呢。', Speaker = 1054, Option = '', Action = _EmptyTable,}, 
	[1000685] = { id = 1000685, Voice = '', Text = '咿维啊…这么一说我倒是想起来了…', Speaker = 1054, Option = '', Action = _EmptyTable,}, 
	[1000684] = { id = 1000684, Voice = '', Text = '你去找宝雅姐姐玩吧。', Speaker = 600085, Option = '', Action = _EmptyTable,}, 
	[1000683] = { id = 1000683, Voice = '', Text = '跟你说你也不明白啦…唉…让我自己呆着吧…', Speaker = 600085, Option = '', Action = _EmptyTable,}, 
	[1000682] = { id = 1000682, Voice = '', Text = '什么东西？', Speaker = 600085, Option = '', Action = _EmptyTable,}, 
	[1000681] = { id = 1000681, Voice = '', Text = '哼哧…得不到的东西才是最好的…', Speaker = 600085, Option = '', Action = _EmptyTable,}, 
	[1000680] = { id = 1000680, Voice = '', Text = '唉…', Speaker = 600085, Option = '怎么了？', Action = _EmptyTable,}, 
	[1000679] = { id = 1000679, Voice = '', Text = '哼哧…你来的好慢哦…', Speaker = 600085, Option = '不好意思', Action = _EmptyTable,}, 
	[1000678] = { id = 1000678, Voice = '', Text = '哼哧…拍得真好看…我先去南门洗照片去哦~', Speaker = 600085, Option = '', Action = _EmptyTable,}, 
	[1000677] = { id = 1000677, Voice = '', Text = '我想去看看这个雕像，顺便合张影…哼哧…来过了总得留点纪念呀~哼哧…', Speaker = 600085, Option = '好的！', Action = _EmptyTable,}, 
	[1000676] = { id = 1000676, Voice = '', Text = '我从《冒险指南》中看到伊斯鲁德有个有名的雕像呢！据说是一个非常强大的人类。', Speaker = 600085, Option = '', Action = _EmptyTable,}, 
	[1000675] = { id = 1000675, Voice = '', Text = '哼哧…你来啦你来啦！今天带我去哪里玩呢？', Speaker = 600085, Option = '听你的', Action = _EmptyTable,}, 
	[1000674] = { id = 1000674, Voice = '', Text = '冒险者，我们明天再见哦~哼哧…', Speaker = 600083, Option = '', Action = _EmptyTable,}, 
	[1000673] = { id = 1000673, Voice = '', Text = '啊哈，我要找地方好好读一读这本书。', Speaker = 600083, Option = '', Action = _EmptyTable,}, 
	[1000672] = { id = 1000672, Voice = '', Text = '真是奇怪…拿去吧！', Speaker = 1024, Option = '', Action = _EmptyTable,}, 
	[1000671] = { id = 1000671, Voice = '', Text = '《冒险指南》？你现在还需要这种初级的书籍吗？', Speaker = 1024, Option = '是的', Action = _EmptyTable,}, 
	[1000670] = { id = 1000670, Voice = '', Text = '名叫《冒险指南》！冒险者你能把这本书带给我吗？', Speaker = 600083, Option = '好的', Action = _EmptyTable,}, 
	[1000669] = { id = 1000669, Voice = '', Text = '往后的几天我也会为冒险做准备~我听说冒险者公会的导师有一本书。', Speaker = 600083, Option = '', Action = _EmptyTable,}, 
	[1000668] = { id = 1000668, Voice = '', Text = '我也要去冒险，成为一头强壮的野猪！', Speaker = 600083, Option = '', Action = _EmptyTable,}, 
	[1000667] = { id = 1000667, Voice = '', Text = '你真是太棒了！我也要像你一样，这样他们就再也不敢欺负我了！', Speaker = 600083, Option = '', Action = _EmptyTable,}, 
	[1000666] = { id = 1000666, Voice = '', Text = '我要欺负回来…哼哧…你会帮我的，对吗？', Speaker = 600083, Option = '好吧', Action = _EmptyTable,}, 
	[1000665] = { id = 1000665, Voice = '', Text = '我刚到南门的时候，这些波利可没少欺负我呢。', Speaker = 600083, Option = '', Action = _EmptyTable,}, 
	[1000664] = { id = 1000664, Voice = '', Text = '哼哧…接下来该找那些波利的麻烦了。', Speaker = 600083, Option = '为什么', Action = _EmptyTable,}, 
	[1000663] = { id = 1000663, Voice = '', Text = '啊~~走得太累了…我先回南门了哦，来南门找我吧。', Speaker = 600083, Option = '', Action = _EmptyTable,}, 
	[1000662] = { id = 1000662, Voice = '', Text = '这是你新养的宠物吗？真是可爱呢…', Speaker = 1108, Option = '', Action = _EmptyTable,}, 
	[1000661] = { id = 1000661, Voice = '', Text = '哦…冒险者，好久不见，你这是？', Speaker = 1156, Option = '遛猪！', Action = _EmptyTable,}, 
	[1000660] = { id = 1000660, Voice = '', Text = '咦…遛狗的倒是不少…这遛猪的倒是很少见啊…', Speaker = 1121, Option = '', Action = _EmptyTable,}, 
	[1000659] = { id = 1000659, Voice = '', Text = '真的吗？哼哧…你真是个好人！', Speaker = 600083, Option = '', Action = _EmptyTable,}, 
	[1000658] = { id = 1000658, Voice = '', Text = '你能带我去见见王国的风景吗？', Speaker = 600083, Option = '我带你走走', Action = _EmptyTable,}, 
	[1000657] = { id = 1000657, Voice = '', Text = '从家里出来的时候，我听说米德加尔特王国的风景闻名整个大陆。', Speaker = 600083, Option = '', Action = _EmptyTable,}, 
	[1000656] = { id = 1000656, Voice = '', Text = '呼…吃撑了…我要消化消化肚子里的食物…', Speaker = 600083, Option = '', Action = _EmptyTable,}, 
	[1000655] = { id = 1000655, Voice = '', Text = '哼哧…味道真不错…', Speaker = 600083, Option = '…', Action = _EmptyTable,}, 
	[1000654] = { id = 1000654, Voice = '', Text = '哼哧…我老远就闻到了蟹肉的香气了！', Speaker = 600083, Option = '', Emoji = 28, Action = _EmptyTable,}, 
	[1000653] = { id = 1000653, Voice = '', Text = '我只是想多吃点而已！哼哧…我真的不是很饿！', Speaker = 600083, Option = '', Action = _EmptyTable,}, 
	[1000652] = { id = 1000652, Voice = '', Text = '嗯…听说人类的海鲜特别好吃~我很想尝一尝…', Speaker = 600083, Option = '…', Action = _EmptyTable,}, 
	[1000651] = { id = 1000651, Voice = '', Text = '哼哧…有、有点…你这什么表情！这是一点点饿而已！', Speaker = 600083, Option = '想吃什么', Action = _EmptyTable,}, 
	[1000650] = { id = 1000650, Voice = '', Text = '哼哧…别小瞧我！我可不是一般的野猪。', Speaker = 600083, Option = '饿了？', Action = _EmptyTable,}, 
	[1000649] = { id = 1000649, Voice = '', Text = '哼哧…冒、冒险者？', Speaker = 600083, Option = '居然会说话', Action = _EmptyTable,}, 
	[1000647] = { id = 1000647, Voice = '', Text = '它的名字叫做咿维。我相信你一定能帮助到它的。', Speaker = 600080, Option = '', Action = _EmptyTable,}, 
	[1000646] = { id = 1000646, Voice = '', Text = '你能够去代替我看看它吗？它的名字叫做咿维。', Speaker = 600080, Option = '{好吧,2},{不好,1}', Action = _EmptyTable,}, 
	[1000645] = { id = 1000645, Voice = '', Text = '我很想帮助它！但是我身上还有着王国的使命。', Speaker = 600080, Option = '', Action = _EmptyTable,}, 
	[1000644] = { id = 1000644, Voice = '', Text = '从它热诚的眼中，我看到了冒险的光芒！但是它现在似乎陷入了困境。', Speaker = 600080, Option = '', Action = _EmptyTable,}, 
	[1000643] = { id = 1000643, Voice = '', Text = '当然！在接受王国的邀请来到这里的路上，我就曾遭遇到这样一头小野猪。', Speaker = 600080, Option = '', Action = _EmptyTable,}, 
	[1000642] = { id = 1000642, Voice = '', Text = '所有生物的内心都是渴望着冒险的，即使它是只野猪。', Speaker = 600080, Option = '野猪？', Action = _EmptyTable,}, 
	[1000641] = { id = 1000641, Voice = '', Text = '今年的主题是——大冒险！要知道在这片充满危险的大陆上，冒险可是永恒的话题！', Speaker = 600080, Option = '', Action = _EmptyTable,}, 
	[1000640] = { id = 1000640, Voice = '', Text = '啊哈~冒险者，告诉你一个令人兴奋的消息！米德加尔特大陆上一年一度的庆典就要开始了！', Speaker = 600080, Option = '', Action = _EmptyTable,}, 
