	[140971] = { id = 140971, Voice = '', Text = '好了，今天你一定也累了。', Speaker = 9522, Option = '', Action = _EmptyTable,}, 
	[140970] = { id = 140970, Voice = '', Text = '我是开个玩笑嘛~', Speaker = 9522, Option = '', Action = _EmptyTable,}, 
	[140969] = { id = 140969, Voice = '', Text = '应该像我一样不穿上衣，才最酷了！', Speaker = 9522, Option = '别吧…', Action = _EmptyTable,}, 
	[140968] = { id = 140968, Voice = '', Text = '哟吼，魔法师太古板啦~', Speaker = 9522, Option = '', Action = _EmptyTable,}, 
	[140967] = { id = 140967, Voice = '', Text = '哈哈，那些看起来很潇洒的魔法师们，居然也会因为衣服太热而苦恼啊。', Speaker = 9522, Option = '是呀', Emoji = 1, Action = _EmptyTable,}, 
	[140966] = { id = 140966, Voice = '', Text = '[c][ffff00][PlayerName][-][/c]，MP奶昔送到了么？', Speaker = 9522, Option = '说明情况', Action = _EmptyTable,}, 
	[140965] = { id = 140965, Voice = '', Text = '现在我终于可以好好享受我的MP奶昔了。', Speaker = 1321, Option = '不用谢', Action = _EmptyTable,}, 
	[140964] = { id = 140964, Voice = '', Text = '你帮我解决了一个大难题呀，冒险者。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[140963] = { id = 140963, Voice = '', Text = '妙啊，果然是极好的夏季制衣材料。', Speaker = 1321, Option = '', Emoji = 17, Action = _EmptyTable,}, 
	[140962] = { id = 140962, Voice = '', Text = '找到清凉棉花了么？', Speaker = 1321, Option = '交出棉花', Action = _EmptyTable,}, 
	[140961] = { id = 140961, Voice = '', Text = '调皮的溜溜猴抢走了清凉棉花，快别让它们跑了。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[140960] = { id = 140960, Voice = '', Text = '冒险家，你能帮我去找些清凉棉花么？', Speaker = 1321, Option = '好的', Action = _EmptyTable,}, 
	[140959] = { id = 140959, Voice = '', Text = '用它制成法袍的内层，穿起来一定凉飕飕的。', Speaker = 1321, Option = '真神奇', Action = _EmptyTable,}, 
	[140958] = { id = 140958, Voice = '', Text = '我想起来了，西门有一种叫清凉棉花的植物，天生透着一股清凉气息。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[140957] = { id = 140957, Voice = '', Text = '哎呀呀，老糊涂了，我怎么就没想到呢。', Speaker = 1321, Option = '哈哈', Action = _EmptyTable,}, 
	[140956] = { id = 140956, Voice = '', Text = '对啊，把法袍换一种更透气的布料不就凉快了？', Speaker = 1321, Option = '', Emoji = 11, Action = _EmptyTable,}, 
	[140955] = { id = 140955, Voice = '', Text = '你胡说些什么呢，就没其他办法了么？', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[140954] = { id = 140954, Voice = '', Text = '冒险家，你有什么好主意么？', Speaker = 1321, Option = '{换透气布料,3},{不做魔法师,4}，{不穿法袍,5}', Action = _EmptyTable,}, 
	[140953] = { id = 140953, Voice = '', Text = '但法袍是魔法师最基本的传统，怎能说不穿就不穿呢。', Speaker = 1321, Option = '对啊', Action = _EmptyTable,}, 
	[140952] = { id = 140952, Voice = '', Text = '好多年轻的魔法师最近向我抱怨太热了，都不想穿法袍了。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[140951] = { id = 140951, Voice = '', Text = '这种布料虽然穿着舒适，但天变热了，就很不透气。', Speaker = 1321, Option = '这样啊', Action = _EmptyTable,}, 
	[140950] = { id = 140950, Voice = '', Text = '我们魔法师的法袍，一直以来用的都是亚麻布。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[140949] = { id = 140949, Voice = '', Text = '哎，都是因为今年夏天太热了…', Speaker = 1321, Option = '', Emoji = 15, Action = _EmptyTable,}, 
	[140948] = { id = 140948, Voice = '', Text = '实在是没太多心情喝MP奶昔啊。', Speaker = 1321, Option = '怎么了？', Action = _EmptyTable,}, 
	[140947] = { id = 140947, Voice = '', Text = '哎呀，可惜老夫我这几天正为一些事头疼呢。', Speaker = 1321, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[140946] = { id = 140946, Voice = '', Text = '天气这么热，真是辛苦你一路送甜品过来啊。', Speaker = 1321, Option = '不用谢', Emoji = 7, Action = _EmptyTable,}, 
	[140945] = { id = 140945, Voice = '', Text = '冒险者，来魔法公会有什么事么？', Speaker = 1321, Option = '交出甜品', Action = _EmptyTable,}, 
	[140944] = { id = 140944, Voice = '', Text = '记得路上多喝点水，别像南南一样中暑了~', Speaker = 9522, Option = '嗯', Emoji = 4, Action = _EmptyTable,}, 
	[140943] = { id = 140943, Voice = '', Text = '到了吉芬直接找雷藏大法师吧。', Speaker = 9522, Option = '', Action = _EmptyTable,}, 
	[140942] = { id = 140942, Voice = '', Text = '是送往吉芬魔法公会的。', Speaker = 9522, Option = '知道了', Action = _EmptyTable,}, 
	[140941] = { id = 140941, Voice = '', Text = '今天的夏日甜品都是专门为魔法师定制的MP奶昔~', Speaker = 9522, Option = '', Emoji = 20, Action = _EmptyTable,}, 
	[140940] = { id = 140940, Voice = '', Text = '[c][ffff00][PlayerName][-][/c]，早上好呀~', Speaker = 9522, Option = '早上好', Action = _EmptyTable,}, 
	[140939] = { id = 140939, Voice = '', Text = '不过甜品现在都还没包装好，要不你明天早上来取货吧。', Speaker = 9522, Option = '没问题', Action = _EmptyTable,}, 
	[140938] = { id = 140938, Voice = '', Text = '够义气耶，你果然是个超酷的朋友！', Speaker = 9522, Option = '那当然', Emoji = 20, Action = _EmptyTable,}, 
	[140937] = { id = 140937, Voice = '', Text = '南南病了，该找谁代替她呢？', Speaker = 9522, Option = '我来吧', Action = _EmptyTable,}, 
	[140936] = { id = 140936, Voice = '', Text = '哎，可之后几天还有要送往其他地方的夏日甜品。', Speaker = 9522, Option = '', Action = _EmptyTable,}, 
	[140935] = { id = 140935, Voice = '', Text = '呼，原来是中暑了，还好人没大碍，甜品也及时送到了。', Speaker = 9522, Option = '', Action = _EmptyTable,}, 
	[140934] = { id = 140934, Voice = '', Text = '怎么样，[c][ffff00][PlayerName][-][/c]，南南和那批冰冻甜品都还好么？', Speaker = 9522, Option = '说明情况', Emoji = 18, Action = _EmptyTable,}, 
	[140933] = { id = 140933, Voice = '', Text = '我代表依斯鲁得的居民感谢你，冒险者。', Speaker = 9537, Option = '举手之劳', Action = _EmptyTable,}, 
	[140932] = { id = 140932, Voice = '', Text = '这些冰冻甜品真是来之不易啊。', Speaker = 9537, Option = '', Action = _EmptyTable,}, 
	[140931] = { id = 140931, Voice = '', Text = '愿众神的赐福保佑她尽快好起来……', Speaker = 9537, Option = '嗯嗯', Action = _EmptyTable,}, 
	[140930] = { id = 140930, Voice = '', Text = '啊？原来南南中暑了，你是代她来送甜品的？', Speaker = 9537, Option = '是的', Emoji = 7, Action = _EmptyTable,}, 
	[140929] = { id = 140929, Voice = '', Text = '这是……？', Speaker = 9537, Option = '说明情况', Action = _EmptyTable,}, 
	[140928] = { id = 140928, Voice = '', Text = '喏，这是要送过去的甜品，交给你了。', Speaker = 1054, Option = '好的', Action = _EmptyTable,}, 
	[140927] = { id = 140927, Voice = '', Text = '太好了，相信南南也很放心由你来帮她的。', Speaker = 1054, Option = '', Action = _EmptyTable,}, 
	[140926] = { id = 140926, Voice = '', Text = '可现在是夏日祭，南门的治安非常重要，我实在脱不开身。', Speaker = 1054, Option = '乐意帮忙', Emoji = 10, Action = _EmptyTable,}, 
	[140925] = { id = 140925, Voice = '', Text = '所以她想委托我帮她送甜品。', Speaker = 1054, Option = '', Action = _EmptyTable,}, 
	[140924] = { id = 140924, Voice = '', Text = '南南说她原本是去依斯鲁得送夏日甜品的，不能因为中暑就耽搁了。', Speaker = 1054, Option = '', Action = _EmptyTable,}, 
	[140923] = { id = 140923, Voice = '', Text = '不过她……', Speaker = 1054, Option = '她怎么了？', Action = _EmptyTable,}, 
	[140922] = { id = 140922, Voice = '', Text = '我已经给她服了药了，休息几天她应该就没事了。', Speaker = 1054, Option = '', Action = _EmptyTable,}, 
	[140921] = { id = 140921, Voice = '', Text = '都怪这天气太热了，南南又拖着小货车，所以累中暑了。', Speaker = 1054, Option = '原来是这样', Action = _EmptyTable,}, 
	[140920] = { id = 140920, Voice = '', Text = '昨天我巡逻的时候，发现了半路晕倒的她，就把她带回来了。', Speaker = 1054, Option = '', Action = _EmptyTable,}, 
	[140919] = { id = 140919, Voice = '', Text = '她正在我的帐篷里休息呢。', Speaker = 1054, Option = '啊？', Action = _EmptyTable,}, 
	[140918] = { id = 140918, Voice = '', Text = '原来是来找甜点师南南啊…', Speaker = 1054, Option = '', Emoji = 21, Action = _EmptyTable,}, 
	[140917] = { id = 140917, Voice = '', Text = '[c][ffff00][PlayerName][-][/c]，怎么急匆匆的，有什么事么？', Speaker = 1054, Option = '说明情况', Emoji = 19, Action = _EmptyTable,}, 
	[140916] = { id = 140916, Voice = '', Text = '你去问问她，应该会知道些什么的。', Speaker = 1008, Option = '嗯嗯', Action = _EmptyTable,}, 
	[140915] = { id = 140915, Voice = '', Text = '不过昨天负责巡逻的，是乐园团的宝雅教官。', Speaker = 1008, Option = '', Action = _EmptyTable,}, 
	[140914] = { id = 140914, Voice = '', Text = '不好意思，我只是有印象，但不清楚她具体去哪了。', Speaker = 1008, Option = '好吧', Action = _EmptyTable,}, 
	[140913] = { id = 140913, Voice = '', Text = '这么说起来，我确实记得昨天看到过一个甜点师。', Speaker = 1008, Option = '她在哪？', Action = _EmptyTable,}, 
	[140912] = { id = 140912, Voice = '', Text = '噢？甜点师南南？', Speaker = 1008, Option = '', Action = _EmptyTable,}, 
	[140911] = { id = 140911, Voice = '', Text = '怎么了冒险者，有什么事么？', Speaker = 1008, Option = '说明情况', Action = _EmptyTable,}, 
