	[140909] = { id = 140909, Voice = '', Text = '[c][ffff00][PlayerName][-][/c]，你能帮我去找找南南么？', Speaker = 9522, Option = '当然可以', Action = _EmptyTable,}, 
	[140908] = { id = 140908, Voice = '', Text = '我很担心甜点师南南是不是在路上出了什么意外。', Speaker = 9522, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[140907] = { id = 140907, Voice = '', Text = '但是直到今天早上，瓦尔基里小姐在信里说依然没收到甜品。', Speaker = 9522, Option = '原来是这样', Action = _EmptyTable,}, 
	[140906] = { id = 140906, Voice = '', Text = '赫露大师昨天早上，就派她的学生南南去送货了……', Speaker = 9522, Option = '', Action = _EmptyTable,}, 
	[140905] = { id = 140905, Voice = '', Text = '嗯，之前她代表依斯鲁得向皇室订购了一批冰冻甜品。', Speaker = 9522, Option = '', Emoji = 25, Action = _EmptyTable,}, 
	[140904] = { id = 140904, Voice = '', Text = '刚才我收到了依斯鲁得的女武神，瓦尔基里小姐的信。', Speaker = 9522, Option = '信？', Action = _EmptyTable,}, 
	[140903] = { id = 140903, Voice = '', Text = '哎，是这样的……', Speaker = 9522, Option = '', Action = _EmptyTable,}, 
