	[140899] = { id = 140899, Voice = '', Text = '哟吼，[c][ffff00][PlayerName][-][/c]，你又来啦~', Speaker = 9522, Option = '嗯', Emoji = 20, Action = _EmptyTable,}, 
	[140898] = { id = 140898, Voice = '', Text = '你先回去吧~明天要是有空，还可以来找我玩！', Speaker = 9522, Option = '好的', Action = _EmptyTable,}, 
