	[140872] = { id = 140872, Voice = '', Text = '希望他会喜欢吧~', Speaker = 9524, Option = '一定会的', Action = _EmptyTable,}, 
	[140871] = { id = 140871, Voice = '', Text = '嗯…还好我为他定制了恢复体力的冰酷雪糕。', Speaker = 9524, Option = '', Action = _EmptyTable,}, 
	[140870] = { id = 140870, Voice = '', Text = '他这几天一定为了夏日祭累惨了吧。', Speaker = 9524, Option = '', Action = _EmptyTable,}, 
	[140869] = { id = 140869, Voice = '', Text = '也真是为难他了，这么热的天还要站在广场宣传夏日祭。', Speaker = 9524, Option = '是啊', Action = _EmptyTable,}, 
