	[140853] = { id = 140853, Voice = '', Text = 'Bingo~答案是……全都正确！', Speaker = 9522, Option = '', Emoji = 11, Action = _EmptyTable,}, 
	[140852] = { id = 140852, Voice = '', Text = '在这样的炎炎夏日，最能让人畅爽的东西，当然是……', Speaker = 9522, Option = '{雪糕,4},{奶昔,4},{刨冰,4}', Emoji = 25, Action = _EmptyTable,}, 
	[140851] = { id = 140851, Voice = '', Text = '啧啧，我看你都热的出汗了，正需要降降温吧~', Speaker = 9522, Option = '嗯嗯', Action = _EmptyTable,}, 
	[140849] = { id = 140849, Voice = '', Text = '快来加入我们超Coooool的清凉夏日祭吧！', Speaker = 9536, Option = '', Action = _EmptyTable,}, 
	[140848] = { id = 140848, Voice = '', Text = '冒险者！天气这么热~想不想一起清凉一下呀？', Speaker = 9536, Option = '', Emoji = 20, Action = _EmptyTable,}, 
	[140841] = { id = 140841, Voice = '', Text = '虽然很忙碌，但忠心的希望你喜欢我们的端午节。', Speaker = 9511, Option = '我…很喜欢', Emoji = 27, Action = _EmptyTable,}, 
	[140840] = { id = 140840, Voice = '', Text = '我代他们一起向你表示感谢。', Speaker = 9511, Option = '小事而已', Action = _EmptyTable,}, 
	[140839] = { id = 140839, Voice = '', Text = '他们都知道你这几天为斐扬辛苦极了。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140838] = { id = 140838, Voice = '', Text = '都是斐扬的居民一起给你准备的。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140837] = { id = 140837, Voice = '', Text = '不过现在，我想送你一份大礼物。', Speaker = 9511, Option = '礼物？', Emoji = 4, Action = _EmptyTable,}, 
	[140836] = { id = 140836, Voice = '', Text = '还有全大陆最美的斐扬！', Speaker = 9511, Option = '一言为定', Action = _EmptyTable,}, 
	[140835] = { id = 140835, Voice = '', Text = '我一定带你欣赏一整天最有趣的端午。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140834] = { id = 140834, Voice = '', Text = '我答应你，如果明年还是由我负责端午节。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140833] = { id = 140833, Voice = '', Text = '你真好，能遇到你真是太幸运了。', Speaker = 9511, Option = '', Emoji = 26, Action = _EmptyTable,}, 
	[140832] = { id = 140832, Voice = '', Text = '很不好意思呢…', Speaker = 9511, Option = '没事的', Action = _EmptyTable,}, 
	[140831] = { id = 140831, Voice = '', Text = '结果到最后也没让你闲下来…', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140830] = { id = 140830, Voice = '', Text = '原本打算今天带你去逛端午的。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140829] = { id = 140829, Voice = '', Text = '呜，都让你帮忙这么多天了。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140828] = { id = 140828, Voice = '', Text = '太好了！', Speaker = 9511, Option = '别客气', Emoji = 27, Action = _EmptyTable,}, 
	[140827] = { id = 140827, Voice = '', Text = '你真是……', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140826] = { id = 140826, Voice = '', Text = '怎么样，统帅的龙舟还能修好么？', Speaker = 9511, Option = '告知情况', Action = _EmptyTable,}, 
	[140825] = { id = 140825, Voice = '', Text = '我决定亲手修好龙舟。', Speaker = 2527, Option = '嗯嗯', Action = _EmptyTable,}, 
	[140824] = { id = 140824, Voice = '', Text = '谢谢你，但是请容我先失陪了。', Speaker = 2527, Option = '', Action = _EmptyTable,}, 
	[140823] = { id = 140823, Voice = '', Text = '一定能把龙舟修复到完好如初。', Speaker = 2527, Option = '', Action = _EmptyTable,}, 
	[140822] = { id = 140822, Voice = '', Text = '太好了，这么多上好的木料。', Speaker = 2527, Option = '', Emoji = 6, Action = _EmptyTable,}, 
	[140821] = { id = 140821, Voice = '', Text = '请求你帮我去找些新的木料来吧。', Speaker = 2527, Option = '好的', Action = _EmptyTable,}, 
	[140820] = { id = 140820, Voice = '', Text = '年轻人，看来你现在是我唯一的希望了。', Speaker = 2527, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[140819] = { id = 140819, Voice = '', Text = '[c][ffff00][PlayerName][-][/c]，你知道西门的树精么？', Speaker = 9511, Option = '当然啦', Action = _EmptyTable,}, 
	[140818] = { id = 140818, Voice = '', Text = '诶，这位[c][ffff00][PlayerName][-][/c]就是从普隆德拉来的冒险者。', Speaker = 9511, Option = '', Emoji = 7, Action = _EmptyTable,}, 
	[140817] = { id = 140817, Voice = '', Text = '那可是远在普隆德拉西门的树精身上才有的上乘木料。', Speaker = 2527, Option = '', Action = _EmptyTable,}, 
	[140816] = { id = 140816, Voice = '', Text = '上哪找人去搜集修补用的木料呢？', Speaker = 2527, Option = '', Action = _EmptyTable,}, 
	[140815] = { id = 140815, Voice = '', Text = '可现在这个节骨眼，大家都在过端午节。', Speaker = 2527, Option = '', Emoji = 15, Action = _EmptyTable,}, 
	[140814] = { id = 140814, Voice = '', Text = '龙舟坏了，再修补起来不就好了。', Speaker = 9511, Option = '对啊', Action = _EmptyTable,}, 
	[140813] = { id = 140813, Voice = '', Text = '别灰心啊，统帅大人。', Speaker = 9511, Option = '', Emoji = 26, Action = _EmptyTable,}, 
	[140812] = { id = 140812, Voice = '', Text = '这真是难以料到的情况。', Speaker = 2527, Option = '是啊', Action = _EmptyTable,}, 
	[140811] = { id = 140811, Voice = '', Text = '原本想在今年端午节为斐扬的居民们表演龙舟的。', Speaker = 2527, Option = '', Action = _EmptyTable,}, 
	[140810] = { id = 140810, Voice = '', Text = '可惜，我的龙舟已经老鼠咬的千疮百孔了。', Speaker = 2527, Option = '哎…', Action = _EmptyTable,}, 
	[140809] = { id = 140809, Voice = '', Text = '年轻人，真是辛苦你了。', Speaker = 2527, Option = '', Emoji = 6, Action = _EmptyTable,}, 
	[140808] = { id = 140808, Voice = '', Text = '呼，终于清理干净了。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140807] = { id = 140807, Voice = '', Text = '你愿意帮我一起去赶走老鼠么？', Speaker = 9511, Option = '好呀', Action = _EmptyTable,}, 
	[140806] = { id = 140806, Voice = '', Text = '[c][ffff00][PlayerName][-][/c]，呜，又要忙了。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140805] = { id = 140805, Voice = '', Text = '老鼠的问题，先交给我来解决吧。', Speaker = 9511, Option = '嗯', Action = _EmptyTable,}, 
	[140804] = { id = 140804, Voice = '', Text = '别急别急，统帅大人的事就是斐扬人的事。', Speaker = 9511, Option = '', Emoji = 18, Action = _EmptyTable,}, 
	[140803] = { id = 140803, Voice = '', Text = '统帅大人可是准备在端午节划龙舟的呀！', Speaker = 9520, Option = '这…', Action = _EmptyTable,}, 
	[140802] = { id = 140802, Voice = '', Text = '都被该死的老鼠咬破了。', Speaker = 9520, Option = '', Action = _EmptyTable,}, 
	[140801] = { id = 140801, Voice = '', Text = '统帅大人珍藏的龙舟啊。', Speaker = 9520, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[140800] = { id = 140800, Voice = '', Text = '啊？！', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140799] = { id = 140799, Voice = '', Text = '统帅府突然闹老鼠了！', Speaker = 9520, Option = '', Action = _EmptyTable,}, 
	[140798] = { id = 140798, Voice = '', Text = '不好了，大事不好了！', Speaker = 9520, Option = '发生什么了？', Emoji = 18, Action = _EmptyTable,}, 
	[140797] = { id = 140797, Voice = '', Text = '先逛哪里好呢……', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140796] = { id = 140796, Voice = '', Text = '嘻嘻，我带你去四处逛逛吧？', Speaker = 9511, Option = '', Emoji = 2, Action = _EmptyTable,}, 
	[140795] = { id = 140795, Voice = '', Text = '也就是说呀，今天我终于不用麻烦你帮忙啦。', Speaker = 9511, Option = '嗯嗯', Action = _EmptyTable,}, 
	[140794] = { id = 140794, Voice = '', Text = '今年的端午节，他们都玩的很尽兴呢。', Speaker = 9511, Option = '那就好', Action = _EmptyTable,}, 
	[140793] = { id = 140793, Voice = '', Text = '那些其他城市的客人基本都已经回去了。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140792] = { id = 140792, Voice = '', Text = '告诉你个好消息吧。', Speaker = 9511, Option = '', Emoji = 9, Action = _EmptyTable,}, 
	[140791] = { id = 140791, Voice = '', Text = '[c][ffff00][PlayerName][-][/c]，怎么样，昨晚是不是睡得很香？', Speaker = 9511, Option = '嗯', Action = _EmptyTable,}, 
	[140790] = { id = 140790, Voice = '', Text = '嗯，我也先回吉芬去了，明年端午再见！', Speaker = 9519, Option = '嗯嗯', Emoji = 17, Action = _EmptyTable,}, 
	[140789] = { id = 140789, Voice = '', Text = '去吧去吧，明天见。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140788] = { id = 140788, Voice = '', Text = '赶紧回去睡一觉，才更能彻底释放压力。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140787] = { id = 140787, Voice = '', Text = '好了，是不是感觉浑身都舒服了？', Speaker = 9511, Option = '是的', Action = _EmptyTable,}, 
	[140786] = { id = 140786, Voice = '', Text = '别着急，你好像念错了。', Speaker = 9519, Option = '', Action = _EmptyTable,}, 
	[140785] = { id = 140785, Voice = '', Text = '沐洗双腿时，应该咏唱——', Speaker = 9511, Option = '{让我与健康同在,8},{让我与生命同在,9}', Action = _EmptyTable,}, 
	[140784] = { id = 140784, Voice = '', Text = '沐洗肩膀时，应该咏唱——', Speaker = 9511, Option = '{感谢你们的馈赠,6},{感谢你们的赐予,7}', Action = _EmptyTable,}, 
	[140783] = { id = 140783, Voice = '', Text = '沐洗头发时，应该咏唱——', Speaker = 9511, Option = '{大自然的精灵啊,4},{大自然的馈赠啊,5}', Action = _EmptyTable,}, 
	[140782] = { id = 140782, Voice = '', Text = '好，那就请梨香小姐为我们开始沐兰汤吧。', Speaker = 9519, Option = '', Action = _EmptyTable,}, 
	[140781] = { id = 140781, Voice = '', Text = '记住了么？', Speaker = 9519, Option = '{记住了,2},{还没记住,3}', Action = _EmptyTable,}, 
	[140780] = { id = 140780, Voice = '', Text = '沐洗双腿时，咏唱——让我与健康同在。', Speaker = 9519, Option = '', Action = _EmptyTable,}, 
	[140779] = { id = 140779, Voice = '', Text = '沐洗肩膀时，咏唱——感谢你们的馈赠。', Speaker = 9519, Option = '', Action = _EmptyTable,}, 
	[140778] = { id = 140778, Voice = '', Text = '沐洗头发时，咏唱——大自然的精灵啊。', Speaker = 9519, Option = '', Action = _EmptyTable,}, 
	[140777] = { id = 140777, Voice = '', Text = '听好了，一共三句，很简单的。', Speaker = 9519, Option = '', Action = _EmptyTable,}, 
	[140776] = { id = 140776, Voice = '', Text = '在沐洗的时候配合咏唱特定的咒语，会达到更好的放松效果。', Speaker = 9519, Option = '原来如此', Action = _EmptyTable,}, 
	[140775] = { id = 140775, Voice = '', Text = '对，沐兰汤是自然精灵魔力的馈赠。', Speaker = 9519, Option = '', Action = _EmptyTable,}, 
	[140774] = { id = 140774, Voice = '', Text = '我教你一段咏唱怎么样？', Speaker = 9519, Option = '咏唱？', Action = _EmptyTable,}, 
	[140773] = { id = 140773, Voice = '', Text = '[c][ffff00][PlayerName][-][/c]，你应该是第一次接触沐兰汤吧？', Speaker = 9519, Option = '', Emoji = 8, Action = _EmptyTable,}, 
	[140772] = { id = 140772, Voice = '', Text = '艾草水和菖蒲都准备好了呢~', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140771] = { id = 140771, Voice = '', Text = '所以嘛，快享受沐兰汤吧。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140770] = { id = 140770, Voice = '', Text = '哎，一言难尽…', Speaker = 9519, Option = '是啊…', Emoji = 25, Action = _EmptyTable,}, 
	[140769] = { id = 140769, Voice = '', Text = '要在吉芬管理魔法师公会大大小小的事务，一定很辛苦吧。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140768] = { id = 140768, Voice = '', Text = '嘻嘻，但是雷藏先生却看起来很疲惫呢。', Speaker = 9511, Option = '', Emoji = 26, Action = _EmptyTable,}, 
	[140767] = { id = 140767, Voice = '', Text = '比上次我见到你时，你看起来更有精神了。', Speaker = 9519, Option = '', Action = _EmptyTable,}, 
	[140766] = { id = 140766, Voice = '', Text = '梨香啊，好久不见。', Speaker = 9519, Option = '', Action = _EmptyTable,}, 
	[140765] = { id = 140765, Voice = '', Text = '来了来了，劳烦晚辈们等我了。', Speaker = 9519, Option = '', Emoji = 17, Action = _EmptyTable,}, 
	[140764] = { id = 140764, Voice = '', Text = '那就好，估计他就快来了。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140763] = { id = 140763, Voice = '', Text = '怎么样，和雷藏先生说了么？', Speaker = 9511, Option = '嗯', Action = _EmptyTable,}, 
	[140762] = { id = 140762, Voice = '', Text = '你先回斐扬吧，我处理完立刻就去！', Speaker = 1321, Option = '好的', Action = _EmptyTable,}, 
	[140761] = { id = 140761, Voice = '', Text = '这会儿我手头还有些小事。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[140760] = { id = 140760, Voice = '', Text = '太需要一次沐兰汤，放松一下自己了。', Speaker = 1321, Option = '嗯嗯', Action = _EmptyTable,}, 
	[140759] = { id = 140759, Voice = '', Text = '这几年我四处忙碌，搞得身体都有些吃不消了。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[140758] = { id = 140758, Voice = '', Text = '太谢谢你来提醒我了。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[140757] = { id = 140757, Voice = '', Text = '怎么让我把端午节的沐兰汤给忘了呢。', Speaker = 1321, Option = '哈哈', Action = _EmptyTable,}, 
	[140756] = { id = 140756, Voice = '', Text = '最近沉迷于新的魔法研发。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[140755] = { id = 140755, Voice = '', Text = '哎呀呀，真是老糊涂了！', Speaker = 1321, Option = '', Emoji = 1, Action = _EmptyTable,}, 
	[140754] = { id = 140754, Voice = '', Text = '找我有什么事么？', Speaker = 1321, Option = '说明情况', Action = _EmptyTable,}, 
	[140753] = { id = 140753, Voice = '', Text = '[c][ffff00][PlayerName][-][/c]，好久不见啊。', Speaker = 1321, Option = '', Action = _EmptyTable,}, 
	[140752] = { id = 140752, Voice = '', Text = '麻烦你去吉芬提醒他快来吧。', Speaker = 9511, Option = '小事一桩', Emoji = 4, Action = _EmptyTable,}, 
	[140751] = { id = 140751, Voice = '', Text = '我很希望他能来享受一次难得的沐兰汤。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
