	[140749] = { id = 140749, Voice = '', Text = '导致一直到现在都还没来斐扬。', Speaker = 9511, Option = '嗯', Action = _EmptyTable,}, 
	[140748] = { id = 140748, Voice = '', Text = '但可能他太忙了忘了看信。', Speaker = 9511, Option = '', Emoji = 19, Action = _EmptyTable,}, 
	[140747] = { id = 140747, Voice = '', Text = '前几天我写信通知过他，端午节快到了。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140746] = { id = 140746, Voice = '', Text = '吉芬的雷藏先生，很早就找我预约了沐兰汤。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140745] = { id = 140745, Voice = '', Text = '不过……', Speaker = 9511, Option = '怎么了？', Action = _EmptyTable,}, 
