	[140743] = { id = 140743, Voice = '', Text = '是一年才有一次的享受呢！', Speaker = 9511, Option = '等不及了', Action = _EmptyTable,}, 
	[140742] = { id = 140742, Voice = '', Text = '这种魔力可以消除沐浴者的疲劳，让人容光焕发。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140741] = { id = 140741, Voice = '', Text = '每年的端午节，植物中会天然蕴含一些魔力。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140740] = { id = 140740, Voice = '', Text = '先以艾草酿成艾叶水，再用菖蒲打湿后，从头到脚洒在沐浴者的身上。', Speaker = 9511, Option = '有意思', Action = _EmptyTable,}, 
	[140739] = { id = 140739, Voice = '', Text = '沐兰汤就是同时发挥这两种植物的魔力。', Speaker = 9511, Option = '', Emoji = 25, Action = _EmptyTable,}, 
	[140738] = { id = 140738, Voice = '', Text = '还有我送给你的菖蒲。', Speaker = 9511, Option = '当然记得', Action = _EmptyTable,}, 
	[140737] = { id = 140737, Voice = '', Text = '还记不记得之前让你去采集的艾草？', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140736] = { id = 140736, Voice = '', Text = '是端午节特有的一种沐洗活动——沐兰汤。', Speaker = 9511, Option = '沐兰汤？', Action = _EmptyTable,}, 
