	[140734] = { id = 140734, Voice = '', Text = '洗澡！', Speaker = 9511, Option = '诶？！', Action = _EmptyTable,}, 
	[140733] = { id = 140733, Voice = '', Text = '答案就是……', Speaker = 9511, Option = '', Emoji = 9, Action = _EmptyTable,}, 
