	[140731] = { id = 140731, Voice = '', Text = '又见到你了，真高兴啊。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140730] = { id = 140730, Voice = '', Text = '明天带你玩一些能放松身心的活动吧~', Speaker = 9511, Option = '好的', Action = _EmptyTable,}, 
	[140729] = { id = 140729, Voice = '', Text = '快回去休息吧。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
