	[140710] = { id = 140710, Voice = '', Text = '啊啊啊，我怎么变成这样了？！', Speaker = 9515, Option = '', Emoji = 1, Action = _EmptyTable,}, 
	[140709] = { id = 140709, Voice = '', Text = '嘿嘿嘿，端午节真好玩。', Speaker = 9514, Option = '{使用黄色触媒,2},{使用蓝色触媒,3},{使用红色触媒,4},{使用绿色触媒,5}', Emoji = 5, Action = _EmptyTable,}, 
	[140708] = { id = 140708, Voice = '', Text = '你快去找卡鲁船长试试吧。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140707] = { id = 140707, Voice = '', Text = '好了，这里是画额用的几种触媒，都给你。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140706] = { id = 140706, Voice = '', Text = '嘻嘻，你去帮我教训教训卡鲁船长吧。', Speaker = 9511, Option = '没问题', Action = _EmptyTable,}, 
	[140705] = { id = 140705, Voice = '', Text = '现在我把画额的技巧也教给你。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140704] = { id = 140704, Voice = '', Text = '就连我也被他欺负过呢。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140703] = { id = 140703, Voice = '', Text = '依斯鲁得的卡鲁船长，这几天在斐扬到处用画额戏弄别人。', Speaker = 9511, Option = '', Emoji = 15, Action = _EmptyTable,}, 
	[140702] = { id = 140702, Voice = '', Text = '嘿嘿，现在我给你一个报仇的机会。', Speaker = 9511, Option = '噢？', Emoji = 5, Action = _EmptyTable,}, 
	[140701] = { id = 140701, Voice = '', Text = '就是用大脚熊的毛发作为触媒的效果。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140700] = { id = 140700, Voice = '', Text = '没错，刚才我把你变成大脚熊。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140699] = { id = 140699, Voice = '', Text = '用某些特定物质作为触媒，画额便会有让人易容变身的效果。', Speaker = 9511, Option = '我懂了', Action = _EmptyTable,}, 
	[140698] = { id = 140698, Voice = '', Text = '经过百年的变迁，画额的触媒已经不止于雄黄粉了。', Speaker = 9511, Option = '', Emoji = 21, Action = _EmptyTable,}, 
	[140697] = { id = 140697, Voice = '', Text = '但依然保留了画额的传统，以纪念那位好心的修行者。', Speaker = 9511, Option = '嗯', Action = _EmptyTable,}, 
	[140696] = { id = 140696, Voice = '', Text = '虽然斐扬的人们不再需要画额来治病。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140695] = { id = 140695, Voice = '', Text = '不过这种病已经消失上百年了。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140694] = { id = 140694, Voice = '', Text = '这，就叫做画额。', Speaker = 9511, Option = '原来如此', Action = _EmptyTable,}, 
	[140693] = { id = 140693, Voice = '', Text = '利用法印注入体内，就有了治病的效果。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140692] = { id = 140692, Voice = '', Text = '嗯，雄黄粉具有恰到好处的微量火属性魔法。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140691] = { id = 140691, Voice = '', Text = '然后涂抹在孩子的额头上，画成特定的法印。', Speaker = 9511, Option = '这么神奇？', Action = _EmptyTable,}, 
	[140690] = { id = 140690, Voice = '', Text = '将雄黄石磨成粉末，作为触媒……', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140689] = { id = 140689, Voice = '', Text = '终于发现了破解怪病的方法…', Speaker = 9511, Option = '是什么？', Emoji = 11, Action = _EmptyTable,}, 
	[140688] = { id = 140688, Voice = '', Text = '他不忍看到孩子们生病，于是研究了七个昼夜。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140687] = { id = 140687, Voice = '', Text = '好在这个时候，斐扬来了一位神秘的修行者。', Speaker = 9511, Option = '', Emoji = 25, Action = _EmptyTable,}, 
	[140686] = { id = 140686, Voice = '', Text = '大人们都很担心，端午节也变得死气沉沉的。', Speaker = 9511, Option = '真可怕', Action = _EmptyTable,}, 
	[140685] = { id = 140685, Voice = '', Text = '那一年的端午节，好多孩子都病了。', Speaker = 9511, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[140684] = { id = 140684, Voice = '', Text = '得病的孩子会变得手脚发冷，胆小怕生。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140683] = { id = 140683, Voice = '', Text = '相传，以前斐扬闹过一种小孩很容易得的怪病。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140682] = { id = 140682, Voice = '', Text = '竖起耳朵，好好听我讲故事~', Speaker = 9511, Option = '嗯', Action = _EmptyTable,}, 
	[140681] = { id = 140681, Voice = '', Text = '那样你就能变回原样了。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140680] = { id = 140680, Voice = '', Text = '把额头的法印洗掉吧。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140679] = { id = 140679, Voice = '', Text = '咳咳，你去河边打点水。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140678] = { id = 140678, Voice = '', Text = '但是你现在的样子太好笑了。', Speaker = 9511, Option = '……', Action = _EmptyTable,}, 
	[140677] = { id = 140677, Voice = '', Text = '哈哈哈，我是想给你讲典故的。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140676] = { id = 140676, Voice = '', Text = '这个叫画额，它也是有典故的。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140675] = { id = 140675, Voice = '', Text = '这个其实也是端午节的传统活动之一哦。', Speaker = 9511, Option = '诶？', Action = _EmptyTable,}, 
	[140674] = { id = 140674, Voice = '', Text = '是不是被我吓到了呀？', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140673] = { id = 140673, Voice = '', Text = '哈哈哈哈，你这个样子超好笑的~', Speaker = 9511, Option = '', Emoji = 17, Action = _EmptyTable,}, 
