	[140671] = { id = 140671, Voice = '', Text = '惊喜就是……', Speaker = 9511, Option = '', Emoji = 5, Action = _EmptyTable,}, 
	[140670] = { id = 140670, Voice = '', Text = '是不是很期待我说的惊喜？', Speaker = 9511, Option = '嗯', Action = _EmptyTable,}, 
	[140669] = { id = 140669, Voice = '', Text = '今天来的挺早嘛~', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140668] = { id = 140668, Voice = '', Text = '明天准时再来找我，我还给你准备了别的惊喜。', Speaker = 9511, Option = '好的', Action = _EmptyTable,}, 
	[140667] = { id = 140667, Voice = '', Text = '好了，你快回去把菖蒲挂在门上。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
