	[140665] = { id = 140665, Voice = '', Text = '我自己培育的品种哟。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140664] = { id = 140664, Voice = '', Text = '这是我从小在自家院子里种的菖蒲。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140663] = { id = 140663, Voice = '', Text = '不过，我可不是随便找了个替代品送给你哦。', Speaker = 9511, Option = '嗯？', Action = _EmptyTable,}, 
	[140662] = { id = 140662, Voice = '', Text = '这是菖蒲，也是端午节用来消灾去厄的植物。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
