	[140651] = { id = 140651, Voice = '', Text = '你帮他把艾草送过去吧。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
	[140650] = { id = 140650, Voice = '', Text = '海陆曼先生现在应该在喝酒。', Speaker = 9511, Option = '', Action = _EmptyTable,}, 
