Table_Dialog_Quest8 =
{ 
	[86023] = { id = 86023, Text = '这真是一张充满意义的合影，这是你们冒险的起点，也是你们荣耀的见证，冒险者，希望你们在之后的旅途中能够将荣耀继续下去。', Speaker = 6078,}, 
	[86022] = { id = 86022, Text = '或许现在的你早已不是当初稚嫩弱小的冒险者，但我仍希望你能保持着一如既往的初心，为了我们在意的人，为了守护心中所爱的世界而继续奋斗。冒险者，请你和指定的公会成员去南门门口合影，一起见证荣耀诞生的起点。', Speaker = 6078,}, 
	[86021] = { id = 86021, Text = '冒险者，当你经过了重重险境，战胜了无数难关之后，你是否也曾怀念过当初冒险旅途的起点？在普隆德拉南门，在这曾汇聚了万千冒险者的地方，一颗颗冒险的初心正是从这里升起向着心中的梦想出发。', Speaker = 6078,}, 
	[86020] = { id = 86020, Text = '你们合照内的身影和灵动轻柔的的泉水相得益彰，透过这张合影，我看到了你们最诚挚知心的友谊，就让这座音乐喷泉成为你们友谊的见证。', Speaker = 6078,}, 
	[86019] = { id = 86019, Text = '正是这样的美妙风景吸引了众多冒险者流连忘返，在这里可是时常可见结伴而来的人群和喷泉拍照留念呢。冒险者，如果你也喜欢和朋友们穿梭在令人赏心悦目的风景中，那就邀请他们一起来感受这独特喷泉的魅力并合影留念吧。', Speaker = 6078,}, 
	[86018] = { id = 86018, Text = '你知道吗？在普隆德拉，最独特的地标建筑恐怕就是矗立在中心广场的音乐喷泉了，随着音乐节奏的激荡回旋，清澈灵动的泉水荡漾起泓泓柔波，在夜色下闪耀着缤纷的斑斓色彩。', Speaker = 6078,}, 
	[85262] = { id = 85262, Text = '透过这张合影，能够清晰地看到你和朋友们最坚定不渝的友谊和对未来的美好憧憬，也许这才是最应该被珍惜的东西。', Speaker = 6108,}, 
	[85261] = { id = 85261, Text = '找到公会内的四名成员，与他们合影一张吧，让这张特殊的合影记录下你对公会最浓厚不舍的情意和最坚定不移的决心。', Speaker = 6108,}, 
	[85260] = { id = 85260, Text = '仔细观察墙上的精美相框，你会发现相框内的照片记录的是公会成员们在公会生活中的光辉瞬间，虽然美好的时光已逝，但从照片里仍然可以看到他们对公会未来无限瑰丽的遐想和展望。', Speaker = 6108,}, 
	[85259] = { id = 85259, Text = '这张漂亮的合影记录的不仅仅你们精彩生活的瞬间，更是你与朋友们真诚热烈的情谊，希望你能够将它好好保存下去。', Speaker = 6108,}, 
	[85258] = { id = 85258, Text = '找到公会内的两名成员，与他们两合影一张吧，让这平凡的生活因为有朋友们的陪伴而变得精彩纷呈，让这张合影来记录下脑海中最美好的记忆。', Speaker = 6108,}, 
	[85257] = { id = 85257, Text = '挂在墙上特殊位置的精美相框，它之所以被摆放在这里，似乎就是在提醒着大家，一个用心生活的人，从来不会错过生活中的点点滴滴，因为这些都是我们最珍贵美好的回忆。', Speaker = 6108,}, 
	[85199] = { id = 85199, Text = '这真是一张构思精巧的合影呢，合影中央清晰可见你和朋友的身影，透过镜头，传达出来的是你们最真挚的友谊和对未来最殷切的展望。', Speaker = 6080,}, 
	[85198] = { id = 85198, Text = '找到一名公会成员，与他合影一张吧，把这张记录着友情岁月的照片也挂在公会的墙上。', Speaker = 6080,}, 
	[85197] = { id = 85197, Text = '墙上挂着的合影涂鸦，涂鸦的画面似乎是几个公会成员肩并着肩，满脸笑容地站在初升旭日下，浓情壮丽的场景令人心生向往。', Speaker = 6080,}, 
	[85196] = { id = 85196, Text = '这真是一张精美的照片呢，从照片中似乎就可以看到你和朋友们的深厚情谊，足以回忆留恋。', Speaker = 6079,}, 
	[85195] = { id = 85195, Text = '用这部相机去拍摄一张公会成员的照片吧，最美好的时光一定要用最精致的照片保存下来。', Speaker = 6079,}, 
	[85194] = { id = 85194, Text = '摆放在桌子上的精美相机，看着它就有一种直接的感觉——用它拍出来的照片一定很好看。', Speaker = 6079,}, 
	[85193] = { id = 85193, Text = '冒险者，捉弄朋友们的感觉很棒吧，但更棒的应该是你终于向朋友们表达出了自己内心深处的想法，我想他们一定可以感受到。', Speaker = 6084,}, 
	[85192] = { id = 85192, Text = '冒险者，如果你也想表达自己内心对于朋友们的关心和爱护却说不出口，那么不妨换一种方式，请你对所在公会的任意五名成员使用一次变傻光线枪，捉弄一下他们吧。', Speaker = 6084,}, 
	[85191] = { id = 85191, Text = '哥布灵们从来都是快乐的追随者，有时候即使被捉弄，我们也毫不放在心上，因为我们知道所有的捉弄都是源自同伴们最诚挚的关心和对快乐的追求。', Speaker = 6084,}, 
	[85190] = { id = 85190, Text = '怎么样，冒险者，有时候不必去纠结，也不必扭捏，善意的捉弄更能够增进你与朋友们的感情呢。', Speaker = 6084,}, 
	[85189] = { id = 85189, Text = '冒险者，请尝试按照我们哥布灵的礼仪，对你所在公会的任意三名成员使用一次变傻光线枪吧，用捉弄去表达你对朋友们的尊重和敬仰。', Speaker = 6084,}, 
	[85188] = { id = 85188, Text = '在我们哥布灵的世界里，从来不会因为被捉弄而感到气愤恼怒，因为在我们看来，被捉弄可是受欢迎的表现哦，只有出色的哥布灵才会受到大家的捉弄。', Speaker = 6084,}, 
	[85187] = { id = 85187, Text = '夸大其实的变傻光线枪显然不能使人真的变傻，但当你用变傻光线枪把朋友们变成一个小矮子时，他估计也会有感到绝望的一瞬间吧。', Speaker = 6083,}, 
	[85186] = { id = 85186, Text = '若是对任意一名公会成员使用这把变傻光线枪的话，他真的会变傻吗？反复的疑问在脑海中回旋，竟有催促着你去亲自实践的趋势。', Speaker = 6083,}, 
	[85185] = { id = 85185, Text = '突然出现在眼前的一把变傻光线枪，独特的造型令人眼前一亮，若是不熟悉的人看到它绝不会有其他反应，但是深知详情的你见到它后却涌起了一股莫名的冲动……', Speaker = 6083,}, 
	[85184] = { id = 85184, Text = '冒险者，你做得很好，相信现在的你一定有所领悟，如果你真的热爱和朋友们相聚在一起，那就用行动去表现出来吧。', Speaker = 6086,}, 
	[85183] = { id = 85183, Text = '冒险者，如果你也想和朋友们一起尽情欢笑，那就请对你所在公会的任意五名成员使用一次我中意你，享受最简单的快乐吧。', Speaker = 6086,}, 
	[85182] = { id = 85182, Text = '嘿嘿，哥布灵最喜欢和朋友们一块玩耍，我们一起捉迷藏，一起放风筝，一起数星星……无忧无虑，简直是天底下最快乐的事情呢。', Speaker = 6086,}, 
	[85181] = { id = 85181, Text = '果然是很有领悟力的冒险者呢，敢于向朋友们表达心中爱意的人，可是能够在公会交往中更受到欢迎和尊重的呢。', Speaker = 6086,}, 
	[85180] = { id = 85180, Text = '冒险者，如果你也想生活在一个爱的大家庭中，那就主动对你所在公会的任意三名成员使用一次我中意你，提升一下彼此的亲密度吧。', Speaker = 6086,}, 
	[85179] = { id = 85179, Text = '千万不要小瞧哥布灵的友谊哦，我们每个哥布灵之间都是最亲密的朋友，彼此联系彼此支持，就像一个充满爱的大家庭。', Speaker = 6086,}, 
	[85178] = { id = 85178, Text = '正如它的名字所表达的那样，当你选择对朋友们表达最诚挚的友谊时，我中意你无疑是最适合的物品。', Speaker = 6085,}, 
	[85177] = { id = 85177, Text = '听说如果选择对一名公会成员使用一次我中意你，就会有某些神奇的事情发生哦……也不知道是不是真的。', Speaker = 6085,}, 
	[85176] = { id = 85176, Text = '在地上被发现的我中意你，也不知它是偶然出现在这里，还是被不知名的人刻意摆放在这里的。', Speaker = 6085,}, 
	[85175] = { id = 85175, Text = '现在的你知道了吧，快乐就是这么简单！如果你非常在意你的朋友们，那就勇敢地去捉弄他们吧。', Speaker = 6088,}, 
	[85174] = { id = 85174, Text = '如果你想像我们哥布灵这样快乐，就先学会捉弄别人吧。悄悄对你所在公会的任意五名成员使用一次我手上的恶作剧魔棒，这样你就能发现快乐的秘密啦。', Speaker = 6088,}, 
	[85173] = { id = 85173, Text = '嘿嘿，我们哥布灵是世界上最快乐的种族，因为我们知道，一个简单的手势、一个平凡的微笑甚至一次善意的捉弄都能成为快乐的开始。', Speaker = 6088,}, 
	[85172] = { id = 85172, Text = '怎么样，我没有骗你吧，有时候小小的恶作剧也能让你和朋友们的关系更加亲密，这可是我们哥布灵的秘密哦。', Speaker = 6088,}, 
	[85171] = { id = 85171, Text = '你只需要悄悄对你所在公会的任意三名成员使用一次这根恶作剧魔棒，就可以感受到恶作剧带来的别样惊喜和快乐了。', Speaker = 6088,}, 
	[85170] = { id = 85170, Text = '嘿嘿，对于我们哥布灵来说，如果这世界上没有恶作剧，那将会是一件多么可悲的事情。怎么样，想感受一下恶作剧的魅力吗？', Speaker = 6088,}, 
	[85169] = { id = 85169, Text = '外形稍显怪异的恶作剧魔棒，却可以带给大家快乐和欢笑，当你把一个人凭空变成一只波利的时候，你还能忍住不笑吗？', Speaker = 6087,}, 
	[85168] = { id = 85168, Text = '如果悄悄对公会里的一名成员使用一次恶作剧魔棒，会有什么事情发生呢？这可真是令人疑惑。', Speaker = 6087,}, 
	[85167] = { id = 85167, Text = '一眼望去这只不过是一根普通的魔棒，但若是仔细观察，不难看到这根魔棒上散发的古怪魔力波动，很显然，这是一根恶作剧魔棒。', Speaker = 6087,}, 
	[85166] = { id = 85166, Text = '怎么样，波伊说得没错吧，和朋友们一起坐在草地上静静地听着悠扬的音乐响起，这是多么幸福的事啊~啵~', Speaker = 6089,}, 
	[85165] = { id = 85165, Text = '冒险者，如果你也喜欢音乐，为什么不邀请你的公会朋友们一起来听呢？', Speaker = 6089,}, 
	[85164] = { id = 85164, Text = '波伊最喜欢听音乐了，每次和同伴们聚集在南门听音乐的时候，大家都会笑得很开心~啵~', Speaker = 6089,}, 
	[85163] = { id = 85163, Text = '这可真是一次幸福愉快的体验呢，经历了音乐海洋的洗礼，我想即使是最艰难的考验，也难不倒你和你的朋友们。', Speaker = 6090,}, 
	[85162] = { id = 85162, Text = '冒险者，如果你也对音乐有着莫大的热情，那就邀请你所在公会的两名成员，一起来南门听我唱歌吧。', Speaker = 6090,}, 
	[85161] = { id = 85161, Text = '你知道吗？音乐的能量能够振奋人心，鼓舞斗志，在漫长的冒险旅途中，婉转的音乐可是最动人心弦的旋律呢。', Speaker = 6090,}, 
	[85160] = { id = 85160, Text = '冒险者，你的表现真是太棒了，我要恭喜你在音乐的海洋里收获了能够相伴一生的真挚友谊呢。', Speaker = 6090,}, 
	[85159] = { id = 85159, Text = '冒险者，请找到你所在公会的任意四名成员，一起组队去南门听歌吧，有时候只有和大家在一起，才能感受到音乐带来的热情和感动哦。', Speaker = 6090,}, 
	[85158] = { id = 85158, Text = '最新的研究表明，美妙的音乐能够营造轻松愉悦的心理环境，更有助于改善人际关系，如果你也为美妙之音着迷，何不把更多的快乐分享给你的朋友们呢？', Speaker = 6090,}, 
	[85157] = { id = 85157, Text = '冒险者，你完成得很出色，怎么样，和朋友们一起努力奋斗的感觉不错吧，或许在不久之后的某一天你也会为曾经的公会和知心好友们而感到骄傲和荣耀。', Speaker = 6091,}, 
	[85156] = { id = 85156, Text = '冒险者，那么请拍摄一张你所在公会任意成员使用蹲坐技能时的照片给我吧，你需要明白，每一个公会成员都是我们冒险旅途中最亲密可靠的伙伴，因而每一份友谊都需要被珍视哦。', Speaker = 6091,}, 
	[85155] = { id = 85155, Text = '冒险者，你知道吗？公会里的每个成员都是独一无二的哦，正是因为有了不同职业、不同角色的成员，公会的成长才会变得不可预测，也变得更加具有成就感呢。', Speaker = 6091,}, 
	[85154] = { id = 85154, Text = '冒险者，你这么快就完成了拍照？虽然这只是一张照片，但却是你和朋友们真挚友谊的见证，让我们一起为了更融洽欢乐的公会集体而努力奋斗吧。', Speaker = 6091,}, 
	[85153] = { id = 85153, Text = '冒险者，你的公会里是不是也充满活力呢？如果是，请拍摄一张你所在公会任意成员使用装死技能时的照片给我。', Speaker = 6091,}, 
	[85152] = { id = 85152, Text = '据我多年的观察发现，一个有活力的公会一定会是良性互动和共享乐趣的集体，成员们不卑不亢，宠辱共进，即使在平凡的日子里也能发现乐趣，享受乐趣。', Speaker = 6091,},
}
Table_Dialog_Quest8_fields = {"id", "Speaker", "Text", "Option", "Emoji", "Action", "Voice", "SubViewId" }
return Table_Dialog_Quest8
	